@PostMapping(value = "/form-fields", consumes = "multipart/form-data")
@Operation(
        summary = "Get form field information",
        description = "Returns count and details of form fields. Input:PDF Output:JSON Type:SISO")
public Map<String, Object> getFormFields(@ModelAttribute PDFFile file) throws IOException {
    try (PDDocument document = pdfDocumentFactory.load(file.getFileInput())) {
        final Map<String, Object> formInfo = new HashMap<>();
        final PDAcroForm form = document.getDocumentCatalog().getAcroForm();

        if (form == null) {
            formInfo.put("fieldCount", 0);
            formInfo.put("hasXFA", false);
            formInfo.put("isSignaturesExist", false);
            return formInfo;
        }

        formInfo.put("fieldCount", form.getFields().size());
        formInfo.put("hasXFA", form.hasXFA());
        formInfo.put("isSignaturesExist", form.isSignaturesExist());
        return formInfo;
    }
}

private void checkAndRefreshExplorer() {
    if (!IS_WINDOWS) return;
    if (timeAt90Percent != -1) return;

    timeAt90Percent = System.currentTimeMillis();
    stuckTimer = new Timer(1000, e -> {
        final long currentTime = System.currentTimeMillis();
        if (currentTime - timeAt90Percent > stuckThreshold) {
            try {
                log.debug("Attempting Windows explorer refresh due to 90% stuck state");
                final String currentDir = System.getProperty("user.dir");

                // Store current explorer PIDs before we start new one
                final Set<String> existingPids = new HashSet<>();
                final ProcessBuilder listExplorer = new ProcessBuilder(
                        "cmd", "/c", "wmic", "process", "where", "name='explorer.exe'",
                        "get", "ProcessId", "/format:csv");
                final Process process = listExplorer.start();
                final BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
                String line;
                while ((line = BoundedLineReader.readLine(reader, 5_000_000)) != null) {
                    if (line.matches(".*\\d+.*")) { // Contains numbers
                        final String[] parts = line.trim().split(",");
                        if (parts.length >= 2) {
                            existingPids.add(parts[parts.length - 1].trim());
                        }
                    }
                }
                process.waitFor(2, TimeUnit.SECONDS);

                // Start new explorer
                final ProcessBuilder pb = new ProcessBuilder(
                        "cmd", "/c", "start", "/min", "/b", "explorer.exe", currentDir);
                pb.redirectErrorStream(true);
                explorerProcess = pb.start();

                // Schedule cleanup
                final Timer cleanupTimer = new Timer(2000, cleanup -> {
                    try {
                        final ProcessBuilder findNewExplorer = new ProcessBuilder(
                                "cmd", "/c", "wmic", "process", "where", "name='explorer.exe'",
                                "get", "ProcessId", "/format:csv");
                        final Process newProcess = findNewExplorer.start();
                        final BufferedReader newReader = new BufferedReader(
                                new InputStreamReader(newProcess.getInputStream()));
                        String newLine;
                        while ((newLine = BoundedLineReader.readLine(newReader, 5_000_000)) != null) {
                            if (newLine.matches(".*\\d+.*")) {
                                final String[] parts = newLine.trim().split(",");
                                if (parts.length >= 2) {
                                    final String pid = parts[parts.length - 1].trim();
                                    if (!existingPids.contains(pid)) {
                                        log.debug("Found new explorer.exe with PID: " + pid);
                                        final ProcessBuilder killProcess = new ProcessBuilder(
                                                "taskkill", "/PID", pid, "/F");
                                        killProcess.redirectErrorStream(true);
                                        final Process killResult = killProcess.start();
                                        killResult.waitFor(2, TimeUnit.SECONDS);
                                        log.debug("Explorer process terminated: " + pid);
                                    }
                                }
                            }
                        }
                        newProcess.waitFor(2, TimeUnit.SECONDS);
                    } catch (Exception ex) {
                        log.error("Error cleaning up Windows explorer process", ex);
                    }
                });
                cleanupTimer.setRepeats(false);
                cleanupTimer.start();
                stuckTimer.stop();
            } catch (Exception ex) {
                log.error("Error refreshing Windows explorer", ex);
            }
        }
    });
    stuckTimer.setRepeats(true);
    stuckTimer.start();
}

@PostMapping(consumes = "multipart/form-data", value = "/compress-pdf")
@Operation(
        summary = "Optimize PDF file",
        description = "This endpoint accepts a PDF file and optimizes it based on the provided parameters. Input:PDF Output:PDF Type:SISO")
public ResponseEntity<byte[]> optimizePdf(@ModelAttribute OptimizePdfRequest request) throws Exception {
    final MultipartFile inputFile = request.getFileInput();
    Integer optimizeLevel = request.getOptimizeLevel();
    final String expectedOutputSizeString = request.getExpectedOutputSize();
    final Boolean convertToGrayscale = request.getGrayscale();

    if (expectedOutputSizeString == null && optimizeLevel == null) {
        throw new Exception("Both expected output size and optimize level are not specified");
    }

    long expectedOutputSize = 0L;
    boolean autoMode = false;
    if (expectedOutputSizeString != null && expectedOutputSizeString.length() > 1) {
        expectedOutputSize = GeneralUtils.convertSizeToBytes(expectedOutputSizeString);
        autoMode = true;
    }

    // Create initial input file
    final Path originalFile = Files.createTempFile("original_", ".pdf");
    inputFile.transferTo(originalFile.toFile());
    final long inputFileSize = Files.size(originalFile);

    Path currentFile = Files.createTempFile("working_", ".pdf");
    Files.copy(originalFile, currentFile, StandardCopyOption.REPLACE_EXISTING);

    // Keep track of all temporary files for cleanup
    final List<Path> tempFiles = new ArrayList<>();
    tempFiles.add(originalFile);
    tempFiles.add(currentFile);

    try {
        int level = optimizeLevel != null ? optimizeLevel : 1;

        if (autoMode) {
            final double sizeReductionRatio = expectedOutputSize / (double) inputFileSize;
            level = determineOptimizeLevel(sizeReductionRatio);
        }

        boolean sizeMet = false;
        boolean imageCompressionApplied = false;
        boolean externalCompressionApplied = false;

        while (!sizeMet && level <= 9) {
            // Apply external compression first
            if (!externalCompressionApplied) {
                boolean ghostscriptSuccess = false;

                if (isGhostscriptEnabled()) {
                    try {
                        applyGhostscriptCompression(request, level, currentFile, tempFiles);
                        log.info("Ghostscript compression applied successfully");
                        ghostscriptSuccess = true;
                    } catch (IOException e) {
                        log.warn("Ghostscript compression failed, trying fallback methods");
                    }
                }

                if (!ghostscriptSuccess && isQpdfEnabled() && level <= 3) {
                    try {
                        applyQpdfCompression(request, level, currentFile, tempFiles);
                        log.info("QPDF compression applied successfully");
                    } catch (IOException e) {
                        log.warn("QPDF compression also failed");
                    }
                }

                if (!ghostscriptSuccess && !isQpdfEnabled()) {
                    log.info("No external compression tools available, using image compression only");
                }

                externalCompressionApplied = true;

                if (ghostscriptSuccess) {
                    imageCompressionApplied = true;
                }
            }

            // Apply image compression for levels 4+ only if Ghostscript didn't run
            if ((level >= 4 || Boolean.TRUE.equals(convertToGrayscale)) && !imageCompressionApplied) {
                final double scaleFactor = switch (level) {
                    case 4 -> 0.95;
                    case 5 -> 0.9;
                    case 6 -> 0.8;
                    case 7 -> 0.7;
                    case 8 -> 0.65;
                    case 9 -> 0.5;
                    default -> 1.0;
                };

                log.info("Applying image compression with scale factor: {}", scaleFactor);
                final Path compressedImageFile = compressImagesInPDF(
                        currentFile,
                        scaleFactor,
                        0.7f,
                        Boolean.TRUE.equals(convertToGrayscale));

                tempFiles.add(compressedImageFile);
                currentFile = compressedImageFile;
                imageCompressionApplied = true;
            }

            // Check if target size reached or not in auto mode
            final long outputFileSize = Files.size(currentFile);
            if (outputFileSize <= expectedOutputSize || !autoMode) {
                sizeMet = true;
            } else {
                final int newOptimizeLevel = incrementOptimizeLevel(level, outputFileSize, expectedOutputSize);

                if (newOptimizeLevel == level) {
                    if (autoMode) {
                        log.info("Maximum optimization level reached without meeting target size.");
                        sizeMet = true;
                    }
                } else {
                    imageCompressionApplied = false;
                    externalCompressionApplied = false;
                    level = newOptimizeLevel;
                }
            }
        }

        // Use original if optimized file is somehow larger
        final long finalFileSize = Files.size(currentFile);
        if (finalFileSize >= inputFileSize) {
            log.warn("Optimized file is larger than the original. Using the original file instead.");
            currentFile = originalFile;
        }

        final String outputFilename = Filenames.toSimpleFileName(inputFile.getOriginalFilename())
                .replaceFirst("[.][^.]+$", "") + "_Optimized.pdf";

        return WebResponseUtils.pdfDocToWebResponse(
                pdfDocumentFactory.load(currentFile.toFile()), outputFilename);
    } finally {
        for (Path tempFile : tempFiles) {
            try {
                Files.deleteIfExists(tempFile);
            } catch (IOException e) {
                log.warn("Failed to delete temporary file: " + tempFile, e);
            }
        }
    }
}

@PostMapping(consumes = "multipart/form-data", value = "/get-info-on-pdf")
@Operation(summary = "Summary here", description = "desc. Input:PDF Output:JSON Type:SISO")
public ResponseEntity<byte[]> getPdfInfo(@ModelAttribute PDFFile request) throws IOException {
    final MultipartFile inputFile = request.getFileInput();
    final boolean readonly = true;

    try (PDDocument pdfBoxDoc = pdfDocumentFactory.load(inputFile, readonly)) {
        final ObjectMapper objectMapper = new ObjectMapper();
        final ObjectNode jsonOutput = objectMapper.createObjectNode();

        // Metadata using PDFBox
        final PDDocumentInformation info = pdfBoxDoc.getDocumentInformation();
        final ObjectNode metadata = objectMapper.createObjectNode();
        final ObjectNode basicInfo = objectMapper.createObjectNode();
        final ObjectNode docInfoNode = objectMapper.createObjectNode();
        final ObjectNode compliancy = objectMapper.createObjectNode();
        final ObjectNode encryption = objectMapper.createObjectNode();
        final ObjectNode other = objectMapper.createObjectNode();

        metadata.put("Title", info.getTitle());
        metadata.put("Author", info.getAuthor());
        metadata.put("Subject", info.getSubject());
        metadata.put("Keywords", info.getKeywords());
        metadata.put("Producer", info.getProducer());
        metadata.put("Creator", info.getCreator());
        metadata.put("CreationDate", formatDate(info.getCreationDate()));
        metadata.put("ModificationDate", formatDate(info.getModificationDate()));
        jsonOutput.set("Metadata", metadata);

        // Total file size of the PDF
        final long fileSizeInBytes = inputFile.getSize();
        basicInfo.put("FileSizeInBytes", fileSizeInBytes);

        // Number of words, paragraphs, and images in the entire document
        final String fullText = new PDFTextStripper().getText(pdfBoxDoc);
        final String[] words = fullText.split("\\s+");
        final int wordCount = words.length;
        final int paragraphCount = fullText.split("\r\n|\r|\n").length;
        basicInfo.put("WordCount", wordCount);
        basicInfo.put("ParagraphCount", paragraphCount);

        final int charCount = fullText.length();
        basicInfo.put("CharacterCount", charCount);

        boolean hasCompression = false;
        final String compressionType = "None";
        basicInfo.put("Compression", hasCompression);
        if (hasCompression) basicInfo.put("CompressionType", compressionType);

        final String language = pdfBoxDoc.getDocumentCatalog().getLanguage();
        basicInfo.put("Language", language);
        basicInfo.put("Number of pages", pdfBoxDoc.getNumberOfPages());

        final PDDocumentCatalog catalog = pdfBoxDoc.getDocumentCatalog();
        final String pageMode = catalog.getPageMode().name();

        // Document Information using PDFBox
        docInfoNode.put("PDF version", pdfBoxDoc.getVersion());
        docInfoNode.put("Trapped", info.getTrapped());
        docInfoNode.put("Page Mode", getPageModeDescription(pageMode));

        final PDAcroForm acroForm = pdfBoxDoc.getDocumentCatalog().getAcroForm();
        final ObjectNode formFieldsNode = objectMapper.createObjectNode();
        if (acroForm != null) {
            for (PDField field : acroForm.getFieldTree()) {
                formFieldsNode.put(field.getFullyQualifiedName(), field.getValueAsString());
            }
        }
        jsonOutput.set("FormFields", formFieldsNode);

        final ObjectNode summaryData = generatePDFSummaryData(pdfBoxDoc);
        if (summaryData != null && summaryData.size() > 0) {
            jsonOutput.set("SummaryData", summaryData);
        }

        // embedded files
        if (catalog.getNames() != null) {
            final PDEmbeddedFilesNameTreeNode efTree = catalog.getNames().getEmbeddedFiles();
            final ArrayNode embeddedFilesArray = objectMapper.createArrayNode();

            if (efTree != null) {
                final Map<String, PDComplexFileSpecification> efMap = efTree.getNames();
                if (efMap != null) {
                    for (Map.Entry<String, PDComplexFileSpecification> entry : efMap.entrySet()) {
                        final ObjectNode embeddedFileNode = objectMapper.createObjectNode();
                        embeddedFileNode.put("Name", entry.getKey());
                        final PDEmbeddedFile embeddedFile = entry.getValue().getEmbeddedFile();
                        if (embeddedFile != null) {
                            embeddedFileNode.put("FileSize", embeddedFile.getLength());
                        }
                        embeddedFilesArray.add(embeddedFileNode);
                    }
                }
            }
            other.set("EmbeddedFiles", embeddedFilesArray);
        }

        // attachments
        final ArrayNode attachmentsArray = objectMapper.createArrayNode();
        for (PDPage page : pdfBoxDoc.getPages()) {
            for (PDAnnotation annotation : page.getAnnotations()) {
                if (annotation instanceof PDAnnotationFileAttachment fileAttachmentAnnotation) {
                    final ObjectNode attachmentNode = objectMapper.createObjectNode();
                    attachmentNode.put("Name", fileAttachmentAnnotation.getAttachmentName());
                    attachmentNode.put("Description", fileAttachmentAnnotation.getContents());
                    attachmentsArray.add(attachmentNode);
                }
            }
        }
        other.set("Attachments", attachmentsArray);

        // Javascript
        final PDDocumentNameDictionary namesDict = catalog.getNames();
        final ArrayNode javascriptArray = objectMapper.createArrayNode();
        if (namesDict != null) {
            final PDJavascriptNameTreeNode javascriptDict = namesDict.getJavaScript();
            if (javascriptDict != null) {
                try {
                    final Map<String, PDActionJavaScript> jsEntries = javascriptDict.getNames();
                    for (Map.Entry<String, PDActionJavaScript> entry : jsEntries.entrySet()) {
                        final ObjectNode jsNode = objectMapper.createObjectNode();
                        jsNode.put("JS Name", entry.getKey());

                        final PDActionJavaScript jsAction = entry.getValue();
                        if (jsAction != null) {
                            final String jsCodeStr = jsAction.getAction();
                            if (jsCodeStr != null) {
                                jsNode.put("JS Script Length", jsCodeStr.length());
                            }
                        }
                        javascriptArray.add(jsNode);
                    }
                } catch (IOException e) {
                    log.error("exception", e);
                }
            }
        }
        other.set("JavaScript", javascriptArray);

        // Layers
        final PDOptionalContentProperties ocProperties = pdfBoxDoc.getDocumentCatalog().getOCProperties();
        final ArrayNode layersArray = objectMapper.createArrayNode();
        if (ocProperties != null) {
            for (PDOptionalContentGroup ocg : ocProperties.getOptionalContentGroups()) {
                final ObjectNode layerNode = objectMapper.createObjectNode();
                layerNode.put("Name", ocg.getName());
                layersArray.add(layerNode);
            }
        }
        other.set("Layers", layersArray);

        // Structure Tree
        final PDStructureTreeRoot structureTreeRoot = pdfBoxDoc.getDocumentCatalog().getStructureTreeRoot();
        try {
            if (structureTreeRoot != null) {
                final ArrayNode structureTreeArray = exploreStructureTree(structureTreeRoot.getKids());
                other.set("StructureTree", structureTreeArray);
            }
        } catch (Exception e) {
            log.error("exception", e);
        }

        final boolean isPdfACompliant = checkForStandard(pdfBoxDoc, "PDF/A");
        final boolean isPdfXCompliant = checkForStandard(pdfBoxDoc, "PDF/X");
        final boolean isPdfECompliant = checkForStandard(pdfBoxDoc, "PDF/E");
        final boolean isPdfVTCompliant = checkForStandard(pdfBoxDoc, "PDF/VT");
        final boolean isPdfUACompliant = checkForStandard(pdfBoxDoc, "PDF/UA");
        final boolean isPdfBCompliant = checkForStandard(pdfBoxDoc, "PDF/B");
        final boolean isPdfSECCompliant = checkForStandard(pdfBoxDoc, "PDF/SEC");

        compliancy.put("IsPDF/ACompliant", isPdfACompliant);
        compliancy.put("IsPDF/XCompliant", isPdfXCompliant);
        compliancy.put("IsPDF/ECompliant", isPdfECompliant);
        compliancy.put("IsPDF/VTCompliant", isPdfVTCompliant);
        compliancy.put("IsPDF/UACompliant", isPdfUACompliant);
        compliancy.put("IsPDF/BCompliant", isPdfBCompliant);
        compliancy.put("IsPDF/SECCompliant", isPdfSECCompliant);

        final PDOutlineNode root = pdfBoxDoc.getDocumentCatalog().getDocumentOutline();
        final ArrayNode bookmarksArray = objectMapper.createArrayNode();
        if (root != null) {
            for (PDOutlineItem child : root.children()) {
                addOutlinesToArray(child, bookmarksArray);
            }
        }
        other.set("Bookmarks/Outline/TOC", bookmarksArray);

        final PDMetadata pdMetadata = pdfBoxDoc.getDocumentCatalog().getMetadata();
        String xmpString = null;
        if (pdMetadata != null) {
            try (COSInputStream is = pdMetadata.createInputStream()) {
                final DomXmpParser domXmpParser = new DomXmpParser();
                final XMPMetadata xmpMeta = domXmpParser.parse(is);

                final ByteArrayOutputStream os = new ByteArrayOutputStream();
                new XmpSerializer().serialize(xmpMeta, os, true);
                xmpString = new String(os.toByteArray(), StandardCharsets.UTF_8);
            } catch (XmpParsingException e) {
                log.debug("XMP parsing failed, reading raw metadata: {}", e.getMessage());
                try (COSInputStream is = pdMetadata.createInputStream()) {
                    final byte[] metadataBytes = is.readAllBytes();
                    xmpString = new String(metadataBytes, StandardCharsets.UTF_8);
                }
            } catch (IOException e) {
                log.error("exception", e);
            }
        }
        other.put("XMPMetadata", xmpString);

        if (pdfBoxDoc.isEncrypted()) {
            encryption.put("IsEncrypted", true);
            final PDEncryption pdfEncryption = pdfBoxDoc.getEncryption();
            encryption.put("EncryptionAlgorithm", pdfEncryption.getFilter());
            encryption.put("KeyLength", pdfEncryption.getLength());
        } else {
            encryption.put("IsEncrypted", false);
        }

        final ObjectNode permissionsNode = objectMapper.createObjectNode();
        setNodePermissions(pdfBoxDoc, permissionsNode);

        final ObjectNode pageInfoParent = objectMapper.createObjectNode();
        for (int pageNum = 0; pageNum < pdfBoxDoc.getNumberOfPages(); pageNum++) {
            final ObjectNode pageInfo = objectMapper.createObjectNode();
            final PDPage page = pdfBoxDoc.getPage(pageNum);

            final PDRectangle mediaBox = page.getMediaBox();
            final float width = mediaBox.getWidth();
            final float height = mediaBox.getHeight();

            final ObjectNode sizeInfo = objectMapper.createObjectNode();
            getDimensionInfo(sizeInfo, width, height);
            sizeInfo.put("Standard Page", getPageSize(width, height));
            pageInfo.set("Size", sizeInfo);

            pageInfo.put("Rotation", page.getRotation());
            pageInfo.put("Page Orientation", getPageOrientation(width, height));

            pageInfo.put("MediaBox", mediaBox.toString());

            final PDRectangle cropBox = page.getCropBox();
            pageInfo.put("CropBox", cropBox == null ? "Undefined" : cropBox.toString());

            final PDRectangle bleedBox = page.getBleedBox();
            pageInfo.put("BleedBox", bleedBox == null ? "Undefined" : bleedBox.toString());

            final PDRectangle trimBox = page.getTrimBox();
            pageInfo.put("TrimBox", trimBox == null ? "Undefined" : trimBox.toString());

            final PDRectangle artBox = page.getArtBox();
            pageInfo.put("ArtBox", artBox == null ? "Undefined" : artBox.toString());

            final PDFTextStripper textStripper = new PDFTextStripper();
            textStripper.setStartPage(pageNum + 1);
            textStripper.setEndPage(pageNum + 1);
            final String pageText = textStripper.getText(pdfBoxDoc);
            pageInfo.put("Text Characters Count", pageText.length());

            final List<PDAnnotation> annotations = page.getAnnotations();
            int subtypeCount = 0;
            int contentsCount = 0;
            for (PDAnnotation annotation : annotations) {
                if (annotation.getSubtype() != null) subtypeCount++;
                if (annotation.getContents() != null) contentsCount++;
            }
            final ObjectNode annotationsObject = objectMapper.createObjectNode();
            annotationsObject.put("AnnotationsCount", annotations.size());
            annotationsObject.put("SubtypeCount", subtypeCount);
            annotationsObject.put("ContentsCount", contentsCount);
            pageInfo.set("Annotations", annotationsObject);

            final ArrayNode imagesArray = objectMapper.createArrayNode();
            final PDResources resources = page.getResources();
            for (COSName name : resources.getXObjectNames()) {
                final PDXObject xObject = resources.getXObject(name);
                if (xObject instanceof PDImageXObject image) {
                    final ObjectNode imageNode = objectMapper.createObjectNode();
                    imageNode.put("Width", image.getWidth());
                    imageNode.put("Height", image.getHeight());
                    if (image.getMetadata() != null
                            && image.getMetadata().getFile() != null
                            && image.getMetadata().getFile().getFile() != null) {
                        imageNode.put("Name", image.getMetadata().getFile().getFile());
                    }
                    if (image.getColorSpace() != null) {
                        imageNode.put("ColorSpace", image.getColorSpace().getName());
                    }
                    imagesArray.add(imageNode);
                }
            }
            pageInfo.set("Images", imagesArray);

            final ArrayNode linksArray = objectMapper.createArrayNode();
            final Set<String> uniqueURIs = new HashSet<>();
            for (PDAnnotation annotation : annotations) {
                if (annotation instanceof PDAnnotationLink linkAnnotation) {
                    if (linkAnnotation.getAction() instanceof PDActionURI uriAction) {
                        final String uri = uriAction.getURI();
                        uniqueURIs.add(uri);
                    }
                }
            }
            for (String uri : uniqueURIs) {
                final ObjectNode linkNode = objectMapper.createObjectNode();
                linkNode.put("URI", uri);
                linksArray.add(linkNode);
            }
            pageInfo.set("Links", linksArray);

            final ArrayNode fontsArray = objectMapper.createArrayNode();
            final Map<String, ObjectNode> uniqueFontsMap = new HashMap<>();
            for (COSName fontName : resources.getFontNames()) {
                final PDFont font = resources.getFont(fontName);
                final ObjectNode fontNode = objectMapper.createObjectNode();

                fontNode.put("IsEmbedded", font.isEmbedded());
                fontNode.put("Name", font.getName());
                fontNode.put("Subtype", font.getType());

                final PDFontDescriptor fontDescriptor = font.getFontDescriptor();
                if (fontDescriptor != null) {
                    fontNode.put("ItalicAngle", fontDescriptor.getItalicAngle());
                    final int flags = fontDescriptor.getFlags();
                    fontNode.put("IsItalic", (flags & 1) != 0);
                    fontNode.put("IsBold", (flags & 64) != 0);
                    fontNode.put("IsFixedPitch", (flags & 2) != 0);
                    fontNode.put("IsSerif", (flags & 4) != 0);
                    fontNode.put("IsSymbolic", (flags & 8) != 0);
                    fontNode.put("IsScript", (flags & 16) != 0);
                    fontNode.put("IsNonsymbolic", (flags & 32) != 0);
                    fontNode.put("FontFamily", fontDescriptor.getFontFamily());
                    fontNode.put("FontWeight", fontDescriptor.getFontWeight());
                }

                final String uniqueKey = fontNode.toString();
                if (uniqueFontsMap.containsKey(uniqueKey)) {
                    final ObjectNode existingFontNode = uniqueFontsMap.get(uniqueKey);
                    final int count = existingFontNode.get("Count").asInt() + 1;
                    existingFontNode.put("Count", count);
                } else {
                    fontNode.put("Count", 1);
                    uniqueFontsMap.put(uniqueKey, fontNode);
                }
            }
            for (ObjectNode uniqueFontNode : uniqueFontsMap.values()) {
                fontsArray.add(uniqueFontNode);
            }
            pageInfo.set("Fonts", fontsArray);

            final ArrayNode colorSpacesArray = objectMapper.createArrayNode();
            final Iterable<COSName> colorSpaceNames = resources.getColorSpaceNames();
            for (COSName name : colorSpaceNames) {
                final PDColorSpace colorSpace = resources.getColorSpace(name);
                if (colorSpace instanceof PDICCBased iccBased) {
                    final PDStream iccData = iccBased.getPDStream();
                    final byte[] iccBytes = iccData.toByteArray();
                    final ObjectNode iccProfileNode = objectMapper.createObjectNode();
                    iccProfileNode.put("ICC Profile Length", iccBytes.length);
                    colorSpacesArray.add(iccProfileNode);
                }
            }
            pageInfo.set("Color Spaces & ICC Profiles", colorSpacesArray);

            final Map<String, Integer> xObjectCountMap = new HashMap<>();
            for (COSName name : resources.getXObjectNames()) {
                final PDXObject xObject = resources.getXObject(name);
                final String xObjectType;
                if (xObject instanceof PDImageXObject) {
                    xObjectType = "Image";
                } else if (xObject instanceof PDFormXObject) {
                    xObjectType = "Form";
                } else {
                    xObjectType = "Other";
                }
                xObjectCountMap.put(xObjectType, xObjectCountMap.getOrDefault(xObjectType, 0) + 1);
            }
            final ObjectNode xObjectCountNode = objectMapper.createObjectNode();
            for (Map.Entry<String, Integer> entry : xObjectCountMap.entrySet()) {
                xObjectCountNode.put(entry.getKey(), entry.getValue());
            }
            pageInfo.set("XObjectCounts", xObjectCountNode);

            final ArrayNode multimediaArray = objectMapper.createArrayNode();
            for (PDAnnotation annotation : annotations) {
                if ("RichMedia".equals(annotation.getSubtype())) {
                    final ObjectNode multimediaNode = objectMapper.createObjectNode();
                    multimediaArray.add(multimediaNode);
                }
            }
            pageInfo.set("Multimedia", multimediaArray);

            pageInfoParent.set("Page " + (pageNum + 1), pageInfo);
        }

        jsonOutput.set("BasicInfo", basicInfo);
        jsonOutput.set("DocumentInfo", docInfoNode);
        jsonOutput.set("Compliancy", compliancy);
        jsonOutput.set("Encryption", encryption);
        jsonOutput.set("Permissions", permissionsNode);
        jsonOutput.set("Other", other);
        jsonOutput.set("PerPageInfo", pageInfoParent);

        final String jsonString = objectMapper.writerWithDefaultPrettyPrinter().writeValueAsString(jsonOutput);
        return WebResponseUtils.bytesToWebResponse(
                jsonString.getBytes(StandardCharsets.UTF_8),
                "response.json",
                MediaType.APPLICATION_JSON);
    } catch (Exception e) {
        log.error("exception", e);
    }
    return null;
}

public static boolean isBlankImage(BufferedImage image, int threshold, double whitePercent, int blurSize) {
    if (image == null) {
        log.info("Error: Image is null");
        return false;
    }

    int whitePixels = 0;
    final int totalPixels = image.getWidth() * image.getHeight();

    for (int i = 0; i < image.getHeight(); i++) {
        for (int j = 0; j < image.getWidth(); j++) {
            final int color = image.getRGB(j, i) & 0xFF;
            if (color >= 255 - threshold) {
                whitePixels++;
            }
        }
    }

    final double whitePixelPercentage = (whitePixels / (double) totalPixels) * 100;
    log.info(String.format("Page has white pixel percent of %.2f%%", whitePixelPercentage));

    return whitePixelPercentage >= whitePercent;
}

@PostMapping(consumes = "multipart/form-data", value = "/filter-page-count")
@Operation(
        summary = "Checks if a PDF is greater, less or equal to a setPageCount",
        description = "Input:PDF Output:Boolean Type:SISO")
public ResponseEntity<byte[]> pageCount(@ModelAttribute PDFComparisonAndCount request)
        throws IOException, InterruptedException {
    final MultipartFile inputFile = request.getFileInput();
    final int pageCount = request.getPageCount();
    final String comparator = request.getComparator();

    final PDDocument document = pdfDocumentFactory.load(inputFile);
    final int actualPageCount = document.getNumberOfPages();

    boolean valid;
    switch (comparator) {
        case "Greater":
            valid = actualPageCount > pageCount;
            break;
        case "Equal":
            valid = actualPageCount == pageCount;
            break;
        case "Less":
            valid = actualPageCount < pageCount;
            break;
        default:
            throw ExceptionUtils.createInvalidArgumentException("comparator", comparator);
    }

    if (valid) return WebResponseUtils.multiPartFileToWebResponse(inputFile);
    return null;
}

private void handleSplitBySize(PDDocument sourceDocument, long maxBytes, ZipOutputStream zipOut, String baseFilename)
        throws IOException {
    log.debug("Starting handleSplitBySize with maxBytes={}", maxBytes);

    PDDocument currentDoc = pdfDocumentFactory.createNewDocumentBasedOnOldDocument(sourceDocument);
    int fileIndex = 1;
    final int totalPages = sourceDocument.getNumberOfPages();
    int pageAdded = 0;

    final int baseCheckFrequency = 5;

    for (int pageIndex = 0; pageIndex < totalPages; pageIndex++) {
        final PDPage page = sourceDocument.getPage(pageIndex);
        log.debug("Processing page {} of {}", pageIndex + 1, totalPages);

        final PDPage newPage = new PDPage(page.getCOSObject());
        currentDoc.addPage(newPage);
        pageAdded++;

        final boolean shouldCheckSize =
                (pageAdded % baseCheckFrequency == 0) || (pageIndex == totalPages - 1) || (pageAdded >= 20);

        if (shouldCheckSize) {
            log.debug("Performing size check after {} pages", pageAdded);
            final ByteArrayOutputStream checkSizeStream = new ByteArrayOutputStream();
            currentDoc.save(checkSizeStream);
            final long actualSize = checkSizeStream.size();
            log.debug("Current document size: {} bytes (max: {} bytes)", actualSize, maxBytes);

            if (actualSize > maxBytes) {
                if (currentDoc.getNumberOfPages() > 1) {
                    currentDoc.removePage(currentDoc.getNumberOfPages() - 1);
                    pageIndex--;
                    log.debug("Size limit exceeded - removed last page");
                }

                log.debug("Saving document with {} pages as part {}", currentDoc.getNumberOfPages(), fileIndex);
                saveDocumentToZip(currentDoc, zipOut, baseFilename, fileIndex++);
                currentDoc = new PDDocument();
                pageAdded = 0;
            } else if (pageIndex < totalPages - 1) {
                if (actualSize < maxBytes * 0.75 && pageAdded > 0) {
                    final int pagesToLookAhead = Math.min(5, totalPages - pageIndex - 1);
                    if (pagesToLookAhead > 0) {
                        log.debug("Testing {} upcoming pages for potential addition", pagesToLookAhead);

                        final PDDocument testDoc = new PDDocument();
                        for (int i = 0; i < currentDoc.getNumberOfPages(); i++) {
                            testDoc.addPage(new PDPage(currentDoc.getPage(i).getCOSObject()));
                        }

                        int extraPagesAdded = 0;
                        for (int i = 0; i < pagesToLookAhead; i++) {
                            final int testPageIndex = pageIndex + 1 + i;
                            final PDPage testPage = sourceDocument.getPage(testPageIndex);
                            testDoc.addPage(new PDPage(testPage.getCOSObject()));

                            final ByteArrayOutputStream testStream = new ByteArrayOutputStream();
                            testDoc.save(testStream);
                            final long testSize = testStream.size();

                            if (testSize <= maxBytes) {
                                extraPagesAdded++;
                                log.debug("Test: Can add page {} (size would be {})", testPageIndex + 1, testSize);
                            } else {
                                log.debug("Test: Cannot add page {} (size would be {})", testPageIndex + 1, testSize);
                                break;
                            }
                        }

                        testDoc.close();

                        if (extraPagesAdded > 0) {
                            log.debug("Adding {} verified pages ahead", extraPagesAdded);
                            for (int i = 0; i < extraPagesAdded; i++) {
                                final int extraPageIndex = pageIndex + 1 + i;
                                final PDPage extraPage = sourceDocument.getPage(extraPageIndex);
                                currentDoc.addPage(new PDPage(extraPage.getCOSObject()));
                            }
                            pageIndex += extraPagesAdded;
                            pageAdded += extraPagesAdded;
                        }
                    }
                }
            }
        }
    }

    if (currentDoc.getNumberOfPages() > 0) {
        log.debug("Saving final document with {} pages as part {}", currentDoc.getNumberOfPages(), fileIndex);
        saveDocumentToZip(currentDoc, zipOut, baseFilename, fileIndex++);
    }

    log.debug("Completed handleSplitBySize with {} document parts created", fileIndex - 1);
}

public static File convertMultipartFileToFile(MultipartFile multipartFile) throws IOException {
    String customTempDir = System.getenv("STIRLING_TEMPFILES_DIRECTORY");
    if (customTempDir == null || customTempDir.isEmpty()) {
        customTempDir = System.getProperty("stirling.tempfiles.directory");
    }

    final Path tempDir = (customTempDir != null && !customTempDir.isEmpty())
            ? Path.of(customTempDir)
            : Path.of(System.getProperty("java.io.tmpdir"), "stirling-pdf");

    if (!Files.exists(tempDir)) {
        Files.createDirectories(tempDir);
    }

    final File tempFile = Files.createTempFile(tempDir, "stirling-pdf-", null).toFile();

    try (InputStream inputStream = multipartFile.getInputStream();
         FileOutputStream outputStream = new FileOutputStream(tempFile)) {

        final byte[] buffer = new byte[8192];
        int bytesRead;
        while ((bytesRead = inputStream.read(buffer)) != -1) {
            outputStream.write(buffer, 0, bytesRead);
        }
    }
    return tempFile;
}

private void cleanupTempFiles() {
    try {
        final Set<Path> files = registry.getAllRegisteredFiles();
        int deletedCount = 0;

        for (Path file : files) {
            try {
                if (Files.exists(file)) {
                    Files.deleteIfExists(file);
                    deletedCount++;
                }
            } catch (IOException e) {
                log.warn("Failed to delete temp file during shutdown: {}", file, e);
            }
        }

        final Set<Path> directories = registry.getTempDirectories();
        for (Path dir : directories) {
            try {
                if (Files.exists(dir)) {
                    GeneralUtils.deleteDirectory(dir);
                    deletedCount++;
                }
            } catch (IOException e) {
                log.warn("Failed to delete temp directory during shutdown: {}", dir, e);
            }
        }

        log.info("Shutdown cleanup complete. Deleted {} temporary files/directories", deletedCount);
        registry.clear();
    } catch (Exception e) {
        log.error("Error during shutdown cleanup", e);
    }
}

private Object convertAttrValue(String attributeName, Object originAttrValue) {
    Object attrValue = originAttrValue;
    try {
        if (originAttrValue instanceof ObjectName) {
            attrValue = String.valueOf(originAttrValue);
        } else if (attrValue instanceof CompositeData) {
            // MBean java.lang:type=MemoryPool,name=*
            CompositeData compositeData = (CompositeData) attrValue;
            attrValue = convertCompositeData(attributeName, compositeData);
        } else if (attrValue instanceof CompositeData[]) {
            // MBean com.sun.management:type=HotSpotDiagnostic
            CompositeData[] compositeDataArray = (CompositeData[]) attrValue;
            List<Map<String, Object>> dataList = new ArrayList<>(compositeDataArray.length);
            for (CompositeData compositeData : compositeDataArray) {
                dataList.add(convertCompositeData(attributeName, compositeData));
            }
            attrValue = dataList;
        } else if (attrValue instanceof TabularData) {
            // MBean java.lang:type=GarbageCollector,name=*
            TabularData tabularData = (TabularData) attrValue;
            @SuppressWarnings("unchecked")
            Collection<CompositeData> compositeDataList =
                    (Collection<CompositeData>) tabularData.values();
            List<Map<String, Object>> dataList = new ArrayList<>(compositeDataList.size());
            for (CompositeData compositeData : compositeDataList) {
                dataList.add(convertCompositeData(attributeName, compositeData));
            }
            attrValue = dataList;
        }
    } catch (Throwable e) {
        logger.error("convert mbean attribute error, attribute: {}={}", attributeName, originAttrValue, e);
        attrValue = String.valueOf(originAttrValue);
    }
    return attrValue;
}

public static int getItemCount(ResultModel model) {
    // If the model implements Countable, let it report its own size
    if (model instanceof Countable) {
        return ((Countable) model).size();
    }

    // For regular models, use reflection to count elements in container-type fields
    // Cache Field objects to minimize overhead
    Class<?> modelClass = model.getClass();
    List<Field> fields = modelFieldMap.get(modelClass.getName());
    if (fields == null) {
        fields = new ArrayList<>();
        Field[] declaredFields = modelClass.getDeclaredFields();
        for (Field field : declaredFields) {
            Class<?> fieldClass = field.getType();
            // Cache fields that are List/Map/Array/Countable for counting later
            if (Collection.class.isAssignableFrom(fieldClass)
                    || Map.class.isAssignableFrom(fieldClass)
                    || Countable.class.isAssignableFrom(fieldClass)
                    || fieldClass.isArray()) {
                field.setAccessible(true);
                fields.add(field);
            }
        }
        List<Field> oldFields = modelFieldMap.putIfAbsent(modelClass.getName(), fields);
        if (oldFields != null) {
            fields = oldFields;
        }
    }

    // Count items in the model instance
    int count = 0;
    try {
        for (Field field : fields) {
            if (!field.isAccessible()) {
                field.setAccessible(true);
            }
            Object value = field.get(model);
            if (value != null) {
                if (value instanceof Collection) {
                    count += ((Collection<?>) value).size();
                } else if (value.getClass().isArray()) {
                    count += Array.getLength(value);
                } else if (value instanceof Map) {
                    count += ((Map<?, ?>) value).size();
                } else if (value instanceof Countable) {
                    count += ((Countable) value).size();
                }
            }
        }
    } catch (Exception e) {
        logger.error("get item count of result model failed, model: {}", JSON.toJSONString(model), e);
    }

    return count > 0 ? count : 1;
}

public void evictSessions() {
    long now = System.currentTimeMillis();
    List<Session> toClose = new ArrayList<>();
    for (Session session : sessions.values()) {
        // Do not close if there is still a job running (e.g., trace command may wait a long time)
        // TODO check background job size
        if (now - session.getLastAccessTime() > sessionTimeoutMillis
                && session.getForegroundJob() == null) {
            toClose.add(session);
        }
        evictConsumers(session);
    }
    for (Session session : toClose) {
        // Interrupt foreground job
        Job job = session.getForegroundJob();
        if (job != null) {
            job.interrupt();
        }
        long timeOutInMinutes = sessionTimeoutMillis / 1000 / 60;
        String reason = String.format("session is inactive for %d min(s).", timeOutInMinutes);
        SharingResultDistributor resultDistributor = session.getResultDistributor();
        if (resultDistributor != null) {
            resultDistributor.appendResult(new MessageModel(reason));
        }
        this.removeSession(session.getSessionId());
        logger.info("Removing inactive session: {}, last access time: {}", session.getSessionId(), session.getLastAccessTime());
    }
}

private TableElement drawTomcatInfo(TomcatInfoVO tomcatInfo) {
    if (tomcatInfo == null) {
        return null;
    }

    // Header
    TableElement table = new TableElement(1, 1).rightCellPadding(1);
    table.add(
            new RowElement()
                    .style(Decoration.bold.fg(Color.black).bg(Color.white))
                    .add("Tomcat", ""));

    if (tomcatInfo.getConnectorStats() != null) {
        for (TomcatInfoVO.ConnectorStats connectorStat : tomcatInfo.getConnectorStats()) {
            table.add(new RowElement().style(Decoration.bold.bold()).add("connector", connectorStat.getName()));
            table.row("QPS", String.format("%.2f", connectorStat.getQps()));
            table.row("RT(ms)", String.format("%.2f", connectorStat.getRt()));
            table.row("error/s", String.format("%.2f", connectorStat.getError()));
            table.row("received/s", formatBytes(connectorStat.getReceived()));
            table.row("sent/s", formatBytes(connectorStat.getSent()));
        }
    }

    if (tomcatInfo.getThreadPools() != null) {
        for (TomcatInfoVO.ThreadPool threadPool : tomcatInfo.getThreadPools()) {
            table.add(new RowElement().style(Decoration.bold.bold()).add("threadpool", threadPool.getName()));
            table.row("busy", String.valueOf(threadPool.getBusy()));
            table.row("total", String.valueOf(threadPool.getTotal()));
        }
    }
    return table;
}

private ApiResponse processExecRequest(ApiRequest apiRequest, Session session) {
    boolean oneTimeAccess = session.get(ONETIME_SESSION_KEY) != null;

    try {
        String commandLine = apiRequest.getCommand();
        Map<String, Object> body = new TreeMap<>();
        body.put("command", commandLine);

        ApiResponse response = new ApiResponse()
                .setSessionId(session.getSessionId())
                .setBody(body);

        if (!session.tryLock()) {
            response.setState(ApiState.REFUSED).setMessage("Another command is executing.");
            return response;
        }

        int lock = session.getLock();
        PackingResultDistributor packingResultDistributor = null;
        Job job = null;
        try {
            Job foregroundJob = session.getForegroundJob();
            if (foregroundJob != null) {
                response.setState(ApiState.REFUSED).setMessage("Another job is running.");
                logger.info("Another job is running, jobId: {}", foregroundJob.id());
                return response;
            }

            packingResultDistributor = new PackingResultDistributorImpl(session);
            // Distribute result message both to origin session channel and request channel
            // ResultDistributor resultDistributor = new CompositeResultDistributorImpl(packingResultDistributor, session.getResultDistributor());
            job = this.createJob(commandLine, session, packingResultDistributor);
            session.setForegroundJob(job);
            updateSessionInputStatus(session, InputStatus.ALLOW_INTERRUPT);

            job.run();

        } catch (Throwable e) {
            logger.error("Exec command failed:" + e.getMessage() + ", command:" + commandLine, e);
            response.setState(ApiState.FAILED).setMessage("Exec command failed:" + e.getMessage());
            return response;
        } finally {
            if (session.getLock() == lock) {
                session.unLock();
            }
        }

        // Wait for job completed or timeout
        Integer timeout = apiRequest.getExecTimeout();
        if (timeout == null || timeout <= 0) {
            timeout = DEFAULT_EXEC_TIMEOUT;
        }
        boolean timeExpired = !waitForJob(job, timeout);
        if (timeExpired) {
            logger.warn("Job is exceeded time limit, force interrupt it, jobId: {}", job.id());
            job.interrupt();
            response.setState(ApiState.INTERRUPTED).setMessage("The job is exceeded time limit, force interrupt");
        } else {
            response.setState(ApiState.SUCCEEDED);
        }

        // Package results
        body.put("jobId", job.id());
        body.put("jobStatus", job.status());
        body.put("timeExpired", timeExpired);
        if (timeExpired) {
            body.put("timeout", timeout);
        }
        body.put("results", packingResultDistributor.getResults());

        return response.setSessionId(session.getSessionId()).setBody(body);
    } finally {
        if (oneTimeAccess) {
            sessionManager.removeSession(session.getSessionId());
        }
    }
}

private String renderLoggerInfo(Map<String, Map<String, Object>> loggerInfos, int width) {
    StringBuilder sb = new StringBuilder(8192);

    for (Map.Entry<String, Map<String, Object>> entry : loggerInfos.entrySet()) {
        Map<String, Object> info = entry.getValue();

        TableElement table = new TableElement(2, 10).leftCellPadding(1).rightCellPadding(1);
        TableElement appendersTable = new TableElement().rightCellPadding(1);

        Class<?> clazz = (Class<?>) info.get(LoggerHelper.clazz);
        table.row(label(LoggerHelper.name).style(Decoration.bold.bold()), label(String.valueOf(info.get(LoggerHelper.name))))
                .row(label(LoggerHelper.clazz).style(Decoration.bold.bold()), label(clazz.getName()))
                .row(label(LoggerHelper.classLoader).style(Decoration.bold.bold()), label(String.valueOf(info.get(LoggerHelper.classLoader))))
                .row(label(LoggerHelper.classLoaderHash).style(Decoration.bold.bold()), label(String.valueOf(info.get(LoggerHelper.classLoaderHash))))
                .row(label(LoggerHelper.level).style(Decoration.bold.bold()), label(String.valueOf(info.get(LoggerHelper.level))));
        if (info.get(LoggerHelper.effectiveLevel) != null) {
            table.row(label(LoggerHelper.effectiveLevel).style(Decoration.bold.bold()),
                    label(String.valueOf(info.get(LoggerHelper.effectiveLevel))));
        }

        if (info.get(LoggerHelper.config) != null) {
            table.row(label(LoggerHelper.config).style(Decoration.bold.bold()),
                    label(String.valueOf(info.get(LoggerHelper.config))));
        }

        table.row(label(LoggerHelper.additivity).style(Decoration.bold.bold()),
                        label(String.valueOf(info.get(LoggerHelper.additivity))))
                .row(label(LoggerHelper.codeSource).style(Decoration.bold.bold()),
                        label(String.valueOf(info.get(LoggerHelper.codeSource))));

        @SuppressWarnings("unchecked")
        List<Map<String, Object>> appenders =
                (List<Map<String, Object>>) info.get(LoggerHelper.appenders);
        if (appenders != null && !appenders.isEmpty()) {

            for (Map<String, Object> appenderInfo : appenders) {
                Class<?> appenderClass = (Class<?>) appenderInfo.get(LoggerHelper.clazz);

                appendersTable.row(label(LoggerHelper.name).style(Decoration.bold.bold()),
                                label(String.valueOf(appenderInfo.get(LoggerHelper.name))));
                appendersTable.row(label(LoggerHelper.clazz), label(appenderClass.getName()));
                appendersTable.row(label(LoggerHelper.classLoader), label(String.valueOf(info.get(LoggerHelper.classLoader))));
                appendersTable.row(label(LoggerHelper.classLoaderHash), label(String.valueOf(info.get(LoggerHelper.classLoaderHash))));
                if (appenderInfo.get(LoggerHelper.file) != null) {
                    appendersTable.row(label(LoggerHelper.file), label(String.valueOf(appenderInfo.get(LoggerHelper.file))));
                }
                if (appenderInfo.get(LoggerHelper.target) != null) {
                    appendersTable.row(label(LoggerHelper.target), label(String.valueOf(appenderInfo.get(LoggerHelper.target))));
                }
                if (appenderInfo.get(LoggerHelper.blocking) != null) {
                    appendersTable.row(label(LoggerHelper.blocking), label(String.valueOf(appenderInfo.get(LoggerHelper.blocking))));
                }
                if (appenderInfo.get(LoggerHelper.appenderRef) != null) {
                    appendersTable.row(label(LoggerHelper.appenderRef), label(String.valueOf(appenderInfo.get(LoggerHelper.appenderRef))));
                }
            }

            table.row(label("appenders").style(Decoration.bold.bold()), appendersTable);
        }

        sb.append(RenderUtil.render(table, width)).append('\n');
    }
    return sb.toString();
}

private void renderNode(StringBuilder sb, TraceNode node, Ansi highlighted) {
    // Render cost: [0.366865ms]
    if (isPrintCost && node instanceof MethodNode) {
        MethodNode methodNode = (MethodNode) node;

        String costStr = renderCost(methodNode);
        if (node == maxCostNode) {
            // The node with max cost will be highlighted
            sb.append(highlighted.a(costStr).reset().toString());
        } else {
            sb.append(costStr);
        }
    }

    // Render method name
    if (node instanceof MethodNode) {
        MethodNode methodNode = (MethodNode) node;
        // clazz.getName() + ":" + method.getName() + "()"
        sb.append(methodNode.getClassName()).append(":").append(methodNode.getMethodName()).append("()");
        // #lineNumber
        if (methodNode.getLineNumber() != -1) {
            sb.append(" #").append(methodNode.getLineNumber());
        }
    } else if (node instanceof ThreadNode) {
        // Render thread info
        ThreadNode threadNode = (ThreadNode) node;
        // ts=2020-04-29 10:34:00;thread_name=main;id=1;is_daemon=false;priority=5;TCCL=sun.misc.Launcher$AppClassLoader@18b4aac2
        sb.append(format("ts=%s;thread_name=%s;id=%d;is_daemon=%s;priority=%d;TCCL=%s",
                DateUtils.formatDateTime(threadNode.getTimestamp()),
                threadNode.getThreadName(),
                threadNode.getThreadId(),
                threadNode.isDaemon(),
                threadNode.getPriority(),
                threadNode.getClassloader()));

        // trace_id
        if (threadNode.getTraceId() != null) {
            sb.append(";trace_id=").append(threadNode.getTraceId());
        }
        if (threadNode.getRpcId() != null) {
            sb.append(";rpc_id=").append(threadNode.getRpcId());
        }
    } else if (node instanceof ThrowNode) {
        ThrowNode throwNode = (ThrowNode) node;
        sb.append("throw:").append(throwNode.getException())
                .append(" #").append(throwNode.getLineNumber())
                .append(" [").append(throwNode.getMessage()).append("]");
    } else {
        throw new UnsupportedOperationException("unknown trace node: " + node.getClass());
    }
}

private static String processInlineImages(final String htmlContent, final EmailContent emailContent) {
    if (htmlContent == null || emailContent == null) {
        return htmlContent;
    }

    // Create a map of Content-ID to attachment data
    final Map<String, EmailAttachment> contentIdMap = new HashMap<>();
    for (final EmailAttachment attachment : emailContent.getAttachments()) {
        if (attachment.isEmbedded()
                && attachment.getContentId() != null
                && attachment.getData() != null) {
            contentIdMap.put(attachment.getContentId(), attachment);
        }
    }

    if (contentIdMap.isEmpty()) {
        return htmlContent;
    }

    // Pattern to match cid: references in img src attributes
    final Pattern cidPattern = Pattern.compile(
            "(?i)<img[^>]*\\ssrc\\s*=\\s*['\"]cid:([^'\"]+)['\"][^>]*>",
            Pattern.CASE_INSENSITIVE);
    final Matcher matcher = cidPattern.matcher(htmlContent);

    final StringBuffer result = new StringBuffer();
    while (matcher.find()) {
        final String contentId = matcher.group(1);
        final EmailAttachment attachment = contentIdMap.get(contentId);

        if (attachment != null && attachment.getData() != null) {
            // Convert to data URI
            String mimeType = attachment.getContentType();
            if (mimeType == null || mimeType.isEmpty()) {
                // Try to determine MIME type from filename
                final String filename = attachment.getFilename();
                if (filename != null) {
                    final String filenameLower = filename.toLowerCase();
                    if (filenameLower.endsWith(".png")) {
                        mimeType = "image/png";
                    } else if (filenameLower.endsWith(".jpg") || filenameLower.endsWith(".jpeg")) {
                        mimeType = "image/jpeg";
                    } else if (filenameLower.endsWith(".gif")) {
                        mimeType = "image/gif";
                    } else if (filenameLower.endsWith(".bmp")) {
                        mimeType = "image/bmp";
                    } else {
                        mimeType = "image/png"; // fallback
                    }
                } else {
                    mimeType = "image/png"; // fallback
                }
            }

            final String base64Data = Base64.getEncoder().encodeToString(attachment.getData());
            final String dataUri = "data:" + mimeType + ";base64," + base64Data;

            // Replace the cid: reference with the data URI
            final String replacement = matcher.group(0)
                    .replaceFirst("cid:" + Pattern.quote(contentId), dataUri);
            matcher.appendReplacement(result, Matcher.quoteReplacement(replacement));
        } else {
            // Keep original if attachment not found
            matcher.appendReplacement(result, Matcher.quoteReplacement(matcher.group(0)));
        }
    }
    matcher.appendTail(result);

    return result.toString();
}

private static String generateEnhancedEmailHtml(final EmailContent content, final EmlToPdfRequest request) {
    final StringBuilder html = new StringBuilder();

    html.append("<!DOCTYPE html>\n");
    html.append("<html><head><meta charset=\"UTF-8\">\n");
    html.append("<title>").append(escapeHtml(content.getSubject())).append("</title>\n");
    html.append("<style>\n");
    appendEnhancedStyles(html);
    html.append("</style>\n");
    html.append("</head><body>\n");

    html.append("<div class=\"email-container\">\n");
    html.append("<div class=\"email-header\">\n");
    html.append("<h1>").append(escapeHtml(content.getSubject())).append("</h1>\n");
    html.append("<div class=\"email-meta\">\n");
    html.append("<div><strong>From:</strong> ")
            .append(escapeHtml(content.getFrom()))
            .append("</div>\n");
    html.append("<div><strong>To:</strong> ")
            .append(escapeHtml(content.getTo()))
            .append("</div>\n");

    if (content.getDate() != null) {
        html.append("<div><strong>Date:</strong> ")
                .append(formatEmailDate(content.getDate()))
                .append("</div>\n");
    }
    html.append("</div></div>\n");

    html.append("<div class=\"email-body\">\n");
    if (content.getHtmlBody() != null && !content.getHtmlBody().trim().isEmpty()) {
        html.append(processEmailHtmlBody(content.getHtmlBody(), content));
    } else if (content.getTextBody() != null && !content.getTextBody().trim().isEmpty()) {
        html.append("<div class=\"text-body\">");
        html.append(convertTextToHtml(content.getTextBody()));
        html.append("</div>");
    } else {
        html.append("<div class=\"no-content\">");
        html.append("<p><em>No content available</em></p>");
        html.append("</div>");
    }
    html.append("</div>\n");

    if (content.getAttachmentCount() > 0 || !content.getAttachments().isEmpty()) {
        html.append("<div class=\"attachment-section\">\n");
        final int displayedAttachmentCount =
                content.getAttachmentCount() > 0
                        ? content.getAttachmentCount()
                        : content.getAttachments().size();
        html.append("<h3>Attachments (").append(displayedAttachmentCount).append(")</h3>\n");

        if (!content.getAttachments().isEmpty()) {
            for (final EmailAttachment attachment : content.getAttachments()) {
                // Create attachment info with paperclip emoji before filename
                final String uniqueId = generateUniqueAttachmentId(attachment.getFilename());
                attachment.setEmbeddedFilename(
                        attachment.getEmbeddedFilename() != null
                                ? attachment.getEmbeddedFilename()
                                : attachment.getFilename());

                html.append("<div class=\"attachment-item\" id=\"")
                        .append(uniqueId)
                        .append("\">")
                        .append("<span class=\"attachment-icon\">")
                        .append(MimeConstants.ATTACHMENT_MARKER)
                        .append("</span> ")
                        .append("<span class=\"attachment-name\">")
                        .append(escapeHtml(safeMimeDecode(attachment.getFilename())))
                        .append("</span>");

                final String sizeStr = formatFileSize(attachment.getSizeBytes());
                html.append(" <span class=\"attachment-details\">(").append(sizeStr);
                if (attachment.getContentType() != null && !attachment.getContentType().isEmpty()) {
                    html.append(", ").append(escapeHtml(attachment.getContentType()));
                }
                html.append(")</span></div>\n");
            }
        }

        if (request.isIncludeAttachments()) {
            html.append("<div class=\"attachment-info-note\">\n");
            html.append("<p><em>Attachments are embedded in the file.</em></p>\n");
            html.append("</div>\n");
        } else {
            html.append("<div class=\"attachment-info-note\">\n");
            html.append("<p><em>Attachment information displayed - files not included in PDF.</em></p>\n");
            html.append("</div>\n");
        }

        html.append("</div>\n");
    }

    html.append("</div>\n");
    html.append("</body></html>");

    return html.toString();
}

