private static EmailContent extractEmailContentAdvanced(final Object message, final EmlToPdfRequest request) {
    final EmailContent content = new EmailContent();

    try {
        final Class<?> messageClass = message.getClass();

        // Extract headers via reflection
        final Method getSubject = messageClass.getMethod("getSubject");
        final String subject = (String) getSubject.invoke(message);
        content.setSubject(subject != null ? safeMimeDecode(subject) : "No Subject");

        final Method getFrom = messageClass.getMethod("getFrom");
        final Object[] fromAddresses = (Object[]) getFrom.invoke(message);
        content.setFrom(fromAddresses != null && fromAddresses.length > 0
                ? safeMimeDecode(fromAddresses[0].toString()) : "");

        final Method getAllRecipients = messageClass.getMethod("getAllRecipients");
        final Object[] recipients = (Object[]) getAllRecipients.invoke(message);
        content.setTo(recipients != null && recipients.length > 0
                ? safeMimeDecode(recipients[0].toString()) : "");

        final Method getSentDate = messageClass.getMethod("getSentDate");
        content.setDate((Date) getSentDate.invoke(message));

        // Extract content
        final Method getContent = messageClass.getMethod("getContent");
        final Object messageContent = getContent.invoke(message);

        if (messageContent instanceof final String stringContent) {
            final Method getContentType = messageClass.getMethod("getContentType");
            final String contentType = (String) getContentType.invoke(message);
            if (contentType != null && contentType.toLowerCase().contains("text/html")) {
                content.setHtmlBody(stringContent);
            } else {
                content.setTextBody(stringContent);
            }
        } else {
            // Handle multipart content
            try {
                final Class<?> multipartClass = Class.forName("jakarta.mail.Multipart");
                if (multipartClass.isInstance(messageContent)) {
                    processMultipartAdvanced(messageContent, content, request);
                }
            } catch (final Exception e) {
                log.warn("Error processing content: {}", e.getMessage());
            }
        }

    } catch (final Exception e) {
        content.setSubject("Email Conversion");
        content.setFrom("Unknown");
        content.setTo("Unknown");
        content.setTextBody("Email content could not be parsed with advanced processing");
    }

    return content;
}

public static RequestCreator loadScaledDownThumbnail(final Context context,
                                                     @NonNull final List<Image> images) {
    // scale down the notification thumbnail for performance
    return PicassoHelper.loadThumbnail(images)
            .transform(new Transformation() {
                @Override
                public Bitmap transform(final Bitmap source) {
                    if (DEBUG) {
                        Log.d(TAG, "Thumbnail - transform() called");
                    }

                    final float maxWidth = context.getResources()
                            .getDimension(R.dimen.player_notification_thumbnail_width);
                    final float targetWidth = Math.min(maxWidth, source.getWidth());
                    final int width = (int) targetWidth;
                    final int height = (int) (source.getHeight() / (source.getWidth() / targetWidth));

                    final Bitmap result = BitmapCompat.createScaledBitmap(
                            source, width, height, null, true);

                    if (result == source || !result.isMutable()) {
                        // create a new mutable bitmap to prevent strange crashes on some devices (see #4638)
                        final float altWidth = targetWidth - 1f;
                        final int width2 = (int) altWidth;
                        final int height2 = (int) (source.getHeight()
                                / (source.getWidth() / altWidth));
                        final Bitmap copied = BitmapCompat.createScaledBitmap(
                                source, width2, height2, null, true);
                        source.recycle();
                        return copied;
                    }

                    source.recycle();
                    return result;
                }

                @Override
                public String key() {
                    return PLAYER_THUMBNAIL_TRANSFORMATION_KEY;
                }
            });
}

private void buildQualityMenu() {
    if (qualityPopupMenu == null) {
        return;
    }
    qualityPopupMenu.getMenu().removeGroup(POPUP_MENU_ID_QUALITY);

    final List<VideoStream> availableStreams = Optional.ofNullable(player.getCurrentMetadata())
            .flatMap(MediaItemTag::getMaybeQuality)
            .map(MediaItemTag.Quality::getSortedVideoStreams)
            .orElse(null);
    if (availableStreams == null) {
        return;
    }

    for (int i = 0; i < availableStreams.size(); i++) {
        final VideoStream videoStream = availableStreams.get(i);
        qualityPopupMenu.getMenu().add(
                POPUP_MENU_ID_QUALITY,
                i,
                Menu.NONE,
                MediaFormat.getNameById(videoStream.getFormatId()) + " " + videoStream.getResolution()
        );
    }
    qualityPopupMenu.setOnMenuItemClickListener(this);
    qualityPopupMenu.setOnDismissListener(this);

    player.getSelectedVideoStream()
            .ifPresent(s -> binding.qualityTextView.setText(s.getResolution()));
}

private void buildCaptionMenu(@NonNull final List<String> availableLanguages) {
    if (captionPopupMenu == null) {
        return;
    }
    captionPopupMenu.getMenu().removeGroup(POPUP_MENU_ID_CAPTION);

    captionPopupMenu.setOnDismissListener(this);

    // add option for turning off caption
    final MenuItem captionOffItem = captionPopupMenu.getMenu().add(
            POPUP_MENU_ID_CAPTION, 0, Menu.NONE, R.string.caption_none);
    captionOffItem.setOnMenuItemClickListener(menuItem -> {
        final int textRendererIndex = player.getCaptionRendererIndex();
        if (textRendererIndex != RENDERER_UNAVAILABLE) {
            player.getTrackSelector().setParameters(
                    player.getTrackSelector()
                            .buildUponParameters()
                            .setRendererDisabled(textRendererIndex, true)
            );
        }
        player.getPrefs().edit()
                .remove(context.getString(R.string.caption_user_set_key))
                .apply();
        return true;
    });

    // add all available captions
    for (int i = 0; i < availableLanguages.size(); i++) {
        final String captionLanguage = availableLanguages.get(i);
        final MenuItem captionItem = captionPopupMenu.getMenu().add(
                POPUP_MENU_ID_CAPTION, i + 1, Menu.NONE, captionLanguage);
        captionItem.setOnMenuItemClickListener(menuItem -> {
            final int textRendererIndex = player.getCaptionRendererIndex();
            if (textRendererIndex != RENDERER_UNAVAILABLE) {
                // DefaultTrackSelector selection order:
                // 1. ANY track exactly matching preferred language name
                // 2. ANY track exactly matching preferred language stem
                // 3. ROLE_FLAG_CAPTION track matching preferred language stem
                // 4. ROLE_FLAG_DESCRIBES_MUSIC_AND_SOUND track matching preferred language stem
                player.getTrackSelector().setParameters(
                        player.getTrackSelector()
                                .buildUponParameters()
                                .setPreferredTextLanguages(
                                        captionLanguage,
                                        PlayerHelper.captionLanguageStemOf(captionLanguage)
                                )
                                .setPreferredTextRoleFlags(C.ROLE_FLAG_CAPTION)
                                .setRendererDisabled(textRendererIndex, false)
                );
                player.getPrefs().edit()
                        .putString(context.getString(R.string.caption_user_set_key), captionLanguage)
                        .apply();
            }
            return true;
        });
    }
    captionPopupMenu.setOnDismissListener(this);

    // apply caption language from previous user preference
    final int textRendererIndex = player.getCaptionRendererIndex();
    if (textRendererIndex == RENDERER_UNAVAILABLE) {
        return;
    }

    // if user prefers to show no caption, then disable the renderer
    final String userPreferredLanguage =
            player.getPrefs().getString(context.getString(R.string.caption_user_set_key), null);
    if (userPreferredLanguage == null) {
        player.getTrackSelector().setParameters(
                player.getTrackSelector().buildUponParameters()
                        .setRendererDisabled(textRendererIndex, true)
        );
        return;
    }

    // only set preferred language if it does not match the user preference
    final List<String> selectedPreferredLanguages =
            player.getTrackSelector().getParameters().preferredTextLanguages;
    if (!selectedPreferredLanguages.contains(userPreferredLanguage)) {
        player.getTrackSelector().setParameters(
                player.getTrackSelector().buildUponParameters()
                        .setPreferredTextLanguages(
                                userPreferredLanguage,
                                PlayerHelper.captionLanguageStemOf(userPreferredLanguage)
                        )
                        .setPreferredTextRoleFlags(C.ROLE_FLAG_CAPTION)
                        .setRendererDisabled(textRendererIndex, false)
        );
    }
}

@SuppressLint("PrivateResource") // we currently use Exoplayer's internal strings and icons
@Nullable
public static NotificationActionData fromNotificationActionEnum(
        @NonNull final Player player,
        @NotificationConstants.Action final int selectedAction
) {
    final int baseActionIcon = NotificationConstants.ACTION_ICONS[selectedAction];
    final Context ctx = player.getContext();

    switch (selectedAction) {
        case NotificationConstants.PREVIOUS:
            return new NotificationActionData(
                    ACTION_PLAY_PREVIOUS,
                    ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_previous_description),
                    baseActionIcon
            );

        case NotificationConstants.NEXT:
            return new NotificationActionData(
                    ACTION_PLAY_NEXT,
                    ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_next_description),
                    baseActionIcon
            );

        case NotificationConstants.REWIND:
            return new NotificationActionData(
                    ACTION_FAST_REWIND,
                    ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_rewind_description),
                    baseActionIcon
            );

        case NotificationConstants.FORWARD:
            return new NotificationActionData(
                    ACTION_FAST_FORWARD,
                    ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_fastforward_description),
                    baseActionIcon
            );

        case NotificationConstants.SMART_REWIND_PREVIOUS:
            if (player.getPlayQueue() != null && player.getPlayQueue().size() > 1) {
                return new NotificationActionData(
                        ACTION_PLAY_PREVIOUS,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_previous_description),
                        com.google.android.exoplayer2.ui.R.drawable.exo_notification_previous
                );
            }
            return new NotificationActionData(
                    ACTION_FAST_REWIND,
                    ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_rewind_description),
                    com.google.android.exoplayer2.ui.R.drawable.exo_controls_rewind
            );

        case NotificationConstants.SMART_FORWARD_NEXT:
            if (player.getPlayQueue() != null && player.getPlayQueue().size() > 1) {
                return new NotificationActionData(
                        ACTION_PLAY_NEXT,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_next_description),
                        com.google.android.exoplayer2.ui.R.drawable.exo_notification_next
                );
            }
            return new NotificationActionData(
                    ACTION_FAST_FORWARD,
                    ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_fastforward_description),
                    com.google.android.exoplayer2.ui.R.drawable.exo_controls_fastforward
            );

        case NotificationConstants.PLAY_PAUSE_BUFFERING:
            if (player.getCurrentState() == Player.STATE_PREFLIGHT
                    || player.getCurrentState() == Player.STATE_BLOCKED
                    || player.getCurrentState() == Player.STATE_BUFFERING) {
                return new NotificationActionData(
                        ACTION_PLAY_PAUSE,
                        ctx.getString(R.string.notification_action_buffering),
                        R.drawable.ic_hourglass_top
                );
            }
            // fallthrough
        case NotificationConstants.PLAY_PAUSE:
            if (player.getCurrentState() == Player.STATE_COMPLETED) {
                return new NotificationActionData(
                        ACTION_PLAY_PAUSE,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_pause_description),
                        R.drawable.ic_replay
                );
            }
            if (player.isPlaying()
                    || player.getCurrentState() == Player.STATE_PREFLIGHT
                    || player.getCurrentState() == Player.STATE_BLOCKED
                    || player.getCurrentState() == Player.STATE_BUFFERING) {
                return new NotificationActionData(
                        ACTION_PLAY_PAUSE,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_pause_description),
                        com.google.android.exoplayer2.ui.R.drawable.exo_notification_pause
                );
            }
            return new NotificationActionData(
                    ACTION_PLAY_PAUSE,
                    ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_play_description),
                    com.google.android.exoplayer2.ui.R.drawable.exo_notification_play
            );

        case NotificationConstants.REPEAT:
            if (player.getRepeatMode() == REPEAT_MODE_ALL) {
                return new NotificationActionData(
                        ACTION_REPEAT,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_repeat_all_description),
                        com.google.android.exoplayer2.ext.mediasession.R.drawable.exo_media_action_repeat_all
                );
            }
            if (player.getRepeatMode() == REPEAT_MODE_ONE) {
                return new NotificationActionData(
                        ACTION_REPEAT,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_repeat_one_description),
                        com.google.android.exoplayer2.ext.mediasession.R.drawable.exo_media_action_repeat_one
                );
            }
            return new NotificationActionData(
                    ACTION_REPEAT,
                    ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_repeat_off_description),
                    com.google.android.exoplayer2.ext.mediasession.R.drawable.exo_media_action_repeat_off
            );

        case NotificationConstants.SHUFFLE:
            if (player.getPlayQueue() != null && player.getPlayQueue().isShuffled()) {
                return new NotificationActionData(
                        ACTION_SHUFFLE,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_shuffle_on_description),
                        com.google.android.exoplayer2.ui.R.drawable.exo_controls_shuffle_on
                );
            }
            return new NotificationActionData(
                    ACTION_SHUFFLE,
                    ctx.getString(com.google.android.exoplayer2.ui.R.string.exo_controls_shuffle_off_description),
                    com.google.android.exoplayer2.ui.R.drawable.exo_controls_shuffle_off
            );

        case NotificationConstants.CLOSE:
            return new NotificationActionData(
                    ACTION_CLOSE,
                    ctx.getString(R.string.close),
                    R.drawable.ic_close
            );

        case NotificationConstants.NOTHING:
        default:
            // do nothing
            return null;
    }
}

private void handleCookiesFromUrl(@Nullable final String url) {
    if (MainActivity.DEBUG) {
        Log.d(TAG, "handleCookiesFromUrl: url=" + (url == null ? "null" : url));
    }
    if (url == null) {
        return;
    }

    final String cookies = CookieManager.getInstance().getCookie(url);
    handleCookies(cookies);

    // sometimes cookies are inside the url
    final int abuseStart = url.indexOf("google_abuse=");
    if (abuseStart != -1) {
        final int abuseEnd = url.indexOf("+path");
        try {
            handleCookies(Utils.decodeUrlUtf8(url.substring(abuseStart + 13, abuseEnd)));
        } catch (final StringIndexOutOfBoundsException e) {
            if (MainActivity.DEBUG) {
                Log.e(TAG, "handleCookiesFromUrl: invalid google abuse starting at "
                        + abuseStart + " and ending at " + abuseEnd + " for url " + url, e);
            }
        }
    }
}

public void toggleFullscreen() {
    if (DEBUG) {
        Log.d(TAG, "toggleFullscreen() called");
    }

    final PlayerServiceEventListener fragmentListener =
            player.getFragmentListener().orElse(null);
    if (fragmentListener == null || player.exoPlayerIsNull()) {
        return;
    }

    isFullscreen = !isFullscreen;
    if (isFullscreen) {
        // Android needs tens milliseconds to send new insets; hide controls to avoid visual jump
        hideControls(0, 0);
    } else {
        // apply window insets because Android will not do it when rotating to portrait
        binding.playbackControlRoot.setPadding(0, 0, 0, 0);
    }
    fragmentListener.onFullscreenStateChanged(isFullscreen);

    final int visible = isFullscreen ? View.VISIBLE : View.GONE;
    final int gone = isFullscreen ? View.GONE : View.VISIBLE;
    binding.titleTextView.setVisibility(visible);
    binding.channelTextView.setVisibility(visible);
    binding.playerCloseButton.setVisibility(gone);
    setupScreenRotationButton();
}

private void initPlayer(final boolean playOnReady) {
    if (DEBUG) {
        Log.d(TAG, "initPlayer() called with: playOnReady = [" + playOnReady + "]");
    }

    simpleExoPlayer = new ExoPlayer.Builder(context, renderFactory)
            .setTrackSelector(trackSelector)
            .setLoadControl(loadController)
            .setUsePlatformDiagnostics(false)
            .build();
    simpleExoPlayer.addListener(this);
    simpleExoPlayer.setPlayWhenReady(playOnReady);
    simpleExoPlayer.setSeekParameters(PlayerHelper.getSeekParameters(context));
    simpleExoPlayer.setWakeMode(C.WAKE_MODE_NETWORK);
    simpleExoPlayer.setHandleAudioBecomingNoisy(true);

    audioReactor = new AudioReactor(context, simpleExoPlayer);

    registerBroadcastReceiver();

    // setup UIs
    UIs.call(PlayerUi::initPlayer);

    // disable media tunneling if requested by the user from ExoPlayer settings
    final boolean disableTunneling = PreferenceManager.getDefaultSharedPreferences(context)
            .getBoolean(context.getString(R.string.disable_media_tunneling_key), false);
    if (!disableTunneling) {
        trackSelector.setParameters(
                trackSelector.buildUponParameters().setTunnelingEnabled(true)
        );
    }
}

private MediaMetadataCompat buildMediaMetadata() {
    if (DEBUG) {
        Log.d(TAG, "buildMediaMetadata called");
    }

    // set title and artist
    final MediaMetadataCompat.Builder builder = new MediaMetadataCompat.Builder()
            .putString(MediaMetadataCompat.METADATA_KEY_TITLE, player.getVideoTitle())
            .putString(MediaMetadataCompat.METADATA_KEY_ARTIST, player.getUploaderName());

    // set duration (-1 for livestreams or if unknown)
    final long duration = player.getCurrentStreamInfo()
            .filter(info -> !StreamTypeUtil.isLiveStream(info.getStreamType()))
            .map(info -> info.getDuration() * 1000L)
            .orElse(-1L);
    builder.putLong(MediaMetadataCompat.METADATA_KEY_DURATION, duration);

    // set album art, unless the user asked not to, or there is no thumbnail available
    final boolean showThumbnail =
            player.getPrefs().getBoolean(context.getString(R.string.show_thumbnail_key), true);
    Optional.ofNullable(player.getThumbnail())
            .filter(bitmap -> showThumbnail)
            .ifPresent(bitmap -> {
                builder.putBitmap(MediaMetadataCompat.METADATA_KEY_ALBUM_ART, bitmap);
                builder.putBitmap(MediaMetadataCompat.METADATA_KEY_DISPLAY_ICON, bitmap);
            });

    return builder.build();
}

public MediaDescriptionCompat getQueueMetadata(final int index) {
    if (player.getPlayQueue() == null) {
        return null;
    }
    final PlayQueueItem item = player.getPlayQueue().getItem(index);
    if (item == null) {
        return null;
    }

    final MediaDescriptionCompat.Builder descBuilder = new MediaDescriptionCompat.Builder()
            .setMediaId(String.valueOf(index))
            .setTitle(item.getTitle())
            .setSubtitle(item.getUploader());

    // set additional metadata for A2DP/AVRCP (audio/video Bluetooth profiles)
    final Bundle additionalMetadata = new Bundle();
    additionalMetadata.putString(MediaMetadataCompat.METADATA_KEY_TITLE, item.getTitle());
    additionalMetadata.putString(MediaMetadataCompat.METADATA_KEY_ARTIST, item.getUploader());
    additionalMetadata.putLong(MediaMetadataCompat.METADATA_KEY_DURATION, item.getDuration() * 1000);
    additionalMetadata.putLong(MediaMetadataCompat.METADATA_KEY_TRACK_NUMBER, index + 1L);
    additionalMetadata.putLong(
            MediaMetadataCompat.METADATA_KEY_NUM_TRACKS, player.getPlayQueue().size());
    descBuilder.setExtras(additionalMetadata);

    try {
        descBuilder.setIconUri(Uri.parse(
                ImageStrategy.choosePreferredImage(item.getThumbnails())));
    } catch (final Throwable e) {
        // no thumbnail available at all, or the user disabled image loading,
        // or the obtained url is not a valid `Uri`
    }

    return descBuilder.build();
}

private synchronized NotificationCompat.Builder createNotification() {
    if (DEBUG) {
        Log.d(TAG, "createNotification()");
    }
    notificationManager = NotificationManagerCompat.from(player.getContext());
    final NotificationCompat.Builder builder = new NotificationCompat.Builder(
            player.getContext(),
            player.getContext().getString(R.string.notification_channel_id)
    );
    final MediaStyle mediaStyle = new MediaStyle();

    // setup media style (compact notification slots and media session)
    if (Build.VERSION.SDK_INT < Build.VERSION_CODES.TIRAMISU) {
        // notification actions are ignored on Android 13+, replaced by code in MediaSessionPlayerUi
        final int[] compactSlots = initializeNotificationSlots();
        mediaStyle.setShowActionsInCompactView(compactSlots);
    }
    player.UIs()
            .get(MediaSessionPlayerUi.class)
            .flatMap(MediaSessionPlayerUi::getSessionToken)
            .ifPresent(mediaStyle::setMediaSession);

    // setup notification builder
    builder.setStyle(mediaStyle)
            .setPriority(NotificationCompat.PRIORITY_HIGH)
            .setVisibility(NotificationCompat.VISIBILITY_PUBLIC)
            .setCategory(NotificationCompat.CATEGORY_TRANSPORT)
            .setShowWhen(false)
            .setSmallIcon(R.drawable.ic_newpipe_triangle_white)
            .setColor(ContextCompat.getColor(player.getContext(), R.color.dark_background_color))
            .setColorized(player.getPrefs().getBoolean(
                    player.getContext().getString(R.string.notification_colorize_key), true))
            .setDeleteIntent(PendingIntentCompat.getBroadcast(
                    player.getContext(),
                    NOTIFICATION_ID,
                    new Intent(ACTION_CLOSE),
                    FLAG_UPDATE_CURRENT,
                    false
            ));

    // set the initial value for the video thumbnail, updatable with updateNotificationThumbnail
    setLargeIcon(builder);

    return builder;
}

void fastPath(Subscriber<? super R> a, Iterator<? extends R> iterator) {
    for (;;) {
        if (cancelled) {
            return;
        }

        R v;
        try {
            v = iterator.next();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            a.onError(ex);
            return;
        }

        a.onNext(v);

        if (cancelled) {
            return;
        }

        boolean b;
        try {
            b = iterator.hasNext();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            a.onError(ex);
            return;
        }

        if (!b) {
            a.onComplete();
            return;
        }
    }
}

@Override
public void onComplete() {
    if (done) {
        return;
    }
    try {
        onComplete.run();
    } catch (Throwable e) {
        fail(e);
        return;
    }

    done = true;
    downstream.onComplete();

    try {
        onAfterTerminate.run();
    } catch (Throwable e) {
        Exceptions.throwIfFatal(e);
        RxJavaPlugins.onError(e);
    }
}

@Override
public void run(long n) {
    long emitted = 0L;
    Iterator<T> iterator = this.iterator;
    ConditionalSubscriber<? super T> downstream = this.downstream;

    for (;;) {

        if (cancelled) {
            clear();
            break;
        } else {
            T next;
            try {
                next = Objects.requireNonNull(iterator.next(), "The Stream's Iterator returned a null value");
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                downstream.onError(ex);
                cancelled = true;
                continue;
            }

            if (downstream.tryOnNext(next)) {
                emitted++;
            }

            if (cancelled) {
                continue;
            }

            try {
                if (!iterator.hasNext()) {
                    downstream.onComplete();
                    cancelled = true;
                    continue;
                }
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                downstream.onError(ex);
                cancelled = true;
                continue;
            }

            if (emitted != n) {
                continue;
            }
        }

        n = get();
        if (emitted == n) {
            if (compareAndSet(n, 0L)) {
                break;
            }
            n = get();
        }
    }
}

void replay(CacheSubscription<T> consumer) {
    // ensure only one replay runs at a time
    if (consumer.getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    long index = consumer.index;
    int offset = consumer.offset;
    Node<T> node = consumer.node;
    AtomicLong requested = consumer.requested;
    Subscriber<? super T> downstream = consumer.downstream;
    int capacity = capacityHint;

    for (;;) {
        boolean sourceDone = done;
        boolean empty = size == index;

        if (sourceDone && empty) {
            consumer.node = null;
            Throwable ex = error;
            if (ex != null) {
                downstream.onError(ex);
            } else {
                downstream.onComplete();
            }
            return;
        }

        if (!empty) {
            long consumerRequested = requested.get();
            if (consumerRequested == Long.MIN_VALUE) {
                consumer.node = null;
                return;
            }
            if (consumerRequested != index) {
                if (offset == capacity) {
                    node = node.next;
                    offset = 0;
                }

                downstream.onNext(node.values[offset]);

                offset++;
                index++;
                continue;
            }
        }

        consumer.index = index;
        consumer.offset = offset;
        consumer.node = node;
        missed = consumer.addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    Observer<? super R> downstream = this.downstream;
    SimpleQueue<T> queue = this.queue;
    AtomicThrowable errors = this.errors;

    for (;;) {

        if (!active) {

            if (cancelled) {
                queue.clear();
                return;
            }

            if (!tillTheEnd) {
                Throwable ex = errors.get();
                if (ex != null) {
                    queue.clear();
                    cancelled = true;
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            boolean d = done;

            T v;
            try {
                v = queue.poll();
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                cancelled = true;
                this.upstream.dispose();
                errors.tryAddThrowableOrReport(ex);
                errors.tryTerminateConsumer(downstream);
                return;
            }

            boolean empty = v == null;

            if (d && empty) {
                cancelled = true;
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (!empty) {
                ObservableSource<? extends R> o;

                try {
                    o = Objects.requireNonNull(mapper.apply(v), "The mapper returned a null ObservableSource");
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    cancelled = true;
                    this.upstream.dispose();
                    queue.clear();
                    errors.tryAddThrowableOrReport(ex);
                    errors.tryTerminateConsumer(downstream);
                    return;
                }

                if (o instanceof Supplier) {
                    R w;
                    try {
                        w = ((Supplier<R>) o).get();
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        errors.tryAddThrowableOrReport(ex);
                        continue;
                    }

                    if (w != null && !cancelled) {
                        downstream.onNext(w);
                    }
                    continue;
                } else {
                    active = true;
                    o.subscribe(observer);
                }
            }
        }

        if (decrementAndGet() == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;

    final Subscriber<? super R> downstream = this.downstream;
    final SimpleQueue<T> queue = this.queue;
    final AtomicThrowable error = this.error;
    Iterator<? extends R> iterator = this.currentIterator;
    long requested = this.requested.get();
    long emitted = this.emitted;
    final int limit = prefetch - (prefetch >> 2);
    boolean canRequest = sourceMode != QueueFuseable.SYNC;

    for (;;) {
        if (cancelled) {
            queue.clear();
            clearCurrentSuppressCloseError();
        } else {
            boolean isDone = upstreamDone;
            if (error.get() != null) {
                downstream.onError(error.get());
                cancelled = true;
                continue;
            }

            if (iterator == null) {
                T t;
                try {
                    t = queue.poll();
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    trySignalError(downstream, ex);
                    continue;
                }

                boolean isEmpty = t == null;

                if (isDone && isEmpty) {
                    downstream.onComplete();
                    cancelled = true;
                } else if (!isEmpty) {
                    if (canRequest && ++consumed == limit) {
                        consumed = 0;
                        upstream.request(limit);
                    }

                    Stream<? extends R> stream;
                    try {
                        stream = Objects.requireNonNull(mapper.apply(t), "The mapper returned a null Stream");
                        iterator = stream.iterator();

                        if (iterator.hasNext()) {
                            currentIterator = iterator;
                            currentCloseable = stream;
                        } else {
                            iterator = null;
                        }
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        trySignalError(downstream, ex);
                    }
                    continue;
                }
            }
            if (iterator != null && emitted != requested) {
                R item;
                try {
                    item = Objects.requireNonNull(iterator.next(), "The Stream.Iterator returned a null value");
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    trySignalError(downstream, ex);
                    continue;
                }

                if (!cancelled) {
                    downstream.onNext(item);
                    emitted++;

                    if (!cancelled) {
                        try {
                            if (!iterator.hasNext()) {
                                iterator = null;
                                clearCurrentRethrowCloseError();
                            }
                        } catch (Throwable ex) {
                            Exceptions.throwIfFatal(ex);
                            trySignalError(downstream, ex);
                        }
                    }
                }
                continue;
            }
        }

        this.emitted = emitted;
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
        requested = this.requested.get();
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    final Subscriber<? super R> a = downstream;
    final ZipSubscriber<T, R>[] qs = subscribers;
    final int n = qs.length;
    Object[] values = current;

    int missed = 1;

    for (;;) {
        long r = requested.get();
        long e = 0L;

        while (r != e) {

            if (cancelled) {
                return;
            }

            if (!delayErrors && errors.get() != null) {
                cancelAll();
                errors.tryTerminateConsumer(a);
                return;
            }

            boolean empty = false;

            for (int j = 0; j < n; j++) {
                ZipSubscriber<T, R> inner = qs[j];
                if (values[j] == null) {
                    boolean d = inner.done;
                    SimpleQueue<T> q = inner.queue;
                    T v = null;
                    try {
                        v = q != null ? q.poll() : null;
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        errors.tryAddThrowableOrReport(ex);
                        if (!delayErrors) {
                            cancelAll();
                            errors.tryTerminateConsumer(a);
                            return;
                        }
                        d = true;
                    }

                    boolean sourceEmpty = v == null;
                    if (d && sourceEmpty) {
                        cancelAll();
                        errors.tryTerminateConsumer(a);
                        return;
                    }
                    if (!sourceEmpty) {
                        values[j] = v;
                    } else {
                        empty = true;
                    }
                }
            }

            if (empty) {
                break;
            }

            R v;
            try {
                v = Objects.requireNonNull(zipper.apply(values.clone()), "The zipper returned a null value");
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                cancelAll();
                errors.tryAddThrowableOrReport(ex);
                errors.tryTerminateConsumer(a);
                return;
            }

            a.onNext(v);
            e++;

            Arrays.fill(values, null);
        }

        if (r == e) {
            if (cancelled) {
                return;
            }

            if (!delayErrors && errors.get() != null) {
                cancelAll();
                errors.tryTerminateConsumer(a);
                return;
            }

            for (int j = 0; j < n; j++) {
                ZipSubscriber<T, R> inner = qs[j];
                if (values[j] == null) {
                    boolean d = inner.done;
                    SimpleQueue<T> q = inner.queue;
                    T v = null;
                    try {
                        v = q != null ? q.poll() : null;
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        errors.tryAddThrowableOrReport(ex);
                        if (!delayErrors) {
                            cancelAll();
                            errors.tryTerminateConsumer(a);
                            return;
                        }
                        d = true;
                    }
                    boolean empty = v == null;
                    if (d && empty) {
                        cancelAll();
                        errors.tryTerminateConsumer(a);
                        return;
                    }
                    if (!empty) {
                        values[j] = v;
                    }
                }
            }
        }

        if (e != 0L) {
            for (ZipSubscriber<T, R> inner : qs) {
                inner.request(e);
            }
            if (r != Long.MAX_VALUE) {
                requested.addAndGet(-e);
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
Node getHead() {
    long timeLimit = scheduler.now(unit) - maxAge;
    Node prev = get();
    Node next = prev.get();
    for (;;) {
        if (next == null) {
            break;
        }
        Timed<?> v = (Timed<?>) next.value;
        if (NotificationLite.isComplete(v.value()) || NotificationLite.isError(v.value())) {
            break;
        }
        if (v.time() <= timeLimit) {
            prev = next;
            next = next.get();
        } else {
            break;
        }
    }
    return prev;
}

@SuppressWarnings("unchecked")
void remove(AsyncSubscription<T> ps) {
    for (;;) {
        AsyncSubscription<T>[] a = subscribers.get();
        int n = a.length;
        if (n == 0) {
            return;
        }

        int j = -1;
        for (int i = 0; i < n; i++) {
            if (a[i] == ps) {
                j = i;
                break;
            }
        }

        if (j < 0) {
            return;
        }

        AsyncSubscription<T>[] b;
        if (n == 1) {
            b = EMPTY;
        } else {
            b = new AsyncSubscription[n - 1];
            System.arraycopy(a, 0, b, 0, j);
            System.arraycopy(a, j + 1, b, j, n - j - 1);
        }
        if (subscribers.compareAndSet(a, b)) {
            return;
        }
    }
}

@Override
public void onSubscribe(Disposable d) {
    if (DisposableHelper.validate(this.upstream, d)) {
        this.upstream = d;
        if (d instanceof QueueDisposable) {
            @SuppressWarnings("unchecked")
            QueueDisposable<T> qd = (QueueDisposable<T>) d;

            int m = qd.requestFusion(QueueDisposable.ANY);
            if (m == QueueDisposable.SYNC) {
                fusionMode = m;
                queue = qd;
                done = true;

                downstream.onSubscribe(this);
                drain();
                return;
            }

            if (m == QueueDisposable.ASYNC) {
                fusionMode = m;
                queue = qd;

                downstream.onSubscribe(this);
                return;
            }
        }

        queue = new SpscLinkedArrayQueue<>(bufferSize);
        downstream.onSubscribe(this);
    }
}

private void saveDocumentToZip(PDDocument document, ZipOutputStream zipOut, String baseFilename, int index) throws IOException {
    log.debug("Starting saveDocumentToZip for document part {}", index);

    ByteArrayOutputStream outStream = new ByteArrayOutputStream();

    try {
        log.debug("Saving document part {} to byte array", index);
        document.save(outStream);
        log.debug("Successfully saved document part {} ({} bytes)", index, outStream.size());
    } catch (Exception e) {
        log.error("Error saving document part {} to byte array", index, e);
        throw ExceptionUtils.createFileProcessingException("split", e);
    }

    try {
        log.debug("Closing document part {}", index);
        document.close();
        log.debug("Successfully closed document part {}", index);
    } catch (Exception e) {
        log.error("Error closing document part {}", index, e);
        // Continue despite close error
    }

    try {
        String entryName = baseFilename + "_" + index + ".pdf";
        log.debug("Creating ZIP entry: {}", entryName);

        ZipEntry zipEntry = new ZipEntry(entryName);
        zipOut.putNextEntry(zipEntry);

        byte[] bytes = outStream.toByteArray();
        log.debug("Writing {} bytes to ZIP entry", bytes.length);

        zipOut.write(bytes);

        log.debug("Closing ZIP entry");
        zipOut.closeEntry();

        log.debug("Successfully added document part {} to ZIP", index);
    } catch (Exception e) {
        log.error("Error adding document part {} to ZIP", index, e);
        throw ExceptionUtils.createFileProcessingException("split", e);
    }
}

private void processRequest(int limitPerDay,
                            String identifier,
                            Map<String, Bucket> buckets,
                            HttpServletRequest request,
                            HttpServletResponse response,
                            FilterChain filterChain) throws IOException, ServletException {
    Bucket userBucket = buckets.computeIfAbsent(identifier, k -> createUserBucket(limitPerDay));
    ConsumptionProbe probe = userBucket.tryConsumeAndReturnRemaining(1);

    if (!probe.isConsumed()) {
        long waitForRefill = probe.getNanosToWaitForRefill() / 1_000_000_000;
        response.setStatus(HttpStatus.TOO_MANY_REQUESTS.value());
        response.setHeader("X-Rate-Limit-Retry-After-Seconds", Newlines.stripAll(String.valueOf(waitForRefill)));
        response.getWriter().write("Rate limit exceeded for POST requests.");
        return;
    }

    response.setHeader("X-Rate-Limit-Remaining",
            stripNewlines(Newlines.stripAll(Long.toString(probe.getRemainingTokens()))));

    filterChain.doFilter(request, response);
}

@SuppressWarnings("deprecation")
public void migrateEnterpriseSettingsToPremium(ApplicationProperties applicationProperties) {
    EnterpriseEdition enterpriseEdition = applicationProperties.getEnterpriseEdition();
    Premium premium = applicationProperties.getPremium();

    if (enterpriseEdition == null || premium == null) {
        return;
    }

    final String ZERO_KEY = "00000000-0000-0000-0000-000000000000";

    // Copy the license key if it's set in enterprise but not in premium
    if (premium.getKey() == null || ZERO_KEY.equals(premium.getKey())) {
        if (enterpriseEdition.getKey() != null && !ZERO_KEY.equals(enterpriseEdition.getKey())) {
            premium.setKey(enterpriseEdition.getKey());
        }
    }

    // Copy enabled state if enterprise is enabled but premium is not
    if (!premium.isEnabled() && enterpriseEdition.isEnabled()) {
        premium.setEnabled(true);
    }

    // Copy SSO auto login setting
    if (!premium.getProFeatures().isSsoAutoLogin() && enterpriseEdition.isSsoAutoLogin()) {
        premium.getProFeatures().setSsoAutoLogin(true);
    }

    Premium.ProFeatures.CustomMetadata premiumMetadata = premium.getProFeatures().getCustomMetadata();
    EnterpriseEdition.CustomMetadata enterpriseMetadata = enterpriseEdition.getCustomMetadata();

    if (enterpriseMetadata == null || premiumMetadata == null) {
        return;
    }

    // Copy autoUpdateMetadata setting
    if (!premiumMetadata.isAutoUpdateMetadata() && enterpriseMetadata.isAutoUpdateMetadata()) {
        premiumMetadata.setAutoUpdateMetadata(true);
    }

    // Copy author if not set in premium but set in enterprise
    if ((premiumMetadata.getAuthor() == null
            || premiumMetadata.getAuthor().trim().isEmpty()
            || "username".equals(premiumMetadata.getAuthor()))
            && enterpriseMetadata.getAuthor() != null
            && !enterpriseMetadata.getAuthor().trim().isEmpty()) {
        premiumMetadata.setAuthor(enterpriseMetadata.getAuthor());
    }

    // Copy creator if not set in premium but set in enterprise and different from default
    if ((premiumMetadata.getCreator() == null || "Stirling-PDF".equals(premiumMetadata.getCreator()))
            && enterpriseMetadata.getCreator() != null
            && !"Stirling-PDF".equals(enterpriseMetadata.getCreator())) {
        premiumMetadata.setCreator(enterpriseMetadata.getCreator());
    }

    // Copy producer if not set in premium but set in enterprise and different from default
    if ((premiumMetadata.getProducer() == null || "Stirling-PDF".equals(premiumMetadata.getProducer()))
            && enterpriseMetadata.getProducer() != null
            && !"Stirling-PDF".equals(enterpriseMetadata.getProducer())) {
        premiumMetadata.setProducer(enterpriseMetadata.getProducer());
    }
}

private void applyGhostscriptCompression(OptimizePdfRequest request,
                                         int optimizeLevel,
                                         Path currentFile,
                                         List<Path> tempFiles) throws IOException {
    long preGsSize = Files.size(currentFile);
    log.info("Pre-Ghostscript file size: {}", GeneralUtils.formatBytes(preGsSize));

    Path gsOutputFile = Files.createTempFile("gs_output_", ".pdf");
    tempFiles.add(gsOutputFile);

    List<String> command = new ArrayList<>();
    command.add("gs");
    command.add("-sDEVICE=pdfwrite");
    command.add("-dCompatibilityLevel=1.5");
    command.add("-dNOPAUSE");
    command.add("-dQUIET");
    command.add("-dBATCH");

    switch (optimizeLevel) {
        case 1:
            command.add("-dPDFSETTINGS=/prepress");
            break;
        case 2:
            command.add("-dPDFSETTINGS=/printer");
            break;
        case 3:
            command.add("-dPDFSETTINGS=/ebook");
            break;
        case 4:
        case 5:
            command.add("-dPDFSETTINGS=/screen");
            break;
        case 6:
        case 7:
            command.add("-dPDFSETTINGS=/screen");
            command.add("-dColorImageResolution=150");
            command.add("-dGrayImageResolution=150");
            command.add("-dMonoImageResolution=300");
            break;
        case 8:
        case 9:
            command.add("-dPDFSETTINGS=/screen");
            command.add("-dColorImageResolution=100");
            command.add("-dGrayImageResolution=100");
            command.add("-dMonoImageResolution=200");
            break;
        case 10:
            command.add("-dPDFSETTINGS=/screen");
            command.add("-dColorImageResolution=72");
            command.add("-dGrayImageResolution=72");
            command.add("-dMonoImageResolution=150");
            break;
        default:
            command.add("-dPDFSETTINGS=/screen");
            break;
    }

    command.add("-sOutputFile=" + gsOutputFile.toString());
    command.add(currentFile.toString());

    ProcessExecutorResult returnCode = null;

    try {
        returnCode = ProcessExecutor.getInstance(ProcessExecutor.Processes.GHOSTSCRIPT)
                .runCommandWithOutputHandling(command);

        if (returnCode.getRc() == 0) {
            Files.copy(gsOutputFile, currentFile, StandardCopyOption.REPLACE_EXISTING);

            long postGsSize = Files.size(currentFile);
            double gsReduction = 100.0 - ((postGsSize * 100.0) / preGsSize);
            log.info("Post-Ghostscript file size: {} (reduced by {}%)",
                    GeneralUtils.formatBytes(postGsSize), String.format("%.1f", gsReduction));
            return;
        }

        log.warn("Ghostscript compression failed with return code: {}", returnCode.getRc());
        throw new IOException("Ghostscript compression failed");
    } catch (Exception e) {
        log.warn("Ghostscript compression failed, will fallback to other methods", e);
        throw new IOException("Ghostscript compression failed", e);
    }
}

private Object executeWithRetries(ProceedingJoinPoint joinPoint,
                                  Object[] args,
                                  boolean async,
                                  long timeout,
                                  int maxRetries,
                                  boolean trackProgress,
                                  boolean queueable,
                                  int resourceWeight) {
    AtomicReference<String> jobIdRef = new AtomicReference<>();

    return jobExecutorService.runJobGeneric(
            async,
            () -> {
                Throwable lastException = null;

                for (int currentAttempt = 1; currentAttempt <= maxRetries; currentAttempt++) {
                    try {
                        if (trackProgress && async) {
                            if (jobIdRef.get() == null) {
                                jobIdRef.set(getJobIdFromContext());
                            }

                            String jobId = jobIdRef.get();
                            if (jobId != null) {
                                log.debug("Tracking progress for job {} (attempt {}/{})",
                                        jobId, currentAttempt, maxRetries);
                            }
                        }

                        return joinPoint.proceed(args);
                    } catch (Throwable ex) {
                        lastException = ex;

                        log.error("AutoJobAspect caught exception during job execution (attempt {}/{}): {}",
                                currentAttempt, maxRetries, ex.getMessage(), ex);

                        if (currentAttempt >= maxRetries) {
                            break;
                        }

                        log.info("Retrying operation, attempt {}/{}", currentAttempt + 1, maxRetries);

                        if (trackProgress && async) {
                            String jobId = jobIdRef.get();
                            if (jobId != null) {
                                log.debug("Recording retry attempt for job {} in TaskManager", jobId);
                            }
                        }

                        long delayMs = RETRY_BASE_DELAY.toMillis() * currentAttempt;

                        CompletableFuture<Object> delayedRetry = new CompletableFuture<>();
                        CompletableFuture.delayedExecutor(delayMs, TimeUnit.MILLISECONDS).execute(() -> delayedRetry.complete(null));

                        try {
                            delayedRetry.join();
                        } catch (Exception e) {
                            Thread.currentThread().interrupt();
                            break;
                        }
                    }
                }

                if (lastException != null) {
                    throw new RuntimeException("Job failed after " + maxRetries + " attempts: " + lastException.getMessage(), lastException);
                }

                throw new RuntimeException("Job failed but no exception was recorded");
            },
            timeout,
            queueable,
            resourceWeight);
}

