private void monitorSubscription(final ChannelInfo info) {
    final Consumer<Throwable> err = throwable -> {
        animate(binding.channelSubscribeButton, false, 100);
        showSnackBarError(new ErrorInfo(throwable, UserAction.SUBSCRIPTION_GET,
                "Get subscription status", currentInfo));
    };

    final Observable<List<SubscriptionEntity>> o = subscriptionManager
            .subscriptionTable()
            .getSubscriptionFlowable(info.getServiceId(), info.getUrl())
            .toObservable();

    disposables.add(
            o.observeOn(AndroidSchedulers.mainThread())
             .subscribe(getSubscribeUpdateMonitor(info), err)
    );

    disposables.add(
            o.map(List::isEmpty)
             .distinctUntilChanged()
             .observeOn(AndroidSchedulers.mainThread())
             .subscribe(isEmpty -> updateSubscribeButton(!isEmpty), err)
    );

    disposables.add(
            o.map(List::isEmpty)
             .distinctUntilChanged()
             .skip(1)
             .filter(x -> NotificationHelper.areNewStreamsNotificationsEnabled(requireContext()))
             .observeOn(AndroidSchedulers.mainThread())
             .subscribe(isEmpty -> {
                 if (!isEmpty) {
                     showNotifySnackbar();
                 }
             }, err)
    );
}

private void runOnVisible(final Consumer<AppCompatActivity> runnable) {
    getActivityContext().ifPresentOrElse(c -> {
        if (getLifecycle().getCurrentState().isAtLeast(Lifecycle.State.STARTED)) {
            c.runOnUiThread(() -> {
                runnable.accept(c);
                inFlight(false);
            });
            return;
        }

        getLifecycle().addObserver(new DefaultLifecycleObserver() {
            @Override
            public void onResume(@NonNull final LifecycleOwner owner) {
                getLifecycle().removeObserver(this);
                getActivityContext().ifPresentOrElse(c2 ->
                        c2.runOnUiThread(() -> {
                            runnable.accept(c2);
                            inFlight(false);
                        }),
                        () -> inFlight(false)
                );
            }
        });

        if (Build.VERSION.SDK_INT < Build.VERSION_CODES.Q && !c.isChangingConfigurations()) {
            final Intent i = new Intent(c, RouterActivity.class);
            i.setFlags(Intent.FLAG_ACTIVITY_REORDER_TO_FRONT);
            startActivity(i);
        }
    }, () -> inFlight(false));
}

public static void showMetaInfoInTextView(@Nullable final List<MetaInfo> metaInfos,
                                          final TextView metaInfoTextView,
                                          final View metaInfoSeparator,
                                          final CompositeDisposable disposables) {
    final Context ctx = metaInfoTextView.getContext();
    if (metaInfos == null || metaInfos.isEmpty()
            || !PreferenceManager.getDefaultSharedPreferences(ctx)
                                 .getBoolean(ctx.getString(R.string.show_meta_info_key), true)) {
        metaInfoTextView.setVisibility(View.GONE);
        metaInfoSeparator.setVisibility(View.GONE);
        return;
    }

    final StringBuilder sb = new StringBuilder();
    for (final MetaInfo mi : metaInfos) {
        if (!isNullOrEmpty(mi.getTitle())) {
            sb.append("<b>").append(mi.getTitle()).append("</b>")
              .append(Localization.DOT_SEPARATOR);
        }

        String s = mi.getContent().getContent().trim();
        if (s.endsWith(".")) {
            s = s.substring(0, s.length() - 1);
        }
        sb.append(s);

        for (int i = 0; i < mi.getUrls().size(); i++) {
            if (i == 0) {
                sb.append(Localization.DOT_SEPARATOR);
            } else {
                sb.append("<br/><br/>");
            }

            sb.append("<a href=\"").append(mi.getUrls().get(i)).append("\">")
              .append(capitalizeIfAllUppercase(mi.getUrlTexts().get(i).trim()))
              .append("</a>");
        }
    }

    metaInfoSeparator.setVisibility(View.VISIBLE);
    TextLinkifier.fromHtml(metaInfoTextView, sb.toString(),
            HtmlCompat.FROM_HTML_SEPARATOR_LINE_BREAK_HEADING, null, null, disposables,
            SET_LINK_MOVEMENT_METHOD);
}

@Override // own playback listener
public void onPlaybackSynchronize(@NonNull final PlayQueueItem item, final boolean wasBlocked) {
    if (DEBUG) {
        Log.d(TAG, "Playback - onPlaybackSynchronize(was blocked: " + wasBlocked
                + ") called with item=[" + item.getTitle() + "], url=[" + item.getUrl() + "]");
    }
    if (exoPlayerIsNull() || playQueue == null || currentItem == item) {
        return;
    }

    final int pqIdx = playQueue.indexOf(item);
    final int plIdx = simpleExoPlayer.getCurrentMediaItemIndex();
    final int plSize = simpleExoPlayer.getCurrentTimeline().getWindowCount();
    final boolean removeThumb = currentItem == null
            || currentItem.getServiceId() != item.getServiceId()
            || !currentItem.getUrl().equals(item.getUrl());

    currentItem = item;

    if (pqIdx != playQueue.getIndex()) {
        Log.e(TAG, "Playback - Play Queue may be not in sync: item index=[" + pqIdx + "], "
                + "queue index=[" + playQueue.getIndex() + "]");
        return;
    }

    if ((plSize > 0 && pqIdx >= plSize) || pqIdx < 0) {
        Log.e(TAG, "Playback - Trying to seek to invalid index=[" + pqIdx
                + "] with playlist length=[" + plSize + "]");
        return;
    }

    if (wasBlocked || plIdx != pqIdx || !isPlaying()) {
        if (DEBUG) {
            Log.d(TAG, "Playback - Rewinding to correct index=[" + pqIdx + "], "
                    + "from=[" + plIdx + "], size=[" + plSize + "].");
        }

        if (removeThumb) {
            onThumbnailLoaded(null);
        }

        if (item.getRecoveryPosition() != PlayQueueItem.RECOVERY_UNSET) {
            simpleExoPlayer.seekTo(pqIdx, item.getRecoveryPosition());
            playQueue.unsetRecovery(pqIdx);
        } else {
            simpleExoPlayer.seekToDefaultPosition(pqIdx);
        }
    }
}

@Nullable
static String choosePreferredImage(@NonNull final List<Image> images,
                                   final PreferredImageQuality nonNoneQuality) {
    final double wOverH = images.stream()
            .filter(i -> i.getHeight() != HEIGHT_UNKNOWN && i.getWidth() != WIDTH_UNKNOWN)
            .mapToDouble(i -> ((double) i.getWidth()) / i.getHeight())
            .findFirst()
            .orElse(1.0);

    final Image.ResolutionLevel pref = nonNoneQuality.toResolutionLevel();
    final Comparator<Image> first = Comparator
            .<Image>comparingInt(i -> {
                if (i.getEstimatedResolutionLevel() == Image.ResolutionLevel.UNKNOWN) {
                    return 3;
                } else if (i.getEstimatedResolutionLevel() == pref) {
                    return 0;
                } else if (i.getEstimatedResolutionLevel() == Image.ResolutionLevel.MEDIUM) {
                    return 1;
                } else {
                    return 2;
                }
            })
            .thenComparing(i ->
                    i.getHeight() == HEIGHT_UNKNOWN && i.getWidth() == WIDTH_UNKNOWN);

    final Comparator<Image> last = switch (nonNoneQuality) {
        case NONE -> first; // unreachable
        case LOW -> first.thenComparingDouble(i -> {
            final double px = estimatePixelCount(i, wOverH);
            return Math.abs(px - BEST_LOW_H * BEST_LOW_H * wOverH);
        });
        case MEDIUM -> first.thenComparingDouble(i -> {
            final double px = estimatePixelCount(i, wOverH);
            return Math.abs(px - BEST_MEDIUM_H * BEST_MEDIUM_H * wOverH);
        });
        case HIGH -> first.thenComparingDouble(i -> -estimatePixelCount(i, wOverH));
    };

    return images.stream()
            .min(last)
            .map(Image::getUrl)
            .orElse(null);
}

private void updateTabLayoutPosition() {
    final ScrollableTabLayout tabLayout = binding.mainTabLayout;
    final ViewPager viewPager = binding.pager;
    final boolean bottom = mainTabsPositionBottom;

    final RelativeLayout.LayoutParams tabParams =
            (RelativeLayout.LayoutParams) tabLayout.getLayoutParams();
    final RelativeLayout.LayoutParams pagerParams =
            (RelativeLayout.LayoutParams) viewPager.getLayoutParams();

    tabParams.removeRule(bottom ? ALIGN_PARENT_TOP : ALIGN_PARENT_BOTTOM);
    tabParams.addRule(bottom ? ALIGN_PARENT_BOTTOM : ALIGN_PARENT_TOP);
    pagerParams.removeRule(bottom ? BELOW : ABOVE);
    pagerParams.addRule(bottom ? ABOVE : BELOW, R.id.main_tab_layout);
    tabLayout.setSelectedTabIndicatorGravity(
            bottom ? INDICATOR_GRAVITY_TOP : INDICATOR_GRAVITY_BOTTOM);

    tabLayout.setLayoutParams(tabParams);
    viewPager.setLayoutParams(pagerParams);

    tabLayout.setBackgroundColor(ThemeHelper.resolveColorFromAttr(requireContext(),
            bottom ? android.R.attr.windowBackground : R.attr.colorPrimary));

    @ColorInt final int iconColor = bottom
            ? ThemeHelper.resolveColorFromAttr(requireContext(), android.R.attr.colorAccent)
            : Color.WHITE;
    tabLayout.setTabRippleColor(ColorStateList.valueOf(iconColor).withAlpha(32));
    tabLayout.setTabIconTint(ColorStateList.valueOf(iconColor));
    tabLayout.setSelectedTabIndicatorColor(iconColor);
}

