public synchronized Map<String, Object> getConnectionInfo(final boolean brief) {
    final Map<String, Object> info = new LinkedHashMap<>();
    info.put("remote_socket_address", getRemoteSocketAddress());
    info.put("interest_ops", getInterestOps());
    info.put("outstanding_requests", getOutstandingRequests());
    info.put("packets_received", getPacketsReceived());
    info.put("packets_sent", getPacketsSent());
    if (!brief) {
        info.put("session_id", getSessionId());
        info.put("last_operation", getLastOperation());
        info.put("established", getEstablished());
        info.put("session_timeout", getSessionTimeout());
        info.put("last_cxid", getLastCxid());
        info.put("last_zxid", getLastZxid());
        info.put("last_response_time", getLastResponseTime());
        info.put("last_latency", getLastLatency());
        info.put("min_latency", getMinLatency());
        info.put("avg_latency", getAvgLatency());
        info.put("max_latency", getMaxLatency());
    }
    return info;
}

public void run() {
    try {
        while (!stopped) {
            try {
                select();
                processAcceptedConnections();
                processInterestOpsUpdateRequests();
            } catch (final RuntimeException e) {
                LOG.warn("Ignoring unexpected runtime exception", e);
            } catch (final Exception e) {
                LOG.warn("Ignoring unexpected exception", e);
            }
        }

        // Close connections still pending on the selector. Any others
        // with in-flight work, let drain out of the work queue.
        for (final SelectionKey key : selector.keys()) {
            final NIOServerCnxn cnxn = (NIOServerCnxn) key.attachment();
            if (cnxn.isSelectable()) {
                cnxn.close(ServerCnxn.DisconnectReason.SERVER_SHUTDOWN);
            }
            cleanupSelectionKey(key);
        }
        SocketChannel accepted;
        while ((accepted = acceptedQueue.poll()) != null) {
            fastCloseSock(accepted);
        }
        updateQueue.clear();
    } finally {
        closeSelector();
        // This will wake up the accept thread and the other selector
        // threads, and tell the worker thread pool to begin shutdown.
        NIOServerCnxnFactory.this.stop();
        LOG.info("selector thread exited run method");
    }
}

void handleWrite(final SelectionKey k) throws IOException {
    if (outgoingBuffers.isEmpty()) {
        return;
    }

    // This is going to reset the buffer position to 0 and the limit to the size of the buffer,
    // so that we can fill it with data from the non-direct buffers that we need to send.
    final ByteBuffer directBuffer = NIOServerCnxnFactory.getDirectBuffer();
    if (directBuffer == null) {
        final ByteBuffer[] bufferList = new ByteBuffer[outgoingBuffers.size()];
        // Use gathered write call. This updates the positions of the byte buffers to reflect the
        // bytes that were written out.
        sock.write(outgoingBuffers.toArray(bufferList));

        // Remove the buffers that we have sent
        ByteBuffer bb;
        while ((bb = outgoingBuffers.peek()) != null) {
            if (bb == ServerCnxnFactory.closeConn) {
                throw new CloseRequestException("close requested", DisconnectReason.CLIENT_CLOSED_CONNECTION);
            }
            if (bb == packetSentinel) {
                packetSent();
            }
            if (bb.remaining() > 0) {
                break;
            }
            outgoingBuffers.remove();
        }
        return;
    }

    directBuffer.clear();

    for (ByteBuffer b : outgoingBuffers) {
        if (directBuffer.remaining() < b.remaining()) {
            // When we call put later, if the directBuffer is too small to hold everything,
            // nothing will be copied, so we've got to slice the buffer if it's too big.
            b = (ByteBuffer) b.slice().limit(directBuffer.remaining());
        }
        // put() is going to modify the positions of both buffers, but we don't want to change
        // the position of the source buffers (we'll do that after the send, if needed), so
        // we save and reset the position after the copy.
        final int p = b.position();
        directBuffer.put(b);
        b.position(p);
        if (directBuffer.remaining() == 0) {
            break;
        }
    }

    // Do the flip: limit becomes position, position gets set to 0. This sets us up for the write.
    directBuffer.flip();

    int sent = sock.write(directBuffer);

    ByteBuffer bb;
    // Remove the buffers that we have sent
    while ((bb = outgoingBuffers.peek()) != null) {
        if (bb == ServerCnxnFactory.closeConn) {
            throw new CloseRequestException("close requested", DisconnectReason.CLIENT_CLOSED_CONNECTION);
        }
        if (bb == packetSentinel) {
            packetSent();
        }
        if (sent < bb.remaining()) {
            // We only partially sent this buffer, so we update the position and exit the loop.
            bb.position(bb.position() + sent);
            break;
        }
        // We've sent the whole buffer, so drop the buffer
        sent -= bb.remaining();
        outgoingBuffers.remove();
    }
}

public Long update(final E elem, final int timeout) {
    Long prevExpiryTime = elemMap.get(elem);
    final long now = Time.currentElapsedTime();
    final Long newExpiryTime = roundToNextInterval(now + timeout);

    if (newExpiryTime.equals(prevExpiryTime)) {
        // No change, so nothing to update
        return null;
    }

    // First add the elem to the new expiry time bucket in expiryMap.
    Set<E> set = expiryMap.get(newExpiryTime);
    if (set == null) {
        // Construct a ConcurrentHashSet using a ConcurrentHashMap
        set = Collections.newSetFromMap(new ConcurrentHashMap<>());
        // Put the new set in the map, but only if another thread hasn't beaten us to it
        final Set<E> existingSet = expiryMap.putIfAbsent(newExpiryTime, set);
        if (existingSet != null) {
            set = existingSet;
        }
    }
    set.add(elem);

    // Map the elem to the new expiry time. If a different previous mapping was present,
    // clean up the previous expiry bucket.
    prevExpiryTime = elemMap.put(elem, newExpiryTime);
    if (prevExpiryTime != null && !newExpiryTime.equals(prevExpiryTime)) {
        final Set<E> prevSet = expiryMap.get(prevExpiryTime);
        if (prevSet != null) {
            prevSet.remove(elem);
        }
    }
    return newExpiryTime;
}

public static synchronized ZooKeeper getClient(final String contextPath,
                                               final String session,
                                               final int expireTime) throws IOException {
    final String connectionId = concat(contextPath, session);

    ZooKeeper zk = zkMap.get(connectionId);
    if (zk == null) {
        if (LOG.isInfoEnabled()) {
            LOG.info(String.format("creating new connection for : '%s'", connectionId));
        }
        final Endpoint e = contextMap.get(contextPath);
        zk = new ZooKeeper(e.getHostPort(), 30000, new MyWatcher(connectionId));

        for (final Map.Entry<String, String> p : e.getZooKeeperAuthInfo().entrySet()) {
            zk.addAuthInfo("digest", String.format("%s:%s", p.getKey(), p.getValue()).getBytes());
        }

        zkMap.put(connectionId, zk);

        // a session should automatically expire after an amount of time
        if (session != null) {
            zkSessionTimers.put(connectionId, new SessionTimerTask(expireTime, session, contextPath, timer));
        }
    }
    return zk;
}

@Test
public void testSimpleCase() throws Exception {
    configureServers(serverCount);
    configureClients(clientCount, SimpleClient.class, getHostPort());
    final Stat stat = new Stat();
    startServers();
    LOG.debug("Connecting to " + getHostPort());
    final ZooKeeper zk = new ZooKeeper(getHostPort(), 15000, this);
    waitForConnect(zk, 10000);
    zk.create("/simpleCase", "orig".getBytes(), Ids.OPEN_ACL_UNSAFE, CreateMode.PERSISTENT);
    startClients();

    // Check that all clients connect properly
    for (int i = 0; i < getClientCount(); i++) {
        for (int j = 0; j < maxTries; j++) {
            try {
                final byte[] b = zk.getData("/simpleCase/" + i, false, stat);
                Assert.assertEquals("orig", new String(b));
            } catch (final NoNodeException e) {
                if (j + 1 == maxTries) {
                    Assert.fail("Max tries exceeded on client " + i);
                }
                Thread.sleep(1000);
            }
        }
    }

    // Kill half the servers, make a change, restart the dead
    // servers, and then bounce the other servers one by one
    for (int i = 0; i < getServerCount(); i++) {
        stopServer(i);
        if (i + 1 > getServerCount() / 2) {
            startServer(i);
        } else if (i + 1 == getServerCount() / 2) {
            Assert.assertTrue("Connection didn't recover", waitForConnect(zk, 10000));
            try {
                zk.setData("/simpleCase", "new".getBytes(), -1);
            } catch (final ConnectionLossException e) {
                Assert.assertTrue("Connection didn't recover", waitForConnect(zk, 10000));
                zk.setData("/simpleCase", "new".getBytes(), -1);
            }
            for (int j = 0; j < i; j++) {
                LOG.info("Starting server " + j);
                startServer(i);
            }
        }
    }
    Thread.sleep(100); // wait for things to stabilize
    Assert.assertTrue("Servers didn't bounce", waitForConnect(zk, 15000));
    try {
        zk.getData("/simpleCase", false, stat);
    } catch (final ConnectionLossException e) {
        Assert.assertTrue("Servers didn't bounce", waitForConnect(zk, 15000));
    }

    // check that the change has propagated to everyone
    for (int i = 0; i < getClientCount(); i++) {
        for (int j = 0; j < maxTries; j++) {
            final byte[] data = zk.getData("/simpleCase/" + i, false, stat);
            if (new String(data).equals("new")) {
                break;
            }
            if (j + 1 == maxTries) {
                Assert.fail("max tries exceeded for " + i);
            }
            Thread.sleep(1000);
        }
    }

    // send out the kill signal
    zk.setData("/simpleCase", "die".getBytes(), -1);

    // watch for everyone to die
    for (int i = 0; i < getClientCount(); i++) {
        try {
            for (int j = 0; j < maxTries; j++) {
                zk.getData("/simpleCase/" + i, false, stat);
                if (j + 1 == maxTries) {
                    Assert.fail("max tries exceeded waiting for child " + i + " to die");
                }
                Thread.sleep(200);
            }
        } catch (final NoNodeException e) {
            // Great this is what we were hoping for!
        }
    }

    stopClients();
    stopServers();
}

