private StringArg readStringArg() throws IOException, FilterException {
    int c = reader.read();
    int last = 0;
    if (c != '"') {
        throw new FilterException("Check the parser, trying to read a string that doesn't begin with quotes");
    }
    final StringBuffer buffer = new StringBuffer();
    while (reader.ready()) {
        last = c;
        c = reader.read();
        if (c == -1) {
            break;
        }

        if (c == '"' && last != '\\') {
            return new StringArg(buffer.toString());
        } else {
            buffer.append((char) c);
        }
    }
    throw new FilterException("Unterminated string");
}

public static void main(final String[] args) throws IOException {
    final MergedLogSource source = new MergedLogSource(args);

    final PrintStream ps_ms = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-ms.out")));
    final PrintStream ps_sec = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-sec.out")));
    final PrintStream ps_min = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-min.out")));
    final PrintStream ps_hour = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-hour.out")));
    LogIterator iter;

    System.out.println(source);
    iter = source.iterator();
    long currentms = 0;
    long currentsec = 0;
    long currentmin = 0;
    long currenthour = 0;
    final Set<Long> zxids_ms = new HashSet<Long>();
    long zxid_sec = 0;
    long zxid_min = 0;
    long zxid_hour = 0;

    while (iter.hasNext()) {
        final LogEntry e = iter.next();
        final TransactionEntry cxn = (TransactionEntry) e;

        final long ms = cxn.getTimestamp();
        final long sec = ms / MS_PER_SEC;
        final long min = ms / MS_PER_MIN;
        final long hour = ms / MS_PER_HOUR;

        if (currentms != ms && currentms != 0) {
            ps_ms.println("" + currentms + " " + zxids_ms.size());

            zxid_sec += zxids_ms.size();
            zxid_min += zxids_ms.size();
            zxid_hour += zxids_ms.size();
            zxids_ms.clear();
        }

        if (currentsec != sec && currentsec != 0) {
            ps_sec.println("" + currentsec * MS_PER_SEC + " " + zxid_sec);
            zxid_sec = 0;
        }

        if (currentmin != min && currentmin != 0) {
            ps_min.println("" + currentmin * MS_PER_MIN + " " + zxid_min);
            zxid_min = 0;
        }

        if (currenthour != hour && currenthour != 0) {
            ps_hour.println("" + currenthour * MS_PER_HOUR + " " + zxid_hour);
            zxid_hour = 0;
        }

        currentms = ms;
        currentsec = sec;
        currentmin = min;
        currenthour = hour;

        zxids_ms.add(cxn.getZxid());
    }

    iter.close();
    ps_ms.close();
    ps_sec.close();
    ps_min.close();
    ps_hour.close();
}

private LogEntry readNextEntry() {
    LogEntry e = null;
    try {
        long crcValue;
        byte[] bytes;
        try {
            crcValue = logStream.readLong("crcvalue");
            bytes = logStream.readBuffer("txnEntry");
        } catch (EOFException ex) {
            return null;
        }

        if (bytes.length == 0) {
            return null;
        }
        final Checksum crc = new Adler32();
        crc.update(bytes, 0, bytes.length);
        if (crcValue != crc.getValue()) {
            throw new IOException("CRC doesn't match " + crcValue + " vs " + crc.getValue());
        }

        final TxnLogEntry logEntry = SerializeUtils.deserializeTxn(bytes);
        final TxnHeader hdr = logEntry.getHeader();
        final Record r = logEntry.getTxn();

        switch (hdr.getType()) {
            case OpCode.createSession: {
                e = new TransactionEntry(
                        hdr.getTime(),
                        hdr.getClientId(),
                        hdr.getCxid(),
                        hdr.getZxid(),
                        "createSession");
            }
                break;
            case OpCode.closeSession: {
                e = new TransactionEntry(
                        hdr.getTime(),
                        hdr.getClientId(),
                        hdr.getCxid(),
                        hdr.getZxid(),
                        "closeSession");
            }
                break;
            case OpCode.create:
                if (r != null) {
                    final CreateTxn create = (CreateTxn) r;
                    final String path = create.getPath();
                    e = new TransactionEntry(
                            hdr.getTime(),
                            hdr.getClientId(),
                            hdr.getCxid(),
                            hdr.getZxid(),
                            "create",
                            path);
                }
                break;
            case OpCode.setData:
                if (r != null) {
                    final SetDataTxn set = (SetDataTxn) r;
                    final String path = set.getPath();
                    e = new TransactionEntry(
                            hdr.getTime(),
                            hdr.getClientId(),
                            hdr.getCxid(),
                            hdr.getZxid(),
                            "setData",
                            path);
                }
                break;
            case OpCode.setACL:
                if (r != null) {
                    final SetACLTxn setacl = (SetACLTxn) r;
                    final String path = setacl.getPath();
                    e = new TransactionEntry(
                            hdr.getTime(),
                            hdr.getClientId(),
                            hdr.getCxid(),
                            hdr.getZxid(),
                            "setACL",
                            path);
                }
                break;
            case OpCode.error:
                if (r != null) {
                    final ErrorTxn error = (ErrorTxn) r;
                    e = new TransactionEntry(
                            hdr.getTime(),
                            hdr.getClientId(),
                            hdr.getCxid(),
                            hdr.getZxid(),
                            "error",
                            "Error: " + error.getErr());
                }
                break;
            default:
                LOG.info("Unknown op: " + hdr.getType());
                break;
        }

        if (logStream.readByte("EOR") != 'B') {
            throw new EOFException("Last transaction was partial.");
        }
    } catch (Exception ex) {
        LOG.error("Error reading transaction from (" + src.file + ") :" + e);
        return null;
    }
    return e;
}

public void run() {
    try {
        while (!stopped) {
            try {
                select();
                processAcceptedConnections();
                processInterestOpsUpdateRequests();
            } catch (RuntimeException e) {
                LOG.warn("Ignoring unexpected runtime exception", e);
            } catch (Exception e) {
                LOG.warn("Ignoring unexpected exception", e);
            }
        }

        // Close connections still pending on the selector. Any others
        // with in-flight work, let drain out of the work queue.
        for (SelectionKey key : selector.keys()) {
            final NIOServerCnxn cnxn = (NIOServerCnxn) key.attachment();
            if (cnxn.isSelectable()) {
                cnxn.close(ServerCnxn.DisconnectReason.SERVER_SHUTDOWN);
            }
            cleanupSelectionKey(key);
        }
        SocketChannel accepted;
        while ((accepted = acceptedQueue.poll()) != null) {
            fastCloseSock(accepted);
        }
        updateQueue.clear();
    } finally {
        closeSelector();
        // This will wake up the accept thread and the other selector
        // threads, and tell the worker thread pool to begin shutdown.
        NIOServerCnxnFactory.this.stop();
        LOG.info("selector thread exited run method");
    }
}

public void reconfigureInstance(final String name, final String params)
        throws NoAssignmentException, InterruptedException, KeeperException {
    if (LOG.isDebugEnabled()) {
        LOG.debug("Reconfiguring " + name + " with " + params);
    }
    final Assigned assigned = instanceToAssignment.get(name);
    if (assigned == null) {
        throw new NoAssignmentException();
    }
    KeeperException lastException = null;
    for (int i = 0; i < maxTries; i++) {
        try {
            zk.setData(
                    assignmentsNode + '/' + assigned.container + '/' + name,
                    ("update " + params).getBytes(),
                    -1);
            break;
        } catch (ConnectionLossException e) {
            lastException = e;
        }
    }
    if (lastException != null) {
        throw lastException;
    }
}

public static void closeSock(final SocketChannel sock) {
    if (!sock.isOpen()) {
        return;
    }

    try {
        /*
         * The following sequence of code is stupid! You would think that
         * only sock.close() is needed, but alas, it doesn't work that way.
         * If you just do sock.close() there are cases where the socket
         * doesn't actually close...
         */
        sock.socket().shutdownOutput();
    } catch (IOException e) {
        // This is a relatively common exception that we can't avoid
        LOG.debug("ignoring exception during output shutdown", e);
    }
    try {
        sock.socket().shutdownInput();
    } catch (IOException e) {
        // This is a relatively common exception that we can't avoid
        LOG.debug("ignoring exception during input shutdown", e);
    }
    try {
        sock.socket().close();
    } catch (IOException e) {
        LOG.debug("ignoring exception during socket close", e);
    }
    try {
        sock.close();
    } catch (IOException e) {
        LOG.debug("ignoring exception during socketchannel close", e);
    }
}

String handleRequest(final JsonRequest request) throws Exception {
    long starttime = request.getNumber("start", 0);
    long endtime = request.getNumber("end", 0);
    final long period = request.getNumber("period", 0);
    final String filterstr = request.getString("filter", "");

    FilterOp fo = null;
    if (filterstr.length() > 0) {
        fo = new FilterParser(filterstr).parse();
    }

    if (starttime == 0) {
        starttime = source.getStartTime();
    }
    if (endtime == 0) {
        if (period > 0) {
            endtime = starttime + period;
        } else {
            endtime = starttime + DEFAULT_PERIOD;
        }
    }

    if (LOG.isDebugEnabled()) {
        LOG.debug("handle(start= " + starttime + ", end=" + endtime + ", period=" + period + ")");
    }

    final LogIterator iterator = (fo != null)
            ? source.iterator(starttime, endtime, fo)
            : source.iterator(starttime, endtime);
    return new JsonGenerator(iterator).toString();
}

@Override
void cleanup() {
    if (sockKey != null) {
        final SocketChannel sock = (SocketChannel) sockKey.channel();
        sockKey.cancel();
        try {
            sock.socket().shutdownInput();
        } catch (IOException e) {
            LOG.debug("Ignoring exception during shutdown input", e);
        }
        try {
            sock.socket().shutdownOutput();
        } catch (IOException e) {
            LOG.debug("Ignoring exception during shutdown output", e);
        }
        try {
            sock.socket().close();
        } catch (IOException e) {
            LOG.debug("Ignoring exception during socket close", e);
        }
        try {
            sock.close();
        } catch (IOException e) {
            LOG.debug("Ignoring exception during channel close", e);
        }
    }
    try {
        Thread.sleep(100);
    } catch (InterruptedException e) {
        LOG.debug("SendThread interrupted during sleep, ignoring");
    }
    sockKey = null;
}

protected void animateVertically(final int posx, final int fromY, final int toY)
        throws InterruptedException {
    toaster.setLocation(posx, fromY);
    if (toY < fromY) {
        for (int i = fromY; i > toY; i -= step) {
            toaster.setLocation(posx, i);
            Thread.sleep(stepTime);
        }
    } else {
        for (int i = fromY; i < toY; i += step) {
            toaster.setLocation(posx, i);
            Thread.sleep(stepTime);
        }
    }
    toaster.setLocation(posx, toY);
}

private void doPostprocessing() {
    errCode = ERROR_NOTHING;
    errObject = null;
    final Thread thread = Thread.currentThread();

    notifyPostProcessing(1);

    if (DEBUG) {
        thread.setName("[" + TAG + "]  ps = " + psAlgorithm + "  filename = " + storage.getName());
    }

    Exception exception = null;

    try {
        psAlgorithm.run(this);
    } catch (Exception err) {
        Log.e(TAG, "Post-processing failed. " + psAlgorithm.toString(), err);

        if (err instanceof InterruptedIOException
                || err instanceof ClosedByInterruptException
                || thread.isInterrupted()) {
            notifyError(DownloadMission.ERROR_POSTPROCESSING_STOPPED, null);
            return;
        }

        if (errCode == ERROR_NOTHING) {
            errCode = ERROR_POSTPROCESSING;
        }

        exception = err;
    } finally {
        notifyPostProcessing(errCode == ERROR_NOTHING ? 2 : 0);
    }

    if (errCode != ERROR_NOTHING) {
        if (exception == null) {
            exception = errObject;
        }
        notifyError(ERROR_POSTPROCESSING, exception);
        return;
    }

    notifyFinished();
}

@Override
public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {
    if (oldVersion == 2) {
        db.execSQL("ALTER TABLE " + MISSIONS_TABLE_NAME_v2 + " ADD COLUMN " + KEY_KIND + " TEXT;");
        oldVersion++;
    }

    if (oldVersion == 3) {
        final String KEY_LOCATION = "location";
        final String KEY_NAME = "name";

        db.execSQL(MISSIONS_CREATE_TABLE);

        Cursor cursor =
                db.query(MISSIONS_TABLE_NAME_v2, null, null, null, null, null, KEY_TIMESTAMP);
        try {
            final int count = cursor.getCount();
            if (count > 0) {
                db.beginTransaction();
                try {
                    while (cursor.moveToNext()) {
                        ContentValues values = new ContentValues();
                        values.put(KEY_SOURCE, cursor.getString(cursor.getColumnIndex(KEY_SOURCE)));
                        values.put(KEY_DONE, cursor.getString(cursor.getColumnIndex(KEY_DONE)));
                        values.put(
                                KEY_TIMESTAMP,
                                cursor.getLong(cursor.getColumnIndex(KEY_TIMESTAMP)));
                        values.put(KEY_KIND, cursor.getString(cursor.getColumnIndex(KEY_KIND)));
                        values.put(
                                KEY_PATH,
                                Uri.fromFile(
                                                new File(
                                                        cursor.getString(
                                                                cursor.getColumnIndex(
                                                                        KEY_LOCATION)),
                                                        cursor.getString(
                                                                cursor.getColumnIndex(KEY_NAME))))
                                        .toString());

                        db.insert(FINISHED_TABLE_NAME, null, values);
                    }
                    db.setTransactionSuccessful();
                } finally {
                    db.endTransaction();
                }
            }
        } finally {
            cursor.close();
        }
        db.execSQL("DROP TABLE " + MISSIONS_TABLE_NAME_v2);
    }
}

@SuppressWarnings("MethodLength")
public void build(final SharpStream output) throws IOException {
    if (done) {
        throw new RuntimeException("already done");
    }
    if (!output.canWrite()) {
        throw new IOException("the provided output is not writable");
    }

    // WARNING: the muxer requires at least 8 samples of every track
    //          not allowed for very short tracks (less than 0.5 seconds)
    outStream = output;
    long read = 8; // mdat box header size
    long totalSampleSize = 0;
    final int[] sampleExtra = new int[readers.length];
    final int[] defaultMediaTime = new int[readers.length];
    final int[] defaultSampleDuration = new int[readers.length];
    final int[] sampleCount = new int[readers.length];

    final TablesInfo[] tablesInfo = new TablesInfo[tracks.length];
    for (int i = 0; i < tablesInfo.length; i++) {
        tablesInfo[i] = new TablesInfo();
    }

    final int singleSampleBuffer;
    if (tracks.length == 1 && tracks[0].kind == TrackKind.Audio) {
        // near 1 second of audio data per chunk, avoid split the audio stream in large chunks
        singleSampleBuffer = tracks[0].trak.mdia.mdhdTimeScale / 1000;
    } else {
        singleSampleBuffer = -1;
    }

    for (int i = 0; i < readers.length; i++) {
        int samplesSize = 0;
        int sampleSizeChanges = 0;
        int compositionOffsetLast = -1;

        Mp4DashChunk chunk;
        while ((chunk = readers[i].getNextChunk(true)) != null) {

            if (defaultMediaTime[i] < 1 && chunk.moof.traf.tfhd.defaultSampleDuration > 0) {
                defaultMediaTime[i] = chunk.moof.traf.tfhd.defaultSampleDuration;
            }

            read += chunk.moof.traf.trun.chunkSize;
            sampleExtra[i] += chunk.moof.traf.trun.chunkDuration; // calculate track duration

            TrunEntry info;
            while ((info = chunk.getNextSampleInfo()) != null) {
                if (info.isKeyframe) {
                    tablesInfo[i].stss++;
                }

                if (info.sampleDuration > defaultSampleDuration[i]) {
                    defaultSampleDuration[i] = info.sampleDuration;
                }

                tablesInfo[i].stsz++;
                if (samplesSize != info.sampleSize) {
                    samplesSize = info.sampleSize;
                    sampleSizeChanges++;
                }

                if (info.hasCompositionTimeOffset) {
                    if (info.sampleCompositionTimeOffset != compositionOffsetLast) {
                        tablesInfo[i].ctts++;
                        compositionOffsetLast = info.sampleCompositionTimeOffset;
                    }
                }

                totalSampleSize += info.sampleSize;
            }
        }

        if (defaultMediaTime[i] < 1) {
            defaultMediaTime[i] = defaultSampleDuration[i];
        }

        readers[i].rewind();

        if (singleSampleBuffer > 0) {
            initChunkTables(tablesInfo[i], singleSampleBuffer, singleSampleBuffer);
        } else {
            initChunkTables(tablesInfo[i], SAMPLES_PER_CHUNK_INIT, SAMPLES_PER_CHUNK);
        }

        sampleCount[i] = tablesInfo[i].stsz;

        if (sampleSizeChanges == 1) {
            tablesInfo[i].stsz = 0;
            tablesInfo[i].stszDefault = samplesSize;
        } else {
            tablesInfo[i].stszDefault = 0;
        }

        if (tablesInfo[i].stss == tablesInfo[i].stsz) {
            tablesInfo[i].stss = -1; // for audio tracks (all samples are keyframes)
        }

        // ensure track duration
        if (tracks[i].trak.tkhd.duration < 1) {
            tracks[i].trak.tkhd.duration = sampleExtra[i]; // this never should happen
        }
    }

    final boolean is64 = read > THRESHOLD_FOR_CO64;

    // calculate the moov size
    final int auxSize = makeMoov(defaultMediaTime, tablesInfo, is64);

    if (auxSize < THRESHOLD_MOOV_LENGTH) {
        auxBuffer = ByteBuffer.allocate(auxSize); // cache moov in the memory
    }

    moovSimulation = false;
    writeOffset = 0;

    final int ftypSize = makeFtyp();

    // reserve moov space in the output stream
    if (auxSize > 0) {
        int length = auxSize;
        final byte[] buffer = new byte[64 * 1024]; // 64 KiB
        while (length > 0) {
            final int count = Math.min(length, buffer.length);
            outWrite(buffer, count);
            length -= count;
        }
    }

    if (auxBuffer == null) {
        outSeek(ftypSize);
    }

    // tablesInfo contains row counts
    // and after returning from makeMoov() will contain those table offsets
    makeMoov(defaultMediaTime, tablesInfo, is64);

    // write tables: stts stsc sbgp
    // reset for ctts table: sampleCount sampleExtra
    for (int i = 0; i < readers.length; i++) {
        writeEntryArray(tablesInfo[i].stts, 2, sampleCount[i], defaultSampleDuration[i]);
        writeEntryArray(
                tablesInfo[i].stsc, tablesInfo[i].stscBEntries.length, tablesInfo[i].stscBEntries);
        tablesInfo[i].stscBEntries = null;
        if (tablesInfo[i].ctts > 0) {
            sampleCount[i] = 1; // the index is not base zero
            sampleExtra[i] = -1;
        }
        if (tablesInfo[i].sbgp > 0) {
            writeEntryArray(tablesInfo[i].sbgp, 1, sampleCount[i]);
        }
    }

    if (auxBuffer == null) {
        outRestore();
    }

    outWrite(makeMdat(totalSampleSize, is64));

    final int[] sampleIndex = new int[readers.length];
    final int[] sizes = new int[singleSampleBuffer > 0 ? singleSampleBuffer : SAMPLES_PER_CHUNK];
    final int[] sync = new int[singleSampleBuffer > 0 ? singleSampleBuffer : SAMPLES_PER_CHUNK];

    int written = readers.length;
    while (written > 0) {
        written = 0;

        for (int i = 0; i < readers.length; i++) {
            if (sampleIndex[i] < 0) {
                continue; // track is done
            }

            final long chunkOffset = writeOffset;
            int syncCount = 0;
            final int limit;
            if (singleSampleBuffer > 0) {
                limit = singleSampleBuffer;
            } else {
                limit = sampleIndex[i] == 0 ? SAMPLES_PER_CHUNK_INIT : SAMPLES_PER_CHUNK;
            }

            int j = 0;
            for (; j < limit; j++) {
                final Mp4DashSample sample = getNextSample(i);

                if (sample == null) {
                    if (tablesInfo[i].ctts > 0 && sampleExtra[i] >= 0) {
                        writeEntryArray(
                                tablesInfo[i].ctts,
                                1,
                                sampleCount[i],
                                sampleExtra[i]); // flush last entries
                        outRestore();
                    }
                    sampleIndex[i] = -1;
                    break;
                }

                sampleIndex[i]++;

                if (tablesInfo[i].ctts > 0) {
                    if (sample.info.sampleCompositionTimeOffset == sampleExtra[i]) {
                        sampleCount[i]++;
                    } else {
                        if (sampleExtra[i] >= 0) {
                            tablesInfo[i].ctts =
                                    writeEntryArray(
                                            tablesInfo[i].ctts, 2, sampleCount[i], sampleExtra[i]);
                            outRestore();
                        }
                        sampleCount[i] = 1;
                        sampleExtra[i] = sample.info.sampleCompositionTimeOffset;
                    }
                }

                if (tablesInfo[i].stss > 0 && sample.info.isKeyframe) {
                    sync[syncCount++] = sampleIndex[i];
                }

                if (tablesInfo[i].stsz > 0) {
                    sizes[j] = sample.data.length;
                }

                outWrite(sample.data, sample.data.length);
            }

            if (j > 0) {
                written++;

                if (tablesInfo[i].stsz > 0) {
                    tablesInfo[i].stsz = writeEntryArray(tablesInfo[i].stsz, j, sizes);
                }

                if (syncCount > 0) {
                    tablesInfo[i].stss = writeEntryArray(tablesInfo[i].stss, syncCount, sync);
                }

                if (tablesInfo[i].stco > 0) {
                    if (is64) {
                        tablesInfo[i].stco = writeEntry64(tablesInfo[i].stco, chunkOffset);
                    } else {
                        tablesInfo[i].stco =
                                writeEntryArray(tablesInfo[i].stco, 1, (int) chunkOffset);
                    }
                }

                outRestore();
            }
        }
    }

    if (auxBuffer != null) {
        // dump moov
        outSeek(ftypSize);
        outStream.write(auxBuffer.array(), 0, auxBuffer.capacity());
        auxBuffer = null;
    }
}

@Override
public void run() {
    if (mMission.current > 0) {
        mMission.resetState(false, true, DownloadMission.ERROR_NOTHING);
    }

    int retryCount = 0;
    int httpCode = 204;

    while (true) {
        try {
            if (mMission.blocks == null && mMission.current == 0) {
                // calculate the whole size of the mission
                long finalLength = 0;
                long lowestSize = Long.MAX_VALUE;

                for (int i = 0; i < mMission.urls.length && mMission.running; i++) {
                    mConn = mMission.openConnection(mMission.urls[i], true, 0, 0);
                    mMission.establishConnection(mId, mConn);
                    dispose();

                    if (Thread.interrupted()) {
                        return;
                    }
                    final long length = Utility.getTotalContentLength(mConn);

                    if (i == 0) {
                        httpCode = mConn.getResponseCode();
                        mMission.length = length;
                    }

                    if (length > 0) {
                        finalLength += length;
                    }
                    if (length < lowestSize) {
                        lowestSize = length;
                    }
                }

                mMission.nearLength = finalLength;

                // reserve space at the start of the file
                if (mMission.psAlgorithm != null && mMission.psAlgorithm.reserveSpace) {
                    if (lowestSize < 1) {
                        // the length is unknown use the default size
                        mMission.offsets[0] = RESERVE_SPACE_DEFAULT;
                    } else {
                        // use the smallest resource size to download, otherwise, use the maximum
                        mMission.offsets[0] =
                                lowestSize < RESERVE_SPACE_MAXIMUM
                                        ? lowestSize
                                        : RESERVE_SPACE_MAXIMUM;
                    }
                }
            } else {
                // ask for the current resource length
                mConn = mMission.openConnection(true, 0, 0);
                mMission.establishConnection(mId, mConn);
                dispose();

                if (!mMission.running || Thread.interrupted()) {
                    return;
                }

                httpCode = mConn.getResponseCode();
                mMission.length = Utility.getTotalContentLength(mConn);
            }

            if (mMission.length == 0 || httpCode == 204) {
                mMission.notifyError(DownloadMission.ERROR_HTTP_NO_CONTENT, null);
                return;
            }

            // check for dynamic generated content
            if (mMission.length == -1 && mConn.getResponseCode() == 200) {
                mMission.blocks = new int[0];
                mMission.length = 0;
                mMission.unknownLength = true;

                if (DEBUG) {
                    Log.d(TAG, "falling back (unknown length)");
                }
            } else {
                // Open again
                mConn = mMission.openConnection(true, mMission.length - 10, mMission.length);
                mMission.establishConnection(mId, mConn);
                dispose();

                if (!mMission.running || Thread.interrupted()) {
                    return;
                }

                synchronized (mMission.LOCK) {
                    if (mConn.getResponseCode() == 206) {

                        if (mMission.threadCount > 1) {
                            int count = (int) (mMission.length / DownloadMission.BLOCK_SIZE);
                            if ((count * DownloadMission.BLOCK_SIZE) < mMission.length) {
                                count++;
                            }

                            mMission.blocks = new int[count];
                        } else {
                            // if one thread is required don't calculate blocks, is useless
                            mMission.blocks = new int[0];
                            mMission.unknownLength = false;
                        }

                        if (DEBUG) {
                            Log.d(TAG, "http response code = " + mConn.getResponseCode());
                        }
                    } else {
                        // Fallback to single thread
                        mMission.blocks = new int[0];
                        mMission.unknownLength = false;

                        if (DEBUG) {
                            Log.d(
                                    TAG,
                                    "falling back due http response code = "
                                            + mConn.getResponseCode());
                        }
                    }
                }

                if (!mMission.running || Thread.interrupted()) {
                    return;
                }
            }

            try (SharpStream fs = mMission.storage.getStream()) {
                fs.setLength(mMission.offsets[mMission.current] + mMission.length);
                fs.seek(mMission.offsets[mMission.current]);
            }

            if (!mMission.running || Thread.interrupted()) {
                return;
            }

            if (!mMission.unknownLength && mMission.recoveryInfo != null) {
                final String entityTag = mConn.getHeaderField("ETAG");
                final String lastModified = mConn.getHeaderField("Last-Modified");
                final MissionRecoveryInfo recovery = mMission.recoveryInfo[mMission.current];

                if (!TextUtils.isEmpty(entityTag)) {
                    recovery.setValidateCondition(entityTag);
                } else if (!TextUtils.isEmpty(lastModified)) {
                    // Note: this is less precise
                    recovery.setValidateCondition(lastModified);
                } else {
                    recovery.setValidateCondition(null);
                }
            }

            mMission.running = false;
            break;
        } catch (InterruptedIOException | ClosedByInterruptException e) {
            return;
        } catch (Exception e) {
            if (!mMission.running || super.isInterrupted()) {
                return;
            }

            if (e instanceof DownloadMission.HttpError
                    && ((DownloadMission.HttpError) e).statusCode == ERROR_HTTP_FORBIDDEN) {
                // for youtube streams. The url has expired
                interrupt();
                mMission.doRecover(ERROR_HTTP_FORBIDDEN);
                return;
            }

            if (e instanceof IOException && e.getMessage().contains("Permission denied")) {
                mMission.notifyError(DownloadMission.ERROR_PERMISSION_DENIED, e);
                return;
            }

            if (retryCount++ > mMission.maxRetry) {
                Log.e(TAG, "initializer failed", e);
                mMission.notifyError(e);
                return;
            }

            Log.e(TAG, "initializer failed, retrying", e);
        }
    }

    mMission.start();
}

private void showError(DownloadMission mission, UserAction action, @StringRes int reason) {
    StringBuilder request = new StringBuilder(256);
    request.append(mission.source);

    request.append(" [");
    if (mission.recoveryInfo != null) {
        for (MissionRecoveryInfo recovery : mission.recoveryInfo) {
            request.append(' ')
                    .append(recovery.toString())
                    .append(' ');
        }
    }
    request.append("]");

    String service;
    try {
        service = NewPipe.getServiceByUrl(mission.source).getServiceInfo().getName();
    } catch (Exception e) {
        service = ErrorInfo.SERVICE_NONE;
    }

    ErrorUtil.createNotification(
            mContext,
            new ErrorInfo(
                    ErrorInfo.Companion.throwableToStringList(mission.errObject),
                    action,
                    service,
                    request.toString(),
                    reason));
}

public void updateMission(Mission mission) {
    final ContentValues values = getValuesOfMission(Objects.requireNonNull(mission));
    final SQLiteDatabase database = getWritableDatabase();
    final String ts = String.valueOf(mission.timestamp);

    final int rowsAffected;

    if (mission instanceof FinishedMission) {
        if (mission.storage.isInvalid()) {
            rowsAffected =
                    database.update(
                            FINISHED_TABLE_NAME, values, KEY_TIMESTAMP + " = ?", new String[] {ts});
        } else {
            rowsAffected =
                    database.update(
                            FINISHED_TABLE_NAME,
                            values,
                            KEY_PATH + " = ?",
                            new String[] {mission.storage.getUri().toString()});
        }
    } else {
        throw new UnsupportedOperationException("DownloadMission");
    }

    if (rowsAffected != 1) {
        Log.e(
                "FinishedMissionStore",
                "Expected 1 row to be affected by update but got " + rowsAffected);
    }
}

private int make(final int type, final int extra, final int columns, final int rows)
        throws IOException {
    final byte base = 16;
    final int size = columns * rows * 4;
    int total = size + base;
    int offset = auxOffset();

    if (extra >= 0) {
        total += 4;
    }

    auxWrite(
            ByteBuffer.allocate(12)
                    .putInt(total)
                    .putInt(type)
                    .putInt(0x00) // default version & flags
                    .array());

    if (extra >= 0) {
        offset += 4;
        auxWrite(extra);
    }

    auxWrite(rows);
    auxSkip(size);

    return offset + base;
}

boolean runMissions() {
    synchronized (this) {
        if (mMissionsPending.size() < 1) {
            return false;
        }
        if (!canDownloadInCurrentNetwork()) {
            return false;
        }

        if (mPrefQueueLimit) {
            for (DownloadMission mission : mMissionsPending) {
                if (!mission.isFinished() && mission.running) {
                    return true;
                }
            }
        }

        boolean flag = false;
        for (DownloadMission mission : mMissionsPending) {
            if (mission.running || !mission.enqueued || mission.isFinished()) {
                continue;
            }

            resumeMission(mission);
            if (mission.errCode != DownloadMission.ERROR_NOTHING) {
                continue;
            }

            if (mPrefQueueLimit) {
                return true;
            }
            flag = true;
        }

        return flag;
    }
}

public void start() {
    if (running || isFinished() || urls.length < 1) {
        return;
    }

    // ensure that the previous state is completely paused.
    joinForThreads(10000);

    running = true;
    errCode = ERROR_NOTHING;

    if (hasInvalidStorage()) {
        notifyError(ERROR_FILE_CREATION, null);
        return;
    }

    if (current >= urls.length) {
        notifyFinished();
        return;
    }

    notify(DownloadManagerService.MESSAGE_RUNNING);

    if (urls[current] == null) {
        doRecover(ERROR_RESOURCE_GONE);
        return;
    }

    if (blocks == null) {
        initializer();
        return;
    }

    init = null;
    finishCount = 0;
    blockAcquired = new boolean[blocks.length];

    if (blocks.length < 1) {
        threads = new Thread[] {runAsync(1, new DownloadRunnableFallback(this))};
    } else {
        int remainingBlocks = 0;
        for (int block : blocks) {
            if (block >= 0) {
                remainingBlocks++;
            }
        }

        if (remainingBlocks < 1) {
            notifyFinished();
            return;
        }

        threads = new Thread[Math.min(threadCount, remainingBlocks)];

        for (int i = 0; i < threads.length; i++) {
            threads[i] = runAsync(i + 1, new DownloadRunnable(this, i));
        }
    }
}

@Override
protected void subscribeActual(CompletableObserver downstream) {
    final InnerCompletableCache inner = new InnerCompletableCache(downstream);
    downstream.onSubscribe(inner);

    if (add(inner)) {
        if (inner.isDisposed()) {
            remove(inner);
        }
        if (once.compareAndSet(false, true)) {
            source.subscribe(this);
        }
        return;
    }

    final Throwable ex = error;
    if (ex != null) {
        downstream.onError(ex);
    } else {
        downstream.onComplete();
    }
}

@Override
public boolean tryOnError(Throwable throwable) {
    if (throwable == null) {
        throwable = ExceptionHelper.createNullPointerException("onError called with a null Throwable.");
    }

    if (get() == DisposableHelper.DISPOSED) {
        return false;
    }

    final Disposable upstream = getAndSet(DisposableHelper.DISPOSED);
    if (upstream == DisposableHelper.DISPOSED) {
        return false;
    }

    try {
        downstream.onError(throwable);
    } finally {
        if (upstream != null) {
            upstream.dispose();
        }
    }
    return true;
}

@SuppressWarnings("unchecked")
public static <T, R> boolean tryScalarXMapSubscribe(
        ObservableSource<T> source,
        Observer<? super R> observer,
        Function<? super T, ? extends ObservableSource<? extends R>> mapper) {

    if (source instanceof Supplier) {
        final T value;
        try {
            value = ((Supplier<T>) source).get();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            EmptyDisposable.error(ex, observer);
            return true;
        }

        if (value == null) {
            EmptyDisposable.complete(observer);
            return true;
        }

        final ObservableSource<? extends R> nextSource;
        try {
            nextSource = Objects.requireNonNull(
                    mapper.apply(value), "The mapper returned a null ObservableSource");
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            EmptyDisposable.error(ex, observer);
            return true;
        }

        if (nextSource instanceof Supplier) {
            final R mappedValue;
            try {
                mappedValue = ((Supplier<R>) nextSource).get();
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                EmptyDisposable.error(ex, observer);
                return true;
            }

            if (mappedValue == null) {
                EmptyDisposable.complete(observer);
                return true;
            }

            final ScalarDisposable<R> sd = new ScalarDisposable<>(observer, mappedValue);
            observer.onSubscribe(sd);
            sd.run();
        } else {
            nextSource.subscribe(observer);
        }

        return true;
    }
    return false;
}

void drainLoop() {
    int missed = 1;

    final Observer<? super R> downstream = this.downstream;
    final AtomicInteger activeCount = active;
    final AtomicReference<SpscLinkedArrayQueue<R>> queueRef = queue;

    for (;;) {
        for (;;) {
            if (cancelled) {
                clear();
                return;
            }

            if (!delayErrors) {
                final Throwable ex = errors.get();
                if (ex != null) {
                    clear();
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            final boolean done = activeCount.get() == 0;
            final SpscLinkedArrayQueue<R> q = queueRef.get();
            final R item = q != null ? q.poll() : null;
            final boolean empty = item == null;

            if (done && empty) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (empty) {
                break;
            }

            downstream.onNext(item);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final SimplePlainQueue<Object> queue = this.queue;
    final Subscriber<? super Flowable<T>> downstream = this.downstream;
    UnicastProcessor<T> window = this.window;

    for (;;) {
        if (upstreamCancelled) {
            queue.clear();
            window = null;
            this.window = null;
        } else {
            final boolean isDone = done;
            final Object signal = queue.poll();
            final boolean isEmpty = signal == null;

            if (isDone && isEmpty) {
                final Throwable ex = error;
                if (ex != null) {
                    if (window != null) {
                        window.onError(ex);
                    }
                    downstream.onError(ex);
                } else {
                    if (window != null) {
                        window.onComplete();
                    }
                    downstream.onComplete();
                }
                cleanupResources();
                upstreamCancelled = true;
                continue;
            } else if (!isEmpty) {
                if (signal instanceof WindowBoundaryRunnable) {
                    final WindowBoundaryRunnable boundary = (WindowBoundaryRunnable) signal;
                    if (boundary.index == emitted || !restartTimerOnMaxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    }
                } else if (window != null) {
                    @SuppressWarnings("unchecked")
                    final T item = (T) signal;
                    window.onNext(item);

                    final long c = this.count + 1;
                    if (c == maxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    } else {
                        this.count = c;
                    }
                }
                continue;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    final Subscriber<? super R> downstream = this.downstream;
    Iterator<? extends R> itLocal = this.it;

    if (outputFused && itLocal != null) {
        downstream.onNext(null);
        downstream.onComplete();
        return;
    }

    int missed = 1;

    for (;;) {
        if (itLocal != null) {
            final long requestedCount = requested.get();

            if (requestedCount == Long.MAX_VALUE) {
                fastPath(downstream, itLocal);
                return;
            }

            long emittedCount = 0L;

            while (emittedCount != requestedCount) {
                if (cancelled) {
                    return;
                }

                final R value;
                try {
                    value = Objects.requireNonNull(itLocal.next(), "The iterator returned a null value");
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    downstream.onError(ex);
                    return;
                }

                downstream.onNext(value);

                if (cancelled) {
                    return;
                }

                emittedCount++;

                final boolean hasMore;
                try {
                    hasMore = itLocal.hasNext();
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    downstream.onError(ex);
                    return;
                }

                if (!hasMore) {
                    downstream.onComplete();
                    return;
                }
            }

            if (emittedCount != 0L) {
                BackpressureHelper.produced(requested, emittedCount);
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }

        if (itLocal == null) {
            itLocal = it;
        }
    }
}

void drainLoop() {
    final Subscriber<? super T> downstream = this.downstream;

    int missed = 1;
    long emittedCount = emitted;
    int consumedCount = consumed;
    final int requestLimit = limit;

    for (;;) {
        final long requestedCount = requested.get();

        while (emittedCount != requestedCount) {
            if (cancelled) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(downstream);
                return;
            }

            int other = otherState;
            if (other == OTHER_STATE_HAS_VALUE) {
                final T v = singleItem;
                singleItem = null;
                otherState = OTHER_STATE_CONSUMED_OR_EMPTY;
                other = OTHER_STATE_CONSUMED_OR_EMPTY;

                downstream.onNext(v);
                emittedCount++;
                continue;
            }

            final boolean mainDoneLocal = mainDone;
            final SimplePlainQueue<T> q = queue;
            final T v = q != null ? q.poll() : null;
            final boolean empty = v == null;

            if (mainDoneLocal && empty && other == OTHER_STATE_CONSUMED_OR_EMPTY) {
                queue = null;
                downstream.onComplete();
                return;
            }

            if (empty) {
                break;
            }

            downstream.onNext(v);
            emittedCount++;

            if (++consumedCount == requestLimit) {
                consumedCount = 0;
                mainSubscription.get().request(requestLimit);
            }
        }

        if (emittedCount == requestedCount) {
            if (cancelled) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(downstream);
                return;
            }

            final boolean mainDoneLocal = mainDone;
            final SimplePlainQueue<T> q = queue;
            final boolean empty = q == null || q.isEmpty();

            if (mainDoneLocal && empty && otherState == OTHER_STATE_CONSUMED_OR_EMPTY) {
                queue = null;
                downstream.onComplete();
                return;
            }
        }

        emitted = emittedCount;
        consumed = consumedCount;

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;

    final Subscriber<? super R> downstream = this.downstream;
    final ErrorMode errorMode = this.errorMode;
    final SimpleQueue<T> queue = this.queue;
    final AtomicThrowable errors = this.errors;
    final AtomicLong requested = this.requested;
    final int limit = prefetch - (prefetch >> 1);
    final boolean syncFused = this.syncFused;

    for (;;) {
        for (;;) {
            if (cancelled) {
                queue.clear();
                item = null;
                break;
            }

            final int s = state;

            if (errors.get() != null) {
                if (errorMode == ErrorMode.IMMEDIATE
                        || (errorMode == ErrorMode.BOUNDARY && s == STATE_INACTIVE)) {
                    queue.clear();
                    item = null;
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            if (s == STATE_INACTIVE) {
                final boolean d = done;
                final T v;
                try {
                    v = queue.poll();
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    upstream.cancel();
                    errors.tryAddThrowableOrReport(ex);
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
                final boolean empty = v == null;

                if (d && empty) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }

                if (empty) {
                    break;
                }

                if (!syncFused) {
                    final int c = consumed + 1;
                    if (c == limit) {
                        consumed = 0;
                        upstream.request(limit);
                    } else {
                        consumed = c;
                    }
                }

                final MaybeSource<? extends R> ms;
                try {
                    ms = Objects.requireNonNull(
                            mapper.apply(v), "The mapper returned a null MaybeSource");
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    upstream.cancel();
                    queue.clear();
                    errors.tryAddThrowableOrReport(ex);
                    errors.tryTerminateConsumer(downstream);
                    return;
                }

                state = STATE_ACTIVE;
                ms.subscribe(inner);
                break;
            } else if (s == STATE_RESULT_VALUE) {
                final long e = emitted;
                if (e != requested.get()) {
                    final R w = item;
                    item = null;

                    downstream.onNext(w);

                    emitted = e + 1;
                    state = STATE_INACTIVE;
                } else {
                    break;
                }
            } else {
                break;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
void remove(@NonNull SingleDisposable<T> inner) {
    for (;;) {
        final SingleDisposable<T>[] current = observers.get();
        final int n = current.length;

        if (n == 0) {
            return;
        }

        int idx = -1;
        for (int i = 0; i < n; i++) {
            if (current[i] == inner) {
                idx = i;
                break;
            }
        }

        if (idx < 0) {
            return;
        }

        final SingleDisposable<T>[] next;
        if (n == 1) {
            next = EMPTY;
        } else {
            next = new SingleDisposable[n - 1];
            System.arraycopy(current, 0, next, 0, idx);
            System.arraycopy(current, idx + 1, next, idx, n - idx - 1);
        }

        if (observers.compareAndSet(current, next)) {
            return;
        }
    }
}

@Override
protected void subscribeActual(@NonNull Subscriber<? super T> subscriber) {
    final AsyncSubscription<T> subscription = new AsyncSubscription<>(subscriber, this);
    subscriber.onSubscribe(subscription);

    if (add(subscription)) {
        if (subscription.isCancelled()) {
            remove(subscription);
        }
        return;
    }

    final Throwable ex = error;
    if (ex != null) {
        subscriber.onError(ex);
        return;
    }

    final T v = value;
    if (v != null) {
        subscription.complete(v);
    } else {
        subscription.onComplete();
    }
}

private static EmailContent extractEmailContentAdvanced(byte[] emlBytes, EmlToPdfRequest request) {
  try {
    // Use Jakarta Mail for processing
    Class<?> sessionClass = Class.forName("jakarta.mail.Session");
    Class<?> mimeMessageClass = Class.forName("jakarta.mail.internet.MimeMessage");

    Method getDefaultInstance = sessionClass.getMethod("getDefaultInstance", Properties.class);
    Object session = getDefaultInstance.invoke(null, new Properties());

    // Cast the session object to the proper type for the constructor
    Class<?>[] constructorArgs = new Class<?>[] { sessionClass, InputStream.class };
    Constructor<?> mimeMessageConstructor = mimeMessageClass.getConstructor(constructorArgs);
    Object message = mimeMessageConstructor.newInstance(session, new ByteArrayInputStream(emlBytes));

    return extractEmailContentAdvanced(message, request);

  } catch (ReflectiveOperationException e) {
    // Create basic EmailContent from basic processing
    EmailContent content = new EmailContent();
    content.setHtmlBody(convertEmlToHtmlBasic(emlBytes, request));
    return content;
  }
}

private static void addAttachmentToInfo(StringBuilder attachmentInfo, String filename, String contentType, String encoding) {
  // Create attachment info with paperclip emoji before filename
  attachmentInfo
      .append("<div class=\"attachment-item\">")
      .append("<span class=\"attachment-icon\">")
      .append(MimeConstants.ATTACHMENT_MARKER)
      .append("</span> ")
      .append("<span class=\"attachment-name\">")
      .append(escapeHtml(filename))
      .append("</span>");

  // Add content type and encoding info
  if (!contentType.isEmpty() || !encoding.isEmpty()) {
    attachmentInfo.append(" <span class=\"attachment-details\">(");
    if (!contentType.isEmpty()) {
      attachmentInfo.append(escapeHtml(contentType));
    }
    if (!encoding.isEmpty()) {
      if (!contentType.isEmpty()) {
        attachmentInfo.append(", ");
      }
      attachmentInfo.append("encoding: ").append(escapeHtml(encoding));
    }
    attachmentInfo.append(")</span>");
  }
  attachmentInfo.append("</div>\n");
}

private static void processPartAdvanced(Object part, EmailContent content, EmlToPdfRequest request) {
  try {
    if (!isValidJakartaMailPart(part)) {
      log.warn("Invalid Jakarta Mail part type: {}", part.getClass().getName());
      return;
    }

    Class<?> partClass = part.getClass();
    Method isMimeType = partClass.getMethod("isMimeType", String.class);
    Method getContent = partClass.getMethod("getContent");
    Method getDisposition = partClass.getMethod("getDisposition");
    Method getFileName = partClass.getMethod("getFileName");
    Method getContentType = partClass.getMethod("getContentType");
    Method getHeader = partClass.getMethod("getHeader", String.class);

    Object disposition = getDisposition.invoke(part);
    String filename = (String) getFileName.invoke(part);
    String contentType = (String) getContentType.invoke(part);

    if ((Boolean) isMimeType.invoke(part, "text/plain") && disposition == null) {
      content.setTextBody((String) getContent.invoke(part));

    } else if ((Boolean) isMimeType.invoke(part, "text/html") && disposition == null) {
      content.setHtmlBody((String) getContent.invoke(part));

    } else if ("attachment".equalsIgnoreCase((String) disposition)
        || (filename != null && !filename.trim().isEmpty())) {

      content.setAttachmentCount(content.getAttachmentCount() + 1);

      // Always extract basic attachment metadata for display
      if (filename != null && !filename.trim().isEmpty()) {
        // Create attachment with metadata only
        EmailAttachment attachment = new EmailAttachment();
        // Apply MIME decoding to filename to handle encoded attachment names
        attachment.setFilename(safeMimeDecode(filename));
        attachment.setContentType(contentType);

        // Check if it's an embedded image
        String[] contentIdHeaders = (String[]) getHeader.invoke(part, "Content-ID");
        if (contentIdHeaders != null && contentIdHeaders.length > 0) {
          attachment.setEmbedded(true);
          // Store the Content-ID, removing angle brackets if present
          String contentId = contentIdHeaders[0];
          if (contentId.startsWith("<") && contentId.endsWith(">")) {
            contentId = contentId.substring(1, contentId.length() - 1);
          }
          attachment.setContentId(contentId);
        }

        // Extract attachment data if attachments should be included OR if it's an embedded image
        // (needed for inline display)
        if ((request != null && request.isIncludeAttachments()) || attachment.isEmbedded()) {
          try {
            Object attachmentContent = getContent.invoke(part);
            byte[] attachmentData = null;

            if (attachmentContent instanceof java.io.InputStream inputStream) {
              try {
                attachmentData = inputStream.readAllBytes();
              } catch (IOException e) {
                log.warn("Failed to read InputStream attachment: {}", e.getMessage());
              }
            } else if (attachmentContent instanceof byte[] byteArray) {
              attachmentData = byteArray;
            } else if (attachmentContent instanceof String stringContent) {
              attachmentData = stringContent.getBytes(StandardCharsets.UTF_8);
            }

            if (attachmentData != null) {
              // Check size limit (use default 10MB if request is null)
              long maxSizeMB = (request != null) ? request.getMaxAttachmentSizeMB() : 10L;
              long maxSizeBytes = maxSizeMB * 1024 * 1024;

              if (attachmentData.length <= maxSizeBytes) {
                attachment.setData(attachmentData);
                attachment.setSizeBytes(attachmentData.length);
              } else {
                // For embedded images, always include data regardless of size to ensure inline display works
                if (attachment.isEmbedded()) {
                  attachment.setData(attachmentData);
                  attachment.setSizeBytes(attachmentData.length);
                } else {
                  // Still show attachment info even if too large
                  attachment.setSizeBytes(attachmentData.length);
                }
              }
            }
          } catch (Exception e) {
            log.warn("Error extracting attachment data: {}", e.getMessage());
          }
        }

        // Add attachment to the list for display (with or without data)
        content.getAttachments().add(attachment);
      }

    } else if ((Boolean) isMimeType.invoke(part, "multipart/*")) {
      // Handle nested multipart content
      try {
        Object multipartContent = getContent.invoke(part);
        Class<?> multipartClass = Class.forName("jakarta.mail.Multipart");
        if (multipartClass.isInstance(multipartContent)) {
          processMultipartAdvanced(multipartContent, content, request);
        }
      } catch (Exception e) {
        log.warn("Error processing multipart content: {}", e.getMessage());
      }
    }

  } catch (Exception e) {
    log.warn("Error processing multipart part: {}", e.getMessage());
  }
}

@PostMapping(consumes = "multipart/form-data", value = "/eml/pdf")
@Operation(
    summary = "Convert EML to PDF",
    description = "This endpoint converts EML (email) files to PDF format with extensive"
        + " customization options. Features include font settings, image constraints, display modes, attachment handling,"
        + " and HTML debug output. Input: EML file, Output: PDF"
        + " or HTML file. Type: SISO")
public ResponseEntity<byte[]> convertEmlToPdf(@ModelAttribute EmlToPdfRequest request) {

  MultipartFile inputFile = request.getFileInput();
  String originalFilename = inputFile.getOriginalFilename();

  // Validate input
  if (inputFile.isEmpty()) {
    log.error("No file provided for EML to PDF conversion.");
    return ResponseEntity.badRequest().body("No file provided".getBytes(StandardCharsets.UTF_8));
  }

  if (originalFilename == null || originalFilename.trim().isEmpty()) {
    log.error("Filename is null or empty.");
    return ResponseEntity.badRequest().body("Please provide a valid filename".getBytes(StandardCharsets.UTF_8));
  }

  // Validate file type - support EML
  String lowerFilename = originalFilename.toLowerCase();
  if (!lowerFilename.endsWith(".eml")) {
    log.error("Invalid file type for EML to PDF: {}", originalFilename);
    return ResponseEntity.badRequest().body("Please upload a valid EML file".getBytes(StandardCharsets.UTF_8));
  }

  String baseFilename = Filenames.toSimpleFileName(originalFilename); // Use Filenames utility

  try {
    byte[] fileBytes = inputFile.getBytes();

    if (request.isDownloadHtml()) {
      try {
        String htmlContent = EmlToPdf.convertEmlToHtml(fileBytes, request);
        log.info("Successfully converted EML to HTML: {}", originalFilename);
        return WebResponseUtils.bytesToWebResponse(
            htmlContent.getBytes(StandardCharsets.UTF_8),
            baseFilename + ".html",
            MediaType.TEXT_HTML);
      } catch (IOException | IllegalArgumentException e) {
        log.error("HTML conversion failed for {}", originalFilename, e);
        return ResponseEntity
            .status(HttpStatus.INTERNAL_SERVER_ERROR)
            .body(("HTML conversion failed: " + e.getMessage()).getBytes(StandardCharsets.UTF_8));
      }
    }

    // Convert EML to PDF with enhanced options
    try {
      byte[] pdfBytes = EmlToPdf.convertEmlToPdf(
          runtimePathConfig.getWeasyPrintPath(), // Use configured WeasyPrint path
          request,
          fileBytes,
          originalFilename,
          false,
          pdfDocumentFactory,
          tempFileManager);

      if (pdfBytes == null || pdfBytes.length == 0) {
        log.error("PDF conversion failed - empty output for {}", originalFilename);
        return ResponseEntity
            .status(HttpStatus.INTERNAL_SERVER_ERROR)
            .body("PDF conversion failed - empty output".getBytes(StandardCharsets.UTF_8));
      }
      log.info("Successfully converted EML to PDF: {}", originalFilename);
      return WebResponseUtils.bytesToWebResponse(pdfBytes, baseFilename + ".pdf, MediaType.APPLICATION_PDF");

    } catch (InterruptedException e) {
      Thread.currentThread().interrupt();
      log.error("EML to PDF conversion was interrupted for {}", originalFilename, e);
      return ResponseEntity
          .status(HttpStatus.INTERNAL_SERVER_ERROR)
          .body("Conversion was interrupted".getBytes(StandardCharsets.UTF_8));
    } catch (IllegalArgumentException e) {
      String errorMessage = buildErrorMessage(e, originalFilename);
      log.error("EML to PDF conversion failed for {}: {}", originalFilename, errorMessage, e);
      return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).body(errorMessage.getBytes(StandardCharsets.UTF_8));
    } catch (RuntimeException e) {
      String errorMessage = buildErrorMessage(e, originalFilename);
      log.error("EML to PDF conversion failed for {}: {}", originalFilename, errorMessage, e);
      return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).body(errorMessage.getBytes(StandardCharsets.UTF_8));
    }

  } catch (IOException e) {
    log.error("File processing error for EML to PDF: {}", originalFilename, e);
    return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR).body("File processing error".getBytes(StandardCharsets.UTF_8));
  }
}

@Override
protected void subscribeActual(final Subscriber<? super T> s) {
    PublishConnection<T> conn;

    for (;;) {
        conn = current.get();

        // don't create a fresh connection if the current is disposed
        if (conn == null) {
            final PublishConnection<T> fresh = new PublishConnection<>(current, bufferSize);
            if (!current.compareAndSet(conn, fresh)) {
                continue;
            }
            conn = fresh;
        }

        break;
    }

    final InnerSubscription<T> inner = new InnerSubscription<>(s, conn);
    s.onSubscribe(inner);

    if (conn.add(inner)) {
        if (inner.isCancelled()) {
            conn.remove(inner);
        } else {
            conn.drain();
        }
        return;
    }

    final Throwable ex = conn.error;
    if (ex != null) {
        inner.downstream.onError(ex);
    } else {
        inner.downstream.onComplete();
    }
}

@Override
Node getHead() {
    final long timeLimit = scheduler.now(unit) - maxAge;

    Node prev = get();
    Node next = prev.get();

    for (;;) {
        if (next == null) {
            break;
        }

        final Timed<?> v = (Timed<?>) next.value;

        if (NotificationLite.isComplete(v.value()) || NotificationLite.isError(v.value())) {
            break;
        }

        if (v.time() <= timeLimit) {
            prev = next;
            next = next.get();
        } else {
            break;
        }
    }

    return prev;
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;

    final Subscriber<? super R> downstream = this.downstream;
    final AtomicThrowable errors = this.errors;
    final AtomicReference<SwitchMapMaybeObserver<R>> inner = this.inner;
    final AtomicLong requested = this.requested;

    long emitted = this.emitted;

    for (;;) {

        for (;;) {
            if (cancelled) {
                return;
            }

            if (errors.get() != null) {
                if (!delayErrors) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            final boolean d = done;
            final SwitchMapMaybeObserver<R> current = inner.get();
            final boolean empty = current == null;

            if (d && empty) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (empty || current.item == null || emitted == requested.get()) {
                break;
            }

            inner.compareAndSet(current, null);

            downstream.onNext(current.item);

            emitted++;
        }

        this.emitted = emitted;

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void run() {
    for (;;) {
        if (disposed) {
            queue.clear();
            return;
        }

        if (!active) {

            final boolean d = done;

            T t;

            try {
                t = queue.poll();
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                dispose();
                queue.clear();
                downstream.onError(ex);
                worker.dispose();
                return;
            }

            final boolean empty = t == null;

            if (d && empty) {
                disposed = true;
                downstream.onComplete();
                worker.dispose();
                return;
            }

            if (!empty) {
                final ObservableSource<? extends U> o;
                try {
                    o = Objects.requireNonNull(mapper.apply(t), "The mapper returned a null ObservableSource");
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    dispose();
                    queue.clear();
                    downstream.onError(ex);
                    worker.dispose();
                    return;
                }

                active = true;
                o.subscribe(inner);
            }
        }

        if (decrementAndGet() == 0) {
            break;
        }
    }
}

@Override
void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;

    final Observer<? super R> downstream = this.downstream;
    final ErrorMode errorMode = this.errorMode;
    final SimpleQueue<T> queue = this.queue;
    final AtomicThrowable errors = this.errors;

    for (;;) {

        for (;;) {
            if (disposed) {
                queue.clear();
                item = null;
                break;
            }

            final int s = state;

            if (errors.get() != null) {
                if (errorMode == ErrorMode.IMMEDIATE
                        || (errorMode == ErrorMode.BOUNDARY && s == STATE_INACTIVE)) {
                    queue.clear();
                    item = null;
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            if (s == STATE_INACTIVE) {
                final boolean d = done;

                T v;

                try {
                    v = queue.poll();
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    disposed = true;
                    upstream.dispose();
                    errors.tryAddThrowableOrReport(ex);
                    errors.tryTerminateConsumer(downstream);
                    return;
                }

                final boolean empty = v == null;

                if (d && empty) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }

                if (empty) {
                    break;
                }

                final MaybeSource<? extends R> ms;

                try {
                    ms = Objects.requireNonNull(mapper.apply(v), "The mapper returned a null MaybeSource");
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    upstream.dispose();
                    queue.clear();
                    errors.tryAddThrowableOrReport(ex);
                    errors.tryTerminateConsumer(downstream);
                    return;
                }

                state = STATE_ACTIVE;
                ms.subscribe(inner);
                break;
            } else if (s == STATE_RESULT_VALUE) {
                final R w = item;
                item = null;
                downstream.onNext(w);

                state = STATE_INACTIVE;
            } else {
                break;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void run() {

    for (;;) {
        if (cancelled) {
            return;
        }

        if (!active) {

            final boolean d = done;

            if (d && !veryEnd) {
                final Throwable ex = errors.get();
                if (ex != null) {
                    errors.tryTerminateConsumer(downstream);
                    worker.dispose();
                    return;
                }
            }

            T v;

            try {
                v = queue.poll();
            } catch (Throwable e) {
                Exceptions.throwIfFatal(e);
                upstream.cancel();
                errors.tryAddThrowableOrReport(e);
                errors.tryTerminateConsumer(downstream);
                worker.dispose();
                return;
            }

            final boolean empty = v == null;

            if (d && empty) {
                errors.tryTerminateConsumer(downstream);
                worker.dispose();
                return;
            }

            if (!empty) {
                final Publisher<? extends R> p;

                try {
                    p = Objects.requireNonNull(mapper.apply(v), "The mapper returned a null Publisher");
                } catch (Throwable e) {
                    Exceptions.throwIfFatal(e);

                    upstream.cancel();
                    errors.tryAddThrowableOrReport(e);
                    errors.tryTerminateConsumer(downstream);
                    worker.dispose();
                    return;
                }

                if (sourceMode != QueueSubscription.SYNC) {
                    final int c = consumed + 1;
                    if (c == limit) {
                        consumed = 0;
                        upstream.request(c);
                    } else {
                        consumed = c;
                    }
                }

                if (p instanceof Supplier) {
                    @SuppressWarnings("unchecked")
                    final Supplier<R> supplier = (Supplier<R>) p;

                    R vr;

                    try {
                        vr = supplier.get();
                    } catch (Throwable e) {
                        Exceptions.throwIfFatal(e);
                        errors.tryAddThrowableOrReport(e);
                        if (!veryEnd) {
                            upstream.cancel();
                            errors.tryTerminateConsumer(downstream);
                            worker.dispose();
                            return;
                        }
                        vr = null;
                    }

                    if (vr == null || cancelled) {
                        continue;
                    }

                    if (inner.isUnbounded()) {
                        downstream.onNext(vr);
                        continue;
                    } else {
                        active = true;
                        inner.setSubscription(new SimpleScalarSubscription<>(vr, inner));
                    }
                } else {
                    active = true;
                    p.subscribe(inner);
                }
            }
        }
        if (decrementAndGet() == 0) {
            break;
        }
    }
}

void drain() {
    if (wip.getAndIncrement() != 0) {
        return;
    }

    int missed = 1;

    SimpleQueue<T> q = queue;

    int upstreamConsumed = consumed;
    final int localLimit = limit;
    final boolean canRequest = sourceMode != QueueSubscription.SYNC;
    final AtomicReference<MulticastSubscription<T>[]> subs = subscribers;

    MulticastSubscription<T>[] array = subs.get();

    outer:
    for (;;) {

        int n = array.length;

        if (q != null && n != 0) {
            long r = Long.MAX_VALUE;

            for (final MulticastSubscription<T> ms : array) {
                final long u = ms.get() - ms.emitted;
                if (u != Long.MIN_VALUE) {
                    if (r > u) {
                        r = u;
                    }
                } else {
                    n--;
                }
            }

            if (n == 0) {
                r = 0;
            }

            while (r != 0) {
                if (isDisposed()) {
                    q.clear();
                    return;
                }

                final boolean d = done;

                if (d && !delayError) {
                    final Throwable ex = error;
                    if (ex != null) {
                        errorAll(ex);
                        return;
                    }
                }

                T v;

                try {
                    v = q.poll();
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    SubscriptionHelper.cancel(upstream);
                    errorAll(ex);
                    return;
                }

                final boolean empty = v == null;

                if (d && empty) {
                    final Throwable ex = error;
                    if (ex != null) {
                        errorAll(ex);
                    } else {
                        completeAll();
                    }
                    return;
                }

                if (empty) {
                    break;
                }

                boolean subscribersChange = false;

                for (final MulticastSubscription<T> ms : array) {
                    final long msr = ms.get();
                    if (msr != Long.MIN_VALUE) {
                        if (msr != Long.MAX_VALUE) {
                            ms.emitted++;
                        }
                        ms.downstream.onNext(v);
                    } else {
                        subscribersChange = true;
                    }
                }

                r--;

                if (canRequest && ++upstreamConsumed == localLimit) {
                    upstreamConsumed = 0;
                    upstream.get().request(localLimit);
                }

                final MulticastSubscription<T>[] freshArray = subs.get();
                if (subscribersChange || freshArray != array) {
                    array = freshArray;
                    continue outer;
                }
            }

            if (r == 0) {
                if (isDisposed()) {
                    q.clear();
                    return;
                }

                final boolean d = done;

                if (d && !delayError) {
                    final Throwable ex = error;
                    if (ex != null) {
                        errorAll(ex);
                        return;
                    }
                }

                if (d && q.isEmpty()) {
                    final Throwable ex = error;
                    if (ex != null) {
                        errorAll(ex);
                    } else {
                        completeAll();
                    }
                    return;
                }
            }
        }

        consumed = upstreamConsumed;

        missed = wip.addAndGet(-missed);
        if (missed == 0) {
            break;
        }

        if (q == null) {
            q = queue;
        }
        array = subs.get();
    }
}

void drain() {

    if (getAndIncrement() != 0) {
        return;
    }

    final Subscriber<? super R> a = downstream;
    final ZipSubscriber<T, R>[] qs = subscribers;
    final int n = qs.length;
    final Object[] values = current;

    int missed = 1;

    for (;;) {

        final long r = requested.get();
        long e = 0L;

        while (r != e) {

            if (cancelled) {
                return;
            }

            if (!delayErrors && errors.get() != null) {
                cancelAll();
                errors.tryTerminateConsumer(a);
                return;
            }

            boolean empty = false;

            for (int j = 0; j < n; j++) {
                final ZipSubscriber<T, R> inner = qs[j];

                if (values[j] == null) {
                    boolean d = inner.done;
                    final SimpleQueue<T> q = inner.queue;
                    T v = null;

                    try {
                        v = q != null ? q.poll() : null;
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        errors.tryAddThrowableOrReport(ex);
                        if (!delayErrors) {
                            cancelAll();
                            errors.tryTerminateConsumer(a);
                            return;
                        }
                        d = true;
                    }

                    final boolean sourceEmpty = v == null;
                    if (d && sourceEmpty) {
                        cancelAll();
                        errors.tryTerminateConsumer(a);
                        return;
                    }
                    if (!sourceEmpty) {
                        values[j] = v;
                    } else {
                        empty = true;
                    }
                }
            }

            if (empty) {
                break;
            }

            final R v;
            try {
                v = Objects.requireNonNull(zipper.apply(values.clone()), "The zipper returned a null value");
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                cancelAll();
                errors.tryAddThrowableOrReport(ex);
                errors.tryTerminateConsumer(a);
                return;
            }

            a.onNext(v);

            e++;

            Arrays.fill(values, null);
        }

        if (r == e) {
            if (cancelled) {
                return;
            }

            if (!delayErrors && errors.get() != null) {
                cancelAll();
                errors.tryTerminateConsumer(a);
                return;
            }

            for (int j = 0; j < n; j++) {
                final ZipSubscriber<T, R> inner = qs[j];

                if (values[j] == null) {
                    boolean d = inner.done;
                    final SimpleQueue<T> q = inner.queue;
                    T v = null;

                    try {
                        v = q != null ? q.poll() : null;
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        errors.tryAddThrowableOrReport(ex);
                        if (!delayErrors) {
                            cancelAll();
                            errors.tryTerminateConsumer(a);
                            return;
                        }
                        d = true;
                    }

                    final boolean empty = v == null;
                    if (d && empty) {
                        cancelAll();
                        errors.tryTerminateConsumer(a);
                        return;
                    }
                    if (!empty) {
                        values[j] = v;
                    }
                }
            }

        }

        if (e != 0L) {

            for (final ZipSubscriber<T, R> inner : qs) {
                inner.request(e);
            }

            if (r != Long.MAX_VALUE) {
                requested.addAndGet(-e);
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void innerSuccess(final InnerObserver inner, final R value) {
    set.delete(inner);

    if (get() == 0 && compareAndSet(0, 1)) {
        downstream.onNext(value);

        final boolean d = active.decrementAndGet() == 0;
        final SpscLinkedArrayQueue<R> q = queue.get();

        if (d && (q == null || q.isEmpty())) {
            errors.tryTerminateConsumer(downstream);
            return;
        }
        if (decrementAndGet() == 0) {
            return;
        }
    } else {
        final SpscLinkedArrayQueue<R> q = getOrCreateQueue();
        synchronized (q) {
            q.offer(value);
        }
        active.decrementAndGet();
        if (getAndIncrement() != 0) {
            return;
        }
    }

    drainLoop();
}

@Override
public void onSubscribe(final Subscription s) {
    boolean cancel;

    if (!done) {
        synchronized (this) {
            if (done) {
                cancel = true;
            } else {
                if (emitting) {
                    AppendOnlyLinkedArrayList<Object> q = queue;
                    if (q == null) {
                        q = new AppendOnlyLinkedArrayList<>(4);
                        queue = q;
                    }
                    q.add(NotificationLite.subscription(s));
                    return;
                }
                emitting = true;
                cancel = false;
            }
        }
    } else {
        cancel = true;
    }

    if (cancel) {
        s.cancel();
    } else {
        actual.onSubscribe(s);
        emitLoop();
    }
}

@Override
public void close() throws IOException {
    boolean triedToClose = false;
    boolean success = false;
    try {
        flush();
        ((FileOutputStream) out).getFD().sync();

        triedToClose = true;
        super.close();
        success = true;
    } finally {
        if (success) {
            boolean renamed = tmpFile.renameTo(origFile);
            if (!renamed) {
                // On windows, renameTo does not replace.
                if (!origFile.delete() || !tmpFile.renameTo(origFile)) {
                    throw new IOException("Could not rename temporary file " + tmpFile + " to " + origFile);
                }
            }
        } else {
            if (!triedToClose) {
                // If we failed when flushing, try to close it to not leak an FD
                IOUtils.closeStream(out);
            }
            // close wasn't successful, try to delete the tmp file
            if (!tmpFile.delete()) {
                LOG.warn("Unable to delete tmp file {}", tmpFile);
            }
        }
    }
}

void handleWrite(SelectionKey k) throws IOException {
    if (outgoingBuffers.isEmpty()) {
        return;
    }

    /*
     * This is going to reset the buffer position to 0 and the limit to the size of the buffer,
     * so that we can fill it with data from the non-direct buffers that we need to send.
     */
    ByteBuffer directBuffer = NIOServerCnxnFactory.getDirectBuffer();
    if (directBuffer == null) {
        ByteBuffer[] bufferList = new ByteBuffer[outgoingBuffers.size()];
        // Use gathered write call. This updates the positions of the byte buffers to reflect the bytes that were written out.
        sock.write(outgoingBuffers.toArray(bufferList));

        // Remove the buffers that we have sent
        ByteBuffer bb;
        while ((bb = outgoingBuffers.peek()) != null) {
            if (bb == ServerCnxnFactory.closeConn) {
                throw new CloseRequestException("close requested", DisconnectReason.CLIENT_CLOSED_CONNECTION);
            }
            if (bb == packetSentinel) {
                packetSent();
            }
            if (bb.remaining() > 0) {
                break;
            }
            outgoingBuffers.remove();
        }
    } else {
        directBuffer.clear();

        for (ByteBuffer b : outgoingBuffers) {
            if (directBuffer.remaining() < b.remaining()) {
                /*
                 * When we call put later, if the directBuffer is too small to hold everything, nothing will be copied,
                 * so we've got to slice the buffer if it's too big.
                 */
                b = (ByteBuffer) b.slice().limit(directBuffer.remaining());
            }
            /*
             * put() is going to modify the positions of both buffers, but we don't want to change the position of
             * the source buffers (we'll do that after the send, if needed), so we save and reset the position after the copy
             */
            int p = b.position();
            directBuffer.put(b);
            b.position(p);
            if (directBuffer.remaining() == 0) {
                break;
            }
        }
        /*
         * Do the flip: limit becomes position, position gets set to 0. This sets us up for the write.
         */
        directBuffer.flip();

        int sent = sock.write(directBuffer);

        ByteBuffer bb;

        // Remove the buffers that we have sent
        while ((bb = outgoingBuffers.peek()) != null) {
            if (bb == ServerCnxnFactory.closeConn) {
                throw new CloseRequestException("close requested", DisconnectReason.CLIENT_CLOSED_CONNECTION);
            }
            if (bb == packetSentinel) {
                packetSent();
            }
            if (sent < bb.remaining()) {
                /*
                 * We only partially sent this buffer, so we update the position and exit the loop.
                 */
                bb.position(bb.position() + sent);
                break;
            }
            // We've sent the whole buffer, so drop the buffer
            sent -= bb.remaining();
            outgoingBuffers.remove();
        }
    }
}

public static void main(String[] args) throws IOException {
    MergedLogSource source = new MergedLogSource(args);

    PrintStream ps_ms = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-ms.out")));
    PrintStream ps_sec = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-sec.out")));
    PrintStream ps_min = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-min.out")));
    PrintStream ps_hour = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-hour.out")));
    LogIterator iter;

    System.out.println(source);
    iter = source.iterator();
    long currentms = 0;
    long currentsec = 0;
    long currentmin = 0;
    long currenthour = 0;
    Set<Long> zxids_ms = new HashSet<Long>();
    long zxid_sec = 0;
    long zxid_min = 0;
    long zxid_hour = 0;

    while (iter.hasNext()) {
        LogEntry e = iter.next();
        TransactionEntry cxn = (TransactionEntry) e;

        long ms = cxn.getTimestamp();
        long sec = ms / MS_PER_SEC;
        long min = ms / MS_PER_MIN;
        long hour = ms / MS_PER_HOUR;

        if (currentms != ms && currentms != 0) {
            ps_ms.println("" + currentms + " " + zxids_ms.size());

            zxid_sec += zxids_ms.size();
            zxid_min += zxids_ms.size();
            zxid_hour += zxids_ms.size();
            zxids_ms.clear();
        }

        if (currentsec != sec && currentsec != 0) {
            ps_sec.println("" + currentsec * MS_PER_SEC + " " + zxid_sec);

            zxid_sec = 0;
        }

        if (currentmin != min && currentmin != 0) {
            ps_min.println("" + currentmin * MS_PER_MIN + " " + zxid_min);

            zxid_min = 0;
        }

        if (currenthour != hour && currenthour != 0) {
            ps_hour.println("" + currenthour * MS_PER_HOUR + " " + zxid_hour);

            zxid_hour = 0;
        }

        currentms = ms;
        currentsec = sec;
        currentmin = min;
        currenthour = hour;

        zxids_ms.add(cxn.getZxid());
    }

    iter.close();
    ps_ms.close();
    ps_sec.close();
    ps_min.close();
    ps_hour.close();
}

public void run() {
    try {
        while (!stopped) {
            try {
                select();
                processAcceptedConnections();
                processInterestOpsUpdateRequests();
            } catch (RuntimeException e) {
                LOG.warn("Ignoring unexpected runtime exception", e);
            } catch (Exception e) {
                LOG.warn("Ignoring unexpected exception", e);
            }
        }

        // Close connections still pending on the selector. Any others with in-flight work, let drain out of the work queue.
        for (SelectionKey key : selector.keys()) {
            NIOServerCnxn cnxn = (NIOServerCnxn) key.attachment();
            if (cnxn.isSelectable()) {
                cnxn.close(ServerCnxn.DisconnectReason.SERVER_SHUTDOWN);
            }
            cleanupSelectionKey(key);
        }
        SocketChannel accepted;
        while ((accepted = acceptedQueue.poll()) != null) {
            fastCloseSock(accepted);
        }
        updateQueue.clear();
    } finally {
        closeSelector();
        // This will wake up the accept thread and the other selector threads, and tell the worker thread pool to begin shutdown.
        NIOServerCnxnFactory.this.stop();
        LOG.info("selector thread exited run method");
    }
}

public String getStatus(String name, long timeout) throws KeeperException, InterruptedException {
    Stat stat = new Stat();
    byte[] data = null;
    long endTime = Time.currentElapsedTime() + timeout;
    KeeperException lastException = null;
    for (int i = 0; i < maxTries && endTime > Time.currentElapsedTime(); i++) {
        try {
            data = zk.getData(reportsNode + '/' + name, false, stat);
            if (LOG.isDebugEnabled()) {
                LOG.debug("Got Data: " + ((data == null) ? "null" : new String(data)));
            }
            lastException = null;
            break;
        } catch (ConnectionLossException e) {
            lastException = e;
        } catch (NoNodeException e) {
            final Object eventObj = new Object();
            synchronized (eventObj) {
                // wait for the node to appear
                Stat eStat = zk.exists(reportsNode + '/' + name, new Watcher() {
                    @Override
                    public void process(WatchedEvent event) {
                        synchronized (eventObj) {
                            eventObj.notifyAll();
                        }
                    }
                });
                if (eStat == null) {
                    eventObj.wait(endTime - Time.currentElapsedTime());
                }
            }
            lastException = e;
        }
    }
    if (lastException != null) {
        throw lastException;
    }
    return new String(data);
}

private void init() throws IOException {
    File f = new File(file);
    RandomAccessFileReader in = new RandomAccessFileReader(f);
    SimpleDateFormat dateformat = new SimpleDateFormat(DATE_FORMAT);
    Pattern idp = Pattern.compile("\\[myid:(\\d+)\\]");

    long lastFp = in.getPosition();
    String line = in.readLine();
    Matcher m = null;

    // if we have read data from the file, and it matches the timep pattern
    if ((line != null) && (m = timep.matcher(line)).lookingAt()) {
        starttime = timestampFromText(dateformat, m.group(1));
    } else {
        throw new IOException("Invalid log format. First line doesn't start with time");
    }

    /*
     * Count number of log entries. Any line starting with a timestamp counts as an entry
     */
    String lastentry = line;
    try {
        while (line != null) {
            m = timep.matcher(line);
            if (m.lookingAt()) {
                if (size % skipN == 0) {
                    long time = timestampFromText(dateformat, m.group(1));
                    skiplist.addMark(time, lastFp, size);
                }
                size++;
                lastentry = line;
            }
            if (serverid == 0 && (m = idp.matcher(line)).find()) {
                serverid = Integer.valueOf(m.group(1));
            }

            lastFp = in.getPosition();
            line = in.readLine();
        }
    } catch (EOFException eof) {
        // ignore, simply end of file, though really (line!=null) should have caught this
    } finally {
        in.close();
    }

    m = timep.matcher(lastentry);
    if (m.lookingAt()) {
        endtime = timestampFromText(dateformat, m.group(1));
    } else {
        throw new IOException("Invalid log format. Last line doesn't start with time");
    }
}

private void distributedConfigureServers(int count) throws IOException {
    StringBuilder sbClient = new StringBuilder();
    StringBuilder sbServer = new StringBuilder();
    try {
        for (int i = 0; i < count; i++) {
            String[] r = QuorumPeerInstance.createServer(im, i);
            if (i > 0) {
                sbClient.append(',');
                sbServer.append(',');
            }
            sbClient.append(r[0]); // r[0] == "host:clientPort"
            sbServer.append(r[1]); // r[1] == "host:leaderPort:leaderElectionPort"
            String[] hostPort = r[0].split(":");
            sbServer.append(';').append(hostPort[1]); // Appending ";clientPort"
        }
        serverHostPort = sbClient.toString();
        quorumHostPort = sbServer.toString();
    } catch (Exception e) {
        IOException ioe = new IOException(e.getMessage());
        ioe.setStackTrace(e.getStackTrace());
        throw ioe;
    }
}

protected void animateVertically(int posx, int fromY, int toY) throws InterruptedException {
    toaster.setLocation(posx, fromY);
    if (toY < fromY) {
        for (int i = fromY; i > toY; i -= step) {
            toaster.setLocation(posx, i);
            Thread.sleep(stepTime);
        }
    } else {
        for (int i = fromY; i < toY; i += step) {
            toaster.setLocation(posx, i);
            Thread.sleep(stepTime);
        }
    }
    toaster.setLocation(posx, toY);
}

