public void start() {
  if (running || isFinished() || urls.length < 1) return;

  // ensure that the previous state is completely paused.
  joinForThreads(10000);

  running = true;
  errCode = ERROR_NOTHING;

  if (hasInvalidStorage()) {
    notifyError(ERROR_FILE_CREATION, null);
    return;
  }

  if (current >= urls.length) {
    notifyFinished();
    return;
  }

  notify(DownloadManagerService.MESSAGE_RUNNING);

  if (urls[current] == null) {
    doRecover(ERROR_RESOURCE_GONE);
    return;
  }

  if (blocks == null) {
    initializer();
    return;
  }

  init = null;
  finishCount = 0;
  blockAcquired = new boolean[blocks.length];

  if (blocks.length < 1) {
    threads = new Thread[] { runAsync(1, new DownloadRunnableFallback(this)) };
  } else {
    int remainingBlocks = 0;
    for (int block : blocks) if (block >= 0) remainingBlocks++;

    if (remainingBlocks < 1) {
      notifyFinished();
      return;
    }

    threads = new Thread[Math.min(threadCount, remainingBlocks)];
    for (int i = 0; i < threads.length; i++) {
      threads[i] = runAsync(i + 1, new DownloadRunnable(this, i));
    }
  }
}

public void parse() throws IOException, NoSuchElementException {
  if (selectedTrack > -1) return;

  box = readBox(ATOM_FTYP);
  brands = parseFtyp(box);
  switch (brands[0]) {
    case BRAND_DASH:
    case BRAND_ISO5: // why not?
      break;
    default:
      throw new NoSuchElementException(
          "Not a MPEG-4 DASH container, major brand is not 'dash' or 'iso5' is " + boxName(brands[0]));
  }

  Moov moov = null;
  int i;

  while (box.type != ATOM_MOOF) {
    ensure(box);
    box = readBox();

    switch (box.type) {
      case ATOM_MOOV:
        moov = parseMoov(box);
        break;
      case ATOM_SIDX:
      case ATOM_MFRA:
        break;
    }
  }

  if (moov == null) {
    throw new IOException("The provided Mp4 doesn't have the 'moov' box");
  }

  tracks = new Mp4Track[moov.trak.length];

  for (i = 0; i < tracks.length; i++) {
    tracks[i] = new Mp4Track();
    tracks[i].trak = moov.trak[i];

    if (moov.mvexTrex != null) {
      for (final Trex mvexTrex : moov.mvexTrex) {
        if (tracks[i].trak.tkhd.trackId == mvexTrex.trackId) {
          tracks[i].trex = mvexTrex;
        }
      }
    }

    switch (moov.trak[i].mdia.hdlr.subType) {
      case HANDLER_VIDE:
        tracks[i].kind = TrackKind.Video;
        break;
      case HANDLER_SOUN:
        tracks[i].kind = TrackKind.Audio;
        break;
      case HANDLER_SUBT:
        tracks[i].kind = TrackKind.Subtitles;
        break;
      default:
        tracks[i].kind = TrackKind.Other;
        break;
    }
  }

  backupBox = box;
}

void establishConnection(int threadId, HttpURLConnection conn) throws IOException, HttpError {
  final int statusCode = conn.getResponseCode();

  if (DEBUG) {
    Log.d(TAG, threadId + ":[request]  Range=" + conn.getRequestProperty("Range"));
    Log.d(TAG, threadId + ":[response] Code=" + statusCode);
    Log.d(TAG, threadId + ":[response] Content-Length=" + conn.getContentLength());
    Log.d(TAG, threadId + ":[response] Content-Range=" + conn.getHeaderField("Content-Range"));
  }

  switch (statusCode) {
    case 204:
    case 205:
    case 207:
      throw new HttpError(statusCode);
    case 416:
      return; // let the download thread handle this error
    default:
      if (statusCode < 200 || statusCode > 299) {
        throw new HttpError(statusCode);
      }
  }
}

private Elst parseEdts(final Box ref) throws IOException {
  final Box b = untilBox(ref, ATOM_ELST);
  if (b == null) return null;

  final Elst obj = new Elst();

  final boolean v1 = stream.read() == 1;
  stream.skipBytes(3); // flags

  final int entryCount = stream.readInt();
  if (entryCount < 1) {
    obj.bMediaRate = 0x00010000; // default media rate (1.0)
    return obj;
  }

  if (v1) {
    stream.skipBytes(DataReader.LONG_SIZE); // segment duration
    obj.mediaTime = stream.readLong();
    // ignore all remain entries
    stream.skipBytes((entryCount - 1) * (DataReader.LONG_SIZE * 2));
  } else {
    stream.skipBytes(DataReader.INTEGER_SIZE); // segment duration
    obj.mediaTime = stream.readInt();
  }

  obj.bMediaRate = stream.readInt();
  return obj;
}

@Override
public void write(byte[] b, int off, int len) throws IOException {
  if (len == 0) return;

  final long offsetOut = out.getOffset();
  final long offsetAux = aux.getOffset();
  final long end = callback.check();

  long available;
  if (end == -1) {
    available = Integer.MAX_VALUE;
  } else if (end < offsetOut) {
    throw new IOException("The reported offset is invalid: " + end + "<" + offsetOut);
  } else {
    available = end - offsetOut;
  }

  final boolean usingAux = aux.length > 0 && offsetOut >= out.length;
  final boolean underflow = offsetAux < aux.length || offsetOut < out.length;

  if (usingAux) {
    // before continue calculate the final length of aux
    long length = offsetAux + len;
    if (underflow) {
      if (aux.length > length) {
        length = aux.length; // the length is not changed
      }
    } else {
      length = aux.length + len;
    }

    aux.write(b, off, len);

    if (length >= THRESHOLD_AUX_LENGTH && length <= available) {
      flushAuxiliar(available);
    }
  } else {
    if (underflow) {
      available = out.length - offsetOut;
    }

    final int length = Math.min(len, (int) Math.min(Integer.MAX_VALUE, available));
    out.write(b, off, length);

    len -= length;
    off += length;

    if (len > 0) {
      aux.write(b, off, len);
    }
  }

  if (onProgress != null) {
    final long absoluteOffset = out.getOffset() + aux.getOffset();
    if (absoluteOffset > reportPosition) {
      reportPosition = absoluteOffset + NOTIFY_BYTES_INTERVAL;
      onProgress.report(absoluteOffset);
    }
  }
}

@Override
public void run() {
  boolean done;
  long start = mMission.fallbackResumeOffset;

  if (DEBUG && !mMission.unknownLength && start > 0) {
    Log.i(TAG, "Resuming a single-thread download at " + start);
  }

  try {
    final long rangeStart = (mMission.unknownLength || start < 1) ? -1 : start;

    final int mId = 1;
    mConn = mMission.openConnection(false, rangeStart, -1);

    if (mRetryCount == 0 && rangeStart == -1) {
      // workaround: bypass android connection pool
      mConn.setRequestProperty("Range", "bytes=0-");
    }

    mMission.establishConnection(mId, mConn);

    // check if the download can be resumed
    if (mConn.getResponseCode() == 416 && start > 0) {
      mMission.notifyProgress(-start);
      start = 0;
      mRetryCount--;
      throw new DownloadMission.HttpError(416);
    }

    // secondary check for the file length
    if (!mMission.unknownLength) {
      mMission.unknownLength = Utility.getContentLength(mConn) == -1;
    }

    if (mMission.unknownLength || mConn.getResponseCode() == 200) {
      // restart amount of bytes downloaded
      mMission.done = mMission.offsets[mMission.current] - mMission.offsets[0];
    }

    mF = mMission.storage.getStream();
    mF.seek(mMission.offsets[mMission.current] + start);

    mIs = mConn.getInputStream();

    final byte[] buf = new byte[DownloadMission.BUFFER_SIZE];
    int len = 0;

    while (mMission.running && (len = mIs.read(buf, 0, buf.length)) != -1) {
      mF.write(buf, 0, len);
      start += len;
      mMission.notifyProgress(len);
    }

    dispose();

    // if thread goes interrupted check if the last part is written. This avoid re-download the whole file
    done = len == -1;
  } catch (Exception e) {
    dispose();

    mMission.fallbackResumeOffset = start;

    if (!mMission.running || e instanceof ClosedByInterruptException) return;

    if (e instanceof HttpError && ((HttpError) e).statusCode == ERROR_HTTP_FORBIDDEN) {
      // for youtube streams. The url has expired, recover
      dispose();
      mMission.doRecover(ERROR_HTTP_FORBIDDEN);
      return;
    }

    if (mRetryCount++ >= mMission.maxRetry) {
      mMission.notifyError(e);
      return;
    }

    if (DEBUG) {
      Log.e(TAG, "got exception, retrying...", e);
    }

    run(); // try again
    return;
  }

  if (done) {
    mMission.notifyFinished();
  } else {
    mMission.fallbackResumeOffset = start;
  }
}

private ArrayList<Object> getSpecialItems() {
  synchronized (DownloadManager.this) {
    final ArrayList<Mission> pending = new ArrayList<>(mMissionsPending);
    final ArrayList<Mission> finished = new ArrayList<>(mMissionsFinished);
    final List<Mission> remove = new ArrayList<>(hidden);

    // hide missions (if required)
    remove.removeIf(mission -> pending.remove(mission) || finished.remove(mission));

    int fakeTotal = pending.size();
    if (fakeTotal > 0) fakeTotal++;

    fakeTotal += finished.size();
    if (finished.size() > 0) fakeTotal++;

    final ArrayList<Object> list = new ArrayList<>(fakeTotal);
    if (pending.size() > 0) {
      list.add(PENDING);
      list.addAll(pending);
    }
    if (finished.size() > 0) {
      list.add(FINISHED);
      list.addAll(finished);
    }

    hasFinished = finished.size() > 0;
    return list;
  }
}

void startMission(DownloadMission mission) {
  synchronized (this) {
    mission.timestamp = System.currentTimeMillis();
    mission.mHandler = mHandler;
    mission.maxRetry = mPrefMaxRetry;

    // create metadata file
    while (true) {
      mission.metadata = new File(mPendingMissionsDir, String.valueOf(mission.timestamp));
      if (!mission.metadata.isFile() && !mission.metadata.exists()) {
        try {
          if (!mission.metadata.createNewFile()) {
            throw new RuntimeException("Cant create download metadata file");
          }
        } catch (IOException e) {
          throw new RuntimeException(e);
        }
        break;
      }
      mission.timestamp = System.currentTimeMillis();
    }

    mSelfMissionsControl = true;
    mMissionsPending.add(mission);

    // Before continue, save the metadata in case the internet connection is not available
    Utility.writeToFile(mission.metadata, mission);

    if (mission.storage == null) {
      // noting to do here
      mission.errCode = DownloadMission.ERROR_FILE_CREATION;
      if (mission.errObject != null) mission.errObject = new IOException("DownloadMission.storage == NULL");
      return;
    }

    final boolean start = !mPrefQueueLimit || getRunningMissionsCount() < 1;

    if (canDownloadInCurrentNetwork() && start) {
      mission.start();
    }
  }
}

private void showPopupMenu() {
  retry.setVisible(false);
  cancel.setVisible(false);
  start.setVisible(false);
  pause.setVisible(false);
  open.setVisible(false);
  queue.setVisible(false);
  showError.setVisible(false);
  delete.setVisible(false);
  source.setVisible(false);
  checksum.setVisible(false);

  final DownloadMission mission = item.mission instanceof DownloadMission ? (DownloadMission) item.mission : null;

  if (mission != null) {
    if (mission.hasInvalidStorage()) {
      retry.setVisible(true);
      delete.setVisible(true);
      showError.setVisible(true);
    } else if (mission.isPsRunning()) {
      switch (mission.errCode) {
        case ERROR_INSUFFICIENT_STORAGE:
        case ERROR_POSTPROCESSING_HOLD:
          retry.setVisible(true);
          cancel.setVisible(true);
          showError.setVisible(true);
          break;
      }
    } else {
      if (mission.running) {
        pause.setVisible(true);
      } else {
        if (mission.errCode != ERROR_NOTHING) {
          showError.setVisible(true);
        }

        queue.setChecked(mission.enqueued);

        delete.setVisible(true);

        final boolean flag = !mission.isPsFailed() && mission.urls.length > 0;
        start.setVisible(flag);
        queue.setVisible(flag);
      }
    }
  } else {
    open.setVisible(true);
    delete.setVisible(true);
    checksum.setVisible(true);
  }

  if (item.mission.source != null && !item.mission.source.isEmpty()) {
    source.setVisible(true);
  }

  popupMenu.show();
}

void drainFused() {
  int missed = 1;

  for (;;) {
    if (disposed) {
      return;
    }

    boolean d = done;
    Throwable ex = error;

    if (!delayError && d && ex != null) {
      disposed = true;
      downstream.onError(error);
      worker.dispose();
      return;
    }

    downstream.onNext(null);

    if (d) {
      disposed = true;
      ex = error;
      if (ex != null) {
        downstream.onError(ex);
      } else {
        downstream.onComplete();
      }
      worker.dispose();
      return;
    }

    missed = addAndGet(-missed);
    if (missed == 0) {
      break;
    }
  }
}

void dispose(List<Disposable> set) {
  if (set == null) {
    return;
  }

  List<Throwable> errors = null;
  for (Disposable o : set) {
    try {
      o.dispose();
    } catch (Throwable ex) {
      Exceptions.throwIfFatal(ex);
      if (errors == null) {
        errors = new ArrayList<>();
      }
      errors.add(ex);
    }
  }

  if (errors != null) {
    if (errors.size() == 1) {
      throw ExceptionHelper.wrapOrThrow(errors.get(0));
    }
    throw new CompositeException(errors);
  }
}

@Override
public void onComplete() {
  if (wip.getAndIncrement() == 0) {
    Publisher<? extends T>[] sources = this.sources;
    int n = sources.length;
    int i = index;

    for (;;) {
      if (i == n) {
        List<Throwable> list = errors;
        if (list != null) {
          if (list.size() == 1) {
            downstream.onError(list.get(0));
          } else {
            downstream.onError(new CompositeException(list));
          }
        } else {
          downstream.onComplete();
        }
        return;
      }

      Publisher<? extends T> p = sources[i];

      if (p == null) {
        Throwable ex = new NullPointerException("A Publisher entry is null");
        if (delayError) {
          List<Throwable> list = errors;
          if (list == null) {
            list = new ArrayList<>(n - i + 1);
            errors = list;
          }
          list.add(ex);
          i++;
          continue;
        } else {
          downstream.onError(ex);
          return;
        }
      } else {
        long r = produced;
        if (r != 0L) {
          produced = 0L;
          produced(r);
        }
        p.subscribe(this);
      }

      index = ++i;

      if (wip.decrementAndGet() == 0) {
        break;
      }
    }
  }
}

void replay(CacheDisposable<T> consumer) {
  // make sure there is only one replay going on at a time
  if (consumer.getAndIncrement() != 0) {
    return;
  }

  // see if there were more replay request in the meantime
  int missed = 1;
  // read out state into locals upfront to avoid being re-read due to volatile reads
  long index = consumer.index;
  int offset = consumer.offset;
  Node<T> node = consumer.node;
  Observer<? super T> downstream = consumer.downstream;
  int capacity = capacityHint;

  for (;;) {
    // if the consumer got disposed, clear the node and quit
    if (consumer.disposed) {
      consumer.node = null;
      return;
    }

    // first see if the source has terminated, read order matters!
    boolean sourceDone = done;
    // and if the number of items is the same as this consumer has received
    boolean empty = size == index;

    // if the source is done and we have all items so far, terminate the consumer
    if (sourceDone && empty) {
      // release the node object to avoid leaks through retained consumers
      consumer.node = null;
      // if error is not null then the source failed
      Throwable ex = error;
      if (ex != null) {
        downstream.onError(ex);
      } else {
        downstream.onComplete();
      }
      return;
    }

    // there are still items not sent to the consumer
    if (!empty) {
      // if the offset in the current node has reached the node capacity
      if (offset == capacity) {
        // switch to the subsequent node
        node = node.next;
        // reset the in-node offset
        offset = 0;
      }

      // emit the cached item
      downstream.onNext(node.values[offset]);

      // move the node offset forward
      offset++;
      // move the total consumed item count forward
      index++;

      // retry for the next item/terminal event if any
      continue;
    }

    // commit the changed references back
    consumer.index = index;
    consumer.offset = offset;
    consumer.node = node;
    // release the changes and see if there were more replay request in the meantime
    missed = consumer.addAndGet(-missed);
    if (missed == 0) {
      break;
    }
  }
}

void drainAsync() {
  int missed = 1;

  SimpleQueue<T> q = queue;
  Subscriber<? super T>[] a = this.subscribers;
  AtomicLongArray r = this.requests;
  long[] e = this.emissions;
  int n = e.length;
  int idx = index;
  int consumed = produced;

  for (;;) {
    int notReady = 0;

    for (;;) {
      if (cancelled) {
        q.clear();
        return;
      }

      boolean d = done;
      if (d) {
        Throwable ex = error;
        if (ex != null) {
          q.clear();
          for (Subscriber<? super T> s : a) {
            s.onError(ex);
          }
          return;
        }
      }

      boolean empty = q.isEmpty();

      if (d && empty) {
        for (Subscriber<? super T> s : a) {
          s.onComplete();
        }
        return;
      }

      if (empty) {
        break;
      }

      long requestAtIndex = r.get(idx);
      long emissionAtIndex = e[idx];
      if (requestAtIndex != emissionAtIndex && r.get(n + idx) == 0) {
        T v;

        try {
          v = q.poll();
        } catch (Throwable ex) {
          Exceptions.throwIfFatal(ex);
          upstream.cancel();
          for (Subscriber<? super T> s : a) {
            s.onError(ex);
          }
          return;
        }

        if (v == null) {
          break;
        }

        a[idx].onNext(v);

        e[idx] = emissionAtIndex + 1;

        int c = ++consumed;
        if (c == limit) {
          consumed = 0;
          upstream.request(c);
        }
        notReady = 0;
      } else {
        notReady++;
      }

      idx++;
      if (idx == n) {
        idx = 0;
      }

      if (notReady == n) {
        break;
      }
    }

    int w = get();
    if (w == missed) {
      index = idx;
      produced = consumed;
      missed = addAndGet(-missed);
      if (missed == 0) {
        break;
      }
    } else {
      missed = w;
    }
  }
}

@Override
public void run() {
  int missed = 1;
  int c = consumed;
  SpscArrayQueue<T> q = queue;
  Subscriber<? super T> a = downstream;
  int lim = limit;

  for (;;) {
    long r = requested.get();
    long e = 0L;

    while (e != r) {
      if (cancelled) {
        q.clear();
        return;
      }

      boolean d = done;

      if (d) {
        Throwable ex = error;
        if (ex != null) {
          q.clear();

          a.onError(ex);

          worker.dispose();
          return;
        }
      }

      T v = q.poll();

      boolean empty = v == null;

      if (d && empty) {
        a.onComplete();

        worker.dispose();
        return;
      }

      if (empty) {
        break;
      }

      a.onNext(v);

      e++;

      int p = ++c;
      if (p == lim) {
        c = 0;
        upstream.request(p);
      }
    }

    if (e == r) {
      if (cancelled) {
        q.clear();
        return;
      }

      if (done) {
        Throwable ex = error;
        if (ex != null) {
          q.clear();

          a.onError(ex);

          worker.dispose();
          return;
        }
        if (q.isEmpty()) {
          a.onComplete();

          worker.dispose();
          return;
        }
      }
    }

    if (e != 0L && r != Long.MAX_VALUE) {
      requested.addAndGet(-e);
    }

    int w = get();
    if (w == missed) {
      consumed = c;
      missed = addAndGet(-missed);
      if (missed == 0) {
        break;
      }
    } else {
      missed = w;
    }
  }
}

@Override
public void drain() {
  if (getAndIncrement() != 0) {
    return;
  }

  int missed = 1;

  for (;;) {
    SimpleQueue<T> q1 = first.queue;
    SimpleQueue<T> q2 = second.queue;

    if (q1 != null && q2 != null) {
      for (;;) {
        if (isDisposed()) {
          first.clear();
          second.clear();
          return;
        }

        Throwable ex = errors.get();
        if (ex != null) {
          cancelAndClear();

          errors.tryTerminateConsumer(downstream);
          return;
        }

        boolean d1 = first.done;

        T a = v1;
        if (a == null) {
          try {
            a = q1.poll();
          } catch (Throwable exc) {
            Exceptions.throwIfFatal(exc);
            cancelAndClear();
            errors.tryAddThrowableOrReport(exc);
            errors.tryTerminateConsumer(downstream);
            return;
          }
          v1 = a;
        }
        boolean e1 = a == null;

        boolean d2 = second.done;
        T b = v2;
        if (b == null) {
          try {
            b = q2.poll();
          } catch (Throwable exc) {
            Exceptions.throwIfFatal(exc);
            cancelAndClear();
            errors.tryAddThrowableOrReport(exc);
            errors.tryTerminateConsumer(downstream);
            return;
          }
          v2 = b;
        }

        boolean e2 = b == null;

        if (d1 && d2 && e1 && e2) {
          downstream.onSuccess(true);
          return;
        }
        if ((d1 && d2) && (e1 != e2)) {
          cancelAndClear();
          downstream.onSuccess(false);
          return;
        }

        if (e1 || e2) {
          break;
        }

        boolean c;

        try {
          c = comparer.test(a, b);
        } catch (Throwable exc) {
          Exceptions.throwIfFatal(exc);
          cancelAndClear();
          errors.tryAddThrowableOrReport(exc);
          errors.tryTerminateConsumer(downstream);
          return;
        }

        if (!c) {
          cancelAndClear();
          downstream.onSuccess(false);
          return;
        }

        v1 = null;
        v2 = null;

        first.request();
        second.request();
      }
    } else {
      if (isDisposed()) {
        first.clear();
        second.clear();
        return;
      }

      Throwable ex = errors.get();
      if (ex != null) {
        cancelAndClear();

        errors.tryTerminateConsumer(downstream);
        return;
      }
    }

    missed = addAndGet(-missed);
    if (missed == 0) {
      break;
    }
  }
}

void drain() {
  if (getAndIncrement() != 0) {
    return;
  }

  final Subscriber<? super R> a = downstream;
  final SimpleQueue<T> q = queue;
  final boolean replenish = fusionMode != SYNC;

  int missed = 1;

  Iterator<? extends R> it = current;

  for (;;) {
    if (it == null) {
      boolean d = done;

      T t;

      try {
        t = q.poll();
      } catch (Throwable ex) {
        Exceptions.throwIfFatal(ex);
        upstream.cancel();
        ExceptionHelper.addThrowable(error, ex);
        ex = ExceptionHelper.terminate(error);

        current = null;
        q.clear();

        a.onError(ex);
        return;
      }

      boolean empty = t == null;

      if (checkTerminated(d, empty, a, q)) {
        return;
      }

      if (t != null) {
        Iterable<? extends R> iterable;

        boolean b;

        try {
          iterable = mapper.apply(t);

          it = iterable.iterator();

          b = it.hasNext();
        } catch (Throwable ex) {
          Exceptions.throwIfFatal(ex);
          upstream.cancel();
          ExceptionHelper.addThrowable(error, ex);
          ex = ExceptionHelper.terminate(error);
          a.onError(ex);
          return;
        }

        if (!b) {
          it = null;
          consumedOne(replenish);
          continue;
        }

        current = it;
      }
    }

    if (it != null) {
      long r = requested.get();
      long e = 0L;

      while (e != r) {
        if (checkTerminated(done, false, a, q)) {
          return;
        }

        R v;

        try {
          v = Objects.requireNonNull(it.next(), "The iterator returned a null value");
        } catch (Throwable ex) {
          Exceptions.throwIfFatal(ex);
          current = null;
          upstream.cancel();
          ExceptionHelper.addThrowable(error, ex);
          ex = ExceptionHelper.terminate(error);
          a.onError(ex);
          return;
        }

        a.onNext(v);

        if (checkTerminated(done, false, a, q)) {
          return;
        }

        e++;

        boolean b;

        try {
          b = it.hasNext();
        } catch (Throwable ex) {
          Exceptions.throwIfFatal(ex);
          current = null;
          upstream.cancel();
          ExceptionHelper.addThrowable(error, ex);
          ex = ExceptionHelper.terminate(error);
          a.onError(ex);
          return;
        }

        if (!b) {
          consumedOne(replenish);
          it = null;
          current = null;
          break;
        }
      }

      if (e == r) {
        boolean d = done;
        boolean empty = q.isEmpty() && it == null;

        if (checkTerminated(d, empty, a, q)) {
          return;
        }
      }

      if (e != 0L) {
        if (r != Long.MAX_VALUE) {
          requested.addAndGet(-e);
        }
      }

      if (it == null) {
        continue;
      }
    }

    missed = addAndGet(-missed);
    if (missed == 0) {
      break;
    }
  }
}

@Override
public void subscribe(Subscriber<? super R>[] subscribers) {
  subscribers = RxJavaPlugins.onSubscribe(this, subscribers);

  if (!validate(subscribers)) {
    return;
  }

  int n = subscribers.length;

  @SuppressWarnings("unchecked")
  Subscriber<T>[] parents = new Subscriber[n];

  for (int i = 0; i < n; i++) {
    R initialValue;

    try {
      initialValue = Objects.requireNonNull(initialSupplier.get(), "The initialSupplier returned a null value");
    } catch (Throwable ex) {
      Exceptions.throwIfFatal(ex);
      reportError(subscribers, ex);
      return;
    }

    parents[i] = new ParallelReduceSubscriber<>(subscribers[i], initialValue, reducer);
  }

  source.subscribe(parents);
}

@Override
public void subscribeActual(Observer<? super T> observer) {
  DeferredScalarDisposable<T> d = new DeferredScalarDisposable<>(observer);
  observer.onSubscribe(d);

  if (d.isDisposed()) {
    return;
  }

  T value;
  try {
    value = ExceptionHelper.nullCheck(supplier.get(), "Supplier returned a null value.");
  } catch (Throwable e) {
    Exceptions.throwIfFatal(e);
    if (!d.isDisposed()) {
      observer.onError(e);
    } else {
      RxJavaPlugins.onError(e);
    }
    return;
  }

  d.complete(value);
}

private List<Integer> duplicate(int totalPages, String pageOrder) {
  final List<Integer> newPageOrder = new ArrayList<>();
  int duplicateCount;
  try {
    duplicateCount = (pageOrder != null && !pageOrder.isEmpty()) ? Integer.parseInt(pageOrder.trim()) : 2;
  } catch (NumberFormatException e) {
    log.error("Invalid duplicate count specified", e);
    duplicateCount = 2;
  }
  if (duplicateCount < 1) {
    duplicateCount = 2;
  }
  for (int pageNum = 0; pageNum < totalPages; pageNum++) {
    for (int dupCount = 0; dupCount < duplicateCount; dupCount++) {
      newPageOrder.add(pageNum);
    }
  }
  return newPageOrder;
}

private void runPipelineAgainstFiles(List<File> filesToProcess, PipelineConfig config, Path dir, Path processingDir)
    throws IOException {
  try {
    final List<Resource> inputFiles = processor.generateInputFiles(filesToProcess.toArray(new File[0]));
    if (inputFiles == null || inputFiles.isEmpty()) return;

    final PipelineResult result = processor.runPipelineAgainstFiles(inputFiles, config);
    if (result.isHasErrors()) {
      log.error("Errors occurred during processing, retaining original files");
      moveToErrorDirectory(filesToProcess, dir);
    } else {
      moveAndRenameFiles(result.getOutputFiles(), config, dir);
      deleteOriginalFiles(filesToProcess, processingDir);
    }
  } catch (Exception e) {
    log.error("Error during processing", e);
    moveFilesBack(filesToProcess, processingDir);
  }
}

private boolean activateMachine(String licenseKey, String licenseId, String machineFingerprint, LicenseContext context)
    throws Exception {
  if (context.isFloatingLicense) {
    log.info("Processing floating license activation. Max machines allowed: {}", context.maxMachines);

    JsonNode machinesResponse = fetchMachinesForLicense(licenseKey, licenseId);
    if (machinesResponse != null) {
      JsonNode machines = machinesResponse.path("data");
      int currentMachines = machines.size();

      log.info("Current machine count: {}, Max allowed: {}", currentMachines, context.maxMachines);

      boolean isCurrentMachineActivated = false;
      String currentMachineId = null;

      for (JsonNode machine : machines) {
        if (machineFingerprint.equals(machine.path("attributes").path("fingerprint").asText())) {
          isCurrentMachineActivated = true;
          currentMachineId = machine.path("id").asText();
          log.info("Current machine is already activated with ID: {}", currentMachineId);
          break;
        }
      }

      if (isCurrentMachineActivated) {
        log.info("Machine already activated. No action needed.");
        return true;
      }

      if (currentMachines >= context.maxMachines) {
        log.info("Max machines reached. Deregistering oldest machine to make room for the new machine.");
        if (machines.size() > 0) {
          String oldestMachineId = null;
          java.time.Instant oldestTime = null;

          for (JsonNode machine : machines) {
            String createdStr = machine.path("attributes").path("created").asText(null);
            if (createdStr != null && !createdStr.isEmpty()) {
              try {
                java.time.Instant createdTime = java.time.Instant.parse(createdStr);
                if (oldestTime == null || createdTime.isBefore(oldestTime)) {
                  oldestTime = createdTime;
                  oldestMachineId = machine.path("id").asText();
                }
              } catch (Exception e) {
                log.warn("Could not parse creation time for machine: {}", e.getMessage());
              }
            }
          }

          if (oldestMachineId == null) {
            log.warn("Could not determine oldest machine by timestamp, using first machine in list");
            oldestMachineId = machines.path(0).path("id").asText();
          }

          log.info("Deregistering machine with ID: {}", oldestMachineId);
          boolean deregistered = deregisterMachine(licenseKey, oldestMachineId);
          if (!deregistered) {
            log.error("Failed to deregister machine. Cannot proceed with activation.");
            return false;
          }
          log.info("Machine deregistered successfully. Proceeding with activation of new machine.");
        } else {
          log.error("License has reached machine limit but no machines were found to deregister. This is unexpected.");
        }
      }
    }
  }

  String hostname;
  try {
    hostname = java.net.InetAddress.getLocalHost().getHostName();
  } catch (Exception e) {
    hostname = "Unknown";
  }

  JSONObject body = new JSONObject()
      .put("data",
          new JSONObject()
              .put("type", "machines")
              .put("attributes",
                  new JSONObject()
                      .put("fingerprint", machineFingerprint)
                      .put("platform", System.getProperty("os.name"))
                      .put("name", hostname))
              .put("relationships",
                  new JSONObject()
                      .put("license",
                          new JSONObject()
                              .put("data",
                                  new JSONObject()
                                      .put("type", "licenses")
                                      .put("id", licenseId)))));


  HttpRequest request = HttpRequest.newBuilder()
      .uri(URI.create(BASE_URL + "/" + ACCOUNT_ID + "/machines"))
      .header("Content-Type", "application/vnd.api+json")
      .header("Accept", "application/vnd.api+json")
      .header("Authorization", "License " + licenseKey)
      .POST(HttpRequest.BodyPublishers.ofString(body.toString()))
      .build();

  HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
  log.info("activateMachine Response body: " + response.body());
  if (response.statusCode() == 201) {
    log.info("Machine activated successfully");
    return true;
  } else {
    log.error("Error activating machine. Status code: {}, error: {}", response.statusCode(), response.body());
    return false;
  }
}

@PostMapping(consumes = "multipart/form-data", value = "/get-info-on-pdf")
@Operation(summary = "Summary here", description = "desc. Input:PDF Output:JSON Type:SISO")
public ResponseEntity<byte[]> getPdfInfo(@ModelAttribute PDFFile request) throws IOException {
  MultipartFile inputFile = request.getFileInput();
  boolean readonly = true;
  try (PDDocument pdfBoxDoc = pdfDocumentFactory.load(inputFile, readonly)) {
    ObjectMapper objectMapper = new ObjectMapper();
    ObjectNode jsonOutput = objectMapper.createObjectNode();

    PDDocumentInformation info = pdfBoxDoc.getDocumentInformation();
    ObjectNode metadata = objectMapper.createObjectNode();
    ObjectNode basicInfo = objectMapper.createObjectNode();
    ObjectNode docInfoNode = objectMapper.createObjectNode();
    ObjectNode compliancy = objectMapper.createObjectNode();
    ObjectNode encryption = objectMapper.createObjectNode();
    ObjectNode other = objectMapper.createObjectNode();

    metadata.put("Title", info.getTitle());
    metadata.put("Author", info.getAuthor());
    metadata.put("Subject", info.getSubject());
    metadata.put("Keywords", info.getKeywords());
    metadata.put("Producer", info.getProducer());
    metadata.put("Creator", info.getCreator());
    metadata.put("CreationDate", formatDate(info.getCreationDate()));
    metadata.put("ModificationDate", formatDate(info.getModificationDate()));
    jsonOutput.set("Metadata", metadata);

    long fileSizeInBytes = inputFile.getSize();
    basicInfo.put("FileSizeInBytes", fileSizeInBytes);

    String fullText = new PDFTextStripper().getText(pdfBoxDoc);
    String[] words = fullText.split("\\s+");
    int wordCount = words.length;
    int paragraphCount = fullText.split("\r\n|\r|\n").length;
    basicInfo.put("WordCount", wordCount);
    basicInfo.put("ParagraphCount", paragraphCount);
    int charCount = fullText.length();
    basicInfo.put("CharacterCount", charCount);

    boolean hasCompression = false;
    String compressionType = "None";
    basicInfo.put("Compression", hasCompression);
    if (hasCompression) basicInfo.put("CompressionType", compressionType);

    String language = pdfBoxDoc.getDocumentCatalog().getLanguage();
    basicInfo.put("Language", language);
    basicInfo.put("Number of pages", pdfBoxDoc.getNumberOfPages());

    PDDocumentCatalog catalog = pdfBoxDoc.getDocumentCatalog();
    String pageMode = catalog.getPageMode().name();

    docInfoNode.put("PDF version", pdfBoxDoc.getVersion());
    docInfoNode.put("Trapped", info.getTrapped());
    docInfoNode.put("Page Mode", getPageModeDescription(pageMode));

    PDAcroForm acroForm = pdfBoxDoc.getDocumentCatalog().getAcroForm();
    ObjectNode formFieldsNode = objectMapper.createObjectNode();
    if (acroForm != null) {
      for (PDField field : acroForm.getFieldTree()) {
        formFieldsNode.put(field.getFullyQualifiedName(), field.getValueAsString());
      }
    }
    jsonOutput.set("FormFields", formFieldsNode);

    ObjectNode summaryData = generatePDFSummaryData(pdfBoxDoc);
    if (summaryData != null && summaryData.size() > 0) {
      jsonOutput.set("SummaryData", summaryData);
    }

    if (catalog.getNames() != null) {
      PDEmbeddedFilesNameTreeNode efTree = catalog.getNames().getEmbeddedFiles();
      ArrayNode embeddedFilesArray = objectMapper.createArrayNode();
      if (efTree != null) {
        Map<String, PDComplexFileSpecification> efMap = efTree.getNames();
        if (efMap != null) {
          for (Map.Entry<String, PDComplexFileSpecification> entry : efMap.entrySet()) {
            ObjectNode embeddedFileNode = objectMapper.createObjectNode();
            embeddedFileNode.put("Name", entry.getKey());
            PDEmbeddedFile embeddedFile = entry.getValue().getEmbeddedFile();
            if (embeddedFile != null) {
              embeddedFileNode.put("FileSize", embeddedFile.getLength());
            }
            embeddedFilesArray.add(embeddedFileNode);
          }
        }
      }
      other.set("EmbeddedFiles", embeddedFilesArray);
    }

    ArrayNode attachmentsArray = objectMapper.createArrayNode();
    for (PDPage page : pdfBoxDoc.getPages()) {
      for (PDAnnotation annotation : page.getAnnotations()) {
        if (annotation instanceof PDAnnotationFileAttachment fileAttachmentAnnotation) {
          ObjectNode attachmentNode = objectMapper.createObjectNode();
          attachmentNode.put("Name", fileAttachmentAnnotation.getAttachmentName());
          attachmentNode.put("Description", fileAttachmentAnnotation.getContents());
          attachmentsArray.add(attachmentNode);
        }
      }
    }
    other.set("Attachments", attachmentsArray);

    PDDocumentNameDictionary namesDict = catalog.getNames();
    ArrayNode javascriptArray = objectMapper.createArrayNode();
    if (namesDict != null) {
      PDJavascriptNameTreeNode javascriptDict = namesDict.getJavaScript();
      if (javascriptDict != null) {
        try {
          Map<String, PDActionJavaScript> jsEntries = javascriptDict.getNames();
          for (Map.Entry<String, PDActionJavaScript> entry : jsEntries.entrySet()) {
            ObjectNode jsNode = objectMapper.createObjectNode();
            jsNode.put("JS Name", entry.getKey());
            PDActionJavaScript jsAction = entry.getValue();
            if (jsAction != null) {
              String jsCodeStr = jsAction.getAction();
              if (jsCodeStr != null) {
                jsNode.put("JS Script Length", jsCodeStr.length());
              }
            }
            javascriptArray.add(jsNode);
          }
        } catch (IOException e) {
          log.error("exception", e);
        }
      }
    }
    other.set("JavaScript", javascriptArray);

    PDOptionalContentProperties ocProperties = pdfBoxDoc.getDocumentCatalog().getOCProperties();
    ArrayNode layersArray = objectMapper.createArrayNode();
    if (ocProperties != null) {
      for (PDOptionalContentGroup ocg : ocProperties.getOptionalContentGroups()) {
        ObjectNode layerNode = objectMapper.createObjectNode();
        layerNode.put("Name", ocg.getName());
        layersArray.add(layerNode);
      }
    }
    other.set("Layers", layersArray);

    PDStructureTreeRoot structureTreeRoot = pdfBoxDoc.getDocumentCatalog().getStructureTreeRoot();
    ArrayNode structureTreeArray;
    try {
      if (structureTreeRoot != null) {
        structureTreeArray = exploreStructureTree(structureTreeRoot.getKids());
        other.set("StructureTree", structureTreeArray);
      }
    } catch (Exception e) {
      log.error("exception", e);
    }

    boolean isPdfACompliant = checkForStandard(pdfBoxDoc, "PDF/A");
    boolean isPdfXCompliant = checkForStandard(pdfBoxDoc, "PDF/X");
    boolean isPdfECompliant = checkForStandard(pdfBoxDoc, "PDF/E");
    boolean isPdfVTCompliant = checkForStandard(pdfBoxDoc, "PDF/VT");
    boolean isPdfUACompliant = checkForStandard(pdfBoxDoc, "PDF/UA");
    boolean isPdfBCompliant = checkForStandard(pdfBoxDoc, "PDF/B");
    boolean isPdfSECCompliant = checkForStandard(pdfBoxDoc, "PDF/SEC");

    compliancy.put("IsPDF/ACompliant", isPdfACompliant);
    compliancy.put("IsPDF/XCompliant", isPdfXCompliant);
    compliancy.put("IsPDF/ECompliant", isPdfECompliant);
    compliancy.put("IsPDF/VTCompliant", isPdfVTCompliant);
    compliancy.put("IsPDF/UACompliant", isPdfUACompliant);
    compliancy.put("IsPDF/BCompliant", isPdfBCompliant);
    compliancy.put("IsPDF/SECCompliant", isPdfSECCompliant);

    PDOutlineNode root = pdfBoxDoc.getDocumentCatalog().getDocumentOutline();
    ArrayNode bookmarksArray = objectMapper.createArrayNode();
    if (root != null) {
      for (PDOutlineItem child : root.children()) {
        addOutlinesToArray(child, bookmarksArray);
      }
    }
    other.set("Bookmarks/Outline/TOC", bookmarksArray);

    PDMetadata pdMetadata = pdfBoxDoc.getDocumentCatalog().getMetadata();
    String xmpString = null;
    if (pdMetadata != null) {
      try {
        try (COSInputStream is = pdMetadata.createInputStream()) {
          DomXmpParser domXmpParser = new DomXmpParser();
          XMPMetadata xmpMeta = domXmpParser.parse(is);
          ByteArrayOutputStream os = new ByteArrayOutputStream();
          new XmpSerializer().serialize(xmpMeta, os, true);
          xmpString = new String(os.toByteArray(), StandardCharsets.UTF_8);
        }
      } catch (XmpParsingException e) {
        log.debug("XMP parsing failed, reading raw metadata: {}", e.getMessage());
        try (COSInputStream is = pdMetadata.createInputStream()) {
          byte[] metadataBytes = is.readAllBytes();
          xmpString = new String(metadataBytes, StandardCharsets.UTF_8);
        }
      } catch (IOException e) {
        log.error("exception", e);
      }
    }
    other.put("XMPMetadata", xmpString);

    if (pdfBoxDoc.isEncrypted()) {
      encryption.put("IsEncrypted", true);
      PDEncryption pdfEncryption = pdfBoxDoc.getEncryption();
      encryption.put("EncryptionAlgorithm", pdfEncryption.getFilter());
      encryption.put("KeyLength", pdfEncryption.getLength());
    } else {
      encryption.put("IsEncrypted", false);
    }

    ObjectNode permissionsNode = objectMapper.createObjectNode();
    setNodePermissions(pdfBoxDoc, permissionsNode);

    ObjectNode pageInfoParent = objectMapper.createObjectNode();
    for (int pageNum = 0; pageNum < pdfBoxDoc.getNumberOfPages(); pageNum++) {
      ObjectNode pageInfo = objectMapper.createObjectNode();
      PDPage page = pdfBoxDoc.getPage(pageNum);

      PDRectangle mediaBox = page.getMediaBox();
      float width = mediaBox.getWidth();
      float height = mediaBox.getHeight();

      ObjectNode sizeInfo = objectMapper.createObjectNode();
      getDimensionInfo(sizeInfo, width, height);
      sizeInfo.put("Standard Page", getPageSize(width, height));
      pageInfo.set("Size", sizeInfo);

      pageInfo.put("Rotation", page.getRotation());
      pageInfo.put("Page Orientation", getPageOrientation(width, height));

      pageInfo.put("MediaBox", mediaBox.toString());

      PDRectangle cropBox = page.getCropBox();
      pageInfo.put("CropBox", cropBox == null ? "Undefined" : cropBox.toString());

      PDRectangle bleedBox = page.getBleedBox();
      pageInfo.put("BleedBox", bleedBox == null ? "Undefined" : bleedBox.toString());

      PDRectangle trimBox = page.getTrimBox();
      pageInfo.put("TrimBox", trimBox == null ? "Undefined" : trimBox.toString());

      PDRectangle artBox = page.getArtBox();
      pageInfo.put("ArtBox", artBox == null ? "Undefined" : artBox.toString());

      PDFTextStripper textStripper = new PDFTextStripper();
      textStripper.setStartPage(pageNum + 1);
      textStripper.setEndPage(pageNum + 1);
      String pageText = textStripper.getText(pdfBoxDoc);
      pageInfo.put("Text Characters Count", pageText.length());

      List<PDAnnotation> annotations = page.getAnnotations();
      int subtypeCount = 0;
      int contentsCount = 0;
      for (PDAnnotation annotation : annotations) {
        if (annotation.getSubtype() != null) subtypeCount++;
        if (annotation.getContents() != null) contentsCount++;
      }
      ObjectNode annotationsObject = objectMapper.createObjectNode();
      annotationsObject.put("AnnotationsCount", annotations.size());
      annotationsObject.put("SubtypeCount", subtypeCount);
      annotationsObject.put("ContentsCount", contentsCount);
      pageInfo.set("Annotations", annotationsObject);

      ArrayNode imagesArray = objectMapper.createArrayNode();
      PDResources resources = page.getResources();
      for (COSName name : resources.getXObjectNames()) {
        PDXObject xObject = resources.getXObject(name);
        if (xObject instanceof PDImageXObject image) {
          ObjectNode imageNode = objectMapper.createObjectNode();
          imageNode.put("Width", image.getWidth());
          imageNode.put("Height", image.getHeight());
          if (image.getMetadata() != null && image.getMetadata().getFile() != null
              && image.getMetadata().getFile().getFile() != null) {
            imageNode.put("Name", image.getMetadata().getFile().getFile());
          }
          if (image.getColorSpace() != null) {
            imageNode.put("ColorSpace", image.getColorSpace().getName());
          }
          imagesArray.add(imageNode);
        }
      }
      pageInfo.set("Images", imagesArray);

      ArrayNode linksArray = objectMapper.createArrayNode();
      Set<String> uniqueURIs = new HashSet<>();
      for (PDAnnotation annotation : annotations) {
        if (annotation instanceof PDAnnotationLink linkAnnotation) {
          if (linkAnnotation.getAction() instanceof PDActionURI uriAction) {
            String uri = uriAction.getURI();
            uniqueURIs.add(uri);
          }
        }
      }
      for (String uri : uniqueURIs) {
        ObjectNode linkNode = objectMapper.createObjectNode();
        linkNode.put("URI", uri);
        linksArray.add(linkNode);
      }
      pageInfo.set("Links", linksArray);

      ArrayNode fontsArray = objectMapper.createArrayNode();
      Map<String, ObjectNode> uniqueFontsMap = new HashMap<>();
      for (COSName fontName : resources.getFontNames()) {
        PDFont font = resources.getFont(fontName);
        ObjectNode fontNode = objectMapper.createObjectNode();

        fontNode.put("IsEmbedded", font.isEmbedded());
        fontNode.put("Name", font.getName());
        fontNode.put("Subtype", font.getType());

        PDFontDescriptor fontDescriptor = font.getFontDescriptor();
        if (fontDescriptor != null) {
          fontNode.put("ItalicAngle", fontDescriptor.getItalicAngle());
          int flags = fontDescriptor.getFlags();
          fontNode.put("IsItalic", (flags & 1) != 0);
          fontNode.put("IsBold", (flags & 64) != 0);
          fontNode.put("IsFixedPitch", (flags & 2) != 0);
          fontNode.put("IsSerif", (flags & 4) != 0);
          fontNode.put("IsSymbolic", (flags & 8) != 0);
          fontNode.put("IsScript", (flags & 16) != 0);
          fontNode.put("IsNonsymbolic", (flags & 32) != 0);
          fontNode.put("FontFamily", fontDescriptor.getFontFamily());
          fontNode.put("FontWeight", fontDescriptor.getFontWeight());
        }

        String uniqueKey = fontNode.toString();
        if (uniqueFontsMap.containsKey(uniqueKey)) {
          ObjectNode existingFontNode = uniqueFontsMap.get(uniqueKey);
          int count = existingFontNode.get("Count").asInt() + 1;
          existingFontNode.put("Count", count);
        } else {
          fontNode.put("Count", 1);
          uniqueFontsMap.put(uniqueKey, fontNode);
        }
      }
      for (ObjectNode uniqueFontNode : uniqueFontsMap.values()) {
        fontsArray.add(uniqueFontNode);
      }
      pageInfo.set("Fonts", fontsArray);

      ArrayNode colorSpacesArray = objectMapper.createArrayNode();
      Iterable<COSName> colorSpaceNames = resources.getColorSpaceNames();
      for (COSName name : colorSpaceNames) {
        PDColorSpace colorSpace = resources.getColorSpace(name);
        if (colorSpace instanceof PDICCBased iccBased) {
          PDStream iccData = iccBased.getPDStream();
          byte[] iccBytes = iccData.toByteArray();
          ObjectNode iccProfileNode = objectMapper.createObjectNode();
          iccProfileNode.put("ICC Profile Length", iccBytes.length);
          colorSpacesArray.add(iccProfileNode);
        }
      }
      pageInfo.set("Color Spaces & ICC Profiles", colorSpacesArray);

      Map<String, Integer> xObjectCountMap = new HashMap<>();
      for (COSName name : resources.getXObjectNames()) {
        PDXObject xObject = resources.getXObject(name);
        String xObjectType;
        if (xObject instanceof PDImageXObject) {
          xObjectType = "Image";
        } else if (xObject instanceof PDFormXObject) {
          xObjectType = "Form";
        } else {
          xObjectType = "Other";
        }
        xObjectCountMap.put(xObjectType, xObjectCountMap.getOrDefault(xObjectType, 0) + 1);
      }
      ObjectNode xObjectCountNode = objectMapper.createObjectNode();
      for (Map.Entry<String, Integer> entry : xObjectCountMap.entrySet()) {
        xObjectCountNode.put(entry.getKey(), entry.getValue());
      }
      pageInfo.set("XObjectCounts", xObjectCountNode);

      ArrayNode multimediaArray = objectMapper.createArrayNode();
      for (PDAnnotation annotation : annotations) {
        if ("RichMedia".equals(annotation.getSubtype())) {
          ObjectNode multimediaNode = objectMapper.createObjectNode();
          multimediaArray.add(multimediaNode);
        }
      }
      pageInfo.set("Multimedia", multimediaArray);

      pageInfoParent.set("Page " + (pageNum + 1), pageInfo);
    }

    jsonOutput.set("BasicInfo", basicInfo);
    jsonOutput.set("DocumentInfo", docInfoNode);
    jsonOutput.set("Compliancy", compliancy);
    jsonOutput.set("Encryption", encryption);
    jsonOutput.set("Permissions", permissionsNode);
    jsonOutput.set("Other", other);
    jsonOutput.set("PerPageInfo", pageInfoParent);

    String jsonString = objectMapper.writerWithDefaultPrettyPrinter().writeValueAsString(jsonOutput);
    return WebResponseUtils.bytesToWebResponse(jsonString.getBytes(StandardCharsets.UTF_8), "response.json", MediaType.APPLICATION_JSON);

  } catch (Exception e) {
    log.error("exception", e);
  }
  return null;
}

private void configureCefSettings(CefAppBuilder builder) {
  CefSettings settings = builder.getCefSettings();
  String basePath = InstallationPathConfig.getClientWebUIPath();
  log.info("basePath " + basePath);

  settings.cache_path = new File(basePath + "cache").getAbsolutePath();
  settings.root_cache_path = new File(basePath + "root_cache").getAbsolutePath();
  settings.log_file = new File(basePath, "debug.log").getAbsolutePath();
  settings.persist_session_cookies = true;
  settings.windowless_rendering_enabled = false;
  settings.log_severity = CefSettings.LogSeverity.LOGSEVERITY_INFO;

  builder.setAppHandler(new MavenCefAppHandlerAdapter() {
    @Override
    public void stateHasChanged(org.cef.CefApp.CefAppState state) {
      log.info("CEF state changed: " + state);
      if (state == CefApp.CefAppState.TERMINATED) {
        System.exit(0);
      }
    }
  });
}

public static PDRectangle textToPageSize(String size) {
  switch (size.toUpperCase()) {
    case "A0":
      return PDRectangle.A0;
    case "A1":
      return PDRectangle.A1;
    case "A2":
      return PDRectangle.A2;
    case "A3":
      return PDRectangle.A3;
    case "A4":
      return PDRectangle.A4;
    case "A5":
      return PDRectangle.A5;
    case "A6":
      return PDRectangle.A6;
    case "LETTER":
      return PDRectangle.LETTER;
    case "LEGAL":
      return PDRectangle.LEGAL;
    default:
      throw ExceptionUtils.createInvalidPageSizeException(size);
  }
}

private Object[] processArgsInPlace(Object[] originalArgs, boolean async) {
  if (originalArgs == null || originalArgs.length == 0) return originalArgs;

  for (int i = 0; i < originalArgs.length; i++) {
    Object arg = originalArgs[i];
    if (arg instanceof PDFFile pdfFile) {
      if (pdfFile.getFileInput() == null && pdfFile.getFileId() != null) {
        try {
          log.debug("Using fileId {} to get file content", pdfFile.getFileId());
          MultipartFile file = fileStorage.retrieveFile(pdfFile.getFileId());
          pdfFile.setFileInput(file);
        } catch (Exception e) {
          throw new RuntimeException("Failed to resolve file by ID: " + pdfFile.getFileId(), e);
        }
      } else if (async && pdfFile.getFileInput() != null) {
        try {
          log.debug("Making persistent copy of uploaded file for async processing");
          MultipartFile originalFile = pdfFile.getFileInput();
          String fileId = fileStorage.storeFile(originalFile);
          pdfFile.setFileId(fileId);
          MultipartFile persistentFile = fileStorage.retrieveFile(fileId);
          pdfFile.setFileInput(persistentFile);
          log.debug("Created persistent file copy with fileId: {}", fileId);
        } catch (IOException e) {
          throw new RuntimeException("Failed to create persistent copy of uploaded file", e);
        }
      }
    }
  }
  return originalArgs;
}

private Map<String, Object> getDockerMetrics() {
  Map<String, Object> dockerMetrics = new HashMap<>();

  dockerMetrics.put("docker_network_mode", System.getenv("DOCKER_NETWORK_MODE"));

  String containerName = System.getenv("CONTAINER_NAME");
  if (containerName != null && !containerName.isEmpty()) {
    dockerMetrics.put("container_name", containerName);
  }

  String composeProject = System.getenv("COMPOSE_PROJECT_NAME");
  String composeService = System.getenv("COMPOSE_SERVICE_NAME");
  if (composeProject != null && composeService != null) {
    dockerMetrics.put("compose_project", composeProject);
    dockerMetrics.put("compose_service", composeService);
  }

  String k8sPodName = System.getenv("KUBERNETES_POD_NAME");
  if (k8sPodName != null) {
    dockerMetrics.put("k8s_pod_name", k8sPodName);
    dockerMetrics.put("k8s_namespace", System.getenv("KUBERNETES_NAMESPACE"));
    dockerMetrics.put("k8s_node_name", System.getenv("KUBERNETES_NODE_NAME"));
  }

  dockerMetrics.put("version_tag", System.getenv("VERSION_TAG"));
  dockerMetrics.put("additional_features_off", System.getenv("ADDITIONAL_FEATURES_OFF"));
  dockerMetrics.put("fat_docker", System.getenv("FAT_DOCKER"));

  return dockerMetrics;
}

private void shutdownSchedulers() {
  log.info("Shutting down job queue");
  shuttingDown = true;

  jobMap.forEach((id, job) -> {
    if (!job.future.isDone()) {
      job.future.completeExceptionally(new RuntimeException("Server shutting down, job cancelled"));
    }
  });

  try {
    scheduler.shutdown();
    if (!scheduler.awaitTermination(5, TimeUnit.SECONDS)) {
      scheduler.shutdownNow();
    }

    jobExecutor.shutdown();
    if (!jobExecutor.awaitTermination(5, TimeUnit.SECONDS)) {
      jobExecutor.shutdownNow();
    }
  } catch (InterruptedException e) {
    Thread.currentThread().interrupt();
    scheduler.shutdownNow();
    jobExecutor.shutdownNow();
  }

  log.info("Job queue shutdown complete. Stats: total={}, rejected={}", totalQueuedJobs, rejectedJobs);
}

private Path createTempFile(String prefix) throws IOException {
  try {
    TempFileManager tempFileManager = ApplicationContextProvider.getBean(TempFileManager.class);
    if (tempFileManager != null) {
      File file = tempFileManager.createTempFile(".tmp");
      log.debug("Created and registered temp file via TempFileManager: {}", file);
      return file.toPath();
    }
  } catch (Exception e) {
    log.debug("TempFileManager not available, falling back to standard temp file creation");
  }

  Path file = Files.createTempFile(prefix + tempCounter.incrementAndGet() + "-", ".tmp");
  log.debug("Created temp file: {}", file);

  try {
    TempFileRegistry registry = ApplicationContextProvider.getBean(TempFileRegistry.class);
    if (registry != null) {
      registry.register(file);
      log.debug("Registered fallback temp file with registry: {}", file);
    }
  } catch (Exception e) {
    log.debug("Could not register fallback temp file with registry: {}", file);
  }

  return file;
}

private void setCommonMetadata(PDDocument pdf, PdfMetadata pdfMetadata)
{
    String title = pdfMetadata.getTitle();
    pdf.getDocumentInformation().setTitle(title);
    pdf.getDocumentInformation().setProducer(stirlingPDFLabel);
    pdf.getDocumentInformation().setSubject(pdfMetadata.getSubject());
    pdf.getDocumentInformation().setKeywords(pdfMetadata.getKeywords());
    pdf.getDocumentInformation().setModificationDate(Calendar.getInstance());

    String author = pdfMetadata.getAuthor();
    if (applicationProperties
                    .getPremium()
                    .getProFeatures()
                    .getCustomMetadata()
                    .isAutoUpdateMetadata()
            && runningProOrHigher)
    {
        author =
                applicationProperties
                        .getPremium()
                        .getProFeatures()
                        .getCustomMetadata()
                        .getAuthor();

        if (userService != null)
        {
            author = author.replace("username", userService.getCurrentUsername());
        }
    }
    pdf.getDocumentInformation().setAuthor(author);
}

@PostMapping(value = "/document-properties", consumes = "multipart/form-data")
@Operation(
        summary = "Get PDF document properties",
        description = "Returns title, author, subject, etc. Input:PDF Output:JSON Type:SISO")
public Map<String, String> getDocumentProperties(@ModelAttribute PDFFile file)
        throws IOException
{
    // Load the document in read-only mode to prevent modifications and ensure the integrity of
    // the original file.
    try (PDDocument document = pdfDocumentFactory.load(file.getFileInput(), true))
    {
        PDDocumentInformation info = document.getDocumentInformation();

        Map<String, String> properties = new HashMap<>();
        properties.put("title", info.getTitle());
        properties.put("author", info.getAuthor());
        properties.put("subject", info.getSubject());
        properties.put("keywords", info.getKeywords());
        properties.put("creator", info.getCreator());
        properties.put("producer", info.getProducer());
        properties.put("creationDate", info.getCreationDate().toString());
        properties.put("modificationDate", info.getModificationDate().toString());

        return properties;
    }
}

private void setupTrayIcon(Image icon)
{
    if (!SystemTray.isSupported())
    {
        log.warn("System tray is not supported");
        return;
    }

    try
    {
        systemTray = SystemTray.getSystemTray();

        // Create popup menu
        PopupMenu popup = new PopupMenu();

        // Create menu items
        MenuItem showItem = new MenuItem("Show");
        showItem.addActionListener(
                e -> {
                    frame.setVisible(true);
                    frame.setState(Frame.NORMAL);
                });

        MenuItem exitItem = new MenuItem("Exit");
        exitItem.addActionListener(
                e -> {
                    cleanup();
                    System.exit(0);
                });

        // Add menu items to popup menu
        popup.add(showItem);
        popup.addSeparator();
        popup.add(exitItem);

        // Create tray icon
        trayIcon = new TrayIcon(icon, "Stirling-PDF", popup);
        trayIcon.setImageAutoSize(true);

        // Add double-click behavior
        trayIcon.addActionListener(
                e -> {
                    frame.setVisible(true);
                    frame.setState(Frame.NORMAL);
                });

        // Add tray icon to system tray
        systemTray.add(trayIcon);

        // Modify frame behavior to minimize to tray
        frame.addWindowStateListener(
                new WindowStateListener()
                {
                    public void windowStateChanged(WindowEvent e)
                    {
                        if (e.getNewState() == Frame.ICONIFIED)
                        {
                            frame.setVisible(false);
                        }
                    }
                });

    }
    catch (AWTException e)
    {
        log.error("Error setting up system tray icon", e);
    }
}

private void handleSplitByDocCount(
        PDDocument sourceDocument,
        int documentCount,
        ZipOutputStream zipOut,
        String baseFilename)
        throws IOException
{
    log.debug("Starting handleSplitByDocCount with documentCount={}", documentCount);

    int totalPageCount = sourceDocument.getNumberOfPages();
    log.debug("Total pages in source document: {}", totalPageCount);

    int pagesPerDocument = totalPageCount / documentCount;
    int extraPages = totalPageCount % documentCount;
    log.debug("Pages per document: {}, Extra pages: {}", pagesPerDocument, extraPages);

    int currentPageIndex = 0;
    int fileIndex = 1;

    for (int i = 0; i < documentCount; i++)
    {
        log.debug("Creating document {} of {}", i + 1, documentCount);
        PDDocument currentDoc = null;

        try
        {
            currentDoc = pdfDocumentFactory.createNewDocumentBasedOnOldDocument(sourceDocument);
            log.debug("Successfully created document {} of {}", i + 1, documentCount);
        }
        catch (Exception e)
        {
            log.error("Error creating document {} of {}", i + 1, documentCount, e);
            throw ExceptionUtils.createFileProcessingException("split", e);
        }

        int pagesToAdd = pagesPerDocument + (i < extraPages ? 1 : 0);
        log.debug("Adding {} pages to document {}", pagesToAdd, i + 1);

        for (int j = 0; j < pagesToAdd; j++)
        {
            try
            {
                log.debug(
                        "Adding page {} (index {}) to document {}",
                        j + 1,
                        currentPageIndex,
                        i + 1);
                currentDoc.addPage(sourceDocument.getPage(currentPageIndex));
                log.debug("Successfully added page {} to document {}", j + 1, i + 1);
                currentPageIndex++;
            }
            catch (Exception e)
            {
                log.error("Error adding page {} to document {}", j + 1, i + 1, e);
                throw ExceptionUtils.createFileProcessingException("split", e);
            }
        }

        try
        {
            log.debug("Saving document {} with {} pages", i + 1, pagesToAdd);
            saveDocumentToZip(currentDoc, zipOut, baseFilename, fileIndex++);
            log.debug("Successfully saved document {}", i + 1);
        }
        catch (Exception e)
        {
            log.error("Error saving document {}", i + 1, e);
            throw e;
        }
    }

    log.debug("Completed handleSplitByDocCount with {} documents created", documentCount);
}

private void processWithTesseract(
        List<String> selectedLanguages, String ocrType, Path tempInputFile, Path tempOutputFile)
        throws IOException, InterruptedException
{
    // Create temp directory for Tesseract processing
    try (TempDirectory tempDir = new TempDirectory(tempFileManager))
    {
        File tempOutputDir = new File(tempDir.getPath().toFile(), "output");
        File tempImagesDir = new File(tempDir.getPath().toFile(), "images");
        File finalOutputFile = new File(tempDir.getPath().toFile(), "final_output.pdf");

        // Create directories
        tempOutputDir.mkdirs();
        tempImagesDir.mkdirs();

        PDFMergerUtility merger = new PDFMergerUtility();
        merger.setDestinationFileName(finalOutputFile.toString());

        try (PDDocument document = pdfDocumentFactory.load(tempInputFile.toFile()))
        {
            PDFRenderer pdfRenderer = new PDFRenderer(document);
            int pageCount = document.getNumberOfPages();

            for (int pageNum = 0; pageNum < pageCount; pageNum++)
            {
                PDPage page = document.getPage(pageNum);
                boolean hasText = false;

                // Check for existing text
                try (PDDocument tempDoc = new PDDocument())
                {
                    tempDoc.addPage(page);
                    PDFTextStripper stripper = new PDFTextStripper();
                    hasText = !stripper.getText(tempDoc).trim().isEmpty();
                }

                boolean shouldOcr =
                        switch (ocrType)
                        {
                            case "skip-text" -> !hasText;
                            case "force-ocr" -> true;
                            default -> true;
                        };

                File pageOutputPath =
                        new File(tempOutputDir, String.format("page_%d.pdf", pageNum));

                if (shouldOcr)
                {
                    // Convert page to image
                    BufferedImage image = pdfRenderer.renderImageWithDPI(pageNum, 300);
                    File imagePath =
                            new File(tempImagesDir, String.format("page_%d.png", pageNum));
                    ImageIO.write(image, "png", imagePath);

                    // Build OCR command
                    List<String> command = new ArrayList<>();
                    command.add("tesseract");
                    command.add(imagePath.toString());
                    command.add(
                            new File(tempOutputDir, String.format("page_%d", pageNum))
                                    .toString());
                    command.add("-l");
                    command.add(String.join("+", selectedLanguages));
                    command.add("pdf"); // Always output PDF

                    ProcessExecutorResult result =
                            ProcessExecutor.getInstance(ProcessExecutor.Processes.TESSERACT)
                                    .runCommandWithOutputHandling(command);

                    if (result.getRc() != 0)
                    {
                        throw ExceptionUtils.createRuntimeException(
                                "error.commandFailed",
                                "{0} command failed with exit code: {1}",
                                null,
                                "Tesseract",
                                result.getRc());
                    }

                    // Add OCR'd PDF to merger
                    merger.addSource(pageOutputPath);
                }
                else
                {
                    // Save original page without OCR
                    try (PDDocument pageDoc = new PDDocument())
                    {
                        pageDoc.addPage(page);
                        pageDoc.save(pageOutputPath);
                        merger.addSource(pageOutputPath);
                    }
                }
            }
        }

        // Merge all pages into final PDF
        merger.mergeDocuments(null);

        // Copy final output to the expected location
        Files.copy(
                finalOutputFile.toPath(),
                tempOutputFile,
                java.nio.file.StandardCopyOption.REPLACE_EXISTING);
    }
}

@PostMapping(value = "/auto-split-pdf", consumes = "multipart/form-data")
@Operation(
        summary = "Auto split PDF pages into separate documents",
        description =
                "This endpoint accepts a PDF file, scans each page for a specific QR code, and"
                        + " splits the document at the QR code boundaries. The output is a zip file"
                        + " containing each separate PDF document. Input:PDF Output:ZIP-PDF"
                        + " Type:SISO")
public ResponseEntity<byte[]> autoSplitPdf(@ModelAttribute AutoSplitPdfRequest request)
        throws IOException
{
    MultipartFile file = request.getFileInput();
    boolean duplexMode = Boolean.TRUE.equals(request.getDuplexMode());

    PDDocument document = null;
    List<PDDocument> splitDocuments = new ArrayList<>();
    Path zipFile = null;
    byte[] data = null;

    try
    {
        document = pdfDocumentFactory.load(file.getInputStream());
        PDFRenderer pdfRenderer = new PDFRenderer(document);
        pdfRenderer.setSubsamplingAllowed(true);

        for (int page = 0; page < document.getNumberOfPages(); ++page)
        {
            BufferedImage bim = pdfRenderer.renderImageWithDPI(page, 150);
            String result = decodeQRCode(bim);

            boolean isValidQrCode = VALID_QR_CONTENTS.contains(result);
            log.debug("detected qr code {}, code is vale={}", result, isValidQrCode);
            if (isValidQrCode && page != 0)
            {
                splitDocuments.add(new PDDocument());
            }

            if (!splitDocuments.isEmpty() && !isValidQrCode)
            {
                splitDocuments.get(splitDocuments.size() - 1).addPage(document.getPage(page));
            }
            else if (page == 0)
            {
                PDDocument firstDocument = new PDDocument();
                firstDocument.addPage(document.getPage(page));
                splitDocuments.add(firstDocument);
            }

            // If duplexMode is true and current page is a divider, then skip next page
            if (duplexMode && isValidQrCode)
            {
                page++;
            }
        }

        // Remove split documents that have no pages
        splitDocuments.removeIf(pdDocument -> pdDocument.getNumberOfPages() == 0);

        zipFile = Files.createTempFile("split_documents", ".zip");
        String filename =
                Filenames.toSimpleFileName(file.getOriginalFilename())
                        .replaceFirst("[.][^.]+$", "");

        try (ZipOutputStream zipOut = new ZipOutputStream(Files.newOutputStream(zipFile)))
        {
            for (int i = 0; i < splitDocuments.size(); i++)
            {
                String fileName = filename + "_" + (i + 1) + ".pdf";
                PDDocument splitDocument = splitDocuments.get(i);

                ByteArrayOutputStream baos = new ByteArrayOutputStream();
                splitDocument.save(baos);
                byte[] pdf = baos.toByteArray();

                ZipEntry pdfEntry = new ZipEntry(fileName);
                zipOut.putNextEntry(pdfEntry);
                zipOut.write(pdf);
                zipOut.closeEntry();
            }
        }

        data = Files.readAllBytes(zipFile);

        return WebResponseUtils.bytesToWebResponse(
                data, filename + ".zip", MediaType.APPLICATION_OCTET_STREAM);
    }
    catch (Exception e)
    {
        log.error("Error in auto split", e);
        throw e;
    }
    finally
    {
        // Clean up resources
        if (document != null)
        {
            try
            {
                document.close();
            }
            catch (IOException e)
            {
                log.error("Error closing main PDDocument", e);
            }
        }

        for (PDDocument splitDoc : splitDocuments)
        {
            try
            {
                splitDoc.close();
            }
            catch (IOException e)
            {
                log.error("Error closing split PDDocument", e);
            }
        }

        if (zipFile != null)
        {
            try
            {
                Files.deleteIfExists(zipFile);
            }
            catch (IOException e)
            {
                log.error("Error deleting temporary zip file", e);
            }
        }
    }
}

private void checkAndRefreshExplorer()
{
    if (!IS_WINDOWS)
    {
        return;
    }

    if (timeAt90Percent == -1)
    {
        timeAt90Percent = System.currentTimeMillis();
        stuckTimer =
                new Timer(
                        1000,
                        e -> {
                            long currentTime = System.currentTimeMillis();
                            if (currentTime - timeAt90Percent > stuckThreshold)
                            {
                                try
                                {
                                    log.debug(
                                            "Attempting Windows explorer refresh due to 90% stuck state");
                                    String currentDir = System.getProperty("user.dir");

                                    // Store current explorer PIDs before we start new one
                                    Set<String> existingPids = new HashSet<>();
                                    ProcessBuilder listExplorer =
                                            new ProcessBuilder(
                                                    "cmd",
                                                    "/c",
                                                    "wmic",
                                                    "process",
                                                    "where",
                                                    "name='explorer.exe'",
                                                    "get",
                                                    "ProcessId",
                                                    "/format:csv");
                                    Process process = listExplorer.start();
                                    BufferedReader reader =
                                            new BufferedReader(
                                                    new InputStreamReader(
                                                            process.getInputStream()));
                                    String line;
                                    while ((line =
                                                    BoundedLineReader.readLine(
                                                            reader, 5_000_000))
                                            != null)
                                    {
                                        if (line.matches(".*\\d+.*"))
                                        {
                                            String[] parts = line.trim().split(",");
                                            if (parts.length >= 2)
                                            {
                                                existingPids.add(
                                                        parts[parts.length - 1].trim());
                                            }
                                        }
                                    }
                                    process.waitFor(2, TimeUnit.SECONDS);

                                    // Start new explorer
                                    ProcessBuilder pb =
                                            new ProcessBuilder(
                                                    "cmd",
                                                    "/c",
                                                    "start",
                                                    "/min",
                                                    "/b",
                                                    "explorer.exe",
                                                    currentDir);
                                    pb.redirectErrorStream(true);
                                    explorerProcess = pb.start();

                                    // Schedule cleanup
                                    Timer cleanupTimer =
                                            new Timer(
                                                    2000,
                                                    cleanup -> {
                                                        try
                                                        {
                                                            // Find new explorer processes
                                                            ProcessBuilder findNewExplorer =
                                                                    new ProcessBuilder(
                                                                            "cmd",
                                                                            "/c",
                                                                            "wmic",
                                                                            "process",
                                                                            "where",
                                                                            "name='explorer.exe'",
                                                                            "get",
                                                                            "ProcessId",
                                                                            "/format:csv");
                                                            Process newProcess =
                                                                    findNewExplorer.start();
                                                            BufferedReader newReader =
                                                                    new BufferedReader(
                                                                            new InputStreamReader(
                                                                                    newProcess
                                                                                            .getInputStream()));
                                                            String newLine;
                                                            while ((newLine =
                                                                            BoundedLineReader
                                                                                    .readLine(
                                                                                            newReader,
                                                                                            5_000_000))
                                                                    != null)
                                                            {
                                                                if (newLine.matches(
                                                                        ".*\\d+.*"))
                                                                {
                                                                    String[] parts =
                                                                            newLine.trim()
                                                                                    .split(",");
                                                                    if (parts.length >= 2)
                                                                    {
                                                                        String pid =
                                                                                parts[
                                                                                        parts.length
                                                                                                - 1]
                                                                                        .trim();
                                                                        if (!existingPids
                                                                                .contains(
                                                                                        pid))
                                                                        {
                                                                            log.debug(
                                                                                    "Found new explorer.exe with PID: "
                                                                                            + pid);
                                                                            ProcessBuilder
                                                                                    killProcess =
                                                                                            new ProcessBuilder(
                                                                                                    "taskkill",
                                                                                                    "/PID",
                                                                                                    pid,
                                                                                                    "/F");
                                                                            killProcess
                                                                                    .redirectErrorStream(
                                                                                            true);
                                                                            Process killResult =
                                                                                    killProcess
                                                                                            .start();
                                                                            killResult.waitFor(
                                                                                    2,
                                                                                    TimeUnit
                                                                                            .SECONDS);
                                                                            log.debug(
                                                                                    "Explorer process terminated: "
                                                                                            + pid);
                                                                        }
                                                                    }
                                                                }
                                                            }
                                                            newProcess.waitFor(
                                                                    2, TimeUnit.SECONDS);
                                                        }
                                                        catch (Exception ex)
                                                        {
                                                            log.error(
                                                                    "Error cleaning up Windows explorer process",
                                                                    ex);
                                                        }
                                                    });
                                    cleanupTimer.setRepeats(false);
                                    cleanupTimer.start();
                                    stuckTimer.stop();
                                }
                                catch (Exception ex)
                                {
                                    log.error("Error refreshing Windows explorer", ex);
                                }
                            }
                        });
        stuckTimer.setRepeats(true);
        stuckTimer.start();
    }
}

private static Path unzipAndGetMainHtml(byte[] fileBytes) throws IOException
{
    Path tempDirectory = Files.createTempDirectory("unzipped_");
    try (ZipInputStream zipIn =
            ZipSecurity.createHardenedInputStream(new ByteArrayInputStream(fileBytes)))
    {
        ZipEntry entry = zipIn.getNextEntry();
        while (entry != null)
        {
            Path filePath = tempDirectory.resolve(sanitizeZipFilename(entry.getName()));
            if (entry.isDirectory())
            {
                Files.createDirectories(filePath); // Explicitly create the directory structure
            }
            else
            {
                Files.createDirectories(
                        filePath.getParent()); // Create parent directories if they don't exist
                Files.copy(zipIn, filePath);
            }
            zipIn.closeEntry();
            entry = zipIn.getNextEntry();
        }
    }

    // Search for the main HTML file.
    try (Stream<Path> walk = Files.walk(tempDirectory))
    {
        List<Path> htmlFiles = walk.filter(file -> file.toString().endsWith(".html")).toList();

        if (htmlFiles.isEmpty())
        {
            throw new IOException("No HTML files found in the unzipped directory.");
        }

        // Prioritize 'index.html' if it exists, otherwise use the first .html file
        for (Path htmlFile : htmlFiles)
        {
            if ("index.html".equals(htmlFile.getFileName().toString()))
            {
                return htmlFile;
            }
        }

        return htmlFiles.get(0);
    }
}

@Scheduled(
        fixedDelayString =
                "#{applicationProperties.system.tempFileManagement.cleanupIntervalMinutes}",
        timeUnit = TimeUnit.MINUTES)
public void scheduledCleanup()
{
    log.info("Running scheduled temporary file cleanup");
    long maxAgeMillis = tempFileManager.getMaxAgeMillis();

    // Clean up registered temp files (managed by TempFileRegistry)
    int registeredDeletedCount = tempFileManager.cleanupOldTempFiles(maxAgeMillis);
    log.info("Cleaned up {} registered temporary files", registeredDeletedCount);

    // Clean up registered temp directories
    int directoriesDeletedCount = 0;
    for (Path directory : registry.getTempDirectories())
    {
        try
        {
            if (Files.exists(directory))
            {
                GeneralUtils.deleteDirectory(directory);
                directoriesDeletedCount++;
                log.debug("Cleaned up temporary directory: {}", directory);
            }
        }
        catch (IOException e)
        {
            log.warn("Failed to clean up temporary directory: {}", directory, e);
        }
    }

    // Clean up PDFBox cache file
    cleanupPDFBoxCache();

    // Clean up unregistered temp files based on our cleanup strategy
    boolean containerMode = isContainerMode();
    int unregisteredDeletedCount = cleanupUnregisteredFiles(containerMode, true, maxAgeMillis);

    if (registeredDeletedCount > 0
            || unregisteredDeletedCount > 0
            || directoriesDeletedCount > 0)
    {
        log.info(
                "Scheduled cleanup complete. Deleted {} registered files, {} unregistered files, {} directories",
                registeredDeletedCount,
                unregisteredDeletedCount,
                directoriesDeletedCount);
    }
}

@Override
protected boolean shouldNotFilter(HttpServletRequest request) throws ServletException
{
    String uri = request.getRequestURI();
    String contextPath = request.getContextPath();
    String[] permitAllPatterns = {
        contextPath + "/login",
        contextPath + "/register",
        contextPath + "/error",
        contextPath + "/images/",
        contextPath + "/public/",
        contextPath + "/css/",
        contextPath + "/fonts/",
        contextPath + "/js/",
        contextPath + "/pdfjs/",
        contextPath + "/pdfjs-legacy/",
        contextPath + "/api/v1/info/status",
        contextPath + "/site.webmanifest"
    };

    for (String pattern : permitAllPatterns)
    {
        if (uri.startsWith(pattern)
                || uri.endsWith(".svg")
                || uri.endsWith(".png")
                || uri.endsWith(".ico"))
        {
            return true;
        }
    }

    return false;
}

private String renderLoggerInfo(Map<String, Map<String, Object>> loggerInfos, int width) {
  StringBuilder sb = new StringBuilder(8192);

  for (Map.Entry<String, Map<String, Object>> entry : loggerInfos.entrySet()) {
    Map<String, Object> info = entry.getValue();

    TableElement table = new TableElement(2, 10).leftCellPadding(1).rightCellPadding(1);
    TableElement appendersTable = new TableElement().rightCellPadding(1);

    Class<?> clazz = (Class<?>) info.get(LoggerHelper.clazz);
    table.row(label(LoggerHelper.name).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.name)))
        .row(label(LoggerHelper.clazz).style(Decoration.bold.bold()), label("" + clazz.getName()))
        .row(label(LoggerHelper.classLoader).style(Decoration.bold.bold()),
            label("" + info.get(LoggerHelper.classLoader)))
        .row(label(LoggerHelper.classLoaderHash).style(Decoration.bold.bold()),
            label("" + info.get(LoggerHelper.classLoaderHash)))
        .row(label(LoggerHelper.level).style(Decoration.bold.bold()),
            label("" + info.get(LoggerHelper.level)));
    if (info.get(LoggerHelper.effectiveLevel) != null) {
      table.row(label(LoggerHelper.effectiveLevel).style(Decoration.bold.bold()),
          label("" + info.get(LoggerHelper.effectiveLevel)));
    }

    if (info.get(LoggerHelper.config) != null) {
      table.row(label(LoggerHelper.config).style(Decoration.bold.bold()),
          label("" + info.get(LoggerHelper.config)));
    }

    table.row(label(LoggerHelper.additivity).style(Decoration.bold.bold()),
        label("" + info.get(LoggerHelper.additivity)))
        .row(label(LoggerHelper.codeSource).style(Decoration.bold.bold()),
            label("" + info.get(LoggerHelper.codeSource)));

    @SuppressWarnings("unchecked")
    List<Map<String, Object>> appenders = (List<Map<String, Object>>) info.get(LoggerHelper.appenders);
    if (appenders != null && !appenders.isEmpty()) {

      for (Map<String, Object> appenderInfo : appenders) {
        Class<?> appenderClass = (Class<?>) appenderInfo.get(LoggerHelper.clazz);

        appendersTable.row(label(LoggerHelper.name).style(Decoration.bold.bold()),
            label("" + appenderInfo.get(LoggerHelper.name)));
        appendersTable.row(label(LoggerHelper.clazz), label("" + appenderClass.getName()));
        appendersTable.row(label(LoggerHelper.classLoader), label("" + info.get(LoggerHelper.classLoader)));
        appendersTable.row(label(LoggerHelper.classLoaderHash),
            label("" + info.get(LoggerHelper.classLoaderHash)));
        if (appenderInfo.get(LoggerHelper.file) != null) {
          appendersTable.row(label(LoggerHelper.file), label("" + appenderInfo.get(LoggerHelper.file)));
        }
        if (appenderInfo.get(LoggerHelper.target) != null) {
          appendersTable.row(label(LoggerHelper.target),
              label("" + appenderInfo.get(LoggerHelper.target)));
        }
        if (appenderInfo.get(LoggerHelper.blocking) != null) {
          appendersTable.row(label(LoggerHelper.blocking),
              label("" + appenderInfo.get(LoggerHelper.blocking)));
        }
        if (appenderInfo.get(LoggerHelper.appenderRef) != null) {
          appendersTable.row(label(LoggerHelper.appenderRef),
              label("" + appenderInfo.get(LoggerHelper.appenderRef)));
        }
      }

      table.row(label("appenders").style(Decoration.bold.bold()), appendersTable);
    }

    sb.append(RenderUtil.render(table, width)).append('\n');
  }
  return sb.toString();
}

private ApiResponse processInitSessionRequest(ApiRequest apiRequest) throws ApiException {
  ApiResponse response = new ApiResponse();

  // create session
  Session session = sessionManager.createSession();
  if (session != null) {

    // Result Distributor
    SharingResultDistributorImpl resultDistributor = new SharingResultDistributorImpl(session);
    // create consumer
    ResultConsumer resultConsumer = new ResultConsumerImpl();
    resultDistributor.addConsumer(resultConsumer);
    session.setResultDistributor(resultDistributor);

    resultDistributor.appendResult(new MessageModel("Welcome to arthas!"));

