    if (ClassLoader.getSystemClassLoader().getResource("jdk/jfr/Recording.class") != null) {
      commandClassList.add(JFRCommand.class);
    }
  } catch (Throwable e) {
    logger.error("This jdk version not support jfr command");
  }

  for (final Class<? extends AnnotatedCommand> clazz : commandClassList) {
    final Name name = clazz.getAnnotation(Name.class);
    if (name != null && name.value() != null) {
      if (disabledCommands.contains(name.value())) continue;
    }
    commands.add(Command.create(clazz));
  }
}

@Override
public String draw() {
  final StringBuilder buf = new StringBuilder();
  try {
    if (GlobalOptions.isUsingJson) {
      return JSON.toJSONString(object, JSONWriter.Feature.IgnoreErrorGetter);
    }
    renderObject(object, 0, deep, buf);
    return buf.toString();
  } catch (ObjectTooLargeException e) {
    buf.append(" Object size exceeds size limit: ")
        .append(maxObjectLength)
        .append(", try to specify -M size_limit in your command, check the help command for more.");
    return buf.toString();
  } catch (Throwable t) {
    logger.error("ObjectView draw error, object class: {}", object.getClass(), t);
    return "ERROR DATA!!! object class: " + object.getClass() + ", exception class: " + t.getClass()
        + ", exception message: " + t.getMessage();
  }
}

public static Map<String, String> removeDashKey(Map<String, String> map) {
  final Map<String, String> result = new HashMap<>(map.size());

  for (Entry<String, String> entry : map.entrySet()) {
    String key = entry.getKey();

    if (key.contains("-")) {
      final StringBuilder sb = new StringBuilder(key.length());
      for (int i = 0; i < key.length(); i++) {
        if (key.charAt(i) == '-' && (i + 1 < key.length()) && Character.isAlphabetic(key.charAt(i + 1))) {
          ++i;
          final char upperChar = Character.toUpperCase(key.charAt(i));
          sb.append(upperChar);
        } else {
          sb.append(key.charAt(i));
        }
      }
      key = sb.toString();
    }

    result.put(key, entry.getValue());
  }

  return result;
}

final void advance() {
  if (nextEntry != null && (nextEntry = nextEntry.next) != null) return;

  while (nextTableIndex >= 0) {
    if ((nextEntry = currentTable[nextTableIndex--]) != null) return;
  }

  while (nextSegmentIndex >= 0) {
    final Segment<K, V> seg = segments[nextSegmentIndex--];
    if (seg.count != 0) {
      currentTable = seg.table;
      for (int j = currentTable.length - 1; j >= 0; --j) {
        if ((nextEntry = currentTable[j]) != null) {
          nextTableIndex = j - 1;
          return;
        }
      }
    }
  }
}

public static boolean containsConstant(Enum<?>[] enumValues, String constant, boolean caseSensitive) {
  for (Enum<?> candidate : enumValues) {
    if (caseSensitive) {
      if (candidate.toString().equals(constant)) return true;
    } else {
      if (candidate.toString().equalsIgnoreCase(constant)) return true;
    }
  }
  return false;
}

public synchronized long size() throws IOException {
    if (LOG.isTraceEnabled()) {
        LOG.trace("size() called");
    }

    if (this.endtime >= src.getEndTime()) {
        return src.size() - skippedAtStart;
    }

    long pos = in.getPosition();

    if (LOG.isTraceEnabled()) {
        LOG.trace("saved pos () = " + pos);
    }

    LogEntry e;

    LogSkipList.Mark lastseg = src.getSkipList().findMarkBefore(this.endtime);
    in.seek(lastseg.getBytes());
    buf = ""; // clear the buf so we don't get something we read before we sought
    // number of entries skipped to get to the end of the iterator, less the number skipped to get to the start
    long count = lastseg.getEntriesSkipped() - skippedAtStart;

    while ((e = readNextEntry()) != null) {
        if (LOG.isTraceEnabled()) {
            // LOG.trace(e);
        }
        if (e.getTimestamp() > this.endtime) {
            break;
        }
        count++;
    }
    in.seek(pos);
    buf = "";

    if (LOG.isTraceEnabled()) {
        LOG.trace("size() = " + count);
    }

    return count;
}

void handleWrite(SelectionKey k) throws IOException {
    if (outgoingBuffers.isEmpty()) {
        return;
    }

    /*
     * This is going to reset the buffer position to 0 and the
     * limit to the size of the buffer, so that we can fill it
     * with data from the non-direct buffers that we need to
     * send.
     */
    ByteBuffer directBuffer = NIOServerCnxnFactory.getDirectBuffer();
    if (directBuffer == null) {
        ByteBuffer[] bufferList = new ByteBuffer[outgoingBuffers.size()];
        // Use gathered write call. This updates the positions of the
        // byte buffers to reflect the bytes that were written out.
        sock.write(outgoingBuffers.toArray(bufferList));

        // Remove the buffers that we have sent
        ByteBuffer bb;
        while ((bb = outgoingBuffers.peek()) != null) {
            if (bb == ServerCnxnFactory.closeConn) {
                throw new CloseRequestException("close requested", DisconnectReason.CLIENT_CLOSED_CONNECTION);
            }
            if (bb == packetSentinel) {
                packetSent();
            }
            if (bb.remaining() > 0) {
                break;
            }
            outgoingBuffers.remove();
        }
    } else {
        directBuffer.clear();

        for (ByteBuffer b : outgoingBuffers) {
            if (directBuffer.remaining() < b.remaining()) {
                /*
                 * When we call put later, if the directBuffer is to
                 * small to hold everything, nothing will be copied,
                 * so we've got to slice the buffer if it's too big.
                 */
                b = (ByteBuffer) b.slice().limit(directBuffer.remaining());
            }
            /*
             * put() is going to modify the positions of both
             * buffers, put we don't want to change the position of
             * the source buffers (we'll do that after the send, if
             * needed), so we save and reset the position after the
             * copy
             */
            int p = b.position();
            directBuffer.put(b);
            b.position(p);
            if (directBuffer.remaining() == 0) {
                break;
            }
        }
        /*
         * Do the flip: limit becomes position, position gets set to
         * 0. This sets us up for the write.
         */
        directBuffer.flip();

        int sent = sock.write(directBuffer);

        ByteBuffer bb;

        // Remove the buffers that we have sent
        while ((bb = outgoingBuffers.peek()) != null) {
            if (bb == ServerCnxnFactory.closeConn) {
                throw new CloseRequestException("close requested", DisconnectReason.CLIENT_CLOSED_CONNECTION);
            }
            if (bb == packetSentinel) {
                packetSent();
            }
            if (sent < bb.remaining()) {
                /*
                 * We only partially sent this buffer, so we update
                 * the position and exit the loop.
                 */
                bb.position(bb.position() + sent);
                break;
            }
            /* We've sent the whole buffer, so drop the buffer */
            sent -= bb.remaining();
            outgoingBuffers.remove();
        }
    }
}

private void select() {
    try {
        selector.select();

        Set<SelectionKey> selected = selector.selectedKeys();
        ArrayList<SelectionKey> selectedList = new ArrayList<>(selected);
        Collections.shuffle(selectedList);
        Iterator<SelectionKey> selectedKeys = selectedList.iterator();
        while (!stopped && selectedKeys.hasNext()) {
            SelectionKey key = selectedKeys.next();
            selected.remove(key);

            if (!key.isValid()) {
                cleanupSelectionKey(key);
                continue;
            }
            if (key.isReadable() || key.isWritable()) {
                handleIO(key);
            } else {
                LOG.warn("Unexpected ops in select {}", key.readyOps());
            }
        }
    } catch (IOException e) {
        LOG.warn("Ignoring IOException while selecting", e);
    }
}

@Override
public void close() throws IOException {
    boolean triedToClose = false;
    boolean success = false;
    try {
        flush();
        ((FileOutputStream) out).getFD().sync();

        triedToClose = true;
        super.close();
        success = true;
    } finally {
        if (success) {
            boolean renamed = tmpFile.renameTo(origFile);
            if (!renamed) {
                // On windows, renameTo does not replace.
                if (!origFile.delete() || !tmpFile.renameTo(origFile)) {
                    throw new IOException("Could not rename temporary file " + tmpFile + " to " + origFile);
                }
            }
        } else {
            if (!triedToClose) {
                // If we failed when flushing, try to close it to not leak an FD
                IOUtils.closeStream(out);
            }
            // close wasn't successful, try to delete the tmp file
            if (!tmpFile.delete()) {
                LOG.warn("Unable to delete tmp file {}", tmpFile);
            }
        }
    }
}

public void doWork() throws InterruptedException {
    if (!key.isValid()) {
        selectorThread.cleanupSelectionKey(key);
        return;
    }

    if (key.isReadable() || key.isWritable()) {
        cnxn.doIO(key);

        // Check if we shutdown or doIO() closed this connection
        if (stopped) {
            cnxn.close(ServerCnxn.DisconnectReason.SERVER_SHUTDOWN);
            return;
        }
        if (!key.isValid()) {
            selectorThread.cleanupSelectionKey(key);
            return;
        }
        touchCnxn(cnxn);
    }

    // Mark this connection as once again ready for selection
    cnxn.enableSelectable();
    // Push an update request on the queue to resume selecting
    // on the current set of interest ops, which may have changed
    // as a result of the I/O operations we just performed.
    if (!selectorThread.addInterestOpsUpdateRequest(key)) {
        cnxn.close(ServerCnxn.DisconnectReason.CONNECTION_MODE_CHANGED);
    }
}

public String getStatus(String name, long timeout) throws KeeperException, InterruptedException {
    Stat stat = new Stat();
    byte[] data = null;
    long endTime = Time.currentElapsedTime() + timeout;
    KeeperException lastException = null;
    for (int i = 0; i < maxTries && endTime > Time.currentElapsedTime(); i++) {
        try {
            data = zk.getData(reportsNode + '/' + name, false, stat);
            if (LOG.isDebugEnabled()) {
                LOG.debug("Got Data: " + ((data == null) ? "null" : new String(data)));
            }
            lastException = null;
            break;
        } catch (ConnectionLossException e) {
            lastException = e;
        } catch (NoNodeException e) {
            final Object eventObj = new Object();
            synchronized (eventObj) {
                // wait for the node to appear
                Stat eStat = zk.exists(reportsNode + '/' + name, new Watcher() {
                    public void process(WatchedEvent event) {
                        synchronized (eventObj) {
                            eventObj.notifyAll();
                        }
                    }
                });
                if (eStat == null) {
                    eventObj.wait(endTime - Time.currentElapsedTime());
                }
            }
            lastException = e;
        }
    }
    if (lastException != null) {
        throw lastException;
    }
    return new String(data);
}

public static void main(String[] args) throws IOException {
    MergedLogSource source = new MergedLogSource(args);

    PrintStream ps_ms = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-ms.out")));
    PrintStream ps_sec = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-sec.out")));
    PrintStream ps_min = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-min.out")));
    PrintStream ps_hour = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-hour.out")));
    LogIterator iter;

    System.out.println(source);
    iter = source.iterator();
    long currentms = 0;
    long currentsec = 0;
    long currentmin = 0;
    long currenthour = 0;
    Set<Long> zxids_ms = new HashSet<Long>();
    long zxid_sec = 0;
    long zxid_min = 0;
    long zxid_hour = 0;

    while (iter.hasNext()) {
        LogEntry e = iter.next();
        TransactionEntry cxn = (TransactionEntry) e;

        long ms = cxn.getTimestamp();
        long sec = ms / MS_PER_SEC;
        long min = ms / MS_PER_MIN;
        long hour = ms / MS_PER_HOUR;

        if (currentms != ms && currentms != 0) {
            ps_ms.println("" + currentms + " " + zxids_ms.size());

            zxid_sec += zxids_ms.size();
            zxid_min += zxids_ms.size();
            zxid_hour += zxids_ms.size();
            zxids_ms.clear();
        }

        if (currentsec != sec && currentsec != 0) {
            ps_sec.println("" + currentsec * MS_PER_SEC + " " + zxid_sec);

            zxid_sec = 0;
        }

        if (currentmin != min && currentmin != 0) {
            ps_min.println("" + currentmin * MS_PER_MIN + " " + zxid_min);

            zxid_min = 0;
        }

        if (currenthour != hour && currenthour != 0) {
            ps_hour.println("" + currenthour * MS_PER_HOUR + " " + zxid_hour);

            zxid_hour = 0;
        }

        currentms = ms;
        currentsec = sec;
        currentmin = min;
        currenthour = hour;

        zxids_ms.add(cxn.getZxid());
    }

    iter.close();
    ps_ms.close();
    ps_sec.close();
    ps_min.close();
    ps_hour.close();
}

public void run() {
    try {
        while (!stopped) {
            try {
                select();
                processAcceptedConnections();
                processInterestOpsUpdateRequests();
            } catch (RuntimeException e) {
                LOG.warn("Ignoring unexpected runtime exception", e);
            } catch (Exception e) {
                LOG.warn("Ignoring unexpected exception", e);
            }
        }

        // Close connections still pending on the selector. Any others
        // with in-flight work, let drain out of the work queue.
        for (SelectionKey key : selector.keys()) {
            NIOServerCnxn cnxn = (NIOServerCnxn) key.attachment();
            if (cnxn.isSelectable()) {
                cnxn.close(ServerCnxn.DisconnectReason.SERVER_SHUTDOWN);
            }
            cleanupSelectionKey(key);
        }
        SocketChannel accepted;
        while ((accepted = acceptedQueue.poll()) != null) {
            fastCloseSock(accepted);
        }
        updateQueue.clear();
    } finally {
        closeSelector();
        // This will wake up the accept thread and the other selector
        // threads, and tell the worker thread pool to begin shutdown.
        NIOServerCnxnFactory.this.stop();
        LOG.info("selector thread exited run method");
    }
}

public static synchronized ZooKeeper getClient(String contextPath, String session, int expireTime)
        throws IOException {
    final String connectionId = concat(contextPath, session);

    ZooKeeper zk = zkMap.get(connectionId);
    if (zk == null) {
        if (LOG.isInfoEnabled()) {
            LOG.info(String.format("creating new connection for : '%s'", connectionId));
        }
        Endpoint e = contextMap.get(contextPath);
        zk = new ZooKeeper(e.getHostPort(), 30000, new MyWatcher(connectionId));

        for (Map.Entry<String, String> p : e.getZooKeeperAuthInfo().entrySet()) {
            zk.addAuthInfo("digest", String.format("%s:%s", p.getKey(), p.getValue()).getBytes());
        }

        zkMap.put(connectionId, zk);

        // a session should automatically expire after an amount of time
        if (session != null) {
            zkSessionTimers.put(connectionId, new SessionTimerTask(expireTime, session, contextPath, timer));
        }
    }
    return zk;
}

public InputStream getView(final int size) {
    if (view == null) {
        view = new InputStream() {
            @Override
            public int read() throws IOException {
                if (viewSize < 1) {
                    return -1;
                }
                final int res = DataReader.this.read();
                if (res > 0) {
                    viewSize--;
                }
                return res;
            }

            @Override
            public int read(final byte[] buffer) throws IOException {
                return read(buffer, 0, buffer.length);
            }

            @Override
            public int read(final byte[] buffer, final int offset, final int count)
                    throws IOException {
                if (viewSize < 1) {
                    return -1;
                }

                final int res =
                        DataReader.this.read(buffer, offset, Math.min(viewSize, count));
                viewSize -= res;

                return res;
            }

            @Override
            public long skip(final long amount) throws IOException {
                if (viewSize < 1) {
                    return 0;
                }
                final int res = (int) DataReader.this.skipBytes(Math.min(amount, viewSize));
                viewSize -= res;

                return res;
            }

            @Override
            public int available() {
                return viewSize;
            }

            @Override
            public void close() {
                viewSize = 0;
            }

            @Override
            public boolean markSupported() {
                return false;
            }
        };
    }
    viewSize = size;

    return view;
}

private ArrayList<Object> getSpecialItems() {
    synchronized (DownloadManager.this) {
        ArrayList<Mission> pending = new ArrayList<>(mMissionsPending);
        ArrayList<Mission> finished = new ArrayList<>(mMissionsFinished);
        List<Mission> remove = new ArrayList<>(hidden);

        // hide missions (if required)
        remove.removeIf(mission -> pending.remove(mission) || finished.remove(mission));

        int fakeTotal = pending.size();
        if (fakeTotal > 0) {
            fakeTotal++;
        }

        fakeTotal += finished.size();
        if (finished.size() > 0) {
            fakeTotal++;
        }

        ArrayList<Object> list = new ArrayList<>(fakeTotal);
        if (pending.size() > 0) {
            list.add(PENDING);
            list.addAll(pending);
        }
        if (finished.size() > 0) {
            list.add(FINISHED);
            list.addAll(finished);
        }

        hasFinished = finished.size() > 0;

        return list;
    }
}

private void loadPendingMissions(Context ctx) {
    File[] subs = mPendingMissionsDir.listFiles();

    if (subs == null) {
        Log.e(TAG, "listFiles() returned null");
        return;
    }
    if (subs.length < 1) {
        return;
    }
    if (DEBUG) {
        Log.d(
                TAG,
                "Loading pending downloads from directory: "
                        + mPendingMissionsDir.getAbsolutePath());
    }

    File tempDir = pickAvailableTemporalDir(ctx);
    Log.i(TAG, "using '" + tempDir + "' as temporal directory");

    for (File sub : subs) {
        if (!sub.isFile()) {
            continue;
        }
        if (sub.getName().equals(".tmp")) {
            continue;
        }

        DownloadMission mis = Utility.readFromFile(sub);
        if (mis == null || mis.isFinished() || mis.hasInvalidStorage()) {
            //noinspection ResultOfMethodCallIgnored
            sub.delete();
            continue;
        }

        mis.threads = new Thread[0];

        boolean exists;
        try {
            mis.storage = StoredFileHelper.deserialize(mis.storage, ctx);
            exists = !mis.storage.isInvalid() && mis.storage.existsAsFile();
        } catch (Exception ex) {
            Log.e(TAG, "Failed to load the file source of " + mis.storage.toString(), ex);
            mis.storage.invalidate();
            exists = false;
        }

        if (mis.isPsRunning()) {
            if (mis.psAlgorithm.worksOnSameFile) {
                // Incomplete post-processing results in a corrupted download file
                // because the selected algorithm works on the same file to save space.
                // the file will be deleted if the storage API
                // is Java IO (avoid showing the "Save as..." dialog)
                if (exists && mis.storage.isDirect() && !mis.storage.delete()) {
                    Log.w(TAG, "Unable to delete incomplete download file: " + sub.getPath());
                }
            }

            mis.psState = 0;
            mis.errCode = DownloadMission.ERROR_POSTPROCESSING_STOPPED;
        } else if (!exists) {
            tryRecover(mis);

            // the progress is lost, reset mission state
            if (mis.isInitialized()) {
                mis.resetState(true, true, DownloadMission.ERROR_PROGRESS_LOST);
            }
        }

        if (mis.psAlgorithm != null) {
            mis.psAlgorithm.cleanupTemporalDir();
            mis.psAlgorithm.setTemporalDir(tempDir);
        }

        mis.metadata = sub;
        mis.maxRetry = mPrefMaxRetry;
        mis.mHandler = mHandler;

        mMissionsPending.add(mis);
    }

    if (mMissionsPending.size() > 1) {
        Collections.sort(mMissionsPending, Comparator.comparingLong(Mission::getTimestamp));
    }
}

public void updateMission(Mission mission) {
    ContentValues values = getValuesOfMission(Objects.requireNonNull(mission));
    SQLiteDatabase database = getWritableDatabase();
    String ts = String.valueOf(mission.timestamp);

    int rowsAffected;

    if (mission instanceof FinishedMission) {
        if (mission.storage.isInvalid()) {
            rowsAffected =
                    database.update(
                            FINISHED_TABLE_NAME, values, KEY_TIMESTAMP + " = ?", new String[] {ts});
        } else {
            rowsAffected =
                    database.update(
                            FINISHED_TABLE_NAME,
                            values,
                            KEY_PATH + " = ?",
                            new String[] {mission.storage.getUri().toString()});
        }
    } else {
        throw new UnsupportedOperationException("DownloadMission");
    }

    if (rowsAffected != 1) {
        Log.e(
                "FinishedMissionStore",
                "Expected 1 row to be affected by update but got " + rowsAffected);
    }
}

private void startMission(Intent intent) {
    String[] urls = intent.getStringArrayExtra(EXTRA_URLS);
    Uri path = IntentCompat.getParcelableExtra(intent, EXTRA_PATH, Uri.class);
    Uri parentPath = IntentCompat.getParcelableExtra(intent, EXTRA_PARENT_PATH, Uri.class);
    int threads = intent.getIntExtra(EXTRA_THREADS, 1);
    char kind = intent.getCharExtra(EXTRA_KIND, '?');
    String psName = intent.getStringExtra(EXTRA_POSTPROCESSING_NAME);
    String[] psArgs = intent.getStringArrayExtra(EXTRA_POSTPROCESSING_ARGS);
    String source = intent.getStringExtra(EXTRA_SOURCE);
    long nearLength = intent.getLongExtra(EXTRA_NEAR_LENGTH, 0);
    String tag = intent.getStringExtra(EXTRA_STORAGE_TAG);
    final var recovery =
            IntentCompat.getParcelableArrayListExtra(
                    intent, EXTRA_RECOVERY_INFO, MissionRecoveryInfo.class);
    Objects.requireNonNull(recovery);

    StoredFileHelper storage;
    try {
        storage = new StoredFileHelper(this, parentPath, path, tag);
    } catch (IOException e) {
        throw new RuntimeException(e); // this never should happen
    }

    Postprocessing ps;
    if (psName == null) {
        ps = null;
    } else {
        ps = Postprocessing.getAlgorithm(psName, psArgs);
    }

    final DownloadMission mission = new DownloadMission(urls, storage, kind, ps);
    mission.threadCount = threads;
    mission.source = source;
    mission.nearLength = nearLength;
    mission.recoveryInfo = recovery.toArray(new MissionRecoveryInfo[0]);

    if (ps != null) {
        ps.setTemporalDir(DownloadManager.pickAvailableTemporalDir(this));
    }

    // first check the actual network status
    handleConnectivityState(true);

    mManager.startMission(mission);
}

@Override
public void run() {
    if (mMission.current > 0) {
        mMission.resetState(false, true, DownloadMission.ERROR_NOTHING);
    }

    int retryCount = 0;
    int httpCode = 204;

    while (true) {
        try {
            if (mMission.blocks == null && mMission.current == 0) {
                // calculate the whole size of the mission
                long finalLength = 0;
                long lowestSize = Long.MAX_VALUE;

                for (int i = 0; i < mMission.urls.length && mMission.running; i++) {
                    mConn = mMission.openConnection(mMission.urls[i], true, 0, 0);
                    mMission.establishConnection(mId, mConn);
                    dispose();

                    if (Thread.interrupted()) {
                        return;
                    }
                    long length = Utility.getTotalContentLength(mConn);

                    if (i == 0) {
                        httpCode = mConn.getResponseCode();
                        mMission.length = length;
                    }

                    if (length > 0) {
                        finalLength += length;
                    }
                    if (length < lowestSize) {
                        lowestSize = length;
                    }
                }

                mMission.nearLength = finalLength;

                // reserve space at the start of the file
                if (mMission.psAlgorithm != null && mMission.psAlgorithm.reserveSpace) {
                    if (lowestSize < 1) {
                        // the length is unknown use the default size
                        mMission.offsets[0] = RESERVE_SPACE_DEFAULT;
                    } else {
                        // use the smallest resource size to download, otherwise, use the maximum
                        mMission.offsets[0] =
                                lowestSize < RESERVE_SPACE_MAXIMUM
                                        ? lowestSize
                                        : RESERVE_SPACE_MAXIMUM;
                    }
                }
            } else {
                // ask for the current resource length
                mConn = mMission.openConnection(true, 0, 0);
                mMission.establishConnection(mId, mConn);
                dispose();

                if (!mMission.running || Thread.interrupted()) {
                    return;
                }

                httpCode = mConn.getResponseCode();
                mMission.length = Utility.getTotalContentLength(mConn);
            }

            if (mMission.length == 0 || httpCode == 204) {
                mMission.notifyError(DownloadMission.ERROR_HTTP_NO_CONTENT, null);
                return;
            }

            // check for dynamic generated content
            if (mMission.length == -1 && mConn.getResponseCode() == 200) {
                mMission.blocks = new int[0];
                mMission.length = 0;
                mMission.unknownLength = true;

                if (DEBUG) {
                    Log.d(TAG, "falling back (unknown length)");
                }
            } else {
                // Open again
                mConn = mMission.openConnection(true, mMission.length - 10, mMission.length);
                mMission.establishConnection(mId, mConn);
                dispose();

                if (!mMission.running || Thread.interrupted()) {
                    return;
                }

                synchronized (mMission.LOCK) {
                    if (mConn.getResponseCode() == 206) {

                        if (mMission.threadCount > 1) {
                            int count = (int) (mMission.length / DownloadMission.BLOCK_SIZE);
                            if ((count * DownloadMission.BLOCK_SIZE) < mMission.length) {
                                count++;
                            }

                            mMission.blocks = new int[count];
                        } else {
                            // if one thread is required don't calculate blocks, is useless
                            mMission.blocks = new int[0];
                            mMission.unknownLength = false;
                        }

                        if (DEBUG) {
                            Log.d(TAG, "http response code = " + mConn.getResponseCode());
                        }
                    } else {
                        // Fallback to single thread
                        mMission.blocks = new int[0];
                        mMission.unknownLength = false;

                        if (DEBUG) {
                            Log.d(TAG, "falling back due http response code = " + mConn.getResponseCode());
                        }
                    }
                }

                if (!mMission.running || Thread.interrupted()) {
                    return;
                }
            }

            try (SharpStream fs = mMission.storage.getStream()) {
                fs.setLength(mMission.offsets[mMission.current] + mMission.length);
                fs.seek(mMission.offsets[mMission.current]);
            }

            if (!mMission.running || Thread.interrupted()) {
                return;
            }

            if (!mMission.unknownLength && mMission.recoveryInfo != null) {
                String entityTag = mConn.getHeaderField("ETAG");
                String lastModified = mConn.getHeaderField("Last-Modified");
                MissionRecoveryInfo recovery = mMission.recoveryInfo[mMission.current];

                if (!TextUtils.isEmpty(entityTag)) {
                    recovery.setValidateCondition(entityTag);
                } else if (!TextUtils.isEmpty(lastModified)) {
                    // Note: this is less precise
                    recovery.setValidateCondition(lastModified);
                } else {
                    recovery.setValidateCondition(null);
                }
            }

            mMission.running = false;
            break;
        } catch (InterruptedIOException | ClosedByInterruptException e) {
            return;
        } catch (Exception e) {
            if (!mMission.running || super.isInterrupted()) {
                return;
            }

            if (e instanceof DownloadMission.HttpError
                    && ((DownloadMission.HttpError) e).statusCode == ERROR_HTTP_FORBIDDEN) {
                // for youtube streams. The url has expired
                interrupt();
                mMission.doRecover(ERROR_HTTP_FORBIDDEN);
                return;
            }

            if (e instanceof IOException && e.getMessage().contains("Permission denied")) {
                mMission.notifyError(DownloadMission.ERROR_PERMISSION_DENIED, e);
                return;
            }

            if (retryCount++ > mMission.maxRetry) {
                Log.e(TAG, "initializer failed", e);
                mMission.notifyError(e);
                return;
            }

            Log.e(TAG, "initializer failed, retrying", e);
        }
    }

    mMission.start();
}

public void pause() {
    if (!running) {
        return;
    }

    if (isPsRunning()) {
        if (DEBUG) {
            Log.w(TAG, "pause during post-processing is not applicable.");
        }
        return;
    }

    running = false;
    notify(DownloadManagerService.MESSAGE_PAUSED);

    if (init != null && init.isAlive()) {
        // NOTE: if start() method is running will no have effect!
        init.interrupt();
        synchronized (LOCK) {
            resetState(false, true, ERROR_NOTHING);
        }
        return;
    }

    if (DEBUG && unknownLength) {
        Log.w(TAG, "pausing a download that can not be resumed (range requests not allowed by the server).");
    }

    init = null;
    pauseThreads();
}

boolean runMissions() {
    synchronized (this) {
        if (mMissionsPending.size() < 1) {
            return false;
        }
        if (!canDownloadInCurrentNetwork()) {
            return false;
        }

        if (mPrefQueueLimit) {
            for (DownloadMission mission : mMissionsPending) {
                if (!mission.isFinished() && mission.running) {
                    return true;
                }
            }
        }

        boolean flag = false;
        for (DownloadMission mission : mMissionsPending) {
            if (mission.running || !mission.enqueued || mission.isFinished()) {
                continue;
            }

            resumeMission(mission);
            if (mission.errCode != DownloadMission.ERROR_NOTHING) {
                continue;
            }

            if (mPrefQueueLimit) {
                return true;
            }
            flag = true;
        }

        return flag;
    }
}

private void notifyPostProcessing(int state) {
    String action;
    switch (state) {
        case 1:
            action = "Running";
            break;
        case 2:
            action = "Completed";
            break;
        default:
            action = "Failed";
    }

    Log.d(TAG, action + " postprocessing on " + storage.getName());

    if (state == 2) {
        psState = state;
        return;
    }

    synchronized (LOCK) {
        // don't return without fully write the current state
        psState = state;
        writeThisToFile();
    }
}

private static String decodeQRCode(BufferedImage bufferedImage) {
    LuminanceSource source;
    int width = bufferedImage.getWidth();
    int height = bufferedImage.getHeight();

    DataBuffer dataBuffer = bufferedImage.getRaster().getDataBuffer();
    if (dataBuffer instanceof DataBufferByte) {
        byte[] pixels = ((DataBufferByte) dataBuffer).getData();
        source = new PlanarYUVLuminanceSource(
                pixels, width, height, 0, 0, width, height, false);
    } else if (dataBuffer instanceof DataBufferInt) {
        int[] pixels = ((DataBufferInt) dataBuffer).getData();
        byte[] newPixels = new byte[pixels.length];
        for (int i = 0; i < pixels.length; i++) {
            newPixels[i] = (byte) (pixels[i] & 0xff);
        }
        source = new PlanarYUVLuminanceSource(
                newPixels, width, height, 0, 0, width, height, false);
    } else {
        throw new IllegalArgumentException("BufferedImage must have 8-bit gray scale, 24-bit RGB, 32-bit ARGB (packed int), byte gray, or 3-byte/4-byte RGB image data");
    }

    BinaryBitmap bitmap = new BinaryBitmap(new HybridBinarizer(source));
    try {
        Result result = new MultiFormatReader().decode(bitmap);
        return result.getText();
    } catch (NotFoundException e) {
        return null; // no QR code in the image
    }
}

private void executeJob(QueuedJob job) {
    if (job.cancelled) {
        log.debug("Job {} was cancelled, not executing", job.jobId);
        return;
    }

    Runnable task = new Runnable() {
        @Override
        public void run() {
            log.debug("Executing queued job {} (queued at {})", job.jobId, job.queuedAt);
            try {
                // execute with timeout
                Object result = executeWithTimeout(job.work, job.timeoutMs);

                // process result
                if (result instanceof ResponseEntity) {
                    job.future.complete((ResponseEntity<?>) result);
                } else {
                    job.future.complete(ResponseEntity.ok(result));
                }
            } catch (Exception e) {
                log.error("Error executing queued job {}: {}", job.jobId, e.getMessage(), e);
                job.future.completeExceptionally(e);
            }
        }
    };

    jobExecutor.execute(task);
}

@DeleteMapping("/api/v1/general/job/{jobId}")
public ResponseEntity<?> cancelJob(@PathVariable("jobId") String jobId) {
    log.debug("Request to cancel job: {}", jobId);

    // verify that this job belongs to the current user
    Object sessionJobIds = request.getSession().getAttribute("userJobIds");
    if (!(sessionJobIds instanceof java.util.Set)) {
        log.warn("Unauthorized attempt to cancel job: {}", jobId);
        return ResponseEntity.status(403).body(Map.of("message", "You are not authorized to cancel this job"));
    }
    java.util.Set<?> userJobIds = (java.util.Set<?>) sessionJobIds;
    if (!userJobIds.contains(jobId)) {
        log.warn("Unauthorized attempt to cancel job: {}", jobId);
        return ResponseEntity.status(403).body(Map.of("message", "You are not authorized to cancel this job"));
    }

    // first check if the job is in the queue
    boolean cancelled = false;
    int queuePosition = -1;

    if (jobQueue.isJobQueued(jobId)) {
        queuePosition = jobQueue.getJobPosition(jobId);
        cancelled = jobQueue.cancelJob(jobId);
        log.info("Cancelled queued job: {} (was at position {})", jobId, queuePosition);
    }

    // if not in queue or couldn't cancel, try to cancel in TaskManager
    if (!cancelled) {
        JobResult result = taskManager.getJobResult(jobId);
        if (result != null && !result.isComplete()) {
            // mark as error with cancellation message
            taskManager.setError(jobId, "Job was cancelled by user");
            cancelled = true;
            log.info("Marked job as cancelled in TaskManager: {}", jobId);
        }
    }

    if (cancelled) {
        return ResponseEntity.ok(
                Map.of(
                        "message", "Job cancelled successfully",
                        "wasQueued", queuePosition >= 0,
                        "queuePosition", queuePosition >= 0 ? queuePosition : "n/a"));
    } else {
        // job not found or already complete
        JobResult result = taskManager.getJobResult(jobId);
        if (result == null) {
            return ResponseEntity.notFound().build();
        } else if (result.isComplete()) {
            return ResponseEntity.badRequest().body(Map.of("message", "Cannot cancel job that is already complete"));
        } else {
            return ResponseEntity.internalServerError().body(Map.of("message", "Failed to cancel job for unknown reason"));
        }
    }
}

public ResponseEntity<String> printFile(@ModelAttribute PrintFileRequest request) throws IOException {
    MultipartFile file = request.getFileInput();
    String originalFilename = file.getOriginalFilename();
    if (originalFilename != null && (originalFilename.contains("..") || Paths.get(originalFilename).isAbsolute())) {
        throw new IOException("Invalid file path detected: " + originalFilename);
    }
    String printerName = request.getPrinterName();
    String contentType = file.getContentType();
    try {
        // find matching printer
        PrintService[] services = PrintServiceLookup.lookupPrintServices(null, null);
        PrintService selectedService = Arrays.stream(services)
                .filter(service -> service.getName().toLowerCase().contains(printerName))
                .findFirst()
                .orElseThrow(() -> new IllegalArgumentException("No matching printer found"));

        log.info("Selected Printer: " + selectedService.getName());

        if ("application/pdf".equals(contentType)) {
            PDDocument document = Loader.loadPDF(file.getBytes());
            PrinterJob job = PrinterJob.getPrinterJob();
            job.setPrintService(selectedService);
            job.setPageable(new PDFPageable(document));
            job.print();
            document.close();
        } else if (contentType != null && contentType.startsWith("image/")) {
            BufferedImage image = ImageIO.read(file.getInputStream());
            PrinterJob job = PrinterJob.getPrinterJob();
            job.setPrintService(selectedService);
            job.setPrintable(new Printable() {
                @Override
                public int print(Graphics graphics, PageFormat pageFormat, int pageIndex) throws PrinterException {
                    if (pageIndex != 0) {
                        return NO_SUCH_PAGE;
                    }
                    Graphics2D g2d = (Graphics2D) graphics;
                    g2d.translate(pageFormat.getImageableX(), pageFormat.getImageableY());
                    g2d.drawImage(
                            image,
                            0,
                            0,
                            (int) pageFormat.getImageableWidth(),
                            (int) pageFormat.getImageableHeight(),
                            null);
                    return PAGE_EXISTS;
                }
            });
            job.print();
        }
        return new ResponseEntity<>("File printed successfully to " + selectedService.getName(), HttpStatus.OK);
    } catch (Exception e) {
        System.err.println("Failed to print: " + e.getMessage());
        return new ResponseEntity<>(e.getMessage(), HttpStatus.BAD_REQUEST);
    }
}

@PostMapping(value = "/auto-split-pdf", consumes = "multipart/form-data")
@Operation(
        summary = "Auto split PDF pages into separate documents",
        description = "This endpoint accepts a PDF file, scans each page for a specific QR code, and splits the document at the QR code boundaries. The output is a zip file containing each separate PDF document. Input:PDF Output:ZIP-PDF Type:SISO")
public ResponseEntity<byte[]> autoSplitPdf(@ModelAttribute AutoSplitPdfRequest request) throws IOException {
    MultipartFile file = request.getFileInput();
    boolean duplexMode = Boolean.TRUE.equals(request.getDuplexMode());

    PDDocument document = null;
    List<PDDocument> splitDocuments = new ArrayList<PDDocument>();
    Path zipFile = null;
    byte[] data = null;

    try {
        document = pdfDocumentFactory.load(file.getInputStream());
        PDFRenderer pdfRenderer = new PDFRenderer(document);
        pdfRenderer.setSubsamplingAllowed(true);

        for (int page = 0; page < document.getNumberOfPages(); ++page) {
            BufferedImage bim = pdfRenderer.renderImageWithDPI(page, 150);
            String result = decodeQRCode(bim);

            boolean isValidQrCode = VALID_QR_CONTENTS.contains(result);
            log.debug("detected qr code {}, code is vale={}", result, isValidQrCode);
            if (isValidQrCode && page != 0) {
                splitDocuments.add(new PDDocument());
            }

            if (!splitDocuments.isEmpty() && !isValidQrCode) {
                splitDocuments.get(splitDocuments.size() - 1).addPage(document.getPage(page));
            } else if (page == 0) {
                PDDocument firstDocument = new PDDocument();
                firstDocument.addPage(document.getPage(page));
                splitDocuments.add(firstDocument);
            }

            // if duplex mode is true and current page is a divider, then skip next page
            if (duplexMode && isValidQrCode) {
                page++;
            }
        }

        // remove split documents that have no pages
        splitDocuments.removeIf(pdDocument -> pdDocument.getNumberOfPages() == 0);

        zipFile = Files.createTempFile("split_documents", ".zip");
        String filename = Filenames.toSimpleFileName(file.getOriginalFilename()).replaceFirst("[.][^.]+$", "");

        try (ZipOutputStream zipOut = new ZipOutputStream(Files.newOutputStream(zipFile))) {
            for (int i = 0; i < splitDocuments.size(); i++) {
                String fileName = filename + "_" + (i + 1) + ".pdf";
                PDDocument splitDocument = splitDocuments.get(i);

                ByteArrayOutputStream baos = new ByteArrayOutputStream();
                splitDocument.save(baos);
                byte[] pdf = baos.toByteArray();

                ZipEntry pdfEntry = new ZipEntry(fileName);
                zipOut.putNextEntry(pdfEntry);
                zipOut.write(pdf);
                zipOut.closeEntry();
            }
        }

        data = Files.readAllBytes(zipFile);
        return WebResponseUtils.bytesToWebResponse(data, filename + ".zip", MediaType.APPLICATION_OCTET_STREAM);
    } catch (Exception e) {
        log.error("Error in auto split", e);
        throw e;
    } finally {
        // clean up resources
        if (document != null) {
            try {
                document.close();
            } catch (IOException e) {
                log.error("Error closing main PDDocument", e);
            }
        }

        for (PDDocument splitDoc : splitDocuments) {
            try {
                splitDoc.close();
            } catch (IOException e) {
                log.error("Error closing split PDDocument", e);
            }
        }

        if (zipFile != null) {
            try {
                Files.deleteIfExists(zipFile);
            } catch (IOException e) {
                log.error("Error deleting temporary zip file", e);
            }
        }
    }
}

@PostMapping(consumes = "multipart/form-data", value = "/auto-rename")
@Operation(
        summary = "Extract header from PDF file",
        description = "This endpoint accepts a PDF file and attempts to extract its title or header based on heuristics. Input:PDF Output:PDF Type:SISO")
public ResponseEntity<byte[]> extractHeader(@ModelAttribute ExtractHeaderRequest request) throws Exception {
    MultipartFile file = request.getFileInput();
    boolean useFirstTextAsFallback = Boolean.TRUE.equals(request.getUseFirstTextAsFallback());

    PDDocument document = pdfDocumentFactory.load(file);
    PDFTextStripper reader = new PDFTextStripper() {
        List<LineInfo> lineInfos = new ArrayList<LineInfo>();
        StringBuilder lineBuilder = new StringBuilder();
        float lastY = -1;
        float maxFontSizeInLine = 0.0f;
        int lineCount = 0;

        @Override
        protected void processTextPosition(TextPosition text) {
            if (lastY != text.getY() && lineCount < LINE_LIMIT) {
                processLine();
                lineBuilder = new StringBuilder(text.getUnicode());
                maxFontSizeInLine = text.getFontSizeInPt();
                lastY = text.getY();
                lineCount++;
            } else if (lineCount < LINE_LIMIT) {
                lineBuilder.append(text.getUnicode());
                if (text.getFontSizeInPt() > maxFontSizeInLine) {
                    maxFontSizeInLine = text.getFontSizeInPt();
                }
            }
        }

        private void processLine() {
            if (lineBuilder.length() > 0 && lineCount < LINE_LIMIT) {
                lineInfos.add(new LineInfo(lineBuilder.toString(), maxFontSizeInLine));
            }
        }

        @Override
        public String getText(PDDocument doc) throws IOException {
            this.lineInfos.clear();
            this.lineBuilder = new StringBuilder();
            this.lastY = -1;
            this.maxFontSizeInLine = 0.0f;
            this.lineCount = 0;
            super.getText(doc);
            processLine(); // process the last line

            // merge lines with same font size
            List<LineInfo> mergedLineInfos = new ArrayList<LineInfo>();
            for (int i = 0; i < lineInfos.size(); i++) {
                String mergedText = lineInfos.get(i).text;
                float fontSize = lineInfos.get(i).fontSize;
                while (i + 1 < lineInfos.size() && lineInfos.get(i + 1).fontSize == fontSize) {
                    mergedText += " " + lineInfos.get(i + 1).text;
                    i++;
                }
                mergedLineInfos.add(new LineInfo(mergedText, fontSize));
            }

            // sort lines by font size in descending order and get the first one
            Collections.sort(mergedLineInfos, new Comparator<LineInfo>() {
                @Override
                public int compare(LineInfo o1, LineInfo o2) {
                    return Float.compare(o2.fontSize, o1.fontSize);
                }
            });
            String title = mergedLineInfos.isEmpty() ? null : mergedLineInfos.get(0).text;

            return title != null
                    ? title
                    : (useFirstTextAsFallback
                    ? (mergedLineInfos.isEmpty() ? null : mergedLineInfos.get(mergedLineInfos.size() - 1).text)
                    : null);
        }

        class LineInfo {
            String text;
            float fontSize;

            LineInfo(String text, float fontSize) {
                this.text = text;
                this.fontSize = fontSize;
            }
        }
    };

    String header = reader.getText(document);

    // sanitize the header string by removing characters not allowed in a filename
    if (header != null && header.length() < 255) {
        header = header.replaceAll("[/\\\\?%*:|\"<>]", "").trim();
        return WebResponseUtils.pdfDocToWebResponse(document, header + ".pdf");
    } else {
        log.info("File has no good title to be found");
        return WebResponseUtils.pdfDocToWebResponse(document, Filenames.toSimpleFileName(file.getOriginalFilename()));
    }
}

@Operation(
        summary = "Validate PDF Digital Signature",
        description = "Validates the digital signatures in a PDF file against default or custom certificates. Input:PDF Output:JSON Type:SISO")
@PostMapping(value = "/validate-signature", consumes = MediaType.MULTIPART_FORM_DATA_VALUE)
public ResponseEntity<List<SignatureValidationResult>> validateSignature(@ModelAttribute SignatureValidationRequest request) throws IOException {
    List<SignatureValidationResult> results = new ArrayList<SignatureValidationResult>();
    MultipartFile file = request.getFileInput();
    MultipartFile certFile = request.getCertFile();

    // load custom certificate if provided
    X509Certificate customCert = null;
    if (certFile != null && !certFile.isEmpty()) {
        try (ByteArrayInputStream certStream = new ByteArrayInputStream(certFile.getBytes())) {
            CertificateFactory cf = CertificateFactory.getInstance("X.509");
            customCert = (X509Certificate) cf.generateCertificate(certStream);
        } catch (CertificateException e) {
            throw ExceptionUtils.createRuntimeException(
                    "error.invalidFormat",
                    "Invalid {0} format: {1}",
                    e,
                    "certificate file",
                    e.getMessage());
        }
    }

    try (PDDocument document = pdfDocumentFactory.load(file.getInputStream())) {
        List<PDSignature> signatures = document.getSignatureDictionaries();

        for (PDSignature sig : signatures) {
            SignatureValidationResult result = new SignatureValidationResult();

            try {
                byte[] signedContent = sig.getSignedContent(file.getInputStream());
                byte[] signatureBytes = sig.getContents(file.getInputStream());

                CMSProcessable content = new CMSProcessableByteArray(signedContent);
                CMSSignedData signedData = new CMSSignedData(content, signatureBytes);

                Store<X509CertificateHolder> certStore = signedData.getCertificates();
                SignerInformationStore signerStore = signedData.getSignerInfos();

                for (SignerInformation signer : signerStore.getSigners()) {
                    X509CertificateHolder certHolder = (X509CertificateHolder) certStore.getMatches(signer.getSID()).iterator().next();
                    X509Certificate cert = new JcaX509CertificateConverter().getCertificate(certHolder);

                    boolean isValid = signer.verify(new JcaSimpleSignerInfoVerifierBuilder().build(cert));
                    result.setValid(isValid);

                    // additional validations
                    result.setChainValid(
                            customCert != null
                                    ? certValidationService.validateCertificateChainWithCustomCert(cert, customCert)
                                    : certValidationService.validateCertificateChain(cert));

                    result.setTrustValid(
                            customCert != null
                                    ? certValidationService.validateTrustWithCustomCert(cert, customCert)
                                    : certValidationService.validateTrustStore(cert));

                    result.setNotRevoked(!certValidationService.isRevoked(cert));
                    result.setNotExpired(!cert.getNotAfter().before(new Date()));

                    // basic signature info
                    result.setSignerName(sig.getName());
                    result.setSignatureDate(sig.getSignDate().getTime().toString());
                    result.setReason(sig.getReason());
                    result.setLocation(sig.getLocation());

                    // certificate details
                    result.setIssuerDN(cert.getIssuerX500Principal().getName());
                    result.setSubjectDN(cert.getSubjectX500Principal().getName());
                    result.setSerialNumber(cert.getSerialNumber().toString(16));
                    result.setValidFrom(cert.getNotBefore().toString());
                    result.setValidUntil(cert.getNotAfter().toString());
                    result.setSignatureAlgorithm(cert.getSigAlgName());

                    // key size (if possible)
                    try {
                        result.setKeySize(((RSAPublicKey) cert.getPublicKey()).getModulus().bitLength());
                    } catch (Exception e) {
                        result.setKeySize(0);
                    }

                    result.setVersion(String.valueOf(cert.getVersion()));

                    // key usage
                    List<String> keyUsages = new ArrayList<String>();
                    boolean[] keyUsageFlags = cert.getKeyUsage();
                    if (keyUsageFlags != null) {
                        String[] keyUsageLabels = {
                                "Digital Signature", "Non-Repudiation", "Key Encipherment",
                                "Data Encipherment", "Key Agreement", "Certificate Signing",
                                "CRL Signing", "Encipher Only", "Decipher Only"
                        };
                        for (int i = 0; i < keyUsageFlags.length; i++) {
                            if (keyUsageFlags[i]) {
                                keyUsages.add(keyUsageLabels[i]);
                            }
                        }
                    }
                    result.setKeyUsages(keyUsages);

                    // self-signed check
                    result.setSelfSigned(cert.getSubjectX500Principal().equals(cert.getIssuerX500Principal()));
                }
            } catch (Exception e) {
                result.setValid(false);
                result.setErrorMessage("Signature validation failed: " + e.getMessage());
            }

            results.add(result);
        }
    }

    return ResponseEntity.ok(results);
}

public Map<String, Object> captureApplicationProperties() {
    Map<String, Object> properties = new HashMap<String, Object>();

    // legal properties
    addIfNotEmpty(properties, "legal_termsAndConditions", applicationProperties.getLegal().getTermsAndConditions());
    addIfNotEmpty(properties, "legal_privacyPolicy", applicationProperties.getLegal().getPrivacyPolicy());
    addIfNotEmpty(properties, "legal_accessibilityStatement", applicationProperties.getLegal().getAccessibilityStatement());
    addIfNotEmpty(properties, "legal_cookiePolicy", applicationProperties.getLegal().getCookiePolicy());
    addIfNotEmpty(properties, "legal_impressum", applicationProperties.getLegal().getImpressum());

    // security properties
    addIfNotEmpty(properties, "security_enableLogin", applicationProperties.getSecurity().getEnableLogin());
    addIfNotEmpty(properties, "security_csrfDisabled", applicationProperties.getSecurity().getCsrfDisabled());
    addIfNotEmpty(properties, "security_loginAttemptCount", applicationProperties.getSecurity().getLoginAttemptCount());
    addIfNotEmpty(properties, "security_loginResetTimeMinutes", applicationProperties.getSecurity().getLoginResetTimeMinutes());
    addIfNotEmpty(properties, "security_loginMethod", applicationProperties.getSecurity().getLoginMethod());

    // oauth2 properties (excluding sensitive information)
    addIfNotEmpty(properties, "security_oauth2_enabled", applicationProperties.getSecurity().getOauth2().getEnabled());
    if (applicationProperties.getSecurity().getOauth2().getEnabled()) {
        addIfNotEmpty(properties, "security_oauth2_autoCreateUser", applicationProperties.getSecurity().getOauth2().getAutoCreateUser());
        addIfNotEmpty(properties, "security_oauth2_blockRegistration", applicationProperties.getSecurity().getOauth2().getBlockRegistration());
        addIfNotEmpty(properties, "security_oauth2_useAsUsername", applicationProperties.getSecurity().getOauth2().getUseAsUsername());
        addIfNotEmpty(properties, "security_oauth2_provider", applicationProperties.getSecurity().getOauth2().getProvider());
    }

    // system properties
    addIfNotEmpty(properties, "system_defaultLocale", applicationProperties.getSystem().getDefaultLocale());
    addIfNotEmpty(properties, "system_googlevisibility", applicationProperties.getSystem().getGooglevisibility());
    addIfNotEmpty(properties, "system_showUpdate", applicationProperties.getSystem().isShowUpdate());
    addIfNotEmpty(properties, "system_showUpdateOnlyAdmin", applicationProperties.getSystem().getShowUpdateOnlyAdmin());
    addIfNotEmpty(properties, "system_customHTMLFiles", applicationProperties.getSystem().isCustomHTMLFiles());
    addIfNotEmpty(properties, "system_tessdataDir", applicationProperties.getSystem().getTessdataDir());
    addIfNotEmpty(properties, "system_enableAlphaFunctionality", applicationProperties.getSystem().getEnableAlphaFunctionality());
    addIfNotEmpty(properties, "system_enableAnalytics", applicationProperties.getSystem().isAnalyticsEnabled());

    // ui properties
    addIfNotEmpty(properties, "ui_appName", applicationProperties.getUi().getAppName());
    addIfNotEmpty(properties, "ui_homeDescription", applicationProperties.getUi().getHomeDescription());
    addIfNotEmpty(properties, "ui_appNameNavbar", applicationProperties.getUi().getAppNameNavbar());

    // metrics properties
    addIfNotEmpty(properties, "metrics_enabled", applicationProperties.getMetrics().getEnabled());

    // enterprise edition properties
    addIfNotEmpty(properties, "enterpriseEdition_enabled", applicationProperties.getPremium().isEnabled());
    if (applicationProperties.getPremium().isEnabled()) {
        addIfNotEmpty(properties, "enterpriseEdition_customMetadata_autoUpdateMetadata",
                applicationProperties.getPremium().getProFeatures().getCustomMetadata().isAutoUpdateMetadata());
        addIfNotEmpty(properties, "enterpriseEdition_customMetadata_author",
                applicationProperties.getPremium().getProFeatures().getCustomMetadata().getAuthor());
        addIfNotEmpty(properties, "enterpriseEdition_customMetadata_creator",
                applicationProperties.getPremium().getProFeatures().getCustomMetadata().getCreator());
        addIfNotEmpty(properties, "enterpriseEdition_customMetadata_producer",
                applicationProperties.getPremium().getProFeatures().getCustomMetadata().getProducer());
    }

    // auto pipeline properties
    addIfNotEmpty(properties, "autoPipeline_outputFolder", applicationProperties.getAutoPipeline().getOutputFolder());

    return properties;
}

public CompletableFuture<ResponseEntity<?>> queueJob(String jobId, int resourceWeight, Supplier<Object> work, long timeoutMs) {
    // create a CompletableFuture to track this job's completion
    CompletableFuture<ResponseEntity<?>> future = new CompletableFuture<ResponseEntity<?>>();

    // create the queued job
    QueuedJob job = new QueuedJob(jobId, resourceWeight, work, timeoutMs, Instant.now(), future, false);

    // store in our map for lookup
    jobMap.put(jobId, job);

    // update stats
    totalQueuedJobs++;

    // synchronize access to the queue
    synchronized (queueLock) {
        currentQueueSize = jobQueue.size();

        // try to add to the queue
        try {
            boolean added = jobQueue.offer(job, 5, TimeUnit.SECONDS);
            if (!added) {
                log.warn("Queue full, rejecting job {}", jobId);
                rejectedJobs++;
                future.completeExceptionally(new RuntimeException("Job queue full, please try again later"));
                jobMap.remove(jobId);
                return future;
            }

            log.debug("Job {} queued for execution (weight: {}, queue size: {})", jobId, resourceWeight, jobQueue.size());
            return future;
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            future.completeExceptionally(new RuntimeException("Job queue interrupted"));
            jobMap.remove(jobId);
            return future;
        }
    }
}

private void applyQpdfCompression(OptimizePdfRequest request, int optimizeLevel, Path currentFile, List<Path> tempFiles) throws IOException {
    long preQpdfSize = Files.size(currentFile);
    log.info("Pre-QPDF file size: {}", GeneralUtils.formatBytes(preQpdfSize));

    // map optimization levels to QPDF compression levels
    int qpdfCompressionLevel;
    switch (optimizeLevel) {
        case 1:
            qpdfCompressionLevel = 5;
            break;
        case 2:
            qpdfCompressionLevel = 9;
            break;
        default:
            qpdfCompressionLevel = 9;
            break;
    }

    // create output file for QPDF
    Path qpdfOutputFile = Files.createTempFile("qpdf_output_", ".pdf");
    tempFiles.add(qpdfOutputFile);

    // build QPDF command
    List<String> command = new ArrayList<String>();
    command.add("qpdf");
    if (request.getNormalize()) {
        command.add("--normalize-content=y");
    }
    if (request.getLinearize()) {
        command.add("--linearize");
    }
    command.add("--recompress-flate");
    command.add("--compression-level=" + qpdfCompressionLevel);
    command.add("--compress-streams=y");
    command.add("--object-streams=generate");
    command.add(currentFile.toString());
    command.add(qpdfOutputFile.toString());

    ProcessExecutorResult returnCode = null;
    try {
        returnCode = ProcessExecutor.getInstance(ProcessExecutor.Processes.QPDF).runCommandWithOutputHandling(command);

        // update current file to the QPDF output
        Files.copy(qpdfOutputFile, currentFile, StandardCopyOption.REPLACE_EXISTING);

        long postQpdfSize = Files.size(currentFile);
        double qpdfReduction = 100.0 - ((postQpdfSize * 100.0) / preQpdfSize);
        log.info("Post-QPDF file size: {} (reduced by {}%)", GeneralUtils.formatBytes(postQpdfSize), String.format("%.1f", qpdfReduction));
    } catch (Exception e) {
        if (returnCode != null && returnCode.getRc() != 3) {
            throw new IOException("QPDF command failed", e);
        }
        // if QPDF fails, keep using the current file
        log.warn("QPDF compression failed, continuing with current file", e);
    }
}

private void handleCookiesFromUrl(@Nullable String url) {
    if (MainActivity.DEBUG) {
        Log.d(TAG, "handleCookiesFromUrl: url=" + (url == null ? "null" : url));
    }
    if (url == null) return;

    String cookies = CookieManager.getInstance().getCookie(url);
    handleCookies(cookies);

    // Sometimes cookies are inside the URL
    int abuseStart = url.indexOf("google_abuse=");
    if (abuseStart != -1) {
        int abuseEnd = url.indexOf("+path");
        try {
            String decoded = Utils.decodeUrlUtf8(url.substring(abuseStart + 13, abuseEnd));
            handleCookies(decoded);
        } catch (StringIndexOutOfBoundsException e) {
            if (MainActivity.DEBUG) {
                Log.e(TAG, "handleCookiesFromUrl: invalid google abuse starting at "
                        + abuseStart + " and ending at " + abuseEnd + " for url " + url, e);
            }
        }
    }
}

@Override
public void onUpdateProgress(int currentProgress, int duration, int bufferPercent) {
    if (duration != binding.playbackSeekBar.getMax()) {
        setVideoDurationToControls(duration);
    }
    if (player.getCurrentState() != STATE_PAUSED) {
        updatePlayBackElementsCurrentDuration(currentProgress);
    }
    if (player.isLoading() || bufferPercent > 90) {
        binding.playbackSeekBar.setSecondaryProgress(
                (int) (binding.playbackSeekBar.getMax() * ((float) bufferPercent / 100)));
    }
    if (DEBUG && bufferPercent % 20 == 0) { // Limit log
        Log.d(TAG, "notifyProgressUpdateToListeners() called with: "
                + "isVisible = " + isControlsVisible() + ", "
                + "currentProgress = [" + currentProgress + "], "
                + "duration = [" + duration + "], bufferPercent = [" + bufferPercent + "]");
    }
    binding.playbackLiveSync.setClickable(!player.isLiveEdge());
}

private void updateQueueTime(int currentTime) {
    PlayQueue playQueue = player.getPlayQueue();
    if (playQueue == null) return;

    int currentStream = playQueue.getIndex();
    int before = 0;
    int after = 0;

    List<PlayQueueItem> streams = playQueue.getStreams();
    int nStreams = streams.size();

    for (int i = 0; i < nStreams; i++) {
        if (i < currentStream) {
            before += streams.get(i).getDuration();
        } else {
            after += streams.get(i).getDuration();
        }
    }

    before *= 1000;
    after *= 1000;

    binding.itemsListHeaderDuration.setText(
            String.format(
                    "%s/%s",
                    getTimeString(currentTime + before),
                    getTimeString(before + after)
            ));
}

@StyleRes
public static int getSettingsThemeStyle(Context context) {
    Resources res = context.getResources();
    String lightTheme = res.getString(R.string.light_theme_key);
    String blackTheme = res.getString(R.string.black_theme_key);
    String automaticDeviceTheme = res.getString(R.string.auto_device_theme_key);

    String selectedTheme = getSelectedThemeKey(context);

    if (selectedTheme.equals(lightTheme)) {
        return R.style.LightSettingsTheme;
    }
    if (selectedTheme.equals(blackTheme)) {
        return R.style.BlackSettingsTheme;
    }
    if (selectedTheme.equals(automaticDeviceTheme)) {
        if (isDeviceDarkThemeEnabled(context)) {
            // Use the dark theme variant preferred by the user
            String selectedNightTheme = getSelectedNightThemeKey(context);
            return selectedNightTheme.equals(blackTheme)
                    ? R.style.BlackSettingsTheme
                    : R.style.DarkSettingsTheme;
        }
        // There is only one day theme
        return R.style.LightSettingsTheme;
    }
    // Default to dark theme
    return R.style.DarkSettingsTheme;
}

public boolean onKeyDown(int keyCode) {
    switch (keyCode) {
        case KeyEvent.KEYCODE_BACK:
            if (DeviceUtils.isTv(context) && isControlsVisible()) {
                hideControls(0, 0);
                return true;
            }
            break;
        case KeyEvent.KEYCODE_DPAD_UP:
        case KeyEvent.KEYCODE_DPAD_LEFT:
        case KeyEvent.KEYCODE_DPAD_DOWN:
        case KeyEvent.KEYCODE_DPAD_RIGHT:
        case KeyEvent.KEYCODE_DPAD_CENTER:
            if ((binding.getRoot().hasFocus() && !binding.playbackControlRoot.hasFocus())
                    || isAnyListViewOpen()) {
                // Do not interfere with focus in playlist and play queue etc.
                break;
            }

            if (player.getCurrentState() == org.schabi.newpipe.player.Player.STATE_BLOCKED) {
                return true;
            }

            if (isControlsVisible()) {
                hideControls(DEFAULT_CONTROLS_DURATION, DPAD_CONTROLS_HIDE_TIME);
            } else {
                binding.playPauseButton.requestFocus();
                showControlsThenHide();
                showSystemUIPartially();
                return true;
            }
            break;
        default:
            break; // Ignore other keys
    }

    return false;
}

