    float endScreenHeight = calculateMaxEndScreenThumbnailHeight(thumbnail);
    Bitmap endScreenBitmap =
            BitmapCompat.createScaledBitmap(
                    thumbnail,
                    (int) (thumbnail.getWidth() / (thumbnail.getHeight() / endScreenHeight)),
