@Override
public void subscribe(Observer<? super T> observer) {
    for (; ; ) {
        final int s = once.get();
        if ((s & HAS_SUBSCRIBER) != 0) {
            break;
        }
        final int u = s | HAS_SUBSCRIBER;
        if (once.compareAndSet(s, u)) {
            observer.onSubscribe(this);
            actual.lazySet(observer);
            if (cancelled.get()) {
                actual.lazySet(null);
            } else {
                drain();
            }
            return;
        }
    }
    EmptyDisposable.error(new IllegalStateException("Only one Observer allowed!"), observer);
}

@Override
public void onSuccess(T value) {
    final Observer<? super R> a = downstream;
    Iterator<? extends R> iterator;
    boolean has;
    try {
        iterator = mapper.apply(value).iterator();
        has = iterator.hasNext();
    } catch (Throwable ex) {
        Exceptions.throwIfFatal(ex);
        downstream.onError(ex);
        return;
    }

    if (!has) {
        a.onComplete();
        return;
    }

    if (outputFused) {
        it = iterator;
        a.onNext(null);
        a.onComplete();
        return;
    }

    for (; ; ) {
        if (cancelled) {
            return;
        }

        R v;
        try {
            v = iterator.next();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            a.onError(ex);
            return;
        }

        a.onNext(v);

        if (cancelled) {
            return;
        }

        boolean b;
        try {
            b = iterator.hasNext();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            a.onError(ex);
            return;
        }

        if (!b) {
            a.onComplete();
            return;
        }
    }
}

@Override
void fastPath() {
    final Iterator<? extends T> it = this.iterator;
    final ConditionalSubscriber<? super T> a = downstream;

    for (; ; ) {
        if (cancelled) {
            return;
        }

        T t;
        try {
            t = it.next();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            a.onError(ex);
            return;
        }

        if (cancelled) {
            return;
        }

        if (t == null) {
            a.onError(new NullPointerException("Iterator.next() returned a null value"));
            return;
        }

        a.tryOnNext(t);

        if (cancelled) {
            return;
        }

        boolean b;
        try {
            b = it.hasNext();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            a.onError(ex);
            return;
        }

        if (!b) {
            if (!cancelled) {
                a.onComplete();
            }
            return;
        }
    }
}

static <T> boolean postCompleteDrain(
        long n,
        Subscriber<? super T> actual,
        Queue<T> queue,
        AtomicLong state,
        BooleanSupplier isCancelled) {

// TODO enable fast-path
//        if (n == -1 || n == Long.MAX_VALUE) {
//            for (; ; ) {
//                if (isCancelled.getAsBoolean()) {
//                    break;
//                }
//
//                T v = queue.poll();
//
//                if (v == null) {
//                    actual.onComplete();
//                    break;
//                }
//
//                actual.onNext(v);
//            }
//
//            return true;
//        }

    long e = n & COMPLETED_MASK;

    for (; ; ) {

        while (e != n) {
            if (isCancelled(isCancelled)) {
                return true;
            }

            T t = queue.poll();

            if (t == null) {
                actual.onComplete();
                return true;
            }

            actual.onNext(t);
            e++;
        }

        if (isCancelled(isCancelled)) {
            return true;
        }

        if (queue.isEmpty()) {
            actual.onComplete();
            return true;
        }

        n = state.get();

        if (n == e) {
            n = state.addAndGet(-(e & REQUESTED_MASK));
            if ((n & REQUESTED_MASK) == 0L) {
                return false;
            }
            e = n & COMPLETED_MASK;
        }
    }
}

