void drainLoop() {
    final Subscriber<? super T> actual = this.downstream;
    int missed = 1;
    long e = emitted;
    int c = consumed;
    final int lim = limit;

    for (; ; ) {
        final long r = requested.get();

        while (e != r) {
            if (cancelled) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(downstream);
                return;
            }

            int os = otherState;
            if (os == OTHER_STATE_HAS_VALUE) {
                T v = singleItem;
                singleItem = null;
                otherState = OTHER_STATE_CONSUMED_OR_EMPTY;
                os = OTHER_STATE_CONSUMED_OR_EMPTY;
                actual.onNext(v);

                e++;
                continue;
            }

            final boolean d = mainDone;
            final SimplePlainQueue<T> q = queue;
            final T v = q != null ? q.poll() : null;
            final boolean empty = v == null;

            if (d && empty && os == OTHER_STATE_CONSUMED_OR_EMPTY) {
                queue = null;
                actual.onComplete();
                return;
            }

            if (empty) {
                break;
            }

            actual.onNext(v);

            e++;

            if (++c == lim) {
                c = 0;
                mainSubscription.get().request(lim);
            }
        }

        if (e == r) {
            if (cancelled) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(downstream);
                return;
            }

            final boolean d = mainDone;
            final SimplePlainQueue<T> q = queue;
            final boolean empty = q == null || q.isEmpty();

            if (d && empty && otherState == 2) {
                queue = null;
                actual.onComplete();
                return;
            }
        }

        emitted = e;
        consumed = c;
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
@NonNull
public final U assertValueAt(int index, @NonNull Predicate<T> valuePredicate) {
    final int s = values.size();
    if (s == 0) {
        throw fail("No values");
    }

    if (index < 0 || index >= s) {
        throw fail("Index " + index + " is out of range [0, " + s + ")");
    }

    boolean found = false;

    final T v = values.get(index);
    try {
        if (valuePredicate.test(v)) {
            found = true;
        }
    } catch (Throwable ex) {
        throw ExceptionHelper.wrapOrThrow(ex);
    }

    if (!found) {
        throw fail("Value " + valueAndClass(v) + " at position " + index + " did not pass the predicate");
    }
    return (U) this;
}

@Override
public void onSubscribe(@NonNull Subscription s) {
    if (SubscriptionHelper.validate(this.upstream, s)) {
        this.upstream = s;
        try {
            downstream.onSubscribe(this);
        } catch (Throwable e) {
            Exceptions.throwIfFatal(e);
            done = true;
            // can't call onError because the actual's state may be corrupt at this point
            try {
                s.cancel();
            } catch (Throwable e1) {
                Exceptions.throwIfFatal(e1);
                RxJavaPlugins.onError(new CompositeException(e, e1));
                return;
            }
            RxJavaPlugins.onError(e);
        }
    }
