private void finishing(Advice advice) {
    try {
        double cost = threadLocalWatch.costInMillis();
        boolean conditionResult = isConditionMet(command.getConditionExpress(), advice, cost);

        if (isVerbose()) {
            process.write("Condition express: " + command.getConditionExpress() + 
                         " , result: " + conditionResult + "\n");
        }

        if (conditionResult) {
            StackModel stackModel = ThreadUtil.getThreadStackModel(advice.getLoader(), Thread.currentThread());
            stackModel.setTs(LocalDateTime.now());
            process.appendResult(stackModel);
            process.times().incrementAndGet();

            if (isLimitExceeded(command.getNumberOfLimit(), process.times().get())) {
                abortProcess(process, command.getNumberOfLimit());
            }
        }
    } catch (Throwable e) {
        logger.warn("stack failed.", e);
        process.end(-1, "stack failed, condition is: " + command.getConditionExpress() + ", " + e.getMessage() +
                  ", visit " + LogUtil.loggingFile() + " for more details.");
    }
}

private Process createProcess(Session session, List<CliToken> line, InternalCommandManager commandManager, 
                             int jobId, Term term, ResultDistributor resultDistributor) {
    try {
        ListIterator<CliToken> tokens = line.listIterator();
        while (tokens.hasNext()) {
            CliToken token = tokens.next();
            if (token.isText()) {
                checkPermission(session, token);
                Command command = commandManager.getCommand(token.value());

                if (command != null) {
                    return createCommandProcess(command, tokens, jobId, term, resultDistributor);
                } else {
                    throw new IllegalArgumentException(token.value() + ": command not found");
                }
            }
        }
        throw new IllegalArgumentException();
    } catch (Exception e) {
        throw new RuntimeException(e);
    }
}

private void initCommands(List<String> disabledCommands) {
    List<Class<? extends AnnotatedCommand>> commandClassList = new ArrayList<>(50);
    commandClassList.add(HelpCommand.class);
    commandClassList.add(AuthCommand.class);
    commandClassList.add(KeymapCommand.class);
    // ... (other commands remain unchanged)

    try {
        if (ClassLoader.getSystemClassLoader().getResource("jdk/jfr/Recording.class") != null) {
            commandClassList.add(JFRCommand.class);
        }
    } catch (Throwable e) {
        logger.error("This jdk version not support jfr command");
    }

    for (Class<? extends AnnotatedCommand> clazz : commandClassList) {
        Name name = clazz.getAnnotation(Name.class);
        if (name != null && name.value() != null && disabledCommands.contains(name.value())) {
            continue;
        }
        commands.add(Command.create(clazz));
    }
}

public synchronized EnhancerAffect enhance(final Instrumentation inst, int maxNumOfMatchedClass) 
        throws UnmodifiableClassException {

    matchingClasses = GlobalOptions.isDisableSubClass
            ? SearchUtils.searchClass(inst, classNameMatcher)
            : SearchUtils.searchSubClass(inst, SearchUtils.searchClass(inst, classNameMatcher));

    if (matchingClasses.size() > maxNumOfMatchedClass) {
        affect.setOverLimitMsg("The number of matched classes is " + matchingClasses.size() + 
                              ", greater than the limit value " + maxNumOfMatchedClass + 
                              ". Try to change the limit with option '-m <arg>'.");
        return affect;
    }

    List<Pair<Class<?>, String>> filteredList = filter(matchingClasses);
    for (Pair<Class<?>, String> filtered : filteredList) {
        logger.info("ignore class: {}, reason: {}", filtered.getFirst().getName(), filtered.getSecond());
    }

    logger.info("enhance matched classes: {}", matchingClasses);
    affect.setTransformer(this);

    try {
        ArthasBootstrap.getInstance().getTransformerManager().addTransformer(this, isTracing);

        if (GlobalOptions.isBatchReTransform) {
            final Class<?>[] classArray = matchingClasses.toArray(new Class<?>[0]);
            if (classArray.length > 0) {
                inst.retransformClasses(classArray);
                logger.info("Success to batch transform classes: {}", Arrays.toString(classArray));
            }
        } else {
            for (Class<?> clazz : matchingClasses) {
                try {
                    inst.retransformClasses(clazz);
                    logger.info("Success to transform class: {}", clazz);
                } catch (Throwable t) {
                    logger.warn("retransform {} failed.", clazz, t);
                    if (t instanceof UnmodifiableClassException) {
                        throw (UnmodifiableClassException) t;
                    } else if (t instanceof RuntimeException) {
                        throw (RuntimeException) t;
                    } 
                    throw new RuntimeException(t);
                }
            }
        }
    } catch (Throwable e) {
        logger.error("Enhancer error, matchingClasses: {}", matchingClasses, e);
        affect.setThrowable(e);
    }

    return affect;
}

public static DefaultFullHttpResponse directView(File dir, String path, FullHttpRequest request, 
                                               ChannelHandlerContext ctx) throws IOException {

    if (path.startsWith("/")) {
        path = path.substring(1);
    }

    File file = new File(dir.getParent(), path);
    if (!isSubFile(dir, file)) {
        return null;
    }

    HttpVersion version = request.protocolVersion();
    DefaultFullHttpResponse fullResp = new DefaultFullHttpResponse(version, HttpResponseStatus.OK);

    if (file.isDirectory()) {
        if (!path.endsWith("/")) {
            fullResp.setStatus(HttpResponseStatus.FOUND)
                    .headers()
                    .set(HttpHeaderNames.LOCATION, "/" + path + "/");
        }
        String renderResult = renderDir(file, !isSameFile(dir, file));
        fullResp.content().writeBytes(renderResult.getBytes(StandardCharsets.UTF_8));
        fullResp.headers().set(HttpHeaderNames.CONTENT_TYPE, "text/html; charset=utf-8");
        ctx.write(fullResp);
        ctx.writeAndFlush(LastHttpContent.EMPTY_LAST_CONTENT).addListener(ChannelFutureListener.CLOSE);
        return fullResp;
    } 

    if (file.isHidden() || !file.exists() || file.isDirectory()) {
        return null;
    }

    long fileLength = file.length();
    if (fileLength < MIN_NETTY_DIRECT_SEND_SIZE) {
        try (FileInputStream fileInputStream = new FileInputStream(file)) {
            byte[] content = IOUtils.getBytes(fileInputStream);
            fullResp.content().writeBytes(content);
            HttpUtil.setContentLength(fullResp, fullResp.content().readableBytes());
        }

        ctx.writeAndFlush(fullResp).addListener(future -> {
            if (future.isSuccess()) {
                ctx.writeAndFlush(LastHttpContent.EMPTY_LAST_CONTENT).addListener(ChannelFutureListener.CLOSE);
            } else {
                future.channel().close();
            }
        });
        return fullResp;
    }

    logger.info("file {} size bigger than {}, send by future.", file.getName(), MIN_NETTY_DIRECT_SEND_SIZE);
    HttpResponse response = new DefaultHttpResponse(HTTP_1_1, OK);
    HttpUtil.setContentLength(response, fileLength);
    setContentTypeHeader(response, file);
    setDateAndCacheHeaders(response, file);

    if (HttpUtil.isKeepAlive(request)) {
        response.headers().set(HttpHeaderNames.CONNECTION, HttpHeaderValues.KEEP_ALIVE);
    }

    ctx.write(response);
    RandomAccessFile raf = new RandomAccessFile(file, "r");

    ChannelFuture sendFileFuture;
    ChannelFuture lastContentFuture;
    if (ctx.pipeline().get(SslHandler.class) == null) {
        sendFileFuture = ctx.write(new DefaultFileRegion(raf.getChannel(), 0, fileLength), ctx.newProgressivePromise());
        lastContentFuture = ctx.writeAndFlush(LastHttpContent.EMPTY_LAST_CONTENT);
    } else {
        sendFileFuture = ctx.writeAndFlush(new HttpChunkedInput(new ChunkedFile(raf, 0, fileLength, 8192)),
                                          ctx.newProgressivePromise());
        lastContentFuture = sendFileFuture;
    }

    sendFileFuture.addListener(new ChannelProgressiveFutureListener() {
        @Override
        public void operationProgressed(ChannelProgressiveFuture future, long progress, long total) {
            logger.info("{} Transfer progress: {}{}", future.channel(), progress, 
                       total < 0 ? "" : " / " + total);
        }

        @Override
        public void operationComplete(ChannelProgressiveFuture future) {
            logger.info("{} Transfer complete.", future.channel());
        }
    });

    if (!HttpUtil.isKeepAlive(request)) {
        lastContentFuture.addListener(ChannelFutureListener.CLOSE);
    }
    return fullResp;
}

@Override
public byte[] transform(ClassLoader inClassLoader, String className, Class<?> classBeingRedefined,
                       ProtectionDomain protectionDomain, byte[] classfileBuffer) {
    try {
        if (inClassLoader != null) {
            try {
                inClassLoader.loadClass(SpyAPI.class.getName());
            } catch (Throwable e) {
                logger.error("the classloader can not load SpyAPI, ignore it. classloader: {}, className: {}",
                            inClassLoader.getClass().getName(), className, e);
                return null;
            }
        }

        if (matchingClasses != null && !matchingClasses.contains(classBeingRedefined)) {
            return null;
        }

        ClassNode classNode = new ClassNode(Opcodes.ASM9);
        ClassReader classReader = AsmUtils.toClassNode(classfileBuffer, classNode);
        classNode = AsmUtils.removeJSRInstructions(classNode);

        DefaultInterceptorClassParser interceptorParser = new DefaultInterceptorClassParser();
        List<InterceptorProcessor> interceptorProcessors = new ArrayList<>();
        interceptorProcessors.addAll(interceptorParser.parse(SpyInterceptor1.class));
        interceptorProcessors.addAll(interceptorParser.parse(SpyInterceptor2.class));
        interceptorProcessors.addAll(interceptorParser.parse(SpyInterceptor3.class));

        if (isTracing) {
            if (!skipJDKTrace) {
                interceptorProcessors.addAll(interceptorParser.parse(SpyTraceInterceptor1.class));
                interceptorProcessors.addAll(interceptorParser.parse(SpyTraceInterceptor2.class));
                interceptorProcessors.addAll(interceptorParser.parse(SpyTraceInterceptor3.class));
            } else {
                interceptorProcessors.addAll(interceptorParser.parse(SpyTraceExcludeJDKInterceptor1.class));
                interceptorProcessors.addAll(interceptorParser.parse(SpyTraceExcludeJDKInterceptor2.class));
                interceptorProcessors.addAll(interceptorParser.parse(SpyTraceExcludeJDKInterceptor3.class));
            }
        }

        List<MethodNode> matchedMethods = new ArrayList<>();
        for (MethodNode methodNode : classNode.methods) {
            if (!isIgnore(methodNode, methodNameMatcher)) {
                matchedMethods.add(methodNode);
            }
        }

        if (AsmUtils.isEnhancerByCGLIB(className)) {
            for (MethodNode methodNode : matchedMethods) {
                if (AsmUtils.isConstructor(methodNode)) {
                    AsmUtils.fixConstructorExceptionTable(methodNode);
                }
            }
        }

        GroupLocationFilter groupFilter = new GroupLocationFilter();
        groupFilter.addFilter(new InvokeContainLocationFilter(Type.getInternalName(SpyAPI.class), "atEnter", LocationType.ENTER));
        groupFilter.addFilter(new InvokeContainLocationFilter(Type.getInternalName(SpyAPI.class), "atExit", LocationType.EXIT));
        groupFilter.addFilter(new InvokeContainLocationFilter(Type.getInternalName(SpyAPI.class), "atExceptionExit", LocationType.EXCEPTION_EXIT));
        groupFilter.addFilter(new InvokeCheckLocationFilter(Type.getInternalName(SpyAPI.class), "atBeforeInvoke", LocationType.INVOKE));
        groupFilter.addFilter(new InvokeCheckLocationFilter(Type.getInternalName(SpyAPI.class), "atInvokeException", LocationType.INVOKE_COMPLETED));
        groupFilter.addFilter(new InvokeCheckLocationFilter(Type.getInternalName(SpyAPI.class), "atInvokeException", LocationType.INVOKE_EXCEPTION_EXIT));

        for (MethodNode methodNode : matchedMethods) {
            if (AsmUtils.isNative(methodNode)) {
                logger.info("ignore native method: {}", 
                           AsmUtils.methodDeclaration(Type.getObjectType(classNode.name), methodNode));
                continue;
            }

            if (AsmUtils.containsMethodInsnNode(methodNode, Type.getInternalName(SpyAPI.class), "atBeforeInvoke")) {
                for (AbstractInsnNode insnNode = methodNode.instructions.getFirst(); 
                     insnNode != null; 
                     insnNode = insnNode.getNext()) {

                    if (insnNode instanceof MethodInsnNode methodInsnNode) {
                        if (skipJDKTrace && methodInsnNode.owner.startsWith("java/")) {
                            continue;
                        }
                        if (AsmOpUtils.isBoxType(Type.getObjectType(methodInsnNode.owner))) {
                            continue;
                        }
                        AdviceListenerManager.registerTraceAdviceListener(inClassLoader, className,
                                methodInsnNode.owner, methodInsnNode.name, methodInsnNode.desc, listener);
                    }
                }
            } else {
                MethodProcessor methodProcessor = new MethodProcessor(classNode, methodNode, groupFilter);
                for (InterceptorProcessor interceptor : interceptorProcessors) {
                    try {
                        List<Location> locations = interceptor.process(methodProcessor);
                        for (Location location : locations) {
                            if (location instanceof MethodInsnNodeWare methodInsnNodeWare) {
                                MethodInsnNode methodInsnNode = methodInsnNodeWare.methodInsnNode();
                                AdviceListenerManager.registerTraceAdviceListener(inClassLoader, className,
                                        methodInsnNode.owner, methodInsnNode.name, methodInsnNode.desc, listener);
                            }
                        }
                    } catch (Throwable e) {
                        logger.error("enhancer error, class: {}, method: {}, interceptor: {}", 
                                    classNode.name, methodNode.name, interceptor.getClass().getName(), e);
                    }
                }
            }

            AdviceListenerManager.registerAdviceListener(inClassLoader, className, 
                                                         methodNode.name, methodNode.desc, listener);
            affect.addMethodAndCount(inClassLoader, className, methodNode.name, methodNode.desc);
        }

        if (AsmUtils.getMajorVersion(classNode.version) < 49) {
            classNode.version = AsmUtils.setMajorVersion(classNode.version, 49);
        }

        byte[] enhancedBytes = AsmUtils.toBytes(classNode, inClassLoader, classReader);
        classBytesCache.put(classBeingRedefined, new Object());
        dumpClassIfNecessary(className, enhancedBytes, affect);
        affect.cCnt(1);

        return enhancedBytes;
    } catch (Throwable t) {
        logger.warn("transform loader[{}]:class[{}] failed.", inClassLoader, className, t);
        affect.setThrowable(t);
    }
    return null;
}

public static boolean completeMethodName(Completion completion) {
    List<CliToken> tokens = completion.lineTokens();
    String lastToken = tokens.isEmpty() ? "" : tokens.get(tokens.size() - 1).value();

    String className;
    if (StringUtils.isBlank(lastToken)) {
        className = tokens.get(tokens.size() - 2).value();
    } else {
        className = tokens.get(tokens.size() - 3).value();
    }

    Set<Class<?>> results = SearchUtils.searchClassOnly(
        completion.session().getInstrumentation(), className, 2);

    if (results.size() != 1) {
        completion.complete(Collections.emptyList());
        return true;
    }

    Class<?> clazz = results.iterator().next();
    List<String> res = new ArrayList<>();
    for (Method method : clazz.getDeclaredMethods()) {
        if (StringUtils.isBlank(lastToken) || method.getName().startsWith(lastToken)) {
            res.add(method.getName());
        }
    }
    res.add("<init>");

    if (res.size() == 1) {
        completion.complete(res.get(0).substring(lastToken.length()), true);
    } else {
        CompletionUtils.complete(completion, res);
    }
    return true;
}

private void agentRegister(ChannelHandlerContext ctx, HandshakeComplete handshake, String requestUri) 
        throws URISyntaxException {

    QueryStringDecoder queryDecoder = new QueryStringDecoder(requestUri);
    Map<String, List<String>> parameters = queryDecoder.parameters();

    String appName = getFirstParameter(parameters, URIConstans.APP_NAME);
    String id = generateAgentId(appName);
    String arthasVersion = getFirstParameter(parameters, URIConstans.ARTHAS_VERSION);

    AgentInfo info = new AgentInfo();
    HttpHeaders headers = handshake.requestHeaders();
    String host = HttpUtils.findClientIP(headers);

    if (host == null) {
        SocketAddress remoteAddress = ctx.channel().remoteAddress();
        if (remoteAddress instanceof InetSocketAddress inetAddr) {
            info.setHost(inetAddr.getHostString());
            info.setPort(inetAddr.getPort());
        }
    } else {
        info.setHost(host);
        Integer port = HttpUtils.findClientPort(headers);
        if (port != null) {
            info.setPort(port);
        }
    }

    info.setChannelHandlerContext(ctx);
    if (arthasVersion != null) {
        info.setArthasVersion(arthasVersion);
    }

    tunnelServer.addAgent(id, info);
    ctx.channel().closeFuture().addListener(future -> tunnelServer.removeAgent(id));

    URI responseUri = UriComponentsBuilder.newInstance()
        .scheme(URIConstans.RESPONSE)
        .path("/")
        .queryParam(URIConstans.METHOD, MethodConstants.AGENT_REGISTER)
        .queryParam(URIConstans.ID, id)
        .build()
        .encode()
        .toUri();

    ctx.channel().writeAndFlush(new TextWebSocketFrame(responseUri.toString()));
}

private String getFirstParameter(Map<String, List<String>> params, String key) {
    List<String> values = params.get(key);
    return (values != null && !values.isEmpty()) ? values.get(0) : null;
}

private String generateAgentId(String appName) {
    return (appName != null ? appName + "_" : "") + 
           RandomStringUtils.random(20, true, true).toUpperCase();
}

@Override
public int[] startSubnegotiationLocal() {
    int compoundWindowSize = m_nWidth * 0x10000 + m_nHeight;
    int responseSize = 5;

    if ((m_nWidth % 0x100) == 0xFF) responseSize++;
    if ((m_nWidth / 0x100) == 0xFF) responseSize++;
    if ((m_nHeight % 0x100) == 0xFF) responseSize++;
    if ((m_nHeight / 0x100) == 0xFF) responseSize++;

    int[] response = new int[responseSize];
    response[0] = WINDOW_SIZE;

    for (int index = 1, shift = 24; index < responseSize; index++, shift -= 8) {
        int turnedOnBits = 0xFF << shift;
        response[index] = (compoundWindowSize & turnedOnBits) >>> shift;

        if (response[index] == 0xff) {
            response[++index] = 0xff;
        }
    }

    return response;
}

public static URL[] getUrls(ClassLoader classLoader) {
    if (classLoader instanceof URLClassLoader urlClassLoader) {
        try {
            return urlClassLoader.getURLs();
        } catch (Throwable e) {
            logger.error("classLoader: {} getUrls error", classLoader, e);
        }
    }

    String className = classLoader.getClass().getName();
    if (className.startsWith("jdk.internal.loader.ClassLoaders$") || 
        className.startsWith("jdk.internal.loader.BuiltinClassLoader")) {

        try {
            Field field = Unsafe.class.getDeclaredField("theUnsafe");
            field.setAccessible(true);
            Unsafe unsafe = (Unsafe) field.get(null);

            Class<?> ucpOwner = classLoader.getClass();
            Field ucpField = findFieldInHierarchy(ucpOwner, "ucp");
            if (ucpField == null) return null;

            long ucpOffset = unsafe.objectFieldOffset(ucpField);
            Object ucpObject = unsafe.getObject(classLoader, ucpOffset);
            if (ucpObject == null) return null;

            Field pathField = ucpField.getType().getDeclaredField("path");
            long pathOffset = unsafe.objectFieldOffset(pathField);
            ArrayList<URL> path = (ArrayList<URL>) unsafe.getObject(ucpObject, pathOffset);

            return path.toArray(new URL[0]);
        } catch (Throwable e) {
            return null;
        }
    }
    return null;
}

private static Field findFieldInHierarchy(Class<?> clazz, String fieldName) {
    while (clazz != Object.class) {
        try {
            return clazz.getDeclaredField(fieldName);
        } catch (NoSuchFieldException e) {
            clazz = clazz.getSuperclass();
        }
    }
    return null;
}

private Minf parseMinf(Box ref) throws IOException {
    Minf obj = new Minf();
    Box b;

    while ((b = untilAnyBox(ref)) != null) {
        switch (b.type) {
            case ATOM_DINF:
                obj.dinf = readFullBox(b);
                break;
            case ATOM_STBL:
                obj.stblStsd = parseStbl(b);
                break;
            case ATOM_VMHD:
            case ATOM_SMHD:
                obj.mhd = readFullBox(b);
                break;
        }
        ensure(b);
    }
    return obj;
}

private Moov parseMoov(Box ref) throws IOException {
    Box b = readBox(ATOM_MVHD);
    Moov moov = new Moov();
    moov.mvhd = parseMvhd();
    ensure(b);

    ArrayList<Trak> tmp = new ArrayList<>((int) moov.mvhd.nextTrackId);
    while ((b = untilBox(ref, ATOM_TRAK, ATOM_MVEX)) != null) {
        switch (b.type) {
            case ATOM_TRAK:
                tmp.add(parseTrak(b));
                break;
            case ATOM_MVEX:
                moov.mvexTrex = parseMvex(b, (int) moov.mvhd.nextTrackId);
                break;
        }
        ensure(b);
    }

    moov.trak = tmp.toArray(new Trak[0]);
    return moov;
}

private ArrayList<Object> getSpecialItems() {
    synchronized (DownloadManager.this) {
        ArrayList<Mission> pending = new ArrayList<>(mMissionsPending);
        ArrayList<Mission> finished = new ArrayList<>(mMissionsFinished);
        List<Mission> remove = new ArrayList<>(hidden);

        remove.removeIf(mission -> pending.remove(mission) || finished.remove(mission));

        int fakeTotal = pending.size();
        if (fakeTotal > 0) fakeTotal++;
        fakeTotal += finished.size();
        if (finished.size() > 0) fakeTotal++;

        ArrayList<Object> list = new ArrayList<>(fakeTotal);
        if (pending.size() > 0) {
            list.add(PENDING);
            list.addAll(pending);
        }
        if (finished.size() > 0) {
            list.add(FINISHED);
            list.addAll(finished);
        }

        hasFinished = finished.size() > 0;
        return list;
    }
}

@Override
public void run() {
    if (mMission.current > 0) mMission.resetState(false, true, DownloadMission.ERROR_NOTHING);

    int retryCount = 0;
    int httpCode = 204;

    while (true) {
        try {
            if (mMission.blocks == null && mMission.current == 0) {
                long finalLength = 0;
                long lowestSize = Long.MAX_VALUE;

                for (int i = 0; i < mMission.urls.length && mMission.running; i++) {
                    mConn = mMission.openConnection(mMission.urls[i], true, 0, 0);
                    mMission.establishConnection(mId, mConn);
                    dispose();

                    if (Thread.interrupted()) return;
                    long length = Utility.getTotalContentLength(mConn);

                    if (i == 0) {
                        httpCode = mConn.getResponseCode();
                        mMission.length = length;
                    }

                    if (length > 0) finalLength += length;
                    if (length < lowestSize) lowestSize = length;
                }

                mMission.nearLength = finalLength;

                if (mMission.psAlgorithm != null && mMission.psAlgorithm.reserveSpace) {
                    mMission.offsets[0] = (lowestSize < 1) 
                        ? RESERVE_SPACE_DEFAULT 
                        : (lowestSize < RESERVE_SPACE_MAXIMUM) 
                            ? lowestSize 
                            : RESERVE_SPACE_MAXIMUM;
                }
            } else {
                mConn = mMission.openConnection(true, 0, 0);
                mMission.establishConnection(mId, mConn);
                dispose();

                if (!mMission.running || Thread.interrupted()) return;

                httpCode = mConn.getResponseCode();
                mMission.length = Utility.getTotalContentLength(mConn);
            }

            if (mMission.length == 0 || httpCode == 204) {
                mMission.notifyError(DownloadMission.ERROR_HTTP_NO_CONTENT, null);
                return;
            }

            if (mMission.length == -1 && mConn.getResponseCode() == 200) {
                mMission.blocks = new int[0];
                mMission.length = 0;
                mMission.unknownLength = true;
                if (DEBUG) Log.d(TAG, "falling back (unknown length)");
            } else {
                mConn = mMission.openConnection(true, mMission.length - 10, mMission.length);
                mMission.establishConnection(mId, mConn);
                dispose();

                if (!mMission.running || Thread.interrupted()) return;

                synchronized (mMission.LOCK) {
                    if (mConn.getResponseCode() == 206) {
                        if (mMission.threadCount > 1) {
                            int count = (int) (mMission.length / DownloadMission.BLOCK_SIZE);
                            if ((count * DownloadMission.BLOCK_SIZE) < mMission.length) count++;
                            mMission.blocks = new int[count];
                        } else {
                            mMission.blocks = new int[0];
                            mMission.unknownLength = false;
                        }
                        if (DEBUG) Log.d(TAG, "http response code = " + mConn.getResponseCode());
                    } else {
                        mMission.blocks = new int[0];
                        mMission.unknownLength = false;
                        if (DEBUG) Log.d(TAG, "falling back due http response code = " + mConn.getResponseCode());
                    }
                }

                if (!mMission.running || Thread.interrupted()) return;
            }

            try (SharpStream fs = mMission.storage.getStream()) {
                fs.setLength(mMission.offsets[mMission.current] + mMission.length);
                fs.seek(mMission.offsets[mMission.current]);
            }

            if (!mMission.running || Thread.interrupted()) return;

            if (!mMission.unknownLength && mMission.recoveryInfo != null) {
                String entityTag = mConn.getHeaderField("ETAG");
                String lastModified = mConn.getHeaderField("Last-Modified");
                MissionRecoveryInfo recovery = mMission.recoveryInfo[mMission.current];

                if (!TextUtils.isEmpty(entityTag)) {
                    recovery.setValidateCondition(entityTag);
                } else if (!TextUtils.isEmpty(lastModified)) {
                    recovery.setValidateCondition(lastModified);
                } else {
                    recovery.setValidateCondition(null);
                }
            }

            mMission.running = false;
            break;
        } catch (InterruptedIOException | ClosedByInterruptException e) {
            return;
        } catch (Exception e) {
            if (!mMission.running || super.isInterrupted()) return;

            if (e instanceof DownloadMission.HttpError && 
                ((DownloadMission.HttpError) e).statusCode == ERROR_HTTP_FORBIDDEN) {
                interrupt();
                mMission.doRecover(ERROR_HTTP_FORBIDDEN);
                return;
            }

            if (e instanceof IOException && e.getMessage().contains("Permission denied")) {
                mMission.notifyError(DownloadMission.ERROR_PERMISSION_DENIED, e);
                return;
            }

            if (retryCount++ > mMission.maxRetry) {
                Log.e(TAG, "initializer failed", e);
                mMission.notifyError(e);
                return;
            }
            Log.e(TAG, "initializer failed, retrying", e);
        }
    }
    mMission.start();
}

@SuppressLint("DefaultLocale")
private void updateProgress(ViewHolderItem h) {
    if (h == null || h.item == null || h.item.mission instanceof FinishedMission) return;

    DownloadMission mission = (DownloadMission) h.item.mission;
    double done = mission.done;
    long length = mission.getLength();
    long now = System.currentTimeMillis();
    boolean hasError = mission.errCode != ERROR_NOTHING;

    h.progress.setMarquee(mission.isRecovering() || 
        (!hasError && (!mission.isInitialized() || mission.unknownLength)));

    double progress = mission.unknownLength ? Double.NaN : done / length;

    if (hasError) {
        h.progress.setProgress(isNotFinite(progress) ? 1d : progress);
        h.status.setText(R.string.msg_error);
    } else if (isNotFinite(progress)) {
        h.status.setText(UNDEFINED_PROGRESS);
    } else {
        h.status.setText(String.format("%.2f%%", progress * 100));
        h.progress.setProgress(progress);
    }

    @StringRes int state;
    String sizeStr = Utility.formatBytes(length).concat("  ");

    if (mission.isPsFailed() || mission.errCode == ERROR_POSTPROCESSING_HOLD) {
        h.size.setText(sizeStr);
        return;
    } else if (!mission.running) {
        state = mission.enqueued ? R.string.queued : R.string.paused;
    } else if (mission.isPsRunning()) {
        state = R.string.post_processing;
    } else if (mission.isRecovering()) {
        state = R.string.recovering;
    } else {
        state = 0;
    }

    if (state != 0) {
        h.size.setText(sizeStr.concat("(").concat(mContext.getString(state)).concat(")"));
        h.resetSpeedMeasure();
        return;
    }

    if (h.lastTimestamp < 0) {
        h.size.setText(sizeStr);
        h.lastTimestamp = now;
        h.lastDone = done;
        return;
    }

    long deltaTime = now - h.lastTimestamp;
    double deltaDone = done - h.lastDone;

    if (h.lastDone > done) {
        h.lastDone = done;
        h.size.setText(sizeStr);
        return;
    }

    if (deltaDone > 0 && deltaTime > 0) {
        float speed = (float) ((deltaDone * 1000d) / deltaTime);
        float averageSpeed = speed;

        if (h.lastSpeedIdx < 0) {
            Arrays.fill(h.lastSpeed, speed);
            h.lastSpeedIdx = 0;
        } else {
            for (int i = 0; i < h.lastSpeed.length; i++) {
                averageSpeed += h.lastSpeed[i];
            }
            averageSpeed /= h.lastSpeed.length + 1.0f;
        }

        String speedStr = Utility.formatSpeed(averageSpeed);
        String etaStr = mission.unknownLength 
            ? "" 
            : Utility.formatBytes((long) done) + "/" + Utility.stringifySeconds(
                (long) Math.ceil((length - done) / averageSpeed)) + "  ";

        h.size.setText(sizeStr.concat(etaStr).concat(speedStr));

        h.lastTimestamp = now;
        h.lastDone = done;
        h.lastSpeed[h.lastSpeedIdx++] = speed;

        if (h.lastSpeedIdx >= h.lastSpeed.length) h.lastSpeedIdx = 0;
    }
}

private Trun parseTrun() throws IOException {
    Trun obj = new Trun();
    obj.bFlags = stream.readInt();
    obj.entryCount = stream.readInt();

    obj.entriesRowSize = 0;
    if (hasFlag(obj.bFlags, 0x0100)) obj.entriesRowSize += 4;
    if (hasFlag(obj.bFlags, 0x0200)) obj.entriesRowSize += 4;
    if (hasFlag(obj.bFlags, 0x0400)) obj.entriesRowSize += 4;
    if (hasFlag(obj.bFlags, 0x0800)) obj.entriesRowSize += 4;

    obj.bEntries = new byte[obj.entriesRowSize * obj.entryCount];

    if (hasFlag(obj.bFlags, 0x0001)) obj.dataOffset = stream.readInt();
    if (hasFlag(obj.bFlags, 0x0004)) obj.bFirstSampleFlags = stream.readInt();

    stream.read(obj.bEntries);

    for (int i = 0; i < obj.entryCount; i++) {
        TrunEntry entry = obj.getEntry(i);
        if (hasFlag(obj.bFlags, 0x0100)) obj.chunkDuration += entry.sampleDuration;
        if (hasFlag(obj.bFlags, 0x0200)) obj.chunkSize += entry.sampleSize;
        if (hasFlag(obj.bFlags, 0x0800) && !hasFlag(obj.bFlags, 0x0100)) {
            obj.chunkDuration += entry.sampleCompositionTimeOffset;
        }
    }
    return obj;
}

private void makeMvhd(long longestTrack) throws IOException {
    auxWrite(new byte[]{
        0x00, 0x00, 0x00, 0x78, 0x6D, 0x76, 0x68, 0x64, 0x01, 0x00, 0x00, 0x00
    });
    auxWrite(ByteBuffer.allocate(28)
        .putLong(time)
        .putLong(time)
        .putInt(DEFAULT_TIMESCALE)
        .putLong(longestTrack)
        .array()
    );

    auxWrite(new byte[]{
        0x00, 0x01, 0x00, 0x00, 0x01, 0x00,
        0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00,
        0x00, 0x01, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00,
        0x00, 0x00, 0x00, 0x01, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00,
        0x00, 0x00, 0x00, 0x00,
        0x40, 0x00, 0x00, 0x00
    });
    auxWrite(new byte[24]);
    auxWrite(ByteBuffer.allocate(4)
        .putInt(tracks.length + 1)
        .array()
    );
}

public Mp4DashChunk getNextChunk(boolean infoOnly) throws IOException {
    Mp4Track track = tracks[selectedTrack];

    while (stream.available()) {
        if (chunkZero) {
            ensure(box);
            if (!stream.available()) break;
            box = readBox();
        } else {
            chunkZero = true;
        }

        switch (box.type) {
            case ATOM_MOOF:
                if (moof != null) throw new IOException("moof found without mdat");

                moof = parseMoof(box, track.trak.tkhd.trackId);
                if (moof.traf != null) {
                    if (hasFlag(moof.traf.trun.bFlags, 0x0001)) {
                        moof.traf.trun.dataOffset -= box.size + 8;
                        if (moof.traf.trun.dataOffset < 0) {
                            throw new IOException("trun box has wrong data offset, points outside of concurrent mdat box");
                        }
                    }

                    if (moof.traf.trun.chunkSize < 1) {
                        moof.traf.trun.chunkSize = hasFlag(moof.traf.tfhd.bFlags, 0x10)
                            ? moof.traf.tfhd.defaultSampleSize * moof.traf.trun.entryCount
                            : (int) (box.size - 8);
                    }
                    if (!hasFlag(moof.traf.trun.bFlags, 0x900) && moof.traf.trun.chunkDuration == 0) {
                        if (hasFlag(moof.traf.tfhd.bFlags, 0x20)) {
                            moof.traf.trun.chunkDuration = moof.traf.tfhd.defaultSampleDuration * moof.traf.trun.entryCount;
                        }
                    }
                }
                break;
            case ATOM_MDAT:
                if (moof == null) throw new IOException("mdat found without moof");
                if (moof.traf == null) {
                    moof = null;
                    continue;
                }

                Mp4DashChunk chunk = new Mp4DashChunk();
                chunk.moof = moof;
                if (!infoOnly) chunk.data = stream.getView(moof.traf.trun.chunkSize);

                moof = null;
                stream.skipBytes(chunk.moof.traf.trun.dataOffset);
                return chunk;
        }
    }
    return null;
}

public void updateMission(Mission mission) {
    ContentValues values = getValuesOfMission(Objects.requireNonNull(mission));
    SQLiteDatabase database = getWritableDatabase();
    String ts = String.valueOf(mission.timestamp);
    int rowsAffected;

    if (mission instanceof FinishedMission) {
        if (mission.storage.isInvalid()) {
            rowsAffected = database.update(FINISHED_TABLE_NAME, values, KEY_TIMESTAMP + " = ?", new String[]{ts});
        } else {
            rowsAffected = database.update(FINISHED_TABLE_NAME, values, KEY_PATH + " = ?", 
                new String[]{mission.storage.getUri().toString()});
        }
    } else {
        throw new UnsupportedOperationException("DownloadMission");
    }

    if (rowsAffected != 1) {
        Log.e("FinishedMissionStore", "Expected 1 row to be affected by update but got " + rowsAffected);
    }
}

private void animatePopupOverlayAndFinishService() {
    int targetTranslationY = (int) (closeOverlayBinding.closeButton.getRootView().getHeight()
            - closeOverlayBinding.closeButton.getY());
    closeOverlayBinding.closeButton.animate().setListener(null).cancel();
    closeOverlayBinding.closeButton.animate()
            .setInterpolator(new AnticipateInterpolator())
            .translationY(targetTranslationY)
            .setDuration(400)
            .setListener(new AnimatorListenerAdapter() {
                @Override
                public void onAnimationCancel(Animator animation) {
                    end();
                }

                @Override
                public void onAnimationEnd(Animator animation) {
                    end();
                }

                private void end() {
                    windowManager.removeView(closeOverlayBinding.getRoot());
                    closeOverlayBinding = null;
                    player.getService().destroyPlayerAndStopService();
                }
            }).start();
}

public void play() {
    if (DEBUG) {
        Log.d(TAG, "play() called");
    }
    if (audioReactor == null || playQueue == null || exoPlayerIsNull()) {
        return;
    }
    if (!isMuted()) {
        audioReactor.requestAudioFocus();
    }
    if (currentState == STATE_COMPLETED) {
        if (playQueue.getIndex() == 0) {
            seekToDefault();
        } else {
            playQueue.setIndex(0);
        }
    }
    simpleExoPlayer.play();
    saveStreamProgressState();
}

