private static String formatFileSize(long size) {
    double t = size / 1024.0 / 1024.0 / 1024.0 / 1024.0;
    double g = size / 1024.0 / 1024.0 / 1024.0;
    double m = size / 1024.0 / 1024.0;
    double k = size / 1024.0;

    DecimalFormat dec = new DecimalFormat("0.00");

    if (t > 1) {
        return dec.format(t) + " TB";
    }
    if (g > 1) {
        return dec.format(g) + " GB";
    }
    if (m > 1) {
        return dec.format(m) + " MB";
    }
    if (k > 1) {
        return dec.format(k) + " KB";
    }
    return dec.format(size) + " Bytes";
}

private static Class[] parseTypes(String desc, ClassLoader loader) throws ClassNotFoundException {
    int lparen = desc.indexOf('(');
    int rparen = desc.indexOf(')', lparen);
    List params = new ArrayList();
    int start = lparen + 1;
    for (;;) {
        int comma = desc.indexOf(',', start);
        if (comma < 0) {
            break;
        }
        params.add(desc.substring(start, comma).trim());
        start = comma + 1;
    }
    if (start < rparen) {
        params.add(desc.substring(start, rparen).trim());
    }
    Class[] types = new Class[params.size()];
    for (int i = 0; i < types.length; i++) {
        types[i] = getClass((String) params.get(i), loader);
    }
    return types;
}

@Override
public void process(final CommandProcess process) {
    Session session = process.session();
    timer = new Timer("Timer-for-arthas-dashboard-" + session.getSessionId(), true);

    process.interruptHandler(new DashboardInterruptHandler(process, timer));

    Handler<Void> stopHandler = new Handler<Void>() {
        @Override
        public void handle(Void event) {
            stop();
        }
    };

    Handler<Void> restartHandler = new Handler<Void>() {
        @Override
        public void handle(Void event) {
            restart(process);
        }
    };
    process.suspendHandler(stopHandler);
    process.resumeHandler(restartHandler);
    process.endHandler(stopHandler);

    process.stdinHandler(new QExitHandler(process));
    timer.scheduleAtFixedRate(new DashboardTimerTask(process), 0, getInterval());
}

public boolean isAvailable() {
    if (!isConnected()) {
        return false;
    }
    try {
        if (_socket_.getInetAddress() == null) {
            return false;
        }
        if (_socket_.getPort() == 0) {
            return false;
        }
        if (_socket_.getRemoteSocketAddress() == null) {
            return false;
        }
        if (_socket_.isClosed()) {
            return false;
        }
        if (_socket_.isInputShutdown()) {
            return false;
        }
        if (_socket_.isOutputShutdown()) {
            return false;
        }
        _socket_.getInputStream();
        _socket_.getOutputStream();
    } catch (IOException ioex) {
        return false;
    }
    return true;
}

public void start() throws Exception {
    final SslContext sslCtx;
    if (ssl) {
        SelfSignedCertificate ssc = new SelfSignedCertificate();
        sslCtx = SslContextBuilder.forServer(ssc.certificate(), ssc.privateKey()).build();
    } else {
        sslCtx = null;
    }

    ServerBootstrap b = new ServerBootstrap();
    b.group(bossGroup, workerGroup)
     .channel(NioServerSocketChannel.class)
     .handler(new LoggingHandler(LogLevel.INFO))
     .childHandler(new TunnelSocketServerInitializer(this, sslCtx));

    if (StringUtils.isBlank(host)) {
        channel = b.bind(port).sync().channel();
    } else {
        channel = b.bind(host, port).sync().channel();
    }

    logger.info("Tunnel server listen at {}:{}", host, port);

    workerGroup.scheduleWithFixedDelay(new Runnable() {
        @Override
        public void run() {
            agentInfoMap.entrySet().removeIf(e -> !e.getValue().getChannelHandlerContext().channel().isActive());
            clientConnectionInfoMap.entrySet()
                    .removeIf(e -> !e.getValue().getChannelHandlerContext().channel().isActive());

            if (tunnelClusterStore != null && clientConnectHost != null) {
                try {
                    for (Entry<String, AgentInfo> entry : agentInfoMap.entrySet()) {
                        tunnelClusterStore.addAgent(entry.getKey(), 
                            new AgentClusterInfo(entry.getValue(), clientConnectHost, port), 
                            60 * 60, 
                            TimeUnit.SECONDS);
                    }
                } catch (Throwable t) {
                    logger.error("update tunnel info error", t);
                }
            }
        }
    }, 60, 60, TimeUnit.SECONDS);
}

public Map<String, Class<?>> build() {
    errors.clear();
    warnings.clear();

    JavaFileManager fileManager = new DynamicJavaFileManager(standardFileManager, dynamicClassLoader);
    DiagnosticCollector<JavaFileObject> collector = new DiagnosticCollector<>();
    JavaCompiler.CompilationTask task = javaCompiler.getTask(null, fileManager, collector, options, null, compilationUnits);

    try {
        if (compilationUnits.isEmpty()) {
            return dynamicClassLoader.getClasses();
        }

        boolean result = task.call();
        if (!result || !collector.getDiagnostics().isEmpty()) {
            for (Diagnostic<? extends JavaFileObject> diagnostic : collector.getDiagnostics()) {
                switch (diagnostic.getKind()) {
                case NOTE:
                case MANDATORY_WARNING:
                case WARNING:
                    warnings.add(diagnostic);
                    break;
                case OTHER:
                case ERROR:
                default:
                    errors.add(diagnostic);
                    break;
                }
            }
            if (!errors.isEmpty()) {
                throw new DynamicCompilerException("Compilation Error", errors);
            }
        }
        return dynamicClassLoader.getClasses();
    } catch (Throwable e) {
        throw new DynamicCompilerException(e, errors);
    } finally {
        compilationUnits.clear();
    }
}

public SimpleHttpResponse query(String targetUrl) throws InterruptedException {
    final Promise<SimpleHttpResponse> httpResponsePromise = GlobalEventExecutor.INSTANCE.newPromise();
    final EventLoopGroup group = new NioEventLoopGroup(1, new DefaultThreadFactory("arthas-ProxyClient", true));
    ChannelFuture closeFuture = null;
    try {
        Bootstrap b = new Bootstrap();
        b.option(ChannelOption.CONNECT_TIMEOUT_MILLIS, 5000)
         .group(group)
         .channel(LocalChannel.class)
         .handler(new ChannelInitializer<LocalChannel>() {
             @Override
             protected void initChannel(LocalChannel ch) {
                 ch.pipeline().addLast(
                     new HttpClientCodec(),
                     new HttpObjectAggregator(ArthasConstants.MAX_HTTP_CONTENT_LENGTH),
                     new HttpProxyClientHandler(httpResponsePromise)
                 );
             }
         });

        LocalAddress localAddress = new LocalAddress(ArthasConstants.NETTY_LOCAL_ADDRESS);
        Channel localChannel = b.connect(localAddress).sync().channel();

        HttpRequest request = new DefaultFullHttpRequest(HttpVersion.HTTP_1_1, HttpMethod.GET, targetUrl);
        request.headers().set(HttpHeaderNames.CONNECTION, HttpHeaderValues.CLOSE);
        localChannel.writeAndFlush(request);

        closeFuture = localChannel.closeFuture();
        return httpResponsePromise.get(5000, TimeUnit.MILLISECONDS);
    } catch (Throwable e) {
        logger.error("ProxyClient error, targetUrl: {}", targetUrl, e);
        SimpleHttpResponse httpResponse = new SimpleHttpResponse();
        try {
            httpResponse.setContent("error".getBytes("utf-8"));
        } catch (UnsupportedEncodingException ignore) {
        }
        return httpResponse;
    } finally {
        if (closeFuture != null) {
            closeFuture.addListener(future -> group.shutdownGracefully());
        } else {
            group.shutdownGracefully();
        }
    }
}

@Override
public void write(int ch) throws IOException {
    synchronized (__client) {
        ch &= 0xff;

        if (__client._requestedWont(TelnetOption.BINARY)) {
            if (__lastWasCR) {
                if (__convertCRtoCRLF) {
                    __client._sendByte('\n');
                    if (ch == '\n') {
                        __lastWasCR = false;
                        return;
                    }
                } else if (ch != '\n') {
                    __client._sendByte('\0');
                }
            }

            switch (ch) {
            case '\r':
                __client._sendByte('\r');
                __lastWasCR = true;
                break;
            case '\n':
                if (!__lastWasCR) {
                    __client._sendByte('\r');
                }
                __client._sendByte(ch);
                __lastWasCR = false;
                break;
            case TelnetCommand.IAC:
                __client._sendByte(TelnetCommand.IAC);
                __client._sendByte(TelnetCommand.IAC);
                __lastWasCR = false;
                break;
            default:
                __client._sendByte(ch);
                __lastWasCR = false;
                break;
            }
        } else if (ch == TelnetCommand.IAC) {
            __client._sendByte(TelnetCommand.IAC);
            __client._sendByte(TelnetCommand.IAC);
        } else {
            __client._sendByte(ch);
        }
    }
}

private String drawField() {
    final StringBuilder fieldSB = new StringBuilder();
    final Field[] fields = clazz.getDeclaredFields();

    for (Field field : fields) {
        KVView kvView = new KVView(
            new TableView.ColumnDefine(TableView.Align.RIGHT),
            new TableView.ColumnDefine(50, false, TableView.Align.LEFT)
        ).add("modifier", StringUtils.modifier(field.getModifiers(), ','))
         .add("type", StringUtils.classname(field.getType()))
         .add("name", field.getName());

        Annotation[] annotations = field.getAnnotations();
        if (annotations.length > 0) {
            StringBuilder annotationSB = new StringBuilder();
            for (Annotation annotation : annotations) {
                annotationSB.append(StringUtils.classname(annotation.annotationType())).append(',');
            }
            annotationSB.setLength(annotationSB.length() - 1);
            kvView.add("annotation", annotationSB.toString());
        }

        if (Modifier.isStatic(field.getModifiers())) {
            boolean accessible = field.isAccessible();
            try {
                field.setAccessible(true);
                kvView.add("value", StringUtils.objectToString(field.get(null)));
            } catch (IllegalAccessException ignore) {
            } finally {
                field.setAccessible(accessible);
            }
        }
        fieldSB.append(kvView.draw()).append('\n');
    }
    return fieldSB.toString();
}

public static Properties splitArrayElementsIntoProperties(String[] array, String delimiter, String charsToDelete) {
    if (ObjectUtils.isEmpty(array)) {
        return null;
    }
    Properties result = new Properties();
    for (String element : array) {
        if (charsToDelete != null) {
            element = deleteAny(element, charsToDelete);
        }
        String[] parts = split(element, delimiter);
        if (parts != null) {
            result.setProperty(parts[0].trim(), parts[1].trim());
        }
    }
    return result;
}

private static long findProcessByTelnetClient(String arthasHomeDir, int telnetPort) {
    // start java telnet client
    List<String> telnetArgs = new ArrayList<String>();
    telnetArgs.add("-c");
    telnetArgs.add("session");
    telnetArgs.add("--execution-timeout");
    telnetArgs.add("2000");
    // telnet port ,ip
    telnetArgs.add("127.0.0.1");
    telnetArgs.add("" + telnetPort);

    try {
        ByteArrayOutputStream out = new ByteArrayOutputStream(1024);
        String error = null;
        int status = ProcessUtils.startArthasClient(arthasHomeDir, telnetArgs, out);
        if (status == STATUS_EXEC_TIMEOUT) {
            error = "detection timeout";
        } else if (status == STATUS_EXEC_ERROR) {
            error = "detection error";
            AnsiLog.error("process status: {}", status);
            AnsiLog.error("process output: {}", out.toString());
        } else {
            // ignore connect error
        }
        if (error != null) {
            AnsiLog.error("The telnet port {} is used, but process {}, you will connect to an unexpected process.", telnetPort, error);
            AnsiLog.error("Try to use a different telnet port, for example: java -jar arthas-boot.jar --telnet-port 9998 --http-port -1");
            System.exit(1);
        }

        //parse output, find java pid
        String output = out.toString("UTF-8");
        String javaPidLine = null;
        Scanner scanner = new Scanner(output);
        while (scanner.hasNextLine()) {
            String line = scanner.nextLine();
            if (line.contains("JAVA_PID")) {
                javaPidLine = line;
                break;
            }
        }
        if (javaPidLine != null) {
            // JAVA_PID    10473
            try {
                String[] strs = javaPidLine.split("JAVA_PID");
                if (strs.length > 1) {
                    return Long.parseLong(strs[strs.length - 1].trim());
                }
            } catch (NumberFormatException e) {
                // ignore
            }
        }
    } catch (Throwable ex) {
        AnsiLog.error("Detection telnet port error");
        AnsiLog.error(ex);
    }

    return -1;
}

