private ApiResponse processAsyncExecRequest(ApiRequest apiRequest, Session session) {
    String commandLine = apiRequest.getCommand();
    Map<String, Object> body = new TreeMap<String, Object>();
    body.put("command", commandLine);

    ApiResponse response = new ApiResponse();
    response.setSessionId(session.getSessionId())
            .setBody(body);

    if (!session.tryLock()) {
        response.setState(ApiState.REFUSED)
                .setMessage("Another command is executing.");
        return response;
    }
    int lock = session.getLock();
    try {

        Job foregroundJob = session.getForegroundJob();
        if (foregroundJob != null) {
            response.setState(ApiState.REFUSED)
                    .setMessage("Another job is running.");
            logger.info("Another job is running, jobId: {}", foregroundJob.id());
            return response;
        }

        //create job
        Job job = this.createJob(commandLine, session, session.getResultDistributor());
        body.put("jobId", job.id());
        body.put("jobStatus", job.status());
        response.setState(ApiState.SCHEDULED);

        //add command before exec job
        CommandRequestModel commandRequestModel = new CommandRequestModel(commandLine, response.getState());
        commandRequestModel.setJobId(job.id());
        SharingResultDistributor resultDistributor = session.getResultDistributor();
        if (resultDistributor != null) {
            resultDistributor.appendResult(commandRequestModel);
        }
        session.setForegroundJob(job);
        updateSessionInputStatus(session, InputStatus.ALLOW_INTERRUPT);

        //run job
        job.run();

        return response;
    } catch (Throwable e) {
        logger.error("Async exec command failed:" + e.getMessage() + ", command:" + commandLine, e);
        response.setState(ApiState.FAILED).setMessage("Async exec command failed:" + e.getMessage());
        CommandRequestModel commandRequestModel = new CommandRequestModel(commandLine, response.getState(), response.getMessage());
        session.getResultDistributor().appendResult(commandRequestModel);
        return response;
    } finally {
        if (session.getLock() == lock) {
            session.unLock();
        }
    }
}

private void renderNode(StringBuilder sb, TraceNode node, Ansi highlighted) {
    //render cost: [0.366865ms]
    if (isPrintCost && node instanceof MethodNode) {
        MethodNode methodNode = (MethodNode) node;

        String costStr = renderCost(methodNode);
        if (node == maxCostNode) {
            // the node with max cost will be highlighted
            sb.append(highlighted.a(costStr).reset().toString());
        } else {
            sb.append(costStr);
        }
    }

    //render method name
    if (node instanceof MethodNode) {
        MethodNode methodNode = (MethodNode) node;
        //clazz.getName() + ":" + method.getName() + "()"
        sb.append(methodNode.getClassName()).append(":").append(methodNode.getMethodName()).append("()");
        // #lineNumber
        if (methodNode.getLineNumber()!= -1) {
            sb.append(" #").append(methodNode.getLineNumber());
        }
    } else if (node instanceof ThreadNode) {
        //render thread info
        ThreadNode threadNode = (ThreadNode) node;
        //ts=2020-04-29 10:34:00;thread_name=main;id=1;is_daemon=false;priority=5;TCCL=sun.misc.Launcher$AppClassLoader@18b4aac2
        sb.append(format("ts=%s;thread_name=%s;id=%d;is_daemon=%s;priority=%d;TCCL=%s",
                DateUtils.formatDateTime(threadNode.getTimestamp()),
                threadNode.getThreadName(),
                threadNode.getThreadId(),
                threadNode.isDaemon(),
                threadNode.getPriority(),
                threadNode.getClassloader()));

        //trace_id
        if (threadNode.getTraceId() != null) {
            sb.append(";trace_id=").append(threadNode.getTraceId());
        }
        if (threadNode.getRpcId() != null) {
            sb.append(";rpc_id=").append(threadNode.getRpcId());
        }
    } else if (node instanceof ThrowNode) {
        ThrowNode throwNode = (ThrowNode) node;
        sb.append("throw:").append(throwNode.getException())
                .append(" #").append(throwNode.getLineNumber())
                .append(" [").append(throwNode.getMessage()).append("]");

    } else {
        throw new UnsupportedOperationException("unknown trace node: " + node.getClass());
    }
}

