@Override
public final <T> T executeCommand(CommandObject<T> commandObject) {
    final Instant deadline = Instant.now().plus(maxTotalRetriesDuration);
    int consecutiveConnectionFailures = 0;
    JedisException lastException = null;
    for (int attemptsLeft = this.maxAttempts; attemptsLeft > 0; attemptsLeft--) {
        Connection connection = null;
        try {
            connection = provider.getConnection(commandObject.getArguments());
            return execute(connection, commandObject);
        } catch (final JedisConnectionException jce) {
            lastException = jce;
            ++consecutiveConnectionFailures;
            log.debug("Failed connecting to Redis: {}", connection, jce);
            final boolean reset = handleConnectionProblem(attemptsLeft - 1, consecutiveConnectionFailures, deadline);
            if (reset) {
                consecutiveConnectionFailures = 0;
            }
        } finally {
            if (connection != null) {
                connection.close();
            }
        }
        if (Instant.now().isAfter(deadline)) {
            throw new JedisException("Retry deadline exceeded.");
        }
    }
    final JedisException maxAttemptsException = new JedisException("No more attempts left.");
    maxAttemptsException.addSuppressed(lastException);
    throw maxAttemptsException;
}

@Override
public Map.Entry<T, ProfilingInfo> build(Object data) {
    final List list = (List) data;
    if (list == null || list.isEmpty()) {
        return null;
    }

    if (list.get(0) instanceof KeyValue) {
        Object resultsData = null, profileData = null;
        for (final KeyValue keyValue : (List<KeyValue>) data) {
            final String keyStr = BuilderFactory.STRING.build(keyValue.getKey());
            switch (keyStr) {
                case PROFILE_STR_REDIS7:
                case PROFILE_STR_REDIS8:
                    profileData = keyValue.getValue();
                    break;
                case RESULTS_STR_REDIS7:
                    resultsData = data;
                    break;
                case RESULTS_STR_REDIS8:
                    resultsData = keyValue.getValue();
                    break;
            }
        }
        assert resultsData != null : "Could not detect Results data.";
        assert profileData != null : "Could not detect Profile data.";
        return KeyValue.of(resultsBuilder.build(resultsData),
                ProfilingInfo.PROFILING_INFO_BUILDER.build(profileData));
    }
    return KeyValue.of(resultsBuilder.build(list.get(0)),
            ProfilingInfo.PROFILING_INFO_BUILDER.build(list.get(1)));
}

@Override
public final List<Object> exec() {
    if (!inMulti) {
        throw new IllegalStateException("EXEC without MULTI");
    }

    try (final Connection connection = failoverProvider.getConnection()) {
        commands.forEach(command -> connection.sendCommand(command.getKey()));
        connection.getMany(commands.size());

        for (int idx = 0; idx < extraCommandCount.get(); ++idx) {
            commands.poll();
        }

        connection.sendCommand(EXEC);
        final List<Object> unformatted = connection.getObjectMultiBulkReply();
        if (unformatted == null) {
            commands.clear();
            return null;
        }

        final List<Object> formatted = new ArrayList<>(unformatted.size() - extraCommandCount.get());
        for (final Object rawReply : unformatted) {
            try {
                final Response<?> response = commands.poll().getValue();
                response.set(rawReply);
                formatted.add(response.get());
            } catch (final JedisDataException e) {
                formatted.add(e);
            }
        }
        return formatted;
    } finally {
        inMulti = false;
        inWatch = false;
    }
}

@Override
public AggregationResult build(Object data) {
    final List list = (List) data;
    if (list.get(0) instanceof KeyValue) {
        final List<KeyValue> kvList = (List<KeyValue>) data;
        long totalResults = -1;
        List<Map<String, Object>> results = null;
        List<String> warnings = null;
        for (final KeyValue kv : kvList) {
            final String key = BuilderFactory.STRING.build(kv.getKey());
            final Object rawVal = kv.getValue();
            switch (key) {
                case TOTAL_RESULTS_STR:
                    totalResults = BuilderFactory.LONG.build(rawVal);
                    break;
                case RESULTS_STR:
                    final List<List<KeyValue>> resList = (List<List<KeyValue>>) rawVal;
                    results = new ArrayList<>(resList.size());
                    for (final List<KeyValue> rikv : resList) {
                        for (final KeyValue ikv : rikv) {
                            if (FIELDS_STR.equals(BuilderFactory.STRING.build(ikv.getKey()))) {
                                results.add(BuilderFactory.ENCODED_OBJECT_MAP.build(ikv.getValue()));
                                break;
                            }
                        }
                    }
                    break;
                case WARNINGS_STR:
                    warnings = BuilderFactory.STRING_LIST.build(rawVal);
                    break;
            }
        }
        return new AggregationResult(totalResults, results, warnings);
    }

    final List<Object> encodedList = (List<Object>) SafeEncoder.encodeObject(data);
    final long totalResults = (Long) encodedList.get(0);
    final List<Map<String, Object>> results = new ArrayList<>(encodedList.size() - 1);
    for (int i = 1; i < encodedList.size(); i++) {
        final List<Object> mapList = (List<Object>) encodedList.get(i);
        final Map<String, Object> map = new HashMap<>(mapList.size() / 2, 1f);
        for (int j = 0; j < mapList.size(); j += 2) {
            final Object r = mapList.get(j);
            if (r instanceof JedisDataException) {
                throw (JedisDataException) r;
            }
            map.put((String) r, mapList.get(j + 1));
        }
        results.add(map);
    }
    return new AggregationResult(totalResults, results);
}

