             .row(label(LoggerHelper.clazz).style(Decoration.bold.bold()), label(clazz.getName()))
             .row(label(LoggerHelper.classLoader).style(Decoration.bold.bold()), label(info.get(LoggerHelper.classLoader).toString()))
             .row(label(LoggerHelper.classLoaderHash).style(Decoration.bold.bold()), label(info.get(LoggerHelper.classLoaderHash).toString()))
