        }
        sb.append(RenderUtil.render(table, width)).append('\n');
    }
    return sb.toString();
}

private void renderNode(StringBuilder sb, TraceNode node, Ansi highlighted) {
    if (isPrintCost && node instanceof MethodNode) {
        MethodNode methodNode = (MethodNode) node;
        String costStr = renderCost(methodNode);
        sb.append(node == maxCostNode 
                 ? highlighted.a(costStr).reset().toString() 
                 : costStr);
    }

    if (node instanceof MethodNode) {
        MethodNode methodNode = (MethodNode) node;
        sb.append(methodNode.getClassName())
          .append(":")
          .append(methodNode.getMethodName())
          .append("()");
        if (methodNode.getLineNumber() != -1) {
            sb.append(" #").append(methodNode.getLineNumber());
        }
    } else if (node instanceof ThreadNode) {
        ThreadNode threadNode = (ThreadNode) node;
        sb.append(String.format("ts=%s;thread_name=%s;id=%d;is_daemon=%s;priority=%d;TCCL=%s",
                DateUtils.formatDateTime(threadNode.getTimestamp()),
                threadNode.getThreadName(),
                threadNode.getThreadId(),
                threadNode.isDaemon(),
                threadNode.getPriority(),
                threadNode.getClassloader()));

        if (threadNode.getTraceId() != null) {
            sb.append(";trace_id=").append(threadNode.getTraceId());
        }
        if (threadNode.getRpcId() != null) {
            sb.append(";rpc_id=").append(threadNode.getRpcId());
        }
    } else if (node instanceof ThrowNode) {
        ThrowNode throwNode = (ThrowNode) node;
        sb.append("throw:")
          .append(throwNode.getException())
          .append(" #").append(throwNode.getLineNumber())
          .append(" [").append(throwNode.getMessage()).append("]");
    } else {
        throw new UnsupportedOperationException("Unknown trace node: " + node.getClass());
    }
}

private static String processInlineImages(final String htmlContent, final EmailContent emailContent) {
    if (htmlContent == null || emailContent == null) {
        return htmlContent;
    }

    // Create a map of Content-ID to attachment data
    final Map<String, EmailAttachment> contentIdMap = new HashMap<>();
    for (final EmailAttachment attachment : emailContent.getAttachments()) {
        if (attachment.isEmbedded()
                && attachment.getContentId() != null
                && attachment.getData() != null) {
            contentIdMap.put(attachment.getContentId(), attachment);
        }
    }

    if (contentIdMap.isEmpty()) {
        return htmlContent;
    }

    // Pattern to match cid: references in img src attributes
    final Pattern cidPattern = Pattern.compile(
            "(?i)<img[^>]*\\ssrc\\s*=\\s*['\"]cid:([^'\"]+)['\"][^>]*>",
            Pattern.CASE_INSENSITIVE);
    final Matcher matcher = cidPattern.matcher(htmlContent);

    final StringBuffer result = new StringBuffer();
    while (matcher.find()) {
        final String contentId = matcher.group(1);
        final EmailAttachment attachment = contentIdMap.get(contentId);

        if (attachment != null && attachment.getData() != null) {
            // Convert to data URI
            final String mimeType;
            if (attachment.getContentType() != null && !attachment.getContentType().isEmpty()) {
                mimeType = attachment.getContentType();
            } else {
                // Try to determine MIME type from filename
                final String filename = attachment.getFilename();
                if (filename == null) {
                    mimeType = "image/png"; // fallback
                } else {
                    final String lowerCaseFilename = filename.toLowerCase();
                    if (lowerCaseFilename.endsWith(".png")) {
                        mimeType = "image/png";
                    } else if (lowerCaseFilename.endsWith(".jpg")
                            || lowerCaseFilename.endsWith(".jpeg")) {
                        mimeType = "image/jpeg";
                    } else if (lowerCaseFilename.endsWith(".gif")) {
                        mimeType = "image/gif";
                    } else if (lowerCaseFilename.endsWith(".bmp")) {
                        mimeType = "image/bmp";
                    } else {
                        mimeType = "image/png"; // fallback
                    }
                }
            }

            final String base64Data = Base64.getEncoder().encodeToString(attachment.getData());
            final String dataUri = "data:" + mimeType + ";base64," + base64Data;

            // Replace the cid: reference with the data URI
            final String replacement = matcher.group(0).replaceFirst(
                    "cid:" + Pattern.quote(contentId), dataUri);
            matcher.appendReplacement(result, Matcher.quoteReplacement(replacement));
        } else {
            // Keep original if attachment not found
            matcher.appendReplacement(result, Matcher.quoteReplacement(matcher.group(0)));
        }
    }
    matcher.appendTail(result);

    return result.toString();
}

private static String generateEnhancedEmailHtml(final EmailContent content, final EmlToPdfRequest request) {
