void fastPath(Subscriber<? super R> a, Iterator<? extends R> iterator) {
    for (;;) {
        if (cancelled) {
            return;
        }

        R v;

        try {
            v = iterator.next();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            a.onError(ex);
            return;
        }

        a.onNext(v);

        if (cancelled) {
            return;
        }

        boolean b;

        try {
            b = iterator.hasNext();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            a.onError(ex);
            return;
        }

        if (!b) {
            a.onComplete();
            return;
        }
    }
}

@Override
public void onComplete() {
    if (done) {
        return;
    }
    try {
        onComplete.run();
    } catch (Throwable e) {
        fail(e);
        return;
    }

    done = true;
    downstream.onComplete();

    try {
        onAfterTerminate.run();
    } catch (Throwable e) {
        Exceptions.throwIfFatal(e);
        RxJavaPlugins.onError(e);
    }
}

@Override
public void run(long n) {
    long emitted = 0L;
    Iterator<T> iterator = this.iterator;
    ConditionalSubscriber<? super T> downstream = this.downstream;

    for (;;) {

        if (cancelled) {
            clear();
            break;
        } else {
            T next;
            try {
                next = Objects.requireNonNull(iterator.next(), "The Stream's Iterator returned a null value");
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                downstream.onError(ex);
                cancelled = true;
                continue;
            }

            if (downstream.tryOnNext(next)) {
                emitted++;
            }

            if (cancelled) {
                continue;
            }

            try {
                if (!iterator.hasNext()) {
                    downstream.onComplete();
                    cancelled = true;
                    continue;
                }
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                downstream.onError(ex);
                cancelled = true;
                continue;
            }

            if (emitted != n) {
                continue;
            }
        }

        n = get();
        if (emitted == n) {
            if (compareAndSet(n, 0L)) {
                break;
            }
            n = get();
        }
    }
}

void replay(CacheSubscription<T> consumer) {
    if (consumer.getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    long index = consumer.index;
    int offset = consumer.offset;
    Node<T> node = consumer.node;
    AtomicLong requested = consumer.requested;
    Subscriber<? super T> downstream = consumer.downstream;
    int capacity = capacityHint;

    for (;;) {
        boolean sourceDone = done;
        boolean empty = size == index;

        if (sourceDone && empty) {
            consumer.node = null;
            Throwable ex = error;
            if (ex != null) {
                downstream.onError(ex);
            } else {
                downstream.onComplete();
            }
            return;
        }

        if (!empty) {
            long consumerRequested = requested.get();
            if (consumerRequested == Long.MIN_VALUE) {
                consumer.node = null;
                return;
            }
            if (consumerRequested != index) {

                if (offset == capacity) {
                    node = node.next;
                    offset = 0;
                }

                downstream.onNext(node.values[offset]);

                offset++;
                index++;

                continue;
            }
        }

        consumer.index = index;
        consumer.offset = offset;
        consumer.node = node;
        missed = consumer.addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    Observer<? super R> downstream = this.downstream;
    SimpleQueue<T> queue = this.queue;
    AtomicThrowable errors = this.errors;

    for (;;) {

        if (!active) {

            if (cancelled) {
                queue.clear();
                return;
            }

            if (!tillTheEnd) {
                Throwable ex = errors.get();
                if (ex != null) {
                    queue.clear();
                    cancelled = true;
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            boolean d = done;

            T v;

            try {
                v = queue.poll();
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                cancelled = true;
                this.upstream.dispose();
                errors.tryAddThrowableOrReport(ex);
                errors.tryTerminateConsumer(downstream);
                return;
            }

            boolean empty = v == null;

            if (d && empty) {
                cancelled = true;
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (!empty) {

                ObservableSource<? extends R> o;

                try {
                    o = Objects.requireNonNull(mapper.apply(v), "The mapper returned a null ObservableSource");
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    cancelled = true;
                    this.upstream.dispose();
                    queue.clear();
                    errors.tryAddThrowableOrReport(ex);
                    errors.tryTerminateConsumer(downstream);
                    return;
                }

                if (o instanceof Supplier) {
                    R w;

                    try {
                        w = ((Supplier<R>)o).get();
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        errors.tryAddThrowableOrReport(ex);
                        continue;
                    }

                    if (w != null && !cancelled) {
                        downstream.onNext(w);
                    }
                    continue;
                } else {
                    active = true;
                    o.subscribe(observer);
                }
            }
        }

        if (decrementAndGet() == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;

    final Subscriber<? super R> downstream = this.downstream;
    final SimpleQueue<T> queue = this.queue;
    final AtomicThrowable error = this.error;
    Iterator<? extends R> iterator = this.currentIterator;
    long requested = this.requested.get();
    long emitted = this.emitted;
    final int limit = prefetch - (prefetch >> 2);
    boolean canRequest = sourceMode != QueueFuseable.SYNC;

    for (;;) {
        if (cancelled) {
            queue.clear();
            clearCurrentSuppressCloseError();
        } else {
            boolean isDone = upstreamDone;
            if (error.get() != null) {
                downstream.onError(error.get());
                cancelled = true;
                continue;
            }

            if (iterator == null) {
                T t;

                try {
                    t = queue.poll();
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    trySignalError(downstream, ex);
                    continue;
                }

                boolean isEmpty = t == null;

                if (isDone && isEmpty) {
                    downstream.onComplete();
                    cancelled = true;
                }
                else if (!isEmpty) {
                    if (canRequest && ++consumed == limit) {
                        consumed = 0;
                        upstream.request(limit);
                    }

                    Stream<? extends R> stream;
                    try {
                        stream = Objects.requireNonNull(mapper.apply(t), "The mapper returned a null Stream");
                        iterator = stream.iterator();

                        if (iterator.hasNext()) {
                            currentIterator = iterator;
                            currentCloseable = stream;
                        } else {
                            iterator = null;
                        }
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        trySignalError(downstream, ex);
                    }
                    continue;
                }
            }
            if (iterator != null && emitted != requested) {
                R item;

                try {
                    item = Objects.requireNonNull(iterator.next(), "The Stream.Iterator returned a null value");
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    trySignalError(downstream, ex);
                    continue;
                }

                if (!cancelled) {
                    downstream.onNext(item);
                    emitted++;

                    if (!cancelled) {
                        try {
                            if (!iterator.hasNext()) {
                                iterator = null;
                                clearCurrentRethrowCloseError();
                            }
                        } catch (Throwable ex) {
                            Exceptions.throwIfFatal(ex);
                            trySignalError(downstream, ex);
                        }
                    }
                }

                continue;
            }
        }

        this.emitted = emitted;
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
        requested = this.requested.get();
    }
}

void drain() {

    if (getAndIncrement() != 0) {
        return;
    }

    final Subscriber<? super R> a = downstream;
    final ZipSubscriber<T, R>[] qs = subscribers;
    final int n = qs.length;
    Object[] values = current;

    int missed = 1;

    for (;;) {

        long r = requested.get();
        long e = 0L;

        while (r != e) {

            if (cancelled) {
                return;
            }

            if (!delayErrors && errors.get() != null) {
                cancelAll();
                errors.tryTerminateConsumer(a);
                return;
            }

            boolean empty = false;

            for (int j = 0; j < n; j++) {
                ZipSubscriber<T, R> inner = qs[j];
                if (values[j] == null) {
                    boolean d = inner.done;
                    SimpleQueue<T> q = inner.queue;
                    T v = null;
                    try {

                        v = q != null ? q.poll() : null;
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);

                        errors.tryAddThrowableOrReport(ex);
                        if (!delayErrors) {
                            cancelAll();
                            errors.tryTerminateConsumer(a);
                            return;
                        }
                        d = true;
                    }

                    boolean sourceEmpty = v == null;
                    if (d && sourceEmpty) {
                        cancelAll();
                        errors.tryTerminateConsumer(a);
                        return;
                    }
                    if (!sourceEmpty) {
                        values[j] = v;
                    } else {
                        empty = true;
                    }
                }
            }

            if (empty) {
                break;
            }

            R v;

            try {
                v = Objects.requireNonNull(zipper.apply(values.clone()), "The zipper returned a null value");
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);
                cancelAll();
                errors.tryAddThrowableOrReport(ex);
                errors.tryTerminateConsumer(a);
                return;
            }

            a.onNext(v);

            e++;

            Arrays.fill(values, null);
        }

        if (r == e) {
            if (cancelled) {
                return;
            }

            if (!delayErrors && errors.get() != null) {
                cancelAll();
                errors.tryTerminateConsumer(a);
                return;
            }

            for (int j = 0; j < n; j++) {
                ZipSubscriber<T, R> inner = qs[j];
                if (values[j] == null) {
                    boolean d = inner.done;
                    SimpleQueue<T> q = inner.queue;
                    T v = null;
                    try {
                        v = q != null ? q.poll() : null;
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        errors.tryAddThrowableOrReport(ex);
                        if (!delayErrors) {
                            cancelAll();
                            errors.tryTerminateConsumer(a);
                            return;
                        }
                        d = true;
                    }
                    boolean empty = v == null;
                    if (d && empty) {
                        cancelAll();
                        errors.tryTerminateConsumer(a);
                        return;
                    }
                    if (!empty) {
                        values[j] = v;
                    }
                }
            }

        }

        if (e != 0L) {

            for (ZipSubscriber<T, R> inner : qs) {
                inner.request(e);
            }

            if (r != Long.MAX_VALUE) {
                requested.addAndGet(-e);
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
Node getHead() {
    long timeLimit = scheduler.now(unit) - maxAge;
    Node prev = get();
    Node next = prev.get();
    for (;;) {
        if (next == null) {
            break;
        }
        Timed<?> v = (Timed<?>)next.value;
        if (NotificationLite.isComplete(v.value()) || NotificationLite.isError(v.value())) {
            break;
        }
        if (v.time() <= timeLimit) {
            prev = next;
            next = next.get();
        } else {
            break;
        }
    }
    return prev;
}

@SuppressWarnings("unchecked")
void remove(AsyncSubscription<T> ps) {
    for (;;) {
        AsyncSubscription<T>[] a = subscribers.get();
        int n = a.length;
        if (n == 0) {
            return;
        }

        int j = -1;
        for (int i = 0; i < n; i++) {
            if (a[i] == ps) {
                j = i;
                break;
            }
        }

        if (j < 0) {
            return;
        }

        AsyncSubscription<T>[] b;

        if (n == 1) {
            b = EMPTY;
        } else {
            b = new AsyncSubscription[n - 1];
            System.arraycopy(a, 0, b, 0, j);
            System.arraycopy(a, j + 1, b, j, n - j - 1);
        }
        if (subscribers.compareAndSet(a, b)) {
            return;
        }
    }
}

@Override
public void onSubscribe(Disposable d) {
    if (DisposableHelper.validate(this.upstream, d)) {
        this.upstream = d;
        if (d instanceof QueueDisposable) {
            @SuppressWarnings("unchecked")
            QueueDisposable<T> qd = (QueueDisposable<T>) d;

            int m = qd.requestFusion(QueueDisposable.ANY);
            if (m == QueueDisposable.SYNC) {
                fusionMode = m;
                queue = qd;
                done = true;

                downstream.onSubscribe(this);

                drain();
                return;
            }

            if (m == QueueDisposable.ASYNC) {
                fusionMode = m;
                queue = qd;

                downstream.onSubscribe(this);

                return;
            }
        }

        queue = new SpscLinkedArrayQueue<>(bufferSize);

        downstream.onSubscribe(this);
    }
}

private void saveDocumentToZip(
        final PDDocument document,
        final ZipOutputStream zipOut,
        final String baseFilename,
        final int index) throws IOException {
    log.debug("Starting saveDocumentToZip for document part {}", index);
    final ByteArrayOutputStream outStream = new ByteArrayOutputStream();

    try {
        log.debug("Saving document part {} to byte array", index);
        document.save(outStream);
        log.debug("Successfully saved document part {} ({} bytes)", index, outStream.size());
    } catch (final Exception e) {
        log.error("Error saving document part {} to byte array", index, e);
        throw ExceptionUtils.createFileProcessingException("split", e);
    }

    try {
        log.debug("Closing document part {}", index);
        document.close();
        log.debug("Successfully closed document part {}", index);
    } catch (final Exception e) {
        log.error("Error closing document part {}", index, e);
    }

    try {
        final String entryName = baseFilename + "_" + index + ".pdf";
        log.debug("Creating ZIP entry: {}", entryName);
        final ZipEntry zipEntry = new ZipEntry(entryName);
        zipOut.putNextEntry(zipEntry);

        final byte[] bytes = outStream.toByteArray();
        log.debug("Writing {} bytes to ZIP entry", bytes.length);
        zipOut.write(bytes);

        log.debug("Closing ZIP entry");
        zipOut.closeEntry();
        log.debug("Successfully added document part {} to ZIP", index);
    } catch (final Exception e) {
        log.error("Error adding document part {} to ZIP", index, e);
        throw ExceptionUtils.createFileProcessingException("split", e);
    }
}

private void processRequest(
        final int limitPerDay,
        final String identifier,
        final Map<String, Bucket> buckets,
        final HttpServletRequest request,
        final HttpServletResponse response,
        final FilterChain filterChain) throws IOException, ServletException {
    final Bucket userBucket = buckets.computeIfAbsent(identifier, k -> createUserBucket(limitPerDay));
    final ConsumptionProbe probe = userBucket.tryConsumeAndReturnRemaining(1);
    if (probe.isConsumed()) {
        response.setHeader("X-Rate-Limit-Remaining", stripNewlines(Newlines.stripAll(Long.toString(probe.getRemainingTokens()))));
        filterChain.doFilter(request, response);
    } else {
        final long waitForRefill = probe.getNanosToWaitForRefill() / 1_000_000_000;
        response.setStatus(HttpStatus.TOO_MANY_REQUESTS.value());
        response.setHeader("X-Rate-Limit-Retry-After-Seconds", Newlines.stripAll(String.valueOf(waitForRefill)));
        response.getWriter().write("Rate limit exceeded for POST requests.");
    }
}

@SuppressWarnings("deprecation")
public void migrateEnterpriseSettingsToPremium(final ApplicationProperties applicationProperties) {
    final EnterpriseEdition enterpriseEdition = applicationProperties.getEnterpriseEdition();
    final Premium premium = applicationProperties.getPremium();

    if (enterpriseEdition == null || premium == null) {
        return;
    }

    if (premium.getKey() == null || "00000000-0000-0000-0000-000000000000".equals(premium.getKey())) {
        if (enterpriseEdition.getKey() != null && !"00000000-0000-0000-0000-000000000000".equals(enterpriseEdition.getKey())) {
            premium.setKey(enterpriseEdition.getKey());
        }
    }

    if (!premium.isEnabled() && enterpriseEdition.isEnabled()) {
        premium.setEnabled(true);
    }

    if (!premium.getProFeatures().isSsoAutoLogin() && enterpriseEdition.isSsoAutoLogin()) {
        premium.getProFeatures().setSsoAutoLogin(true);
    }

    final Premium.ProFeatures.CustomMetadata premiumMetadata = premium.getProFeatures().getCustomMetadata();
    final EnterpriseEdition.CustomMetadata enterpriseMetadata = enterpriseEdition.getCustomMetadata();

    if (enterpriseMetadata != null && premiumMetadata != null) {
        if (!premiumMetadata.isAutoUpdateMetadata() && enterpriseMetadata.isAutoUpdateMetadata()) {
            premiumMetadata.setAutoUpdateMetadata(true);
        }

        if ((premiumMetadata.getAuthor() == null || premiumMetadata.getAuthor().trim().isEmpty() || "username".equals(premiumMetadata.getAuthor()))
                && enterpriseMetadata.getAuthor() != null
                && !enterpriseMetadata.getAuthor().trim().isEmpty()) {
            premiumMetadata.setAuthor(enterpriseMetadata.getAuthor());
        }

        if ((premiumMetadata.getCreator() == null || "Stirling-PDF".equals(premiumMetadata.getCreator()))
                && enterpriseMetadata.getCreator() != null
                && !"Stirling-PDF".equals(enterpriseMetadata.getCreator())) {
            premiumMetadata.setCreator(enterpriseMetadata.getCreator());
        }

        if ((premiumMetadata.getProducer() == null || "Stirling-PDF".equals(premiumMetadata.getProducer()))
                && enterpriseMetadata.getProducer() != null
                && !"Stirling-PDF".equals(enterpriseMetadata.getProducer())) {
            premiumMetadata.setProducer(enterpriseMetadata.getProducer());
        }
    }
}

private void applyGhostscriptCompression(
        final OptimizePdfRequest request,
        final int optimizeLevel,
        final Path currentFile,
        final List<Path> tempFiles) throws IOException {

    final long preGsSize = Files.size(currentFile);
    log.info("Pre-Ghostscript file size: {}", GeneralUtils.formatBytes(preGsSize));

    final Path gsOutputFile = Files.createTempFile("gs_output_", ".pdf");
    tempFiles.add(gsOutputFile);

    final List<String> command = new ArrayList<>();
    command.add("gs");
    command.add("-sDEVICE=pdfwrite");
    command.add("-dCompatibilityLevel=1.5");
    command.add("-dNOPAUSE");
    command.add("-dQUIET");
    command.add("-dBATCH");

    switch (optimizeLevel) {
        case 1 -> command.add("-dPDFSETTINGS=/prepress");
        case 2 -> command.add("-dPDFSETTINGS=/printer");
        case 3 -> command.add("-dPDFSETTINGS=/ebook");
        case 4, 5 -> command.add("-dPDFSETTINGS=/screen");
        case 6, 7 -> {
            command.add("-dPDFSETTINGS=/screen");
            command.add("-dColorImageResolution=150");
            command.add("-dGrayImageResolution=150");
            command.add("-dMonoImageResolution=300");
        }
        case 8, 9 -> {
            command.add("-dPDFSETTINGS=/screen");
            command.add("-dColorImageResolution=100");
            command.add("-dGrayImageResolution=100");
            command.add("-dMonoImageResolution=200");
        }
        case 10 -> {
            command.add("-dPDFSETTINGS=/screen");
            command.add("-dColorImageResolution=72");
            command.add("-dGrayImageResolution=72");
            command.add("-dMonoImageResolution=150");
        }
        default -> command.add("-dPDFSETTINGS=/screen");
    }

    command.add("-sOutputFile=" + gsOutputFile.toString());
    command.add(currentFile.toString());

    ProcessExecutorResult returnCode = null;
    try {
        returnCode = ProcessExecutor.getInstance(ProcessExecutor.Processes.GHOSTSCRIPT)
                .runCommandWithOutputHandling(command);

        if (returnCode.getRc() == 0) {
            Files.copy(gsOutputFile, currentFile, StandardCopyOption.REPLACE_EXISTING);
            final long postGsSize = Files.size(currentFile);
            final double gsReduction = 100.0 - ((postGsSize * 100.0) / preGsSize);
            log.info("Post-Ghostscript file size: {} (reduced by {}%)",
                    GeneralUtils.formatBytes(postGsSize), String.format("%.1f", gsReduction));
        } else {
            log.warn("Ghostscript compression failed with return code: {}", returnCode.getRc());
            throw new IOException("Ghostscript compression failed");
        }
    } catch (final Exception e) {
        log.warn("Ghostscript compression failed, will fallback to other methods", e);
        throw new IOException("Ghostscript compression failed", e);
    }
}

private Object executeWithRetries(
        final ProceedingJoinPoint joinPoint,
        final Object[] args,
        final boolean async,
        final long timeout,
        final int maxRetries,
        final boolean trackProgress,
        final boolean queueable,
        final int resourceWeight) {

    final AtomicReference<String> jobIdRef = new AtomicReference<>();

    return jobExecutorService.runJobGeneric(
            async,
            () -> {
                Throwable lastException = null;

                for (int currentAttempt = 1; currentAttempt <= maxRetries; currentAttempt++) {
                    try {
                        if (trackProgress && async && jobIdRef.get() == null) {
                            jobIdRef.set(getJobIdFromContext());
                        }
                        final String jobId = jobIdRef.get();
                        if (trackProgress && async && jobId != null) {
                            log.debug("Tracking progress for job {} (attempt {}/{})", jobId, currentAttempt, maxRetries);
                        }

                        return joinPoint.proceed(args);
                    } catch (final Throwable ex) {
                        lastException = ex;
                        log.error("AutoJobAspect caught exception during job execution (attempt {}/{}): {}",
                                currentAttempt, maxRetries, ex.getMessage(), ex);

                        if (currentAttempt < maxRetries) {
                            log.info("Retrying operation, attempt {}/{}", currentAttempt + 1, maxRetries);

                            if (trackProgress && async && jobId != null) {
                                log.debug("Recording retry attempt for job {} in TaskManager", jobId);
                            }

                            final long delayMs = RETRY_BASE_DELAY.toMillis() * currentAttempt;
                            final CompletableFuture<Object> delayedRetry = new CompletableFuture<>();
                            CompletableFuture.delayedExecutor(delayMs, TimeUnit.MILLISECONDS)
                                    .execute(() -> delayedRetry.complete(null));

                            try {
                                delayedRetry.join();
                            } catch (final Exception e) {
                                Thread.currentThread().interrupt();
                                break;
                            }
                        } else {
                            break;
                        }
                    }
                }

                if (lastException != null) {
                    throw new RuntimeException("Job failed after " + maxRetries + " attempts: " + lastException.getMessage(), lastException);
                }
                throw new RuntimeException("Job failed but no exception was recorded");
            },
            timeout,
            queueable,
            resourceWeight);
}

