public List<PDDocument> splitPdfPages(
        final PDDocument document, final int horizontalDivisions, final int verticalDivisions)
        throws IOException {
    final List<PDDocument> splitDocuments = new ArrayList<>();

    for (final PDPage originalPage : document.getPages()) {
        final PDRectangle originalMediaBox = originalPage.getMediaBox();
        final float width = originalMediaBox.getWidth();
        final float height = originalMediaBox.getHeight();
        final float subPageWidth = width / horizontalDivisions;
        final float subPageHeight = height / verticalDivisions;

        final LayerUtility layerUtility = new LayerUtility(document);

        for (int i = 0; i < horizontalDivisions; i++) {
            for (int j = 0; j < verticalDivisions; j++) {
                final PDDocument subDoc = new PDDocument();
                final PDPage subPage = new PDPage(new PDRectangle(subPageWidth, subPageHeight));
                subDoc.addPage(subPage);

                final PDFormXObject form =
                        layerUtility.importPageAsForm(
                                document, document.getPages().indexOf(originalPage));

                try (final PDPageContentStream contentStream =
                        new PDPageContentStream(
                                subDoc, subPage, AppendMode.APPEND, true, true)) {
                    final float translateX = -subPageWidth * i;
                    final float translateY = -subPageHeight * (verticalDivisions - 1 - j);

                    contentStream.saveGraphicsState();
                    contentStream.addRect(0, 0, subPageWidth, subPageHeight);
                    contentStream.clip();
                    contentStream.transform(new Matrix(1, 0, 0, 1, translateX, translateY));
                    contentStream.drawForm(form);
                    contentStream.restoreGraphicsState();
                }

                splitDocuments.add(subDoc);
            }
        }
    }

    return splitDocuments;
}

public boolean isEndpointEnabled(final String endpoint) {
    final String original = endpoint;
    final String normalizedEndpoint = endpoint.startsWith("/")
            ? endpoint.substring(1)
            : endpoint;

    final Boolean explicitStatus = endpointStatuses.get(normalizedEndpoint);
    if (Boolean.FALSE.equals(explicitStatus)) {
        log.debug("isEndpointEnabled('{}') -> false (explicitly disabled)", original);
        return false;
    }

    for (final Map.Entry<String, Set<String>> groupEntry : endpointGroups.entrySet()) {
        final String group = groupEntry.getKey();
        if (disabledGroups.contains(group) && groupEntry.getValue().contains(normalizedEndpoint)) {
            if (!isToolGroup(group)) {
                log.debug(
                        "isEndpointEnabled('{}') -> false (functional group '{}' disabled)",
                        original, group);
                return false;
            }
        }
    }

    final Set<String> alternatives = endpointAlternatives.get(normalizedEndpoint);
    if (alternatives != null && !alternatives.isEmpty()) {
        final boolean hasEnabledToolGroup = alternatives.stream()
                .anyMatch(toolGroup -> !disabledGroups.contains(toolGroup));
        log.debug(
                "isEndpointEnabled('{}') -> {} (tool groups check)",
                original, hasEnabledToolGroup);
        return hasEnabledToolGroup;
    }

    for (final Map.Entry<String, Set<String>> groupEntry : endpointGroups.entrySet()) {
        final String group = groupEntry.getKey();
        if (isToolGroup(group)
                && disabledGroups.contains(group)
                && groupEntry.getValue().contains(normalizedEndpoint)) {
            log.debug(
                    "isEndpointEnabled('{}') -> false (single tool group '{}' disabled, no alternatives)",
                    original, group);
            return false;
        }
    }

    final boolean enabled = !Boolean.FALSE.equals(explicitStatus);
    log.debug("isEndpointEnabled('{}') -> {} (default)", original, enabled);
    return enabled;
}

private void setupLoadHandler() {
    final long initStartTime = System.currentTimeMillis();
    log.info("Setting up load handler at: {}", initStartTime);

    client.addLoadHandler(
            new CefLoadHandlerAdapter() {
                @Override
                public void onLoadingStateChange(
                        final CefBrowser browser,
                        final boolean isLoading,
                        final boolean canGoBack,
                        final boolean canGoForward) {
                    log.debug(
                            "Loading state change - isLoading: {}, canGoBack: {}, canGoForward: {}, browserInitialized: {}, Time elapsed: {}ms",
                            isLoading, canGoBack, canGoForward, browserInitialized, System.currentTimeMillis() - initStartTime);

                    if (!isLoading && !browserInitialized) {
                        browserInitialized = true;
                        SwingUtilities.invokeLater(
                                () -> {
                                    try {
                                        if (loadingWindow == null) return;

                                        log.info("Browser finished loading, preparing to initialize UI components");
                                        loadingWindow.setVisible(false);
                                        loadingWindow.dispose();
                                        loadingWindow = null;
                                        log.info("Loading window disposed");

                                        frame.setVisible(false);
                                        frame.dispose();
                                        frame.setOpacity(1.0f);
                                        frame.setUndecorated(false);
                                        frame.pack();
                                        frame.setSize(
                                                UIScaling.scaleWidth(1280),
                                                UIScaling.scaleHeight(800));
                                        frame.setLocationRelativeTo(null);
                                        log.debug("Frame reconfigured");

                                        frame.setVisible(true);
                                        frame.requestFocus();
                                        frame.toFront();
                                        log.info("Main frame displayed and focused");

                                        final Timer focusTimer = new Timer(100, e -> {
                                            try {
                                                browser.getUIComponent().requestFocus();
                                                log.info("Browser component focused");
                                            } catch (final Exception ex) {
                                                log.error("Error focusing browser", ex);
                                            }
                                        });
                                        focusTimer.setRepeats(false);
                                        focusTimer.start();
                                    } catch (final Exception e) {
                                        log.error("Error during UI initialization", e);
                                        if (loadingWindow != null) {
                                            loadingWindow.dispose();
                                            loadingWindow = null;
                                        }
                                        if (frame != null) {
                                            frame.setVisible(true);
                                            frame.requestFocus();
                                        }
                                    }
                                });
                    }
                }
            });
}

private void processWithTesseract(
        final List<String> selectedLanguages, final String ocrType, final Path tempInputFile, final Path tempOutputFile)
        throws IOException, InterruptedException {

    try (final TempDirectory tempDir = new TempDirectory(tempFileManager)) {
        final File tempOutputDir = new File(tempDir.getPath().toFile(), "output");
        final File tempImagesDir = new File(tempDir.getPath().toFile(), "images");
        final File finalOutputFile = new File(tempDir.getPath().toFile(), "final_output.pdf");

        tempOutputDir.mkdirs();
        tempImagesDir.mkdirs();

        final PDFMergerUtility merger = new PDFMergerUtility();
        merger.setDestinationFileName(finalOutputFile.toString());

        try (final PDDocument document = pdfDocumentFactory.load(tempInputFile.toFile())) {
            final PDFRenderer pdfRenderer = new PDFRenderer(document);
            final int pageCount = document.getNumberOfPages();

            for (int pageNum = 0; pageNum < pageCount; pageNum++) {
                final PDPage page = document.getPage(pageNum);
                boolean hasText = false;

                try (final PDDocument tempDoc = new PDDocument()) {
                    tempDoc.addPage(page);
                    final PDFTextStripper stripper = new PDFTextStripper();
                    hasText = !stripper.getText(tempDoc).trim().isEmpty();
                }

                final boolean shouldOcr = switch (ocrType) {
                    case "skip-text" -> !hasText;
                    case "force-ocr" -> true;
                    default -> true;
                };

                final File pageOutputPath = new File(tempOutputDir, String.format("page_%d.pdf", pageNum));

                if (shouldOcr) {
                    final BufferedImage image = pdfRenderer.renderImageWithDPI(pageNum, 300);
                    final File imagePath = new File(tempImagesDir, String.format("page_%d.png", pageNum));
                    ImageIO.write(image, "png", imagePath);

                    final List<String> command = new ArrayList<>();
                    command.add("tesseract");
                    command.add(imagePath.toString());
                    command.add(new File(tempOutputDir, String.format("page_%d", pageNum)).toString());
                    command.add("-l");
                    command.add(String.join("+", selectedLanguages));
                    command.add("pdf");

                    final ProcessExecutorResult result =
                            ProcessExecutor.getInstance(ProcessExecutor.Processes.TESSERACT)
                                    .runCommandWithOutputHandling(command);

                    if (result.getRc() != 0) {
                        throw ExceptionUtils.createRuntimeException(
                                "error.commandFailed",
                                "{0} command failed with exit code: {1}",
                                null,
                                "Tesseract",
                                result.getRc());
                    }
                    merger.addSource(pageOutputPath);
                } else {
                    try (final PDDocument pageDoc = new PDDocument()) {
                        pageDoc.addPage(page);
                        pageDoc.save(pageOutputPath);
                        merger.addSource(pageOutputPath);
                    }
                }
            }
        }

        merger.mergeDocuments(null);
        Files.copy(
                finalOutputFile.toPath(),
                tempOutputFile,
                java.nio.file.StandardCopyOption.REPLACE_EXISTING);
    }
}

private boolean processJWTLicensePayload(final String payload, final LicenseContext context) {
    try {
        log.info("Processing license payload: {}", payload);
        final JSONObject licenseData = new JSONObject(payload);

        JSONObject licenseObj = licenseData.optJSONObject("license");
        if (licenseObj == null) {
            final String id = licenseData.optString("id", null);
            if (id != null) {
                licenseObj = licenseData;
            } else {
                log.error("License data not found in payload");
                return false;
            }
        }

        final String licenseId = licenseObj.optString("id", "unknown");
        log.info("Processing license with ID: {}", licenseId);

        context.isFloatingLicense = licenseObj.optBoolean("floating", false);
        context.maxMachines = licenseObj.optInt("maxMachines", 1);
        if (context.isFloatingLicense) {
            log.info("Detected floating license with max machines: {}", context.maxMachines);
        }

        final String expiryStr = licenseObj.optString("expiry", null);
        if (expiryStr != null && !"null".equals(expiryStr)) {
            final Instant expiry = Instant.parse(expiryStr);
            final Instant now = Instant.now();

            if (now.isAfter(expiry)) {
                log.error("License has expired on {}", expiryStr);
                return false;
            }
            log.info("License valid until {}", expiryStr);
        } else {
            log.info("License has no expiration date");
        }

        final JSONObject accountObj = licenseData.optJSONObject("account");
        if (accountObj != null) {
            final String accountId = accountObj.optString("id", "unknown");
            log.info("License belongs to account: {}", accountId);
            if (!ACCOUNT_ID.equals(accountId)) {
                log.warn("License account ID does not match expected account ID");
            }
        }

        final JSONObject policyObj = licenseData.optJSONObject("policy");
        if (policyObj != null) {
            final String policyId = policyObj.optString("id", "unknown");
            log.info("License uses policy: {}", policyId);

            final boolean policyFloating = policyObj.optBoolean("floating", false);
            final int policyMaxMachines = policyObj.optInt("maxMachines", 1);

            if (policyFloating) {
                context.isFloatingLicense = true;
                context.maxMachines = policyMaxMachines;
                log.info("Policy defines floating license with max machines: {}", context.maxMachines);
            }

            int users = policyObj.optInt("users", 1);
            context.isEnterpriseLicense = policyObj.optBoolean("isEnterprise", false);

            if (users > 0) {
                applicationProperties.getPremium().setMaxUsers(users);
                log.info("License allows for {} users", users);
            } else {
                final Object metadataObj = policyObj.opt("metadata");
                if (metadataObj instanceof JSONObject) {
                    final JSONObject metadata = (JSONObject) metadataObj;
                    users = metadata.optInt("users", 1);
                    applicationProperties.getPremium().setMaxUsers(users);
                    log.info("License allows for {} users (from metadata)", users);
                    context.isEnterpriseLicense = metadata.optBoolean("isEnterprise", false);
                } else {
                    applicationProperties.getPremium().setMaxUsers(1);
                    log.info("Using default of 1 user for license");
                }
            }
        }

        return true;
    } catch (final Exception e) {
        log.error("Error processing license payload: {}", e.getMessage(), e);
        return false;
    }
}

@PostMapping(value = "/add-page-numbers", consumes = "multipart/form-data")
@Operation(
        summary = "Add page numbers to a PDF document",
        description =
                "This operation takes an input PDF file and adds page numbers to it. Input:PDF"
                        + " Output:PDF Type:SISO")
public ResponseEntity<byte[]> addPageNumbers(@ModelAttribute final AddPageNumbersRequest request)
        throws IOException {

    final MultipartFile file = request.getFileInput();
    final String customMargin = request.getCustomMargin();
    final int position = request.getPosition();
    int pageNumber = request.getStartingNumber();
    String pagesToNumber = request.getPagesToNumber();
    String customText = request.getCustomText();
    final float fontSize = request.getFontSize();
    final String fontType = request.getFontType();

    try (final PDDocument document = pdfDocumentFactory.load(file)) {
        final float marginFactor = switch (customMargin.toLowerCase()) {
            case "small" -> 0.02f;
            case "large" -> 0.05f;
            case "x-large" -> 0.075f;
            case "medium" -> 0.035f;
            default -> 0.035f;
        };

        if (pagesToNumber == null || pagesToNumber.isEmpty()) pagesToNumber = "all";
        if (customText == null || customText.isEmpty()) customText = "{n}";

        final List<Integer> pagesToNumberList =
                GeneralUtils.parsePageList(pagesToNumber.split(","), document.getNumberOfPages());

        for (final int i : pagesToNumberList) {
            final PDPage page = document.getPage(i);
            final PDRectangle pageSize = page.getMediaBox();

            final String text = customText
                    .replace("{n}", String.valueOf(pageNumber))
                    .replace("{total}", String.valueOf(document.getNumberOfPages()))
                    .replace(
                            "{filename}",
                            Filenames.toSimpleFileName(file.getOriginalFilename())
                                    .replaceFirst("[.][^.]+$", ""));

            final PDType1Font currentFont = switch (fontType.toLowerCase()) {
                case "courier" -> new PDType1Font(Standard14Fonts.FontName.COURIER);
                case "times" -> new PDType1Font(Standard14Fonts.FontName.TIMES_ROMAN);
                default -> new PDType1Font(Standard14Fonts.FontName.HELVETICA);
            };

            float x, y;

            if (position == 5) {
                final float textWidth = currentFont.getStringWidth(text) / 1000 * fontSize;
                final float ascent = currentFont.getFontDescriptor().getAscent() / 1000 * fontSize;
                final float descent = currentFont.getFontDescriptor().getDescent() / 1000 * fontSize;
                final float centerX = pageSize.getLowerLeftX() + (pageSize.getWidth() / 2);
                final float centerY = pageSize.getLowerLeftY() + (pageSize.getHeight() / 2);
                x = centerX - (textWidth / 2);
                y = centerY - (ascent + descent) / 2;
            } else {
                final int xGroup = (position - 1) % 3;
                final int yGroup = 2 - (position - 1) / 3;

                x = switch (xGroup) {
                    case 0 -> pageSize.getLowerLeftX() + marginFactor * pageSize.getWidth();
                    case 1 -> pageSize.getLowerLeftX() + (pageSize.getWidth() / 2);
                    default -> pageSize.getUpperRightX() - marginFactor * pageSize.getWidth();
                };

                y = switch (yGroup) {
                    case 0 -> pageSize.getLowerLeftY() + marginFactor * pageSize.getHeight();
                    case 1 -> pageSize.getLowerLeftY() + (pageSize.getHeight() / 2);
                    default -> pageSize.getUpperRightY() - marginFactor * pageSize.getHeight();
                };
            }

            try (final PDPageContentStream contentStream =
                    new PDPageContentStream(
                            document, page, PDPageContentStream.AppendMode.APPEND, true, true)) {
                contentStream.beginText();
                contentStream.setFont(currentFont, fontSize);
                contentStream.newLineAtOffset(x, y);
                contentStream.showText(text);
                contentStream.endText();
            }
            pageNumber++;
        }

        final ByteArrayOutputStream baos = new ByteArrayOutputStream();
        document.save(baos);
        return WebResponseUtils.bytesToWebResponse(
                baos.toByteArray(),
                Filenames.toSimpleFileName(file.getOriginalFilename()).replaceFirst("[.][^.]+$", "")
                        + "_numbersAdded.pdf",
                MediaType.APPLICATION_PDF);
    }
}

public int calculateDynamicQueueCapacity(final int baseCapacity, final int minCapacity) {
    final ResourceMetrics metrics = latestMetrics.get();
    final ResourceStatus status = currentStatus.get();

    double capacityFactor = switch (status) {
        case OK -> 1.0;
        case WARNING -> 0.6;
        case CRITICAL -> 0.3;
    };

    if (metrics.memoryUsage > 0.8) {
        capacityFactor *= 0.5;
    }

    final int capacity = (int) Math.max(minCapacity, Math.ceil(baseCapacity * capacityFactor));
    log.debug(
            "Dynamic queue capacity: {} (base: {}, factor: {:.2f}, status: {})",
            capacity, baseCapacity, capacityFactor, status);
    return capacity;
}

public StreamCacheCreateFunction getStreamCacheFunction(final long contentSize) {
    final long maxMemory = Runtime.getRuntime().maxMemory();
    final long totalMemory = Runtime.getRuntime().totalMemory();
    final long freeMemory = Runtime.getRuntime().freeMemory();
    final long usedMemory = totalMemory - freeMemory;
    final double freeMemoryPercent = (double) (maxMemory - usedMemory) / maxMemory * 100;
    final long actualFreeMemory = maxMemory - usedMemory;

    log.debug(
            "Memory status - Free: {}MB ({}%), Used: {}MB, Max: {}MB",
            actualFreeMemory / (1024 * 1024),
            String.format("%.2f", freeMemoryPercent),
            usedMemory / (1024 * 1024),
            maxMemory / (1024 * 1024));

    if (freeMemoryPercent < MIN_FREE_MEMORY_PERCENTAGE
            || actualFreeMemory < MIN_FREE_MEMORY_BYTES) {
        log.debug("Low memory detected ({}%), forcing file-based cache", String.format("%.2f", freeMemoryPercent));
        return createScratchFileCacheFunction(MemoryUsageSetting.setupTempFileOnly());
    } else if (contentSize < SMALL_FILE_THRESHOLD) {
        log.debug("Using memory-only cache for small document ({}KB)", contentSize / 1024);
        return IOUtils.createMemoryOnlyStreamCache();
    } else if (contentSize < LARGE_FILE_THRESHOLD) {
        log.debug("Using mixed memory/file cache for medium document ({}MB)", contentSize / (1024 * 1024));
        return createScratchFileCacheFunction(MemoryUsageSetting.setupMixed(LARGE_FILE_USAGE));
    } else {
        log.debug("Using file-based cache for large document");
        return createScratchFileCacheFunction(MemoryUsageSetting.setupTempFileOnly());
    }
}

@Override
public void onStopTrackingTouch(SeekBar seekBar) {
    if (DEBUG) {
        Log.d(TAG, "onStopTrackingTouch() called with: seekBar = [" + seekBar + "]");
    }

    player.seekTo(seekBar.getProgress());
    if (player.getExoPlayer().getDuration() == seekBar.getProgress()) {
        player.getExoPlayer().play();
    }

    binding.playbackCurrentTime.setText(getTimeString(seekBar.getProgress()));
    animate(binding.currentDisplaySeek, false, 200, AnimationType.SCALE_AND_ALPHA);
    animate(binding.currentSeekbarPreviewThumbnail, false, 200, AnimationType.SCALE_AND_ALPHA);

    if (player.getCurrentState() == STATE_PAUSED_SEEK) {
        player.changeState(STATE_BUFFERING);
    }
    if (!player.isProgressLoopRunning()) {
        player.startProgressLoop();
    }

    showControlsThenHide();
}

@Override
public boolean onMenuItemClick(@NonNull MenuItem menuItem) {
    if (DEBUG) {
        Log.d(TAG, "onMenuItemClick() called with: "
                + "menuItem = [" + menuItem + "], "
                + "menuItem.getItemId = [" + menuItem.getItemId() + "]");
    }

    if (menuItem.getGroupId() == POPUP_MENU_ID_QUALITY) {
        onQualityItemClick(menuItem);
        return true;
    } else if (menuItem.getGroupId() == POPUP_MENU_ID_AUDIO_TRACK) {
