private void startMission(final Intent intent) {
    final String[] urls = intent.getStringArrayExtra(EXTRA_URLS);
    final Uri path = IntentCompat.getParcelableExtra(intent, EXTRA_PATH, Uri.class);
    final Uri parentPath = IntentCompat.getParcelableExtra(intent, EXTRA_PARENT_PATH, Uri.class);
    final int threads = intent.getIntExtra(EXTRA_THREADS, 1);
    final char kind = intent.getCharExtra(EXTRA_KIND, '?');
    final String psName = intent.getStringExtra(EXTRA_POSTPROCESSING_NAME);
    final String[] psArgs = intent.getStringArrayExtra(EXTRA_POSTPROCESSING_ARGS);
    final String source = intent.getStringExtra(EXTRA_SOURCE);
    final long nearLength = intent.getLongExtra(EXTRA_NEAR_LENGTH, 0);
    final String tag = intent.getStringExtra(EXTRA_STORAGE_TAG);
    final var recovery = IntentCompat.getParcelableArrayListExtra(intent, EXTRA_RECOVERY_INFO, MissionRecoveryInfo.class);
    Objects.requireNonNull(recovery);

    final StoredFileHelper storage;
    try {
        storage = new StoredFileHelper(this, parentPath, path, tag);
    } catch (IOException e) {
        throw new RuntimeException(e); // Should never happen
    }

    final Postprocessing ps = (psName != null) 
        ? Postprocessing.getAlgorithm(psName, psArgs) 
        : null;

    final DownloadMission mission = new DownloadMission(urls, storage, kind, ps);
    mission.threadCount = threads;
    mission.source = source;
    mission.nearLength = nearLength;
    mission.recoveryInfo = recovery.toArray(new MissionRecoveryInfo[0]);

    if (ps != null)
        ps.setTemporalDir(DownloadManager.pickAvailableTemporalDir(this));

    handleConnectivityState(true); // Check actual network status

    mManager.startMission(mission);
}

public void pause() {
    if (!running) return;

    if (isPsRunning()) {
        if (DEBUG) Log.w(TAG, "Pause during post-processing is not applicable");
        return;
    }

    running = false;
    notify(DownloadManagerService.MESSAGE_PAUSED);

    if (init != null && init.isAlive()) {
        init.interrupt();
        synchronized (LOCK) {
            resetState(false, true, ERROR_NOTHING);
        }
        return;
    }

    if (DEBUG && unknownLength)
        Log.w(TAG, "Pausing a download that cannot be resumed (range requests not allowed)");

    init = null;
    pauseThreads();
}

void handleConnectivityState(final NetworkState currentStatus, final boolean updateOnly) {
    if (currentStatus == mLastNetworkStatus) return;

    mLastNetworkStatus = currentStatus;
    if (currentStatus == NetworkState.Unavailable) return;
    if (!mSelfMissionsControl || updateOnly) return; // No touch without user interaction

    final boolean isMetered = mPrefMeteredDownloads && 
        mLastNetworkStatus == NetworkState.MeteredOperating;

    synchronized (this) {
        for (DownloadMission mission : mMissionsPending) {
            if (mission.isCorrupt() || mission.isPsRunning()) continue;

            if (mission.running && isMetered) {
                mission.pause();
            } else if (!mission.running && !isMetered && mission.enqueued) {
                mission.start();
                if (mPrefQueueLimit) break;
            }
        }
    }
}

@SuppressLint("DefaultLocale")
private void updateProgress(final ViewHolderItem h) {
    if (h == null || h.item == null || h.item.mission instanceof FinishedMission) 
        return;

    final DownloadMission mission = (DownloadMission) h.item.mission;
    final double done = mission.done;
    final long length = mission.getLength();
    final long now = System.currentTimeMillis();
    final boolean hasError = mission.errCode != ERROR_NOTHING;

    h.progress.setMarquee(mission.isRecovering() || 
        (!hasError && (!mission.isInitialized() || mission.unknownLength)));

    final double progress = mission.unknownLength 
        ? Double.NaN 
        : done / length;

    if (hasError) {
        h.progress.setProgress(isNotFinite(progress) ? 1d : progress);
        h.status.setText(R.string.msg_error);
    } else if (isNotFinite(progress)) {
        h.status.setText(UNDEFINED_PROGRESS);
    } else {
        h.status.setText(String.format("%.2f%%", progress * 100));
        h.progress.setProgress(progress);
    }

    @StringRes final int state;
    final String sizeStr = Utility.formatBytes(length).concat("  ");

    if (mission.isPsFailed() || mission.errCode == ERROR_POSTPROCESSING_HOLD) {
        h.size.setText(sizeStr);
        return;
    } else if (!mission.running) {
        state = mission.enqueued ? R.string.queued : R.string.paused;
    } else if (mission.isPsRunning()) {
        state = R.string.post_processing;
    } else if (mission.isRecovering()) {
        state = R.string.recovering;
    } else {
        state = 0;
    }

    if (state != 0) {
        h.size.setText(sizeStr.concat("(").concat(mContext.getString(state)).concat(")"));
        h.resetSpeedMeasure();
        return;
    }

    if (h.lastTimestamp < 0) {
        h.size.setText(sizeStr);
        h.lastTimestamp = now;
        h.lastDone = done;
        return;
    }

    final long deltaTime = now - h.lastTimestamp;
    double deltaDone = done - h.lastDone;

    if (h.lastDone > done) {
        h.lastDone = done;
        h.size.setText(sizeStr);
        return;
    }

    if (deltaDone > 0 && deltaTime > 0) {
        float speed = (float) ((deltaDone * 1000d) / deltaTime);
        float averageSpeed = speed;

        if (h.lastSpeedIdx < 0) {
            Arrays.fill(h.lastSpeed, speed);
            h.lastSpeedIdx = 0;
        } else {
            for (int i = 0; i < h.lastSpeed.length; i++) {
                averageSpeed += h.lastSpeed[i];
            }
            averageSpeed /= h.lastSpeed.length + 1.0f;
        }

        final String speedStr = Utility.formatSpeed(averageSpeed);
        final String etaStr = mission.unknownLength 
            ? "" 
            : Utility.formatBytes((long) done) + "/" + 
              Utility.stringifySeconds((long) Math.ceil((length - done) / averageSpeed)) + "  ";

        h.size.setText(sizeStr.concat(etaStr).concat(speedStr));
        h.lastTimestamp = now;
        h.lastDone = done;
        h.lastSpeed[h.lastSpeedIdx++] = speed;

        if (h.lastSpeedIdx >= h.lastSpeed.length) 
            h.lastSpeedIdx = 0;
    }
}

public InputStream getView(final int size) {
    if (view == null) {
        view = new InputStream() {
            @Override
            public int read() throws IOException {
                if (viewSize < 1) return -1;
                final int res = DataReader.this.read();
                if (res > 0) viewSize--;
                return res;
            }

            @Override
            public int read(final byte[] buffer) throws IOException {
                return read(buffer, 0, buffer.length);
            }

            @Override
            public int read(final byte[] buffer, final int offset, final int count) throws IOException {
                if (viewSize < 1) return -1;
                final int res = DataReader.this.read(buffer, offset, Math.min(viewSize, count));
                viewSize -= res;
                return res;
            }

            @Override
            public long skip(final long amount) throws IOException {
                if (viewSize < 1) return 0;
                final int res = (int) DataReader.this.skipBytes(Math.min(amount, viewSize));
                viewSize -= res;
                return res;
            }

            @Override
            public int available() {
                return viewSize;
            }

            @Override
            public void close() {
                viewSize = 0;
            }

            @Override
            public boolean markSupported() {
                return false;
            }
        };
    }
    viewSize = size;
    return view;
}

public int read(final byte[] buffer, final int off, final int c) throws IOException {
    int offset = off;
    int count = c;
    if (readCount < 0) return -1;

    int total = 0;
    if (count >= readBuffer.length) {
        if (readCount > 0) {
            System.arraycopy(readBuffer, readOffset, buffer, offset, readCount);
            offset += readCount;
            count -= readCount;
            total = readCount;
            readCount = 0;
        }
        total += Math.max(stream.read(buffer, offset, count), 0);
    } else {
        while (count > 0 && !fillBuffer()) {
            final int read = Math.min(readCount, count);
            System.arraycopy(readBuffer, readOffset, buffer, offset, read);
            readOffset += read;
            readCount -= read;
            offset += read;
            count -= read;
            total += read;
        }
    }
    position += total;
    return total;
}

public void parse() throws IOException, NoSuchElementException {
    if (selectedTrack > -1) return;

    box = readBox(ATOM_FTYP);
    brands = parseFtyp(box);
    switch (brands[0]) {
        case BRAND_DASH:
        case BRAND_ISO5: break;
        default:
            throw new NoSuchElementException(
                "Not a MPEG-4 DASH container, major brand is not 'dash' or 'iso5' is " + boxName(brands[0])
            );
    }

    Moov moov = null;
    int i;

    while (box.type != ATOM_MOOF) {
        ensure(box);
        box = readBox();

        switch (box.type) {
            case ATOM_MOOV: moov = parseMoov(box); break;
            case ATOM_SIDX:
            case ATOM_MFRA: break;
        }
    }

    if (moov == null) 
        throw new IOException("The provided Mp4 doesn't have the 'moov' box");

    tracks = new Mp4Track[moov.trak.length];
    for (i = 0; i < tracks.length; i++) {
        tracks[i] = new Mp4Track();
        tracks[i].trak = moov.trak[i];

        if (moov.mvexTrex != null) {
            for (final Trex mvexTrex : moov.mvexTrex) {
                if (tracks[i].trak.tkhd.trackId == mvexTrex.trackId) {
                    tracks[i].trex = mvexTrex;
                }
            }
        }

        switch (moov.trak[i].mdia.hdlr.subType) {
            case HANDLER_VIDE: tracks[i].kind = TrackKind.Video; break;
            case HANDLER_SOUN: tracks[i].kind = TrackKind.Audio; break;
            case HANDLER_SUBT: tracks[i].kind = TrackKind.Subtitles; break;
            default: tracks[i].kind = TrackKind.Other; break;
        }
    }
    backupBox = box;
}

private void loadPendingMissions(final Context ctx) {
    final File[] subs = mPendingMissionsDir.listFiles();
    if (subs == null) {
        Log.e(TAG, "listFiles() returned null");
        return;
    }
    if (subs.length < 1) return;
    if (DEBUG) 
        Log.d(TAG, "Loading pending downloads from: " + mPendingMissionsDir.getAbsolutePath());

    final File tempDir = pickAvailableTemporalDir(ctx);
    Log.i(TAG, "Using '" + tempDir + "' as temporal directory");

    for (File sub : subs) {
        if (!sub.isFile() || sub.getName().equals(".tmp")) continue;

        final DownloadMission mis = Utility.readFromFile(sub);
        if (mis == null || mis.isFinished() || mis.hasInvalidStorage()) {
            sub.delete();
            continue;
        }

        mis.threads = new Thread[0];
        boolean exists;
        try {
            mis.storage = StoredFileHelper.deserialize(mis.storage, ctx);
            exists = !mis.storage.isInvalid() && mis.storage.existsAsFile();
        } catch (Exception ex) {
            Log.e(TAG, "Failed to load file source: " + mis.storage, ex);
            mis.storage.invalidate();
            exists = false;
        }

        if (mis.isPsRunning()) {
            if (mis.psAlgorithm.worksOnSameFile && exists && mis.storage.isDirect()) {
                if (!mis.storage.delete())
                    Log.w(TAG, "Unable to delete incomplete download: " + sub);
            }
            mis.psState = 0;
            mis.errCode = DownloadMission.ERROR_POSTPROCESSING_STOPPED;
        } else if (!exists) {
            tryRecover(mis);
            if (mis.isInitialized())
                mis.resetState(true, true, DownloadMission.ERROR_PROGRESS_LOST);
        }

        if (mis.psAlgorithm != null) {
            mis.psAlgorithm.cleanupTemporalDir();
            mis.psAlgorithm.setTemporalDir(tempDir);
        }

        mis.metadata = sub;
        mis.maxRetry = mPrefMaxRetry;
        mis.mHandler = mHandler;
        mMissionsPending.add(mis);
    }

    if (mMissionsPending.size() > 1)
        Collections.sort(mMissionsPending, Comparator.comparingLong(Mission::getTimestamp));
}

void startMission(final DownloadMission mission) {
    synchronized (this) {
        mission.timestamp = System.currentTimeMillis();
        mission.mHandler = mHandler;
        mission.maxRetry = mPrefMaxRetry;

        while (true) {
            mission.metadata = new File(mPendingMissionsDir, String.valueOf(mission.timestamp));
            if (!mission.metadata.isFile() && !mission.metadata.exists()) {
                try {
                    if (!mission.metadata.createNewFile())
                        throw new RuntimeException("Can't create metadata file");
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
                break;
            }
            mission.timestamp = System.currentTimeMillis();
        }

        mSelfMissionsControl = true;
        mMissionsPending.add(mission);
        Utility.writeToFile(mission.metadata, mission);

        if (mission.storage == null) {
            mission.errCode = DownloadMission.ERROR_FILE_CREATION;
            if (mission.errObject != null)
                mission.errObject = new IOException("DownloadMission.storage == NULL");
            return;
        }

        final boolean start = !mPrefQueueLimit || getRunningMissionsCount() < 1;
        if (canDownloadInCurrentNetwork() && start) {
            mission.start();
        }
    }
}

private static void processPartAdvanced(
        Object part, EmailContent content, EmlToPdfRequest request) {
    try {
        if (!isValidJakartaMailPart(part)) {
            log.warn("Invalid Jakarta Mail part type: {}", part.getClass().getName());
            return;
        }

        Class<?> partClass = part.getClass();
        Method isMimeType = partClass.getMethod("isMimeType", String.class);
        Method getContent = partClass.getMethod("getContent");
        Method getDisposition = partClass.getMethod("getDisposition");
        Method getFileName = partClass.getMethod("getFileName");
        Method getContentType = partClass.getMethod("getContentType");
        Method getHeader = partClass.getMethod("getHeader", String.class);

        Object disposition = getDisposition.invoke(part);
        String filename = (String) getFileName.invoke(part);
        String contentType = (String) getContentType.invoke(part);

        if ((Boolean) isMimeType.invoke(part, "text/plain") 
                && disposition == null) {
            content.setTextBody((String) getContent.invoke(part));
        } else if ((Boolean) isMimeType.invoke(part, "text/html") 
                && disposition == null) {
            content.setHtmlBody((String) getContent.invoke(part));
        } else if ("attachment".equalsIgnoreCase((String) disposition)
                || (filename != null && !filename.trim().isEmpty())) {

            content.setAttachmentCount(content.getAttachmentCount() + 1);

            if (filename != null && !filename.trim().isEmpty()) {
                EmailAttachment attachment = new EmailAttachment();
                attachment.setFilename(safeMimeDecode(filename));
                attachment.setContentType(contentType);

                String[] contentIdHeaders = (String[]) getHeader.invoke(part, "Content-ID");
                if (contentIdHeaders != null && contentIdHeaders.length > 0) {
                    attachment.setEmbedded(true);
                    String contentId = contentIdHeaders[0];
                    if (contentId.startsWith("<") && contentId.endsWith(">")) {
                        contentId = contentId.substring(1, contentId.length() - 1);
                    }
                    attachment.setContentId(contentId);
                }

                if ((request != null && request.isIncludeAttachments())
                        || attachment.isEmbedded()) {
                    try {
                        Object attachmentContent = getContent.invoke(part);
                        byte[] attachmentData = null;

                        if (attachmentContent instanceof java.io.InputStream inputStream) {
                            try {
                                attachmentData = inputStream.readAllBytes();
                            } catch (IOException e) {
                                log.warn(
                                        "Failed to read InputStream attachment: {}",
                                        e.getMessage());
                            }
                        } else if (attachmentContent instanceof byte[] byteArray) {
                            attachmentData = byteArray;
                        } else if (attachmentContent instanceof String stringContent) {
                            attachmentData = stringContent.getBytes(StandardCharsets.UTF_8);
                        }

                        if (attachmentData != null) {
                            long maxSizeMB =
                                    request != null ? request.getMaxAttachmentSizeMB() : 10L;
                            long maxSizeBytes = maxSizeMB * 1024 * 1024;

                            if (attachmentData.length <= maxSizeBytes) {
                                attachment.setData(attachmentData);
                                attachment.setSizeBytes(attachmentData.length);
                            } else {
                                if (attachment.isEmbedded()) {
                                    attachment.setData(attachmentData);
                                    attachment.setSizeBytes(attachmentData.length);
                                } else {
                                    attachment.setSizeBytes(attachmentData.length);
                                }
                            }
                        }
                    } catch (Exception e) {
                        log.warn("Error extracting attachment data: {}", e.getMessage());
                    }
                }

                content.getAttachments().add(attachment);
            }
        } else if ((Boolean) isMimeType.invoke(part, "multipart/*")) {
            try {
                Object multipartContent = getContent.invoke(part);
                Class<?> multipartClass = Class.forName("jakarta.mail.Multipart");
                if (multipartClass.isInstance(multipartContent)) {
                    processMultipartAdvanced(multipartContent, content, request);
                }
            } catch (Exception e) {
                log.warn("Error processing multipart content: {}", e.getMessage());
            }
        }

    } catch (Exception e) {
        log.warn("Error processing multipart part: {}", e.getMessage());
    }
}

private static void addAttachmentToInfo(
        StringBuilder attachmentInfo, String filename, String contentType, String encoding) {
    attachmentInfo
            .append("<div class=\"attachment-item\">")
            .append("<span class=\"attachment-icon\">")
            .append(MimeConstants.ATTACHMENT_MARKER)
            .append("</span> ")
            .append("<span class=\"attachment-name\">")
            .append(escapeHtml(filename))
            .append("</span>");

    if (!contentType.isEmpty() || !encoding.isEmpty()) {
        attachmentInfo.append(" <span class=\"attachment-details\">(");
        if (!contentType.isEmpty()) {
            attachmentInfo.append(escapeHtml(contentType));
        }
        if (!encoding.isEmpty()) {
            if (!contentType.isEmpty()) {
                attachmentInfo.append(", ");
            }
            attachmentInfo.append("encoding: ").append(escapeHtml(encoding));
        }
        attachmentInfo.append(")</span>");
    }
    attachmentInfo.append("</div>\n");
}

@PostMapping(value = "/scanner-effect", consumes = "multipart/form-data")
@Operation(
        summary = "Apply scanner effect to PDF",
        description =
                "Applies various effects to simulate a scanned document, including rotation, noise, and edge softening. Input:PDF Output:PDF Type:SISO")
public ResponseEntity<byte[]> scannerEffect(@Valid @ModelAttribute ScannerEffectRequest request)
        throws IOException {
    MultipartFile file = request.getFileInput();

    if (!request.isAdvancedEnabled()) {
        switch (request.getQuality()) {
            case high -> request.applyHighQualityPreset();
            case medium -> request.applyMediumQualityPreset();
            case low -> request.applyLowQualityPreset();
        }
    }

    int baseRotation = request.getRotationValue() + request.getRotate();
    int rotateVariance = request.getRotateVariance();
    int borderPx = request.getBorder();
    float brightness = request.getBrightness();
    float contrast = request.getContrast();
    float blur = request.getBlur();
    float noise = request.getNoise();
    boolean yellowish = request.isYellowish();
    int resolution = request.getResolution();
    ScannerEffectRequest.Colorspace colorspace = request.getColorspace();

    try (PDDocument document = pdfDocumentFactory.load(file)) {
        PDDocument outputDocument = new PDDocument();
        PDFRenderer pdfRenderer = new PDFRenderer(document);

        for (int i = 0; i < document.getNumberOfPages(); i++) {
            PDRectangle pageSize = document.getPage(i).getMediaBox();
            float pageWidthPts = pageSize.getWidth();
            float pageHeightPts = pageSize.getHeight();

            int projectedWidth = (int) Math.ceil(pageWidthPts * resolution / 72.0);
            int projectedHeight = (int) Math.ceil(pageHeightPts * resolution / 72.0);
            long projectedPixels = (long) projectedWidth * projectedHeight;

            int safeResolution = resolution;
            if (projectedWidth > MAX_IMAGE_WIDTH
                    || projectedHeight > MAX_IMAGE_HEIGHT
                    || projectedPixels > MAX_IMAGE_PIXELS) {
                double widthScale = (double) MAX_IMAGE_WIDTH / projectedWidth;
                double heightScale = (double) MAX_IMAGE_HEIGHT / projectedHeight;
                double pixelScale = Math.sqrt((double) MAX_IMAGE_PIXELS / projectedPixels);
                double minScale = Math.min(
                        Math.min(widthScale, heightScale), 
                        pixelScale);
                safeResolution = (int) Math.max(72, resolution * minScale);

                log.warn(
                        "Page {} would be too large at {}dpi ({}x{} pixels). Reducing to {}dpi",
                        i + 1,
                        resolution,
                        projectedWidth,
                        projectedHeight,
                        safeResolution);
            }

            BufferedImage image = pdfRenderer.renderImageWithDPI(i, safeResolution);

            log.debug(
                    "Processing page {} with dimensions {}x{} ({} pixels) at {}dpi",
                    i + 1,
                    image.getWidth(),
                    image.getHeight(),
                    (long) image.getWidth() * image.getHeight(),
                    safeResolution);

            BufferedImage processed;
            if (colorspace == ScannerEffectRequest.Colorspace.grayscale) {
                processed = new BufferedImage(
                        image.getWidth(),
                        image.getHeight(),
                        BufferedImage.TYPE_INT_RGB);
                Graphics2D gGray = processed.createGraphics();
                gGray.setColor(Color.BLACK);
                gGray.fillRect(0, 0, image.getWidth(), image.getHeight());
                gGray.drawImage(image, 0, 0, null);
                gGray.dispose();

                for (int y = 0; y < processed.getHeight(); y++) {
                    for (int x = 0; x < processed.getWidth(); x++) {
                        int rgb = processed.getRGB(x, y);
                        int r = (rgb >> 16) & 0xFF;
                        int g = (rgb >> 8) & 0xFF;
                        int b = rgb & 0xFF;
                        int gray = (r + g + b) / 3;
                        int grayRGB = (gray << 16) | (gray << 8) | gray;
                        processed.setRGB(x, y, grayRGB);
                    }
                }
            } else {
                processed = new BufferedImage(
                        image.getWidth(),
                        image.getHeight(),
                        BufferedImage.TYPE_INT_RGB);
                Graphics2D gCol = processed.createGraphics();
                gCol.drawImage(image, 0, 0, null);
                gCol.dispose();
            }

            int baseW = processed.getWidth() + 2 * borderPx;
            int baseH = processed.getHeight() + 2 * borderPx;
            boolean vertical = RANDOM.nextBoolean();
            float startGrey = 0.6f + 0.3f * RANDOM.nextFloat();
            float endGrey = 0.6f + 0.3f * RANDOM.nextFloat();
            Color startColor = new Color(
                    Math.round(startGrey * 255),
                    Math.round(startGrey * 255),
                    Math.round(startGrey * 255));
            Color endColor = new Color(
                    Math.round(endGrey * 255),
                    Math.round(endGrey * 255),
                    Math.round(endGrey * 255));
            BufferedImage composed = new BufferedImage(baseW, baseH, processed.getType());
            Graphics2D gBg = composed.createGraphics();
            for (int y = 0; y < baseH; y++) {
                for (int x = 0; x < baseW; x++) {
                    float frac = vertical ? (float) y / (baseH - 1) : (float) x / (baseW - 1);
                    int r = Math.round(
                            startColor.getRed()
                                    + (endColor.getRed() - startColor.getRed()) * frac);
                    int g = Math.round(
                            startColor.getGreen()
                                    + (endColor.getGreen() - startColor.getGreen()) * frac);
                    int b = Math.round(
                            startColor.getBlue()
                                    + (endColor.getBlue() - startColor.getBlue()) * frac);
                    composed.setRGB(x, y, new Color(r, g, b).getRGB());
                }
            }
            gBg.drawImage(processed, borderPx, borderPx, null);
            gBg.dispose();

            double pageRotation = baseRotation;
            if (baseRotation != 0 || rotateVariance != 0) {
                pageRotation += (RANDOM.nextDouble() * 2 - 1) * rotateVariance;
            }

            BufferedImage rotated;
            int w = composed.getWidth();
            int h = composed.getHeight();
            int rotW = w;
            int rotH = h;

            if (pageRotation == 0) {
                rotated = composed;
            } else {
                double radians = Math.toRadians(pageRotation);
                double sin = Math.abs(Math.sin(radians));
                double cos = Math.abs(Math.cos(radians));
                rotW = (int) Math.floor(w * cos + h * sin);
                rotH = (int) Math.floor(h * cos + w * sin);
                BufferedImage rotatedBg = new BufferedImage(rotW, rotH, composed.getType());
                Graphics2D gBgRot = rotatedBg.createGraphics();
                for (int y = 0; y < rotH; y++) {
                    for (int x = 0; x < rotW; x++) {
                        float frac = vertical ? (float) y / (rotH - 1) : (float) x / (rotW - 1);
                        int r = Math.round(
                                startColor.getRed()
                                        + (endColor.getRed() - startColor.getRed()) * frac);
                        int g = Math.round(
                                startColor.getGreen()
                                        + (endColor.getGreen() - startColor.getGreen()) * frac);
                        int b = Math.round(
                                startColor.getBlue()
                                        + (endColor.getBlue() - startColor.getBlue()) * frac);
                        rotatedBg.setRGB(x, y, new Color(r, g, b).getRGB());
                    }
                }
                gBgRot.dispose();
                rotated = new BufferedImage(rotW, rotH, composed.getType());
                Graphics2D g2d = rotated.createGraphics();
                g2d.drawImage(rotatedBg, 0, 0, null);
                AffineTransform at = new AffineTransform();
                at.translate((rotW - w) / 2.0, (rotH - h) / 2.0);
                at.rotate(radians, w / 2.0, h / 2.0);
                g2d.setRenderingHint(
                        RenderingHints.KEY_INTERPOLATION,
                        RenderingHints.VALUE_INTERPOLATION_BICUBIC);
                g2d.setRenderingHint(
                        RenderingHints.KEY_RENDERING, 
                        RenderingHints.VALUE_RENDER_QUALITY);
                g2d.setRenderingHint(
                        RenderingHints.KEY_ANTIALIASING, 
                        RenderingHints.VALUE_ANTIALIAS_ON);
                g2d.drawImage(composed, at, null);
                g2d.dispose();
            }

            PDRectangle origPageSize = document.getPage(i).getMediaBox();
            float origW = origPageSize.getWidth();
            float origH = origPageSize.getHeight();
            float scale = Math.max(origW / rotW, origH / rotH);
            float drawW = rotW * scale;
            float drawH = rotH * scale;
            float offsetX = (origW - drawW) / 2f;
            float offsetY = (origH - drawH) / 2f;

            BufferedImage softened = softenEdges(
                    rotated,
                    Math.max(10, Math.round(Math.min(rotW, rotH) * 0.02f)),
                    startColor,
                    endColor,
                    vertical);
            BufferedImage blurred = applyGaussianBlur(softened, blur);

            BufferedImage adjusted = adjustBrightnessContrast(blurred, brightness, contrast);

            if (yellowish) {
                applyYellowishEffect(adjusted);
            }
            addGaussianNoise(adjusted, noise);

            PDPage newPage = new PDPage(new PDRectangle(origW, origH));
            outputDocument.addPage(newPage);
            try (PDPageContentStream contentStream =
                    new PDPageContentStream(outputDocument, newPage)) {
                PDImageXObject pdImage =
                        LosslessFactory.createFromImage(outputDocument, adjusted);
                contentStream.drawImage(pdImage, offsetX, offsetY, drawW, drawH);
            }
        }

        ByteArrayOutputStream outputStream = new ByteArrayOutputStream();
        outputDocument.save(outputStream);
        outputDocument.close();

        String outputFilename = Filenames.toSimpleFileName(file.getOriginalFilename())
                                .replaceFirst("[.][^.]+$", "")
                        + "_scanner_effect.pdf";

        return WebResponseUtils.bytesToWebResponse(
                outputStream.toByteArray(), outputFilename, MediaType.APPLICATION_PDF);
    }
}

@PostMapping(consumes = "multipart/form-data", value = "/eml/pdf")
@Operation(
        summary = "Convert EML to PDF",
        description =
                "This endpoint converts EML (email) files to PDF format with extensive"
                        + " customization options. Features include font settings, image constraints, display modes, attachment handling,"
                        + " and HTML debug output. Input: EML file, Output: PDF"
                        + " or HTML file. Type: SISO")
public ResponseEntity<byte[]> convertEmlToPdf(@ModelAttribute EmlToPdfRequest request) {

    MultipartFile inputFile = request.getFileInput();
    String originalFilename = inputFile.getOriginalFilename();

    if (inputFile.isEmpty()) {
        log.error("No file provided for EML to PDF conversion.");
        return ResponseEntity.badRequest()
                .body("No file provided".getBytes(StandardCharsets.UTF_8));
    }

    if (originalFilename == null || originalFilename.trim().isEmpty()) {
        log.error("Filename is null or empty.");
        return ResponseEntity.badRequest()
                .body("Please provide a valid filename".getBytes(StandardCharsets.UTF_8));
    }

    String lowerFilename = originalFilename.toLowerCase();
    if (!lowerFilename.endsWith(".eml")) {
        log.error("Invalid file type for EML to PDF: {}", originalFilename);
        return ResponseEntity.badRequest()
                .body("Please upload a valid EML file".getBytes(StandardCharsets.UTF_8));
    }

    String baseFilename = Filenames.toSimpleFileName(originalFilename);

    try {
        byte[] fileBytes = inputFile.getBytes();

        if (request.isDownloadHtml()) {
            try {
                String htmlContent = EmlToPdf.convertEmlToHtml(fileBytes, request);
                log.info("Successfully converted EML to HTML: {}", originalFilename);
                return WebResponseUtils.bytesToWebResponse(
                        htmlContent.getBytes(StandardCharsets.UTF_8),
                        baseFilename + ".html",
                        MediaType.TEXT_HTML);
            } catch (IOException | IllegalArgumentException e) {
                log.error("HTML conversion failed for {}", originalFilename, e);
                return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
                        .body(
                                ("HTML conversion failed: " + e.getMessage())
                                        .getBytes(StandardCharsets.UTF_8));
            }
        }

        try {
            byte[] pdfBytes = EmlToPdf.convertEmlToPdf(
                    runtimePathConfig.getWeasyPrintPath(),
                    request,
                    fileBytes,
                    originalFilename,
                    false,
                    pdfDocumentFactory,
                    tempFileManager);

            if (pdfBytes == null || pdfBytes.length == 0) {
                log.error("PDF conversion failed - empty output for {}", originalFilename);
                return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
                        .body(
                                "PDF conversion failed - empty output"
                                        .getBytes(StandardCharsets.UTF_8));
            }
            log.info("Successfully converted EML to PDF: {}", originalFilename);
            return WebResponseUtils.bytesToWebResponse(
                    pdfBytes, baseFilename + ".pdf", MediaType.APPLICATION_PDF);

        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            log.error("EML to PDF conversion was interrupted for {}", originalFilename, e);
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
                    .body("Conversion was interrupted".getBytes(StandardCharsets.UTF_8));
        } catch (IllegalArgumentException e) {
            String errorMessage = buildErrorMessage(e, originalFilename);
            log.error(
                    "EML to PDF conversion failed for {}: {}",
                    originalFilename,
                    errorMessage,
                    e);
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
                    .body(errorMessage.getBytes(StandardCharsets.UTF_8));
        } catch (RuntimeException e) {
            String errorMessage = buildErrorMessage(e, originalFilename);
            log.error(
                    "EML to PDF conversion failed for {}: {}",
                    originalFilename,
                    errorMessage,
                    e);
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
                    .body(errorMessage.getBytes(StandardCharsets.UTF_8));
        }

    } catch (IOException e) {
        log.error("File processing error for EML to PDF: {}", originalFilename, e);
        return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
                .body("File processing error".getBytes(StandardCharsets.UTF_8));
    }
}

private void distributedConfigureServers(int count) throws IOException {
    StringBuilder sbClient = new StringBuilder();
    StringBuilder sbServer = new StringBuilder();
    try {
        for (int i = 0; i < count; i++) {
            String[] r = QuorumPeerInstance.createServer(im, i);
            if (i > 0) {
                sbClient.append(',');
                sbServer.append(',');
            }
            sbClient.append(r[0]); // r[0] == "host:clientPort"
            sbServer.append(r[1]); // r[1] == "host:leaderPort:leaderElectionPort"
            sbServer.append(";" + (r[0].split(":"))[1]); // Appending ";clientPort"
        }
        serverHostPort = sbClient.toString();
        quorumHostPort = sbServer.toString();
    } catch (Exception e) {
        IOException ioe = new IOException(e.getMessage());
        ioe.setStackTrace(e.getStackTrace());
        throw ioe;
    }
}

