private void showError(DownloadMission mission, UserAction action, @StringRes int reason) {
    StringBuilder request = new StringBuilder(256);
    request.append(mission.source).append(" [");

    if (mission.recoveryInfo != null) {
        for (MissionRecoveryInfo recovery : mission.recoveryInfo) {
            request.append(' ').append(recovery.toString()).append(' ');
        }
    }
    request.append("]");

    String service = ErrorInfo.SERVICE_NONE;
    try {
        service = NewPipe.getServiceByUrl(mission.source).getServiceInfo().getName();
    } catch (Exception e) {
        Log.w("ServiceLookup", "Failed to find service for: " + mission.source, e);
    }

    ErrorUtil.createNotification(
        mContext,
        new ErrorInfo(
            ErrorInfo.Companion.throwableToStringList(mission.errObject),
            action,
            service,
            request.toString(),
            reason
        )
    );
}

public void updateMission(Mission mission) {
    try {
        ContentValues values = getValuesOfMission(Objects.requireNonNull(mission));
        SQLiteDatabase database = getWritableDatabase();
        String ts = String.valueOf(mission.timestamp);

        int rowsAffected;

        if (mission instanceof FinishedMission) {
            if (mission.storage.isInvalid()) {
                rowsAffected = database.update(
                    FINISHED_TABLE_NAME, values, KEY_TIMESTAMP + " = ?", new String[]{ts}
                );
            } else {
                rowsAffected = database.update(
                    FINISHED_TABLE_NAME, values, KEY_PATH + " = ?", 
                    new String[]{mission.storage.getUri().toString()}
                );
            }
        } else {
            throw new UnsupportedOperationException("DownloadMission");
        }

        if (rowsAffected != 1) {
            Log.e("MissionUpdate", "Expected 1 affected row, got " + rowsAffected);
        }
    } catch (Exception e) {
        Log.e("MissionUpdate", "Failed to update mission", e);
    }
}

private int make(final int type, final int extra, final int columns, final int rows)
        throws IOException {
    try {
        final byte base = 16;
        final int size = columns * rows * 4;
        int total = size + base;
        int offset = auxOffset();

        if (extra >= 0) {
            total += 4;
        }

        auxWrite(ByteBuffer.allocate(12)
            .putInt(total)
            .putInt(type)
            .putInt(0x00) // version & flags
            .array()
        );

        if (extra >= 0) {
            offset += 4;
            auxWrite(extra);
        }

        auxWrite(rows);
        auxSkip(size);

        return offset + base;
    } catch (IOException e) {
        Log.e("BoxCreation", "Error creating box type: " + type, e);
        throw e;
    }
}

boolean runMissions() {
    synchronized (this) {
        if (mMissionsPending.isEmpty()) return false;
        if (!canDownloadInCurrentNetwork()) return false;

        if (mPrefQueueLimit) {
            for (DownloadMission mission : mMissionsPending) {
                if (!mission.isFinished() && mission.running) {
                    return true;
                }
            }
        }

        boolean flag = false;
        for (DownloadMission mission : mMissionsPending) {
            if (mission.running || !mission.enqueued || mission.isFinished()) {
                continue;
            }

            resumeMission(mission);
            if (mission.errCode != DownloadMission.ERROR_NOTHING) {
                continue;
            }

            if (mPrefQueueLimit) {
                return true;
            }
            flag = true;
        }

        return flag;
    }
}

public void start() {
    try {
        if (running || isFinished() || urls.length < 1) return;

        joinForThreads(10000); // Clean previous state

        running = true;
        errCode = ERROR_NOTHING;

        if (hasInvalidStorage()) {
            notifyError(ERROR_FILE_CREATION, null);
            return;
        }

        if (current >= urls.length) {
            notifyFinished();
            return;
        }

        notify(DownloadManagerService.MESSAGE_RUNNING);

        if (urls[current] == null) {
            doRecover(ERROR_RESOURCE_GONE);
            return;
        }

        if (blocks == null) {
            initializer();
            return;
        }

        initializeThreads();
    } catch (Exception e) {
        Log.e("MissionStart", "Failed to start mission", e);
        notifyError(e);
    }
}

private void initializeThreads() {
    init = null;
    finishCount = 0;
    blockAcquired = new boolean[blocks.length];

    if (blocks.length < 1) {
        threads = new Thread[]{
            runAsync(1, new DownloadRunnableFallback(this))
        };
    } else {
        int remainingBlocks = calculateRemainingBlocks();

        if (remainingBlocks < 1) {
            notifyFinished();
            return;
        }

        threads = new Thread[Math.min(threadCount, remainingBlocks)];
        for (int i = 0; i < threads.length; i++) {
            threads[i] = runAsync(i + 1, new DownloadRunnable(this, i));
        }
    }
}

private int calculateRemainingBlocks() {
    int remaining = 0;
    for (int block : blocks) {
        if (block >= 0) remaining++;
    }
    return remaining;
}

@Override
protected void subscribeActual(CompletableObserver observer) {
    InnerCompletableCache inner = new InnerCompletableCache(observer);
    observer.onSubscribe(inner);

    if (add(inner)) {
        if (inner.isDisposed()) {
            remove(inner);
        }

        if (once.compareAndSet(false, true)) {
            source.subscribe(this);
        }
    } else {
        Throwable e = error;
        if (e != null) {
            observer.onError(e);
        } else {
            observer.onComplete();
        }
    }
}

@Override
public boolean tryOnError(Throwable t) {
    if (t == null) {
        t = ExceptionHelper.createNullPointerException("onError called with a null Throwable.");
    }
    if (get() != DisposableHelper.DISPOSED) {
        Disposable d = getAndSet(DisposableHelper.DISPOSED);
        if (d != DisposableHelper.DISPOSED) {
            try {
                downstream.onError(t);
            } finally {
                if (d != null) {
                    d.dispose();
                }
            }
            return true;
        }
    }
    return false;
}

@SuppressWarnings("unchecked")
public static <T, R> boolean tryScalarXMapSubscribe(ObservableSource<T> source,
        Observer<? super R> observer,
        Function<? super T, ? extends ObservableSource<? extends R>> mapper) {
    if (source instanceof Supplier) {
        T t;

        try {
            t = ((Supplier<T>) source).get();
        } catch (Throwable e) {
            Exceptions.throwIfFatal(e);
            EmptyDisposable.error(e, observer);
            return true;
        }

        if (t == null) {
            EmptyDisposable.complete(observer);
            return true;
        }

        ObservableSource<? extends R> r;

        try {
            r = Objects.requireNonNull(mapper.apply(t), "The mapper returned a null ObservableSource");
        } catch (Throwable e) {
            Exceptions.throwIfFatal(e);
            EmptyDisposable.error(e, observer);
            return true;
        }

        if (r instanceof Supplier) {
            R u;

            try {
                u = ((Supplier<R>) r).get();
            } catch (Throwable e) {
                Exceptions.throwIfFatal(e);
                EmptyDisposable.error(e, observer);
                return true;
            }

            if (u == null) {
                EmptyDisposable.complete(observer);
                return true;
            }
            ScalarDisposable<R> sd = new ScalarDisposable<>(observer, u);
            observer.onSubscribe(sd);
            sd.run();
        } else {
            r.subscribe(observer);
        }

        return true;
    }
    return false;
}

void drainLoop() {
    int missed = 1;
    Observer<? super R> a = downstream;
    AtomicInteger n = active;
    AtomicReference<SpscLinkedArrayQueue<R>> qr = queue;

    for (;;) {
        for (;;) {
            if (cancelled) {
                clear();
                return;
            }

            if (!delayErrors) {
                Throwable e = errors.get();
                if (e != null) {
                    clear();
                    errors.tryTerminateConsumer(a);
                    return;
                }
            }

            boolean d = n.get() == 0;
            SpscLinkedArrayQueue<R> q = qr.get();
            R v = q != null ? q.poll() : null;
            boolean empty = v == null;

            if (d && empty) {
                errors.tryTerminateConsumer(a);
                return;
            }

            if (empty) {
                break;
            }

            a.onNext(v);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final SimplePlainQueue<Object> queue = this.queue;
    final Subscriber<? super Flowable<T>> downstream = this.downstream;
    UnicastProcessor<T> window = this.window;

    for (;;) {
        if (upstreamCancelled) {
            queue.clear();
            window = null;
            this.window = null;
        } else {
            boolean isDone = done;
            Object o = queue.poll();
            boolean isEmpty = o == null;

            if (isDone && isEmpty) {
                Throwable e = error;
                if (e != null) {
                    if (window != null) {
                        window.onError(e);
                    }
                    downstream.onError(e);
                } else {
                    if (window != null) {
                        window.onComplete();
                    }
                    downstream.onComplete();
                }
                cleanupResources();
                upstreamCancelled = true;
                continue;
            } else if (!isEmpty) {
                if (o instanceof WindowBoundaryRunnable) {
                    WindowBoundaryRunnable boundary = (WindowBoundaryRunnable) o;
                    if (boundary.index == emitted || !restartTimerOnMaxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    }
                } else if (window != null) {
                    @SuppressWarnings("unchecked")
                    T item = (T) o;
                    window.onNext(item);

                    long count = this.count + 1;
                    if (count == maxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    } else {
                        this.count = count;
                    }
                }
                continue;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    Subscriber<? super R> a = downstream;
    Iterator<? extends R> iterator = this.it;

    if (outputFused && iterator != null) {
        a.onNext(null);
        a.onComplete();
        return;
    }

    int missed = 1;

    for (;;) {
        if (iterator != null) {
            long r = requested.get();

            if (r == Long.MAX_VALUE) {
                fastPath(a, iterator);
                return;
            }

            long e = 0L;

            while (e != r) {
                if (cancelled) {
                    return;
                }

                R v;

                try {
                    v = Objects.requireNonNull(iterator.next(), "The iterator returned a null value");
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    a.onError(ex);
                    return;
                }

                a.onNext(v);

                if (cancelled) {
                    return;
                }

                e++;

                boolean b;

                try {
                    b = iterator.hasNext();
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    a.onError(ex);
                    return;
                }

                if (!b) {
                    a.onComplete();
                    return;
                }
            }

            if (e != 0L) {
                BackpressureHelper.produced(requested, e);
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }

        if (iterator == null) {
            iterator = it;
        }
    }
}

void drainLoop() {
    Subscriber<? super T> actual = this.downstream;
    int missed = 1;
    long e = emitted;
    int c = consumed;
    int lim = limit;

    for (;;) {
        long r = requested.get();

        while (e != r) {
            if (cancelled) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(downstream);
                return;
            }

            int os = otherState;
            if (os == OTHER_STATE_HAS_VALUE) {
                T v = singleItem;
                singleItem = null;
                otherState = OTHER_STATE_CONSUMED_OR_EMPTY;
                os = OTHER_STATE_CONSUMED_OR_EMPTY;
                actual.onNext(v);
                e++;
                continue;
            }

            boolean d = mainDone;
            SimplePlainQueue<T> q = queue;
            T v = q != null ? q.poll() : null;
            boolean empty = v == null;

            if (d && empty && os == OTHER_STATE_CONSUMED_OR_EMPTY) {
                queue = null;
                actual.onComplete();
                return;
            }

            if (empty) {
                break;
            }

            actual.onNext(v);
            e++;

            if (++c == lim) {
                c = 0;
                mainSubscription.get().request(lim);
            }
        }

        if (e == r) {
            if (cancelled) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(downstream);
                return;
            }

            boolean d = mainDone;
            SimplePlainQueue<T> q = queue;
            boolean empty = q == null || q.isEmpty();

            if (d && empty && otherState == 2) {
                queue = null;
                actual.onComplete();
                return;
            }
        }

        emitted = e;
        consumed = c;
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    Subscriber<? super R> downstream = this.downstream;
    ErrorMode errorMode = this.errorMode;
    SimpleQueue<T> queue = this.queue;
    AtomicThrowable errors = this.errors;
    AtomicLong requested = this.requested;
    int limit = prefetch - (prefetch >> 1);
    boolean syncFused = this.syncFused;

    for (;;) {
        for (;;) {
            if (cancelled) {
                queue.clear();
                item = null;
                break;
            }

            int s = state;

            if (errors.get() != null) {
                if (errorMode == ErrorMode.IMMEDIATE || 
                    (errorMode == ErrorMode.BOUNDARY && s == STATE_INACTIVE)) {
                    queue.clear();
                    item = null;
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            if (s == STATE_INACTIVE) {
                boolean d = done;
                T v;
                try {
                    v = queue.poll();
                } catch (Throwable e) {
                    Exceptions.throwIfFatal(e);
                    upstream.cancel();
                    errors.tryAddThrowableOrReport(e);
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
                boolean empty = v == null;

                if (d && empty) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }

                if (empty) {
                    break;
                }

                if (!syncFused) {
                    int c = consumed + 1;
                    if (c == limit) {
                        consumed = 0;
                        upstream.request(limit);
                    } else {
                        consumed = c;
                    }
                }

                MaybeSource<? extends R> ms;

                try {
                    ms = Objects.requireNonNull(mapper.apply(v), "The mapper returned a null MaybeSource");
                } catch (Throwable e) {
                    Exceptions.throwIfFatal(e);
                    upstream.cancel();
                    queue.clear();
                    errors.tryAddThrowableOrReport(e);
                    errors.tryTerminateConsumer(downstream);
                    return;
                }

                state = STATE_ACTIVE;
                ms.subscribe(inner);
                break;
            } else if (s == STATE_RESULT_VALUE) {
                long e = emitted;
                if (e != requested.get()) {
                    R w = item;
                    item = null;
                    downstream.onNext(w);
                    emitted = e + 1;
                    state = STATE_INACTIVE;
                } else {
                    break;
                }
            } else {
                break;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
void remove(@NonNull SingleDisposable<T> inner) {
    for (;;) {
        SingleDisposable<T>[] a = observers.get();
        int n = a.length;
        if (n == 0) {
            return;
        }

        int j = -1;

        for (int i = 0; i < n; i++) {
            if (a[i] == inner) {
                j = i;
                break;
            }
        }

        if (j < 0) {
            return;
        }
        SingleDisposable<T>[] b;
        if (n == 1) {
            b = EMPTY;
        } else {
            b = new SingleDisposable[n - 1];
            System.arraycopy(a, 0, b, 0, j);
            System.arraycopy(a, j + 1, b, j, n - j - 1);
        }

        if (observers.compareAndSet(a, b)) {
            return;
        }
    }
}

@Override
protected void subscribeActual(@NonNull Subscriber<? super T> s) {
    AsyncSubscription<T> as = new AsyncSubscription<>(s, this);
    s.onSubscribe(as);
    if (add(as)) {
        if (as.isCancelled()) {
            remove(as);
        }
    } else {
        Throwable e = error;
        if (e != null) {
            s.onError(e);
        } else {
            T v = value;
            if (v != null) {
                as.complete(v);
            } else {
                as.onComplete();
            }
        }
    }
}

private static EmailContent extractEmailContentAdvanced(byte[] emlBytes, EmlToPdfRequest request) {
    try {
        Class<?> sessionClass = Class.forName("jakarta.mail.Session");
        Class<?> mimeMessageClass = Class.forName("jakarta.mail.internet.MimeMessage");

        Method getDefaultInstance = sessionClass.getMethod("getDefaultInstance", Properties.class);
        Object session = getDefaultInstance.invoke(null, new Properties());

        Class<?>[] constructorArgs = new Class<?>[] {sessionClass, InputStream.class};
        Constructor<?> mimeMessageConstructor = mimeMessageClass.getConstructor(constructorArgs);
        Object message = mimeMessageConstructor.newInstance(session, new ByteArrayInputStream(emlBytes));

        return extractEmailContentAdvanced(message, request);
    } catch (ReflectiveOperationException e) {
        EmailContent content = new EmailContent();
        content.setHtmlBody(convertEmlToHtmlBasic(emlBytes, request));
        return content;
    }
}

private static void addAttachmentToInfo(StringBuilder attachmentInfo, String filename, String contentType, String encoding) {
    attachmentInfo
        .append("<div class=\"attachment-item\">")
        .append("<span class=\"attachment-icon\">")
        .append(MimeConstants.ATTACHMENT_MARKER)
        .append("</span> ")
        .append("<span class=\"attachment-name\">")
        .append(escapeHtml(filename))
        .append("</span>");

    if (!contentType.isEmpty() || !encoding.isEmpty()) {
        attachmentInfo.append(" <span class=\"attachment-details\">(");
        if (!contentType.isEmpty()) {
            attachmentInfo.append(escapeHtml(contentType));
        }
        if (!encoding.isEmpty()) {
            if (!contentType.isEmpty()) {
                attachmentInfo.append(", ");
            }
            attachmentInfo.append("encoding: ").append(escapeHtml(encoding));
        }
        attachmentInfo.append(")</span>");
    }
    attachmentInfo.append("</div>\n");
}

private static void processPartAdvanced(Object part, EmailContent content, EmlToPdfRequest request) {
    try {
        if (!isValidJakartaMailPart(part)) {
            log.warn("Invalid Jakarta Mail part type: {}", part.getClass().getName());
            return;
        }

        Class<?> partClass = part.getClass();
        Method isMimeType = partClass.getMethod("isMimeType", String.class);
        Method getContent = partClass.getMethod("getContent");
        Method getDisposition = partClass.getMethod("getDisposition");
        Method getFileName = partClass.getMethod("getFileName");
        Method getContentType = partClass.getMethod("getContentType");
        Method getHeader = partClass.getMethod("getHeader", String.class);

        Object disposition = getDisposition.invoke(part);
        String filename = (String) getFileName.invoke(part);
        String contentType = (String) getContentType.invoke(part);

        if ((Boolean) isMimeType.invoke(part, "text/plain") && disposition == null) {
            content.setTextBody((String) getContent.invoke(part));
        } else if ((Boolean) isMimeType.invoke(part, "text/html") && disposition == null) {
            content.setHtmlBody((String) getContent.invoke(part));
        } else if ("attachment".equalsIgnoreCase((String) disposition) || (filename != null && !filename.trim().isEmpty())) {
            content.setAttachmentCount(content.getAttachmentCount() + 1);

            if (filename != null && !filename.trim().isEmpty()) {
                EmailAttachment attachment = new EmailAttachment();
                attachment.setFilename(safeMimeDecode(filename));
                attachment.setContentType(contentType);

                String[] contentIdHeaders = (String[]) getHeader.invoke(part, "Content-ID");
                if (contentIdHeaders != null && contentIdHeaders.length > 0) {
                    attachment.setEmbedded(true);
                    String contentId = contentIdHeaders[0];
                    if (contentId.startsWith("<") && contentId.endsWith(">")) {
                        contentId = contentId.substring(1, contentId.length() - 1);
                    }
                    attachment.setContentId(contentId);
                }

                if ((request != null && request.isIncludeAttachments()) || attachment.isEmbedded()) {
                    try {
                        Object attachmentContent = getContent.invoke(part);
                        byte[] attachmentData = null;

                        if (attachmentContent instanceof java.io.InputStream inputStream) {
                            try {
                                attachmentData = inputStream.readAllBytes();
                            } catch (IOException e) {
                                log.warn("Failed to read InputStream attachment: {}", e.getMessage());
                            }
                        } else if (attachmentContent instanceof byte[] byteArray) {
                            attachmentData = byteArray;
                        } else if (attachmentContent instanceof String stringContent) {
                            attachmentData = stringContent.getBytes(StandardCharsets.UTF_8);
                        }

                        if (attachmentData != null) {
                            long maxSizeMB = request != null ? request.getMaxAttachmentSizeMB() : 10L;
                            long maxSizeBytes = maxSizeMB * 1024 * 1024;

                            if (attachmentData.length <= maxSizeBytes) {
                                attachment.setData(attachmentData);
                                attachment.setSizeBytes(attachmentData.length);
                            } else {
                                if (attachment.isEmbedded()) {
                                    attachment.setData(attachmentData);
                                    attachment.setSizeBytes(attachmentData.length);
                                } else {
                                    attachment.setSizeBytes(attachmentData.length);
                                }
                            }
                        }
                    } catch (Exception e) {
                        log.warn("Error extracting attachment data: {}", e.getMessage());
                    }
                }
                content.getAttachments().add(attachment);
            }
        } else if ((Boolean) isMimeType.invoke(part, "multipart/*")) {
            try {
                Object multipartContent = getContent.invoke(part);
                Class<?> multipartClass = Class.forName("jakarta.mail.Multipart");
                if (multipartClass.isInstance(multipartContent)) {
                    processMultipartAdvanced(multipartContent, content, request);
                }
            } catch (Exception e) {
                log.warn("Error processing multipart content: {}", e.getMessage());
            }
        }
    } catch (Exception e) {
        log.warn("Error processing multipart part: {}", e.getMessage());
    }
}

@PostMapping(consumes = "multipart/form-data", value = "/eml/pdf")
@Operation(
    summary = "Convert EML to PDF",
    description = "This endpoint converts EML (email) files to PDF format with extensive"
        + " customization options. Features include font settings, image constraints, display modes, attachment handling,"
        + " and HTML debug output. Input: EML file, Output: PDF"
        + " or HTML file. Type: SISO")
public ResponseEntity<byte[]> convertEmlToPdf(@ModelAttribute EmlToPdfRequest request) {
    MultipartFile inputFile = request.getFileInput();
    String originalFilename = inputFile.getOriginalFilename();

    if (inputFile.isEmpty()) {
        log.error("No file provided for EML to PDF conversion.");
        return ResponseEntity.badRequest().body("No file provided".getBytes(StandardCharsets.UTF_8));
    }
    if (originalFilename == null || originalFilename.trim().isEmpty()) {
        log.error("Filename is null or empty.");
        return ResponseEntity.badRequest().body("Please provide a valid filename".getBytes(StandardCharsets.UTF_8));
    }

    String lowerFilename = originalFilename.toLowerCase();
    if (!lowerFilename.endsWith(".eml")) {
        log.error("Invalid file type for EML to PDF: {}", originalFilename);
        return ResponseEntity.badRequest().body("Please upload a valid EML file".getBytes(StandardCharsets.UTF_8));
    }

    String baseFilename = Filenames.toSimpleFileName(originalFilename);

    try {
        byte[] fileBytes = inputFile.getBytes();

        if (request.isDownloadHtml()) {
            try {
                String htmlContent = EmlToPdf.convertEmlToHtml(fileBytes, request);
                log.info("Successfully converted EML to HTML: {}", originalFilename);
                return WebResponseUtils.bytesToWebResponse(
                    htmlContent.getBytes(StandardCharsets.UTF_8),
                    baseFilename + ".html",
                    MediaType.TEXT_HTML);
            } catch (IOException | IllegalArgumentException e) {
                log.error("HTML conversion failed for {}", originalFilename, e);
                return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
                    .body(("HTML conversion failed: " + e.getMessage()).getBytes(StandardCharsets.UTF_8));
            }
        }

        try {
            byte[] pdfBytes = EmlToPdf.convertEmlToPdf(
                runtimePathConfig.getWeasyPrintPath(),
                request,
                fileBytes,
                originalFilename,
                false,
                pdfDocumentFactory,
                tempFileManager);

            if (pdfBytes == null || pdfBytes.length == 0) {
                log.error("PDF conversion failed - empty output for {}", originalFilename);
                return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
                    .body("PDF conversion failed - empty output".getBytes(StandardCharsets.UTF_8));
            }
            log.info("Successfully converted EML to PDF: {}", originalFilename);
            return WebResponseUtils.bytesToWebResponse(pdfBytes, baseFilename + ".pdf", MediaType.APPLICATION_PDF);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            log.error("EML to PDF conversion was interrupted for {}", originalFilename, e);
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
                .body("Conversion was interrupted".getBytes(StandardCharsets.UTF_8));
        } catch (IllegalArgumentException e) {
            String errorMessage = buildErrorMessage(e, originalFilename);
            log.error("EML to PDF conversion failed for {}: {}", originalFilename, errorMessage, e);
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
                .body(errorMessage.getBytes(StandardCharsets.UTF_8));
        } catch (RuntimeException e) {
            String errorMessage = buildErrorMessage(e, originalFilename);
            log.error("EML to PDF conversion failed for {}: {}", originalFilename, errorMessage, e);
            return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
                .body(errorMessage.getBytes(StandardCharsets.UTF_8));
        }
    } catch (IOException e) {
        log.error("File processing error for EML to PDF: {}", originalFilename, e);
        return ResponseEntity.status(HttpStatus.INTERNAL_SERVER_ERROR)
            .body("File processing error".getBytes(StandardCharsets.UTF_8));
    }
}

@Override
protected void subscribeActual(Subscriber<? super T> subscriber) {
    PublishConnection<T> connection = null;

    // Attempt to establish or retrieve a valid connection
    for (;;) {
        connection = current.get();

        // Create new connection if current is null (disposed)
        if (connection == null) {
            PublishConnection<T> freshConnection = new PublishConnection<>(current, bufferSize);
            if (!current.compareAndSet(null, freshConnection)) {
                continue; // CAS failed, retry
            }
            connection = freshConnection;
        }
        break;
    }

    // Create inner subscription and link to downstream
    InnerSubscription<T> innerSubscription = new InnerSubscription<>(subscriber, connection);
    subscriber.onSubscribe(innerSubscription);

    // Handle subscription lifecycle
    if (connection.add(innerSubscription)) {
        if (innerSubscription.isCancelled()) {
            connection.remove(innerSubscription);
        } else {
            connection.drain(); // Start data flow
        }
        return;
    }

    // Handle terminal state
    Throwable error = connection.error;
    if (error != null) {
        innerSubscription.downstream.onError(error);
    } else {
        innerSubscription.downstream.onComplete();
    }
}

@Override
Node getHead() {
    final long timeThreshold = scheduler.now(unit) - maxAge;
    Node previous = get();
    Node nextNode = previous.get();

    // Traverse nodes until expiration condition met
    for (;;) {
        if (nextNode == null) {
            break;
        }
        Timed<?> timedValue = (Timed<?>) nextNode.value;
        boolean isTerminal = NotificationLite.isComplete(timedValue.value()) || 
                             NotificationLite.isError(timedValue.value());
        if (isTerminal) {
            break;
        }
        if (timedValue.time() <= timeThreshold) {
            previous = nextNode;
            nextNode = nextNode.get();
        } else {
            break;
        }
    }
    return previous;
}

void drain() {
    // Ensure single-threaded processing
    if (getAndIncrement() != 0) {
        return;
    }

    int missedOperations = 1;
    final Subscriber<? super R> downstreamSubscriber = this.downstream;
    final AtomicThrowable errorState = this.errors;
    final AtomicReference<SwitchMapMaybeObserver<R>> innerRef = this.inner;
    final AtomicLong requestCounter = this.requested;
    long emittedCount = this.emitted;

    // Main processing loop
    for (;;) {
        for (;;) {
            if (cancelled) {
                return;
            }

            // Handle pending errors
            if (errorState.get() != null) {
                if (!delayErrors) {
                    errorState.tryTerminateConsumer(downstreamSubscriber);
                    return;
                }
            }

            final boolean isDone = done;
            final SwitchMapMaybeObserver<R> currentInner = innerRef.get();
            final boolean isEmpty = (currentInner == null);

            // Terminal state checks
            if (isDone && isEmpty) {
                errorState.tryTerminateConsumer(downstreamSubscriber);
                return;
            }

            // Backpressure/state check
            if (isEmpty || currentInner.item == null || emittedCount == requestCounter.get()) {
                break;
            }

            // Process and reset inner state
            innerRef.compareAndSet(currentInner, null);
            downstreamSubscriber.onNext(currentInner.item);
            emittedCount++;
        }

        // Update state and check completion
        this.emitted = emittedCount;
        missedOperations = addAndGet(-missedOperations);
        if (missedOperations == 0) {
            break;
        }
    }
}

@Override
public void run() {
    for (;;) {
        // Handle disposal
        if (disposed) {
            queue.clear();
            return;
        }

        if (!active) {
            final boolean isDone = done;
            T itemValue;

            // Poll item safely
            try {
                itemValue = queue.poll();
            } catch (Throwable exception) {
                Exceptions.throwIfFatal(exception);
                dispose();
                queue.clear();
                downstream.onError(exception);
                worker.dispose();
                return;
            }

            final boolean isEmpty = (itemValue == null);

            // Terminal state handling
            if (isDone && isEmpty) {
                disposed = true;
                downstream.onComplete();
                worker.dispose();
                return;
            }

            if (!isEmpty) {
                ObservableSource<? extends U> observableSource;

                // Map item to observable
                try {
                    observableSource = Objects.requireNonNull(
                        mapper.apply(itemValue), 
                        "Mapper returned null ObservableSource"
                    );
                } catch (Throwable exception) {
                    Exceptions.throwIfFatal(exception);
                    dispose();
                    queue.clear();
                    downstream.onError(exception);
                    worker.dispose();
                    return;
                }

                // Activate and subscribe
                active = true;
                observableSource.subscribe(inner);
            }
        }

        // Concurrency control
        if (decrementAndGet() == 0) {
            break;
        }
    }
}

@Override
void drain() {
    // Single-entry guard
    if (getAndIncrement() != 0) {
        return;
    }

    int missedOperations = 1;
    final Observer<? super R> downstreamSubscriber = this.downstream;
    final ErrorMode errorHandlingMode = this.errorMode;
    final SimpleQueue<T> sourceQueue = this.queue;
    final AtomicThrowable errorState = this.errors;

    // Main processing loop
    for (;;) {
        for (;;) {
            // Handle disposal
            if (disposed) {
                sourceQueue.clear();
                item = null;
                break;
            }

            final int currentState = state;

            // Error propagation logic
            if (errorState.get() != null) {
                if (errorHandlingMode == ErrorMode.IMMEDIATE ||
                    (errorHandlingMode == ErrorMode.BOUNDARY && currentState == STATE_INACTIVE)) {
                    sourceQueue.clear();
                    item = null;
                    errorState.tryTerminateConsumer(downstreamSubscriber);
                    return;
                }
            }

            if (currentState == STATE_INACTIVE) {
                final boolean isDone = done;
                T value;

                // Safe polling
                try {
                    value = sourceQueue.poll();
                } catch (Throwable exception) {
                    Exceptions.throwIfFatal(exception);
                    disposed = true;
                    upstream.dispose();
                    errorState.tryAddThrowableOrReport(exception);
                    errorState.tryTerminateConsumer(downstreamSubscriber);
                    return;
                }

                final boolean isEmpty = (value == null);

                // Terminal state check
                if (isDone && isEmpty) {
                    errorState.tryTerminateConsumer(downstreamSubscriber);
                    return;
                }

                if (isEmpty) {
                    break;
                }

                MaybeSource<? extends R> maybeSource;

                // Map value to MaybeSource
                try {
                    maybeSource = Objects.requireNonNull(
                        mapper.apply(value), 
                        "Mapper returned null MaybeSource"
                    );
                } catch (Throwable exception) {
                    Exceptions.throwIfFatal(exception);
                    upstream.dispose();
                    sourceQueue.clear();
                    errorState.tryAddThrowableOrReport(exception);
                    errorState.tryTerminateConsumer(downstreamSubscriber);
                    return;
                }

                // Activate and subscribe
                state = STATE_ACTIVE;
                maybeSource.subscribe(inner);
                break;
            } 
            else if (currentState == STATE_RESULT_VALUE) {
                // Emit buffered result
                R resultValue = item;
                item = null;
                downstreamSubscriber.onNext(resultValue);
                state = STATE_INACTIVE;
            } 
            else {
                break;
            }
        }

        // Missed operation accounting
        missedOperations = addAndGet(-missedOperations);
        if (missedOperations == 0) {
            break;
        }
    }
}

@Override
public void run() {
    for (;;) {
        if (cancelled) {
            return;
        }

        if (!active) {
            final boolean isDone = done;

            // Pre-terminal error check
            if (isDone && !veryEnd) {
                Throwable error = errors.get();
                if (error != null) {
                    errors.tryTerminateConsumer(downstream);
                    worker.dispose();
                    return;
                }
            }

            T value;

            // Safe polling
            try {
                value = queue.poll();
            } catch (Throwable exception) {
                Exceptions.throwIfFatal(exception);
                upstream.cancel();
                errors.tryAddThrowableOrReport(exception);
                errors.tryTerminateConsumer(downstream);
                worker.dispose();
                return;
            }

            final boolean isEmpty = (value == null);

            // Terminal state handling
            if (isDone && isEmpty) {
                errors.tryTerminateConsumer(downstream);
                worker.dispose();
                return;
            }

            if (!isEmpty) {
                Publisher<? extends R> publisher;

                // Map value to publisher
                try {
                    publisher = Objects.requireNonNull(
                        mapper.apply(value), 
                        "Mapper returned null Publisher"
                    );
                } catch (Throwable exception) {
                    Exceptions.throwIfFatal(exception);
                    upstream.cancel();
                    errors.tryAddThrowableOrReport(exception);
                    errors.tryTerminateConsumer(downstream);
                    worker.dispose();
                    return;
                }

                // Backpressure management
                if (sourceMode != QueueSubscription.SYNC) {
                    int currentConsumed = consumed + 1;
                    if (currentConsumed == limit) {
                        consumed = 0;
                        upstream.request(limit);
                    } else {
                        consumed = currentConsumed;
                    }
                }

                // Handle immediate suppliers
                if (publisher instanceof Supplier) {
                    R supplierResult;
                    try {
                        supplierResult = ((Supplier<R>) publisher).get();
                    } catch (Throwable exception) {
                        Exceptions.throwIfFatal(exception);
                        errors.tryAddThrowableOrReport(exception);
                        if (!veryEnd) {
                            upstream.cancel();
                            errors.tryTerminateConsumer(downstream);
                            worker.dispose();
                            return;
                        }
                        supplierResult = null;
                    }

                    // Skip null results or cancellation
                    if (supplierResult == null || cancelled) {
                        continue;
                    }

                    // Immediate emission or buffering
                    if (inner.isUnbounded()) {
                        downstream.onNext(supplierResult);
                        continue;
                    } else {
                        active = true;
                        inner.setSubscription(new SimpleScalarSubscription<>(supplierResult, inner));
                    }
                } else {
                    // Regular publisher subscription
                    active = true;
                    publisher.subscribe(inner);
                }
            }
        }

        // Concurrency control
        if (decrementAndGet() == 0) {
            break;
        }
    }
}

void drain() {
    // Single-entry guard
    if (wip.getAndIncrement() != 0) {
        return;
    }

    int missedOperations = 1;
    SimpleQueue<T> sourceQueue = queue;
    int consumedCount = consumed;
    final int requestThreshold = limit;
    final boolean canRequest = (sourceMode != QueueSubscription.SYNC);
    final AtomicReference<MulticastSubscription<T>[]> subscribersRef = this.subscribers;

    MulticastSubscription<T>[] currentSubscribers = subscribersRef.get();

    // Main processing loop with labeled continue
    outer:
    for (;;) {
        int activeSubscriberCount = currentSubscribers.length;

        // Process if queue and subscribers exist
        if (sourceQueue != null && activeSubscriberCount != 0) {
            long minRequest = Long.MAX_VALUE;

            // Calculate minimum request across subscribers
            for (MulticastSubscription<T> subscriber : currentSubscribers) {
                long remaining = subscriber.get() - subscriber.emitted;
                if (remaining == Long.MIN_VALUE) {
                    activeSubscriberCount--;
                } else if (remaining < minRequest) {
                    minRequest = remaining;
                }
            }

            if (activeSubscriberCount == 0) {
                minRequest = 0;
            }

            // Process minRequest items
            while (minRequest != 0) {
                if (isDisposed()) {
                    sourceQueue.clear();
                    return;
                }

                final boolean isDone = done;
                if (isDone && !delayError) {
                    Throwable error = this.error;
                    if (error != null) {
                        errorAll(error);
                        return;
                    }
                }

                T value;
                try {
                    value = sourceQueue.poll();
                } catch (Throwable exception) {
                    Exceptions.throwIfFatal(exception);
                    SubscriptionHelper.cancel(upstream);
                    errorAll(exception);
                    return;
                }

                final boolean isEmpty = (value == null);

                // Terminal state handling
                if (isDone && isEmpty) {
                    Throwable error = this.error;
                    if (error != null) {
                        errorAll(error);
                    } else {
                        completeAll();
                    }
                    return;
                }

                if (isEmpty) {
                    break;
                }

                boolean subscriberChangeDetected = false;

                // Dispatch item to subscribers
                for (MulticastSubscription<T> subscriber : currentSubscribers) {
                    long subscriberRequest = subscriber.get();
                    if (subscriberRequest != Long.MIN_VALUE) {
                        if (subscriberRequest != Long.MAX_VALUE) {
                            subscriber.emitted++;
                        }
                        subscriber.downstream.onNext(value);
                    } else {
                        subscriberChangeDetected = true;
                    }
                }

                minRequest--;

                // Backpressure replenishment
                if (canRequest && ++consumedCount == requestThreshold) {
                    consumedCount = 0;
                    upstream.get().request(requestThreshold);
                }

                // Refresh subscriber list if changed
                MulticastSubscription<T>[] newSubscribers = subscribersRef.get();
                if (subscriberChangeDetected || newSubscribers != currentSubscribers) {
                    currentSubscribers = newSubscribers;
                    continue outer;
                }
            }

            // Terminal checks after processing
            if (minRequest == 0) {
                if (isDisposed()) {
                    sourceQueue.clear();
                    return;
                }

                final boolean isDone = done;
                if (isDone && !delayError) {
                    Throwable error = this.error;
                    if (error != null) {
                        errorAll(error);
                        return;
                    }
                }

                if (isDone && sourceQueue.isEmpty()) {
                    Throwable error = this.error;
                    if (error != null) {
                        errorAll(error);
                    } else {
                        completeAll();
                    }
                    return;
                }
            }
        }

        // Update state and check completion
        consumed = consumedCount;
        missedOperations = wip.addAndGet(-missedOperations);
        if (missedOperations == 0) {
            break;
        }
        if (sourceQueue == null) {
            sourceQueue = queue;
        }
        currentSubscribers = subscribersRef.get();
    }
}

void drain() {
    // Single-entry guard
    if (getAndIncrement() != 0) {
        return;
    }

    final Subscriber<? super R> downstreamSubscriber = downstream;
    final ZipSubscriber<T, R>[] subscriberArray = subscribers;
    final int subscriberCount = subscriberArray.length;
    Object[] currentValues = current;
    int missedOperations = 1;

    // Main processing loop
    for (;;) {
        long requestCount = requested.get();
        long emittedCount = 0L;

        // Emission loop
        while (requestCount != emittedCount) {
            if (cancelled) {
                return;
            }

            // Immediate error handling
            if (!delayErrors && errors.get() != null) {
                cancelAll();
                errors.tryTerminateConsumer(downstreamSubscriber);
                return;
            }

            boolean hasEmpty = false;

            // Gather values from each subscriber
            for (int index = 0; index < subscriberCount; index++) {
                if (currentValues[index] == null) {
                    ZipSubscriber<T, R> innerSubscriber = subscriberArray[index];
                    boolean isDone = innerSubscriber.done;
                    SimpleQueue<T> innerQueue = innerSubscriber.queue;
                    T value = null;

                    // Safe polling
                    try {
                        value = innerQueue != null ? innerQueue.poll() : null;
                    } catch (Throwable exception) {
                        Exceptions.throwIfFatal(exception);
                        errors.tryAddThrowableOrReport(exception);
                        if (!delayErrors) {
                            cancelAll();
                            errors.tryTerminateConsumer(downstreamSubscriber);
                            return;
                        }
                        isDone = true;
                    }

                    boolean isEmptyValue = (value == null);
                    if (isDone && isEmptyValue) {
                        cancelAll();
                        errors.tryTerminateConsumer(downstreamSubscriber);
                        return;
                    }
                    if (!isEmptyValue) {
                        currentValues[index] = value;
                    } else {
                        hasEmpty = true;
                    }
                }
            }

            if (hasEmpty) {
                break;
            }

            // Combine values
            R combinedValue;
            try {
                combinedValue = Objects.requireNonNull(
                    zipper.apply(currentValues.clone()), 
                    "Zipper returned null"
                );
            } catch (Throwable exception) {
                Exceptions.throwIfFatal(exception);
                cancelAll();
                errors.tryAddThrowableOrReport(exception);
                errors.tryTerminateConsumer(downstreamSubscriber);
                return;
            }

            // Emit and reset
            downstreamSubscriber.onNext(combinedValue);
            emittedCount++;
            Arrays.fill(currentValues, null);
        }

        // Terminal check after emission
        if (requestCount == emittedCount) {
            if (cancelled) {
                return;
            }

            // Final error check
            if (!delayErrors && errors.get() != null) {
                cancelAll();
                errors.tryTerminateConsumer(downstreamSubscriber);
                return;
            }

            // Refresh values for next cycle
            for (int index = 0; index < subscriberCount; index++) {
                if (currentValues[index] == null) {
                    ZipSubscriber<T, R> innerSubscriber = subscriberArray[index];
                    boolean isDone = innerSubscriber.done;
                    SimpleQueue<T> innerQueue = innerSubscriber.queue;
                    T value = null;

                    try {
                        value = innerQueue != null ? innerQueue.poll() : null;
                    } catch (Throwable exception) {
                        Exceptions.throwIfFatal(exception);
                        errors.tryAddThrowableOrReport(exception);
                        if (!delayErrors) {
                            cancelAll();
                            errors.tryTerminateConsumer(downstreamSubscriber);
                            return;
                        }
                        isDone = true;
                    }

                    boolean isEmptyValue = (value == null);
                    if (isDone && isEmptyValue) {
                        cancelAll();
                        errors.tryTerminateConsumer(downstreamSubscriber);
                        return;
                    }
                    if (!isEmptyValue) {
                        currentValues[index] = value;
                    }
                }
            }
        }

        // Request management
        if (emittedCount != 0L) {
            for (ZipSubscriber<T, R> innerSubscriber : subscriberArray) {
                innerSubscriber.request(emittedCount);
            }
            if (requestCount != Long.MAX_VALUE) {
                requested.addAndGet(-emittedCount);
            }
        }

        // Missed operation accounting
        missedOperations = addAndGet(-missedOperations);
        if (missedOperations == 0) {
            break;
        }
    }
}

void innerSuccess(InnerObserver innerObserver, R value) {
    // Remove completed observer
    set.delete(innerObserver);

    // Try fast-path emission
    if (get() == 0 && compareAndSet(0, 1)) {
        downstream.onNext(value);
        final boolean isLast = active.decrementAndGet() == 0;
        SpscLinkedArrayQueue<R> valueQueue = queue.get();

        // Terminal state check
        if (isLast && (valueQueue == null || valueQueue.isEmpty())) {
            errors.tryTerminateConsumer(downstream);
            return;
        }
        if (decrementAndGet() == 0) {
            return;
        }
    } 
    else {
        // Buffer value and schedule drain
        SpscLinkedArrayQueue<R> valueQueue = getOrCreateQueue();
        synchronized (valueQueue) {
            valueQueue.offer(value);
        }
        active.decrementAndGet();
        if (getAndIncrement() != 0) {
            return;
        }
    }
    drainLoop();
}

@Override
public void onSubscribe(Subscription subscription) {
    boolean shouldCancel = false;

    if (!done) {
        // Synchronized state management
        synchronized (this) {
            if (done) {
                shouldCancel = true;
            } else {
                if (emitting) {
                    // Buffer subscription if mid-emission
                    AppendOnlyLinkedArrayList<Object> buffer = queue;
                    if (buffer == null) {
                        buffer = new AppendOnlyLinkedArrayList<>(4);
                        queue = buffer;
                    }
                    buffer.add(NotificationLite.subscription(subscription));
                    return;
                }
                emitting = true;
                shouldCancel = false;
            }
        }
    } else {
        shouldCancel = true;
    }

    // Handle cancellation or propagation
    if (shouldCancel) {
        subscription.cancel();
    } else {
        actual.onSubscribe(subscription);
        emitLoop();
    }
}

@Override
public void close() throws IOException {
    boolean triedToClose = false;
    boolean success = false;
    try {
        flush();
        ((FileOutputStream) out).getFD().sync();

        triedToClose = true;
        super.close();
        success = true;
    } finally {
        if (success) {
            boolean renamed = tmpFile.renameTo(origFile);
            if (!renamed) {
                // On windows, renameTo does not replace
                if (!origFile.delete() || !tmpFile.renameTo(origFile)) {
                    throw new IOException("Could not rename temporary file " + tmpFile + " to " + origFile);
                }
            }
        } else {
            if (!triedToClose) {
                // If we failed when flushing, try to close it to not leak an FD
                IOUtils.closeStream(out);
            }
            // close wasn't successful, try to delete the tmp file
            if (!tmpFile.delete()) {
                LOG.warn("Unable to delete tmp file {}", tmpFile);
            }
        }
    }
}

void handleWrite(SelectionKey k) throws IOException {
    if (outgoingBuffers.isEmpty()) {
        return;
    }

    /*
     * This will reset buffer position to 0 and limit to buffer size, 
     * allowing us to fill it with data from non-direct buffers
     */
    ByteBuffer directBuffer = NIOServerCnxnFactory.getDirectBuffer();
    if (directBuffer == null) {
        ByteBuffer[] bufferList = new ByteBuffer[outgoingBuffers.size()];
        // Use gathered write call (updates buffer positions)
        sock.write(outgoingBuffers.toArray(bufferList));

        // Remove sent buffers
        ByteBuffer bb;
        while ((bb = outgoingBuffers.peek()) != null) {
            if (bb == ServerCnxnFactory.closeConn) {
                throw new CloseRequestException("close requested", DisconnectReason.CLIENT_CLOSED_CONNECTION);
            }
            if (bb == packetSentinel) {
                packetSent();
            }
            if (bb.remaining() > 0) {
                break;
            }
            outgoingBuffers.remove();
        }
    } else {
        directBuffer.clear();

        for (ByteBuffer b : outgoingBuffers) {
            if (directBuffer.remaining() < b.remaining()) {
                // Slice buffer if too big for directBuffer
                b = (ByteBuffer) b.slice().limit(directBuffer.remaining());
            }
            // Save/reset position after copy
            int p = b.position();
            directBuffer.put(b);
            b.position(p);
            if (directBuffer.remaining() == 0) {
                break;
            }
        }
        // Prepare for write: limit->position, position->0
        directBuffer.flip();

        int sent = sock.write(directBuffer);
        ByteBuffer bb;

        // Remove sent buffers
        while ((bb = outgoingBuffers.peek()) != null) {
            if (bb == ServerCnxnFactory.closeConn) {
                throw new CloseRequestException("close requested", DisconnectReason.CLIENT_CLOSED_CONNECTION);
            }
            if (bb == packetSentinel) {
                packetSent();
            }
            if (sent < bb.remaining()) {
                // Partially sent - update position
                bb.position(bb.position() + sent);
                break;
            }
            // Fully sent - remove
            sent -= bb.remaining();
            outgoingBuffers.remove();
        }
    }
}

public static void main(String[] args) throws IOException {
    MergedLogSource source = new MergedLogSource(args);

    PrintStream ps_ms = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-ms.out")));
    PrintStream ps_sec = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-sec.out")));
    PrintStream ps_min = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-min.out")));
    PrintStream ps_hour = new PrintStream(new BufferedOutputStream(new FileOutputStream("throughput-hour.out")));
    LogIterator iter;

    System.out.println(source);
    iter = source.iterator();
    long currentms = 0;
    long currentsec = 0;
    long currentmin = 0;
    long currenthour = 0;
    Set<Long> zxids_ms = new HashSet<Long>();
    long zxid_sec = 0;
    long zxid_min = 0;
    long zxid_hour = 0;

    while (iter.hasNext()) {
        LogEntry e = iter.next();
        TransactionEntry cxn = (TransactionEntry) e;

        long ms = cxn.getTimestamp();
        long sec = ms / MS_PER_SEC;
        long min = ms / MS_PER_MIN;
        long hour = ms / MS_PER_HOUR;

        if (currentms != ms && currentms != 0) {
            ps_ms.println("" + currentms + " " + zxids_ms.size());

            zxid_sec += zxids_ms.size();
            zxid_min += zxids_ms.size();
            zxid_hour += zxids_ms.size();
            zxids_ms.clear();
        }

        if (currentsec != sec && currentsec != 0) {
            ps_sec.println("" + currentsec * MS_PER_SEC + " " + zxid_sec);
            zxid_sec = 0;
        }

        if (currentmin != min && currentmin != 0) {
            ps_min.println("" + currentmin * MS_PER_MIN + " " + zxid_min);
            zxid_min = 0;
        }

        if (currenthour != hour && currenthour != 0) {
            ps_hour.println("" + currenthour * MS_PER_HOUR + " " + zxid_hour);
            zxid_hour = 0;
        }

        currentms = ms;
        currentsec = sec;
        currentmin = min;
        currenthour = hour;

        zxids_ms.add(cxn.getZxid());
    }

    iter.close();
    ps_ms.close();
    ps_sec.close();
    ps_min.close();
    ps_hour.close();
}

public void run() {
    try {
        while (!stopped) {
            try {
                select();
                processAcceptedConnections();
                processInterestOpsUpdateRequests();
            } catch (RuntimeException e) {
                LOG.warn("Ignoring unexpected runtime exception", e);
            } catch (Exception e) {
                LOG.warn("Ignoring unexpected exception", e);
            }
        }

        // Close pending connections
        for (SelectionKey key : selector.keys()) {
            NIOServerCnxn cnxn = (NIOServerCnxn) key.attachment();
            if (cnxn.isSelectable()) {
                cnxn.close(ServerCnxn.DisconnectReason.SERVER_SHUTDOWN);
            }
            cleanupSelectionKey(key);
        }
        SocketChannel accepted;
        while ((accepted = acceptedQueue.poll()) != null) {
            fastCloseSock(accepted);
        }
        updateQueue.clear();
    } finally {
        closeSelector();
        // Wake up accept thread and begin shutdown
        NIOServerCnxnFactory.this.stop();
        LOG.info("selector thread exited run method");
    }
}

public String getStatus(String name, long timeout) throws KeeperException, InterruptedException {
    Stat stat = new Stat();
    byte[] data = null;
    long endTime = Time.currentElapsedTime() + timeout;
    KeeperException lastException = null;
    for (int i = 0; i < maxTries && endTime > Time.currentElapsedTime(); i++) {
        try {
            data = zk.getData(reportsNode + '/' + name, false, stat);
            if (LOG.isDebugEnabled()) {
                LOG.debug("Got Data: " + ((data == null) ? "null" : new String(data)));
            }
            lastException = null;
            break;
        } catch (ConnectionLossException e) {
            lastException = e;
        } catch (NoNodeException e) {
            final Object eventObj = new Object();
            synchronized (eventObj) {
                // Wait for node to appear
                Stat eStat = zk.exists(reportsNode + '/' + name, new Watcher() {
                    public void process(WatchedEvent event) {
                        synchronized (eventObj) {
                            eventObj.notifyAll();
                        }
                    }
                });
                if (eStat == null) {
                    eventObj.wait(endTime - Time.currentElapsedTime());
                }
            }
            lastException = e;
        }
    }
    if (lastException != null) {
        throw lastException;
    }
    return new String(data);
}

