public static PDRectangle textToPageSize(String size) {
  return switch (size.toUpperCase()) {
    case "A0" -> PDRectangle.A0;
    case "A1" -> PDRectangle.A1;
    case "A2" -> PDRectangle.A2;
    case "A3" -> PDRectangle.A3;
    case "A4" -> PDRectangle.A4;
    case "A5" -> PDRectangle.A5;
    case "A6" -> PDRectangle.A6;
    case "LETTER" -> PDRectangle.LETTER;
    case "LEGAL" -> PDRectangle.LEGAL;
    default -> throw ExceptionUtils.createInvalidPageSizeException(size);
  };
}

private Object[] processArgsInPlace(Object[] originalArgs, boolean async) {
  if (originalArgs == null || originalArgs.length == 0) return originalArgs;

  for (int i = 0; i < originalArgs.length; i++) {
    if (!(originalArgs[i] instanceof PDFFile pdfFile)) continue;

    if (pdfFile.getFileInput() == null && pdfFile.getFileId() != null) {
      try {
        log.debug("Resolving file: {}", pdfFile.getFileId());
        pdfFile.setFileInput(fileStorage.retrieveFile(pdfFile.getFileId()));
      } catch (Exception e) {
        throw new RuntimeException("File resolution failed: " + pdfFile.getFileId(), e);
      }
    } else if (async && pdfFile.getFileInput() != null) {
      try {
        log.debug("Persisting file for async");
        String fileId = fileStorage.storeFile(pdfFile.getFileInput());
        pdfFile.setFileId(fileId);
        pdfFile.setFileInput(fileStorage.retrieveFile(fileId));
      } catch (IOException e) {
        throw new RuntimeException("File persistence failed", e);
      }
    }
  }
  return originalArgs;
}

private Map<String, Object> getDockerMetrics() {
