}

private void shutdownSchedulers() {
  log.info("Shutting down job queue");
  shuttingDown = true;

  jobMap.forEach((id, job) -> {
    if (!job.future.isDone()) {
      job.future.completeExceptionally(new RuntimeException("Shutdown cancellation"));
    }
  });

  try {
    scheduler.shutdown();
    if (!scheduler.awaitTermination(5, TimeUnit.SECONDS)) scheduler.shutdownNow();

    jobExecutor.shutdown();
    if (!jobExecutor.awaitTermination(5, TimeUnit.SECONDS)) jobExecutor.shutdownNow();
  } catch (InterruptedException e) {
    Thread.currentThread().interrupt();
    scheduler.shutdownNow();
    jobExecutor.shutdownNow();
  }

  log.info("Shutdown complete. Stats: total={}, rejected={}", totalQueuedJobs, rejectedJobs);
}

private Path createTempFile(String prefix) throws IOException {
  try {
    TempFileManager manager = ApplicationContextProvider.getBean(TempFileManager.class);
    if (manager != null) {
      File file = manager.createTempFile(".tmp");
      log.debug("TempFileManager file: {}", file);
      return file.toPath();
    }
  } catch (Exception e) {
    log.debug("TempFileManager unavailable");
  }

  Path file = Files.createTempFile(prefix + tempCounter.incrementAndGet() + "-", ".tmp");
  log.debug("Fallback temp file: {}", file);

  try {
    TempFileRegistry registry = ApplicationContextProvider.getBean(TempFileRegistry.class);
    if (registry != null) {
      registry.register(file);
      log.debug("Registered temp file: {}", file);
    }
  } catch (Exception e) {
    log.debug("Registry unavailable: {}", file);
  }

  return file;
}

