private HttpResponse readFileFromResource(FullHttpRequest request, String path) throws IOException {
  DefaultFullHttpResponse fullResp = null;

  try (InputStream in = HttpTtyConnection.class.getResourceAsStream("/com/taobao/arthas/core/http" + path)) {
    if (in != null) {
      fullResp = new DefaultFullHttpResponse(request.protocolVersion(), HttpResponseStatus.OK);
      byte[] buffer = new byte[256];

      for (int length; (length = in.read(buffer)) != -1; ) {
        fullResp.content().writeBytes(buffer, 0, length);
      }

      int dotIndex = path.lastIndexOf('.');
      if (dotIndex != -1 && dotIndex != path.length() - 1) {
        String ext = path.substring(dotIndex + 1);
        String contentType = null;

        if ("html".equals(ext)) contentType = "text/html";
        else if ("js".equals(ext)) contentType = "application/javascript";
        else if ("css".equals(ext)) contentType = "text/css";

        if (contentType != null) {
          fullResp.headers().set(HttpHeaderNames.CONTENT_TYPE, contentType);
        }
      }
    }
  }
  return fullResp;
}

@Override
public void draw(CommandProcess process, StackModel result) {
  StringBuilder sb = new StringBuilder()
    .append(ThreadUtil.getThreadTitle(result)).append("\n");

  StackTraceElement[] stackTraceElements = result.getStackTrace();
  StackTraceElement locationElement = stackTraceElements[0];

  sb.append(String.format("    @%s.%s()", locationElement.getClassName(), locationElement.getMethodName()))
    .append("\n");

  for (int i = 1; i < stackTraceElements.length; i++) {
    StackTraceElement ste = stackTraceElements[i];
    sb
      .append("        at ")
      .append(ste.getClassName()).append(".")
      .append(ste.getMethodName()).append("(")
      .append(ste.getFileName()).append(":")
      .append(ste.getLineNumber()).append(")\n");
  }
  process.write("ts=" + DateUtils.formatDateTime(result.getTs()) + ";" + sb + "\n");
}

private ApiResponse processExecRequest(ApiRequest apiRequest, Session session) {
  boolean oneTimeAccess = session.get(ONETIME_SESSION_KEY) != null;

  try {
    String commandLine = apiRequest.getCommand();
    Map<String, Object> body = new TreeMap<>();
    body.put("command", commandLine);

    ApiResponse response = new ApiResponse()
      .setSessionId(session.getSessionId())
      .setBody(body);

    if (!session.tryLock()) {
      return response
        .setState(ApiState.REFUSED)
        .setMessage("Another command is executing.");
    }

    int lock = session.getLock();
    PackingResultDistributor packingResultDistributor = null;
    Job job = null;

    try {
      Job foregroundJob = session.getForegroundJob();
      if (foregroundJob != null) {
        logger.info("Another job is running, jobId: {}", foregroundJob.id());
        return response
          .setState(ApiState.REFUSED)
          .setMessage("Another job is running.");
      }

      packingResultDistributor = new PackingResultDistributorImpl(session);
      job = createJob(commandLine, session, packingResultDistributor);

      session.setForegroundJob(job);
      updateSessionInputStatus(session, InputStatus.ALLOW_INTERRUPT);
      job.run();
    } catch (Throwable e) {
      logger.error("Exec command failed:" + e.getMessage() + ", command:" + commandLine, e);
      return response
        .setState(ApiState.FAILED)
        .setMessage("Exec command failed:" + e.getMessage());
    } finally {
      if (session.getLock() == lock) session.unLock();
    }

    Integer timeout = apiRequest.getExecTimeout();
    if (timeout == null || timeout <= 0) timeout = DEFAULT_EXEC_TIMEOUT;

    boolean timeExpired = !waitForJob(job, timeout);
    if (timeExpired) {
      logger.warn("Job is exceeded time limit, force interrupt it, jobId: {}", job.id());
      job.interrupt();
      response
        .setState(ApiState.INTERRUPTED)
        .setMessage("The job is exceeded time limit, force interrupt");
    } else {
      response.setState(ApiState.SUCCEEDED);
    }

    body
      .put("jobId", job.id())
      .put("jobStatus", job.status())
      .put("timeExpired", timeExpired);

    if (timeExpired) body.put("timeout", timeout);
    body.put("results", packingResultDistributor.getResults());

    return response
      .setSessionId(session.getSessionId())
      .setBody(body);
  } finally {
    if (oneTimeAccess) sessionManager.removeSession(session.getSessionId());
  }
}

public static String drawThreadInfo(List<ThreadVO> threads, int width, int height) {
  TableElement table = new TableElement(1, 6, 3, 2, 2, 2, 2, 2, 2, 2)
