    if (array == null) {
        return "null";
    }
    int length = array.length;
    if (length == 0) {
        return "{}";
    }

    StringBuilder sb = new StringBuilder("{");
    for (int i = 0; i < length; ++i) {
        if (i > 0) {
            sb.append(", ");
        }
        sb.append('\'').append(array[i]).append('\'');
    }
    sb.append('}');
    return sb.toString();
