    Uri parentPath = IntentCompat.getParcelableExtra(intent, EXTRA_PARENT_PATH, Uri.class);
    int threads = intent.getIntExtra(EXTRA_THREADS, 1);
    char kind = intent.getCharExtra(EXTRA_KIND, '?');
    String psName = intent.getStringExtra(EXTRA_POSTPROCESSING_NAME);
    String[] psArgs = intent.getStringArrayExtra(EXTRA_POSTPROCESSING_ARGS);
    String source = intent.getStringExtra(EXTRA_SOURCE);
    long nearLength = intent.getLongExtra(EXTRA_NEAR_LENGTH, 0);
    String tag = intent.getStringExtra(EXTRA_STORAGE_TAG);
    final var recovery = IntentCompat.getParcelableArrayListExtra(intent, EXTRA_RECOVERY_INFO, 
        MissionRecoveryInfo.class);
    Objects.requireNonNull(recovery);

    StoredFileHelper storage;
    try {
        storage = new StoredFileHelper(this, parentPath, path, tag);
    } catch (IOException e) {
        throw new RuntimeException(e);
    }

    Postprocessing ps = null;
    if (psName != null) {
        ps = Postprocessing.getAlgorithm(psName, psArgs);
    }

    final DownloadMission mission = new DownloadMission(urls, storage, kind, ps);
    mission.threadCount = threads;
    mission.source = source;
    mission.nearLength = nearLength;
    mission.recoveryInfo = recovery.toArray(new MissionRecoveryInfo[0]);

    if (ps != null) {
        ps.setTemporalDir(DownloadManager.pickAvailableTemporalDir(this));
    }

    handleConnectivityState(true);
    mManager.startMission(mission);
}

@Override
public void run() {
    if (mMission.current > 0) {
        mMission.resetState(false, true, DownloadMission.ERROR_NOTHING);
    }

    int retryCount = 0;
    int httpCode = 204;

    while (true) {
        try {
            if (mMission.blocks == null && mMission.current == 0) {
                long finalLength = 0;
                long lowestSize = Long.MAX_VALUE;

                for (int i = 0; i < mMission.urls.length && mMission.running; i++) {
                    mConn = mMission.openConnection(mMission.urls[i], true, 0, 0);
                    mMission.establishConnection(mId, mConn);
                    dispose();

                    if (Thread.interrupted()) return;
                    long length = Utility.getTotalContentLength(mConn);

                    if (i == 0) {
                        httpCode = mConn.getResponseCode();
                        mMission.length = length;
                    }

                    if (length > 0) finalLength += length;
                    if (length < lowestSize) lowestSize = length;
                }

                mMission.nearLength = finalLength;

                if (mMission.psAlgorithm != null && mMission.psAlgorithm.reserveSpace) {
                    if (lowestSize < 1) {
                        mMission.offsets[0] = RESERVE_SPACE_DEFAULT;
                    } else {
                        mMission.offsets[0] = lowestSize < RESERVE_SPACE_MAXIMUM ? 
                            lowestSize : RESERVE_SPACE_MAXIMUM;
                    }
                }
            } else {
                mConn = mMission.openConnection(true, 0, 0);
                mMission.establishConnection(mId, mConn);
                dispose();

                if (!mMission.running || Thread.interrupted()) return;

                httpCode = mConn.getResponseCode();
                mMission.length = Utility.getTotalContentLength(mConn);
            }

            if (mMission.length == 0 || httpCode == 204) {
                mMission.notifyError(DownloadMission.ERROR_HTTP_NO_CONTENT, null);
                return;
            }

            if (mMission.length == -1 && mConn.getResponseCode() == 200) {
                mMission.blocks = new int[0];
                mMission.length = 0;
                mMission.unknownLength = true;

                if (DEBUG) {
                    Log.d(TAG, "falling back (unknown length)");
                }
            } else {
                mConn = mMission.openConnection(true, mMission.length - 10, mMission.length);
                mMission.establishConnection(mId, mConn);
                dispose();

                if (!mMission.running || Thread.interrupted()) return;

                synchronized (mMission.LOCK) {
                    if (mConn.getResponseCode() == 206) {
                        if (mMission.threadCount > 1) {
                            int count = (int) (mMission.length / DownloadMission.BLOCK_SIZE);
                            if ((count * DownloadMission.BLOCK_SIZE) < mMission.length) {
                                count++;
                            }
                            mMission.blocks = new int[count];
                        } else {
                            mMission.blocks = new int[0];
                            mMission.unknownLength = false;
                        }
                    } else {
                        mMission.blocks = new int[0];
                        mMission.unknownLength = false;
                    }
                }

                if (!mMission.running || Thread.interrupted()) return;
            }

            try (SharpStream fs = mMission.storage.getStream()) {
                fs.setLength(mMission.offsets[mMission.current] + mMission.length);
                fs.seek(mMission.offsets[mMission.current]);
            }

            if (!mMission.running || Thread.interrupted()) return;

            if (!mMission.unknownLength && mMission.recoveryInfo != null) {
                String entityTag = mConn.getHeaderField("ETAG");
                String lastModified = mConn.getHeaderField("Last-Modified");
                MissionRecoveryInfo recovery = mMission.recoveryInfo[mMission.current];

                if (!TextUtils.isEmpty(entityTag)) {
                    recovery.setValidateCondition(entityTag);
                } else if (!TextUtils.isEmpty(lastModified)) {
                    recovery.setValidateCondition(lastModified);
                } else {
                    recovery.setValidateCondition(null);
                }
            }

            mMission.running = false;
            break;
        } catch (InterruptedIOException | ClosedByInterruptException e) {
            return;
        } catch (Exception e) {
            if (!mMission.running || super.isInterrupted()) return;

            if (e instanceof DownloadMission.HttpError && 
                ((DownloadMission.HttpError) e).statusCode == ERROR_HTTP_FORBIDDEN) {
                interrupt();
                mMission.doRecover(ERROR_HTTP_FORBIDDEN);
                return;
            }

            if (e instanceof IOException && e.getMessage().contains("Permission denied")) {
                mMission.notifyError(DownloadMission.ERROR_PERMISSION_DENIED, e);
                return;
            }

            if (retryCount++ > mMission.maxRetry) {
                Log.e(TAG, "initializer failed", e);
                mMission.notifyError(e);
                return;
            }
            Log.e(TAG, "initializer failed, retrying", e);
        }
    }
    mMission.start();
}

public void pause() {
    if (!running) return;

    if (isPsRunning()) {
        if (DEBUG) {
            Log.w(TAG, "pause during post-processing is not applicable.");
        }
        return;
    }

    running = false;
    notify(DownloadManagerService.MESSAGE_PAUSED);

    if (init != null && init.isAlive()) {
        init.interrupt();
        synchronized (LOCK) {
            resetState(false, true, ERROR_NOTHING);
        }
        return;
    }

    if (DEBUG && unknownLength) {
        Log.w(TAG, "pausing a download that cannot be resumed (range requests not allowed by the server).");
    }

    init = null;
    pauseThreads();
}

boolean runMissions() {
    synchronized (this) {
        if (mMissionsPending.size() < 1) return false;
        if (!canDownloadInCurrentNetwork()) return false;

        if (mPrefQueueLimit) {
            for (DownloadMission mission : mMissionsPending) {
                if (!mission.isFinished() && mission.running) return true;
            }
        }

        boolean flag = false;
        for (DownloadMission mission : mMissionsPending) {
            if (mission.running || !mission.enqueued || mission.isFinished()) {
                continue;
            }

            resumeMission(mission);
            if (mission.errCode != DownloadMission.ERROR_NOTHING) continue;

            if (mPrefQueueLimit) return true;
            flag = true;
        }
        return flag;
    }
}

private void notifyPostProcessing(int state) {
    String action;
    switch (state) {
        case 1:
            action = "Running";
            break;
        case 2:
            action = "Completed";
            break;
        default:
            action = "Failed";
    }

    Log.d(TAG, "%s postprocessing on %s", action, storage.getName());

    if (state == 2) {
        psState = state;
        return;
    }

    synchronized (LOCK) {
        psState = state;
        writeThisToFile();
    }
}

private static String decodeQRCode(BufferedImage bufferedImage) {
    LuminanceSource source;

    if (bufferedImage.getRaster().getDataBuffer() instanceof DataBufferByte dataBufferByte) {
        byte[] pixels = dataBufferByte.getData();
        source = new PlanarYUVLuminanceSource(
            pixels,
            bufferedImage.getWidth(),
            bufferedImage.getHeight(),
            0, 0,
            bufferedImage.getWidth(),
            bufferedImage.getHeight(),
            false
        );
    } else if (bufferedImage.getRaster().getDataBuffer() instanceof DataBufferInt dataBufferInt) {
        int[] pixels = dataBufferInt.getData();
        byte[] newPixels = new byte[pixels.length];
        for (int i = 0; i < pixels.length; i++) 
            newPixels[i] = (byte)(pixels[i] & 0xff);

        source = new PlanarYUVLuminanceSource(
            newPixels,
            bufferedImage.getWidth(),
            bufferedImage.getHeight(),
            0, 0,
            bufferedImage.getWidth(),
            bufferedImage.getHeight(),
            false
        );
    } else {
        throw new IllegalArgumentException(
            "BufferedImage must have 8-bit gray scale, 24-bit RGB, 32-bit ARGB (packed int), " +
            "byte gray, or 3-byte/4-byte RGB image data"
        );
    }

    BinaryBitmap bitmap = new BinaryBitmap(new HybridBinarizer(source));
    try {
        return new MultiFormatReader().decode(bitmap).getText();
    } catch (NotFoundException e) {
        return null;  // No QR code found
    }
}

private void executeJob(QueuedJob job) {
    if (job.cancelled) {
        log.debug("Job {} was cancelled, not executing", job.jobId);
        return;
    }

    jobExecutor.execute(() -> {
        log.debug("Executing queued job {} (queued at {})", job.jobId, job.queuedAt);
        try {
            Object result = executeWithTimeout(job.work, job.timeoutMs);
            job.future.complete(result instanceof ResponseEntity ? 
                (ResponseEntity<?>)result : ResponseEntity.ok(result));
        } catch (Exception e) {
            log.error("Error executing queued job {}: {}", job.jobId, e.getMessage(), e);
            job.future.completeExceptionally(e);
        }
    });
}

@DeleteMapping("/api/v1/general/job/{jobId}")
public ResponseEntity<?> cancelJob(@PathVariable("jobId") String jobId) {
    log.debug("Request to cancel job: {}", jobId);
    Object sessionJobIds = request.getSession().getAttribute("userJobIds");

    if (sessionJobIds == null || !(sessionJobIds instanceof java.util.Set) || 
        !((java.util.Set<?>)sessionJobIds).contains(jobId)) {
        log.warn("Unauthorized attempt to cancel job: {}", jobId);
        return ResponseEntity.status(403)
            .body(Map.of("message", "You are not authorized to cancel this job"));
    }

    boolean cancelled = false;
    int queuePosition = -1;

    if (jobQueue.isJobQueued(jobId)) {
        queuePosition = jobQueue.getJobPosition(jobId);
        cancelled = jobQueue.cancelJob(jobId);
        log.info("Cancelled queued job: {} (was at position {})", jobId, queuePosition);
    }

    if (!cancelled) {
        JobResult result = taskManager.getJobResult(jobId);
        if (result != null && !result.isComplete()) {
            taskManager.setError(jobId, "Job was cancelled by user");
            cancelled = true;
            log.info("Marked job as cancelled in TaskManager: {}", jobId);
        }
    }

    if (cancelled) {
        return ResponseEntity.ok(Map.of(
            "message", "Job cancelled successfully",
            "wasQueued", queuePosition >= 0,
            "queuePosition", queuePosition >= 0 ? queuePosition : "n/a"
        ));
    } else {
        JobResult result = taskManager.getJobResult(jobId);
        if (result == null) return ResponseEntity.notFound().build();
        if (result.isComplete()) 
            return ResponseEntity.badRequest()
                .body(Map.of("message", "Cannot cancel job that is already complete"));

        return ResponseEntity.internalServerError()
            .body(Map.of("message", "Failed to cancel job for unknown reason"));
    }
}

public ResponseEntity<String> printFile(@ModelAttribute PrintFileRequest request) throws IOException {
    MultipartFile file = request.getFileInput();
    String originalFilename = file.getOriginalFilename();
    if (originalFilename != null && (originalFilename.contains("..") || Paths.get(originalFilename).isAbsolute())) 
        throw new IOException("Invalid file path: " + originalFilename);

    String printerName = request.getPrinterName();
    String contentType = file.getContentType();
    try {
        PrintService selectedService = Arrays.stream(PrintServiceLookup.lookupPrintServices(null, null))
            .filter(service -> service.getName().toLowerCase().contains(printerName))
            .findFirst()
            .orElseThrow(() -> new IllegalArgumentException("No matching printer found"));

        log.info("Selected Printer: {}", selectedService.getName());

        if ("application/pdf".equals(contentType)) {
            PDDocument document = Loader.loadPDF(file.getBytes());
            PrinterJob job = PrinterJob.getPrinterJob();
            job.setPrintService(selectedService);
            job.setPageable(new PDFPageable(document));
            job.print();
            document.close();
        } else if (contentType.startsWith("image/")) {
            BufferedImage image = ImageIO.read(file.getInputStream());
            PrinterJob job = PrinterJob.getPrinterJob();
            job.setPrintService(selectedService);
            job.setPrintable((graphics, pageFormat, pageIndex) -> {
                if (pageIndex != 0) return NO_SUCH_PAGE;
                Graphics2D g2d = (Graphics2D)graphics;
                g2d.translate(pageFormat.getImageableX(), pageFormat.getImageableY());
                g2d.drawImage(image, 0, 0, 
                    (int)pageFormat.getImageableWidth(), 
                    (int)pageFormat.getImageableHeight(), null);
                return PAGE_EXISTS;
            });
            job.print();
        }
        return new ResponseEntity<>("File printed to " + selectedService.getName(), HttpStatus.OK);
    } catch (Exception e) {
        System.err.println("Print failed: " + e.getMessage());
        return new ResponseEntity<>(e.getMessage(), HttpStatus.BAD_REQUEST);
    }
}

@PostMapping(value = "/auto-split-pdf", consumes = "multipart/form-data")
@Operation(summary = "Auto split PDF pages into separate documents",
    description = "Accepts PDF, splits at QR boundaries. Output: ZIP-PDF Type:SISO")
public ResponseEntity<byte[]> autoSplitPdf(@ModelAttribute AutoSplitPdfRequest request) throws IOException {
    MultipartFile file = request.getFileInput();
    boolean duplexMode = Boolean.TRUE.equals(request.getDuplexMode());
    PDDocument document = null;
    List<PDDocument> splitDocuments = new ArrayList<>();
    Path zipFile = null;
    byte[] data = null;

    try {
        document = pdfDocumentFactory.load(file.getInputStream());
        PDFRenderer pdfRenderer = new PDFRenderer(document);
        pdfRenderer.setSubsamplingAllowed(true);

        for (int page = 0; page < document.getNumberOfPages(); ++page) {
            BufferedImage bim = pdfRenderer.renderImageWithDPI(page, 150);
            String result = decodeQRCode(bim);
            boolean isValidQrCode = VALID_QR_CONTENTS.contains(result);
            log.debug("Detected QR {} valid={}", result, isValidQrCode);

            if (isValidQrCode && page != 0) splitDocuments.add(new PDDocument());

            if (!splitDocuments.isEmpty() && !isValidQrCode) 
                splitDocuments.get(splitDocuments.size()-1).addPage(document.getPage(page));
            else if (page == 0) {
                PDDocument firstDoc = new PDDocument();
                firstDoc.addPage(document.getPage(page));
                splitDocuments.add(firstDoc);
            }

            if (duplexMode && isValidQrCode) page++;
        }

        splitDocuments.removeIf(pdDoc -> pdDoc.getNumberOfPages() == 0);
        zipFile = Files.createTempFile("split_documents", ".zip");
        String filename = Filenames.toSimpleFileName(file.getOriginalFilename()).replaceFirst("[.][^.]+$", "");

        try (ZipOutputStream zipOut = new ZipOutputStream(Files.newOutputStream(zipFile))) {
            for (int i = 0; i < splitDocuments.size(); i++) {
                String fileName = filename + "_" + (i+1) + ".pdf";
                PDDocument splitDoc = splitDocuments.get(i);
                ByteArrayOutputStream baos = new ByteArrayOutputStream();
                splitDoc.save(baos);
                byte[] pdf = baos.toByteArray();

                zipOut.putNextEntry(new ZipEntry(fileName));
                zipOut.write(pdf);
                zipOut.closeEntry();
            }
        }
        data = Files.readAllBytes(zipFile);
        return WebResponseUtils.bytesToWebResponse(data, filename+".zip", MediaType.APPLICATION_OCTET_STREAM);
    } catch (Exception e) {
        log.error("Auto split error", e);
        throw e;
    } finally {
        if (document != null) try { document.close(); } catch (IOException e) { log.error("Error closing main doc", e); }
        for (PDDocument splitDoc : splitDocuments) 
            try { splitDoc.close(); } catch (IOException e) { log.error("Error closing split doc", e); }
        if (zipFile != null) try { Files.deleteIfExists(zipFile); } catch (IOException e) { log.error("Error deleting zip", e); }
    }
}

@PostMapping(consumes = "multipart/form-data", value = "/auto-rename")
@Operation(summary = "Extract header from PDF file",
    description = "Extracts title/header heuristically. Input:PDF Output:PDF Type:SISO")
public ResponseEntity<byte[]> extractHeader(@ModelAttribute ExtractHeaderRequest request) throws Exception {
    MultipartFile file = request.getFileInput();
    boolean useFirstTextAsFallback = Boolean.TRUE.equals(request.getUseFirstTextAsFallback());
    PDDocument document = pdfDocumentFactory.load(file);

    PDFTextStripper reader = new PDFTextStripper() {
        List<LineInfo> lineInfos = new ArrayList<>();
        StringBuilder lineBuilder = new StringBuilder();
        float lastY = -1;
        float maxFontSizeInLine = 0.0f;
        int lineCount = 0;

        @Override
        protected void processTextPosition(TextPosition text) {
            if (lastY != text.getY() && lineCount < LINE_LIMIT) {
                processLine();
                lineBuilder = new StringBuilder(text.getUnicode());
                maxFontSizeInLine = text.getFontSizeInPt();
                lastY = text.getY();
                lineCount++;
            } else if (lineCount < LINE_LIMIT) {
                lineBuilder.append(text.getUnicode());
                if (text.getFontSizeInPt() > maxFontSizeInLine) 
                    maxFontSizeInLine = text.getFontSizeInPt();
            }
        }

        private void processLine() {
            if (lineBuilder.length() > 0 && lineCount < LINE_LIMIT) 
                lineInfos.add(new LineInfo(lineBuilder.toString(), maxFontSizeInLine));
        }

        @Override
        public String getText(PDDocument doc) throws IOException {
            this.lineInfos.clear();
            this.lineBuilder = new StringBuilder();
            this.lastY = -1;
            this.maxFontSizeInLine = 0.0f;
            this.lineCount = 0;
            super.getText(doc);
            processLine();  // Final line

            List<LineInfo> mergedLineInfos = new ArrayList<>();
            for (int i = 0; i < lineInfos.size(); i++) {
                String mergedText = lineInfos.get(i).text;
                float fontSize = lineInfos.get(i).fontSize;
                while (i+1 < lineInfos.size() && lineInfos.get(i+1).fontSize == fontSize) {
                    mergedText += " " + lineInfos.get(i+1).text;
                    i++;
                }
                mergedLineInfos.add(new LineInfo(mergedText, fontSize));
            }

            mergedLineInfos.sort(Comparator.comparing((LineInfo li) -> li.fontSize).reversed());
            String title = mergedLineInfos.isEmpty() ? null : mergedLineInfos.get(0).text;
            return title != null ? title : 
                (useFirstTextAsFallback ? 
                    (mergedLineInfos.isEmpty() ? null : mergedLineInfos.get(mergedLineInfos.size()-1).text) : 
                    null);
        }

        class LineInfo {
            String text;
            float fontSize;
            LineInfo(String text, float fontSize) { this.text = text; this.fontSize = fontSize; }
        }
    };

    String header = reader.getText(document);
    if (header != null && header.length() < 255) {
        header = header.replaceAll("[/\\\\?%*:|\"<>]", "").trim();
        return WebResponseUtils.pdfDocToWebResponse(document, header + ".pdf");
    } else {
        log.info("No valid title found");
        return WebResponseUtils.pdfDocToWebResponse(
            document, Filenames.toSimpleFileName(file.getOriginalFilename()));
    }
}

@Operation(summary = "Validate PDF Digital Signature",
    description = "Validates PDF signatures against certificates. Input:PDF Output:JSON Type:SISO")
@PostMapping(value = "/validate-signature", consumes = MediaType.MULTIPART_FORM_DATA_VALUE)
public ResponseEntity<List<SignatureValidationResult>> validateSignature(
        @ModelAttribute SignatureValidationRequest request) throws IOException {
    List<SignatureValidationResult> results = new ArrayList<>();
    MultipartFile file = request.getFileInput();
    MultipartFile certFile = request.getCertFile();
    X509Certificate customCert = null;

    if (certFile != null && !certFile.isEmpty()) {
        try (ByteArrayInputStream certStream = new ByteArrayInputStream(certFile.getBytes())) {
            CertificateFactory cf = CertificateFactory.getInstance("X.509");
            customCert = (X509Certificate)cf.generateCertificate(certStream);
        } catch (CertificateException e) {
            throw ExceptionUtils.createRuntimeException("error.invalidFormat", 
                "Invalid certificate format: {}", e, e.getMessage());
        }
    }

    try (PDDocument document = pdfDocumentFactory.load(file.getInputStream())) {
        for (PDSignature sig : document.getSignatureDictionaries()) {
            SignatureValidationResult result = new SignatureValidationResult();
            try {
                byte[] signedContent = sig.getSignedContent(file.getInputStream());
                byte[] signatureBytes = sig.getContents(file.getInputStream());
                CMSProcessable content = new CMSProcessableByteArray(signedContent);
                CMSSignedData signedData = new CMSSignedData(content, signatureBytes);

                Store<X509CertificateHolder> certStore = signedData.getCertificates();
                SignerInformationStore signerStore = signedData.getSignerInfos();

                for (SignerInformation signer : signerStore.getSigners()) {
                    X509CertificateHolder certHolder = (X509CertificateHolder)certStore.getMatches(signer.getSID()).iterator().next();
                    X509Certificate cert = new JcaX509CertificateConverter().getCertificate(certHolder);
                    boolean isValid = signer.verify(new JcaSimpleSignerInfoVerifierBuilder().build(cert));
                    result.setValid(isValid);

                    result.setChainValid(customCert != null ? 
                        certValidationService.validateCertificateChainWithCustomCert(cert, customCert) :
                        certValidationService.validateCertificateChain(cert));

                    result.setTrustValid(customCert != null ? 
                        certValidationService.validateTrustWithCustomCert(cert, customCert) :
                        certValidationService.validateTrustStore(cert));

                    result.setNotRevoked(!certValidationService.isRevoked(cert));
                    result.setNotExpired(!cert.getNotAfter().before(new Date()));

                    result.setSignerName(sig.getName());
                    result.setSignatureDate(sig.getSignDate().getTime().toString());
                    result.setReason(sig.getReason());
                    result.setLocation(sig.getLocation());

                    result.setIssuerDN(cert.getIssuerX500Principal().getName());
                    result.setSubjectDN(cert.getSubjectX500Principal().getName());
                    result.setSerialNumber(cert.getSerialNumber().toString(16));
                    result.setValidFrom(cert.getNotBefore().toString());
                    result.setValidUntil(cert.getNotAfter().toString());
                    result.setSignatureAlgorithm(cert.getSigAlgName());

                    try {
                        result.setKeySize(((RSAPublicKey)cert.getPublicKey()).getModulus().bitLength());
                    } catch (Exception e) {
                        result.setKeySize(0);
                    }

                    result.setVersion(String.valueOf(cert.getVersion()));

                    List<String> keyUsages = new ArrayList<>();
                    boolean[] keyUsageFlags = cert.getKeyUsage();
                    if (keyUsageFlags != null) {
                        String[] keyUsageLabels = {
                            "Digital Signature", "Non-Repudiation", "Key Encipherment",
                            "Data Encipherment", "Key Agreement", "Certificate Signing",
                            "CRL Signing", "Encipher Only", "Decipher Only"
                        };
                        for (int i = 0; i < keyUsageFlags.length; i++) 
                            if (keyUsageFlags[i]) keyUsages.add(keyUsageLabels[i]);
                    }
                    result.setKeyUsages(keyUsages);
                    result.setSelfSigned(cert.getSubjectX500Principal().equals(cert.getIssuerX500Principal()));
                }
            } catch (Exception e) {
                result.setValid(false);
                result.setErrorMessage("Validation failed: " + e.getMessage());
            }
            results.add(result);
        }
    }
    return ResponseEntity.ok(results);
}

public Map<String, Object> captureApplicationProperties() {
    Map<String, Object> properties = new HashMap<>();
    // Legal
    addIfNotEmpty(properties, "legal_termsAndConditions", applicationProperties.getLegal().getTermsAndConditions());
    addIfNotEmpty(properties, "legal_privacyPolicy", applicationProperties.getLegal().getPrivacyPolicy());
    addIfNotEmpty(properties, "legal_accessibilityStatement", applicationProperties.getLegal().getAccessibilityStatement());
    addIfNotEmpty(properties, "legal_cookiePolicy", applicationProperties.getLegal().getCookiePolicy());
    addIfNotEmpty(properties, "legal_impressum", applicationProperties.getLegal().getImpressum());
    // Security
    addIfNotEmpty(properties, "security_enableLogin", applicationProperties.getSecurity().getEnableLogin());
    addIfNotEmpty(properties, "security_csrfDisabled", applicationProperties.getSecurity().getCsrfDisabled());
    addIfNotEmpty(properties, "security_loginAttemptCount", applicationProperties.getSecurity().getLoginAttemptCount());
    addIfNotEmpty(properties, "security_loginResetTimeMinutes", applicationProperties.getSecurity().getLoginResetTimeMinutes());
    addIfNotEmpty(properties, "security_loginMethod", applicationProperties.getSecurity().getLoginMethod());
    // OAuth2
    addIfNotEmpty(properties, "security_oauth2_enabled", applicationProperties.getSecurity().getOauth2().getEnabled());
    if (applicationProperties.getSecurity().getOauth2().getEnabled()) {
        addIfNotEmpty(properties, "security_oauth2_autoCreateUser", applicationProperties.getSecurity().getOauth2().getAutoCreateUser());
        addIfNotEmpty(properties, "security_oauth2_blockRegistration", applicationProperties.getSecurity().getOauth2().getBlockRegistration());
        addIfNotEmpty(properties, "security_oauth2_useAsUsername", applicationProperties.getSecurity().getOauth2().getUseAsUsername());
        addIfNotEmpty(properties, "security_oauth2_provider", applicationProperties.getSecurity().getOauth2().getProvider());
    }
    // System
    addIfNotEmpty(properties, "system_defaultLocale", applicationProperties.getSystem().getDefaultLocale());
    addIfNotEmpty(properties, "system_googlevisibility", applicationProperties.getSystem().getGooglevisibility());
    addIfNotEmpty(properties, "system_showUpdate", applicationProperties.getSystem().isShowUpdate());
    addIfNotEmpty(properties, "system_showUpdateOnlyAdmin", applicationProperties.getSystem().getShowUpdateOnlyAdmin());
    addIfNotEmpty(properties, "system_customHTMLFiles", applicationProperties.getSystem().isCustomHTMLFiles());
