void drainLoop() {
    int missed = 1;
    Observer<? super R> a = downstream;
    AtomicInteger n = active;
    AtomicReference<SpscLinkedArrayQueue<R>> qr = queue;

    for (;;) {
        for (;;) {
            if (cancelled) {
                clear();
                return;
            }

            if (!delayErrors) {
                Throwable ex = errors.get();
                if (ex != null) {
                    clear();
                    errors.tryTerminateConsumer(a);
                    return;
                }
            }

            boolean d = n.get() == 0;
            SpscLinkedArrayQueue<R> q = qr.get();
            R v = q != null ? q.poll() : null;
            boolean empty = v == null;

            if (d && empty) {
                errors.tryTerminateConsumer(a);
                return;
            }

            if (empty) {
                break;
            }

            a.onNext(v);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void onNext(T t) {
    if (done) {
        return;
    }
    boolean b;
    try {
        b = predicate.test(t);
    } catch (Throwable e) {
        Exceptions.throwIfFatal(e);
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        onError(e);
        return;
    }
    if (b) {
        done = true;
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        downstream.onSuccess(true);
    }
}

@SuppressWarnings("unchecked")
void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    AtomicReference<Object> c = current;
    Subscriber<? super T> a = downstream;
    Disposable cancelled = disposables;

    for (;;) {
        if (cancelled.isDisposed()) {
            c.lazySet(null);
            return;
        }

        Object o = c.get();

        if (o != null) {
            boolean goNextSource;
            if (o != NotificationLite.COMPLETE) {
                long p = produced;
                if (p != requested.get()) {
                    produced = p + 1;
                    c.lazySet(null);
                    goNextSource = true;

                    a.onNext((T)o);
                } else {
                    goNextSource = false;
                }
            } else {
                goNextSource = true;
                c.lazySet(null);
            }

            if (goNextSource && !cancelled.isDisposed()) {
                int i = index;
                if (i == sources.length) {
                    a.onComplete();
                    return;
                }
                index = i + 1;

                sources[i].subscribe(this);
            }
        }

        if (decrementAndGet() == 0) {
            break;
        }
    }
}

@Override
void runAsync() {
    int missed = 1;

    final Subscriber<? super T> a = downstream;
    final SimpleQueue<T> q = queue;

    long e = produced;

    for (;;) {

        long r = requested.get();

        while (e != r) {
            boolean d = done;
            T v;

            try {
                v = q.poll();
            } catch (Throwable ex) {
                Exceptions.throwIfFatal(ex);

                cancelled = true;
                upstream.cancel();
                q.clear();

                a.onError(ex);
                worker.dispose();
                return;
            }

            boolean empty = v == null;

            if (checkTerminated(d, empty, a)) {
                return;
            }

            if (empty) {
                break;
            }

            a.onNext(v);

            e++;
            if (e == limit) {
                if (r != Long.MAX_VALUE) {
                    r = requested.addAndGet(-e);
                }
                upstream.request(e);
                e = 0L;
            }
        }

        if (e == r && checkTerminated(done, q.isEmpty(), a)) {
            return;
        }

        int w = get();
        if (missed == w) {
            produced = e;
            missed = addAndGet(-missed);
            if (missed == 0) {
                break;
            }
        } else {
            missed = w;
        }
    }
}

void drainLoop() {
    int missed = 1;

    JoinInnerSubscriber<T>[] s = this.subscribers;
    int n = s.length;
    Subscriber<? super T> a = this.downstream;

    for (;;) {

        long r = requested.get();
        long e = 0;

        middle:
        while (e != r) {
            if (cancelled) {
                cleanup();
                return;
            }

            boolean d = done.get() == 0;

            boolean empty = true;

            for (int i = 0; i < n; i++) {
                JoinInnerSubscriber<T> inner = s[i];

                SimplePlainQueue<T> q = inner.queue;
                if (q != null) {
                    T v = q.poll();

                    if (v != null) {
                        empty = false;
                        a.onNext(v);
                        inner.requestOne();
                        if (++e == r) {
                            break middle;
                        }
                    }
                }
            }

            if (d && empty) {
                errors.tryTerminateConsumer(a);
                return;
            }

            if (empty) {
                break;
            }
        }

        if (e == r) {
            if (cancelled) {
                cleanup();
                return;
            }

            boolean d = done.get() == 0;

            boolean empty = true;

            for (int i = 0; i < n; i++) {
                JoinInnerSubscriber<T> inner = s[i];

                SimpleQueue<T> q = inner.queue;
                if (q != null && !q.isEmpty()) {
                    empty = false;
                    break;
                }
            }

            if (d && empty) {
                errors.tryTerminateConsumer(a);
                return;
            }
        }

        if (e != 0) {
            BackpressureHelper.produced(requested, e);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;

    AtomicReference<T> latest = this.latest;
    AtomicLong requested = this.requested;
    Subscriber<? super T> downstream = this.downstream;

    for (;;) {

        for (;;) {
            if (cancelled) {
                clear();
                return;
            }

            boolean d = done;
            Throwable error = this.error;

            if (d && error != null) {
                if (onDropped != null) {
                    T v = latest.getAndSet(null);
                    if (v != null) {
                        try {
                            onDropped.accept(v);
                        } catch (Throwable ex) {
                            Exceptions.throwIfFatal(ex);
                            error = new CompositeException(error, ex);
                        }
                    }
                } else {
                    latest.lazySet(null);
                }
                downstream.onError(error);
                worker.dispose();
                return;
            }

            T v = latest.get();
            boolean empty = v == null;

            if (d) {
                if (!empty) {
                    v = latest.getAndSet(null);
                    if (emitLast) {
                        long e = emitted;
                        if (e != requested.get()) {
                            emitted = e + 1;
                            downstream.onNext(v);
                            downstream.onComplete();
                        } else {
                            tryDropAndSignalMBE(v);
                        }
                    } else {
                        if (onDropped != null) {
                            try {
                                onDropped.accept(v);
                            } catch (Throwable ex) {
                                Exceptions.throwIfFatal(ex);
                                downstream.onError(ex);
                                worker.dispose();
                                return;
                            }
                        }
                        downstream.onComplete();
                    }
                } else {
                    downstream.onComplete();
                }
                worker.dispose();
                return;
            }

            if (empty) {
                if (timerFired) {
                    timerRunning = false;
                    timerFired = false;
                }
                break;
            }

            if (!timerRunning || timerFired) {
                v = latest.getAndSet(null);
                long e = emitted;
                if (e != requested.get()) {
                    downstream.onNext(v);
                    emitted = e + 1;
                } else {
                    upstream.cancel();
                    tryDropAndSignalMBE(v);
                    worker.dispose();
                    return;
                }

                timerFired = false;
                timerRunning = true;
                worker.schedule(this, timeout, unit);
            } else {
                break;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drain() {
    if (wip.getAndIncrement() != 0) {
        return;
    }

    int missed = 1;

    SimpleQueue<T> q = queue;

    int upstreamConsumed = consumed;
    int localLimit = limit;
    boolean canRequest = sourceMode != QueueSubscription.SYNC;
    AtomicReference<MulticastSubscription<T>[]> subs = subscribers;

    MulticastSubscription<T>[] array = subs.get();

    outer:
    for (;;) {

        int n = array.length;

        if (q != null && n != 0) {
            long r = Long.MAX_VALUE;

            for (MulticastSubscription<T> ms : array) {
                long u = ms.get() - ms.emitted;
                if (u != Long.MIN_VALUE) {
                    if (r > u) {
                        r = u;
                    }
                } else {
                    n--;
                }
            }

            if (n == 0) {
                r = 0;
            }

            while (r != 0) {
                if (isDisposed()) {
                    q.clear();
                    return;
                }

                boolean d = done;

                if (d && !delayError) {
                    Throwable ex = error;
                    if (ex != null) {
                        errorAll(ex);
                        return;
                    }
                }

                T v;

                try {
                    v = q.poll();
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    SubscriptionHelper.cancel(upstream);
                    errorAll(ex);
                    return;
                }

                boolean empty = v == null;

                if (d && empty) {
                    Throwable ex = error;
                    if (ex != null) {
                        errorAll(ex);
                    } else {
                        completeAll();
                    }
                    return;
                }

                if (empty) {
                    break;
                }

                boolean subscribersChange = false;

                for (MulticastSubscription<T> ms : array) {
                    long msr = ms.get();
                    if (msr != Long.MIN_VALUE) {
                        if (msr != Long.MAX_VALUE) {
                            ms.emitted++;
                        }
                        ms.downstream.onNext(v);
                    } else {
                        subscribersChange = true;
                    }
                }

                r--;

                if (canRequest && ++upstreamConsumed == localLimit) {
                    upstreamConsumed = 0;
                    upstream.get().request(localLimit);
                }

                MulticastSubscription<T>[] freshArray = subs.get();
                if (subscribersChange || freshArray != array) {
                    array = freshArray;
                    continue outer;
                }
            }

            if (r == 0) {
                if (isDisposed()) {
                    q.clear();
                    return;
                }

                boolean d = done;

                if (d && !delayError) {
                    Throwable ex = error;
                    if (ex != null) {
                        errorAll(ex);
                        return;
                    }
                }

                if (d && q.isEmpty()) {
                    Throwable ex = error;
                    if (ex != null) {
                        errorAll(ex);
                    } else {
                        completeAll();
                    }
                    return;
                }
            }
        }

        consumed = upstreamConsumed;
        missed = wip.addAndGet(-missed);
        if (missed == 0) {
            break;
        }
        if (q == null) {
            q = queue;
        }
        array = subs.get();
    }
}

@SuppressWarnings("unchecked")
void subscribeInner(ObservableSource<? extends U> p) {
    for (;;) {
        if (p instanceof Supplier) {
            if (tryEmitScalar(((Supplier<? extends U>)p)) && maxConcurrency != Integer.MAX_VALUE) {
                boolean empty = false;
                synchronized (this) {
                    p = sources.poll();
                    if (p == null) {
                        wip--;
                        empty = true;
                    }
                }
                if (empty) {
                    drain();
                    break;
                }
            } else {
                break;
            }
        } else {
            InnerObserver<T, U> inner = new InnerObserver<>(this, uniqueId++);
            if (addInner(inner)) {
                p.subscribe(inner);
            }
            break;
        }
    }
}

@BackpressureSupport(BackpressureKind.SPECIAL)
@CheckReturnValue
@SchedulerSupport(SchedulerSupport.NONE)
@NonNull
public static <@NonNull T> Flowable<T> fromObservable(@NonNull ObservableSource<T> source, @NonNull BackpressureStrategy strategy) {
    Objects.requireNonNull(source, "source is null");
    Objects.requireNonNull(strategy, "strategy is null");
    Flowable<T> f = new FlowableFromObservable<>(source);
    switch (strategy) {
        case DROP:
            return f.onBackpressureDrop();
        case LATEST:
            return f.onBackpressureLatest();
        case MISSING:
            return f;
        case ERROR:
            return RxJavaPlugins.onAssembly(new FlowableOnBackpressureError<>(f));
        default:
            return f.onBackpressureBuffer();
    }
}

public static <T> void subscribe(ObservableSource<? extends T> o, Observer<? super T> observer) {
    final BlockingQueue<Object> queue = new LinkedBlockingQueue<>();

    BlockingObserver<T> bs = new BlockingObserver<>(queue);
    observer.onSubscribe(bs);

    o.subscribe(bs);
    for (;;) {
        if (bs.isDisposed()) {
            break;
        }
        Object v = queue.poll();
        if (v == null) {
            try {
                v = queue.take();
            } catch (InterruptedException ex) {
                bs.dispose();
                observer.onError(ex);
                return;
            }
        }
        if (bs.isDisposed()
                || v == BlockingObserver.TERMINATED
                || NotificationLite.acceptFull(v, observer)) {
            break;
        }
    }
}

@Override
public void onTextTracksChanged(@NonNull final Tracks currentTracks) {
    super.onTextTracksChanged(currentTracks);

    final boolean trackTypeTextSupported = !currentTracks.containsType(C.TRACK_TYPE_TEXT)
            || currentTracks.isTypeSupported(C.TRACK_TYPE_TEXT, false);
    if (getPlayer().getTrackSelector().getCurrentMappedTrackInfo() == null
            || !trackTypeTextSupported) {
        binding.captionTextView.setVisibility(View.GONE);
        return;
    }

    // Extract all loaded languages
    final List<Tracks.Group> textTracks = new ArrayList<>();
    for (Tracks.Group trackGroupInfo : currentTracks.getGroups()) {
        if (C.TRACK_TYPE_TEXT == trackGroupInfo.getType()) {
            textTracks.add(trackGroupInfo);
        }
    }

    final List<String> availableLanguages = new ArrayList<>();
    for (Tracks.Group group : textTracks) {
        TrackGroup textTrack = group.getMediaTrackGroup();
        if (textTrack.length > 0) {
            availableLanguages.add(textTrack.getFormat(0).language);
        }
    }

    // Find selected text track
    Format selectedTrack = null;
    for (Tracks.Group group : textTracks) {
        if (group.isSelected() && group.getMediaTrackGroup().length >= 1) {
            selectedTrack = group.getMediaTrackGroup().getFormat(0);
            break;
        }
    }

    // Build UI
    buildCaptionMenu(availableLanguages);
    if (player.getTrackSelector().getParameters().getRendererDisabled(
            player.getCaptionRendererIndex()) || selectedTrack == null) {
        binding.captionTextView.setText(R.string.caption_none);
    } else {
        binding.captionTextView.setText(selectedTrack.language);
    }
    binding.captionTextView.setVisibility(
            availableLanguages.isEmpty() ? View.GONE : View.VISIBLE);
}

public void play() {
    if (DEBUG) {
        Log.d(TAG, "play() called");
    }
    if (audioReactor == null || playQueue == null || exoPlayerIsNull()) {
        return;
    }

    if (!isMuted()) {
        audioReactor.requestAudioFocus();
    }

    if (currentState == STATE_COMPLETED) {
        if (playQueue.getIndex() == 0) {
            seekToDefault();
        } else {
            playQueue.setIndex(0);
        }
    }

    simpleExoPlayer.play();
    saveStreamProgressState();
}

@Nullable
static String choosePreferredImage(@NonNull final List<Image> images,
                                   final PreferredImageQuality nonNoneQuality) {
    // this will be used to estimate the pixel count for images where only one of height or
    // width are known
    double widthOverHeight = 1.0;
    for (Image image : images) {
        if (image.getHeight() != HEIGHT_UNKNOWN
                && image.getWidth() != WIDTH_UNKNOWN) {
            widthOverHeight = ((double) image.getWidth()) / image.getHeight();
            break;
        }
    }

    final Image.ResolutionLevel preferredLevel = nonNoneQuality.toResolutionLevel();
    final Comparator<Image> initialComparator = Comparator
            // the first step splits the images into groups of resolution levels
            .<Image>comparingInt(i -> {
                if (i.getEstimatedResolutionLevel() == Image.ResolutionLevel.UNKNOWN) {
                    return 3; // avoid unknowns as much as possible
                } else if (i.getEstimatedResolutionLevel() == preferredLevel) {
                    return 0; // prefer a matching resolution level
                } else if (i.getEstimatedResolutionLevel() == Image.ResolutionLevel.MEDIUM) {
                    return 1; // the preferredLevel is only 1 "step" away (either HIGH or LOW)
                } else {
                    return 2; // the preferredLevel is the furthest away possible (2 "steps")
                }
            })
            // then each level's group is further split into two subgroups, one with known image
            // size (which is also the preferred subgroup) and the other without
            .thenComparing(image ->
                    image.getHeight() == HEIGHT_UNKNOWN && image.getWidth() == WIDTH_UNKNOWN);

    // The third step chooses, within each subgroup with known image size, the best image based
    // on how close its size is to BEST_LOW_H or BEST_MEDIUM_H (with proper units). Subgroups
    // without known image size will be left untouched since estimatePixelCount always returns
    // the same number for those.
    final Comparator<Image> finalComparator;
    switch (nonNoneQuality) {
        case NONE:
            finalComparator = initialComparator; // unreachable
            break;
        case LOW:
            finalComparator = initialComparator.thenComparingDouble(image -> {
                final double pixelCount = estimatePixelCount(image, widthOverHeight);
                return Math.abs(pixelCount - BEST_LOW_H * BEST_LOW_H * widthOverHeight);
            });
            break;
        case MEDIUM:
            finalComparator = initialComparator.thenComparingDouble(image -> {
                final double pixelCount = estimatePixelCount(image, widthOverHeight);
                return Math.abs(pixelCount - BEST_MEDIUM_H * BEST_MEDIUM_H * widthOverHeight);
            });
            break;
        case HIGH:
            finalComparator = initialComparator.thenComparingDouble(
                    // this is reversed with a - so that the highest resolution is chosen
                    i -> -estimatePixelCount(i, widthOverHeight));
            break;
        default:
            throw new IllegalStateException("Unexpected value: " + nonNoneQuality);
    }

    Image bestImage = null;
    for (Image image : images) {
        if (bestImage == null || finalComparator.compare(image, bestImage) < 0) {
            bestImage = image;
        }
    }
    if (bestImage != null) {
        return bestImage.getUrl();
    }
    return null;
}

@SuppressLint("PrivateResource") // we currently use Exoplayer's internal strings and icons
@Nullable
public static NotificationActionData fromNotificationActionEnum(
        @NonNull final Player player,
        @NotificationConstants.Action final int selectedAction
) {

    final int baseActionIcon = NotificationConstants.ACTION_ICONS[selectedAction];
    final Context ctx = player.getContext();

    switch (selectedAction) {
        case NotificationConstants.PREVIOUS:
            return new NotificationActionData(ACTION_PLAY_PREVIOUS,
                    ctx.getString(com.google.android.exoplayer2.ui.R.string
                            .exo_controls_previous_description), baseActionIcon);

        case NotificationConstants.NEXT:
            return new NotificationActionData(ACTION_PLAY_NEXT,
                    ctx.getString(com.google.android.exoplayer2.ui.R.string
                            .exo_controls_next_description), baseActionIcon);

        case NotificationConstants.REWIND:
            return new NotificationActionData(ACTION_FAST_REWIND,
                    ctx.getString(com.google.android.exoplayer2.ui.R.string
                            .exo_controls_rewind_description), baseActionIcon);

        case NotificationConstants.FORWARD:
            return new NotificationActionData(ACTION_FAST_FORWARD,
                    ctx.getString(com.google.android.exoplayer2.ui.R.string
                            .exo_controls_fastforward_description), baseActionIcon);

        case NotificationConstants.SMART_REWIND_PREVIOUS:
            if (player.getPlayQueue() != null && player.getPlayQueue().size() > 1) {
                return new NotificationActionData(ACTION_PLAY_PREVIOUS,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string
                                .exo_controls_previous_description),
                        com.google.android.exoplayer2.ui.R.drawable.exo_notification_previous);
            } else {
                return new NotificationActionData(ACTION_FAST_REWIND,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string
                                .exo_controls_rewind_description),
                        com.google.android.exoplayer2.ui.R.drawable.exo_controls_rewind);
            }

        case NotificationConstants.SMART_FORWARD_NEXT:
            if (player.getPlayQueue() != null && player.getPlayQueue().size() > 1) {
                return new NotificationActionData(ACTION_PLAY_NEXT,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string
                                .exo_controls_next_description),
                        com.google.android.exoplayer2.ui.R.drawable.exo_notification_next);
            } else {
                return new NotificationActionData(ACTION_FAST_FORWARD,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string
                                .exo_controls_fastforward_description),
                        com.google.android.exoplayer2.ui.R.drawable.exo_controls_fastforward);
            }

        case NotificationConstants.PLAY_PAUSE_BUFFERING:
            if (player.getCurrentState() == Player.STATE_PREFLIGHT
                    || player.getCurrentState() == Player.STATE_BLOCKED
                    || player.getCurrentState() == Player.STATE_BUFFERING) {
                return new NotificationActionData(ACTION_PLAY_PAUSE,
                        ctx.getString(R.string.notification_action_buffering),
                        R.drawable.ic_hourglass_top);
            }

            // fallthrough
        case NotificationConstants.PLAY_PAUSE:
            if (player.getCurrentState() == Player.STATE_COMPLETED) {
                return new NotificationActionData(ACTION_PLAY_PAUSE,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string
                                .exo_controls_pause_description),
                        R.drawable.ic_replay);
            } else if (player.isPlaying()
                    || player.getCurrentState() == Player.STATE_PREFLIGHT
                    || player.getCurrentState() == Player.STATE_BLOCKED
                    || player.getCurrentState() == Player.STATE_BUFFERING) {
                return new NotificationActionData(ACTION_PLAY_PAUSE,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string
                                .exo_controls_pause_description),
                        com.google.android.exoplayer2.ui.R.drawable.exo_notification_pause);
            } else {
                return new NotificationActionData(ACTION_PLAY_PAUSE,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string
                                .exo_controls_play_description),
                        com.google.android.exoplayer2.ui.R.drawable.exo_notification_play);
            }

        case NotificationConstants.REPEAT:
            if (player.getRepeatMode() == REPEAT_MODE_ALL) {
                return new NotificationActionData(ACTION_REPEAT,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string
                                .exo_controls_repeat_all_description),
                        com.google.android.exoplayer2.ext.mediasession.R.drawable
                                .exo_media_action_repeat_all);
            } else if (player.getRepeatMode() == REPEAT_MODE_ONE) {
                return new NotificationActionData(ACTION_REPEAT,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string
                                .exo_controls_repeat_one_description),
                        com.google.android.exoplayer2.ext.mediasession.R.drawable
                                .exo_media_action_repeat_one);
            } else /* player.getRepeatMode() == REPEAT_MODE_OFF */ {
                return new NotificationActionData(ACTION_REPEAT,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string
                                .exo_controls_repeat_off_description),
                        com.google.android.exoplayer2.ext.mediasession.R.drawable
                                .exo_media_action_repeat_off);
            }

        case NotificationConstants.SHUFFLE:
            if (player.getPlayQueue() != null && player.getPlayQueue().isShuffled()) {
                return new NotificationActionData(ACTION_SHUFFLE,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string
                                .exo_controls_shuffle_on_description),
                        com.google.android.exoplayer2.ui.R.drawable.exo_controls_shuffle_on);
            } else {
                return new NotificationActionData(ACTION_SHUFFLE,
                        ctx.getString(com.google.android.exoplayer2.ui.R.string
                                .exo_controls_shuffle_off_description),
                        com.google.android.exoplayer2.ui.R.drawable.exo_controls_shuffle_off);
            }

        case NotificationConstants.CLOSE:
            return new NotificationActionData(ACTION_CLOSE, ctx.getString(R.string.close),
                    R.drawable.ic_close);

        case NotificationConstants.NOTHING:
        default:
            // do nothing
            return null;
    }
}

private void updatePlaybackState(final boolean playWhenReady, final int playbackState) {
    if (DEBUG) {
        Log.d(TAG, "ExoPlayer - updatePlaybackState() called with: "
                + "playWhenReady = [" + playWhenReady + "], "
                + "playbackState = [" + playbackState + "]");
    }

    if (currentState == STATE_PAUSED_SEEK) {
        if (DEBUG) {
            Log.d(TAG, "updatePlaybackState() is currently blocked");
        }
        return;
    }

    switch (playbackState) {
        case com.google.android.exoplayer2.Player.STATE_IDLE: // 1
            isPrepared = false;
            break;
        case com.google.android.exoplayer2.Player.STATE_BUFFERING: // 2
            if (isPrepared) {
                changeState(STATE_BUFFERING);
            }
            break;
        case com.google.android.exoplayer2.Player.STATE_READY: //3
            if (!isPrepared) {
                isPrepared = true;
                onPrepared(playWhenReady);
            }
            changeState(playWhenReady ? STATE_PLAYING : STATE_PAUSED);
            break;
        case com.google.android.exoplayer2.Player.STATE_ENDED: // 4
            changeState(STATE_COMPLETED);
            saveStreamProgressStateCompleted();
            isPrepared = false;
            break;
    }
}

public MediaDescriptionCompat getQueueMetadata(final int index) {
    if (player.getPlayQueue() == null) {
        return null;
    }
    final PlayQueueItem item = player.getPlayQueue().getItem(index);
    if (item == null) {
        return null;
    }

    final MediaDescriptionCompat.Builder descBuilder = new MediaDescriptionCompat.Builder()
            .setMediaId(String.valueOf(index))
            .setTitle(item.getTitle())
            .setSubtitle(item.getUploader());

    // set additional metadata for A2DP/AVRCP (Audio/Video Bluetooth profiles)
    final Bundle additionalMetadata = new Bundle();
    additionalMetadata.putString(MediaMetadataCompat.METADATA_KEY_TITLE, item.getTitle());
    additionalMetadata.putString(MediaMetadataCompat.METADATA_KEY_ARTIST, item.getUploader());
    additionalMetadata
            .putLong(MediaMetadataCompat.METADATA_KEY_DURATION, item.getDuration() * 1000);
    additionalMetadata.putLong(MediaMetadataCompat.METADATA_KEY_TRACK_NUMBER, index + 1L);
    additionalMetadata
            .putLong(MediaMetadataCompat.METADATA_KEY_NUM_TRACKS, player.getPlayQueue().size());
    descBuilder.setExtras(additionalMetadata);

    try {
        descBuilder.setIconUri(Uri.parse(
                ImageStrategy.choosePreferredImage(item.getThumbnails())));
    } catch (final Throwable e) {
        // no thumbnail available at all, or the user disabled image loading,
        // or the obtained url is not a valid `Uri`
    }

    return descBuilder.build();
}

public void changeState(final int state) {
    if (DEBUG) {
        Log.d(TAG, "changeState() called with: state = [" + state + "]");
    }
    currentState = state;
    switch (state) {
        case STATE_BLOCKED:
            onBlocked();
            break;
        case STATE_PLAYING:
            onPlaying();
            break;
        case STATE_BUFFERING:
            onBuffering();
            break;
        case STATE_PAUSED:
            onPaused();
            break;
        case STATE_PAUSED_SEEK:
            onPausedSeek();
            break;
        case STATE_COMPLETED:
            onCompleted();
            break;
    }
    notifyPlaybackUpdateToListeners();
}

private synchronized NotificationCompat.Builder createNotification() {
    if (DEBUG) {
        Log.d(TAG, "createNotification()");
    }
    notificationManager = NotificationManagerCompat.from(player.getContext());
    final NotificationCompat.Builder builder =
            new NotificationCompat.Builder(player.getContext(),
            player.getContext().getString(R.string.notification_channel_id));
    final MediaStyle mediaStyle = new MediaStyle();

    // setup media style (compact notification slots and media session)
    if (Build.VERSION.SDK_INT < Build.VERSION_CODES.TIRAMISU) {
        // notification actions are ignored on Android 13+, and are replaced by code in
        // MediaSessionPlayerUi
        final int[] compactSlots = initializeNotificationSlots();
        mediaStyle.setShowActionsInCompactView(compactSlots);
    }
    player.UIs()
            .get(MediaSessionPlayerUi.class)
            .flatMap(MediaSessionPlayerUi::getSessionToken)
            .ifPresent(mediaStyle::setMediaSession);

    // setup notification builder
    builder.setStyle(mediaStyle)
            .setPriority(NotificationCompat.PRIORITY_HIGH)
            .setVisibility(NotificationCompat.VISIBILITY_PUBLIC)
            .setCategory(NotificationCompat.CATEGORY_TRANSPORT)
            .setShowWhen(false)
            .setSmallIcon(R.drawable.ic_newpipe_triangle_white)
            .setColor(ContextCompat.getColor(player.getContext(),
                    R.color.dark_background_color))
            .setColorized(player.getPrefs().getBoolean(
                    player.getContext().getString(R.string.notification_colorize_key), true))
            .setDeleteIntent(PendingIntentCompat.getBroadcast(player.getContext(),
                    NOTIFICATION_ID, new Intent(ACTION_CLOSE), FLAG_UPDATE_CURRENT, false));

    // set the initial value for the video thumbnail, updatable with updateNotificationThumbnail
    setLargeIcon(builder);

    return builder;
}

@SuppressLint("SwitchIntDef") // only fit, fill and zoom are supported by NewPipe
@ResizeMode
public static int nextResizeModeAndSaveToPrefs(final Player player,
                                               @ResizeMode final int resizeMode) {
    final int newResizeMode;
    switch (resizeMode) {
        case AspectRatioFrameLayout.RESIZE_MODE_FIT:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FILL;
            break;
        case AspectRatioFrameLayout.RESIZE_MODE_FILL:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_ZOOM;
            break;
        case AspectRatioFrameLayout.RESIZE_MODE_ZOOM:
        default:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FIT;
            break;
    }

    // save the new resize mode so it can be restored in a future session
    player.getPrefs().edit().putInt(
            player.getContext().getString(R.string.last_resize_mode), newResizeMode).apply();
    return newResizeMode;
}

