    Iterator<? extends T> it = this.iterator;
    ConditionalSubscriber<? super T> a = downstream;

    for (;;) {
        if (cancelled) return;

        T t;
        try {
            t = it.next();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            a.onError(ex);
            return;
        }

        if (cancelled) return;
        if (t == null) {
            a.onError(new NullPointerException("Iterator.next() returned a null value"));
            return;
        } else {
            a.tryOnNext(t);
        }

        if (cancelled) return;

        boolean b;
        try {
            b = it.hasNext();
        } catch (Throwable ex) {
            Exceptions.throwIfFatal(ex);
            a.onError(ex);
            return;
        }

        if (!b) {
            if (!cancelled) {
                a.onComplete();
            }
            return;
        }
    }
}

static <T> boolean postCompleteDrain(long n,
                                     Subscriber<? super T> actual,
                                     Queue<T> queue,
                                     AtomicLong state,
                                     BooleanSupplier isCancelled) {

    long e = n & COMPLETED_MASK;

    for (;;) {
        while (e != n) {
            if (isCancelled(isCancelled)) return true;

            T t = queue.poll();
            if (t == null) {
                actual.onComplete();
                return true;
            }

            actual.onNext(t);
            e++;
        }

        if (isCancelled(isCancelled)) return true;
        if (queue.isEmpty()) {
            actual.onComplete();
            return true;
        }

        n = state.get();
        if (n == e) {
            n = state.addAndGet(-(e & REQUESTED_MASK));

            if ((n & REQUESTED_MASK) == 0L) {
                return false;
            }

            e = n & COMPLETED_MASK;
        }
    }
}

