public static long select(boolean v, long telnetPortPid, String select) throws InputMismatchException {
    Map<Long, String> processMap = listProcessByJps(v);

    // Move the port that is already listening to the first position
    if (telnetPortPid > 0 && processMap.containsKey(telnetPortPid)) {
        String telnetPortProcess = processMap.get(telnetPortPid);
        processMap.remove(telnetPortPid);
        Map<Long, String> newProcessMap = new LinkedHashMap<>();
        newProcessMap.put(telnetPortPid, telnetPortProcess);
        newProcessMap.putAll(processMap);
        processMap = newProcessMap;
    }

    if (processMap.isEmpty()) {
        AnsiLog.info(
                "Can not find java process. Try to run `jps` command lists the instrumented Java HotSpot VMs on the target system.");
        return -1;
    }

    // Select target process by the '--select' option when match only one process
    if (select != null && !select.trim().isEmpty()) {
        int matchedSelectCount = 0;
        Long matchedPid = null;
        for (Map.Entry<Long, String> entry : processMap.entrySet()) {
            if (entry.getValue().contains(select)) {
                matchedSelectCount++;
                matchedPid = entry.getKey();
            }
        }
        if (matchedSelectCount == 1) {
            return matchedPid;
        }
    }

    AnsiLog.info(
            "Found existing java process, please choose one and input the serial number of the process, eg : 1. Then hit ENTER.");
    // Print list
    int count = 1;
    for (String process : processMap.values()) {
        if (count == 1) {
            System.out.println("* [" + count + "]: " + process);
        } else {
            System.out.println("  [" + count + "]: " + process);
        }
        count++;
    }

    // Read choice
    String line = new Scanner(System.in).nextLine();
    if (line.trim().isEmpty()) {
        // Get the first process id
        return processMap.keySet().iterator().next();
    }

    int choice = new Scanner(line).nextInt();

    if (choice <= 0 || choice > processMap.size()) {
        return -1;
    }

    Iterator<Long> idIter = processMap.keySet().iterator();
    for (int i = 1; i <= choice; ++i) {
        if (i == choice) {
            return idIter.next();
        }
        idIter.next();
    }

    return -1;
}

void _processDo(int option) throws IOException {
    if (debugoptions) {
        System.err.println("RECEIVED DO: " + TelnetOption.getOption(option));
    }

    if (__notifhand != null) {
        __notifhand.receivedNegotiation(
                TelnetNotificationHandler.RECEIVED_DO, option);
    }

    boolean acceptNewState = false;

    // open TelnetOptionHandler functionality (start)
    if (optionHandlers[option] != null) {
        acceptNewState = optionHandlers[option].getAcceptLocal();
    } else {
        // open TelnetOptionHandler functionality (end)
        // TERMINAL-TYPE option (start)
        if (option == TERMINAL_TYPE) {
            if (terminalType != null && terminalType.length() > 0) {
                acceptNewState = true;
            }
        }
        // TERMINAL-TYPE option (end)
        // open TelnetOptionHandler functionality (start)
    }
    // open TelnetOptionHandler functionality (end)

    if (_willResponse[option] > 0) {
        --_willResponse[option];
        if (_willResponse[option] > 0 && _stateIsWill(option)) {
            --_willResponse[option];
        }
    }

    if (_willResponse[option] == 0) {
        if (_requestedWont(option)) {
            switch (option) {
                default:
                    break;
            }

            if (acceptNewState) {
                _setWantWill(option);
                _sendWill(option);
            } else {
                ++_willResponse[option];
                _sendWont(option);
            }
        } else {
            // Other end has acknowledged option.
            switch (option) {
                default:
                    break;
            }
        }
    }

    _setWill(option);
}

private void bind(Configure configure) throws Throwable {
    long start = System.currentTimeMillis();

    if (!isBindRef.compareAndSet(false, true)) {
        throw new IllegalStateException("already bind");
    }

    // Initialize random port
    if (configure.getTelnetPort() != null && configure.getTelnetPort() == 0) {
        int newTelnetPort = SocketUtils.findAvailableTcpPort();
        configure.setTelnetPort(newTelnetPort);
        logger().info("generate random telnet port: {}", newTelnetPort);
    }
    if (configure.getHttpPort() != null && configure.getHttpPort() == 0) {
        int newHttpPort = SocketUtils.findAvailableTcpPort();
        configure.setHttpPort(newHttpPort);
        logger().info("generate random http port: {}", newHttpPort);
    }
    // Try to find appName
    if (configure.getAppName() == null) {
        configure.setAppName(System.getProperty(
                ArthasConstants.PROJECT_NAME,
                System.getProperty(ArthasConstants.SPRING_APPLICATION_NAME, null)));
    }

    try {
        if (configure.getTunnelServer() != null) {
            tunnelClient = new TunnelClient();
            tunnelClient.setAppName(configure.getAppName());
            tunnelClient.setId(configure.getAgentId());
            tunnelClient.setTunnelServerUrl(configure.getTunnelServer());
            tunnelClient.setVersion(ArthasBanner.version());
            ChannelFuture channelFuture = tunnelClient.start();
            channelFuture.await(10, TimeUnit.SECONDS);
        }
    } catch (Throwable t) {
        logger().error("start tunnel client error", t);
    }

    try {
        ShellServerOptions options =
                new ShellServerOptions()
                        .setInstrumentation(instrumentation)
                        .setPid(PidUtils.currentLongPid())
                        .setWelcomeMessage(ArthasBanner.welcome());
        if (configure.getSessionTimeout() != null) {
            options.setSessionTimeout(configure.getSessionTimeout() * 1000);
        }

        this.httpSessionManager = new HttpSessionManager();
        if (IPUtils.isAllZeroIP(configure.getIp()) && StringUtils.isBlank(configure.getPassword())) {
            // When listening on 0.0.0.0, force generate password to prevent remote connection
            String errorMsg =
                    "Listening on 0.0.0.0 is very dangerous! External users can connect to your machine! "
                            + "No password is currently configured. "
                            + "Therefore, a default password is generated, "
                            + "and clients need to use the password to connect!";
            AnsiLog.error(errorMsg);
            configure.setPassword(StringUtils.randomString(64));
            AnsiLog.error("Generated arthas password: {}", configure.getPassword());

            logger().error(errorMsg);
            logger().info("Generated arthas password: {}", configure.getPassword());
        }

        this.securityAuthenticator = new SecurityAuthenticatorImpl(configure.getUsername(), configure.getPassword());

        shellServer = new ShellServerImpl(options);

        List<String> disabledCommands = new ArrayList<>();
        if (configure.getDisabledCommands() != null) {
            String[] strings = StringUtils.tokenizeToStringArray(configure.getDisabledCommands(), ",");
            if (strings != null) {
                disabledCommands.addAll(Arrays.asList(strings));
            }
        }
        BuiltinCommandPack builtinCommands = new BuiltinCommandPack(disabledCommands);
        List<CommandResolver> resolvers = new ArrayList<>();
        resolvers.add(builtinCommands);

        // Worker group
        workerGroup = new NioEventLoopGroup(new DefaultThreadFactory("arthas-TermServer", true));

        // TODO: discover user provided command resolver
        if (configure.getTelnetPort() != null && configure.getTelnetPort() > 0) {
            logger().info("try to bind telnet server, host: {}, port: {}.", configure.getIp(), configure.getTelnetPort());
            shellServer.registerTermServer(
                    new HttpTelnetTermServer(
                            configure.getIp(),
                            configure.getTelnetPort(),
                            options.getConnectionTimeout(),
                            workerGroup,
                            httpSessionManager));
        } else {
            logger().info("telnet port is {}, skip bind telnet server.", configure.getTelnetPort());
        }
        if (configure.getHttpPort() != null && configure.getHttpPort() > 0) {
            logger().info("try to bind http server, host: {}, port: {}.", configure.getIp(), configure.getHttpPort());
            shellServer.registerTermServer(
                    new HttpTermServer(
                            configure.getIp(),
                            configure.getHttpPort(),
                            options.getConnectionTimeout(),
                            workerGroup,
                            httpSessionManager));
        } else {
            // Listen local address in VM communication
            if (configure.getTunnelServer() != null) {
                shellServer.registerTermServer(
                        new HttpTermServer(
                                configure.getIp(),
                                configure.getHttpPort(),
                                options.getConnectionTimeout(),
                                workerGroup,
                                httpSessionManager));
            }
            logger().info("http port is {}, skip bind http server.", configure.getHttpPort());
        }

        for (CommandResolver resolver : resolvers) {
            shellServer.registerCommandResolver(resolver);
        }

        shellServer.listen(new BindHandler(isBindRef));
        if (!isBind()) {
            throw new IllegalStateException(
                    "Arthas failed to bind telnet or http port! Telnet port: {}"
                            + ", http port: {}",
                    String.valueOf(configure.getTelnetPort()),
                    String.valueOf(configure.getHttpPort()));
        }

        // http api session manager
        sessionManager = new SessionManagerImpl(options, shellServer.getCommandManager(), shellServer.getJobController());
        // http api handler
        httpApiHandler = new HttpApiHandler(historyManager, sessionManager);

        logger().info(
                "as-server listening on network={};telnet={};http={};timeout={};",
                configure.getIp(),
                configure.getTelnetPort(),
                configure.getHttpPort(),
                options.getConnectionTimeout());

        // Asynchronously report start statistics
        if (configure.getStatUrl() != null) {
            logger().info("arthas stat url: {}", configure.getStatUrl());
        }
        UserStatUtil.setStatUrl(configure.getStatUrl());
        UserStatUtil.setAgentId(configure.getAgentId());
        UserStatUtil.arthasStart();

        try {
            SpyAPI.init();
        } catch (Throwable e) {
            // ignore
        }

        logger().info("as-server started in {} ms", System.currentTimeMillis() - start);
    } catch (Throwable e) {
        logger().error("Error during start as-server", e);
        destroy();
        throw e;
    }
}

private int __read(boolean mayBlock) throws IOException {
    int ch;

    while (true) {
        // If there is no more data AND we were told not to block,
        // just return WOULD_BLOCK (-2). (More efficient than exception.)
        if (!mayBlock && super.available() == 0) {
            return WOULD_BLOCK;
        }

        // Otherwise, exit only when we reach end of stream.
        if ((ch = super.read()) < 0) {
            return EOF;
        }

        ch = (ch & 0xff);

        // Code Section added for supporting AYT (start)
        synchronized (__client) {
            __client._processAYTResponse();
        }
        // Code Section added for supporting AYT (end)

        // Code Section added for supporting spystreams (start)
        __client._spyRead(ch);
        // Code Section added for supporting spystreams (end)

        switch (__receiveState) {
            case _STATE_CR:
                if (ch == '\0') {
                    // Strip null
                    continue;
                }
                // Handle as normal data by falling through to _STATE_DATA case

                //$FALL-THROUGH$
            case _STATE_DATA:
                if (ch == TelnetCommand.IAC) {
                    __receiveState = _STATE_IAC;
                    continue;
                }

                if (ch == '\r') {
                    synchronized (__client) {
                        if (__client._requestedDont(TelnetOption.BINARY)) {
                            __receiveState = _STATE_CR;
                        } else {
                            __receiveState = _STATE_DATA;
                        }
                    }
                } else {
                    __receiveState = _STATE_DATA;
                }
                break;

            case _STATE_IAC:
                switch (ch) {
                    case TelnetCommand.WILL:
                        __receiveState = _STATE_WILL;
                        continue;
                    case TelnetCommand.WONT:
                        __receiveState = _STATE_WONT;
                        continue;
                    case TelnetCommand.DO:
                        __receiveState = _STATE_DO;
                        continue;
                    case TelnetCommand.DONT:
                        __receiveState = _STATE_DONT;
                        continue;
                    // TERMINAL-TYPE option (start)
                    case TelnetCommand.SB:
                        __suboption_count = 0;
                        __receiveState = _STATE_SB;
                        continue;
                    // TERMINAL-TYPE option (end)
                    case TelnetCommand.IAC:
                        __receiveState = _STATE_DATA;
                        break; // exit to enclosing switch to return IAC from read
                    case TelnetCommand.SE: // unexpected byte! ignore it (don't send it as a command)
                        __receiveState = _STATE_DATA;
                        continue;
                    default:
                        __receiveState = _STATE_DATA;
                        __client._processCommand(ch); // Notify the user
                        continue; // move on the next char
                }
                break; // exit and return from read

            case _STATE_WILL:
                synchronized (__client) {
                    __client._processWill(ch);
                    __client._flushOutputStream();
                }
                __receiveState = _STATE_DATA;
                continue;
            case _STATE_WONT:
                synchronized (__client) {
                    __client._processWont(ch);
                    __client._flushOutputStream();
                }
                __receiveState = _STATE_DATA;
                continue;
            case _STATE_DO:
                synchronized (__client) {
                    __client._processDo(ch);
                    __client._flushOutputStream();
                }
                __receiveState = _STATE_DATA;
                continue;
            case _STATE_DONT:
                synchronized (__client) {
                    __client._processDont(ch);
                    __client._flushOutputStream();
                }
                __receiveState = _STATE_DATA;
                continue;
            // TERMINAL-TYPE option (start)
            case _STATE_SB:
                switch (ch) {
                    case TelnetCommand.IAC:
                        __receiveState = _STATE_IAC_SB;
                        continue;
                    default:
                        // store suboption char
                        if (__suboption_count < __suboption.length) {
                            __suboption[__suboption_count++] = ch;
                        }
                        break;
                }
                __receiveState = _STATE_SB;
                continue;
            case _STATE_IAC_SB: // IAC received during SB phase
                switch (ch) {
                    case TelnetCommand.SE:
                        synchronized (__client) {
                            __client._processSuboption(__suboption, __suboption_count);
                            __client._flushOutputStream();
                        }
                        __receiveState = _STATE_DATA;
                        continue;
                    case TelnetCommand.IAC: // De-dup the duplicated IAC
                        if (__suboption_count < __suboption.length) {
                            __suboption[__suboption_count++] = ch;
                        }
                        break;
                    default: // unexpected byte! ignore it
                        break;
                }
                __receiveState = _STATE_SB;
                continue;
            // TERMINAL-TYPE option (end)
        }

        break;
    }

    return ch;
}

private static String formatFileSize(long size) {
    String hrSize;

    double b = size;
    double k = size / 1024.0;
    double m = k / 1024.0;
    double g = m / 1024.0;
    double t = g / 1024.0;

    DecimalFormat dec = new DecimalFormat("0.00");

    if (t > 1) {
        hrSize = dec.format(t).concat(" TB");
    } else if (g > 1) {
        hrSize = dec.format(g).concat(" GB");
    } else if (m > 1) {
        hrSize = dec.format(m).concat(" MB");
    } else if (k > 1) {
        hrSize = dec.format(k).concat(" KB");
    } else {
        hrSize = dec.format(b).concat(" Bytes");
    }

    return hrSize;
}

@Override
public byte[] transform(
        ClassLoader loader,
        String className,
        Class<?> classBeingRedefined,
        ProtectionDomain protectionDomain,
        byte[] classfileBuffer
) throws IllegalClassFormatException {

    if (className == null) {
        return null;
    }

    className = className.replace('/', '.');

    List<RetransformEntry> allRetransformEntries = allRetransformEntries();
    // Reverse order, because configs are applied in order
    ListIterator<RetransformEntry> listIterator = allRetransformEntries.listIterator(allRetransformEntries.size());
    while (listIterator.hasPrevious()) {
        RetransformEntry retransformEntry = listIterator.previous();
        int id = retransformEntry.getId();

        // Check class name match
        boolean updateFlag = false;
        // If class name matches, then check loader if necessary, else match
        if (className.equals(retransformEntry.getClassName())) {
            if (retransformEntry.getClassLoaderClass() != null || retransformEntry.getHashCode() != null) {
                updateFlag = isLoaderMatch(retransformEntry, loader);
            } else {
                updateFlag = true;
            }
        }

        if (updateFlag) {
            logger.info(
                    "RetransformCommand match class: {}, id: {}, classLoaderClass: {}, hashCode: {}",
                    className,
                    id,
                    retransformEntry.getClassLoaderClass(),
                    retransformEntry.getHashCode());
            retransformEntry.incTransformCount();
            return retransformEntry.getBytes();
        }
    }

    return null;
}

public static void drawPlayException(TableElement table, ObjectVO throwableVO) {
    // Execution failed: output failure status
    table.row("IS-RETURN", String.valueOf(false));
    table.row("IS-EXCEPTION", String.valueOf(true));

    // Execution failed: output failure exception info
    Throwable cause;
    Throwable t = (Throwable) throwableVO.getObject();
    if (t instanceof InvocationTargetException) {
        cause = t.getCause();
    } else {
        cause = t;
    }

    if (throwableVO.needExpand()) {
        table.row("THROW-EXCEPTION", new ObjectView(cause, throwableVO.expandOrDefault()).draw());
    } else {
        StringWriter stringWriter = new StringWriter();
        PrintWriter printWriter = new PrintWriter(stringWriter);
        try {
            cause.printStackTrace(printWriter);
            table.row("THROW-EXCEPTION", stringWriter.toString());
        } finally {
            printWriter.close();
        }
    }
}

private AsyncProfiler profilerInstance() {
    if (profiler != null) {
        return profiler;
    }

    // Try to load from special path
    if (ProfilerAction.load.toString().equals(action)) {
        profiler = AsyncProfiler.getInstance(this.actionArg);
    }

    if (libPath != null) {
        // Load from arthas directory
        // Try to copy lib file to temp file to avoid Native Library already loaded in another classloader
        FileOutputStream tmpLibOutputStream = null;
        FileInputStream libInputStream = null;
        try {
            File tmpLibFile = File.createTempFile(VmTool.JNI_LIBRARY_NAME, null);
            tmpLibOutputStream = new FileOutputStream(tmpLibFile);
            libInputStream = new FileInputStream(libPath);

            IOUtils.copy(libInputStream, tmpLibOutputStream);
            libPath = tmpLibFile.getAbsolutePath();
            logger.debug("copy {} to {}", libPath, tmpLibFile);
        } catch (Throwable e) {
            logger.error("try to copy lib error! libPath: {}", libPath, e);
        } finally {
            IOUtils.close(libInputStream);
            IOUtils.close(tmpLibOutputStream);
        }
        profiler = AsyncProfiler.getInstance(libPath);
    } else {
        if (OSUtils.isLinux() || OSUtils.isMac()) {
            throw new IllegalStateException(
                    "Can not find libasyncProfiler so, please check the arthas directory.");
        } else {
            throw new IllegalStateException(
                    "Current OS do not support AsyncProfiler, Only support Linux/Mac.");
        }
    }

    return profiler;
}

    private void doPostprocessing() {
	errCode = ERROR_NOTHING;
	errObject = null;
	Thread thread = Thread.currentThread();

	notifyPostProcessing(1);

	if (DEBUG) {
	    thread.setName("[" + TAG + "]  ps = " + psAlgorithm + "  filename = " + storage.getName());
	}

	Exception exception = null;

	try {
	    psAlgorithm.run(this);
	} catch (Exception err) {
	    Log.e(TAG, "Post-processing failed. " + psAlgorithm.toString(), err);

	    if (err instanceof InterruptedIOException ||
		err instanceof ClosedByInterruptException ||
		thread.isInterrupted()) {
		notifyError(DownloadMission.ERROR_POSTPROCESSING_STOPPED, null);
		return;
	    }

	    if (errCode == ERROR_NOTHING) {
		errCode = ERROR_POSTPROCESSING;
	    }

	    exception = err;
	} finally {
	    notifyPostProcessing(errCode == ERROR_NOTHING ? 2 : 0);
	}

	if (errCode != ERROR_NOTHING) {
	    if (exception == null) {
		exception = errObject;
	    }
	    notifyError(ERROR_POSTPROCESSING, exception);
	    return;
	}

	notifyFinished();
    }

    void establishConnection(int threadId, HttpURLConnection conn) throws IOException, HttpError {
	int statusCode = conn.getResponseCode();

	if (DEBUG) {
	    Log.d(TAG, threadId + ":[request]  Range=" + conn.getRequestProperty("Range"));
	    Log.d(TAG, threadId + ":[response] Code=" + statusCode);
	    Log.d(TAG, threadId + ":[response] Content-Length=" + conn.getContentLength());
	    Log.d(TAG, threadId + ":[response] Content-Range=" + conn.getHeaderField("Content-Range"));
	}

	switch (statusCode) {
	case 204:
	case 205:
	case 207:
	    throw new HttpError(statusCode);
	case 416:
	    return; // let the download thread handle this error
	default:
	    if (statusCode < 200 || statusCode > 299) {
		throw new HttpError(statusCode);
	    }
	}
    }

    private Elst parseEdts(final Box ref) throws IOException {
	final Box b = untilBox(ref, ATOM_ELST);
	if (b == null) {
	    return null;
	}

	final Elst obj = new Elst();

	final boolean v1 = stream.read() == 1;
	stream.skipBytes(3); // flags

	final int entryCount = stream.readInt();
	if (entryCount < 1) {
	    obj.bMediaRate = 0x00010000; // default media rate (1.0)
	    return obj;
	}

	if (v1) {
	    stream.skipBytes(DataReader.LONG_SIZE); // segment duration
	    obj.mediaTime = stream.readLong();
	    // ignore all remain entries
	    stream.skipBytes((entryCount - 1) * (DataReader.LONG_SIZE * 2));
	} else {
	    stream.skipBytes(DataReader.INTEGER_SIZE); // segment duration
	    obj.mediaTime = stream.readInt();
	}

	obj.bMediaRate = stream.readInt();

	return obj;
    }

    @Override
    public void run() {
	boolean done;
	long start = mMission.fallbackResumeOffset;

	if (DEBUG && !mMission.unknownLength && start > 0) {
	    Log.i(TAG, "Resuming a single-thread download at " + start);
	}

	try {
	    long rangeStart = (mMission.unknownLength || start < 1) ? -1 : start;

	    int mId = 1;
	    mConn = mMission.openConnection(false, rangeStart, -1);

	    if (mRetryCount == 0 && rangeStart == -1) {
		// workaround: bypass android connection pool
		mConn.setRequestProperty("Range", "bytes=0-");
	    }

	    mMission.establishConnection(mId, mConn);

	    // check if the download can be resumed
	    if (mConn.getResponseCode() == 416 && start > 0) {
		mMission.notifyProgress(-start);
		start = 0;
		mRetryCount--;
		throw new DownloadMission.HttpError(416);
	    }

	    // secondary check for the file length
	    if (!mMission.unknownLength) {
		mMission.unknownLength = Utility.getContentLength(mConn) == -1;
	    }

	    if (mMission.unknownLength || mConn.getResponseCode() == 200) {
		// restart amount of bytes downloaded
		mMission.done = mMission.offsets[mMission.current] - mMission.offsets[0];
	    }

	    mF = mMission.storage.getStream();
	    mF.seek(mMission.offsets[mMission.current] + start);

	    mIs = mConn.getInputStream();

	    byte[] buf = new byte[DownloadMission.BUFFER_SIZE];
	    int len = 0;

	    while (mMission.running && (len = mIs.read(buf, 0, buf.length)) != -1) {
		mF.write(buf, 0, len);
		start += len;
		mMission.notifyProgress(len);
	    }

	    dispose();

	    // if thread goes interrupted check if the last part is written. This avoid re-download the whole file
	    done = len == -1;
	} catch (Exception e) {
	    dispose();

	    mMission.fallbackResumeOffset = start;

	    if (!mMission.running || e instanceof ClosedByInterruptException) {
		return;
	    }

	    if (e instanceof HttpError && ((HttpError) e).statusCode == ERROR_HTTP_FORBIDDEN) {
		// for youtube streams. The url has expired, recover
		dispose();
		mMission.doRecover(ERROR_HTTP_FORBIDDEN);
		return;
	    }

	    if (mRetryCount++ >= mMission.maxRetry) {
		mMission.notifyError(e);
		return;
	    }

	    if (DEBUG) {
		Log.e(TAG, "got exception, retrying...", e);
	    }

	    run(); // try again
	    return;
	}

	if (done) {
	    mMission.notifyFinished();
	} else {
	    mMission.fallbackResumeOffset = start;
	}
    }

    private void flushAuxiliar(long amount) throws IOException {
	if (aux.length < 1) {
	    return;
	}

	out.flush();
	aux.flush();

	boolean underflow = aux.offset < aux.length || out.offset < out.length;
	byte[] buffer = new byte[COPY_BUFFER_SIZE];

	aux.target.seek(0);
	out.target.seek(out.length);

	long length = amount;
	while (length > 0) {
	    int read = (int) Math.min(length, Integer.MAX_VALUE);
	    read = aux.target.read(buffer, 0, Math.min(read, buffer.length));

	    if (read < 1) {
		amount -= length;
		break;
	    }

	    out.writeProof(buffer, read);
	    length -= read;
	}

	if (underflow) {
	    if (out.offset >= out.length) {
		// calculate the aux underflow pointer
		if (aux.offset < amount) {
		    out.offset += aux.offset;
		    aux.offset = 0;
		    out.target.seek(out.offset);
		} else {
		    aux.offset -= amount;
		    out.offset = out.length + amount;
		}
	    } else {
		aux.offset = 0;
	    }
	} else {
	    out.offset += amount;
	    aux.offset -= amount;
	}

	out.length += amount;

	if (out.length > maxLengthKnown) {
	    maxLengthKnown = out.length;
	}

	if (amount < aux.length) {
	    // move the excess data to the beginning of the file
	    long readOffset = amount;
	    long writeOffset = 0;

	    aux.length -= amount;
	    length = aux.length;
	    while (length > 0) {
		int read = (int) Math.min(length, Integer.MAX_VALUE);
		read = aux.target.read(buffer, 0, Math.min(read, buffer.length));

		aux.target.seek(writeOffset);
		aux.writeProof(buffer, read);

		writeOffset += read;
		readOffset += read;
		length -= read;

		aux.target.seek(readOffset);
	    }

	    aux.target.setLength(aux.length);
	    return;
	}

	if (aux.length > THRESHOLD_AUX_LENGTH) {
	    aux.target.setLength(THRESHOLD_AUX_LENGTH); // or setLength(0);
	}

	aux.reset();
    }

    private void showError(DownloadMission mission, UserAction action, @StringRes int reason) {
	StringBuilder request = new StringBuilder(256);
	request.append(mission.source);

	request.append(" [");
	if (mission.recoveryInfo != null) {
	    for (MissionRecoveryInfo recovery : mission.recoveryInfo) {
		request.append(' ')
		       .append(recovery.toString())
		       .append(' ');
	    }
	}
	request.append("]");

	String service;
	try {
	    service = NewPipe.getServiceByUrl(mission.source).getServiceInfo().getName();
	} catch (Exception e) {
	    service = ErrorInfo.SERVICE_NONE;
	}

	ErrorUtil.createNotification(
	    mContext,
	    new ErrorInfo(ErrorInfo.Companion.throwableToStringList(mission.errObject),
			  action,
			  service,
			  request.toString(),
			  reason));
    }

    void startMission(DownloadMission mission) {
	synchronized (this) {
	    mission.timestamp = System.currentTimeMillis();
	    mission.mHandler = mHandler;
	    mission.maxRetry = mPrefMaxRetry;

	    // create metadata file
	    while (true) {
		mission.metadata = new File(mPendingMissionsDir, String.valueOf(mission.timestamp));
		if (!mission.metadata.isFile() && !mission.metadata.exists()) {
		    try {
			if (!mission.metadata.createNewFile()) {
			    throw new RuntimeException("Cant create download metadata file");
			}
		    } catch (IOException e) {
			throw new RuntimeException(e);
		    }
		    break;
		}
		mission.timestamp = System.currentTimeMillis();
	    }

	    mSelfMissionsControl = true;
	    mMissionsPending.add(mission);

	    // Before continue, save the metadata in case the internet connection is not available
	    Utility.writeToFile(mission.metadata, mission);

	    if (mission.storage == null) {
		// noting to do here
		mission.errCode = DownloadMission.ERROR_FILE_CREATION;
		if (mission.errObject != null) {
		    mission.errObject = new IOException("DownloadMission.storage == NULL");
		}
		return;
	    }

	    boolean start = !mPrefQueueLimit || getRunningMissionsCount() < 1;

	    if (canDownloadInCurrentNetwork() && start) {
		mission.start();
	    }
	}
    }

    @Override
    int process(SharpStream out, SharpStream... sources) throws IOException {
	// check if the subtitle is already in srt and copy, this should never happen
	String format = getArgumentAt(0, null);
	boolean ignoreEmptyFrames = getArgumentAt(1, "true").equals("true");

	if (format == null || format.equals("ttml")) {
	    SrtFromTtmlWriter writer = new SrtFromTtmlWriter(out, ignoreEmptyFrames);

	    try {
		writer.build(sources[0]);
	    } catch (Exception err) {
		Log.e(TAG, "subtitle parse failed", err);
		return (err instanceof IOException) ? 1 : 8;
	    }

	    return OK_RESULT;
	} else if (format.equals("srt")) {
	    byte[] buffer = new byte[8 * 1024];
	    int read;
	    while ((read = sources[0].read(buffer)) > 0) {
		out.write(buffer, 0, read);
	    }
	    return OK_RESULT;
	}

	throw new UnsupportedOperationException(
	    "Can't convert this subtitle, unimplemented format: " + format);
    }

    private void joinForThreads(int millis) {
	final Thread currentThread = Thread.currentThread();

	if (init != null && init != currentThread && init.isAlive()) {
	    init.interrupt();

	    if (millis > 0) {
		try {
		    init.join(millis);
		} catch (InterruptedException e) {
		    Log.w(TAG, "Initializer thread is still running", e);
		    return;
		}
	    }
	}

	// if a thread is still alive, possible reasons:
	//      slow device
	//      the user is spamming start/pause buttons
	//      start() method called quickly after pause()

	for (Thread thread : threads) {
	    if (!thread.isAlive() || thread == Thread.currentThread()) {
		continue;
	    }
	    thread.interrupt();
	}

	try {
	    for (Thread thread : threads) {
		if (!thread.isAlive()) {
		    continue;
		}
		if (DEBUG) {
		    Log.w(TAG, "thread alive: " + thread.getName());
		}
		if (millis > 0) {
		    thread.join(millis);
		}
	    }
	} catch (InterruptedException e) {
	    throw new RuntimeException("A download thread is still running", e);
	}
    }

@SuppressLint("SwitchIntDef") // only fit, fill and zoom are supported by NewPipe
@ResizeMode
public static int nextResizeModeAndSaveToPrefs(final Player player,
        @ResizeMode final int resizeMode) {
    final int newResizeMode;
    switch (resizeMode) {
    case AspectRatioFrameLayout.RESIZE_MODE_FIT:
        newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FILL;
        break;
    case AspectRatioFrameLayout.RESIZE_MODE_FILL:
        newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_ZOOM;
        break;
    case AspectRatioFrameLayout.RESIZE_MODE_ZOOM:
    default:
        newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FIT;
        break;
    }

    // save the new resize mode so it can be restored in a future session
    player.getPrefs().edit().putInt(
            player.getContext().getString(R.string.last_resize_mode), newResizeMode).apply();
    return newResizeMode;
}

@Override
public void onBroadcastReceived(final Intent intent) {
    super.onBroadcastReceived(intent);
    if (Intent.ACTION_CONFIGURATION_CHANGED.equals(intent.getAction())) {
        // Close it because when changing orientation from portrait
        // (in fullscreen mode) the size of queue layout can be larger than the screen size
        closeItemsList();
    } else if (ACTION_PLAY_PAUSE.equals(intent.getAction())) {
        // Ensure that we have audio-only stream playing when a user
        // started to play from notification's play button from outside of the app
        if (!fragmentIsVisible) {
            onFragmentStopped();
        }
    } else if (VideoDetailFragment.ACTION_VIDEO_FRAGMENT_STOPPED.equals(intent.getAction())) {
        fragmentIsVisible = false;
        onFragmentStopped();
    } else if (VideoDetailFragment.ACTION_VIDEO_FRAGMENT_RESUMED.equals(intent.getAction())) {
        // Restore video source when user returns to the fragment
        fragmentIsVisible = true;
        player.useVideoSource(true);

        // When a user returns from background, the system UI will always be shown even if
        // controls are invisible: hide it in that case
        if (!isControlsVisible()) {
            hideSystemUIIfNeeded();
        }
    }
}

@StyleRes
public static int getSettingsThemeStyle(final Context context) {
    final Resources res = context.getResources();
    final String lightTheme = res.getString(R.string.light_theme_key);
    final String blackTheme = res.getString(R.string.black_theme_key);
    final String automaticDeviceTheme = res.getString(R.string.auto_device_theme_key);

    final String selectedTheme = getSelectedThemeKey(context);

    if (selectedTheme.equals(lightTheme)) {
        return R.style.LightSettingsTheme;
    } else if (selectedTheme.equals(blackTheme)) {
        return R.style.BlackSettingsTheme;
    } else if (selectedTheme.equals(automaticDeviceTheme)) {
        if (isDeviceDarkThemeEnabled(context)) {
            // use the dark theme variant preferred by the user
            final String selectedNightTheme = getSelectedNightThemeKey(context);
            if (selectedNightTheme.equals(blackTheme)) {
                return R.style.BlackSettingsTheme;
            } else {
                return R.style.DarkSettingsTheme;
            }
        } else {
            // there is only one day theme
            return R.style.LightSettingsTheme;
        }
    } else {
        // default to dark theme
        return R.style.DarkSettingsTheme;
    }
}

@Override
public Bitmap transform(final Bitmap source) {
    if (DEBUG) {
        Log.d(TAG, "Thumbnail - transform() called");
    }

    final float notificationThumbnailWidth = Math.min(
            context.getResources()
                    .getDimension(R.dimen.player_notification_thumbnail_width),
            source.getWidth());

    final Bitmap result = BitmapCompat.createScaledBitmap(
            source,
            (int) notificationThumbnailWidth,
            (int) (source.getHeight()
                    / (source.getWidth() / notificationThumbnailWidth)),
            null,
            true);

    if (result == source || !result.isMutable()) {
        // create a new mutable bitmap to prevent strange crashes on some
        // devices (see #4638)
        final Bitmap copied = BitmapCompat.createScaledBitmap(
                source,
                (int) notificationThumbnailWidth - 1,
                (int) (source.getHeight() / (source.getWidth()
                        / (notificationThumbnailWidth - 1))),
                null,
                true);
        source.recycle();
        return copied;
    } else {
        source.recycle();
        return result;
    }
}

private void onBroadcastReceived(final Intent intent) {
    if (intent == null || intent.getAction() == null) {
        return;
    }

    if (DEBUG) {
        Log.d(TAG, "onBroadcastReceived() called with: intent = [" + intent + "]");
    }

    switch (intent.getAction()) {
    case AudioManager.ACTION_AUDIO_BECOMING_NOISY:
        pause();
        break;
    case ACTION_CLOSE:
        service.destroyPlayerAndStopService();
        break;
    case ACTION_PLAY_PAUSE:
        playPause();
        break;
    case ACTION_PLAY_PREVIOUS:
        playPrevious();
        break;
    case ACTION_PLAY_NEXT:
        playNext();
        break;
    case ACTION_FAST_REWIND:
        fastRewind();
        break;
    case ACTION_FAST_FORWARD:
        fastForward();
        break;
    case ACTION_REPEAT:
        cycleNextRepeatMode();
        break;
    case ACTION_SHUFFLE:
        toggleShuffleModeEnabled();
        break;
    case Intent.ACTION_CONFIGURATION_CHANGED:
        if (DEBUG) {
            Log.d(TAG, "ACTION_CONFIGURATION_CHANGED received");
        }
        break;
    }

    UIs.call(playerUi -> playerUi.onBroadcastReceived(intent));
}

private static void handleError(final Context context, final ErrorInfo errorInfo) {
    if (errorInfo.getThrowable() != null) {
        errorInfo.getThrowable().printStackTrace();
    }

    if (errorInfo.getThrowable() instanceof ReCaptchaException) {
        Toast.makeText(context, R.string.recaptcha_request_toast, Toast.LENGTH_LONG).show();
        // Starting ReCaptcha Challenge Activity
        final Intent intent = new Intent(context, ReCaptchaActivity.class);
        intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK);
        context.startActivity(intent);
    } else if (errorInfo.getThrowable() != null
            && ExceptionUtils.isNetworkRelated(errorInfo.getThrowable())) {
        Toast.makeText(context, R.string.network_error, Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof AgeRestrictedContentException) {
        Toast.makeText(context, R.string.restricted_video_no_stream,
                Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof GeographicRestrictionException) {
        Toast.makeText(context, R.string.georestricted_content, Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof PaidContentException) {
        Toast.makeText(context, R.string.paid_content, Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof PrivateContentException) {
        Toast.makeText(context, R.string.private_content, Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof SoundCloudGoPlusContentException) {
        Toast.makeText(context, R.string.soundcloud_go_plus_content,
                Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof YoutubeMusicPremiumContentException) {
        Toast.makeText(context, R.string.youtube_music_premium_content,
                Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof ContentNotAvailableException) {
        Toast.makeText(context, R.string.content_not_available, Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof ContentNotSupportedException) {
        Toast.makeText(context, R.string.content_not_supported, Toast.LENGTH_LONG).show();
    } else {
        ErrorUtil.createNotification(context, errorInfo);
    }

    if (context instanceof RouterActivity) {
        ((RouterActivity) context).finish();
    }
}

public static void openVideoDetailFragment(@NonNull final Context context,
        @NonNull final FragmentManager fragmentManager,
        final int serviceId,
        @Nullable final String url,
        @NonNull final String title,
        @Nullable final PlayQueue playQueue,
        final boolean switchingPlayers) {

    final boolean autoPlay;
    @Nullable final PlayerType playerType = PlayerHolder.getInstance().getType();
    if (playerType == null) {
        // no player open
        autoPlay = PlayerHelper.isAutoplayAllowedByUser(context);
    } else if (switchingPlayers) {
        // switching player to main player
        autoPlay = PlayerHolder.getInstance().isPlaying(); // keep play/pause state
    } else if (playerType == PlayerType.MAIN) {
        // opening new stream while already playing in main player
        autoPlay = PlayerHelper.isAutoplayAllowedByUser(context);
    } else {
        // opening new stream while already playing in another player
        autoPlay = false;
    }

    final RunnableWithVideoDetailFragment onVideoDetailFragmentReady = detailFragment -> {
        expandMainPlayer(detailFragment.requireActivity());
        detailFragment.setAutoPlay(autoPlay);
        if (switchingPlayers) {
            // Situation when user switches from players to main player. All needed data is
            // here, we can start watching (assuming newQueue equals playQueue).
            // Starting directly in fullscreen if the previous player type was popup.
            detailFragment.openVideoPlayer(playerType == PlayerType.POPUP
                    || PlayerHelper.isStartMainPlayerFullscreenEnabled(context));
        } else {
            detailFragment.selectAndLoadVideo(serviceId, url, title, playQueue);
        }
        detailFragment.scrollToTop();
    };

    final Fragment fragment = fragmentManager.findFragmentById(R.id.fragment_player_holder);
    if (fragment instanceof VideoDetailFragment && fragment.isVisible()) {
        onVideoDetailFragmentReady.run((VideoDetailFragment) fragment);
    } else {
        // Specify no url here, otherwise the VideoDetailFragment will start loading the
        // stream automatically if it's the first time it is being opened, but then
        // onVideoDetailFragmentReady will kick in and start another loading process.
        // See VideoDetailFragment.wasCleared() and its usage in doInitialLoadLogic().
        final VideoDetailFragment instance = VideoDetailFragment
                .getInstance(serviceId, null, title, playQueue);
        instance.setAutoPlay(autoPlay);

        defaultTransaction(fragmentManager)
                .replace(R.id.fragment_player_holder, instance)
                .runOnCommit(() -> onVideoDetailFragmentReady.run(instance))
                .commit();
    }
}

@Override
public void handleResult(@NonNull final ChannelInfo result) {
    super.handleResult(result);
    currentInfo = result;
    setInitialData(result.getServiceId(), result.getOriginalUrl(), result.getName());

    if (ImageStrategy.shouldLoadImages() && !result.getBanners().isEmpty()) {
        PicassoHelper.loadBanner(result.getBanners()).tag(PICASSO_CHANNEL_TAG)
                .into(binding.channelBannerImage);
    } else {
        // do not waste space for the banner, if the user disabled images or there is not one
        binding.channelBannerImage.setImageDrawable(null);
    }

    PicassoHelper.loadAvatar(result.getAvatars()).tag(PICASSO_CHANNEL_TAG)
            .into(binding.channelAvatarView);
    PicassoHelper.loadAvatar(result.getParentChannelAvatars()).tag(PICASSO_CHANNEL_TAG)
            .into(binding.subChannelAvatarView);

    binding.channelTitleView.setText(result.getName());
    binding.channelSubscriberView.setVisibility(View.VISIBLE);
    if (result.getSubscriberCount() >= 0) {
        binding.channelSubscriberView.setText(Localization
                .shortSubscriberCount(activity, result.getSubscriberCount()));
    } else {
        binding.channelSubscriberView.setText(R.string.subscribers_count_not_available);
    }

    if (!TextUtils.isEmpty(currentInfo.getParentChannelName())) {
        binding.subChannelTitleView.setText(String.format(
                getString(R.string.channel_created_by),
                currentInfo.getParentChannelName())
        );
        binding.subChannelTitleView.setVisibility(View.VISIBLE);
        binding.subChannelAvatarView.setVisibility(View.VISIBLE);
    }

    updateRssButton();

    channelContentNotSupported = false;
    for (final Throwable throwable : result.getErrors()) {
        if (throwable instanceof ContentNotSupportedException) {
            channelContentNotSupported = true;
            showContentNotSupportedIfNeeded();
            break;
        }
    }

    disposables.clear();
    if (subscribeButtonMonitor != null) {
        subscribeButtonMonitor.dispose();
    }

    updateTabs();
    updateSubscription(result);
    monitorSubscription(result);
}

@Override
public void onTextTracksChanged(@NonNull final Tracks currentTracks) {
    super.onTextTracksChanged(currentTracks);

    final boolean trackTypeTextSupported = !currentTracks.containsType(C.TRACK_TYPE_TEXT)
            || currentTracks.isTypeSupported(C.TRACK_TYPE_TEXT, false);
    if (getPlayer().getTrackSelector().getCurrentMappedTrackInfo() == null
            || !trackTypeTextSupported) {
        binding.captionTextView.setVisibility(View.GONE);
        return;
    }

    // Extract all loaded languages
    final List<Tracks.Group> textTracks = currentTracks
            .getGroups()
            .stream()
            .filter(trackGroupInfo -> C.TRACK_TYPE_TEXT == trackGroupInfo.getType())
            .collect(Collectors.toList());
    final List<String> availableLanguages = textTracks.stream()
            .map(Tracks.Group::getMediaTrackGroup)
            .filter(textTrack -> textTrack.length > 0)
            .map(textTrack -> textTrack.getFormat(0).language)
            .collect(Collectors.toList());

    // Find selected text track
    final Optional<Format> selectedTracks = textTracks.stream()
            .filter(Tracks.Group::isSelected)
            .filter(info -> info.getMediaTrackGroup().length >= 1)
            .map(info -> info.getMediaTrackGroup().getFormat(0))
            .findFirst();

    // Build UI
    buildCaptionMenu(availableLanguages);
    if (player.getTrackSelector().getParameters().getRendererDisabled(
            player.getCaptionRendererIndex()) || selectedTracks.isEmpty()) {
        binding.captionTextView.setText(R.string.caption_none);
    } else {
        binding.captionTextView.setText(selectedTracks.get().language);
    }
    binding.captionTextView.setVisibility(
            availableLanguages.isEmpty() ? View.GONE : View.VISIBLE);
}

