public static Map<String, String> toStringMap(
        Map<String, Object> input, boolean stringEscape) {
    Map<String, String> output = new HashMap<>(input.size());
    for (Map.Entry<String, Object> entry : input.entrySet()) {
        String key = entry.getKey();
        Object obj = entry.getValue();
        if (key == null || obj == null) {
            throw new NullPointerException("A null argument cannot be sent to Redis.");
        }
        String str;
        if (obj instanceof byte[] bytes) {
            str = SafeEncoder.encode(bytes);
        } else if (obj instanceof redis.clients.jedis.GeoCoordinate geo) {
            str = geo.getLongitude() + "," + geo.getLatitude();
        } else if (obj instanceof String s) {
            str = stringEscape ? escape(s) : s;
        } else {
            str = String.valueOf(obj);
        }
        output.put(key, str);
    }
    return output;
}

