@Override
public Connection getConnection() {
    final List<ConnectionPool> pools = getShuffledNodesPool();

    JedisException suppressed = null;
    for (ConnectionPool pool : pools) {
        Connection jedis = null;
        try {
            jedis = pool.getResource();
            if (jedis == null) {
                continue;
            }

            jedis.ping();
            return jedis;

        } catch (JedisException ex) {
            if (suppressed == null) { // remembering first suppressed exception
                suppressed = ex;
            }
            if (jedis != null) {
                jedis.close();
            }
        }
    }

    final JedisException noReachableNode = new JedisException("No reachable shard.");
    if (suppressed != null) {
        noReachableNode.addSuppressed(suppressed);
    }
    throw noReachableNode;
}

@Override
public CacheEntry set(CacheKey cacheKey, CacheEntry entry) {
    lock.lock();
    try {
        entry = putIntoStore(cacheKey, entry);
        final EvictionPolicy policy = getEvictionPolicy();
        policy.touch(cacheKey);
        final CacheKey evictedKey = policy.evictNext();
        if (evictedKey != null) {
            delete(evictedKey);
            stats.evict();
        }
        for (Object redisKey : cacheKey.getRedisKeys()) {
            final ByteBuffer mapKey = makeKeyForRedisKeysToCacheKeys(redisKey);
            if (redisKeysToCacheKeys.containsKey(mapKey)) {
                redisKeysToCacheKeys.get(mapKey).add(cacheKey);
            } else {
                final Set<CacheKey<?>> set = ConcurrentHashMap.newKeySet();
                set.add(cacheKey);
                redisKeysToCacheKeys.put(mapKey, set);
            }
        }
        stats.load();
        return entry;
    } finally {
        lock.unlock();
    }
}

@Override
public Class<?> build(Object data) {
    if (data == null) return null;
    final String str = STRING.build(data);
    switch (str) {
        case "null":
            return null;
        case "boolean":
            return boolean.class;
        case "integer":
            return int.class;
        case "number":
            return float.class;
        case "string":
            return String.class;
        case "object":
            return Object.class;
        case "array":
            return List.class;
        default:
            throw new JedisException("Unknown type: " + str);
    }
}

