        scheduledWorkRequest.run();
    }
}

@Override
public void close() throws IOException {
    boolean triedToClose = false;
    boolean success = false;
    try {
        flush();
        ((FileOutputStream) out).getFD().sync();

        triedToClose = true;
        super.close();
        success = true;
    } finally {
        if (success) {
            final boolean renamed = tmpFile.renameTo(origFile);
            if (!renamed) {
                // On windows, renameTo does not replace.
                if (!origFile.delete() || !tmpFile.renameTo(origFile)) {
                    throw new IOException("Could not rename temporary file " + tmpFile + " to " + origFile);
                }
            }
        } else {
            if (!triedToClose) {
                // If we failed when flushing, try to close it to not leak
                // an FD
                IOUtils.closeStream(out);
            }
            // close wasn't successful, try to delete the tmp file
            if (!tmpFile.delete()) {
                LOG.warn("Unable to delete tmp file {}", tmpFile);
            }
        }
    }
}

private synchronized LogEntry readNextEntry() {
    try {
        try {
            while (true) {
                final String line = in.readLine();
                if (line == null) {
                    break;
                }

                Matcher m = src.timep.matcher(line);
                if (m.lookingAt()) {
                    if (buf.length() > 0) {
                        final LogEntry e = new Log4JEntry(
                                src.timestampFromText(dateformat, buf),
                                src.getServerId(),
                                buf);
                        buf = line;
                        return e;
                    }
                    buf = line;
                } else if (buf.length() > 0) {
                    buf += line + "\n";
                }
            }
        } catch (final EOFException eof) {
            // ignore, we've simply come to the end of the file
        }
        if (buf.length() > 0) {
            final LogEntry e = new Log4JEntry(
                    src.timestampFromText(dateformat, buf),
                    src.getServerId(),
                    buf);
            buf = "";
            return e;
        }
    } catch (final Exception e) {
        LOG.error("Error reading next entry in file (" + src.file + "): " + e);
        return null;
    }
    return null;
}

public static void main(final String[] args) {
    if (args.length < 3) {
        usage();
        return;
    }
    final String host = args[0];
    final int port = Integer.parseInt(args[1]);
    final String op = args[2];
    if (op.equalsIgnoreCase("gettracemask")) {
        getTraceMask(host, port);
    } else if (op.equalsIgnoreCase("settracemask")) {
        setTraceMask(host, port, args[3]);
    } else if (op.equalsIgnoreCase("ruok")) {
        ruok(host, port);
    } else if (op.equalsIgnoreCase("kill")) {
        kill(host, port);
    } else if (op.equalsIgnoreCase("stat")) {
        stat(host, port);
    } else if (op.equalsIgnoreCase("dump")) {
        dump(host, port);
    } else {
        System.out.println("Unrecognized op: " + op);
    }
}

private void doPostprocessing() {
    errCode = ERROR_NOTHING;
    errObject = null;
    Thread thread = Thread.currentThread();

    notifyPostProcessing(1);

    if (DEBUG) {
        thread.setName(
                "[" + TAG + "]  ps = " + psAlgorithm + "  filename = " + storage.getName());
    }

    Exception exception = null;

    try {
        psAlgorithm.run(this);
    } catch (Exception err) {
        Log.e(TAG, "Post-processing failed. " + psAlgorithm.toString(), err);

        if (err instanceof InterruptedIOException
                || err instanceof ClosedByInterruptException
                || thread.isInterrupted()) {
            notifyError(DownloadMission.ERROR_POSTPROCESSING_STOPPED, null);
            return;
        }

        if (errCode == ERROR_NOTHING) {
            errCode = ERROR_POSTPROCESSING;
        }

        exception = err;
    } finally {
        notifyPostProcessing(errCode == ERROR_NOTHING ? 2 : 0);
    }

    if (errCode != ERROR_NOTHING) {
        if (exception == null) {
            exception = errObject;
        }
        notifyError(ERROR_POSTPROCESSING, exception);
        return;
    }

    notifyFinished();
}

void establishConnection(int threadId, HttpURLConnection conn) throws IOException, HttpError {
    int statusCode = conn.getResponseCode();

    if (DEBUG) {
        Log.d(TAG, threadId + ":[request]  Range=" + conn.getRequestProperty("Range"));
        Log.d(TAG, threadId + ":[response] Code=" + statusCode);
        Log.d(TAG, threadId + ":[response] Content-Length=" + conn.getContentLength());
        Log.d(TAG, threadId + ":[response] Content-Range=" + conn.getHeaderField("Content-Range"));
    }

    switch (statusCode) {
        case 204, 205, 207 -> throw new HttpError(statusCode);
        case 416 -> {
            // let the download thread handle this error
            return;
        }
        default -> {
            if (statusCode < 200 || statusCode > 299) {
                throw new HttpError(statusCode);
            }
        }
    }
}

private Elst parseEdts(final Box ref) throws IOException {
    final Box b = untilBox(ref, ATOM_ELST);
    if (b == null) {
        return null;
    }

    final Elst obj = new Elst();

    final boolean v1 = stream.read() == 1;
    stream.skipBytes(3); // flags

    final int entryCount = stream.readInt();
    if (entryCount < 1) {
        obj.bMediaRate = 0x00010000; // default media rate (1.0)
        return obj;
    }

    if (v1) {
        stream.skipBytes(DataReader.LONG_SIZE); // segment duration
        obj.mediaTime = stream.readLong();
        // ignore all remain entries
        stream.skipBytes((entryCount - 1) * (DataReader.LONG_SIZE * 2));
    } else {
        stream.skipBytes(DataReader.INTEGER_SIZE); // segment duration
        obj.mediaTime = stream.readInt();
    }

    obj.bMediaRate = stream.readInt();

    return obj;
}

@Override
public void run() {
    boolean done;
    long start = mMission.fallbackResumeOffset;

    if (DEBUG && !mMission.unknownLength && start > 0) {
        Log.i(TAG, "Resuming a single-thread download at " + start);
    }

    try {
        long rangeStart = (mMission.unknownLength || start < 1) ? -1 : start;

        int mId = 1;
        mConn = mMission.openConnection(false, rangeStart, -1);

        if (mRetryCount == 0 && rangeStart == -1) {
            // workaround: bypass android connection pool
            mConn.setRequestProperty("Range", "bytes=0-");
        }

        mMission.establishConnection(mId, mConn);

        // check if the download can be resumed
        if (mConn.getResponseCode() == 416 && start > 0) {
            mMission.notifyProgress(-start);
            start = 0;
            mRetryCount--;
            throw new DownloadMission.HttpError(416);
        }

        // secondary check for the file length
        if (!mMission.unknownLength) {
            mMission.unknownLength = Utility.getContentLength(mConn) == -1;
        }

        if (mMission.unknownLength || mConn.getResponseCode() == 200) {
            // restart amount of bytes downloaded
            mMission.done = mMission.offsets[mMission.current] - mMission.offsets[0];
        }

        mF = mMission.storage.getStream();
        mF.seek(mMission.offsets[mMission.current] + start);

        mIs = mConn.getInputStream();

        byte[] buf = new byte[DownloadMission.BUFFER_SIZE];
        int len = 0;

        while (mMission.running && (len = mIs.read(buf, 0, buf.length)) != -1) {
            mF.write(buf, 0, len);
            start += len;
            mMission.notifyProgress(len);
        }

        dispose();

        // if thread goes interrupted check if the last part is written. This avoid re-download the whole file
        done = len == -1;
    } catch (Exception e) {
        dispose();

        mMission.fallbackResumeOffset = start;

        if (!mMission.running || e instanceof ClosedByInterruptException) {
            return;
        }

        if (e instanceof HttpError && ((HttpError) e).statusCode == ERROR_HTTP_FORBIDDEN) {
            // for youtube streams. The url has expired, recover
            dispose();
            mMission.doRecover(ERROR_HTTP_FORBIDDEN);
            return;
        }

        if (mRetryCount++ >= mMission.maxRetry) {
            mMission.notifyError(e);
            return;
        }

        if (DEBUG) {
            Log.e(TAG, "got exception, retrying...", e);
        }

        run(); // try again
        return;
    }

    if (done) {
        mMission.notifyFinished();
    } else {
        mMission.fallbackResumeOffset = start;
    }
}

private void flushAuxiliar(long amount) throws IOException {
    if (aux.length < 1) {
        return;
    }

    out.flush();
    aux.flush();

    boolean underflow = aux.offset < aux.length || out.offset < out.length;
    byte[] buffer = new byte[COPY_BUFFER_SIZE];

    aux.target.seek(0);
    out.target.seek(out.length);

    long length = amount;
    while (length > 0) {
        int read = (int) Math.min(length, Integer.MAX_VALUE);
        read = aux.target.read(buffer, 0, Math.min(read, buffer.length));

        if (read < 1) {
            amount -= length;
            break;
        }

        out.writeProof(buffer, read);
        length -= read;
    }

    if (underflow) {
        if (out.offset >= out.length) {
            // calculate the aux underflow pointer
            if (aux.offset < amount) {
                out.offset += aux.offset;
                aux.offset = 0;
                out.target.seek(out.offset);
            } else {
                aux.offset -= amount;
                out.offset = out.length + amount;
            }
        } else {
            aux.offset = 0;
        }
    } else {
        out.offset += amount;
        aux.offset -= amount;
    }

    out.length += amount;

    if (out.length > maxLengthKnown) {
        maxLengthKnown = out.length;
    }

    if (amount < aux.length) {
        // move the excess data to the beginning of the file
        long readOffset = amount;
        long writeOffset = 0;

        aux.length -= amount;
        length = aux.length;
        while (length > 0) {
            int read = (int) Math.min(length, Integer.MAX_VALUE);
            read = aux.target.read(buffer, 0, Math.min(read, buffer.length));

            aux.target.seek(writeOffset);
            aux.writeProof(buffer, read);

            writeOffset += read;
            readOffset += read;
            length -= read;

            aux.target.seek(readOffset);
        }

        aux.target.setLength(aux.length);
        return;
    }

    if (aux.length > THRESHOLD_AUX_LENGTH) {
        aux.target.setLength(THRESHOLD_AUX_LENGTH); // or setLength(0);
    }

    aux.reset();
}

private void showError(
        DownloadMission mission, UserAction action, @StringRes int reason) {
    StringBuilder request = new StringBuilder(256);
    request.append(mission.source);

    request.append(" [");
    if (mission.recoveryInfo != null) {
        for (MissionRecoveryInfo recovery : mission.recoveryInfo) {
            request.append(' ')
                    .append(recovery.toString())
                    .append(' ');
        }
    }
    request.append("]");

    String service;
    try {
        service = NewPipe.getServiceByUrl(mission.source).getServiceInfo().getName();
    } catch (Exception e) {
        service = ErrorInfo.SERVICE_NONE;
    }

    ErrorUtil.createNotification(
            mContext,
            new ErrorInfo(
                    ErrorInfo.Companion.throwableToStringList(mission.errObject),
                    action,
                    service,
                    request.toString(),
                    reason));
}

void startMission(DownloadMission mission) {
    synchronized (this) {
        mission.timestamp = System.currentTimeMillis();
        mission.mHandler = mHandler;
        mission.maxRetry = mPrefMaxRetry;

        // create metadata file
        while (true) {
            mission.metadata = new File(mPendingMissionsDir, String.valueOf(mission.timestamp));
            if (!mission.metadata.isFile() && !mission.metadata.exists()) {
                try {
                    if (!mission.metadata.createNewFile()) {
                        throw new RuntimeException("Cant create download metadata file");
                    }
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
                break;
            }
            mission.timestamp = System.currentTimeMillis();
        }

        mSelfMissionsControl = true;
        mMissionsPending.add(mission);

        // Before continue, save the metadata in case the internet connection is not available
        Utility.writeToFile(mission.metadata, mission);

        if (mission.storage == null) {
            // noting to do here
            mission.errCode = DownloadMission.ERROR_FILE_CREATION;
            if (mission.errObject != null) {
                mission.errObject = new IOException("DownloadMission.storage == NULL");
            }
            return;
        }

        boolean start = !mPrefQueueLimit || getRunningMissionsCount() < 1;

        if (canDownloadInCurrentNetwork() && start) {
            mission.start();
        }
    }
}

@Override
int process(SharpStream out, SharpStream... sources) throws IOException {
    // check if the subtitle is already in srt and copy, this should never happen
    String format = getArgumentAt(0, null);
    boolean ignoreEmptyFrames = getArgumentAt(1, "true").equals("true");

    if (format == null || format.equals("ttml")) {
        SrtFromTtmlWriter writer = new SrtFromTtmlWriter(out, ignoreEmptyFrames);

        try {
            writer.build(sources[0]);
        } catch (Exception err) {
            Log.e(TAG, "subtitle parse failed", err);
            return err instanceof IOException ? 1 : 8;
        }

        return OK_RESULT;
    } else if (format.equals("srt")) {
        byte[] buffer = new byte[8 * 1024];
        int read;
        while ((read = sources[0].read(buffer)) > 0) {
            out.write(buffer, 0, read);
        }
        return OK_RESULT;
    }

    throw new UnsupportedOperationException(
            "Can't convert this subtitle, unimplemented format: " + format);
}

private void joinForThreads(int millis) {
    final Thread currentThread = Thread.currentThread();

    if (init != null && init != currentThread && init.isAlive()) {
        init.interrupt();

        if (millis > 0) {
            try {
                init.join(millis);
            } catch (InterruptedException e) {
                Log.w(TAG, "Initializer thread is still running", e);
                return;
            }
        }
    }

    // if a thread is still alive, possible reasons:
    //      slow device
    //      the user is spamming start/pause buttons
    //      start() method called quickly after pause()

    for (Thread thread : threads) {
        if (!thread.isAlive() || thread == Thread.currentThread()) {
            continue;
        }
        thread.interrupt();
    }

    try {
        for (Thread thread : threads) {
            if (!thread.isAlive()) {
                continue;
            }
            if (DEBUG) {
                Log.w(TAG, "thread alive: " + thread.getName());
            }
            if (millis > 0) {
                thread.join(millis);
            }
        }
    } catch (InterruptedException e) {
        throw new RuntimeException("A download thread is still running", e);
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    Observer<? super R> downstream = this.downstream;
    AtomicThrowable errors = this.errors;
    AtomicReference<SwitchMapMaybeObserver<R>> inner = this.inner;

    while (true) {
        while (true) {
            if (cancelled) {
                return;
            }

            if (errors.get() != null) {
                if (!delayErrors) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            boolean d = done;
            SwitchMapMaybeObserver<R> current = inner.get();
            boolean empty = current == null;

            if (d && empty) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (empty || current.item == null) {
                break;
            }

            inner.compareAndSet(current, null);
            downstream.onNext(current.item);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void onNext(T t) {
    if (done) {
        return;
    }
    boolean b;
    try {
        b = predicate.test(t);
    } catch (Throwable e) {
        Exceptions.throwIfFatal(e);
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        onError(e);
        return;
    }
    if (!b) {
        done = true;
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        downstream.onSuccess(false);
    }
}

void drainLoop() {
    Observer<? super T> actual = this.downstream;
    int missed = 1;
    while (true) {
        while (true) {
            if (disposed) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(actual);
                return;
            }

            int os = otherState;
            if (os == OTHER_STATE_HAS_VALUE) {
                T v = singleItem;
                singleItem = null;
                otherState = OTHER_STATE_CONSUMED_OR_EMPTY;
                os = OTHER_STATE_CONSUMED_OR_EMPTY;
                actual.onNext(v);
            }

            boolean d = mainDone;
            SimplePlainQueue<T> q = queue;
            T v = q != null ? q.poll() : null;
            boolean empty = v == null;

            if (d && empty && os == OTHER_STATE_CONSUMED_OR_EMPTY) {
                queue = null;
                actual.onComplete();
                return;
            }

            if (empty) {
                break;
            }

            actual.onNext(v);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final SimplePlainQueue<Object> queue = this.queue;
    final Subscriber<? super Flowable<T>> downstream = this.downstream;
    UnicastProcessor<T> window = this.window;

    while (true) {
        if (upstreamCancelled) {
            queue.clear();
            window = null;
            this.window = null;
        } else {
            boolean isDone = done;
            Object o = queue.poll();
            boolean isEmpty = o == null;

            if (isDone && isEmpty) {
                Throwable ex = error;
                if (ex != null) {
                    if (window != null) {
                        window.onError(ex);
                    }
                    downstream.onError(ex);
                } else {
                    if (window != null) {
                        window.onComplete();
                    }
                    downstream.onComplete();
                }
                cleanupResources();
                upstreamCancelled = true;
                continue;
            } else if (!isEmpty) {
                if (o instanceof WindowBoundaryRunnable) {
                    WindowBoundaryRunnable boundary = (WindowBoundaryRunnable) o;
                    if (boundary.index == emitted || !restartTimerOnMaxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    }
                } else if (window != null) {
                    @SuppressWarnings("unchecked")
                    T item = (T) o;
                    window.onNext(item);

                    long count = this.count + 1;
                    if (count == maxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    } else {
                        this.count = count;
                    }
                }
                continue;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    EqualObserver<T>[] as = observers;

    final EqualObserver<T> observer1 = as[0];
    final SpscLinkedArrayQueue<T> q1 = observer1.queue;
    final EqualObserver<T> observer2 = as[1];
    final SpscLinkedArrayQueue<T> q2 = observer2.queue;

    while (true) {
        while (true) {
            if (cancelled) {
                q1.clear();
                q2.clear();
                return;
            }

            boolean d1 = observer1.done;

            if (d1) {
                Throwable e = observer1.error;
                if (e != null) {
                    cancel(q1, q2);
                    downstream.onError(e);
                    return;
                }
            }

            boolean d2 = observer2.done;
            if (d2) {
                Throwable e = observer2.error;
                if (e != null) {
                    cancel(q1, q2);
                    downstream.onError(e);
                    return;
                }
            }

            if (v1 == null) {
                v1 = q1.poll();
            }
            boolean e1 = v1 == null;

            if (v2 == null) {
                v2 = q2.poll();
            }
            boolean e2 = v2 == null;

            if (d1 && d2 && e1 && e2) {
                downstream.onSuccess(true);
                return;
            }
            if ((d1 && d2) && (e1 != e2)) {
                cancel(q1, q2);
                downstream.onSuccess(false);
                return;
            }

            if (!e1 && !e2) {
                boolean c;
                try {
                    c = comparer.test(v1, v2);
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    cancel(q1, q2);
                    downstream.onError(ex);
                    return;
                }
                if (!c) {
                    cancel(q1, q2);
                    downstream.onSuccess(false);
                    return;
                }
                v1 = null;
                v2 = null;
            }

            if (e1 || e2) {
                break;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drainLoop() {
    int missed = 1;

    JoinInnerSubscriber<T>[] s = this.subscribers;
    int n = s.length;
    Subscriber<? super T> a = this.downstream;

    while (true) {
        long r = requested.get();
        long e = 0;

        middle:
        while (e != r) {
            if (cancelled) {
                cleanup();
                return;
            }

            Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }

            boolean d = done.get() == 0;
            boolean empty = true;

            for (int i = 0; i < s.length; i++) {
                JoinInnerSubscriber<T> inner = s[i];
                SimplePlainQueue<T> q = inner.queue;
                if (q != null) {
                    T v = q.poll();

                    if (v != null) {
                        empty = false;
                        a.onNext(v);
                        inner.requestOne();
                        if (++e == r) {
                            break middle;
                        }
                    }
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }

            if (empty) {
                break;
            }
        }

        if (e == r) {
            if (cancelled) {
                cleanup();
                return;
            }

            Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }

            boolean d = done.get() == 0;
            boolean empty = true;

            for (int i = 0; i < n; i++) {
                JoinInnerSubscriber<T> inner = s[i];
                SimpleQueue<T> q = inner.queue;
                if (q != null && !q.isEmpty()) {
                    empty = false;
                    break;
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }
        }

        if (e != 0) {
            BackpressureHelper.produced(requested, e);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    InnerQueuedSubscriber<R> inner = current;
    Subscriber<? super R> a = downstream;
    ErrorMode em = errorMode;

    while (true) {
        long r = requested.get();
        long e = 0L;

        if (inner == null) {
            if (em != ErrorMode.END) {
                Throwable ex = errors.get();
                if (ex != null) {
                    cancelAll();
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            boolean outerDone = done;
            inner = subscribers.poll();

            if (outerDone && inner == null) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (inner != null) {
                current = inner;
            }
        }

        boolean continueNextSource = false;

        if (inner != null) {
            SimpleQueue<R> q = inner.queue();
            if (q != null) {
                while (e != r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }

                    if (em == ErrorMode.IMMEDIATE) {
                        Throwable ex = errors.get();
                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();
                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    boolean d = inner.isDone();
                    R v;

                    try {
                        v = q.poll();
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        current = null;
                        inner.cancel();
                        cancelAll();
                        a.onError(ex);
                        return;
                    }

                    boolean empty = v == null;

                    if (d && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                        break;
                    }

                    if (empty) {
                        break;
                    }

                    a.onNext(v);
                    e++;
                    inner.request(1L);
                }

                if (e == r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }

                    if (em == ErrorMode.IMMEDIATE) {
                        Throwable ex = errors.get();
                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();
                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    boolean d = inner.isDone();
                    boolean empty = q.isEmpty();

                    if (d && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                    }
                }
            }
        }

        if (e != 0L && r != Long.MAX_VALUE) {
            requested.addAndGet(-e);
        }

        if (continueNextSource) {
            continue;
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

