public static String renderEnhancerAffect(EnhancerAffectVO affectVO) {
  final StringBuilder infoSB = new StringBuilder();
  List<String> classDumpFiles = affectVO.getClassDumpFiles();
  if (classDumpFiles != null) {
    for (String classDumpFile : classDumpFiles) {
      infoSB.append("[dump: ").append(classDumpFile).append("]\n");
    }
  }

  List<String> methods = affectVO.getMethods();
  if (methods != null) {
    for (String method : methods) {
      infoSB.append("[Affect method: ").append(method).append("]\n");
    }
  }

  infoSB.append(format("Affect(class count: %d , method count: %d) cost in %s ms, listenerId: %d",
      affectVO.getClassCount(),
      affectVO.getMethodCount(),
      affectVO.getCost(),
      affectVO.getListenerId()));
  if (!StringUtils.isEmpty(affectVO.getOverLimitMsg())) {
    infoSB.append("\n").append(affectVO.getOverLimitMsg());
  }
  if (affectVO.getThrowable() != null) {
    infoSB.append("\nEnhance error! exception: ").append(affectVO.getThrowable());
  }
  infoSB.append("\n");

  return infoSB.toString();
}

public List<ThreadVO> sample(Collection<ThreadVO> originThreads) {

  List<ThreadVO> threads = new ArrayList<>(originThreads);

  // Sample CPU
  if (lastCpuTimes.isEmpty()) {
    lastSampleTimeNanos = System.nanoTime();
    for (ThreadVO thread : threads) {
      if (thread.getId() > 0) {
        long cpu = threadMXBean.getThreadCpuTime(thread.getId());
        lastCpuTimes.put(thread, cpu);
        thread.setTime(cpu / 1000000);
      }
    }

    // add internal threads
    Map<String, Long> internalThreadCpuTimes = getInternalThreadCpuTimes();
    if (internalThreadCpuTimes != null) {
      for (Map.Entry<String, Long> entry : internalThreadCpuTimes.entrySet()) {
        String key = entry.getKey();
        ThreadVO thread = createThreadVO(key);
        thread.setTime(entry.getValue() / 1000000);
        threads.add(thread);
        lastCpuTimes.put(thread, entry.getValue());
      }
    }

    // sort by time
    threads.sort(new Comparator<ThreadVO>() {
      @Override
      public int compare(ThreadVO o1, ThreadVO o2) {
        long l1 = o1.getTime();
        long l2 = o2.getTime();
        if (l1 < l2) {
          return 1;
        } else if (l1 > l2) {
          return -1;
        } else {
          return 0;
        }
      }
    });
    return threads;
  }

  // Resample
  long newSampleTimeNanos = System.nanoTime();
  Map<ThreadVO, Long> newCpuTimes = new HashMap<>(threads.size());
  for (ThreadVO thread : threads) {
    if (thread.getId() > 0) {
      long cpu = threadMXBean.getThreadCpuTime(thread.getId());
      newCpuTimes.put(thread, cpu);
    }
  }
  // internal threads
  Map<String, Long> newInternalThreadCpuTimes = getInternalThreadCpuTimes();
  if (newInternalThreadCpuTimes != null) {
    for (Map.Entry<String, Long> entry : newInternalThreadCpuTimes.entrySet()) {
      ThreadVO threadVO = createThreadVO(entry.getKey());
      threads.add(threadVO);
      newCpuTimes.put(threadVO, entry.getValue());
    }
  }

  // Compute delta time
  final Map<ThreadVO, Long> deltas = new HashMap<>(threads.size());
  for (ThreadVO thread : newCpuTimes.keySet()) {
    Long t = lastCpuTimes.get(thread);
    if (t == null) {
      t = 0L;
    }
    long time1 = t;
    long time2 = newCpuTimes.get(thread);
    if (time1 == -1) {
      time1 = time2;
    } else if (time2 == -1) {
      time2 = time1;
    }
    long delta = time2 - time1;
    deltas.put(thread, delta);
  }

  long sampleIntervalNanos = newSampleTimeNanos - lastSampleTimeNanos;

  // Compute cpu usage
  final HashMap<ThreadVO, Double> cpuUsages = new HashMap<>(threads.size());
  for (ThreadVO thread : threads) {
    double cpu = sampleIntervalNanos == 0 ? 0 : (Math.rint(deltas.get(thread) * 10000.0 / sampleIntervalNanos) / 100.0);
    cpuUsages.put(thread, cpu);
  }

  // Sort by CPU time : should be a rendering hint...
  threads.sort(new Comparator<ThreadVO>() {
    @Override
    public int compare(ThreadVO o1, ThreadVO o2) {
      long l1 = deltas.get(o1);
      long l2 = deltas.get(o2);
      if (l1 < l2) {
        return 1;
      } else if (l1 > l2) {
        return -1;
      } else {
        return 0;
      }
    }
  });

  for (ThreadVO thread : threads) {
    // nanos to mills
    long timeMills = newCpuTimes.get(thread) / 1000000;
    long deltaTime = deltas.get(thread) / 1000000;
    double cpu = cpuUsages.get(thread);

    thread.setCpu(cpu);
    thread.setTime(timeMills);
    thread.setDeltaTime(deltaTime);
  }
  lastCpuTimes = newCpuTimes;
  lastSampleTimeNanos = newSampleTimeNanos;

  return threads;
}

private ApiResponse processAsyncExecRequest(ApiRequest apiRequest, Session session) {
  String commandLine = apiRequest.getCommand();
  Map<String, Object> body = new TreeMap<>();
  body.put("command", commandLine);

  ApiResponse response = new ApiResponse();
  response.setSessionId(session.getSessionId())
      .setBody(body);

  if (!session.tryLock()) {
    response.setState(ApiState.REFUSED)
        .setMessage("Another command is executing.");
    return response;
  }
  int lock = session.getLock();
  try {

    Job foregroundJob = session.getForegroundJob();
    if (foregroundJob != null) {
      response.setState(ApiState.REFUSED)
          .setMessage("Another job is running.");
      logger.info("Another job is running, jobId: {}", foregroundJob.id());
      return response;
    }

    // create job
    Job job = this.createJob(commandLine, session, session.getResultDistributor());
    body.put("jobId", job.id());
    body.put("jobStatus", job.status());
    response.setState(ApiState.SCHEDULED);

    // add command before exec job
    CommandRequestModel commandRequestModel = new CommandRequestModel(commandLine, response.getState());
    commandRequestModel.setJobId(job.id());
    SharingResultDistributor resultDistributor = session.getResultDistributor();
    if (resultDistributor != null) {
      resultDistributor.appendResult(commandRequestModel);
    }
    session.setForegroundJob(job);
    updateSessionInputStatus(session, InputStatus.ALLOW_INTERRUPT);

    // run job
    job.run();

    return response;
  } catch (Throwable e) {
    logger.error("Async exec command failed:" + e.getMessage() + ", command:" + commandLine, e);
    response.setState(ApiState.FAILED).setMessage("Async exec command failed:" + e.getMessage());
    CommandRequestModel commandRequestModel = new CommandRequestModel(commandLine, response.getState(), response.getMessage());
    session.getResultDistributor().appendResult(commandRequestModel);
    return response;
  } finally {
    if (session.getLock() == lock) {
      session.unLock();
    }
  }
}

private String renderLoggerInfo(Map<String, Map<String, Object>> loggerInfos, int width) {
  StringBuilder sb = new StringBuilder(8192);

  for (Map.Entry<String, Map<String, Object>> entry : loggerInfos.entrySet()) {
    Map<String, Object> info = entry.getValue();

    TableElement table = new TableElement(2, 10)
        .leftCellPadding(1)
        .rightCellPadding(1);
    TableElement appendersTable = new TableElement()
        .rightCellPadding(1);

    Class<?> clazz = (Class<?>) info.get(LoggerHelper.clazz);
    table.row(label(LoggerHelper.name).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.name)))
        .row(label(LoggerHelper.clazz).style(Decoration.bold.bold()), label("" + clazz.getName()))
        .row(label(LoggerHelper.classLoader).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.classLoader)))
        .row(label(LoggerHelper.classLoaderHash).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.classLoaderHash)))
        .row(label(LoggerHelper.level).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.level)));
    if (info.get(LoggerHelper.effectiveLevel) != null) {
      table.row(label(LoggerHelper.effectiveLevel).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.effectiveLevel)));
    }
    if (info.get(LoggerHelper.config) != null) {
      table.row(label(LoggerHelper.config).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.config)));
    }

    table.row(label(LoggerHelper.additivity).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.additivity)))
        .row(label(LoggerHelper.codeSource).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.codeSource)));

    @SuppressWarnings("unchecked")
    List<Map<String, Object>> appenders = (List<Map<String, Object>>) info.get(LoggerHelper.appenders);
    if (appenders != null && !appenders.isEmpty()) {
      for (Map<String, Object> appenderInfo : appenders) {
        Class<?> appenderClass = (Class<?>) appenderInfo.get(LoggerHelper.clazz);

        appendersTable.row(label(LoggerHelper.name).style(Decoration.bold.bold()), label("" + appenderInfo.get(LoggerHelper.name)));
        appendersTable.row(label(LoggerHelper.clazz), label("" + appenderClass.getName()));
        appendersTable.row(label(LoggerHelper.classLoader), label("" + info.get(LoggerHelper.classLoader)));
        appendersTable.row(label(LoggerHelper.classLoaderHash), label("" + info.get(LoggerHelper.classLoaderHash)));
        if (appenderInfo.get(LoggerHelper.file) != null) {
          appendersTable.row(label(LoggerHelper.file), label("" + appenderInfo.get(LoggerHelper.file)));
        }
        if (appenderInfo.get(LoggerHelper.target) != null) {
          appendersTable.row(label(LoggerHelper.target), label("" + appenderInfo.get(LoggerHelper.target)));
        }
        if (appenderInfo.get(LoggerHelper.blocking) != null) {
          appendersTable.row(label(LoggerHelper.blocking), label("" + appenderInfo.get(LoggerHelper.blocking)));
        }
        if (appenderInfo.get(LoggerHelper.appenderRef) != null) {
          appendersTable.row(label(LoggerHelper.appenderRef), label("" + appenderInfo.get(LoggerHelper.appenderRef)));
        }
      }

      table.row(label("appenders").style(Decoration.bold.bold()), appendersTable);
    }

    sb.append(RenderUtil.render(table, width)).append('\n');
  }
  return sb.toString();
}

public static int startArthasClient(String arthasHomeDir, List<String> telnetArgs, OutputStream out) throws Throwable {
  // start java telnet client
  // find arthas-client.jar
  URLClassLoader classLoader = new URLClassLoader(
      new URL[]{new File(arthasHomeDir, "arthas-client.jar").toURI().toURL()});
  Class<?> telnetConsoleClass = classLoader.loadClass("com.taobao.arthas.client.TelnetConsole");
  Method processMethod = telnetConsoleClass.getMethod("process", String[].class);

  // redirect System.out/System.err
  PrintStream originSysOut = System.out;
  PrintStream originSysErr = System.err;
  PrintStream newOut = new PrintStream(out);
  PrintStream newErr = new PrintStream(out);

  // call TelnetConsole.process()
  // fix https://github.com/alibaba/arthas/issues/833
  ClassLoader tccl = Thread.currentThread().getContextClassLoader();
  try {
    System.setOut(newOut);
    System.setErr(newErr);
    Thread.currentThread().setContextClassLoader(classLoader);
    return (Integer) processMethod.invoke(null, new Object[]{telnetArgs.toArray(new String[0])});
  } catch (Throwable e) {
    // java.lang.reflect.InvocationTargetException : java.net.ConnectException
    e = e.getCause();
    if (e instanceof IOException || e instanceof InterruptedException) {
      // ignore connection error and interrupted error
      return STATUS_ERROR;
    } else {
      // process error
      AnsiLog.error("process error: {}", e.toString());
      AnsiLog.error(e);
      return STATUS_EXEC_ERROR;
    }
  } finally {
    Thread.currentThread().setContextClassLoader(tccl);

    // reset System.out/System.err
    System.setOut(originSysOut);
    System.setErr(originSysErr);
    // flush output
    newOut.flush();
    newErr.flush();
  }
}

public static FieldVO[] getFields(Class clazz, Integer expand) {
  Field[] fields = clazz.getDeclaredFields();
  if (fields.length == 0) {
    return new FieldVO[0];
  }

  List<FieldVO> list = new ArrayList<>(fields.length);
  for (Field field : fields) {
    FieldVO fieldVO = new FieldVO();
    fieldVO.setName(field.getName());
    fieldVO.setType(StringUtils.classname(field.getType()));
    fieldVO.setModifier(StringUtils.modifier(field.getModifiers(), ','));
    fieldVO.setAnnotations(getAnnotations(field.getAnnotations()));
    if (Modifier.isStatic(field.getModifiers())) {
      fieldVO.setStatic(true);
      fieldVO.setValue(new ObjectVO(getFieldValue(field), expand));
    } else {
      fieldVO.setStatic(false);
    }
    list.add(fieldVO);
  }
  return list.toArray(new FieldVO[0]);
}

private static String normalizeArch(String value) {
  value = normalize(value);

  if (value.matches("^(x8664|amd64|ia32e|em64t|x64)$")) {
    return "x86_64";
  }
  if (value.matches("^(x8632|x86|i[3-6]86|ia32|x32)$")) {
    return "x86_32";
  }
  if (value.matches("^(ia64w?|itanium64)$")) {
    return "itanium_64";
  }
  if ("ia64n".equals(value)) {
    return "itanium_32";
  }
  if (value.matches("^(sparc|sparc32)$")) {
    return "sparc_32";
  }
  if (value.matches("^(sparcv9|sparc64)$")) {
    return "sparc_64";
  }
  if (value.matches("^(arm|arm32)$")) {
    return "arm_32";
  }
  if ("aarch64".equals(value)) {
    return "aarch_64";
  }
  if (value.matches("^(mips|mips32)$")) {
    return "mips_32";
  }
  if (value.matches("^(mipsel|mips32el)$")) {
    return "mipsel_32";
  }
  if ("mips64".equals(value)) {
    return "mips_64";
  }
  if ("mips64el".equals(value)) {
    return "mipsel_64";
  }
  if (value.matches("^(ppc|ppc32)$")) {
    return "ppc_32";
  }
  if (value.matches("^(ppcle|ppc32le)$")) {
    return "ppcle_32";
  }
  if ("ppc64".equals(value)) {
    return "ppc_64";
  }
  if ("ppc64le".equals(value)) {
    return "ppcle_64";
  }
  if ("s390".equals(value)) {
    return "s390_32";
  }
  if ("s390x".equals(value)) {
    return "s390_64";
  }
  return value;
}

private static Map<String, Object> doGetLoggerInfo(Logger logger) {
  Map<String, Object> info = new HashMap<>();
  info.put(LoggerHelper.name, logger.getName());
  info.put(LoggerHelper.clazz, logger.getClass());
  CodeSource codeSource = logger.getClass().getProtectionDomain().getCodeSource();
  if (codeSource != null) {
    info.put(LoggerHelper.codeSource, codeSource.getLocation());
  }
  info.put(LoggerHelper.additivity, logger.getAdditivity());

  Level level = logger.getLevel();
  Level effectiveLevel = logger.getEffectiveLevel();
  if (level != null) {
    info.put(LoggerHelper.level, level.toString());
  }
  if (effectiveLevel != null) {
    info.put(LoggerHelper.effectiveLevel, effectiveLevel.toString());
  }

  @SuppressWarnings("unchecked")
  List<Map<String, Object>> result = doGetLoggerAppenders(logger.getAllAppenders());
  info.put(LoggerHelper.appenders, result);
  return info;
}

@SuppressWarnings("resource")
public static long select(boolean v, long telnetPortPid, String select) throws InputMismatchException {
  Map<Long, String> processMap = listProcessByJps(v);

  if (telnetPortPid > 0 && processMap.containsKey(telnetPortPid)) {
    String telnetPortProcess = processMap.get(telnetPortPid);
    processMap.remove(telnetPortPid);
    Map<Long, String> newProcessMap = new LinkedHashMap<>();
    newProcessMap.put(telnetPortPid, telnetPortProcess);
    newProcessMap.putAll(processMap);
    processMap = newProcessMap;
  }

  if (processMap.isEmpty()) {
    AnsiLog.info("Can not find java process. Try to run `jps` command lists the instrumented Java HotSpot VMs on the target system.");
    return -1;
  }

  // select target process by the '--select' option when match only one process
  if (select != null && !select.trim().isEmpty()) {
    int matchedSelectCount = 0;
    Long matchedPid = null;
    for (Entry<Long, String> entry : processMap.entrySet()) {
      if (entry.getValue().contains(select)) {
        matchedSelectCount++;
        matchedPid = entry.getKey();
      }
    }
    if (matchedSelectCount == 1) {
      return matchedPid;
    }
  }

  AnsiLog.info("Found existing java process, please choose one and input the serial number of the process, eg : 1. Then hit ENTER.");

  int count = 1;
  for (String process : processMap.values()) {
    if (count == 1) {
      System.out.println("* [" + count + "]: " + process);
    } else {
      System.out.println("  [" + count + "]: " + process);
    }
    count++;
  }

  String line = new Scanner(System.in).nextLine();
  if (line.trim().isEmpty()) {
    return processMap.keySet().iterator().next();
  }

  int choice = new Scanner(line).nextInt();

  if (choice <= 0 || choice > processMap.size()) {
    return -1;
  }

  Iterator<Long> idIter = processMap.keySet().iterator();
  for (int i = 1; i <= choice; ++i) {
    if (i == choice) {
      return idIter.next();
    }
    idIter.next();
  }

  return -1;
}

void _processDo(int option) throws IOException {
  if (debugoptions) {
    System.err.println("RECEIVED DO: " + TelnetOption.getOption(option));
  }

  if (__notifhand != null) {
    __notifhand.receivedNegotiation(
        TelnetNotificationHandler.RECEIVED_DO,
        option);
  }

  boolean acceptNewState = false;

  /* open TelnetOptionHandler functionality (start)*/
  if (optionHandlers[option] != null) {
    acceptNewState = optionHandlers[option].getAcceptLocal();
  } else {
    /* open TelnetOptionHandler functionality (end)*/
    /* TERMINAL-TYPE option (start)*/
    if (option == TERMINAL_TYPE) {
      if ((terminalType != null) && (terminalType.length() > 0)) {
        acceptNewState = true;
      }
    }
    /* TERMINAL-TYPE option (end)*/
    /* open TelnetOptionHandler functionality (start)*/
  }
  /* open TelnetOptionHandler functionality (end)*/

  if (_willResponse[option] > 0) {
    --_willResponse[option];
    if (_willResponse[option] > 0 && _stateIsWill(option)) {
      --_willResponse[option];
    }
  }

  if (_willResponse[option] == 0) {
    if (_requestedWont(option)) {

      switch (option) {
        default:
          break;
      }

      if (acceptNewState) {
        _setWantWill(option);
        _sendWill(option);
      } else {
        ++_willResponse[option];
        _sendWont(option);
      }
    } else {
      // Other end has acknowledged option.
      switch (option) {
        default:
          break;
      }
    }
  }

  _setWill(option);
}

private void bind(Configure configure) throws Throwable {

  long start = System.currentTimeMillis();

  if (!isBindRef.compareAndSet(false, true)) {
    throw new IllegalStateException("already bind");
  }

  // init random port
  if (configure.getTelnetPort() != null && configure.getTelnetPort() == 0) {
    int newTelnetPort = SocketUtils.findAvailableTcpPort();
    configure.setTelnetPort(newTelnetPort);
    logger().info("generate random telnet port: " + newTelnetPort);
  }
  if (configure.getHttpPort() != null && configure.getHttpPort() == 0) {
    int newHttpPort = SocketUtils.findAvailableTcpPort();
    configure.setHttpPort(newHttpPort);
    logger().info("generate random http port: " + newHttpPort);
  }
  // try to find appName
  if (configure.getAppName() == null) {
    configure.setAppName(System.getProperty(ArthasConstants.PROJECT_NAME,
        System.getProperty(ArthasConstants.SPRING_APPLICATION_NAME, null)));
  }

  try {
    if (configure.getTunnelServer() != null) {
      tunnelClient = new TunnelClient();
      tunnelClient.setAppName(configure.getAppName());
      tunnelClient.setId(configure.getAgentId());
      tunnelClient.setTunnelServerUrl(configure.getTunnelServer());
      tunnelClient.setVersion(ArthasBanner.version());
      ChannelFuture channelFuture = tunnelClient.start();
      channelFuture.await(10, TimeUnit.SECONDS);
    }
  } catch (Throwable t) {
    logger().error("start tunnel client error", t);
  }

  try {
    ShellServerOptions options = new ShellServerOptions()
        .setInstrumentation(instrumentation)
        .setPid(PidUtils.currentLongPid())
        .setWelcomeMessage(ArthasBanner.welcome());
    if (configure.getSessionTimeout() != null) {
      options.setSessionTimeout(configure.getSessionTimeout() * 1000);
    }

    this.httpSessionManager = new HttpSessionManager();
    if (IPUtils.isAllZeroIP(configure.getIp()) && StringUtils.isBlank(configure.getPassword())) {
      String errorMsg = "Listening on 0.0.0.0 is very dangerous! External users can connect to your machine! "
          + "No password is currently configured. " + "Therefore, a default password is generated, "
          + "and clients need to use the password to connect!";
      AnsiLog.error(errorMsg);
      configure.setPassword(StringUtils.randomString(64));
      AnsiLog.error("Generated arthas password: " + configure.getPassword());

      logger().error(errorMsg);
      logger().info("Generated arthas password: " + configure.getPassword());
    }

    this.securityAuthenticator = new SecurityAuthenticatorImpl(configure.getUsername(), configure.getPassword());

    shellServer = new ShellServerImpl(options);

    List<String> disabledCommands = new ArrayList<>();
    if (configure.getDisabledCommands() != null) {
      String[] strings = StringUtils.tokenizeToStringArray(configure.getDisabledCommands(), ",");
      if (strings != null) {
        disabledCommands.addAll(Arrays.asList(strings));
      }
    }
    BuiltinCommandPack builtinCommands = new BuiltinCommandPack(disabledCommands);
    List<CommandResolver> resolvers = new ArrayList<>();
    resolvers.add(builtinCommands);

    // worker group
    workerGroup = new NioEventLoopGroup(new DefaultThreadFactory("arthas-TermServer", true));

    // TODO: discover user provided command resolver
    if (configure.getTelnetPort() != null && configure.getTelnetPort() > 0) {
      logger().info("try to bind telnet server, host: {}, port: {}.", configure.getIp(), configure.getTelnetPort());
      shellServer.registerTermServer(new HttpTelnetTermServer(
          configure.getIp(), configure.getTelnetPort(),
          options.getConnectionTimeout(), workerGroup, httpSessionManager));
    } else {
      logger().info("telnet port is {}, skip bind telnet server.", configure.getTelnetPort());
    }
    if (configure.getHttpPort() != null && configure.getHttpPort() > 0) {
      logger().info("try to bind http server, host: {}, port: {}.", configure.getIp(), configure.getHttpPort());
      shellServer.registerTermServer(new HttpTermServer(
          configure.getIp(), configure.getHttpPort(),
          options.getConnectionTimeout(), workerGroup, httpSessionManager));
    } else {
      if (configure.getTunnelServer() != null) {
        shellServer.registerTermServer(new HttpTermServer(
            configure.getIp(), configure.getHttpPort(),
            options.getConnectionTimeout(), workerGroup, httpSessionManager));
      }
      logger().info("http port is {}, skip bind http server.", configure.getHttpPort());
    }

    for (CommandResolver resolver : resolvers) {
      shellServer.registerCommandResolver(resolver);
    }

    shellServer.listen(new BindHandler(isBindRef));
    if (!isBind()) {
      throw new IllegalStateException("Arthas failed to bind telnet or http port! Telnet port: "
          + String.valueOf(configure.getTelnetPort()) + ", http port: "
          + String.valueOf(configure.getHttpPort()));
    }

    sessionManager = new SessionManagerImpl(options, shellServer.getCommandManager(), shellServer.getJobController());
    httpApiHandler = new HttpApiHandler(historyManager, sessionManager);

    logger().info("as-server listening on network={};telnet={};http={};timeout={};", configure.getIp(),
        configure.getTelnetPort(), configure.getHttpPort(), options.getConnectionTimeout());

    if (configure.getStatUrl() != null) {
      logger().info("arthas stat url: {}", configure.getStatUrl());
    }
    UserStatUtil.setStatUrl(configure.getStatUrl());
    UserStatUtil.setAgentId(configure.getAgentId());
    UserStatUtil.arthasStart();

    try {
      SpyAPI.init();
    } catch (Throwable e) {
      // ignore
    }

    logger().info("as-server started in {} ms", System.currentTimeMillis() - start);
  } catch (Throwable e) {
    logger().error("Error during start as-server", e);
    destroy();
    throw e;
  }
}

private int __read(boolean mayBlock) throws IOException {
  int ch;

  while (true) {

    // If there is no more data AND we were told not to block,
    // just return WOULD_BLOCK (-2). (More efficient than exception.)
    if (!mayBlock && super.available() == 0) {
      return WOULD_BLOCK;
    }

    // Otherwise, exit only when we reach end of stream.
    if ((ch = super.read()) < 0) {
      return EOF;
    }

    ch = (ch & 0xff);

    /* Code Section added for supporting AYT (start)*/
    synchronized (__client) {
      __client._processAYTResponse();
    }
    /* Code Section added for supporting AYT (end)*/

    /* Code Section added for supporting spystreams (start)*/
    __client._spyRead(ch);
    /* Code Section added for supporting spystreams (end)*/

    switch (__receiveState) {

      case _STATE_CR:
        if (ch == '\0') {
          // Strip null
          continue;
        }
        // How do we handle newline after cr?
        //  else if (ch == '\n' && _requestedDont(TelnetOption.ECHO) &&

        // Handle as normal data by falling through to _STATE_DATA case

        //$FALL-THROUGH$
      case _STATE_DATA:
        if (ch == TelnetCommand.IAC) {
          __receiveState = _STATE_IAC;
          continue;
        }

        if (ch == '\r') {
          synchronized (__client) {
            if (__client._requestedDont(TelnetOption.BINARY)) {
              __receiveState = _STATE_CR;
            } else {
              __receiveState = _STATE_DATA;
            }
          }
        } else {
          __receiveState = _STATE_DATA;
        }
        break;

      case _STATE_IAC:
        switch (ch) {
          case TelnetCommand.WILL:
            __receiveState = _STATE_WILL;
            continue;
          case TelnetCommand.WONT:
            __receiveState = _STATE_WONT;
            continue;
          case TelnetCommand.DO:
            __receiveState = _STATE_DO;
            continue;
          case TelnetCommand.DONT:
            __receiveState = _STATE_DONT;
            continue;
          /* TERMINAL-TYPE option (start)*/
          case TelnetCommand.SB:
            __suboption_count = 0;
            __receiveState = _STATE_SB;
            continue;
          /* TERMINAL-TYPE option (end)*/
          case TelnetCommand.IAC:
            __receiveState = _STATE_DATA;
            break;
          case TelnetCommand.SE:
            __receiveState = _STATE_DATA;
            continue;
          default:
            __receiveState = _STATE_DATA;
            __client._processCommand(ch);
            continue;
        }
        break;
      case _STATE_WILL:
        synchronized (__client) {
          __client._processWill(ch);
          __client._flushOutputStream();
        }
        __receiveState = _STATE_DATA;
        continue;
      case _STATE_WONT:
        synchronized (__client) {
          __client._processWont(ch);
          __client._flushOutputStream();
        }
        __receiveState = _STATE_DATA;
        continue;
      case _STATE_DO:
        synchronized (__client) {
          __client._processDo(ch);
          __client._flushOutputStream();
        }
        __receiveState = _STATE_DATA;
        continue;
      case _STATE_DONT:
        synchronized (__client) {
          __client._processDont(ch);
          __client._flushOutputStream();
        }
        __receiveState = _STATE_DATA;
        continue;
      /* TERMINAL-TYPE option (start)*/
      case _STATE_SB:
        switch (ch) {
          case TelnetCommand.IAC:
            __receiveState = _STATE_IAC_SB;
            continue;
          default:
            if (__suboption_count < __suboption.length) {
              __suboption[__suboption_count++] = ch;
            }
            break;
        }
        __receiveState = _STATE_SB;
        continue;
      case _STATE_IAC_SB:
        switch (ch) {
          case TelnetCommand.SE:
            synchronized (__client) {
              __client._processSuboption(__suboption, __suboption_count);
              __client._flushOutputStream();
            }
            __receiveState = _STATE_DATA;
            continue;
          case TelnetCommand.IAC:
            if (__suboption_count < __suboption.length) {
              __suboption[__suboption_count++] = ch;
            }
            break;
          default:
            break;
        }
        __receiveState = _STATE_SB;
        continue;
      /* TERMINAL-TYPE option (end)*/
    }

    break;
  }

  return ch;
}

private static String formatFileSize(long size) {
  String hrSize;

  double b = size;
  double k = size / 1024.0;
  double m = (size / 1024.0) / 1024.0;
  double g = ((size / 1024.0) / 1024.0) / 1024.0;
  double t = (((size / 1024.0) / 1024.0) / 1024.0) / 1024.0;

  DecimalFormat dec = new DecimalFormat("0.00");

  if (t > 1) {
    hrSize = dec.format(t).concat(" TB");
  } else if (g > 1) {
    hrSize = dec.format(g).concat(" GB");
  } else if (m > 1) {
    hrSize = dec.format(m).concat(" MB");
  } else if (k > 1) {
    hrSize = dec.format(k).concat(" KB");
  } else {
    hrSize = dec.format(b).concat(" Bytes");
  }

  return hrSize;
}

@Override
public byte[] transform(ClassLoader loader, String className, Class<?> classBeingRedefined,
    ProtectionDomain protectionDomain, byte[] classfileBuffer) throws IllegalClassFormatException {

  if (className == null) {
    return null;
  }

  className = className.replace('/', '.');

  List<RetransformEntry> allRetransformEntries = allRetransformEntries();
  ListIterator<RetransformEntry> listIterator = allRetransformEntries.listIterator(allRetransformEntries.size());
  while (listIterator.hasPrevious()) {
    RetransformEntry retransformEntry = listIterator.previous();
    int id = retransformEntry.getId();
    boolean updateFlag = false;
    if (className.equals(retransformEntry.getClassName())) {
      if (retransformEntry.getClassLoaderClass() != null || retransformEntry.getHashCode() != null) {
        updateFlag = isLoaderMatch(retransformEntry, loader);
      } else {
        updateFlag = true;
      }
    }

    if (updateFlag) {
      logger.info("RetransformCommand match class: {}, id: {}, classLoaderClass: {}, hashCode: {}",
          className, id, retransformEntry.getClassLoaderClass(), retransformEntry.getHashCode());
      retransformEntry.incTransformCount();
      return retransformEntry.getBytes();
    }

  }

  return null;
}

public static void drawPlayException(TableElement table, ObjectVO throwableVO) {
  table.row("IS-RETURN", "" + false);
  table.row("IS-EXCEPTION", "" + true);

  Throwable cause;
  Throwable t = (Throwable) throwableVO.getObject();
  if (t instanceof InvocationTargetException) {
    cause = t.getCause();
  } else {
    cause = t;
  }

  if (throwableVO.needExpand()) {
    table.row("THROW-EXCEPTION", new ObjectView(cause, throwableVO.expandOrDefault()).draw());
  } else {
    StringWriter stringWriter = new StringWriter();
    PrintWriter printWriter = new PrintWriter(stringWriter);
    try {
      cause.printStackTrace(printWriter);
      table.row("THROW-EXCEPTION", stringWriter.toString());
    } finally {
      printWriter.close();
    }
  }
}

private AsyncProfiler profilerInstance() {
  if (profiler != null) {
    return profiler;
  }

  if (ProfilerAction.load.toString().equals(action)) {
    profiler = AsyncProfiler.getInstance(this.actionArg);
  }

  if (libPath != null) {
    FileOutputStream tmpLibOutputStream = null;
    FileInputStream libInputStream = null;
    try {
      File tmpLibFile = File.createTempFile(VmTool.JNI_LIBRARY_NAME, null);
      tmpLibOutputStream = new FileOutputStream(tmpLibFile);
      libInputStream = new FileInputStream(libPath);

      IOUtils.copy(libInputStream, tmpLibOutputStream);
      libPath = tmpLibFile.getAbsolutePath();
      logger.debug("copy {} to {}", libPath, tmpLibFile);
    } catch (Throwable e) {
      logger.error("try to copy lib error! libPath: {}", libPath, e);
    } finally {
      IOUtils.close(libInputStream);
      IOUtils.close(tmpLibOutputStream);
    }
    profiler = AsyncProfiler.getInstance(libPath);
  } else {
    if (OSUtils.isLinux() || OSUtils.isMac()) {
      throw new IllegalStateException("Can not find libasyncProfiler so, please check the arthas directory.");
    } else {
      throw new IllegalStateException("Current OS do not support AsyncProfiler, Only support Linux/Mac.");
    }
  }

  return profiler;
}

public static AdminServer createAdminServer() {
    if (!"false".equals(System.getProperty("zookeeper.admin.enableServer"))) {
        try {
            Class<?> jettyAdminServerC = Class.forName("org.apache.zookeeper.server.admin.JettyAdminServer");
            Object adminServer = jettyAdminServerC.getConstructor().newInstance();
            return (AdminServer) adminServer;
        } catch (ClassNotFoundException
                | InstantiationException
                | IllegalAccessException
                | InvocationTargetException
                | NoSuchMethodException e) {
            LOG.warn("Unable to start JettyAdminServer", e);
        } catch (NoClassDefFoundError e) {
            LOG.warn("Unable to load jetty, not starting JettyAdminServer", e);
        }
    }
    return new DummyAdminServer();
}

public void run() {
    try {
        while (!stopped) {
            try {
                select();
                processAcceptedConnections();
                processInterestOpsUpdateRequests();
            } catch (RuntimeException e) {
                LOG.warn("Ignoring unexpected runtime exception", e);
            } catch (Exception e) {
                LOG.warn("Ignoring unexpected exception", e);
            }
        }

        // Close connections still pending on the selector. Any others
        // with in-flight work, let drain out of the work queue.
        for (SelectionKey key : selector.keys()) {
            NIOServerCnxn cnxn = (NIOServerCnxn) key.attachment();
            if (cnxn.isSelectable()) {
                cnxn.close(ServerCnxn.DisconnectReason.SERVER_SHUTDOWN);
            }
            cleanupSelectionKey(key);
        }
        SocketChannel accepted;
        while ((accepted = acceptedQueue.poll()) != null) {
            fastCloseSock(accepted);
        }
        updateQueue.clear();
    } finally {
        closeSelector();
        // This will wake up the accept thread and the other selector
        // threads, and tell the worker thread pool to begin shutdown.
        NIOServerCnxnFactory.this.stop();
        LOG.info("selector thread exited run method");
    }
}

public static synchronized ZooKeeper getClient(
        String contextPath, String session, int expireTime) throws IOException {
    final String connectionId = concat(contextPath, session);

    ZooKeeper zk = zkMap.get(connectionId);
    if (zk == null) {
        if (LOG.isInfoEnabled()) {
            LOG.info(
                    String.format(
                            "creating new connection for : '%s'", connectionId));
        }
        Endpoint e = contextMap.get(contextPath);
        zk = new ZooKeeper(e.getHostPort(), 30000, new MyWatcher(connectionId));

        for (Map.Entry<String, String> p : e.getZooKeeperAuthInfo().entrySet()) {
            zk.addAuthInfo(
                    "digest",
                    String.format("%s:%s", p.getKey(), p.getValue()).getBytes());
        }

        zkMap.put(connectionId, zk);

        // a session should automatically expire after an amount of time
        if (session != null) {
            zkSessionTimers.put(
                    connectionId,
                    new SessionTimerTask(expireTime, session, contextPath, timer));
        }
    }
    return zk;
}

private void init() throws IOException {
    File f = new File(file);
    RandomAccessFileReader in = new RandomAccessFileReader(f);
    SimpleDateFormat dateformat = new SimpleDateFormat(DATE_FORMAT);
    Pattern idp = Pattern.compile("\\[myid:(\\d+)\\]");

    long lastFp = in.getPosition();
    String line = in.readLine();
    Matcher m = null;

    // if we have read data from the file, and it matches the timep pattern
    if ((line != null) && (m = timep.matcher(line)).lookingAt()) {
        starttime = timestampFromText(dateformat, m.group(1));
    } else {
        throw new IOException("Invalid log format. First line doesn't start with time");
    }

    /*
      Count number of log entries. Any line starting with a timestamp counts as an entry
    */
    String lastentry = line;
    try {
        while (line != null) {
            m = timep.matcher(line);
            if (m.lookingAt()) {
                if (size % skipN == 0) {
                    long time = timestampFromText(dateformat, m.group(1));
                    skiplist.addMark(time, lastFp, size);
                }
                size++;
                lastentry = line;
            }
            if (serverid == 0 && (m = idp.matcher(line)).find()) {
                serverid = Integer.valueOf(m.group(1));
            }

            lastFp = in.getPosition();
            line = in.readLine();
        }
    } catch (EOFException eof) {
        // ignore, simply end of file, though really (line!=null) should have caught this
    } finally {
        in.close();
    }

    m = timep.matcher(lastentry);
    if (m.lookingAt()) {
        endtime = timestampFromText(dateformat, m.group(1));
    } else {
        throw new IOException("Invalid log format. Last line doesn't start with time");
    }
}

@POST
@Produces({MediaType.APPLICATION_JSON, "application/javascript", MediaType.APPLICATION_XML})
public Response createSession(
        @QueryParam("op") String op,
        @DefaultValue("5") @QueryParam("expire") String expire,
        @Context UriInfo ui) {
    if (!op.equals("create")) {
        throw new WebApplicationException(
                Response.status(Response.Status.BAD_REQUEST)
                        .entity(new ZError(ui.getRequestUri().toString(), ""))
                        .build());
    }

    int expireInSeconds;
    try {
        expireInSeconds = Integer.parseInt(expire);
    } catch (NumberFormatException e) {
        throw new WebApplicationException(
                Response.status(Response.Status.BAD_REQUEST).build());
    }

    String uuid = UUID.randomUUID().toString();
    while (ZooKeeperService.isConnected(contextPath, uuid)) {
        uuid = UUID.randomUUID().toString();
    }

    // establish the connection to the ZooKeeper cluster
    try {
        ZooKeeperService.getClient(contextPath, uuid, expireInSeconds);
    } catch (IOException e) {
        LOG.error("Failed while trying to create a new session", e);
        throw new WebApplicationException(
                Response.status(Response.Status.INTERNAL_SERVER_ERROR).build());
    }

    URI uri = ui.getAbsolutePathBuilder().path(uuid).build();
    return Response.created(uri)
            .entity(new JSONWithPadding(new ZSession(uuid, uri.toString())))
            .build();
}

public void schedule(WorkRequest workRequest, long id) {
    if (stopped) {
        workRequest.cleanup();
        return;
    }

    ScheduledWorkRequest scheduledWorkRequest = new ScheduledWorkRequest(workRequest);

    // If we have a worker thread pool, use that; otherwise, do the work directly.
    int size = workers.size();
    if (size > 0) {
        try {
            // make sure to map negative ids as well to [0, size-1]
            int workerNum = ((int) (id % size) + size) % size;
            ExecutorService worker = workers.get(workerNum);
            worker.execute(scheduledWorkRequest);
        } catch (RejectedExecutionException e) {
            LOG.warn("ExecutorService rejected execution", e);
            workRequest.cleanup();
        }
    } else {
        // When there is no worker thread pool, do the work directly
        // and wait for its completion
        scheduledWorkRequest.run();
    }
}

@Override
public void close() throws IOException {
    boolean triedToClose = false;
    boolean success = false;
    try {
        flush();
        ((FileOutputStream) out).getFD().sync();

        triedToClose = true;
        super.close();
        success = true;
    } finally {
        if (success) {
            boolean renamed = tmpFile.renameTo(origFile);
            if (!renamed) {
                // On windows, renameTo does not replace.
                if (!origFile.delete() || !tmpFile.renameTo(origFile)) {
                    throw new IOException(
                            "Could not rename temporary file " + tmpFile + " to " + origFile);
                }
            }
        } else {
            if (!triedToClose) {
                // If we failed when flushing, try to close it to not leak
                // an FD
                IOUtils.closeStream(out);
            }
            // close wasn't successful, try to delete the tmp file
            if (!tmpFile.delete()) {
                LOG.warn("Unable to delete tmp file {}", tmpFile);
            }
        }
    }
}

private synchronized LogEntry readNextEntry() {
    try {
        try {
            while (true) {
                String line = in.readLine();
                if (line == null) {
                    break;
                }

                Matcher m = src.timep.matcher(line);
                if (m.lookingAt()) {
                    if (buf.length() > 0) {
                        LogEntry e =
                                new Log4JEntry(
                                        src.timestampFromText(dateformat, buf),
                                        src.getServerId(),
                                        buf);
                        buf = line;
                        return e;
                    }
                    buf = line;
                } else if (buf.length() > 0) {
                    buf += line + "\n";
                }
            }
        } catch (EOFException eof) {
            // ignore, we've simply come to the end of the file
        }
        if (buf.length() > 0) {
            LogEntry e =
                    new Log4JEntry(
                            src.timestampFromText(dateformat, buf),
                            src.getServerId(),
                            buf);
            buf = "";
            return e;
        }
    } catch (Exception e) {
        LOG.error(
                "Error reading next entry in file (" + src.file + "): " + e);
        return null;
    }
    return null;
}

