private void doPostprocessing() {
    errCode = ERROR_NOTHING;
    errObject = null;
    final Thread thread = Thread.currentThread();

    notifyPostProcessing(1);

    if (DEBUG) {
        thread.setName("[" + TAG + "]  ps = " + psAlgorithm + "  filename = " + storage.getName());
    }

    Exception exception = null;

    try {
        psAlgorithm.run(this);
    } catch (final Exception err) {
        Log.e(TAG, "Post-processing failed. " + psAlgorithm.toString(), err);

        if (err instanceof InterruptedIOException
                || err instanceof ClosedByInterruptException
                || thread.isInterrupted()) {
            notifyError(DownloadMission.ERROR_POSTPROCESSING_STOPPED, null);
            return;
        }

        if (errCode == ERROR_NOTHING) {
            errCode = ERROR_POSTPROCESSING;
        }

        exception = err;
    } finally {
        notifyPostProcessing(errCode == ERROR_NOTHING ? 2 : 0);
    }

    if (errCode != ERROR_NOTHING) {
        if (exception == null) {
            exception = errObject;
        }
        notifyError(ERROR_POSTPROCESSING, exception);
        return;
    }

    notifyFinished();
}

void establishConnection(final int threadId, final HttpURLConnection conn)
        throws IOException, HttpError {
    final int statusCode = conn.getResponseCode();

    if (DEBUG) {
        Log.d(TAG, threadId + ":[request]  Range=" + conn.getRequestProperty("Range"));
        Log.d(TAG, threadId + ":[response] Code=" + statusCode);
        Log.d(TAG, threadId + ":[response] Content-Length=" + conn.getContentLength());
        Log.d(TAG, threadId + ":[response] Content-Range=" + conn.getHeaderField("Content-Range"));
    }

    switch (statusCode) {
        case 204:
        case 205:
        case 207:
            throw new HttpError(statusCode);
        case 416:
            // let the download thread handle this error
            return;
        default:
            if (statusCode < 200 || statusCode > 299) {
                throw new HttpError(statusCode);
            }
            break;
    }
}

private Elst parseEdts(@NonNull final Box ref) throws IOException {
    final Box b = untilBox(ref, ATOM_ELST);
    if (b == null) {
        return null;
    }

    final Elst obj = new Elst();

    final boolean v1 = stream.read() == 1;
    stream.skipBytes(3); // flags

    final int entryCount = stream.readInt();
    if (entryCount < 1) {
        obj.bMediaRate = 0x00010000; // default media rate (1.0)
        return obj;
    }

    if (v1) {
        stream.skipBytes(DataReader.LONG_SIZE); // segment duration
        obj.mediaTime = stream.readLong();
        // ignore all remain entries
        stream.skipBytes((entryCount - 1) * (DataReader.LONG_SIZE * 2));
    } else {
        stream.skipBytes(DataReader.INTEGER_SIZE); // segment duration
        obj.mediaTime = stream.readInt();
    }

    obj.bMediaRate = stream.readInt();

    return obj;
}

@Override
public void run() {
    boolean done;
    long start = mMission.fallbackResumeOffset;

    if (DEBUG && !mMission.unknownLength && start > 0) {
        Log.i(TAG, "Resuming a single-thread download at " + start);
    }

    try {
        final long rangeStart = (mMission.unknownLength || start < 1) ? -1 : start;

        final int mId = 1;
        mConn = mMission.openConnection(false, rangeStart, -1);

        if (mRetryCount == 0 && rangeStart == -1) {
            // workaround: bypass android connection pool
            mConn.setRequestProperty("Range", "bytes=0-");
        }

        mMission.establishConnection(mId, mConn);

        // check if the download can be resumed
        if (mConn.getResponseCode() == 416 && start > 0) {
            mMission.notifyProgress(-start);
            start = 0;
            mRetryCount--;
            throw new DownloadMission.HttpError(416);
        }

        // secondary check for the file length
        if (!mMission.unknownLength) {
            mMission.unknownLength = Utility.getContentLength(mConn) == -1;
        }

        if (mMission.unknownLength || mConn.getResponseCode() == 200) {
            // restart amount of bytes downloaded
            mMission.done = mMission.offsets[mMission.current] - mMission.offsets[0];
        }

        mF = mMission.storage.getStream();
        mF.seek(mMission.offsets[mMission.current] + start);

        mIs = mConn.getInputStream();

        final byte[] buf = new byte[DownloadMission.BUFFER_SIZE];
        int len = 0;

        while (mMission.running && (len = mIs.read(buf, 0, buf.length)) != -1) {
            mF.write(buf, 0, len);
            start += len;
            mMission.notifyProgress(len);
        }

        dispose();

        // if thread goes interrupted check if the last part is written. This avoid re-download the whole file
        done = len == -1;
    } catch (final Exception e) {
        dispose();

        mMission.fallbackResumeOffset = start;

        if (!mMission.running || e instanceof ClosedByInterruptException) {
            return;
        }

        if (e instanceof HttpError
                && ((HttpError) e).statusCode == ERROR_HTTP_FORBIDDEN) {
            // for youtube streams. The url has expired, recover
            dispose();
            mMission.doRecover(ERROR_HTTP_FORBIDDEN);
            return;
        }

        if (mRetryCount++ >= mMission.maxRetry) {
            mMission.notifyError(e);
            return;
        }

        if (DEBUG) {
            Log.e(TAG, "got exception, retrying...", e);
        }

        run(); // try again
        return;
    }

    if (done) {
        mMission.notifyFinished();
    } else {
        mMission.fallbackResumeOffset = start;
    }
}

private void flushAuxiliar(final long amount) throws IOException {
    if (aux.length < 1) {
        return;
    }

    out.flush();
    aux.flush();

    final boolean underflow = aux.offset < aux.length || out.offset < out.length;
    final byte[] buffer = new byte[COPY_BUFFER_SIZE];

    aux.target.seek(0);
    out.target.seek(out.length);

    long length = amount;
    while (length > 0) {
        int read = (int) Math.min(length, Integer.MAX_VALUE);
        read = aux.target.read(buffer, 0, Math.min(read, buffer.length));

        if (read < 1) {
            amount -= length;
            break;
        }

        out.writeProof(buffer, read);
        length -= read;
    }

    if (underflow) {
        if (out.offset >= out.length) {
            // calculate the aux underflow pointer
            if (aux.offset < amount) {
                out.offset += aux.offset;
                aux.offset = 0;
                out.target.seek(out.offset);
            } else {
                aux.offset -= amount;
                out.offset = out.length + amount;
            }
        } else {
            aux.offset = 0;
        }
    } else {
        out.offset += amount;
        aux.offset -= amount;
    }

    out.length += amount;

    if (out.length > maxLengthKnown) {
        maxLengthKnown = out.length;
    }

    if (amount < aux.length) {
        // move the excess data to the beginning of the file
        long readOffset = amount;
        long writeOffset = 0;

        aux.length -= amount;
        length = aux.length;
        while (length > 0) {
            int read = (int) Math.min(length, Integer.MAX_VALUE);
            read = aux.target.read(buffer, 0, Math.min(read, buffer.length));

            aux.target.seek(writeOffset);
            aux.writeProof(buffer, read);

            writeOffset += read;
            readOffset += read;
            length -= read;

            aux.target.seek(readOffset);
        }

        aux.target.setLength(aux.length);
        return;
    }

    if (aux.length > THRESHOLD_AUX_LENGTH) {
        aux.target.setLength(THRESHOLD_AUX_LENGTH); // or setLength(0);
    }

    aux.reset();
}

private void showError(@NonNull final DownloadMission mission,
                       @NonNull final UserAction action,
                       @StringRes final int reason) {
    final StringBuilder request = new StringBuilder(256);
    request.append(mission.source);

    request.append(" [");
    if (mission.recoveryInfo != null) {
        for (final MissionRecoveryInfo recovery : mission.recoveryInfo) {
            request.append(' ')
                    .append(recovery.toString())
                    .append(' ');
        }
    }
    request.append("]");

    final String service;
    try {
        service = NewPipe.getServiceByUrl(mission.source)
                .getServiceInfo()
                .getName();
    } catch (final Exception e) {
        service = ErrorInfo.SERVICE_NONE;
    }

    ErrorUtil.createNotification(mContext,
            new ErrorInfo(ErrorInfo.Companion.throwableToStringList(mission.errObject),
                    action,
                    service,
                    request.toString(),
                    reason));
}

void startMission(@NonNull final DownloadMission mission) {
    synchronized (this) {
        mission.timestamp = System.currentTimeMillis();
        mission.mHandler = mHandler;
        mission.maxRetry = mPrefMaxRetry;

        // create metadata file
        while (true) {
            mission.metadata = new File(mPendingMissionsDir, String.valueOf(mission.timestamp));
            if (!mission.metadata.isFile() && !mission.metadata.exists()) {
                try {
                    if (!mission.metadata.createNewFile()) {
                        throw new RuntimeException("Cant create download metadata file");
                    }
                } catch (final IOException e) {
                    throw new RuntimeException(e);
                }
                break;
            }
            mission.timestamp = System.currentTimeMillis();
        }

        mSelfMissionsControl = true;
        mMissionsPending.add(mission);

        // Before continue, save the metadata in case the internet connection is not available
        Utility.writeToFile(mission.metadata, mission);

        if (mission.storage == null) {
            // nothing to do here
            mission.errCode = DownloadMission.ERROR_FILE_CREATION;
            if (mission.errObject != null) {
                mission.errObject = new IOException("DownloadMission.storage == NULL");
            }
            return;
        }

        final boolean start = !mPrefQueueLimit || getRunningMissionsCount() < 1;

        if (canDownloadInCurrentNetwork() && start) {
            mission.start();
        }
    }
}

@Override
int process(@NonNull final SharpStream out, @NonNull final SharpStream... sources)
        throws IOException {
    // check if the subtitle is already in srt and copy, this should never happen
    final String format = getArgumentAt(0, null);
    final boolean ignoreEmptyFrames = getArgumentAt(1, "true").equals("true");

    if (format == null || format.equals("ttml")) {
        final SrtFromTtmlWriter writer = new SrtFromTtmlWriter(out, ignoreEmptyFrames);

        try {
            writer.build(sources[0]);
        } catch (final Exception err) {
            Log.e(TAG, "subtitle parse failed", err);
            return err instanceof IOException ? 1 : 8;
        }

        return OK_RESULT;
    } else if (format.equals("srt")) {
        final byte[] buffer = new byte[8 * 1024];
        int read;
        while ((read = sources[0].read(buffer)) > 0) {
            out.write(buffer, 0, read);
        }
        return OK_RESULT;
    }

    throw new UnsupportedOperationException("Can't convert this subtitle, unimplemented format: " + format);
}

private void joinForThreads(final int millis) {
    final Thread currentThread = Thread.currentThread();

    if (init != null && init != currentThread && init.isAlive()) {
        init.interrupt();

        if (millis > 0) {
            try {
                init.join(millis);
            } catch (final InterruptedException e) {
                Log.w(TAG, "Initializer thread is still running", e);
                return;
            }
        }
    }

    // if a thread is still alive, possible reasons:
    //      slow device
    //      the user is spamming start/pause buttons
    //      start() method called quickly after pause()

    for (final Thread thread : threads) {
        if (!thread.isAlive() || thread == Thread.currentThread()) {
            continue;
        }
        thread.interrupt();
    }

    try {
        for (final Thread thread : threads) {
            if (!thread.isAlive()) {
                continue;
            }
            if (DEBUG) {
                Log.w(TAG, "thread alive: " + thread.getName());
            }
            if (millis > 0) {
                thread.join(millis);
            }
        }
    } catch (final InterruptedException e) {
        throw new RuntimeException("A download thread is still running", e);
    }
}

public static AdminServer createAdminServer() {
    if (!"false".equals(System.getProperty("zookeeper.admin.enableServer"))) {
        try {
            Class<?> jettyAdminServerC = Class.forName("org.apache.zookeeper.server.admin.JettyAdminServer");
            Object adminServer = jettyAdminServerC.getConstructor().newInstance();
            return (AdminServer) adminServer;
        } catch (ClassNotFoundException | InstantiationException | IllegalAccessException | InvocationTargetException | NoSuchMethodException e) {
            LOG.warn("Unable to start JettyAdminServer", e);
        } catch (NoClassDefFoundError e) {
            LOG.warn("Unable to load jetty, not starting JettyAdminServer", e);
        }
    }
    return new DummyAdminServer();
}

@Override
public void run() {
    try {
        while (!stopped) {
            try {
                select();
                processAcceptedConnections();
                processInterestOpsUpdateRequests();
            } catch (RuntimeException e) {
                LOG.warn("Ignoring unexpected runtime exception", e);
            } catch (Exception e) {
                LOG.warn("Ignoring unexpected exception", e);
            }
        }

        // Close connections still pending on the selector. Any others
        // with in-flight work, let drain out of the work queue.
        for (SelectionKey key : selector.keys()) {
            NIOServerCnxn cnxn = (NIOServerCnxn) key.attachment();
            if (cnxn.isSelectable()) {
                cnxn.close(ServerCnxn.DisconnectReason.SERVER_SHUTDOWN);
            }
            cleanupSelectionKey(key);
        }
        SocketChannel accepted;
        while ((accepted = acceptedQueue.poll()) != null) {
            fastCloseSock(accepted);
        }
        updateQueue.clear();
    } finally {
        closeSelector();
        // This will wake up the accept thread and the other selector
        // threads, and tell the worker thread pool to begin shutdown.
        NIOServerCnxnFactory.this.stop();
        LOG.info("selector thread exited run method");
    }
}

public static synchronized ZooKeeper getClient(String contextPath, String session, int expireTime) throws IOException {
    final String connectionId = concat(contextPath, session);

    ZooKeeper zk = zkMap.get(connectionId);
    if (zk == null) {

        if (LOG.isInfoEnabled()) {
            LOG.info(String.format("creating new connection for : '%s'", connectionId));
        }
        Endpoint e = contextMap.get(contextPath);
        zk = new ZooKeeper(e.getHostPort(), 30000, new MyWatcher(connectionId));

        for (Map.Entry<String, String> p : e.getZooKeeperAuthInfo().entrySet()) {
            zk.addAuthInfo("digest", String.format("%s:%s", p.getKey(), p.getValue()).getBytes());
        }

        zkMap.put(connectionId, zk);

        // a session should automatically expire after an amount of time
        if (session != null) {
            zkSessionTimers.put(connectionId, new SessionTimerTask(expireTime, session, contextPath, timer));
        }
    }
    return zk;
}

private void init() throws IOException {
    File f = new File(file);
    RandomAccessFileReader in = new RandomAccessFileReader(f);
    SimpleDateFormat dateformat = new SimpleDateFormat(DATE_FORMAT);
    Pattern idp = Pattern.compile("\\[myid:(\\d+)\\]");

    long lastFp = in.getPosition();
    String line = in.readLine();
    Matcher m = null;

    // if we have read data from the file, and it matches the timep pattern
    if ((line != null) && (m = timep.matcher(line)).lookingAt()) {
        starttime = timestampFromText(dateformat, m.group(1));
    } else {
        throw new IOException("Invalid log format. First line doesn't start with time");
    }

    /*
      Count number of log entries. Any line starting with a timestamp counts as an entry
    */
    String lastentry = line;
    try {
        for (;;) {
            if (line == null) {
                break;
            }
            m = timep.matcher(line);
            if (m.lookingAt()) {
                if (size % skipN == 0) {
                    long time = timestampFromText(dateformat, m.group(1));
                    skiplist.addMark(time, lastFp, size);
                }
                size++;
                lastentry = line;
            }
            if (serverid == 0 && (m = idp.matcher(line)).find()) {
                serverid = Integer.valueOf(m.group(1));
            }

            lastFp = in.getPosition();
            line = in.readLine();
        }
    } catch (EOFException eof) {
        // ignore, simply end of file, though really (line!=null) should have caught this
    } finally {
        in.close();
    }

    m = timep.matcher(lastentry);
    if (m.lookingAt()) {
        endtime = timestampFromText(dateformat, m.group(1));
    } else {
        throw new IOException("Invalid log format. Last line doesn't start with time");
    }
}

@POST
@Produces({ MediaType.APPLICATION_JSON, "application/javascript", MediaType.APPLICATION_XML })
public Response createSession(@QueryParam("op") String op,
                              @DefaultValue("5") @QueryParam("expire") String expire,
                              @Context UriInfo ui) {
    if (!op.equals("create")) {
        throw new WebApplicationException(
            Response.status(Response.Status.BAD_REQUEST)
                    .entity(new ZError(ui.getRequestUri().toString(), ""))
                    .build()
        );
    }

    int expireInSeconds;
    try {
        expireInSeconds = Integer.parseInt(expire);
    } catch (NumberFormatException e) {
        throw new WebApplicationException(Response.status(Response.Status.BAD_REQUEST).build());
    }

    String uuid = UUID.randomUUID().toString();
    while (ZooKeeperService.isConnected(contextPath, uuid)) {
        uuid = UUID.randomUUID().toString();
    }

    // establish the connection to the ZooKeeper cluster
    try {
        ZooKeeperService.getClient(contextPath, uuid, expireInSeconds);
    } catch (IOException e) {
        LOG.error("Failed while trying to create a new session", e);

        throw new WebApplicationException(Response.status(Response.Status.INTERNAL_SERVER_ERROR).build());
    }

    URI uri = ui.getAbsolutePathBuilder().path(uuid).build();
    return Response.created(uri)
            .entity(new JSONWithPadding(new ZSession(uuid, uri.toString())))
            .build();
}

public void schedule(WorkRequest workRequest, long id) {
    if (stopped) {
        workRequest.cleanup();
        return;
    }

    ScheduledWorkRequest scheduledWorkRequest = new ScheduledWorkRequest(workRequest);

    // If we have a worker thread pool, use that; otherwise, do the work directly.
    int size = workers.size();
    if (size > 0) {
        try {
            // make sure to map negative ids as well to [0, size-1]
            int workerNum = ((int) (id % size) + size) % size;
            ExecutorService worker = workers.get(workerNum);
            worker.execute(scheduledWorkRequest);
        } catch (RejectedExecutionException e) {
            LOG.warn("ExecutorService rejected execution", e);
            workRequest.cleanup();
        }
    } else {
        // When there is no worker thread pool, do the work directly
        // and wait for its completion
        scheduledWorkRequest.run();
    }
}

@Override
public void close() throws IOException {
    boolean triedToClose = false;
    boolean success = false;
    try {
        flush();
        ((FileOutputStream) out).getFD().sync();

        triedToClose = true;
        super.close();
        success = true;
    } finally {
        if (success) {
            boolean renamed = tmpFile.renameTo(origFile);
            if (!renamed) {
                // On windows, renameTo does not replace.
                if (!origFile.delete() || !tmpFile.renameTo(origFile)) {
                    throw new IOException("Could not rename temporary file " + tmpFile + " to " + origFile);
                }
            }
        } else {
            if (!triedToClose) {
                // If we failed when flushing, try to close it to not leak an FD
                IOUtils.closeStream(out);
            }
            // close wasn't successful, try to delete the tmp file
            if (!tmpFile.delete()) {
                LOG.warn("Unable to delete tmp file {}", tmpFile);
            }
        }
    }
}

private synchronized LogEntry readNextEntry() {
    try {
        for (;;) {
            String line = in.readLine();
            if (line == null) {
                break;
            }

            Matcher m = src.timep.matcher(line);
            if (m.lookingAt()) {
                if (buf.length() > 0) {
                    LogEntry e = new Log4JEntry(src.timestampFromText(dateformat, buf), src.getServerId(), buf);
                    buf = line;
                    return e;
                }
                buf = line;
            } else if (buf.length() > 0) {
                buf += line + "\n";
            }
        }
        if (buf.length() > 0) {
            LogEntry e = new Log4JEntry(src.timestampFromText(dateformat, buf), src.getServerId(), buf);
            buf = "";
            return e;
        }
    } catch (EOFException eof) {
        // ignore, we've simply come to the end of the file
    } catch (Exception e) {
        LOG.error("Error reading next entry in file (" + src.file + "): " + e);
        return null;
    }
    return null;
}

