void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final Observer<? super R> downstream = this.downstream;
    final AtomicThrowable errors = this.errors;
    final AtomicReference<SwitchMapMaybeObserver<R>> inner = this.inner;

    for (;;) {
        for (;;) {
            if (cancelled) {
                return;
            }

            if (errors.get() != null) {
                if (!delayErrors) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            final boolean d = done;
            final SwitchMapMaybeObserver<R> current = inner.get();
            final boolean empty = current == null;

            if (d && empty) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (empty || current.item == null) {
                break;
            }

            inner.compareAndSet(current, null);

            downstream.onNext(current.item);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void onNext(final T t) {
    if (done) {
        return;
    }
    final boolean b;
    try {
        b = predicate.test(t);
    } catch (final Throwable e) {
        Exceptions.throwIfFatal(e);
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        onError(e);
        return;
    }
    if (!b) {
        done = true;
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        downstream.onSuccess(false);
    }
}

void drainLoop() {
    final Observer<? super T> actual = this.downstream;
    int missed = 1;
    for (;;) {
        for (;;) {
            if (disposed) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(actual);
                return;
            }

            int os = otherState;
            if (os == OTHER_STATE_HAS_VALUE) {
                final T v = singleItem;
                singleItem = null;
                otherState = OTHER_STATE_CONSUMED_OR_EMPTY;
                os = OTHER_STATE_CONSUMED_OR_EMPTY;
                actual.onNext(v);
            }

            final boolean d = mainDone;
            final SimplePlainQueue<T> q = queue;
            final T v = q != null ? q.poll() : null;
            final boolean empty = v == null;

            if (d && empty && os == OTHER_STATE_CONSUMED_OR_EMPTY) {
                queue = null;
                actual.onComplete();
                return;
            }

            if (empty) {
                break;
            }

            actual.onNext(v);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final SimplePlainQueue<Object> queue = this.queue;
    final Subscriber<? super Flowable<T>> downstream = this.downstream;
    UnicastProcessor<T> window = this.window;

    for (;;) {
        if (upstreamCancelled) {
            queue.clear();
            window = null;
            this.window = null;
        } else {
            final boolean isDone = done;
            final Object o = queue.poll();
            final boolean isEmpty = o == null;

            if (isDone && isEmpty) {
                final Throwable ex = error;
                if (ex != null) {
                    if (window != null) {
                        window.onError(ex);
                    }
                    downstream.onError(ex);
                } else {
                    if (window != null) {
                        window.onComplete();
                    }
                    downstream.onComplete();
                }
                cleanupResources();
                upstreamCancelled = true;
                continue;
            } else if (!isEmpty) {
                if (o instanceof WindowBoundaryRunnable) {
                    final WindowBoundaryRunnable boundary = (WindowBoundaryRunnable) o;
                    if (boundary.index == emitted || !restartTimerOnMaxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    }
                } else if (window != null) {
                    @SuppressWarnings("unchecked")
                    final T item = (T) o;
                    window.onNext(item);

                    final long count = this.count + 1;
                    if (count == maxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    } else {
                        this.count = count;
                    }
                }
                continue;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final EqualObserver<T>[] as = observers;

    final EqualObserver<T> observer1 = as[0];
    final SpscLinkedArrayQueue<T> q1 = observer1.queue;
    final EqualObserver<T> observer2 = as[1];
    final SpscLinkedArrayQueue<T> q2 = observer2.queue;

    for (;;) {
        for (;;) {
            if (cancelled) {
                q1.clear();
                q2.clear();
                return;
            }

            final boolean d1 = observer1.done;

            if (d1) {
                final Throwable e = observer1.error;
                if (e != null) {
                    cancel(q1, q2);

                    downstream.onError(e);
                    return;
                }
            }

            final boolean d2 = observer2.done;
            if (d2) {
                final Throwable e = observer2.error;
                if (e != null) {
                    cancel(q1, q2);

                    downstream.onError(e);
                    return;
                }
            }

            if (v1 == null) {
                v1 = q1.poll();
            }
            final boolean e1 = v1 == null;

            if (v2 == null) {
                v2 = q2.poll();
            }
            final boolean e2 = v2 == null;

            if (d1 && d2 && e1 && e2) {
                downstream.onSuccess(true);
                return;
            }
            if ((d1 && d2) && (e1 != e2)) {
                cancel(q1, q2);

                downstream.onSuccess(false);
                return;
            }

            if (!e1 && !e2) {
                final boolean c;
                try {
                    c = comparer.test(v1, v2);
                } catch (final Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    cancel(q1, q2);

                    downstream.onError(ex);
                    return;
                }

                if (!c) {
                    cancel(q1, q2);

                    downstream.onSuccess(false);
                    return;
                }

                v1 = null;
                v2 = null;
            }

            if (e1 || e2) {
                break;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drainLoop() {
    int missed = 1;

    final JoinInnerSubscriber<T>[] s = this.subscribers;
    final int n = s.length;
    final Subscriber<? super T> a = this.downstream;

    for (;;) {
        final long r = requested.get();
        long e = 0;

        middle:
        while (e != r) {
            if (cancelled) {
                cleanup();
                return;
            }

            final Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }

            final boolean d = done.get() == 0;

            boolean empty = true;

            for (int i = 0; i < s.length; i++) {
                final JoinInnerSubscriber<T> inner = s[i];
                final SimplePlainQueue<T> q = inner.queue;
                if (q != null) {
                    final T v = q.poll();

                    if (v != null) {
                        empty = false;
                        a.onNext(v);
                        inner.requestOne();
                        if (++e == r) {
                            break middle;
                        }
                    }
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }

            if (empty) {
                break;
            }
        }

        if (e == r) {
            if (cancelled) {
                cleanup();
                return;
            }

            final Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }

            final boolean d = done.get() == 0;

            boolean empty = true;

            for (int i = 0; i < n; i++) {
                final JoinInnerSubscriber<T> inner = s[i];
                final SimpleQueue<T> q = inner.queue;
                if (q != null && !q.isEmpty()) {
                    empty = false;
                    break;
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }
        }

        if (e != 0) {
            BackpressureHelper.produced(requested, e);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    InnerQueuedSubscriber<R> inner = current;
    final Subscriber<? super R> a = downstream;
    final ErrorMode em = errorMode;

    for (;;) {
        final long r = requested.get();
        long e = 0L;

        if (inner == null) {
            if (em != ErrorMode.END) {
                final Throwable ex = errors.get();
                if (ex != null) {
                    cancelAll();
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            final boolean outerDone = done;

            inner = subscribers.poll();

            if (outerDone && inner == null) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (inner != null) {
                current = inner;
            }
        }

        boolean continueNextSource = false;

        if (inner != null) {
            final SimpleQueue<R> q = inner.queue();
            if (q != null) {
                while (e != r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }

                    if (em == ErrorMode.IMMEDIATE) {
                        final Throwable ex = errors.get();
                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();

                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    final boolean d = inner.isDone();

                    final R v;
                    try {
                        v = q.poll();
                    } catch (final Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        current = null;
                        inner.cancel();
                        cancelAll();
                        a.onError(ex);
                        return;
                    }

                    final boolean empty = v == null;

                    if (d && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                        break;
                    }

                    if (empty) {
                        break;
                    }

                    a.onNext(v);

                    e++;

                    inner.request(1L);
                }

                if (e == r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }

                    if (em == ErrorMode.IMMEDIATE) {
                        final Throwable ex = errors.get();
                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();

                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    final boolean d = inner.isDone();

                    final boolean empty = q.isEmpty();

                    if (d && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                    }
                }
