  if (list == null || list.isEmpty()) return null;

  if (list.get(0) instanceof KeyValue) { // RESP3
    Object resultsData = null, profileData = null;

    for (KeyValue keyValue : (List<KeyValue>) data) {
      String keyStr = BuilderFactory.STRING.build(keyValue.getKey());
      switch (keyStr) {
        case PROFILE_STR_REDIS7:
        case PROFILE_STR_REDIS8:
          profileData = keyValue.getValue();
          break;
        case RESULTS_STR_REDIS7:
          resultsData = data;
          break;
        case RESULTS_STR_REDIS8:
          resultsData = keyValue.getValue();
          break;
      }
    }

    assert resultsData != null : "Could not detect Results data.";
    assert profileData != null : "Could not detect Profile data.";
    return KeyValue.of(resultsBuilder.build(resultsData),
            ProfilingInfo.PROFILING_INFO_BUILDER.build(profileData));
  }

  return KeyValue.of(resultsBuilder.build(list.get(0)),
      ProfilingInfo.PROFILING_INFO_BUILDER.build(list.get(1)));
}

public SSLContext createSslContext() throws IOException, GeneralSecurityException {
    KeyManager[] keyManagers = null;
    TrustManager[] trustManagers = null;

    if (sslVerifyMode == SslVerifyMode.FULL) {
        this.sslParameters.setEndpointIdentificationAlgorithm("HTTPS");
    } else if (sslVerifyMode == SslVerifyMode.CA) {
        this.sslParameters.setEndpointIdentificationAlgorithm("");
    } else if (sslVerifyMode == SslVerifyMode.INSECURE) {
        trustManagers = new TrustManager[] { INSECURE_TRUST_MANAGER };
    }

    if (keystoreResource != null) {
        KeyStore keyStore = KeyStore.getInstance(keyStoreType);
        try (InputStream keystoreStream = keystoreResource.get()) {
            keyStore.load(keystoreStream, keystorePassword);
        }

        KeyManagerFactory keyManagerFactory = KeyManagerFactory.getInstance(keyManagerAlgorithm);
        keyManagerFactory.init(keyStore, keystorePassword);
        keyManagers = keyManagerFactory.getKeyManagers();
    }

    if (trustManagers == null && truststoreResource != null) {
        KeyStore trustStore = KeyStore.getInstance(trustStoreType);
        try (InputStream truststoreStream = truststoreResource.get()) {
            trustStore.load(truststoreStream, truststorePassword);
        }

        TrustManagerFactory trustManagerFactory = TrustManagerFactory.getInstance(trustManagerAlgorithm);
        trustManagerFactory.init(trustStore);
        trustManagers = trustManagerFactory.getTrustManagers();
    }

    SSLContext sslContext = SSLContext.getInstance(sslProtocol);
    sslContext.init(keyManagers, trustManagers, null);

    return sslContext;
}

@Override
public TSInfo build(Object data) {
  List<KeyValue> list = (List<KeyValue>) data;
  Map<String, Object> properties = new HashMap<>();
  Map<String, String> labels = null;
  Map<String, Rule> rules = null;
  List<Map<String, Object>> chunks = null;

  for (KeyValue propertyValue : list) {
    String prop = BuilderFactory.STRING.build(propertyValue.getKey());
    Object value = propertyValue.getValue();
    if (value instanceof List) {
      switch (prop) {
        case LABELS_PROPERTY:
          labels = BuilderFactory.STRING_MAP.build(value);
          value = labels;
          break;
        case RULES_PROPERTY:
          List<KeyValue> rulesDataList = (List<KeyValue>) value;
          Map<String, List<Object>> rulesValueMap = new HashMap<>(rulesDataList.size(), 1f);
          rules = new HashMap<>(rulesDataList.size());
          for (KeyValue rkv : rulesDataList) {
            String ruleName = BuilderFactory.STRING.build(rkv.getKey());
            List<Object> ruleValueList = BuilderFactory.ENCODED_OBJECT_LIST.build(rkv.getValue());
            rulesValueMap.put(ruleName, ruleValueList);
            rules.put(ruleName, new Rule(ruleName, ruleValueList));
          }
          value = rulesValueMap;
          break;
        case CHUNKS_PROPERTY:
          List<List<KeyValue>> chunksDataList = (List<List<KeyValue>>) value;
          List<Map<String, Object>> chunksValueList = new ArrayList<>(chunksDataList.size());
          chunks = new ArrayList<>(chunksDataList.size());
          for (List<KeyValue> chunkDataAsList : chunksDataList) {
            Map<String, Object> chunk = chunkDataAsList.stream()
                .collect(Collectors.toMap(kv -> BuilderFactory.STRING.build(kv.getKey()),
                    kv -> BuilderFactory.ENCODED_OBJECT.build(kv.getValue())));
            chunksValueList.add(chunk);
            chunks.add(chunk);
          }
          value = chunksValueList;
          break;
        default:
          value = SafeEncoder.encodeObject(value);
          break;
      }
    } else if (value instanceof byte[]) {
      value = BuilderFactory.STRING.build(value);
      if (DUPLICATE_POLICY_PROPERTY.equals(prop)) {
        try {
          value = DuplicatePolicy.valueOf(((String) value).toUpperCase());
        } catch (Exception e) { }
      }
    }
    properties.put(prop, value);
  }

  return new TSInfo(properties, labels, rules, chunks);
}

@Override
public void addParams(CommandArguments args) {
  if (retentionPeriod != null) {
    args.add(RETENTION).add(toByteArray(retentionPeriod));
  }

  if (encoding != null) {
    args.add(ENCODING).add(encoding);
  }

  if (chunkSize != null) {
    args.add(CHUNK_SIZE).add(toByteArray(chunkSize));
  }

  if (duplicatePolicy != null) {
    args.add(DUPLICATE_POLICY).add(duplicatePolicy);
  }

  if (duplicatePolicy != null) {
    args.add(DUPLICATE_POLICY).add(duplicatePolicy);
  }

  if (onDuplicate != null) {
    args.add(ON_DUPLICATE).add(onDuplicate);
  }

  if (ignore) {
    args.add(IGNORE).add(ignoreMaxTimediff).add(ignoreMaxValDiff);
  }

  if (labels != null) {
    args.add(LABELS);
    labels.entrySet().forEach((entry) -> args.add(entry.getKey()).add(entry.getValue()));
  }
}

@Override
public Connection getConnection() {
  List<ConnectionPool> pools = getShuffledNodesPool();

  JedisException suppressed = null;
  for (ConnectionPool pool : pools) {
    Connection jedis = null;
    try {
      jedis = pool.getResource();
      if (jedis == null) {
        continue;
      }

      jedis.ping();
      return jedis;

    } catch (JedisException ex) {
      if (suppressed == null) { // remembering first suppressed exception
        suppressed = ex;
      }
      if (jedis != null) {
        jedis.close();
      }
    }
  }

  JedisException noReachableNode = new JedisException("No reachable shard.");
  if (suppressed != null) {
    noReachableNode.addSuppressed(suppressed);
  }
  throw noReachableNode;
}

@Override
public CacheEntry set(CacheKey cacheKey, CacheEntry entry) {
  lock.lock();
  try {
    entry = putIntoStore(cacheKey, entry);
    EvictionPolicy policy = getEvictionPolicy();
    policy.touch(cacheKey);
    CacheKey evictedKey = policy.evictNext();
    if (evictedKey != null) {
      delete(evictedKey);
      stats.evict();
    }
    for (Object redisKey : cacheKey.getRedisKeys()) {
      ByteBuffer mapKey = makeKeyForRedisKeysToCacheKeys(redisKey);
      if (redisKeysToCacheKeys.containsKey(mapKey)) {
        redisKeysToCacheKeys.get(mapKey).add(cacheKey);
      } else {
        Set<CacheKey<?>> set = ConcurrentHashMap.newKeySet();
        set.add(cacheKey);
        redisKeysToCacheKeys.put(mapKey, set);
      }
    }
    stats.load();
    return entry;
  } finally {
    lock.unlock();
  }
}

@Override
public Class<?> build(Object data) {
  if (data == null) return null;
  String str = STRING.build(data);
  switch (str) {
    case "null":
      return null;
    case "boolean":
      return boolean.class;
    case "integer":
      return int.class;
    case "number":
      return float.class;
    case "string":
      return String.class;
    case "object":
      return Object.class;
    case "array":
      return List.class;
    default:
      throw new JedisException("Unknown type: " + str);
  }
}

private static String convertEmlToHtmlBasic(byte[] emlBytes, EmlToPdfRequest request) {
    if (emlBytes == null || emlBytes.length == 0) {
        throw new IllegalArgumentException("EML file is empty or null");
    }

    String emlContent = new String(emlBytes, StandardCharsets.UTF_8);

    // Basic email parsing
    String subject = extractBasicHeader(emlContent, "Subject:");
    String from = extractBasicHeader(emlContent, "From:");
    String to = extractBasicHeader(emlContent, "To:");
    String cc = extractBasicHeader(emlContent, "Cc:");
    String bcc = extractBasicHeader(emlContent, "Bcc:");
    String date = extractBasicHeader(emlContent, "Date:");

    // Try to extract HTML content
    String htmlBody = extractHtmlBody(emlContent);
    if (htmlBody == null) {
        String textBody = extractTextBody(emlContent);
        htmlBody = convertTextToHtml(
                textBody != null ? textBody : "Email content could not be parsed");
    }

    // Generate HTML with custom styling based on request
    StringBuilder html = new StringBuilder();
    html.append("<!DOCTYPE html>\n");
    html.append("<html><head><meta charset=\"UTF-8\">\n");
    html.append("<title>").append(escapeHtml(subject)).append("</title>\n");
    html.append("<style>\n");
    appendEnhancedStyles(html);
    html.append("</style>\n");
    html.append("</head><body>\n");

    html.append("<div class=\"email-container\">\n");
    html.append("<div class=\"email-header\">\n");
    html.append("<h1>").append(escapeHtml(subject)).append("</h1>\n");
    html.append("<div class=\"email-meta\">\n");
    html.append("<div><strong>From:</strong> ").append(escapeHtml(from)).append("</div>\n");
    html.append("<div><strong>To:</strong> ").append(escapeHtml(to)).append("</div>\n");

    // Include CC and BCC if present and requested
    if (request != null && request.isIncludeAllRecipients()) {
        if (!cc.trim().isEmpty()) {
            html.append("<div><strong>CC:</strong> ").append(escapeHtml(cc)).append("</div>\n");
        }
        if (!bcc.trim().isEmpty()) {
            html.append("<div><strong>BCC:</strong> ")
                .append(escapeHtml(bcc))
                .append("</div>\n");
        }
    }

    if (!date.trim().isEmpty()) {
        html.append("<div><strong>Date:</strong> ").append(escapeHtml(date)).append("</div>\n");
    }
    html.append("</div></div>\n");

    html.append("<div class=\"email-body\">\n");
    html.append(processEmailHtmlBody(htmlBody));
    html.append("</div>\n");

    // Add attachment information - always check for and display attachments
    String attachmentInfo = extractAttachmentInfo(emlContent);
    if (!attachmentInfo.isEmpty()) {
        html.append("<div class=\"attachment-section\">\n");
        html.append("<h3>Attachments</h3>\n");
        html.append(attachmentInfo);

        // Add a status message about attachment inclusion
        if (request != null && request.isIncludeAttachments()) {
            html.append("<div class=\"attachment-inclusion-note\">\n");
            html.append(
                "<p><strong>Note:</strong> Attachments are saved as external files and linked in this PDF. Click the links to open files externally.</p>\n");
            html.append("</div>\n");
        } else {
            html.append("<div class=\"attachment-info-note\">\n");
            html.append(
                "<p><em>Attachment information displayed - files not included in PDF. Enable 'Include attachments' to embed files.</em></p>\n");
            html.append("</div>\n");
        }

        html.append("</div>\n");
    }

    // Show advanced features status if requested
    assert request != null;
    if (request.getFileInput().isEmpty()) {
        html.append("<div class=\"advanced-features-notice\">\n");
        html.append(
            "<p><em>Note: Some advanced features require Jakarta Mail dependencies.</em></p>\n");
        html.append("</div>\n");
    }

    html.append("</div>\n");
    html.append("</body></html>");

    return html.toString();
}

private static boolean isValidJakartaMailMultipart(Object multipart) {
    if (multipart == null) return false;

    try {
        // Check if the object implements jakarta.mail.Multipart interface
        Class<?> multipartInterface = Class.forName("jakarta.mail.Multipart");
        if (!multipartInterface.isInstance(multipart)) {
            return false;
        }

        // Additional check for MimeMultipart
        try {
            Class<?> mimeMultipartClass = Class.forName("jakarta.mail.internet.MimeMultipart");
            if (mimeMultipartClass.isInstance(multipart)) {
                log.debug("Found MimeMultipart instance for enhanced processing");
                return true;
            }
        } catch (ClassNotFoundException e) {
            log.debug("MimeMultipart not available, using base Multipart interface");
        }

        return true;
    } catch (ClassNotFoundException e) {
        log.debug("Jakarta Mail Multipart interface not available for validation");
        return false;
    }
}

private static String extractBasicHeader(String emlContent, String headerName) {
    try {
        String[] lines = emlContent.split("\r?\n");
        for (int i = 0; i < lines.length; i++) {
            String line = lines[i];
            if (line.toLowerCase().startsWith(headerName.toLowerCase())) {
                StringBuilder value = new StringBuilder(line.substring(headerName.length()).trim());
                // Handle multi-line headers
                for (int j = i + 1; j < lines.length; j++) {
                    if (lines[j].startsWith(" ") || lines[j].startsWith("\t")) {
                        value.append(" ").append(lines[j].trim());
                    } else {
                        break;
                    }
                }
                // Apply MIME header decoding
                return safeMimeDecode(value.toString());
            }
            if (line.trim().isEmpty()) break;
        }
    } catch (RuntimeException e) {
        log.warn("Error extracting header '{}': {}", headerName, e.getMessage());
    }
    return "";
}

private static void addAttachmentAnnotationsToDocument(
        PDDocument document, List<EmailAttachment> attachments) throws IOException {
    if (document.getNumberOfPages() == 0 || attachments == null || attachments.isEmpty()) {
        return;
    }

    // 1. Find the screen position of all attachment markers
    AttachmentMarkerPositionFinder finder = new AttachmentMarkerPositionFinder();
    finder.setSortByPosition(true); // Process pages in order
    finder.getText(document);
    List<MarkerPosition> markerPositions = finder.getPositions();

    // 2. Warn if the number of markers and attachments don't match
    if (markerPositions.size() != attachments.size()) {
        log.warn(
            "Found {} attachment markers, but there are {} attachments. Annotation count may be incorrect.",
            markerPositions.size(),
            attachments.size());
    }

    // 3. Create an invisible annotation over each found marker
    int annotationsToAdd = Math.min(markerPositions.size(), attachments.size());
    for (int i = 0; i < annotationsToAdd; i++) {
        MarkerPosition position = markerPositions.get(i);
        EmailAttachment attachment = attachments.get(i);

        if (attachment.getEmbeddedFilename() != null) {
            PDPage page = document.getPage(position.getPageIndex());
            addAttachmentAnnotationToPage(
                document, page, attachment, position.getX(), position.getY());
        }
    }
}

private JsonNode validateLicense(
        String licenseKey, String machineFingerprint, LicenseContext context) throws Exception {
    String requestBody = String.format(
            "{\"meta\":{\"key\":\"%s\",\"scope\":{\"fingerprint\":\"%s\"}}}",
            licenseKey, machineFingerprint);

    HttpRequest request = HttpRequest.newBuilder()
            .uri(URI.create(BASE_URL + "/" + ACCOUNT_ID + "/licenses/actions/validate-key"))
            .header("Content-Type", "application/vnd.api+json")
            .header("Accept", "application/vnd.api+json")
            // .header("Authorization", "License " + licenseKey)
            .POST(HttpRequest.BodyPublishers.ofString(requestBody))
            .build();

    HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
    log.debug("ValidateLicenseResponse body: {}", response.body());

    JsonNode jsonResponse = objectMapper.readTree(response.body());

    if (response.statusCode() == 200) {
        JsonNode metaNode = jsonResponse.path("meta");
        boolean isValid = metaNode.path("valid").asBoolean();

        String detail = metaNode.path("detail").asText();
        String code = metaNode.path("code").asText();

        log.info("License validity: {}", isValid);
        log.info("Validation detail: {}", detail);
        log.info("Validation code: {}", code);

        // Check if the license itself has floating attribute
        JsonNode licenseAttrs = jsonResponse.path("data").path("attributes");
        if (!licenseAttrs.isMissingNode()) {
            context.isFloatingLicense = licenseAttrs.path("floating").asBoolean(false);
            context.maxMachines = licenseAttrs.path("maxMachines").asInt(1);

            log.info("License floating (from license): {}, maxMachines: {}",
                    context.isFloatingLicense,
                    context.maxMachines);
        }

        // Also check the policy for floating license support if included
        JsonNode includedNode = jsonResponse.path("included");
        JsonNode policyNode = null;

        if (includedNode.isArray()) {
            for (JsonNode node : includedNode) {
                if ("policies".equals(node.path("type").asText())) {
                    policyNode = node;
                    break;
                }
            }
        }

        if (policyNode != null) {
            // Check if this is a floating license from policy
            boolean policyFloating = policyNode.path("attributes").path("floating").asBoolean(false);
            int policyMaxMachines = policyNode.path("attributes").path("maxMachines").asInt(1);

            // Policy takes precedence over license attributes
            if (policyFloating) {
                context.isFloatingLicense = true;
                context.maxMachines = policyMaxMachines;
            }

            log.info("License floating (from policy): {}, maxMachines: {}",
                    context.isFloatingLicense,
                    context.maxMachines);
        }

        // Extract user count, default to 1 if not specified
        int users = jsonResponse
                .path("data")
                .path("attributes")
                .path("metadata")
                .path("users")
                .asInt(1);
        applicationProperties.getPremium().setMaxUsers(users);

        // Extract isEnterprise flag
        context.isEnterpriseLicense = jsonResponse
                .path("data")
                .path("attributes")
                .path("metadata")
                .path("isEnterprise")
                .asBoolean(false);

        log.debug(applicationProperties.toString());
    } else {
        log.error("Error validating license. Status code: {}", response.statusCode());
    }

    return jsonResponse;
}

@PostMapping(consumes = "multipart/form-data", value = "/file/pdf")
@Operation(
        summary = "Convert a file to a PDF using LibreOffice",
        description =
                "This endpoint converts a given file to a PDF using LibreOffice API  Input:ANY"
                        + " Output:PDF Type:SISO")
public ResponseEntity<byte[]> processFileToPDF(@ModelAttribute GeneralFile generalFile)
        throws Exception {
    MultipartFile inputFile = generalFile.getFileInput();
    // unused but can start server instance if startup time is to long
    // LibreOfficeListener.getInstance().start();
    File file = null;

    try {
        file = convertToPdf(inputFile);

        PDDocument doc = pdfDocumentFactory.load(file);

        return WebResponseUtils.pdfDocToWebResponse(
