private void doPostprocessing() {
    errCode = ERROR_NOTHING;
    errObject = null;
    Thread thread = Thread.currentThread();

    notifyPostProcessing(1);

    if (DEBUG) {
        thread.setName("[" + TAG + "]  ps = " + psAlgorithm + "  filename = " + storage.getName());
    }

    Exception exception = null;

    try {
        psAlgorithm.run(this);
    } catch (Exception err) {
        Log.e(TAG, "Post-processing failed. " + psAlgorithm.toString(), err);

        // Check if the operation was interrupted
        if (err instanceof InterruptedIOException 
                || err instanceof ClosedByInterruptException 
                || thread.isInterrupted()) {
            notifyError(DownloadMission.ERROR_POSTPROCESSING_STOPPED, null);
            return;
        }

        // Set error code if not already set
        if (errCode == ERROR_NOTHING) {
            errCode = ERROR_POSTPROCESSING;
        }

        exception = err;
    } finally {
        notifyPostProcessing(errCode == ERROR_NOTHING ? 2 : 0);
    }

    // Handle errors if any occurred
    if (errCode != ERROR_NOTHING) {
        if (exception == null) {
            exception = errObject;
        }
        notifyError(ERROR_POSTPROCESSING, exception);
        return;
    }

    notifyFinished();
}

void establishConnection(int threadId, HttpURLConnection conn) throws IOException, HttpError {
    int statusCode = conn.getResponseCode();

    if (DEBUG) {
        Log.d(TAG, threadId + ":[request]  Range=" + conn.getRequestProperty("Range"));
        Log.d(TAG, threadId + ":[response] Code=" + statusCode);
        Log.d(TAG, threadId + ":[response] Content-Length=" + conn.getContentLength());
        Log.d(TAG, threadId + ":[response] Content-Range=" + conn.getHeaderField("Content-Range"));
    }

    switch (statusCode) {
        case 204:
        case 205:
        case 207:
            throw new HttpError(statusCode);
        case 416:
            return; // let the download thread handle this error
        default:
            if (statusCode < 200 || statusCode > 299) {
                throw new HttpError(statusCode);
            }
    }
}

private Elst parseEdts(final Box ref) throws IOException {
    final Box b = untilBox(ref, ATOM_ELST);
    if (b == null) {
        return null;
    }

    final Elst obj = new Elst();

    // Check version
    final boolean v1 = stream.read() == 1;
    stream.skipBytes(3); // flags

    // Read entry count
    final int entryCount = stream.readInt();
    if (entryCount < 1) {
        obj.bMediaRate = 0x00010000; // default media rate (1.0)
        return obj;
    }

    // Process based on version
    if (v1) {
        stream.skipBytes(DataReader.LONG_SIZE); // segment duration
        obj.mediaTime = stream.readLong();
        // ignore all remain entries
        stream.skipBytes((entryCount - 1) * (DataReader.LONG_SIZE * 2));
    } else {
        stream.skipBytes(DataReader.INTEGER_SIZE); // segment duration
        obj.mediaTime = stream.readInt();
    }

    obj.bMediaRate = stream.readInt();

    return obj;
}

@Override
public void run() {
    boolean done;
    long start = mMission.fallbackResumeOffset;

    if (DEBUG && !mMission.unknownLength && start > 0) {
        Log.i(TAG, "Resuming a single-thread download at " + start);
    }

    try {
        // Determine range start position
        long rangeStart = (mMission.unknownLength || start < 1) ? -1 : start;

        // Establish connection
        int mId = 1;
        mConn = mMission.openConnection(false, rangeStart, -1);

        // Workaround: bypass android connection pool on first attempt
        if (mRetryCount == 0 && rangeStart == -1) {
            mConn.setRequestProperty("Range", "bytes=0-");
        }

        mMission.establishConnection(mId, mConn);

        // Check if the download can be resumed
        if (mConn.getResponseCode() == 416 && start > 0) {
            mMission.notifyProgress(-start);
            start = 0;
            mRetryCount--;
            throw new DownloadMission.HttpError(416);
        }

        // Secondary check for the file length
        if (!mMission.unknownLength) {
            mMission.unknownLength = Utility.getContentLength(mConn) == -1;
        }

        // Reset download progress if needed
        if (mMission.unknownLength || mConn.getResponseCode() == 200) {
            // Restart amount of bytes downloaded
            mMission.done = mMission.offsets[mMission.current] - mMission.offsets[0];
        }

        // Prepare file stream and position
        mF = mMission.storage.getStream();
        mF.seek(mMission.offsets[mMission.current] + start);

        mIs = mConn.getInputStream();

        // Download data
        byte[] buf = new byte[DownloadMission.BUFFER_SIZE];
        int len = 0;

        while (mMission.running && (len = mIs.read(buf, 0, buf.length)) != -1) {
            mF.write(buf, 0, len);
            start += len;
            mMission.notifyProgress(len);
        }

        dispose();

        // If thread goes interrupted check if the last part is written
        // This avoids re-downloading the whole file
        done = len == -1;
    } catch (Exception e) {
        dispose();

        mMission.fallbackResumeOffset = start;

        if (!mMission.running || e instanceof ClosedByInterruptException) {
            return;
        }

        if (e instanceof HttpError && ((HttpError) e).statusCode == ERROR_HTTP_FORBIDDEN) {
            // For youtube streams. The url has expired, recover
            dispose();
            mMission.doRecover(ERROR_HTTP_FORBIDDEN);
            return;
        }

        if (mRetryCount++ >= mMission.maxRetry) {
            mMission.notifyError(e);
            return;
        }

        if (DEBUG) {
            Log.e(TAG, "got exception, retrying...", e);
        }

        // Try again
        run();
        return;
    }

    // Finalize download
    if (done) {
        mMission.notifyFinished();
    } else {
        mMission.fallbackResumeOffset = start;
    }
}

private void flushAuxiliar(long amount) throws IOException {
    // Return early if no data to flush
    if (aux.length < 1) {
        return;
    }

    out.flush();
    aux.flush();

    boolean underflow = aux.offset < aux.length || out.offset < out.length;
    byte[] buffer = new byte[COPY_BUFFER_SIZE];

    // Position file pointers
    aux.target.seek(0);
    out.target.seek(out.length);

    // Copy data from auxiliary to output
    long length = amount;
    while (length > 0) {
        int read = (int) Math.min(length, Integer.MAX_VALUE);
        read = aux.target.read(buffer, 0, Math.min(read, buffer.length));

        if (read < 1) {
            amount -= length;
            break;
        }

        out.writeProof(buffer, read);
        length -= read;
    }

    // Update offset positions based on underflow condition
    if (underflow) {
        if (out.offset >= out.length) {
            // Calculate the aux underflow pointer
            if (aux.offset < amount) {
                out.offset += aux.offset;
                aux.offset = 0;
                out.target.seek(out.offset);
            } else {
                aux.offset -= amount;
                out.offset = out.length + amount;
            }
        } else {
            aux.offset = 0;
        }
    } else {
        out.offset += amount;
        aux.offset -= amount;
    }

    out.length += amount;

    // Update max length if needed
    if (out.length > maxLengthKnown) {
        maxLengthKnown = out.length;
    }

    // Handle remaining data in auxiliary file
    if (amount < aux.length) {
        // Move the excess data to the beginning of the file
        long readOffset = amount;
        long writeOffset = 0;

        aux.length -= amount;
        length = aux.length;
        while (length > 0) {
            int read = (int) Math.min(length, Integer.MAX_VALUE);
            read = aux.target.read(buffer, 0, Math.min(read, buffer.length));

            aux.target.seek(writeOffset);
            aux.writeProof(buffer, read);

            writeOffset += read;
            readOffset += read;
            length -= read;

            aux.target.seek(readOffset);
        }

        aux.target.setLength(aux.length);
        return;
    }

    // Truncate auxiliary file if it's too large
    if (aux.length > THRESHOLD_AUX_LENGTH) {
        aux.target.setLength(THRESHOLD_AUX_LENGTH);
    }

    aux.reset();
}

private void showError(DownloadMission mission, UserAction action, @StringRes int reason) {
    StringBuilder request = new StringBuilder(256);
    request.append(mission.source);

    request.append(" [");
    if (mission.recoveryInfo != null) {
        for (MissionRecoveryInfo recovery : mission.recoveryInfo) {
            request.append(' ')
                    .append(recovery.toString())
                    .append(' ');
        }
    }
    request.append("]");

    String service;
    try {
        service = NewPipe.getServiceByUrl(mission.source).getServiceInfo().getName();
    } catch (Exception e) {
        service = ErrorInfo.SERVICE_NONE;
    }

    ErrorUtil.createNotification(
            mContext,
            new ErrorInfo(
                    ErrorInfo.Companion.throwableToStringList(mission.errObject),
                    action,
                    service,
                    request.toString(),
                    reason));
}

void startMission(DownloadMission mission) {
    synchronized (this) {
        mission.timestamp = System.currentTimeMillis();
        mission.mHandler = mHandler;
        mission.maxRetry = mPrefMaxRetry;

        // Create metadata file
        while (true) {
            mission.metadata = new File(mPendingMissionsDir, String.valueOf(mission.timestamp));
            if (!mission.metadata.isFile() && !mission.metadata.exists()) {
                try {
                    if (!mission.metadata.createNewFile()) {
                        throw new RuntimeException("Cant create download metadata file");
                    }
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
                break;
            }
            mission.timestamp = System.currentTimeMillis();
        }

        mSelfMissionsControl = true;
        mMissionsPending.add(mission);

        // Before continue, save the metadata in case the internet connection is not available
        Utility.writeToFile(mission.metadata, mission);

        if (mission.storage == null) {
            // Nothing to do here
            mission.errCode = DownloadMission.ERROR_FILE_CREATION;
            if (mission.errObject != null) {
                mission.errObject = new IOException("DownloadMission.storage == NULL");
            }
            return;
        }

        boolean start = !mPrefQueueLimit || getRunningMissionsCount() < 1;

        if (canDownloadInCurrentNetwork() && start) {
            mission.start();
        }
    }
}

@Override
int process(SharpStream out, SharpStream... sources) throws IOException {
    // check if the subtitle is already in srt and copy, this should never happen
    String format = getArgumentAt(0, null);
    boolean ignoreEmptyFrames = getArgumentAt(1, "true").equals("true");

    if (format == null || format.equals("ttml")) {
        SrtFromTtmlWriter writer = new SrtFromTtmlWriter(out, ignoreEmptyFrames);

        try {
            writer.build(sources[0]);
        } catch (Exception err) {
            Log.e(TAG, "subtitle parse failed", err);
            return err instanceof IOException ? 1 : 8;
        }

        return OK_RESULT;
    } else if (format.equals("srt")) {
        byte[] buffer = new byte[8 * 1024];
        int read;

        while ((read = sources[0].read(buffer)) > 0) {
            out.write(buffer, 0, read);
        }

        return OK_RESULT;
    }

    throw new UnsupportedOperationException(
            "Can't convert this subtitle, unimplemented format: " + format);
}

private void joinForThreads(int millis) {
    final Thread currentThread = Thread.currentThread();

    // Handle initialization thread
    if (init != null && init != currentThread && init.isAlive()) {
        init.interrupt();

        if (millis > 0) {
            try {
                init.join(millis);
            } catch (InterruptedException e) {
                Log.w(TAG, "Initializer thread is still running", e);
                return;
            }
        }
    }

    // if a thread is still alive, possible reasons:
    //      slow device
    //      the user is spamming start/pause buttons
    //      start() method called quickly after pause()

    // Interrupt all active threads except current thread
    for (Thread thread : threads) {
        if (!thread.isAlive() || thread == Thread.currentThread()) {
            continue;
        }
        thread.interrupt();
    }

    // Join all active threads
    try {
        for (Thread thread : threads) {
            if (!thread.isAlive()) {
                continue;
            }

            if (DEBUG) {
                Log.w(TAG, "thread alive: " + thread.getName());
            }

            if (millis > 0) {
                thread.join(millis);
            }
        }
    } catch (InterruptedException e) {
        throw new RuntimeException("A download thread is still running", e);
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    Observer<? super R> downstream = this.downstream;
    AtomicThrowable errors = this.errors;
    AtomicReference<SwitchMapMaybeObserver<R>> inner = this.inner;

    for (;;) {
        for (;;) {
            // Check if operation was cancelled
            if (cancelled) {
                return;
            }

            // Handle errors based on error delay configuration
            if (errors.get() != null) {
                if (!delayErrors) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            boolean d = done;
            SwitchMapMaybeObserver<R> current = inner.get();
            boolean empty = current == null;

            // If done and no current observer, terminate with any errors
            if (d && empty) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            // Break inner loop if no item to process
            if (empty || current.item == null) {
                break;
            }

            // Clear the current observer and emit its item
            inner.compareAndSet(current, null);
            downstream.onNext(current.item);
        }

        // Manage the missed counter for work coordination
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void onNext(T t) {
    if (done) {
        return;
    }

    boolean b;

    try {
        b = predicate.test(t);
    } catch (Throwable e) {
        Exceptions.throwIfFatal(e);
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        onError(e);
        return;
    }

    if (!b) {
        done = true;
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        downstream.onSuccess(false);
    }
}

void drainLoop() {
    Observer<? super T> actual = this.downstream;
    int missed = 1;

    for (;;) {
        for (;;) {
            if (disposed) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(actual);
                return;
            }

            int os = otherState;
            if (os == OTHER_STATE_HAS_VALUE) {
                T v = singleItem;
                singleItem = null;
                otherState = OTHER_STATE_CONSUMED_OR_EMPTY;
                os = OTHER_STATE_CONSUMED_OR_EMPTY;
                actual.onNext(v);
            }

            boolean d = mainDone;
            SimplePlainQueue<T> q = queue;
            T v = q != null ? q.poll() : null;
            boolean empty = v == null;

            if (d && empty && os == OTHER_STATE_CONSUMED_OR_EMPTY) {
                queue = null;
                actual.onComplete();
                return;
            }

            if (empty) {
                break;
            }

            actual.onNext(v);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final SimplePlainQueue<Object> queue = this.queue;
    final Subscriber<? super Flowable<T>> downstream = this.downstream;
    UnicastProcessor<T> window = this.window;

    for (;;) {
        // Handle upstream cancellation case
        if (upstreamCancelled) {
            queue.clear();
            window = null;
            this.window = null;
        } else {
            // Check for terminal conditions and process queue items
            boolean isDone = done;
            Object o = queue.poll();
            boolean isEmpty = o == null;

            // Handle terminal state with empty queue
            if (isDone && isEmpty) {
                Throwable ex = error;
                if (ex != null) {
                    // Propagate error to window and downstream
                    if (window != null) {
                        window.onError(ex);
                    }
                    downstream.onError(ex);
                } else {
                    // Complete window and downstream normally
                    if (window != null) {
                        window.onComplete();
                    }
                    downstream.onComplete();
                }
                cleanupResources();
                upstreamCancelled = true;
                continue;
            } else if (!isEmpty) {
                // Process non-empty queue
                if (o instanceof WindowBoundaryRunnable) {
                    // Handle window boundary event
                    WindowBoundaryRunnable boundary = (WindowBoundaryRunnable) o;
                    if (boundary.index == emitted || !restartTimerOnMaxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    }
                } else if (window != null) {
                    // Process regular item
                    @SuppressWarnings("unchecked")
                    T item = (T)o;
                    window.onNext(item);

                    // Check if we've reached max size for the window
                    long count = this.count + 1;
                    if (count == maxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    } else {
                        this.count = count;
                    }
                }

                continue;
            }
        }

        // Check if we should continue draining
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    EqualObserver<T>[] as = observers;

    final EqualObserver<T> observer1 = as[0];
    final SpscLinkedArrayQueue<T> q1 = observer1.queue;
    final EqualObserver<T> observer2 = as[1];
    final SpscLinkedArrayQueue<T> q2 = observer2.queue;

    for (;;) {
        for (;;) {
            // Check if operation was cancelled
            if (cancelled) {
                q1.clear();
                q2.clear();
                return;
            }

            // Check first observer's status
            boolean d1 = observer1.done;
            if (d1) {
                Throwable e = observer1.error;
                if (e != null) {
                    cancel(q1, q2);
                    downstream.onError(e);
                    return;
                }
            }

            // Check second observer's status
            boolean d2 = observer2.done;
            if (d2) {
                Throwable e = observer2.error;
                if (e != null) {
                    cancel(q1, q2);
                    downstream.onError(e);
                    return;
                }
            }

            // Get values from queues if needed
            if (v1 == null) {
                v1 = q1.poll();
            }
            boolean e1 = v1 == null;

            if (v2 == null) {
                v2 = q2.poll();
            }
            boolean e2 = v2 == null;

            // Both streams are done and empty - they're equal
            if (d1 && d2 && e1 && e2) {
                downstream.onSuccess(true);
                return;
            }

            // Both streams are done but one has more items than the other
            if ((d1 && d2) && (e1 != e2)) {
                cancel(q1, q2);
                downstream.onSuccess(false);
                return;
            }

            // Compare values if both queues have items
            if (!e1 && !e2) {
                boolean c;
                try {
                    c = comparer.test(v1, v2);
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    cancel(q1, q2);
                    downstream.onError(ex);
                    return;
                }

                // Items don't match
                if (!c) {
                    cancel(q1, q2);
                    downstream.onSuccess(false);
                    return;
                }

                // Items match, clear them for next iteration
                v1 = null;
                v2 = null;
            }

            // If either queue is empty, break inner loop
            if (e1 || e2) {
                break;
            }
        }

        // Check if we should continue processing
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drainLoop() {
    int missed = 1;

    JoinInnerSubscriber<T>[] s = this.subscribers;
    int n = s.length;
    Subscriber<? super T> a = this.downstream;

    for (;;) {
        long r = requested.get();
        long e = 0;

        middle:
        while (e != r) {
            if (cancelled) {
                cleanup();
                return;
            }

            Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }

            boolean d = done.get() == 0;
            boolean empty = true;

            for (int i = 0; i < s.length; i++) {
                JoinInnerSubscriber<T> inner = s[i];
                SimplePlainQueue<T> q = inner.queue;
                if (q != null) {
                    T v = q.poll();

                    if (v != null) {
                        empty = false;
                        a.onNext(v);
                        inner.requestOne();
                        if (++e == r) {
                            break middle;
                        }
                    }
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }

            if (empty) {
                break;
            }
        }

        if (e == r) {
            // Check for cancellation
            if (cancelled) {
                cleanup();
                return;
            }

            // Check for errors
            Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }

            boolean d = done.get() == 0;
            boolean empty = true;

            // Check if all queues are empty
            for (int i = 0; i < n; i++) {
                JoinInnerSubscriber<T> inner = s[i];
                SimpleQueue<T> q = inner.queue;
                if (q != null && !q.isEmpty()) {
                    empty = false;
                    break;
                }
            }

            // Complete if done and all queues are empty
            if (d && empty) {
                a.onComplete();
                return;
            }
        }

        // Update requested count if items were emitted
        if (e != 0) {
            BackpressureHelper.produced(requested, e);
        }

        // Check if we should continue the drain loop
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    InnerQueuedSubscriber<R> inner = current;
    Subscriber<? super R> a = downstream;
    ErrorMode em = errorMode;

    for (;;) {
        long r = requested.get();
        long e = 0L;

        if (inner == null) {
            // Check for errors if not in END mode
            if (em != ErrorMode.END) {
                Throwable ex = errors.get();
                if (ex != null) {
                    cancelAll();
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            boolean outerDone = done;
            inner = subscribers.poll();

            if (outerDone && inner == null) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (inner != null) {
                current = inner;
            }
        }

        boolean continueNextSource = false;

        if (inner != null) {
            SimpleQueue<R> q = inner.queue();
            if (q != null) {
                // Process the queue while respecting request amount
                while (e != r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }

                    // Handle errors in IMMEDIATE mode
                    if (em == ErrorMode.IMMEDIATE) {
                        Throwable ex = errors.get();
                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();
                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    boolean d = inner.isDone();
                    R v;

                    try {
                        v = q.poll();
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        current = null;
                        inner.cancel();
                        cancelAll();
                        a.onError(ex);
                        return;
                    }

                    boolean empty = v == null;

                    // If inner source is done and queue is empty, move to next source
                    if (d && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                        break;
                    }

                    if (empty) {
                        break;
                    }

                    // Emit the item
                    a.onNext(v);
                    e++;
                    inner.request(1L);
                }

                // Check completion condition after delivering requested amount
                if (e == r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }

                    // Handle errors in IMMEDIATE mode
                    if (em == ErrorMode.IMMEDIATE) {
                        Throwable ex = errors.get();
                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();
                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    boolean d = inner.isDone();
                    boolean empty = q.isEmpty();

                    // If inner source is done and queue is empty, move to next source
                    if (d && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                    }
                }
            }
        }

        // Adjust the request counter
        if (e != 0L && r != Long.MAX_VALUE) {
            requested.addAndGet(-e);
        }

        if (continueNextSource) {
            continue;
        }

        // Exit loop if no more work
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
void remove(CacheDisposable<T> consumer) {
    for (;;) {
        CacheDisposable<T>[] current = observers.get();
        int n = current.length;
        if (n == 0) {
            return;
        }

        // Find the index of the consumer
        int j = -1;
        for (int i = 0; i < n; i++) {
            if (current[i] == consumer) {
                j = i;
                break;
            }
        }

        // If consumer not found, return
        if (j < 0) {
            return;
        }

        // Create new array without the consumer
        CacheDisposable<T>[] next;
        if (n == 1) {
            next = EMPTY;
        } else {
            next = new CacheDisposable[n - 1];
            System.arraycopy(current, 0, next, 0, j);
            System.arraycopy(current, j + 1, next, j, n - j - 1);
        }

        // Try to update the observers array
        if (observers.compareAndSet(current, next)) {
            return;
        }
    }
}

void drainFused() {
    int missed = 1;

    for (;;) {
        if (disposed) {
            return;
        }

        boolean d = done;
        Throwable ex = error;

        // Handle error case with higher priority if not delaying errors
        if (!delayError && d && ex != null) {
            disposed = true;
            downstream.onError(error);
            worker.dispose();
            return;
        }

        // Emit the item to downstream
        downstream.onNext(null);

        // Check for completion after emission
        if (d) {
            disposed = true;
            ex = error;
            if (ex != null) {
                downstream.onError(ex);
            } else {
                downstream.onComplete();
            }
            worker.dispose();
            return;
        }

        // Manage the request accounting
        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
void remove(PublishSubscription<T> ps) {
    for (;;) {
        PublishSubscription<T>[] a = subscribers.get();
        if (a == TERMINATED || a == EMPTY) {
            return;
        }

        int n = a.length;
        int j = -1;

        // Find the subscription in the array
        for (int i = 0; i < n; i++) {
            if (a[i] == ps) {
                j = i;
                break;
            }
        }

        // If subscription not found, return
        if (j < 0) {
            return;
        }

        PublishSubscription<T>[] b;

        // Create new array without the subscription
        if (n == 1) {
            b = EMPTY;
        } else {
            b = new PublishSubscription[n - 1];
            System.arraycopy(a, 0, b, 0, j);
            System.arraycopy(a, j + 1, b, j, n - j - 1);
        }

        // Try to update the subscribers atomically
        if (subscribers.compareAndSet(a, b)) {
            return;
        }
    }
}

private static String convertEmlToHtmlBasic(byte[] emlBytes, EmlToPdfRequest request) {
  if (emlBytes == null || emlBytes.length == 0) {
    throw new IllegalArgumentException("EML file is empty or null");
  }

  String emlContent = new String(emlBytes, StandardCharsets.UTF_8);

  // Basic email parsing
  String subject = extractBasicHeader(emlContent, "Subject:");
  String from = extractBasicHeader(emlContent, "From:");
  String to = extractBasicHeader(emlContent, "To:");
  String cc = extractBasicHeader(emlContent, "Cc:");
  String bcc = extractBasicHeader(emlContent, "Bcc:");
  String date = extractBasicHeader(emlContent, "Date:");

  // Try to extract HTML content
  String htmlBody = extractHtmlBody(emlContent);
  if (htmlBody == null) {
    String textBody = extractTextBody(emlContent);
    htmlBody = convertTextToHtml(textBody != null ? textBody : "Email content could not be parsed");
  }

  // Generate HTML with custom styling based on request
  StringBuilder html = new StringBuilder();
  html.append("<!DOCTYPE html>\n");
  html.append("<html><head><meta charset=\"UTF-8\">\n");
  html.append("<title>").append(escapeHtml(subject)).append("</title>\n");
  html.append("<style>\n");
  appendEnhancedStyles(html);
  html.append("</style>\n");
  html.append("</head><body>\n");

  html.append("<div class=\"email-container\">\n");
  html.append("<div class=\"email-header\">\n");
  html.append("<h1>").append(escapeHtml(subject)).append("</h1>\n");
  html.append("<div class=\"email-meta\">\n");
  html.append("<div><strong>From:</strong> ").append(escapeHtml(from)).append("</div>\n");
  html.append("<div><strong>To:</strong> ").append(escapeHtml(to)).append("</div>\n");

  // Include CC and BCC if present and requested
  if (request != null && request.isIncludeAllRecipients()) {
    if (!cc.trim().isEmpty()) {
      html.append("<div><strong>CC:</strong> ").append(escapeHtml(cc)).append("</div>\n");
    }
    if (!bcc.trim().isEmpty()) {
      html.append("<div><strong>BCC:</strong> ").append(escapeHtml(bcc)).append("</div>\n");
    }
  }

  if (!date.trim().isEmpty()) {
    html.append("<div><strong>Date:</strong> ").append(escapeHtml(date)).append("</div>\n");
  }
  html.append("</div></div>\n");

  html.append("<div class=\"email-body\">\n");
  html.append(processEmailHtmlBody(htmlBody));
  html.append("</div>\n");

  // Add attachment information - always check for and display attachments
  String attachmentInfo = extractAttachmentInfo(emlContent);
  if (!attachmentInfo.isEmpty()) {
    html.append("<div class=\"attachment-section\">\n");
    html.append("<h3>Attachments</h3>\n");
    html.append(attachmentInfo);

    // Add a status message about attachment inclusion
    if (request != null && request.isIncludeAttachments()) {
      html.append("<div class=\"attachment-inclusion-note\">\n");
      html.append("<p><strong>Note:</strong> Attachments are saved as external files and linked in this PDF. Click the links to open files externally.</p>\n");
      html.append("</div>\n");
    } else {
      html.append("<div class=\"attachment-info-note\">\n");
      html.append("<p><em>Attachment information displayed - files not included in PDF. Enable 'Include attachments' to embed files.</em></p>\n");
      html.append("</div>\n");
    }

    html.append("</div>\n");
  }

  // Show advanced features status if requested
  assert request != null;
  if (request.getFileInput().isEmpty()) {
    html.append("<div class=\"advanced-features-notice\">\n");
    html.append("<p><em>Note: Some advanced features require Jakarta Mail dependencies.</em></p>\n");
    html.append("</div>\n");
  }

  html.append("</div>\n");
  html.append("</body></html>");

  return html.toString();
}

private static boolean isValidJakartaMailMultipart(Object multipart) {
  if (multipart == null) return false;

  try {
    // Check if the object implements jakarta.mail.Multipart interface
    Class<?> multipartInterface = Class.forName("jakarta.mail.Multipart");
    if (!multipartInterface.isInstance(multipart)) {
      return false;
    }

    // Additional check for MimeMultipart
    try {
      Class<?> mimeMultipartClass = Class.forName("jakarta.mail.internet.MimeMultipart");
      if (mimeMultipartClass.isInstance(multipart)) {
        log.debug("Found MimeMultipart instance for enhanced processing");
        return true;
      }
    } catch (ClassNotFoundException e) {
      log.debug("MimeMultipart not available, using base Multipart interface");
    }

    return true;
  } catch (ClassNotFoundException e) {
    log.debug("Jakarta Mail Multipart interface not available for validation");
    return false;
  }
}

private static String extractBasicHeader(String emlContent, String headerName) {
  try {
    String[] lines = emlContent.split("\r?\n");
    for (int i = 0; i < lines.length; i++) {
      String line = lines[i];
      if (line.toLowerCase().startsWith(headerName.toLowerCase())) {
        StringBuilder value = new StringBuilder(line.substring(headerName.length()).trim());
        // Handle multi-line headers
        for (int j = i + 1; j < lines.length; j++) {
          if (lines[j].startsWith(" ") || lines[j].startsWith("\t")) {
            value.append(" ").append(lines[j].trim());
          } else {
            break;
          }
        }
        // Apply MIME header decoding
        return safeMimeDecode(value.toString());
      }
      if (line.trim().isEmpty()) break;
    }
  } catch (RuntimeException e) {
    log.warn("Error extracting header '{}': {}", headerName, e.getMessage());
  }
  return "";
}

private static void addAttachmentAnnotationsToDocument(
        PDDocument document, List<EmailAttachment> attachments) throws IOException {
  if (document.getNumberOfPages() == 0 || attachments == null || attachments.isEmpty()) {
    return;
  }

  // 1. Find the screen position of all attachment markers
  AttachmentMarkerPositionFinder finder = new AttachmentMarkerPositionFinder();
  finder.setSortByPosition(true); // Process pages in order
  finder.getText(document);
  List<MarkerPosition> markerPositions = finder.getPositions();

  // 2. Warn if the number of markers and attachments don't match
  if (markerPositions.size() != attachments.size()) {
    log.warn(
        "Found {} attachment markers, but there are {} attachments. Annotation count may be incorrect.",
        markerPositions.size(),
        attachments.size());
  }

  // 3. Create an invisible annotation over each found marker
  int annotationsToAdd = Math.min(markerPositions.size(), attachments.size());
  for (int i = 0; i < annotationsToAdd; i++) {
    MarkerPosition position = markerPositions.get(i);
    EmailAttachment attachment = attachments.get(i);

    if (attachment.getEmbeddedFilename() != null) {
      PDPage page = document.getPage(position.getPageIndex());
      addAttachmentAnnotationToPage(
          document, page, attachment, position.getX(), position.getY());
    }
  }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    Observer<? super R> downstream = this.downstream;
    AtomicThrowable errors = this.errors;
    AtomicReference<SwitchMapMaybeObserver<R>> inner = this.inner;

    for (;;) {
        for (;;) {
            if (cancelled) {
                return;
            }

            if (errors.get() != null) {
                if (!delayErrors) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            boolean d = done;
            SwitchMapMaybeObserver<R> current = inner.get();
            boolean empty = current == null;

            if (d && empty) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (empty || current.item == null) {
                break;
            }

            inner.compareAndSet(current, null);

            downstream.onNext(current.item);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void onNext(T t) {
    if (done) {
        return;
    }
    boolean b;
    try {
        b = predicate.test(t);
    } catch (Throwable e) {
        Exceptions.throwIfFatal(e);
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        onError(e);
        return;
    }
    if (!b) {
        done = true;
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        downstream.onSuccess(false);
    }
}

void drainLoop() {
    Observer<? super T> actual = this.downstream;
    int missed = 1;
    for (;;) {

        for (;;) {
            if (disposed) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(actual);
                return;
            }

            int os = otherState;
            if (os == OTHER_STATE_HAS_VALUE) {
                T v = singleItem;
                singleItem = null;
                otherState = OTHER_STATE_CONSUMED_OR_EMPTY;
                os = OTHER_STATE_CONSUMED_OR_EMPTY;
                actual.onNext(v);
            }

            boolean d = mainDone;
            SimplePlainQueue<T> q = queue;
            T v = q != null ? q.poll() : null;
            boolean empty = v == null;

            if (d && empty && os == OTHER_STATE_CONSUMED_OR_EMPTY) {
                queue = null;
                actual.onComplete();
                return;
            }

            if (empty) {
                break;
            }

            actual.onNext(v);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final SimplePlainQueue<Object> queue = this.queue;
    final Subscriber<? super Flowable<T>> downstream = this.downstream;
    UnicastProcessor<T> window = this.window;

    for (;;) {
        if (upstreamCancelled) {
            queue.clear();
            window = null;
            this.window = null;
        } else {
            boolean isDone = done;
            Object o = queue.poll();
            boolean isEmpty = o == null;

            if (isDone && isEmpty) {
                Throwable ex = error;
                if (ex != null) {
                    if (window != null) {
                        window.onError(ex);
                    }
                    downstream.onError(ex);
                } else {
                    if (window != null) {
                        window.onComplete();
                    }
                    downstream.onComplete();
                }
                cleanupResources();
                upstreamCancelled = true;
                continue;
            } else if (!isEmpty) {
                if (o instanceof WindowBoundaryRunnable) {
                    WindowBoundaryRunnable boundary = (WindowBoundaryRunnable) o;
                    if (boundary.index == emitted || !restartTimerOnMaxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    }
                } else if (window != null) {
                    @SuppressWarnings("unchecked")
                    T item = (T)o;
                    window.onNext(item);

                    long count = this.count + 1;
                    if (count == maxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    } else {
                        this.count = count;
                    }
                }
                continue;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    EqualObserver<T>[] as = observers;

    final EqualObserver<T> observer1 = as[0];
    final SpscLinkedArrayQueue<T> q1 = observer1.queue;
    final EqualObserver<T> observer2 = as[1];
    final SpscLinkedArrayQueue<T> q2 = observer2.queue;

    for (;;) {
        for (;;) {
            if (cancelled) {
                q1.clear();
                q2.clear();
                return;
            }

            boolean d1 = observer1.done;

            if (d1) {
                Throwable e = observer1.error;
                if (e != null) {
                    cancel(q1, q2);

                    downstream.onError(e);
                    return;
                }
            }

            boolean d2 = observer2.done;
            if (d2) {
                Throwable e = observer2.error;
                if (e != null) {
                    cancel(q1, q2);

                    downstream.onError(e);
                    return;
                }
            }

            if (v1 == null) {
                v1 = q1.poll();
            }
            boolean e1 = v1 == null;

            if (v2 == null) {
                v2 = q2.poll();
            }
            boolean e2 = v2 == null;

            if (d1 && d2 && e1 && e2) {
                downstream.onSuccess(true);
                return;
            }
            if ((d1 && d2) && (e1 != e2)) {
                cancel(q1, q2);

                downstream.onSuccess(false);
                return;
            }

            if (!e1 && !e2) {
                boolean c;

                try {
                    c = comparer.test(v1, v2);
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    cancel(q1, q2);

                    downstream.onError(ex);
                    return;
                }

                if (!c) {
                    cancel(q1, q2);

                    downstream.onSuccess(false);
                    return;
                }

                v1 = null;
                v2 = null;
            }

            if (e1 || e2) {
                break;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drainLoop() {
    int missed = 1;

    JoinInnerSubscriber<T>[] s = this.subscribers;
    int n = s.length;
    Subscriber<? super T> a = this.downstream;

    for (;;) {
        long r = requested.get();
        long e = 0;

        middle:
        while (e != r) {
            if (cancelled) {
                cleanup();
                return;
            }

            Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }

            boolean d = done.get() == 0;
            boolean empty = true;

            for (int i = 0; i < s.length; i++) {
                JoinInnerSubscriber<T> inner = s[i];
                SimplePlainQueue<T> q = inner.queue;
                if (q != null) {
                    T v = q.poll();

                    if (v != null) {
                        empty = false;
                        a.onNext(v);
                        inner.requestOne();
                        if (++e == r) {
                            break middle;
                        }
                    }
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }

            if (empty) {
                break;
            }
        }

        if (e == r) {
            if (cancelled) {
                cleanup();
                return;
            }

            Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }

            boolean d = done.get() == 0;
            boolean empty = true;

            for (int i = 0; i < n; i++) {
                JoinInnerSubscriber<T> inner = s[i];
                SimpleQueue<T> q = inner.queue;
                if (q != null && !q.isEmpty()) {
                    empty = false;
                    break;
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }
        }

        if (e != 0) {
            BackpressureHelper.produced(requested, e);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    InnerQueuedSubscriber<R> inner = current;
    Subscriber<? super R> a = downstream;
    ErrorMode em = errorMode;

    for (;;) {
        long r = requested.get();
        long e = 0L;

        if (inner == null) {
            if (em != ErrorMode.END) {
                Throwable ex = errors.get();
                if (ex != null) {
                    cancelAll();
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            boolean outerDone = done;
            inner = subscribers.poll();

            if (outerDone && inner == null) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (inner != null) {
                current = inner;
            }
        }

        boolean continueNextSource = false;

        if (inner != null) {
            SimpleQueue<R> q = inner.queue();
            if (q != null) {
                while (e != r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }

                    if (em == ErrorMode.IMMEDIATE) {
                        Throwable ex = errors.get();
                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();
                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    boolean d = inner.isDone();
                    R v;

                    try {
                        v = q.poll();
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        current = null;
                        inner.cancel();
                        cancelAll();
                        a.onError(ex);
                        return;
                    }

                    boolean empty = v == null;

                    if (d && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                        break;
                    }

                    if (empty) {
                        break;
                    }

                    a.onNext(v);
                    e++;
                    inner.request(1L);
                }

                if (e == r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }

                    if (em == ErrorMode.IMMEDIATE) {
                        Throwable ex = errors.get();
                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();
                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    boolean d = inner.isDone();
                    boolean empty = q.isEmpty();

                    if (d && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                    }
                }
            }
        }

        if (e != 0L && r != Long.MAX_VALUE) {
            requested.addAndGet(-e);
        }

        if (continueNextSource) {
            continue;
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
void remove(CacheDisposable<T> consumer) {
    for (;;) {
        CacheDisposable<T>[] current = observers.get();
        int n = current.length;
        if (n == 0) {
            return;
        }

        int j = -1;
        for (int i = 0; i < n; i++) {
            if (current[i] == consumer) {
                j = i;
                break;
            }
        }

        if (j < 0) {
            return;
        }

        CacheDisposable<T>[] next;
        if (n == 1) {
            next = EMPTY;
        } else {
            next = new CacheDisposable[n - 1];
            System.arraycopy(current, 0, next, 0, j);
            System.arraycopy(current, j + 1, next, j, n - j - 1);
        }

        if (observers.compareAndSet(current, next)) {
            return;
        }
    }
}

void drainFused() {
    int missed = 1;

    for (;;) {
        if (disposed) {
            return;
        }

        boolean d = done;
        Throwable ex = error;

        if (!delayError && d && ex != null) {
            disposed = true;
            downstream.onError(error);
            worker.dispose();
            return;
        }

        downstream.onNext(null);

        if (d) {
            disposed = true;
            ex = error;
            if (ex != null) {
                downstream.onError(ex);
            } else {
                downstream.onComplete();
            }
            worker.dispose();
            return;
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
void remove(PublishSubscription<T> ps) {
    for (;;) {
        PublishSubscription<T>[] a = subscribers.get();
        if (a == TERMINATED || a == EMPTY) {
            return;
        }

        int n = a.length;
        int j = -1;
        for (int i = 0; i < n; i++) {
            if (a[i] == ps) {
                j = i;
                break;
            }
        }

        if (j < 0) {
            return;
        }

        PublishSubscription<T>[] b;

        if (n == 1) {
            b = EMPTY;
        } else {
            b = new PublishSubscription[n - 1];
            System.arraycopy(a, 0, b, 0, j);
            System.arraycopy(a, j + 1, b, j, n - j - 1);
        }
        if (subscribers.compareAndSet(a, b)) {
            return;
        }
    }
}

public static AdminServer createAdminServer() {
    if (!"false".equals(System.getProperty("zookeeper.admin.enableServer"))) {
        try {
            Class<?> jettyAdminServerC = Class.forName("org.apache.zookeeper.server.admin.JettyAdminServer");
            Object adminServer = jettyAdminServerC.getConstructor().newInstance();
            return (AdminServer) adminServer;
        } catch (ClassNotFoundException e) {
            LOG.warn("Unable to start JettyAdminServer", e);
        } catch (InstantiationException e) {
            LOG.warn("Unable to start JettyAdminServer", e);
        } catch (IllegalAccessException e) {
            LOG.warn("Unable to start JettyAdminServer", e);
        } catch (InvocationTargetException e) {
