void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    Observer<? super R> downstream = this.downstream;
    AtomicThrowable errors = this.errors;
    AtomicReference<SwitchMapMaybeObserver<R>> inner = this.inner;

    for (;;) {
        for (;;) {
            if (cancelled) {
                return;
            }

            if (errors.get() != null) {
                if (!delayErrors) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            boolean d = done;
            SwitchMapMaybeObserver<R> current = inner.get();
            boolean empty = current == null;

            if (d && empty) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (empty || current.item == null) {
                break;
            }

            inner.compareAndSet(current, null);

            downstream.onNext(current.item);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void onNext(T t) {
    if (done) {
        return;
    }

    boolean b;
    try {
        b = predicate.test(t);
    } catch (Throwable e) {
        Exceptions.throwIfFatal(e);
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        onError(e);
        return;
    }

    if (!b) {
        done = true;
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        downstream.onSuccess(false);
    }
}

void drainLoop() {
    Observer<? super T> actual = this.downstream;
    int missed = 1;
    for (;;) {

        for (;;) {
            if (disposed) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(actual);
                return;
            }

            int os = otherState;
            if (os == OTHER_STATE_HAS_VALUE) {
                T v = singleItem;
                singleItem = null;
                otherState = OTHER_STATE_CONSUMED_OR_EMPTY;
                os = OTHER_STATE_CONSUMED_OR_EMPTY;
                actual.onNext(v);
            }

            boolean d = mainDone;
            SimplePlainQueue<T> q = queue;
            T v = q != null ? q.poll() : null;
            boolean empty = v == null;

            if (d && empty && os == OTHER_STATE_CONSUMED_OR_EMPTY) {
                queue = null;
                actual.onComplete();
                return;
            }

            if (empty) {
                break;
            }

            actual.onNext(v);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final SimplePlainQueue<Object> queue = this.queue;
    final Subscriber<? super Flowable<T>> downstream = this.downstream;
    UnicastProcessor<T> window = this.window;

    for (;;) {
        if (upstreamCancelled) {
            queue.clear();
            window = null;
            this.window = null;
        } else {
            boolean isDone = done;
            Object o = queue.poll();
            boolean isEmpty = o == null;

            if (isDone && isEmpty) {
                Throwable ex = error;
                if (ex != null) {
                    if (window != null) {
                        window.onError(ex);
                    }
                    downstream.onError(ex);
                } else {
                    if (window != null) {
                        window.onComplete();
                    }
                    downstream.onComplete();
                }
                cleanupResources();
                upstreamCancelled = true;
                continue;
            } else if (!isEmpty) {
                if (o instanceof WindowBoundaryRunnable) {
                    WindowBoundaryRunnable boundary = (WindowBoundaryRunnable) o;
                    if (boundary.index == emitted || !restartTimerOnMaxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    }
                } else if (window != null) {
                    @SuppressWarnings("unchecked")
                    T item = (T) o;
                    window.onNext(item);

                    long count = this.count + 1;
                    if (count == maxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    } else {
                        this.count = count;
                    }
                }

                continue;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    EqualObserver<T>[] as = observers;

    final EqualObserver<T> observer1 = as[0];
    final SpscLinkedArrayQueue<T> q1 = observer1.queue;
    final EqualObserver<T> observer2 = as[1];
    final SpscLinkedArrayQueue<T> q2 = observer2.queue;

    for (;;) {
        for (;;) {
            if (cancelled) {
                q1.clear();
                q2.clear();
                return;
            }

            boolean d1 = observer1.done;

            if (d1) {
                Throwable e = observer1.error;
                if (e != null) {
                    cancel(q1, q2);

                    downstream.onError(e);
                    return;
                }
            }

            boolean d2 = observer2.done;
            if (d2) {
                Throwable e = observer2.error;
                if (e != null) {
                    cancel(q1, q2);

                    downstream.onError(e);
                    return;
                }
            }

            if (v1 == null) {
                v1 = q1.poll();
            }
            boolean e1 = v1 == null;

            if (v2 == null) {
                v2 = q2.poll();
            }
            boolean e2 = v2 == null;

            if (d1 && d2 && e1 && e2) {
                downstream.onSuccess(true);
                return;
            }

            if ((d1 && d2) && (e1 != e2)) {
                cancel(q1, q2);

                downstream.onSuccess(false);
                return;
            }

            if (!e1 && !e2) {
                boolean c;

                try {
                    c = comparer.test(v1, v2);
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    cancel(q1, q2);

                    downstream.onError(ex);
                    return;
                }

                if (!c) {
                    cancel(q1, q2);

                    downstream.onSuccess(false);
                    return;
                }

                v1 = null;
                v2 = null;
            }

            if (e1 || e2) {
                break;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drainLoop() {
    int missed = 1;

    JoinInnerSubscriber<T>[] s = this.subscribers;
    int n = s.length;
    Subscriber<? super T> a = this.downstream;

    for (;;) {
        long r = requested.get();
        long e = 0;

        middle:
        while (e != r) {
            if (cancelled) {
                cleanup();
                return;
            }

            Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }

            boolean d = done.get() == 0;
            boolean empty = true;

            for (int i = 0; i < s.length; i++) {
                JoinInnerSubscriber<T> inner = s[i];
                SimplePlainQueue<T> q = inner.queue;
                if (q != null) {
                    T v = q.poll();

                    if (v != null) {
                        empty = false;
                        a.onNext(v);
                        inner.requestOne();
                        if (++e == r) {
                            break middle;
                        }
                    }
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }

            if (empty) {
                break;
            }
        }

        if (e == r) {
            if (cancelled) {
                cleanup();
                return;
            }

            Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }

            boolean d = done.get() == 0;
            boolean empty = true;

            for (int i = 0; i < n; i++) {
                JoinInnerSubscriber<T> inner = s[i];
                SimpleQueue<T> q = inner.queue;
                if (q != null && !q.isEmpty()) {
                    empty = false;
                    break;
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }
        }

        if (e != 0) {
            BackpressureHelper.produced(requested, e);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    InnerQueuedSubscriber<R> inner = current;
    Subscriber<? super R> a = downstream;
    ErrorMode em = errorMode;

    for (;;) {
        long r = requested.get();
        long e = 0L;

        if (inner == null) {
            if (em != ErrorMode.END) {
                Throwable ex = errors.get();
                if (ex != null) {
                    cancelAll();

                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            boolean outerDone = done;
            inner = subscribers.poll();

            if (outerDone && inner == null) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (inner != null) {
                current = inner;
            }
        }

        boolean continueNextSource = false;

        if (inner != null) {
            SimpleQueue<R> q = inner.queue();
            if (q != null) {
                while (e != r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }

                    if (em == ErrorMode.IMMEDIATE) {
                        Throwable ex = errors.get();
                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();

                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    boolean d = inner.isDone();
                    R v;

                    try {
                        v = q.poll();
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        current = null;
                        inner.cancel();
                        cancelAll();
                        a.onError(ex);
                        return;
                    }

                    boolean empty = v == null;

                    if (d && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                        break;
                    }

                    if (empty) {
                        break;
                    }

                    a.onNext(v);
                    e++;
                    inner.request(1L);
                }

                if (e == r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }

                    if (em == ErrorMode.IMMEDIATE) {
                        Throwable ex = errors.get();
                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();

                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    boolean d = inner.isDone();
                    boolean empty = q.isEmpty();

                    if (d && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                    }
                }
            }
        }

        if (e != 0L && r != Long.MAX_VALUE) {
            requested.addAndGet(-e);
        }

        if (continueNextSource) {
            continue;
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
void remove(CacheDisposable<T> consumer) {
    for (;;) {
        CacheDisposable<T>[] current = observers.get();
        int n = current.length;
        if (n == 0) {
            return;
        }

        int j = -1;
        for (int i = 0; i < n; i++) {
            if (current[i] == consumer) {
                j = i;
                break;
            }
        }

        if (j < 0) {
            return;
        }

        CacheDisposable<T>[] next;
        if (n == 1) {
            next = EMPTY;
        } else {
            next = new CacheDisposable[n - 1];
            System.arraycopy(current, 0, next, 0, j);
            System.arraycopy(current, j + 1, next, j, n - j - 1);
        }

        if (observers.compareAndSet(current, next)) {
            return;
        }
    }
}

void drainFused() {
    int missed = 1;

    for (;;) {
        if (disposed) {
            return;
        }

        boolean d = done;
        Throwable ex = error;

        if (!delayError && d && ex != null) {
            disposed = true;
            downstream.onError(error);
            worker.dispose();
            return;
        }

        downstream.onNext(null);

        if (d) {
            disposed = true;
            ex = error;
            if (ex != null) {
                downstream.onError(ex);
            } else {
                downstream.onComplete();
            }
            worker.dispose();
            return;
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
void remove(PublishSubscription<T> ps) {
    for (;;) {
        PublishSubscription<T>[] a = subscribers.get();
        if (a == TERMINATED || a == EMPTY) {
            return;
        }

        int n = a.length;
        int j = -1;

        // Find the subscription in the array
        for (int i = 0; i < n; i++) {
            if (a[i] == ps) {
                j = i;
                break;
            }
        }

        if (j < 0) {
            return;
        }

        PublishSubscription<T>[] b;

        // Create new array without the subscription
        if (n == 1) {
            b = EMPTY;
        } else {
            b = new PublishSubscription[n - 1];
            System.arraycopy(a, 0, b, 0, j);
            System.arraycopy(a, j + 1, b, j, n - j - 1);
        }

        if (subscribers.compareAndSet(a, b)) {
            return;
        }
    }
}

@SuppressLint("SwitchIntDef") // only fit, fill and zoom are supported by NewPipe
@ResizeMode
public static int nextResizeModeAndSaveToPrefs(
        final Player player,
        @ResizeMode final int resizeMode) {
    final int newResizeMode;
    switch (resizeMode) {
        case AspectRatioFrameLayout.RESIZE_MODE_FIT:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FILL;
            break;
        case AspectRatioFrameLayout.RESIZE_MODE_FILL:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_ZOOM;
            break;
        case AspectRatioFrameLayout.RESIZE_MODE_ZOOM:
        default:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FIT;
            break;
    }

    // save the new resize mode so it can be restored in a future session
    player.getPrefs().edit()
            .putInt(player.getContext().getString(R.string.last_resize_mode), newResizeMode)
            .apply();
    return newResizeMode;
}

@Override
public void onBroadcastReceived(final Intent intent) {
    super.onBroadcastReceived(intent);

    if (Intent.ACTION_CONFIGURATION_CHANGED.equals(intent.getAction())) {
        // Close it because when changing orientation from portrait
        // (in fullscreen mode) the size of queue layout can be larger than the screen size
        closeItemsList();
    } else if (ACTION_PLAY_PAUSE.equals(intent.getAction())) {
        // Ensure that we have audio-only stream playing when a user
        // started to play from notification's play button from outside of the app
        if (!fragmentIsVisible) {
            onFragmentStopped();
        }
    } else if (VideoDetailFragment.ACTION_VIDEO_FRAGMENT_STOPPED.equals(intent.getAction())) {
        fragmentIsVisible = false;
        onFragmentStopped();
    } else if (VideoDetailFragment.ACTION_VIDEO_FRAGMENT_RESUMED.equals(intent.getAction())) {
        // Restore video source when user returns to the fragment
        fragmentIsVisible = true;
        player.useVideoSource(true);

        // When a user returns from background, the system UI will always be shown even if
        // controls are invisible: hide it in that case
        if (!isControlsVisible()) {
            hideSystemUIIfNeeded();
        }
    }
}

@StyleRes
public static int getSettingsThemeStyle(final Context context) {
    final Resources res = context.getResources();
    final String lightTheme = res.getString(R.string.light_theme_key);
    final String blackTheme = res.getString(R.string.black_theme_key);
    final String automaticDeviceTheme = res.getString(R.string.auto_device_theme_key);

    final String selectedTheme = getSelectedThemeKey(context);

    if (selectedTheme.equals(lightTheme)) {
        return R.style.LightSettingsTheme;
    } else if (selectedTheme.equals(blackTheme)) {
        return R.style.BlackSettingsTheme;
    } else if (selectedTheme.equals(automaticDeviceTheme)) {
        if (isDeviceDarkThemeEnabled(context)) {
            // use the dark theme variant preferred by the user
            final String selectedNightTheme = getSelectedNightThemeKey(context);
            if (selectedNightTheme.equals(blackTheme)) {
                return R.style.BlackSettingsTheme;
            } else {
                return R.style.DarkSettingsTheme;
            }
        } else {
            // there is only one day theme
            return R.style.LightSettingsTheme;
        }
    } else {
        // default to dark theme
        return R.style.DarkSettingsTheme;
    }
}

@Override
public Bitmap transform(final Bitmap source) {
    if (DEBUG) {
        Log.d(TAG, "Thumbnail - transform() called");
    }

    final float notificationThumbnailWidth = Math.min(
            context.getResources()
                    .getDimension(R.dimen.player_notification_thumbnail_width),
            source.getWidth());

    final Bitmap result = BitmapCompat.createScaledBitmap(
            source,
            (int) notificationThumbnailWidth,
            (int) (source.getHeight()
                    / (source.getWidth() / notificationThumbnailWidth)),
            null,
            true);

    if (result == source || !result.isMutable()) {
        // create a new mutable bitmap to prevent strange crashes on some
        // devices (see #4638)
        final Bitmap copied = BitmapCompat.createScaledBitmap(
                source,
                (int) notificationThumbnailWidth - 1,
                (int) (source.getHeight() / (source.getWidth()
                        / (notificationThumbnailWidth - 1))),
                null,
                true);
        source.recycle();
        return copied;
    } else {
        source.recycle();
        return result;
    }
}

private void onBroadcastReceived(final Intent intent) {
    if (intent == null || intent.getAction() == null) {
        return;
    }

    if (DEBUG) {
        Log.d(TAG, "onBroadcastReceived() called with: intent = [" + intent + "]");
    }

    switch (intent.getAction()) {
        case AudioManager.ACTION_AUDIO_BECOMING_NOISY:
            pause();
            break;
        case ACTION_CLOSE:
            service.destroyPlayerAndStopService();
            break;
        case ACTION_PLAY_PAUSE:
            playPause();
            break;
        case ACTION_PLAY_PREVIOUS:
            playPrevious();
            break;
        case ACTION_PLAY_NEXT:
            playNext();
            break;
        case ACTION_FAST_REWIND:
            fastRewind();
            break;
        case ACTION_FAST_FORWARD:
            fastForward();
            break;
        case ACTION_REPEAT:
            cycleNextRepeatMode();
            break;
        case ACTION_SHUFFLE:
            toggleShuffleModeEnabled();
            break;
        case Intent.ACTION_CONFIGURATION_CHANGED:
            if (DEBUG) {
                Log.d(TAG, "ACTION_CONFIGURATION_CHANGED received");
            }
            break;
    }

    UIs.call(
            playerUi -> {
                playerUi.onBroadcastReceived(intent);
            });
}

private static void handleError(final Context context, final ErrorInfo errorInfo) {
    if (errorInfo.getThrowable() != null) {
        errorInfo.getThrowable().printStackTrace();
    }

    if (errorInfo.getThrowable() instanceof ReCaptchaException) {
        Toast.makeText(context, R.string.recaptcha_request_toast, Toast.LENGTH_LONG).show();
        // Starting ReCaptcha Challenge Activity
        final Intent intent = new Intent(context, ReCaptchaActivity.class);
        intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK);
        context.startActivity(intent);
    } else if (errorInfo.getThrowable() != null
            && ExceptionUtils.isNetworkRelated(errorInfo.getThrowable())) {
        Toast.makeText(context, R.string.network_error, Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof AgeRestrictedContentException) {
        Toast.makeText(context, 
                R.string.restricted_video_no_stream,
                Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof GeographicRestrictionException) {
        Toast.makeText(context, R.string.georestricted_content, Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof PaidContentException) {
        Toast.makeText(context, R.string.paid_content, Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof PrivateContentException) {
        Toast.makeText(context, R.string.private_content, Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof SoundCloudGoPlusContentException) {
        Toast.makeText(context, 
                R.string.soundcloud_go_plus_content,
                Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof YoutubeMusicPremiumContentException) {
        Toast.makeText(context, 
                R.string.youtube_music_premium_content,
                Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof ContentNotAvailableException) {
        Toast.makeText(context, R.string.content_not_available, Toast.LENGTH_LONG).show();
    } else if (errorInfo.getThrowable() instanceof ContentNotSupportedException) {
        Toast.makeText(context, R.string.content_not_supported, Toast.LENGTH_LONG).show();
    } else {
        ErrorUtil.createNotification(context, errorInfo);
    }

    if (context instanceof RouterActivity) {
        ((RouterActivity) context).finish();
    }
}

public static void openVideoDetailFragment(@NonNull final Context context,
                                           @NonNull final FragmentManager fragmentManager,
                                           final int serviceId,
                                           @Nullable final String url,
                                           @NonNull final String title,
                                           @Nullable final PlayQueue playQueue,
                                           final boolean switchingPlayers) {
    final boolean autoPlay;
    @Nullable final PlayerType playerType = PlayerHolder.getInstance().getType();

    if (playerType == null) {
        // no player open
        autoPlay = PlayerHelper.isAutoplayAllowedByUser(context);
    } else if (switchingPlayers) {
        // switching player to main player
        autoPlay = PlayerHolder.getInstance().isPlaying(); // keep play/pause state
    } else if (playerType == PlayerType.MAIN) {
        // opening new stream while already playing in main player
        autoPlay = PlayerHelper.isAutoplayAllowedByUser(context);
    } else {
        // opening new stream while already playing in another player
        autoPlay = false;
    }

    final RunnableWithVideoDetailFragment onVideoDetailFragmentReady = detailFragment -> {
        expandMainPlayer(detailFragment.requireActivity());
        detailFragment.setAutoPlay(autoPlay);

        if (switchingPlayers) {
            // Situation when user switches from players to main player. All needed data is
            // here, we can start watching (assuming newQueue equals playQueue).
            // Starting directly in fullscreen if the previous player type was popup.
            detailFragment.openVideoPlayer(playerType == PlayerType.POPUP
                    || PlayerHelper.isStartMainPlayerFullscreenEnabled(context));
        } else {
            detailFragment.selectAndLoadVideo(serviceId, url, title, playQueue);
        }
        detailFragment.scrollToTop();
    };

    final Fragment fragment = fragmentManager.findFragmentById(R.id.fragment_player_holder);
    if (fragment instanceof VideoDetailFragment && fragment.isVisible()) {
        onVideoDetailFragmentReady.run((VideoDetailFragment) fragment);
    } else {
        // Specify no url here, otherwise the VideoDetailFragment will start loading the
        // stream automatically if it's the first time it is being opened, but then
        // onVideoDetailFragmentReady will kick in and start another loading process.
        // See VideoDetailFragment.wasCleared() and its usage in doInitialLoadLogic().
        final VideoDetailFragment instance = VideoDetailFragment
                .getInstance(serviceId, null, title, playQueue);
        instance.setAutoPlay(autoPlay);

        defaultTransaction(fragmentManager)
                .replace(R.id.fragment_player_holder, instance)
                .runOnCommit(() -> onVideoDetailFragmentReady.run(instance))
                .commit();
    }
}

@Override
public void handleResult(@NonNull final ChannelInfo result) {
    super.handleResult(result);
    currentInfo = result;
    setInitialData(result.getServiceId(), result.getOriginalUrl(), result.getName());

    if (ImageStrategy.shouldLoadImages() && !result.getBanners().isEmpty()) {
        PicassoHelper.loadBanner(result.getBanners())
                .tag(PICASSO_CHANNEL_TAG)
                .into(binding.channelBannerImage);
    } else {
        // Do not waste space for the banner, if the user disabled images or there is not one
        binding.channelBannerImage.setImageDrawable(null);
    }

    PicassoHelper.loadAvatar(result.getAvatars())
            .tag(PICASSO_CHANNEL_TAG)
            .into(binding.channelAvatarView);
    PicassoHelper.loadAvatar(result.getParentChannelAvatars())
            .tag(PICASSO_CHANNEL_TAG)
            .into(binding.subChannelAvatarView);

    binding.channelTitleView.setText(result.getName());
    binding.channelSubscriberView.setVisibility(View.VISIBLE);

    if (result.getSubscriberCount() >= 0) {
        binding.channelSubscriberView.setText(Localization
                .shortSubscriberCount(activity, result.getSubscriberCount()));
    } else {
        binding.channelSubscriberView.setText(R.string.subscribers_count_not_available);
    }

    if (!TextUtils.isEmpty(currentInfo.getParentChannelName())) {
        binding.subChannelTitleView.setText(
                String.format(
                        getString(R.string.channel_created_by),
                        currentInfo.getParentChannelName())
        );
        binding.subChannelTitleView.setVisibility(View.VISIBLE);
        binding.subChannelAvatarView.setVisibility(View.VISIBLE);
    }

    updateRssButton();

    channelContentNotSupported = false;
    for (final Throwable throwable : result.getErrors()) {
        if (throwable instanceof ContentNotSupportedException) {
            channelContentNotSupported = true;
            showContentNotSupportedIfNeeded();
            break;
        }
    }

    disposables.clear();
    if (subscribeButtonMonitor != null) {
        subscribeButtonMonitor.dispose();
    }

    updateTabs();
    updateSubscription(result);
    monitorSubscription(result);
}

@Override
public void onTextTracksChanged(@NonNull final Tracks currentTracks) {
    super.onTextTracksChanged(currentTracks);

    final boolean trackTypeTextSupported = !currentTracks.containsType(C.TRACK_TYPE_TEXT)
            || currentTracks.isTypeSupported(C.TRACK_TYPE_TEXT, false);
    if (getPlayer().getTrackSelector().getCurrentMappedTrackInfo() == null
            || !trackTypeTextSupported) {
        binding.captionTextView.setVisibility(View.GONE);
        return;
    }

    // Extract all loaded languages
    final List<Tracks.Group> textTracks = currentTracks
            .getGroups()
            .stream()
            .filter(trackGroupInfo -> C.TRACK_TYPE_TEXT == trackGroupInfo.getType())
            .collect(Collectors.toList());
    final List<String> availableLanguages = textTracks.stream()
            .map(Tracks.Group::getMediaTrackGroup)
            .filter(textTrack -> textTrack.length > 0)
            .map(textTrack -> textTrack.getFormat(0).language)
            .collect(Collectors.toList());

    // Find selected text track
    final Optional<Format> selectedTracks = textTracks.stream()
            .filter(Tracks.Group::isSelected)
            .filter(info -> info.getMediaTrackGroup().length >= 1)
            .map(info -> info.getMediaTrackGroup().getFormat(0))
            .findFirst();

    // Build UI
    buildCaptionMenu(availableLanguages);
    if (player.getTrackSelector().getParameters().getRendererDisabled(
            player.getCaptionRendererIndex()) || selectedTracks.isEmpty()) {
        binding.captionTextView.setText(R.string.caption_none);
    } else {
        binding.captionTextView.setText(selectedTracks.get().language);
    }
    binding.captionTextView.setVisibility(
            availableLanguages.isEmpty() ? View.GONE : View.VISIBLE);
}

public static RequestCreator loadScaledDownThumbnail(final Context context,
        @NonNull final List<Image> images) {
    // scale down the notification thumbnail for performance
    return PicassoHelper.loadThumbnail(images)
            .transform(new Transformation() {
                @Override
                public Bitmap transform(final Bitmap source) {
                    if (DEBUG) {
                        Log.d(TAG, "Thumbnail - transform() called");
                    }

                    final float notificationThumbnailWidth = Math.min(
                            context.getResources()
                                    .getDimension(R.dimen.player_notification_thumbnail_width),
                            source.getWidth());

                    final Bitmap result = BitmapCompat.createScaledBitmap(
                            source,
                            (int) notificationThumbnailWidth,
                            (int) (source.getHeight()
                                    / (source.getWidth() / notificationThumbnailWidth)),
                            null,
                            true);

                    if (result == source || !result.isMutable()) {
                        // create a new mutable bitmap to prevent strange crashes on some
                        // devices (see #4638)
                        final Bitmap copied = BitmapCompat.createScaledBitmap(
                                source,
                                (int) notificationThumbnailWidth - 1,
                                (int) (source.getHeight() / (source.getWidth()
                                        / (notificationThumbnailWidth - 1))),
                                null,
                                true);
                        source.recycle();
                        return copied;
                    } else {
                        source.recycle();
                        return result;
                    }
                }

                @Override
                public String key() {
                    return PLAYER_THUMBNAIL_TRANSFORMATION_KEY;
                }
            });
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    Observer<? super R> downstream = this.downstream;
    AtomicThrowable errors = this.errors;
    AtomicReference<SwitchMapMaybeObserver<R>> inner = this.inner;

    for (;;) {
        for (;;) {
            if (cancelled) {
                return;
            }

            if (errors.get() != null) {
                if (!delayErrors) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            final boolean d = done;
            final SwitchMapMaybeObserver<R> current = inner.get();
            final boolean empty = current == null;

            if (d && empty) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (empty || current.item == null) {
                break;
            }

            inner.compareAndSet(current, null);

            downstream.onNext(current.item);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void onNext(final T t) {
    if (done) {
        return;
    }

    boolean b;
    try {
        b = predicate.test(t);
    } catch (final Throwable e) {
        Exceptions.throwIfFatal(e);
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        onError(e);
        return;
    }

    if (!b) {
        done = true;
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        downstream.onSuccess(false);
    }
}

private void drainLoop() {
    final Observer<? super T> actual = this.downstream;
    int missed = 1;
    for (;;) {

        for (;;) {
            if (disposed) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(actual);
                return;
            }

            final int os = otherState;
            if (os == OTHER_STATE_HAS_VALUE) {
                final T v = singleItem;
                singleItem = null;
                otherState = OTHER_STATE_CONSUMED_OR_EMPTY;
                final int newOs = OTHER_STATE_CONSUMED_OR_EMPTY;
                actual.onNext(v);
            }

            final boolean d = mainDone;
            final SimplePlainQueue<T> q = queue;
            final T v = q != null ? q.poll() : null;
            final boolean empty = v == null;

            if (d && empty && os == OTHER_STATE_CONSUMED_OR_EMPTY) {
                queue = null;
                actual.onComplete();
                return;
            }

            if (empty) {
                break;
            }

            actual.onNext(v);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
void drain() {
    if (DEBUG) {
        Log.d(TAG, "drain() called");
    }

    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final SimplePlainQueue<Object> queue = this.queue;
    final Subscriber<? super Flowable<T>> downstream = this.downstream;
    UnicastProcessor<T> window = this.window;

    for (;;) {
        if (upstreamCancelled) {
            queue.clear();
            window = null;
            this.window = null;
        } else {
            boolean isDone = done;
            Object o = queue.poll();
            boolean isEmpty = o == null;

            if (isDone && isEmpty) {
                Throwable ex = error;
                if (ex != null) {
                    if (window != null) {
                        window.onError(ex);
                    }
                    downstream.onError(ex);
                } else {
                    if (window != null) {
                        window.onComplete();
                    }
                    downstream.onComplete();
                }
                cleanupResources();
                upstreamCancelled = true;
                continue;
            } else if (!isEmpty) {
                if (o instanceof WindowBoundaryRunnable) {
                    WindowBoundaryRunnable boundary = (WindowBoundaryRunnable) o;
                    if (boundary.index == emitted || !restartTimerOnMaxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    }
                } else if (window != null) {
                    @SuppressWarnings("unchecked")
                    T item = (T) o;
                    window.onNext(item);

                    long count = this.count + 1;
                    if (count == maxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    } else {
                        this.count = count;
                    }
                }

                continue;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    EqualObserver<T>[] as = observers;

    final EqualObserver<T> observer1 = as[0];
    final SpscLinkedArrayQueue<T> q1 = observer1.queue;
    final EqualObserver<T> observer2 = as[1];
    final SpscLinkedArrayQueue<T> q2 = observer2.queue;

    for (;;) {
        for (;;) {
            if (cancelled) {
                q1.clear();
                q2.clear();
                return;
            }

            final boolean d1 = observer1.done;

            if (d1) {
                final Throwable e = observer1.error;
                if (e != null) {
                    cancel(q1, q2);

                    downstream.onError(e);
                    return;
                }
            }

            final boolean d2 = observer2.done;
            if (d2) {
                final Throwable e = observer2.error;
                if (e != null) {
                    cancel(q1, q2);

                    downstream.onError(e);
                    return;
                }
            }

            if (v1 == null) {
                v1 = q1.poll();
            }
            final boolean e1 = v1 == null;

            if (v2 == null) {
                v2 = q2.poll();
            }
            final boolean e2 = v2 == null;

            if (d1 && d2 && e1 && e2) {
                downstream.onSuccess(true);
                return;
            }
            if ((d1 && d2) && (e1 != e2)) {
                cancel(q1, q2);

                downstream.onSuccess(false);
                return;
            }

            if (!e1 && !e2) {
                boolean c;

                try {
                    c = comparer.test(v1, v2);
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    cancel(q1, q2);

                    downstream.onError(ex);
                    return;
                }

                if (!c) {
                    cancel(q1, q2);

                    downstream.onSuccess(false);
                    return;
                }

                v1 = null;
                v2 = null;
            }

            if (e1 || e2) {
                break;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drainLoop() {
    if (DEBUG) {
        Log.d(TAG, "drainLoop() called");
    }

    int missed = 1;

    JoinInnerSubscriber<T>[] s = this.subscribers;
    int n = s.length;
    Subscriber<? super T> a = this.downstream;

    for (;;) {
        long r = requested.get();
        long e = 0;

        middle:
        while (e != r) {
            if (cancelled) {
                cleanup();
                return;
            }

            Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }

            boolean d = done.get() == 0;
            boolean empty = true;

            for (int i = 0; i < s.length; i++) {
                JoinInnerSubscriber<T> inner = s[i];
                SimplePlainQueue<T> q = inner.queue;
                if (q != null) {
                    T v = q.poll();

                    if (v != null) {
                        empty = false;
                        a.onNext(v);
                        inner.requestOne();
                        if (++e == r) {
                            break middle;
                        }
                    }
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }

            if (empty) {
                break;
            }
        }

        if (e == r) {
            if (cancelled) {
                cleanup();
                return;
            }

            Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }

            boolean d = done.get() == 0;
            boolean empty = true;

            for (int i = 0; i < n; i++) {
                JoinInnerSubscriber<T> inner = s[i];
                SimpleQueue<T> q = inner.queue;
                if (q != null && !q.isEmpty()) {
                    empty = false;
                    break;
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }
        }

        if (e != 0) {
            BackpressureHelper.produced(requested, e);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    InnerQueuedSubscriber<R> inner = current;
    Subscriber<? super R> a = downstream;
    ErrorMode em = errorMode;

    for (;;) {
        long r = requested.get();
        long e = 0L;

        if (inner == null) {
            if (em != ErrorMode.END) {
                Throwable ex = errors.get();
                if (ex != null) {
                    cancelAll();

                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            boolean outerDone = done;

            inner = subscribers.poll();

            if (outerDone && inner == null) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (inner != null) {
                current = inner;
            }
        }

        boolean continueNextSource = false;

        if (inner != null) {
            SimpleQueue<R> q = inner.queue();
            if (q != null) {
                while (e != r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }

                    if (em == ErrorMode.IMMEDIATE) {
                        Throwable ex = errors.get();
                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();

                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    boolean d = inner.isDone();

                    R v;

                    try {
                        v = q.poll();
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        current = null;
                        inner.cancel();
                        cancelAll();
                        a.onError(ex);
                        return;
                    }

                    boolean empty = v == null;

                    if (d && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                        break;
                    }

                    if (empty) {
                        break;
                    }

                    a.onNext(v);

                    e++;

                    inner.request(1L);
                }

                if (e == r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }

                    if (em == ErrorMode.IMMEDIATE) {
                        Throwable ex = errors.get();
                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();

                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    boolean d = inner.isDone();
                    boolean empty = q.isEmpty();

                    if (d && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                    }
                }
            }
        }

        if (e != 0L && r != Long.MAX_VALUE) {
            requested.addAndGet(-e);
        }

        if (continueNextSource) {
            continue;
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
void remove(final CacheDisposable<T> consumer) {
    for (;;) {
        final CacheDisposable<T>[] current = observers.get();
        final int n = current.length;
        if (n == 0) {
            return;
        }

        int j = -1;
        for (int i = 0; i < n; i++) {
            if (current[i] == consumer) {
                j = i;
                break;
            }
        }

        if (j < 0) {
            return;
        }

        CacheDisposable<T>[] next;
        if (n == 1) {
            next = EMPTY;
        } else {
            next = new CacheDisposable[n - 1];
            System.arraycopy(current, 0, next, 0, j);
            System.arraycopy(current, j + 1, next, j, n - j - 1);
        }

        if (observers.compareAndSet(current, next)) {
            return;
        }
    }
}

void drainFused() {
    int missed = 1;

    for (;;) {
        if (disposed) {
            return;
        }

        final boolean d = done;
        final Throwable ex = error;

        if (!delayError && d && ex != null) {
            disposed = true;
            downstream.onError(error);
            worker.dispose();
            return;
        }

        downstream.onNext(null);

        if (d) {
            disposed = true;
            final Throwable finalEx = error;
            if (finalEx != null) {
                downstream.onError(finalEx);
            } else {
                downstream.onComplete();
            }
            worker.dispose();
            return;
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
void remove(final PublishSubscription<T> ps) {
    if (DEBUG) {
        Log.d(TAG, "remove() called with: ps = [" + ps + "]");
    }

    for (;;) {
        final PublishSubscription<T>[] a = subscribers.get();
        if (a == TERMINATED || a == EMPTY) {
            return;
        }

        final int n = a.length;
        int j = -1;
        for (int i = 0; i < n; i++) {
            if (a[i] == ps) {
                j = i;
                break;
            }
        }

        if (j < 0) {
            return;
        }

        PublishSubscription<T>[] b;

        if (n == 1) {
            b = EMPTY;
        } else {
            b = new PublishSubscription[n - 1];
            System.arraycopy(a, 0, b, 0, j);
            System.arraycopy(a, j + 1, b, j, n - j - 1);
        }

        if (subscribers.compareAndSet(a, b)) {
            return;
        }
    }
}

    void drain() {
        if (getAndIncrement() != 0) {
            return;
        }

        int missed = 1;
        Observer<? super R> downstream = this.downstream;
        AtomicThrowable errors = this.errors;
        AtomicReference<SwitchMapMaybeObserver<R>> inner = this.inner;

        for (;;) {
            for (;;) {
                if (cancelled) {
                    return;
                }

                if (errors.get() != null) {
                    if (!delayErrors) {
                        errors.tryTerminateConsumer(downstream);
                        return;
                    }
                }

                boolean d = done;
                SwitchMapMaybeObserver<R> current = inner.get();
                boolean empty = current == null;

                if (d && empty) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }

                if (empty || current.item == null) {
                    break;
                }

                inner.compareAndSet(current, null);

                downstream.onNext(current.item);
            }

            missed = addAndGet(-missed);
            if (missed == 0) {
                break;
            }
        }
    }


    private void showPopupMenu() {
        retry.setVisible(false);
        cancel.setVisible(false);
        start.setVisible(false);
        pause.setVisible(false);
        open.setVisible(false);
        queue.setVisible(false);
        showError.setVisible(false);
        delete.setVisible(false);
        source.setVisible(false);
        checksum.setVisible(false);

        DownloadMission mission = item.mission instanceof DownloadMission ? (DownloadMission) item.mission : null;

        if (mission != null) {
            if (mission.hasInvalidStorage()) {
                retry.setVisible(true);
                delete.setVisible(true);
                showError.setVisible(true);
            } else if (mission.isPsRunning()) {
                switch (mission.errCode) {
                    case ERROR_INSUFFICIENT_STORAGE:
                    case ERROR_POSTPROCESSING_HOLD:
                        retry.setVisible(true);
                        cancel.setVisible(true);
                        showError.setVisible(true);
                        break;
                }
            } else {
                if (mission.running) {
                    pause.setVisible(true);
                } else {
                    if (mission.errCode != ERROR_NOTHING) {
                        showError.setVisible(true);
                    }

                    queue.setChecked(mission.enqueued);

                    delete.setVisible(true);

                    boolean flag = !mission.isPsFailed() && mission.urls.length > 0;
                    start.setVisible(flag);
                    queue.setVisible(flag);
                }
            }
        } else {
            open.setVisible(true);
            delete.setVisible(true);
            checksum.setVisible(true);
        }

        if (item.mission.source != null && !item.mission.source.isEmpty()) {
            source.setVisible(true);
        }

        popupMenu.show();
    }


private void loadPendingMissions(Context ctx) {
    File[] subs = mPendingMissionsDir.listFiles();

    if (subs == null) {
        Log.e(TAG, "listFiles() returned null");
        return;
    }
    if (subs.length < 1) {
        return;
    }
    if (DEBUG) {
        Log.d(TAG, "Loading pending downloads from directory: " + mPendingMissionsDir.getAbsolutePath());
    }

    File tempDir = pickAvailableTemporalDir(ctx);
    Log.i(TAG, "using '" + tempDir + "' as temporal directory");

    for (File sub : subs) {
        if (!sub.isFile()) continue;
        if (sub.getName().equals(".tmp")) continue;

        DownloadMission mis = Utility.readFromFile(sub);
        if (mis == null || mis.isFinished() || mis.hasInvalidStorage()) {
            //noinspection ResultOfMethodCallIgnored
            sub.delete();
            continue;
        }

        mis.threads = new Thread[0];

        boolean exists;
        try {
            mis.storage = StoredFileHelper.deserialize(mis.storage, ctx);
            exists = !mis.storage.isInvalid() && mis.storage.existsAsFile();
        } catch (Exception ex) {
            Log.e(TAG, "Failed to load the file source of " + mis.storage.toString(), ex);
            mis.storage.invalidate();
            exists = false;
        }

        if (mis.isPsRunning()) {
            if (mis.psAlgorithm.worksOnSameFile) {
                // Incomplete post-processing results in a corrupted download file
                // because the selected algorithm works on the same file to save space.
                // the file will be deleted if the storage API
                // is Java IO (avoid showing the "Save as..." dialog)
                if (exists && mis.storage.isDirect() && !mis.storage.delete())
                    Log.w(TAG, "Unable to delete incomplete download file: " + sub.getPath());
            }

            mis.psState = 0;
            mis.errCode = DownloadMission.ERROR_POSTPROCESSING_STOPPED;
        } else if (!exists) {
            tryRecover(mis);

            // the progress is lost, reset mission state
            if (mis.isInitialized())
                mis.resetState(true, true, DownloadMission.ERROR_PROGRESS_LOST);
        }

        if (mis.psAlgorithm != null) {
            mis.psAlgorithm.cleanupTemporalDir();
            mis.psAlgorithm.setTemporalDir(tempDir);
        }

        mis.metadata = sub;
        mis.maxRetry = mPrefMaxRetry;
        mis.mHandler = mHandler;

        mMissionsPending.add(mis);
    }

    if (mMissionsPending.size() > 1)
        Collections.sort(mMissionsPending, Comparator.comparingLong(Mission::getTimestamp));
}


@SuppressLint("DefaultLocale")
private void updateProgress(ViewHolderItem h) {
    if (h == null || h.item == null || h.item.mission instanceof FinishedMission) return;

    DownloadMission mission = (DownloadMission) h.item.mission;
    double done = mission.done;
    long length = mission.getLength();
    long now = System.currentTimeMillis();
    boolean hasError = mission.errCode != ERROR_NOTHING;

    // hide on error
    // show if current resource length is not fetched
    // show if length is unknown
    h.progress.setMarquee(mission.isRecovering() || !hasError && (!mission.isInitialized() || mission.unknownLength));

    double progress;
    if (mission.unknownLength) {
        progress = Double.NaN;
        h.progress.setProgress(0.0f);
    } else {
        progress = done / length;
    }

    if (hasError) {
        h.progress.setProgress(isNotFinite(progress) ? 1d : progress);
        h.status.setText(R.string.msg_error);
    } else if (isNotFinite(progress)) {
        h.status.setText(UNDEFINED_PROGRESS);
    } else {
        h.status.setText(String.format("%.2f%%", progress * 100));
        h.progress.setProgress(progress);
    }

    @StringRes int state;
    String sizeStr = Utility.formatBytes(length).concat("  ");

    if (mission.isPsFailed() || mission.errCode == ERROR_POSTPROCESSING_HOLD) {
        h.size.setText(sizeStr);
        return;
    } else if (!mission.running) {
        state = mission.enqueued ? R.string.queued : R.string.paused;
    } else if (mission.isPsRunning()) {
        state = R.string.post_processing;
    } else if (mission.isRecovering()) {
        state = R.string.recovering;
    } else {
        state = 0;
    }

    if (state != 0) {
        // update state without download speed
        h.size.setText(sizeStr.concat("(").concat(mContext.getString(state)).concat(")"));
        h.resetSpeedMeasure();
        return;
    }

    if (h.lastTimestamp < 0) {
        h.size.setText(sizeStr);
        h.lastTimestamp = now;
        h.lastDone = done;
        return;
    }

    long deltaTime = now - h.lastTimestamp;
    double deltaDone = done - h.lastDone;

    if (h.lastDone > done) {
        h.lastDone = done;
        h.size.setText(sizeStr);
        return;
    }

    if (deltaDone > 0 && deltaTime > 0) {
        float speed = (float) ((deltaDone * 1000d) / deltaTime);
        float averageSpeed = speed;

        if (h.lastSpeedIdx < 0) {
            Arrays.fill(h.lastSpeed, speed);
            h.lastSpeedIdx = 0;
        } else {
            for (int i = 0; i < h.lastSpeed.length; i++) {
                averageSpeed += h.lastSpeed[i];
            }
            averageSpeed /= h.lastSpeed.length + 1.0f;
        }

        String speedStr = Utility.formatSpeed(averageSpeed);
        String etaStr;

        if (mission.unknownLength) {
            etaStr = "";
        } else {
            long eta = (long) Math.ceil((length - done) / averageSpeed);
            etaStr = Utility.formatBytes((long) done) + "/" + Utility.stringifySeconds(eta) + "  ";
        }

        h.size.setText(sizeStr.concat(etaStr).concat(speedStr));

        h.lastTimestamp = now;
        h.lastDone = done;
        h.lastSpeed[h.lastSpeedIdx++] = speed;

        if (h.lastSpeedIdx >= h.lastSpeed.length) h.lastSpeedIdx = 0;
    }
}

@Override
public void onNext(T t) {
    if (done) {
        return;
    }

    boolean b;

    try {
        b = predicate.test(t);
    } catch (Throwable e) {
        Exceptions.throwIfFatal(e);
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        onError(e);
        return;
    }

    if (!b) {
        done = true;
        upstream.cancel();
        upstream = SubscriptionHelper.CANCELLED;
        downstream.onSuccess(false);
    }
}

void drainLoop() {
    Observer<? super T> actual = this.downstream;
    int missed = 1;
    for (;;) {

        for (;;) {
            if (disposed) {
                singleItem = null;
                queue = null;
                return;
            }

            if (errors.get() != null) {
                singleItem = null;
                queue = null;
                errors.tryTerminateConsumer(actual);
                return;
            }

            int os = otherState;
            if (os == OTHER_STATE_HAS_VALUE) {
                T v = singleItem;
                singleItem = null;
                otherState = OTHER_STATE_CONSUMED_OR_EMPTY;
                os = OTHER_STATE_CONSUMED_OR_EMPTY;
                actual.onNext(v);
            }

            boolean d = mainDone;
            SimplePlainQueue<T> q = queue;
            T v = q != null ? q.poll() : null;
            boolean empty = v == null;

            if (d && empty && os == OTHER_STATE_CONSUMED_OR_EMPTY) {
                queue = null;
                actual.onComplete();
                return;
            }

            if (empty) {
                break;
            }

            actual.onNext(v);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final SimplePlainQueue<Object> queue = this.queue;
    final Subscriber<? super Flowable<T>> downstream = this.downstream;
    UnicastProcessor<T> window = this.window;

    for (;;) {
        if (upstreamCancelled) {
            queue.clear();
            window = null;
            this.window = null;
        } else {
            boolean isDone = done;
            Object o = queue.poll();
            boolean isEmpty = o == null;

            if (isDone && isEmpty) {
                Throwable ex = error;
                if (ex != null) {
                    if (window != null) {
                        window.onError(ex);
                    }
                    downstream.onError(ex);
                } else {
                    if (window != null) {
                        window.onComplete();
                    }
                    downstream.onComplete();
                }
                cleanupResources();
                upstreamCancelled = true;
                continue;
            } else if (!isEmpty) {
                if (o instanceof WindowBoundaryRunnable) {
                    WindowBoundaryRunnable boundary = (WindowBoundaryRunnable) o;
                    if (boundary.index == emitted || !restartTimerOnMaxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    }
                } else if (window != null) {
                    @SuppressWarnings("unchecked")
                    T item = (T)o;
                    window.onNext(item);

                    long count = this.count + 1;
                    if (count == maxSize) {
                        this.count = 0;
                        window = createNewWindow(window);
                    } else {
                        this.count = count;
                    }
                }

                continue;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    final EqualObserver<T>[] as = observers;

    final EqualObserver<T> observer1 = as[0];
    final SpscLinkedArrayQueue<T> q1 = observer1.queue;
    final EqualObserver<T> observer2 = as[1];
    final SpscLinkedArrayQueue<T> q2 = observer2.queue;

    for (;;) {
        for (;;) {
            if (cancelled) {
                q1.clear();
                q2.clear();
                return;
            }

            final boolean d1 = observer1.done;

            if (d1) {
                final Throwable e = observer1.error;
                if (e != null) {
                    cancel(q1, q2);

                    downstream.onError(e);
                    return;
                }
            }

            final boolean d2 = observer2.done;
            if (d2) {
                final Throwable e = observer2.error;
                if (e != null) {
                    cancel(q1, q2);

                    downstream.onError(e);
                    return;
                }
            }

            if (v1 == null) {
                v1 = q1.poll();
            }
            final boolean e1 = v1 == null;

            if (v2 == null) {
                v2 = q2.poll();
            }
            final boolean e2 = v2 == null;

            if (d1 && d2 && e1 && e2) {
                downstream.onSuccess(true);
                return;
            }
            if ((d1 && d2) && (e1 != e2)) {
                cancel(q1, q2);

                downstream.onSuccess(false);
                return;
            }

            if (!e1 && !e2) {
                boolean c;

                try {
                    c = comparer.test(v1, v2);
                } catch (Throwable ex) {
                    Exceptions.throwIfFatal(ex);
                    cancel(q1, q2);

                    downstream.onError(ex);
                    return;
                }

                if (!c) {
                    cancel(q1, q2);

                    downstream.onSuccess(false);
                    return;
                }

                v1 = null;
                v2 = null;
            }

            if (e1 || e2) {
                break;
            }
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

void drainLoop() {
    int missed = 1;

    JoinInnerSubscriber<T>[] s = this.subscribers;
    int n = s.length;
    Subscriber<? super T> a = this.downstream;

    for (;;) {
        long r = requested.get();
        long e = 0;

        middle:
        while (e != r) {
            if (cancelled) {
                cleanup();
                return;
            }

            Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }

            boolean d = done.get() == 0;
            boolean empty = true;

            for (int i = 0; i < s.length; i++) {
                JoinInnerSubscriber<T> inner = s[i];
                SimplePlainQueue<T> q = inner.queue;
                if (q != null) {
                    T v = q.poll();

                    if (v != null) {
                        empty = false;
                        a.onNext(v);
                        inner.requestOne();
                        if (++e == r) {
                            break middle;
                        }
                    }
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }

            if (empty) {
                break;
            }
        }

        if (e == r) {
            if (cancelled) {
                cleanup();
                return;
            }

            Throwable ex = errors.get();
            if (ex != null) {
                cleanup();
                a.onError(ex);
                return;
            }

            boolean d = done.get() == 0;
            boolean empty = true;

            for (int i = 0; i < n; i++) {
                JoinInnerSubscriber<T> inner = s[i];
                SimpleQueue<T> q = inner.queue;
                if (q != null && !q.isEmpty()) {
                    empty = false;
                    break;
                }
            }

            if (d && empty) {
                a.onComplete();
                return;
            }
        }

        if (e != 0) {
            BackpressureHelper.produced(requested, e);
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@Override
public void drain() {
    if (getAndIncrement() != 0) {
        return;
    }

    int missed = 1;
    InnerQueuedSubscriber<R> inner = current;
    Subscriber<? super R> a = downstream;
    ErrorMode em = errorMode;

    for (;;) {
        long r = requested.get();
        long e = 0L;

        if (inner == null) {
            if (em != ErrorMode.END) {
                Throwable ex = errors.get();
                if (ex != null) {
                    cancelAll();

                    errors.tryTerminateConsumer(downstream);
                    return;
                }
            }

            boolean outerDone = done;

            inner = subscribers.poll();

            if (outerDone && inner == null) {
                errors.tryTerminateConsumer(downstream);
                return;
            }

            if (inner != null) {
                current = inner;
            }
        }

        boolean continueNextSource = false;

        if (inner != null) {
            SimpleQueue<R> q = inner.queue();
            if (q != null) {
                while (e != r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }

                    if (em == ErrorMode.IMMEDIATE) {
                        Throwable ex = errors.get();
                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();

                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    boolean d = inner.isDone();

                    R v;

                    try {
                        v = q.poll();
                    } catch (Throwable ex) {
                        Exceptions.throwIfFatal(ex);
                        current = null;
                        inner.cancel();
                        cancelAll();
                        a.onError(ex);
                        return;
                    }

                    boolean empty = v == null;

                    if (d && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                        break;
                    }

                    if (empty) {
                        break;
                    }

                    a.onNext(v);

                    e++;

                    inner.request(1L);
                }

                if (e == r) {
                    if (cancelled) {
                        cancelAll();
                        return;
                    }

                    if (em == ErrorMode.IMMEDIATE) {
                        Throwable ex = errors.get();
                        if (ex != null) {
                            current = null;
                            inner.cancel();
                            cancelAll();

                            errors.tryTerminateConsumer(downstream);
                            return;
                        }
                    }

                    boolean d = inner.isDone();
                    boolean empty = q.isEmpty();

                    if (d && empty) {
                        inner = null;
                        current = null;
                        upstream.request(1);
                        continueNextSource = true;
                    }
                }
            }
        }

        if (e != 0L && r != Long.MAX_VALUE) {
            requested.addAndGet(-e);
        }

        if (continueNextSource) {
            continue;
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
void remove(CacheDisposable<T> consumer) {
    for (;;) {
        CacheDisposable<T>[] current = observers.get();
        int n = current.length;
        if (n == 0) {
            return;
        }

        int j = -1;
        for (int i = 0; i < n; i++) {
            if (current[i] == consumer) {
                j = i;
                break;
            }
        }

        if (j < 0) {
            return;
        }

        CacheDisposable<T>[] next;

        if (n == 1) {
            next = EMPTY;
        } else {
            next = new CacheDisposable[n - 1];
            System.arraycopy(current, 0, next, 0, j);
            System.arraycopy(current, j + 1, next, j, n - j - 1);
        }

        if (observers.compareAndSet(current, next)) {
            return;
        }
    }
}

void drainFused() {
    int missed = 1;

    for (;;) {
        if (disposed) {
            return;
        }

        boolean d = done;
        Throwable ex = error;

        if (!delayError && d && ex != null) {
            disposed = true;
            downstream.onError(error);
            worker.dispose();
            return;
        }

        downstream.onNext(null);

        if (d) {
            disposed = true;
            ex = error;
            if (ex != null) {
                downstream.onError(ex);
            } else {
                downstream.onComplete();
            }
            worker.dispose();
            return;
        }

        missed = addAndGet(-missed);
        if (missed == 0) {
            break;
        }
    }
}

@SuppressWarnings("unchecked")
void remove(PublishSubscription<T> ps) {
    for (;;) {
        PublishSubscription<T>[] a = subscribers.get();
        if (a == TERMINATED || a == EMPTY) {
            return;
        }

        int n = a.length;
        int j = -1;

        for (int i = 0; i < n; i++) {
            if (a[i] == ps) {
                j = i;
                break;
            }
        }

        if (j < 0) {
            return;
        }

        PublishSubscription<T>[] b;

        if (n == 1) {
            b = EMPTY;
        } else {
            b = new PublishSubscription[n - 1];
            System.arraycopy(a, 0, b, 0, j);
            System.arraycopy(a, j + 1, b, j, n - j - 1);
        }

        if (subscribers.compareAndSet(a, b)) {
            return;
        }
    }
}
