            file.delete();
    }
}

private boolean verifyCertificateLicense(String licenseFile, LicenseContext context) {
    try {
        String encodedPayload = licenseFile;
        // Remove the header
        encodedPayload = encodedPayload.replace(CERT_PREFIX, "");
        // Remove the footer
        encodedPayload = encodedPayload.replace(CERT_SUFFIX, "");
        // Remove all newlines
        encodedPayload = encodedPayload.replaceAll("\\r?\\n", "");

        byte [] payloadBytes = Base64.getDecoder().decode(encodedPayload);
        String payload = new String(payloadBytes );
        log.info("Decoded certificate payload: {}", payload);
        String encryptedData = "";
        String encodedSignature = "";
        String algorithm = "";
        try {
            JSONObject attrs = new JSONObject(payload );
            encryptedData = (String) attrs.get("enc");
            encodedSignature = (String) attrs.get("sig");
            algorithm = (String) attrs.get("alg");
        } catch (JSONException e) {
            log.error("Failed to parse license file: {}", e.getMessage());
            return false;
        }

        // Verify license file algorithm

        if (!"base64+ed25519".equals(algorithm)) {
            log.error("Unsupported algorithm: {}. Only base64+ed25519 is supported.", algorithm);
            return false;
        }

        // Verify signature

        boolean isSignatureValid = verifyEd25519Signature(encryptedData, encodedSignature);

        if (!isSignatureValid) {
            log.error("License file signature is invalid");
            return false;
        }

        log.info("License file signature is valid");

        // Decode the base64 data
        String decodedData;
        try {
            decodedData = new String(Base64.getDecoder().decode(encryptedData));
        } catch (IllegalArgumentException e) {
            log.error("Failed to decode license data: {}", e.getMessage());
            return false;
        }

        // Process the certificate data

        boolean isValid = processCertificateData(decodedData, context);
        return isValid;
    } catch (Exception e) {
        log.error("Error verifying certificate license: {}", e.getMessage(), e);
        return false;
    }
}

private static void sanitizeHtmlFilesInZip(Path zipFilePath, boolean disableSanitize, TempFileManager tempFileManager) throws
    IOException {
    try (TempDirectory tempUnzippedDir = new TempDirectory(tempFileManager )) {
        try (ZipInputStream zipIn = ZipSecurity.createHardenedInputStream(new ByteArrayInputStream(Files.readAllBytes(zipFilePath)))) {
            ZipEntry entry = zipIn.getNextEntry();
            while(entry != null) {
                Path filePath = tempUnzippedDir.getPath().resolve(sanitizeZipFilename(entry.getName()));

                if (!entry.isDirectory()) {
                    Files.createDirectories(filePath.getParent());
                    if (entry.getName().toLowerCase().endsWith(".html") || entry.getName().toLowerCase().endsWith(".htm")) {
                        String content = new String(zipIn.readAllBytes(), StandardCharsets.UTF_8);
                        String sanitizedContent = sanitizeHtmlContent(content, disableSanitize);
                        Files.write(filePath, sanitizedContent.getBytes(StandardCharsets.UTF_8));
                    } else {
                        Files.copy(zipIn, filePath);
                    }
                }

                zipIn.closeEntry();
                entry = zipIn.getNextEntry();
            }
        }

        // Repack the sanitized files

        zipDirectory(tempUnzippedDir.getPath(), zipFilePath);
    } // tempUnzippedDir auto-cleaned
}

public static void addImageToDocument(PDDocument doc, PDImageXObject image, String fitOption, boolean autoRotate) throws
    IOException {
    boolean imageIsLandscape = image.getWidth() > image.getHeight();
    PDRectangle pageSize = PDRectangle.A4;

    if (autoRotate && imageIsLandscape) {
        pageSize = new PDRectangle(pageSize.getHeight(), pageSize.getWidth() );
    }

    if ("fitDocumentToImage".equals(fitOption)) {
        pageSize = new PDRectangle(image.getWidth(), image.getHeight() );
    }

    PDPage page = new PDPage(pageSize );
    doc.addPage(page);

    float pageWidth = page.getMediaBox().getWidth();
    float pageHeight = page.getMediaBox().getHeight();
    try (PDPageContentStream contentStream = new PDPageContentStream(doc, page, AppendMode.APPEND, true, true )) {
        if ("fillPage".equals(fitOption) || "fitDocumentToImage".equals(fitOption)) {
            contentStream.drawImage(image, 0, 0, pageWidth, pageHeight);
        } else if ("maintainAspectRatio".equals(fitOption)) {
                   float imageAspectRatio = (float) image.getWidth() / (float) image.getHeight();
                   float pageAspectRatio = pageWidth / pageHeight;
                   float scaleFactor = 1.0f;

                   if (imageAspectRatio > pageAspectRatio) {
                       scaleFactor = pageWidth / image.getWidth();
                   } else {
                       scaleFactor = pageHeight / image.getHeight();
                   }

                   float xPos = (pageWidth - (image.getWidth() * scaleFactor)) / 2;
                   float yPos = (pageHeight - (image.getHeight() * scaleFactor)) / 2;
                   contentStream.drawImage(image, xPos, yPos, image.getWidth() * scaleFactor, image.getHeight() * scaleFactor);
               }
    } catch (IOException e) {
        log.error("Error adding image to PDF", e);
        throw e;
    }
}

public static String generateMachineFingerprint() {
    try {
        // Get the MAC address
        StringBuilder sb = new StringBuilder();
        InetAddress ip = InetAddress.getLocalHost();
        NetworkInterface network = NetworkInterface.getByInetAddress(ip);

        if (network == null) {
            Enumeration <NetworkInterface > networks = NetworkInterface.getNetworkInterfaces();
            while(networks.hasMoreElements()) {
                NetworkInterface net = networks.nextElement();
                byte [] mac = net.getHardwareAddress();

                if (mac != null) {
                    for (int i = 0; i < mac.length; i++) {
                        sb.append(String.format("%02X", mac[i]));
                    }

                    break; // Use the first network interface with a MAC address
                }
            }
        } else {
            byte [] mac = network.getHardwareAddress();

            if (mac != null) {
                for (int i = 0; i < mac.length; i++) {
                    sb.append(String.format("%02X", mac[i]));
                }
            }
        }

        // Hash the MAC address for privacy and consistency

        MessageDigest md = MessageDigest.getInstance("SHA-256");
        byte [] hash = md.digest(sb.toString().getBytes(StandardCharsets.UTF_8));
        StringBuilder fingerprint = new StringBuilder();
        for (byte b : hash) {
            fingerprint.append(String.format("%02x", b));
        }

        return fingerprint.toString();
    } catch (Exception e) {
        return "GenericID";
    }
}

private void setCommonMetadata(PDDocument pdf, PdfMetadata pdfMetadata) {
    String title = pdfMetadata.getTitle();
    pdf.getDocumentInformation().setTitle(title);
    pdf.getDocumentInformation().setProducer(stirlingPDFLabel);
    pdf.getDocumentInformation().setSubject(pdfMetadata.getSubject());
    pdf.getDocumentInformation().setKeywords(pdfMetadata.getKeywords());
    pdf.getDocumentInformation().setModificationDate(Calendar.getInstance());
    String author = pdfMetadata.getAuthor();

    if (applicationProperties.getPremium().getProFeatures().getCustomMetadata().isAutoUpdateMetadata() && runningProOrHigher) {
        author = applicationProperties.getPremium().getProFeatures().getCustomMetadata().getAuthor();
        if (userService != null) {
            author = author.replace("username", userService.getCurrentUsername());
        }
    }

    pdf.getDocumentInformation().setAuthor(author);
}

@ Bean(name = "machineType" ) public String determineMachineType() {
    try {
        boolean isDocker = runningInDocker();
        boolean isKubernetes = System.getenv("KUBERNETES_SERVICE_HOST") != null;
        boolean isBrowserOpen = "true".equalsIgnoreCase(env.getProperty("BROWSER_OPEN"));

        if (isKubernetes) {
            return "Kubernetes";
        } else if (isDocker) {
                   return "Docker";
        } else if (isBrowserOpen) {
                   String os = System.getProperty("os.name").toLowerCase(Locale.ROOT);

                   if (os.contains("win")) {
                       return "Client-windows";
                   } else if (os.contains("mac")) {
                              return "Client-mac";
                   } else {
                              return "Client-unix";
                   }
               } else {
                   return "Server-jar";
               }
    } catch (Exception e) {
        return "Unknown";
    }
}

private void processRequest(int limitPerDay, String identifier, Map<String, Bucket> buckets, HttpServletRequest request, HttpServletResponse response, FilterChain filterChain) throws IOException, ServletException {
    Bucket userBucket = buckets.computeIfAbsent(identifier, k->
                            createUserBucket(limitPerDay));
    ConsumptionProbe probe = userBucket.tryConsumeAndReturnRemaining(1);

    if (probe.isConsumed()) {
        response.setHeader("X-Rate-Limit-Remaining", stripNewlines(Newlines.stripAll(Long.toString(probe.getRemainingTokens()))));
        filterChain.doFilter(request, response);
    } else {
        long waitForRefill = probe.getNanosToWaitForRefill() / 1_000_000_000;
        response.setStatus(HttpStatus.TOO_MANY_REQUESTS.value());
        response.setHeader("X-Rate-Limit-Retry-After-Seconds", Newlines.stripAll(String.valueOf(waitForRefill)));
        response.getWriter().write("Rate limit exceeded for POST requests.");
    }
}

@Override public void draw(CommandProcess process, JvmModel result) {
    TableElement table = new TableElement(2, 5).leftCellPadding(1)
                                               .rightCellPadding(1);
    for(Map.Entry<String, List<JvmItemVO>> entry :result.getJvmInfo().entrySet()) {
        String group = entry.getKey();
        List<JvmItemVO> items = entry.getValue();
        table.row(true, label(group).style(Decoration.bold.bold()));
        for(JvmItemVO item :items) {
            String valueStr;
            if (item.getValue() instanceof Map && item.getName().endsWith("MEMORY-USAGE")) {
                valueStr =
                    renderMemoryUsage((Map<String, Object>) item.getValue());
            } else {
                valueStr =
                    renderItemValue(item.getValue());
            }

            if (item.getDesc() != null) {
                table.row(item.getName() + "\n[" + item.getDesc() + "]", valueStr);
            } else {
                table.row(item.getName(), valueStr);
            }
        }

        table.row("", "");
    }

    process.write(RenderUtil.render(table, process.width()));
}

public static String renderEnhancerAffect(EnhancerAffectVO affectVO) {
    final StringBuilder infoSB = new StringBuilder();
    List<String> classDumpFiles = affectVO.getClassDumpFiles();
    if (classDumpFiles != null) {
        for(String classDumpFile :classDumpFiles) {
            infoSB.append("[dump: ").append(classDumpFile).append("]\n");
        }
    }


    List<String> methods = affectVO.getMethods();
    if (methods != null) {
        for(String method :methods) {
            infoSB.append("[Affect method: ").append(method).append("]\n");
        }
    }

    infoSB.append(format("Affect(class count: %d , method count: %d) cost in %s ms, listenerId: %d", affectVO.getClassCount(), affectVO.getMethodCount(), affectVO.getCost(), affectVO.getListenerId()));
    if (!StringUtils.isEmpty(affectVO.getOverLimitMsg())) {
        infoSB.append("\n" + affectVO.getOverLimitMsg());
    }

    if (affectVO.getThrowable() != null) {
        infoSB.append("\nEnhance error! exception: ").append(affectVO.getThrowable());
    }

    infoSB.append("\n");
    return infoSB.toString();
}

public List<ThreadVO> sample(Collection<ThreadVO> originThreads) {
    List<ThreadVO> threads = new ArrayList<ThreadVO>(originThreads);

    // Sample CPU
    if (lastCpuTimes.isEmpty()) {
        lastSampleTimeNanos =
            System.nanoTime();
        for(ThreadVO thread :threads) {
            if (thread.getId() > 0) {
                long cpu = threadMXBean.getThreadCpuTime(thread.getId());
                lastCpuTimes.put(thread, cpu);
                thread.setTime(cpu / 1000000);
            }
        }

        // add internal threads


        Map<String, Long> internalThreadCpuTimes = getInternalThreadCpuTimes();
        if (internalThreadCpuTimes != null) {
            for(Map.Entry<String, Long> entry :internalThreadCpuTimes.entrySet()) {
                String key = entry.getKey();
                ThreadVO thread = createThreadVO(key);
                thread.setTime(entry.getValue() / 1000000);
                threads.add(thread);
                lastCpuTimes.put(thread, entry.getValue());
            }
        }

        //sort by time

        Collections.sort(threads, new Comparator<ThreadVO>() {
                                      @Override public int compare(ThreadVO o1, ThreadVO o2) {
                                          long l1 = o1.getTime();
                                          long l2 = o2.getTime();
                                          if (l1< l2) {
                                              return 1;
                                          } else if (l1 > l2) {
                                                     return -1;
                                          } else {
                                                     return 0;
                                          }
                                      }

                                  });
        return threads;
    }

    // Resample


    long newSampleTimeNanos = System.nanoTime();
    Map<ThreadVO, Long> newCpuTimes = new HashMap<ThreadVO, Long>(threads.size());
    for(ThreadVO thread :threads) {
        if (thread.getId() > 0) {
            long cpu = threadMXBean.getThreadCpuTime(thread.getId());
            newCpuTimes.put(thread, cpu);
        }
    }
    // internal threads


    Map<String, Long> newInternalThreadCpuTimes = getInternalThreadCpuTimes();
    if (newInternalThreadCpuTimes != null) {
        for(Map.Entry<String, Long> entry :newInternalThreadCpuTimes.entrySet()) {
            ThreadVO threadVO = createThreadVO(entry.getKey());
            threads.add(threadVO);
            newCpuTimes.put(threadVO, entry.getValue());
        }
    }

    // Compute delta time


    final Map<ThreadVO, Long> deltas = new HashMap<ThreadVO, Long>(threads.size());
    for(ThreadVO thread :newCpuTimes.keySet()) {
        Long t = lastCpuTimes.get(thread);
        if (t == null) {
            t =
                0L;
        }


        long time1 = t;
        long time2 = newCpuTimes.get(thread);
        if (time1 == -1) {
            time1 =
                time2;
        } else if (time2 == -1) {
                   time2 =
                       time1;
               }


        long delta = time2 - time1;
        deltas.put(thread, delta);
    }


    long sampleIntervalNanos = newSampleTimeNanos - lastSampleTimeNanos;

    // Compute cpu usage
    final HashMap<ThreadVO, Double> cpuUsages = new HashMap<ThreadVO, Double>(threads.size());
    for(ThreadVO thread :threads) {
        double cpu = sampleIntervalNanos == 0?0:
            (Math.rint(deltas.get(thread)* 10000.0/ sampleIntervalNanos) / 100.0);
        cpuUsages.put(thread, cpu);
    }

    // Sort by CPU time : should be a rendering hint...

    Collections.sort(threads, new Comparator<ThreadVO>() {
                                  @Override public int compare(ThreadVO o1, ThreadVO o2) {
                                      long l1 = deltas.get(o1);
                                      long l2 = deltas.get(o2);
                                      if (l1< l2) {
                                          return 1;
                                      } else if (l1 > l2) {
                                                 return -1;
                                      } else {
                                                 return 0;
                                      }
                                  }

                              });
    for(ThreadVO thread :threads) {
        //nanos to mills
        long timeMills = newCpuTimes.get(thread) / 1000000;
        long deltaTime = deltas.get(thread) / 1000000;
        double cpu = cpuUsages.get(thread);
        thread.setCpu(cpu);
        thread.setTime(timeMills);
        thread.setDeltaTime(deltaTime);
    }

    lastCpuTimes =
        newCpuTimes;
    lastSampleTimeNanos =
        newSampleTimeNanos;
    return threads;
}

private ApiResponse processAsyncExecRequest(ApiRequest apiRequest, Session session) {
    String commandLine = apiRequest.getCommand();
    Map<String, Object> body = new TreeMap<String, Object>();
    body.put("command", commandLine);
    ApiResponse response = new ApiResponse();
    response.setSessionId(session.getSessionId()).setBody(body);
    if (!session.tryLock()) {
        response.setState(ApiState.REFUSED).setMessage("Another command is executing.");
        return response;
    }


    int lock = session.getLock();
    try {
        Job foregroundJob = session.getForegroundJob();
        if (foregroundJob != null) {
            response.setState(ApiState.REFUSED).setMessage("Another job is running.");
            logger.info("Another job is running, jobId: {}", foregroundJob.id());
            return response;
        }

        //create job


        Job job = this.createJob(commandLine, session, session.getResultDistributor());
        body.put("jobId", job.id());
        body.put("jobStatus", job.status());
        response.setState(ApiState.SCHEDULED);

        //add command before exec job
        CommandRequestModel commandRequestModel = new CommandRequestModel(commandLine, response.getState());
        commandRequestModel.setJobId(job.id());
        SharingResultDistributor resultDistributor = session.getResultDistributor();
        if (resultDistributor != null) {
            resultDistributor.appendResult(commandRequestModel);
        }

        session.setForegroundJob(job);
        updateSessionInputStatus(session, InputStatus.ALLOW_INTERRUPT);

        //run job
        job.run();
        return response;
    } catch (Throwable e) {
        logger.error("Async exec command failed:" + e.getMessage() + ", command:" + commandLine, e);
        response.setState(ApiState.FAILED).setMessage("Async exec command failed:" + e.getMessage());
        CommandRequestModel commandRequestModel = new CommandRequestModel(commandLine, response.getState(), response.getMessage());
        session.getResultDistributor().appendResult(commandRequestModel);
        return response;
    } finally {
              if (session.getLock() == lock) {
                  session.unLock();
              }
    }
}

private String renderLoggerInfo(Map<String, Map<String, Object>> loggerInfos, int width) {
    StringBuilder sb = new StringBuilder(8192);
    for(Map.Entry<String, Map<String, Object>> entry :loggerInfos.entrySet()) {
        Map<String, Object> info = entry.getValue();
        TableElement table = new TableElement(2, 10).leftCellPadding(1)
                                                    .rightCellPadding(1);
        TableElement appendersTable = new TableElement().rightCellPadding(1);
        Class<?> clazz = (Class<?>) info.get(LoggerHelper.clazz);
        table.row(label(LoggerHelper.name).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.name))).row(label(LoggerHelper.clazz).style(Decoration.bold.bold()), label("" + clazz.getName()))
            .row(label(LoggerHelper.classLoader).style(Decoration.bold.bold()),
                 label("" + info.get(LoggerHelper.classLoader)))
                     .row(label(LoggerHelper.classLoaderHash).style(Decoration.bold.bold()),
                          label("" + info.get(LoggerHelper.classLoaderHash))).row(label(LoggerHelper.level).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.level)));
        if (info.get(LoggerHelper.effectiveLevel) != null) {
            table.row(
                      label(LoggerHelper.effectiveLevel).style(Decoration.bold.bold()),
                      label("" + info.get(LoggerHelper.effectiveLevel)));
        }

        if (info.get(LoggerHelper.config) != null) {
            table.row(label(LoggerHelper.config).style(Decoration.bold.bold()), label("" + info.get(LoggerHelper.config)));
        }

        table.row(label(LoggerHelper.additivity).style(Decoration.bold.bold()),
                  label("" + info.get(LoggerHelper.additivity))).row(label(LoggerHelper.codeSource).style(Decoration.bold.bold()),
                                                                     label("" + info.get(LoggerHelper.codeSource)));


        @ SuppressWarnings("unchecked" ) List<Map<String, Object>> appenders = (List<Map<String, Object>>) info.get(LoggerHelper.appenders);
        if (appenders != null && !appenders.isEmpty()) {
            for(Map<String, Object> appenderInfo :appenders) {
                Class<?> appenderClass = (Class<?>) appenderInfo.get(LoggerHelper.clazz);
                appendersTable.row(label(LoggerHelper.name).style(Decoration.bold.bold()), label("" + appenderInfo.get(LoggerHelper.name)));
                appendersTable.row(label(LoggerHelper.clazz), label("" + appenderClass.getName()));
                appendersTable.row(label(LoggerHelper.classLoader), label("" + info.get(LoggerHelper.classLoader)));
                appendersTable.row(label(LoggerHelper.classLoaderHash), label("" + info.get(LoggerHelper.classLoaderHash)));
                if (appenderInfo.get(LoggerHelper.file) != null) {
                    appendersTable.row(label(LoggerHelper.file), label("" + appenderInfo.get(LoggerHelper.file)));
                }

                if (appenderInfo.get(LoggerHelper.target) != null) {
