        final EqualObserver<T> observer1 = as[0];
        final SpscLinkedArrayQueue<T> q1 = observer1.queue;
        final EqualObserver<T> observer2 = as[1];
        final SpscLinkedArrayQueue<T> q2 = observer2.queue;
        for(;;
