        JoinInnerSubscriber<T>[] s = this.subscribers;
        int n = s.length;
        Subscriber<? super T> a = this.downstream;
        for(;;
) {
            long r = requested.get();
            long e = 0;
            middle: while (e != r) {
                        if (cancelled) {
                            cleanup();
                            return;
                        }

                        Throwable ex = errors.get();
                        if (ex != null) {
                            cleanup();
                            a.onError(ex);
                            return;
                        }

                        boolean d = done.get() == 0;
                        boolean empty = true;
                        for(int i = 0;
i < s.length;
i++) {
                            JoinInnerSubscriber<T> inner = s[ i];
                            SimplePlainQueue<T> q = inner.queue;
                            if (q != null) {
                                T v = q.poll();
                                if (v != null) {
                                    empty = false;
                                    a.onNext(v);
                                    inner.requestOne();
                                    if (++ e == r) {
                                        break middle;
                                    }
                                }
                            }
                        }

                        if (d && empty) {
                            a.onComplete();
                            return;
                        }

                        if (empty) {
                            break;
                        }
                    }

            if (e == r) {
                if (cancelled) {
                    cleanup();
                    return;
                }

                Throwable ex = errors.get();
                if (ex != null) {
                    cleanup();
                    a.onError(ex);
                    return;
                }

                boolean d = done.get() == 0;
                boolean empty = true;
                for(int i = 0;
i < n;
i++) {
                    JoinInnerSubscriber<T> inner = s[ i];
                    SimpleQueue<T> q = inner.queue;
                    if (q != null && !q.isEmpty()) {
                        empty = false;
                        break;
                    }
                }

                if (d && empty) {
                    a.onComplete();
                    return;
                }
            }

            if (e != 0) {
                BackpressureHelper.produced(requested, e);
            }

            missed = addAndGet(-missed);
            if (missed == 0) {
                break;
            }
        }
    }

    @Override
    public void drain() {
        if (getAndIncrement() != 0) {
            return;
        }

        int missed = 1;
        InnerQueuedSubscriber<R> inner = current;
        Subscriber<? super R> a = downstream;
        ErrorMode em = errorMode;
        for(;;
) {
            long r = requested.get();
            long e = 0L;
            if (inner == null) {
                if (em != ErrorMode.END) {
                    Throwable ex = errors.get();
                    if (ex != null) {
                        cancelAll();
                        errors.tryTerminateConsumer(downstream);
                        return;
                    }
                }

                boolean outerDone = done;
                inner = subscribers.poll();
                if (outerDone && inner == null) {
                    errors.tryTerminateConsumer(downstream);
                    return;
                }

                if (inner != null) {
                    current = inner;
                }
            }

            boolean continueNextSource = false;
            if (inner != null) {
                SimpleQueue<R> q = inner.queue();
                if (q != null) {
                    while (e != r) {
                        if (cancelled) {
                            cancelAll();
                            return;
                        }

                        if (em == ErrorMode.IMMEDIATE) {
                            Throwable ex = errors.get();
                            if (ex != null) {
                                current = null;
                                inner.cancel();
                                cancelAll();
                                errors.tryTerminateConsumer(downstream);
                                return;
                            }
                        }

                        boolean d = inner.isDone();
                        R v;
                        try {
                            v = q.poll();
                        } catch (Throwable ex) {
                            Exceptions.throwIfFatal(ex);
                            current = null;
                            inner.cancel();
                            cancelAll();
                            a.onError(ex);
                            return;
                        }

                        boolean empty = v == null;
                        if (d && empty) {
                            inner = null;
                            current = null;
                            upstream.request(1);
                            continueNextSource = true;
                            break;
                        }

                        if (empty) {
                            break;
                        }

                        a.onNext(v);
                        e++;
                        inner.request(1L);
                    }

                    if (e == r) {
                        if (cancelled) {
                            cancelAll();
                            return;
                        }

                        if (em == ErrorMode.IMMEDIATE) {
                            Throwable ex = errors.get();
                            if (ex != null) {
                                current = null;
                                inner.cancel();
                                cancelAll();
                                errors.tryTerminateConsumer(downstream);
                                return;
                            }
                        }

                        boolean d = inner.isDone();
                        boolean empty = q.isEmpty();
                        if (d && empty) {
                            inner = null;
                            current = null;
                            upstream.request(1);
                            continueNextSource = true;
                        }
                    }
                }
            }

            if (e != 0L && r != Long.MAX_VALUE) {
                requested.addAndGet(-e);
            }

            if (continueNextSource) {
                continue;
            }

            missed = addAndGet(-missed);
            if (missed == 0) {
                break;
            }
        }
    }

    @ SuppressWarnings("unchecked" ) void remove(CacheDisposable<T> consumer) {
        for(;;
) {
            CacheDisposable<T>[] current = observers.get();
            int n = current.length;
            if (n == 0) {
                return;
            }

            int j = -1;
            for(int i = 0;
i < n;
i++) {
                if (current[ i] == consumer) {
                    j = i;
                    break;
                }
            }

            if (j < 0) {
                return;
            }

            CacheDisposable<T>[] next;
            if (n == 1) {
                next = EMPTY;
            } else {
                next = new CacheDisposable[n - 1];
                System.arraycopy(current, 0, next, 0, j);
                System.arraycopy(current, j + 1, next, j, n - j - 1);
            }

            if (observers.compareAndSet(current, next)) {
                return;
            }
        }
    }

    void drainFused() {
        int missed = 1;
        for(;;
) {
