                return;
            }

            boolean d = done;
            Throwable ex = error;
            if (!delayError && d
                    && ex != null) {
                disposed = true;
                downstream.onError(error);
                worker.dispose();
                return;
            }

            downstream.onNext(null);
            if (d) {
                disposed = true;
                ex = error;
                if (ex != null) {
                    downstream.onError(ex);
                } else {
                    downstream.onComplete();
                }

                worker.dispose();
                return;
            }

            missed = addAndGet(-missed);
            if (missed == 0) {
                break;
            }
        }
    }

