@ Bean(name = "machineType")
public String determineMachineType() {
    try {
        boolean isDocker = runningInDocker();
        boolean isKubernetes = System.getenv("KUBERNETES_SERVICE_HOST") != null;
        boolean isBrowserOpen = "true".equalsIgnoreCase(env.getProperty("BROWSER_OPEN"));
        if (isKubernetes) {
            return "Kubernetes";
        } else if (isDocker) {
                   return "Docker";
        } else if (isBrowserOpen) {
                   String os = System.getProperty("os.name").toLowerCase(Locale.ROOT);
                   if (os.contains("win")) {
                       return "Client-windows";
                   } else if (os.contains("mac")) {
                              return "Client-mac";
                   } else {
                       return "Client-unix";
                   }
               } else {
                   return "Server-jar";
               }
    } catch (Exception e) {
        return "Unknown";
    }
}

private void processRequest(int limitPerDay, String identifier, Map<String, Bucket> buckets, HttpServletRequest request, HttpServletResponse response, FilterChain filterChain)throws IOException, ServletException {
    Bucket userBucket = buckets.computeIfAbsent(identifier, k->createUserBucket(limitPerDay));
    ConsumptionProbe probe = userBucket.tryConsumeAndReturnRemaining(1);
    if (probe.isConsumed()) {
        response.setHeader("X-Rate-Limit-Remaining", stripNewlines(Newlines.stripAll(Long.toString(probe.getRemainingTokens()))));
        filterChain.doFilter(request, response);
    } else {
        long waitForRefill = probe.getNanosToWaitForRefill() / 1_000_000_000;
        response.setStatus(HttpStatus.TOO_MANY_REQUESTS.value());
        response.setHeader("X-Rate-Limit-Retry-After-Seconds", Newlines.stripAll(String.valueOf(waitForRefill)));
        response.getWriter().write("Rate limit exceeded for POST requests.");
    }
}

    private void doPostprocessing() {
        errCode = ERROR_NOTHING;
        errObject = null;
        Thread thread = Thread.currentThread();
        notifyPostProcessing(1);
        if (DEBUG) {
            thread.setName("[" + TAG + "]  ps = " + psAlgorithm + "  filename = " + storage.getName());
        }
        Exception exception = null;
        try {
            psAlgorithm.run(this);
        }

            catch(Exception err ) {
            Log.e(TAG, "Post-processing failed. " + psAlgorithm.toString(), err);
            if (err instanceof InterruptedIOException || err instanceof ClosedByInterruptException || thread.isInterrupted()) {
                notifyError(DownloadMission.ERROR_POSTPROCESSING_STOPPED, null);
                return;
            }

            if (errCode == ERROR_NOTHING) errCode = ERROR_POSTPROCESSING;
            exception = err;
        }

finally {
            notifyPostProcessing(errCode == ERROR_NOTHING?2:
                                                         0);
        }

        if (errCode != ERROR_NOTHING) {
            if (exception == null) exception = errObject;
            notifyError(ERROR_POSTPROCESSING, exception);
            return;
        }
        notifyFinished();
    }

void establishConnection(int threadId, HttpURLConnection conn) throws IOException, HttpError {
    int statusCode = conn.getResponseCode();
    if (DEBUG) {
        Log.d(TAG, threadId + ":[request]  Range=" + conn.getRequestProperty("Range"));
        Log.d(TAG, threadId + ":[response] Code=" + statusCode);
        Log.d(TAG, threadId + ":[response] Content-Length=" + conn.getContentLength());
        Log.d(TAG, threadId + ":[response] Content-Range=" + conn.getHeaderField("Content-Range"));
    }
    switch (statusCode) {
        case 204:
        case 205:
        case 207:
            throw new HttpError(statusCode);
        case 416:
            return;// let the download thread handle this error
        default:
            if (statusCode < 200
                    || statusCode > 299) {
                throw new HttpError(statusCode);
            }
    }
}

private Elst parseEdts(final Box ref) throws IOException {
    final Box b = untilBox(ref, ATOM_ELST);
    if (b == null) {
        return null;
    }

    final Elst obj = new Elst();
    final boolean v1 = stream.read() == 1;
    stream.skipBytes(3); // flags
    final int entryCount = stream.readInt();
    if (entryCount < 1) {
        obj.bMediaRate = 0x00010000; // default media rate (1.0)
        return obj;
    }

    if (v1) {
        stream.skipBytes(DataReader.LONG_SIZE); // segment duration
        obj.mediaTime = stream.readLong();
        // ignore all remain entries
        stream.skipBytes((entryCount - 1) *(DataReader.LONG_SIZE* 2));
    } else {
        stream.skipBytes(DataReader.INTEGER_SIZE); // segment duration
        obj.mediaTime = stream.readInt();
    }
    obj.bMediaRate = stream.readInt();
    return obj;
}

@POST
@Produces({MediaType.APPLICATION_JSON, "application/javascript", MediaType.APPLICATION_XML})
public Response createSession(@QueryParam("op")
                    String op, @DefaultValue("5") @QueryParam("expire")
                    String expire,
                              @Context UriInfo ui) {
    if (!op.equals("create")) {
        throw new WebApplicationException(Response.status(Response.Status.BAD_REQUEST) .entity(new ZError(ui.getRequestUri() .toString(), "") ) .build());
    }

    int expireInSeconds;

    try {
        expireInSeconds = Integer.parseInt(expire);
    } catch (NumberFormatException e) {
        throw new WebApplicationException(Response.status(Response.Status.BAD_REQUEST) .build());
    }

    String uuid = UUID.randomUUID() .toString();

    while (ZooKeeperService.isConnected(contextPath, uuid)) {
        uuid = UUID.randomUUID() .toString();
    }

    // establish the connection to the ZooKeeper cluster

    try {
        ZooKeeperService.getClient(contextPath, uuid, expireInSeconds);
    } catch (IOException e) {
        LOG.error("Failed while trying to create a new session", e);
        throw new WebApplicationException(Response.status(Response.Status.INTERNAL_SERVER_ERROR) .build());
    }

    URI uri = ui.getAbsolutePathBuilder() .path(uuid) .build();
    return Response.created(uri) .entity(new JSONWithPadding(new ZSession(uuid, uri.toString())) ) .build();
}

public void schedule(WorkRequest workRequest,
                     long id) {
    if (stopped) {
        workRequest.cleanup();
        return;
    }

    ScheduledWorkRequest scheduledWorkRequest = new ScheduledWorkRequest(workRequest);

    // If we have a worker thread pool, use that; otherwise, do the work
    // directly.
    int size = workers.size();

    if (size > 0) {
        try {
            // make sure to map negative ids as well to [0, size-1]
            int workerNum = ((int) (id%size) + size)%size;
            ExecutorService worker = workers.get(workerNum);
            worker.execute(scheduledWorkRequest);
        } catch (RejectedExecutionException e) {
            LOG.warn("ExecutorService rejected execution", e);
            workRequest.cleanup();
        }
    } else {
        // When there is no worker thread pool, do the work directly
        // and wait for its completion
        scheduledWorkRequest.run();
    }
}

@Override
public void close() throws IOException {
    boolean triedToClose = false, success = false;

    try {
        flush();
        ((FileOutputStream) out) .getFD().sync();
        triedToClose = true;
        super.close();
        success = true;
    } finally {
              if (success) {
                  boolean renamed = tmpFile.renameTo(origFile);

                  if (!renamed) {
                // On windows, renameTo does not replace.
                      if (!origFile.delete()||!tmpFile.renameTo(origFile)) {
                          throw new IOException("Could not rename temporary file " + tmpFile + " to " + origFile);
                      }
                  }
              } else {
                  if (!triedToClose) {
                // If we failed when flushing, try to close it to not leak
                // an FD
                      IOUtils.closeStream(out);
                  }
            // close wasn't successful, try to delete the tmp file

                  if (!tmpFile.delete()) {
                      LOG.warn("Unable to delete tmp file {}", tmpFile);
                  }
              }
    }
}

synchronized private
LogEntry readNextEntry() {
    try {
        try {
            while (true) {
                String line = in.readLine();

                if (line == null) {
                    break;
                }

                Matcher m = src.timep.matcher(line);

                if (m.lookingAt()) {
                    if (buf.length()> 0) {
                        LogEntry e = new Log4JEntry(src.timestampFromText(dateformat, buf), src.getServerId(), buf);
                        buf = line;
                        return e;
                    }

                    buf = line;
                } else if (buf.length()> 0) {
                           buf += line + "\n";
                       }
            }
        } catch (EOFException eof) {
        // ignore, we've simply come to the end of the file
        }

        if (buf.length()> 0) {
            LogEntry e = new Log4JEntry(src.timestampFromText(dateformat, buf), src.getServerId(), buf);
            buf = "";
            return e;
        }
    } catch (Exception e) {
        LOG.error("Error reading next entry in file (" + src.file + "): " + e);
        return null;
    }

    return null;
}

