  }

  boolean continueNextSource = false;

  if (inner != null) {
   SimpleQueue<R> q = inner.queue();
   if (q != null) {
    while (e != r) {
     if (cancelled) {
      cancelAll();
      return;
     }
     if (em == ErrorMode.IMMEDIATE) {
      Throwable ex = errors.get();
      if (ex != null) {
       current = null;
       inner.cancel();
       cancelAll();
       errors.tryTerminateConsumer(downstream);
       return;
      }
     }

     boolean d = inner.isDone();

     R v;
     try {
      v = q.poll();
     } catch (Throwable ex) {
      Exceptions.throwIfFatal(ex);
      current = null;
      inner.cancel();
      cancelAll();
      a.onError(ex);
      return;
     }
     boolean empty = v == null;

     if (d && empty) {
      inner = null;
      current = null;
      upstream.request(1);
      continueNextSource = true;
      break;
     }
     if (empty) {
      break;
     }
     a.onNext(v);
     e++;
     inner.request(1L);
    }
    if (e == r) {
     if (cancelled) {
      cancelAll();
      return;
     }
     if (em == ErrorMode.IMMEDIATE) {
      Throwable ex = errors.get();
      if (ex != null) {
       current = null;
       inner.cancel();
       cancelAll();
       errors.tryTerminateConsumer(downstream);
       return;
      }
     }

     boolean d = inner.isDone();

     boolean empty = q.isEmpty();
     if (d && empty) {
      inner = null;
      current = null;
      upstream.request(1);
      continueNextSource = true;
     }
    }
   }
  }

  if (e != 0L && r != Long.MAX_VALUE) {
   requested.addAndGet(-e);
  }

  if (continueNextSource) {
   continue;
  }
  missed = addAndGet(-missed);
  if (missed == 0) {
   break;
  }
 }
}

@SuppressWarnings("unchecked") void remove(CacheDisposable<T> consumer) {
 for (;;) {
  CacheDisposable<T>[] current = observers.get();
  int n = current.length;
  if (n == 0) {
   return;
  }
  int j = -1;
  for (int i = 0; i < n; i++) {
   if (current[i] == consumer) {
    j = i;
    break;
   }
  }
  if (j < 0) {
   return;
  }
  CacheDisposable<T>[] next;

  if (n == 1) {
   next = EMPTY;
  } else {
   next = new CacheDisposable[n - 1];
   System.arraycopy(current, 0, next, 0, j);
   System.arraycopy(current, j + 1, next, j, n - j - 1);
  }

  if (observers.compareAndSet(current, next)) {
   return;
  }
 }
}

void drainFused() {
 int missed = 1;
 for (;;) {
  if (disposed) {
   return;
  }
  boolean d = done;
  Throwable ex = error;
  if (!delayError && d && ex != null) {
   disposed = true;
   downstream.onError(error);
   worker.dispose();
   return;
  }
  downstream.onNext(null);

  if (d) {
   disposed = true;
   ex = error;
   if (ex != null) {
    downstream.onError(ex);
   } else {
    downstream.onComplete();
   }
   worker.dispose();
   return;
  }

  missed = addAndGet(-missed);

  if (missed == 0) {
   break;
  }
 }
}

@SuppressWarnings("unchecked") void remove(PublishSubscription<T> ps) {
 for (;;) {
  PublishSubscription<T>[] a = subscribers.get();
  if (a == TERMINATED || a == EMPTY) {
   return;
  }
  int n = a.length;
  int j = -1;
  for (int i = 0; i < n; i++) {
   if (a[i] == ps) {
    j = i;
    break;
   }
  }
  if (j < 0) {
   return;
  }
  PublishSubscription<T>[] b;

  if (n == 1) {
   b = EMPTY;
  } else {
   b = new PublishSubscription[n - 1];
   System.arraycopy(a, 0, b, 0, j);
   System.arraycopy(a, j + 1, b, j, n - j - 1);
  }

  if (subscribers.compareAndSet(a, b)) {
   return;
  }
 }
}

public static Map<String, String> toStringMap(Map<String, Object> input, boolean stringEscape) {
    Map<String, String> output = new HashMap<>(input.size());
    for (Map.Entry<String, Object> entry : input.entrySet()) {
        String key = entry.getKey();
        Object obj = entry.getValue();
        if (key == null || obj == null) {
            throw new NullPointerException("A null argument cannot be sent to Redis.");
        }
        String str;
        if (!(obj instanceof byte[])) {
            if (obj instanceof redis.clients.jedis.GeoCoordinate) {
                redis.clients.jedis.GeoCoordinate geo = (redis.clients.jedis.GeoCoordinate) obj;
                str = geo.getLongitude() + "," + geo.getLatitude();

            } else {
                if (obj instanceof String) {
                    if (stringEscape) { 
                        str = escape((String) obj); 
                    } else { 
                        str = (String) obj; 
                    } 
                } else {
                    str = String.valueOf(obj);
                }
            }
        }else {
            str = SafeEncoder.encode((byte[]) obj);
        } 
        output.put(key, str);
    }
    return output;
}

