@Override
public void onTextTracksChanged(@NonNull final Tracks currentTracks) {
 super.onTextTracksChanged(currentTracks);
 final boolean trackTypeTextSupported = !currentTracks.containsType(C.TRACK_TYPE_TEXT) || currentTracks.isTypeSupported(C.TRACK_TYPE_TEXT, false);

 if (getPlayer().getTrackSelector().getCurrentMappedTrackInfo() == null
 || !trackTypeTextSupported) {
  binding.captionTextView.setVisibility(View.GONE);
  return;
 }

 // Extract all loaded languages
 final List<Tracks.Group> textTracks = currentTracks.getGroups().stream().filter(trackGroupInfo -> C.TRACK_TYPE_TEXT == trackGroupInfo.getType())
 .collect(Collectors.toList());

 final List<String> availableLanguages = textTracks.stream().map(Tracks.Group::getMediaTrackGroup).filter(textTrack -> textTrack.length > 0)
 .map(textTrack -> textTrack.getFormat(0).language)
 .collect(Collectors.toList());

 // Find selected text track
 final Optional<Format> selectedTracks = textTracks.stream().filter(Tracks.Group::isSelected).filter(info -> info.getMediaTrackGroup().length >= 1)
 .map(info -> info.getMediaTrackGroup().getFormat(0))
 .findFirst();

 // Build UI
 buildCaptionMenu(availableLanguages);

 if (player.getTrackSelector().getParameters().getRendererDisabled(player.getCaptionRendererIndex()) 
 || selectedTracks.isEmpty()) {
  binding.captionTextView.setText(R.string.caption_none);
 } else {
  binding.captionTextView.setText(selectedTracks.get().language);
 }

 binding.captionTextView.setVisibility(availableLanguages.isEmpty() ? View.GONE : View.VISIBLE);
}

public static RequestCreator loadScaledDownThumbnail(final Context context,
@NonNull final List<Image> images) {
 // scale down the notification thumbnail for performance
 return PicassoHelper.loadThumbnail(images).transform(new Transformation() {
  @Override
  public Bitmap transform(final Bitmap source) {
   if (DEBUG) {
    Log.d(TAG, "Thumbnail - transform() called");
   }
   final float notificationThumbnailWidth = Math.min(context.getResources().getDimension(R.dimen.player_notification_thumbnail_width), source.getWidth());

   final Bitmap result = BitmapCompat.createScaledBitmap(source, (int) notificationThumbnailWidth, (int) (source.getHeight() / (source.getWidth() / notificationThumbnailWidth)), null, true);

   if (!(result == source || !result.isMutable())) {
    source.recycle();
    return result;
   } else {
    // create a new mutable bitmap to prevent strange crashes on some
    // devices (see #4638)
    final Bitmap copied = BitmapCompat.createScaledBitmap(source, (int) notificationThumbnailWidth - 1, (int) (source.getHeight() / (source.getWidth() / (notificationThumbnailWidth - 1))), null, true);
    source.recycle();

    return copied;
   } 
  }

  @Override
  public String key() {
   return PLAYER_THUMBNAIL_TRANSFORMATION_KEY;
  }
 });
}

void drain() {
     if (getAndIncrement() != 0) {
          return;
     }
     int missed = 1;

     Observer<? super R> downstream = this.downstream;

     AtomicThrowable errors = this.errors;

     AtomicReference<SwitchMapMaybeObserver<R>> inner = this.inner;

     while (true) {
          while (true) {
               if (cancelled) {
                    return;
               }

               if (errors.get() != null && !delayErrors) {
                    errors.tryTerminateConsumer(downstream);
                    return;
               }

               boolean d = done;

               SwitchMapMaybeObserver<R> current = inner.get();

               boolean empty = current == null;

               if (d && empty) {
                    errors.tryTerminateConsumer(downstream);
                    return;
               }

               if (empty || current.item == null) {
                    break;
               }

               inner.compareAndSet(current, null);
               downstream.onNext(current.item);
          }
          missed = addAndGet(-missed);

          if (missed == 0) {
               break;
          }
     }
}

@Override
public void onNext(T t) 
{
     if (done) {
          return;
     }
     boolean b;

     try {
          b = predicate.test(t);
     } catch (Throwable e) {
          Exceptions.throwIfFatal(e);
          upstream.cancel();
          upstream = SubscriptionHelper.CANCELLED;
          onError(e);
          return;
     }

     if (!b) {
          done = true;
          upstream.cancel();
          upstream = SubscriptionHelper.CANCELLED;
          downstream.onSuccess(false);
     }
}

