void drainLoop() {
     int missed = 1;

     JoinInnerSubscriber<T>[] s = this.subscribers;
     int n = s.length;

     Subscriber<? super T> a = this.downstream;

     while (true) {
          long r = requested.get();
          long e = 0;

          middle:
          while (e != r) {
               if (cancelled) {
                    cleanup();
                    return;
               }
               Throwable ex = errors.get();
               if (ex != null) {
                    cleanup();
                    a.onError(ex);
                    return;
               }
               boolean d = done.get() == 0;
               boolean empty = true;

               for (int i = 0; i < s.length; i++) {
                    JoinInnerSubscriber<T> inner = s[i];
                    SimplePlainQueue<T> q = inner.queue;

                    if (q != null) {
                         T v = q.poll();

                         if (v != null) {
                              empty = false;
                              a.onNext(v);
                              inner.requestOne();
                              if (++e == r) {
                                   break middle;
                              }
                         }
                    }
               }

               if (d && empty) {
                    a.onComplete();
                    return;
               }

               if (empty) {
                    break;
               }
          }

          if (e == r) {
               if (cancelled) {
                    cleanup();
                    return;
               }
               Throwable ex = errors.get();
               if (ex != null) {
                    cleanup();
                    a.onError(ex);
                    return;
               }
               boolean d = done.get() == 0;
               boolean empty = true;

               for (int i = 0; i < n; i++) {
                    JoinInnerSubscriber<T> inner = s[i];
                    SimpleQueue<T> q = inner.queue;

                    if (q != null && !q.isEmpty()) {
                         empty = false;
                         break;
                    }
               }

               if (d && empty) {
                    a.onComplete();
                    return;
               }
          }

          if (e != 0) {
               BackpressureHelper.produced(requested, e);
          }

          missed = addAndGet(-missed);
          if (missed == 0) {
               break;
          }
     }
}

@Override
public void drain() {
     if (getAndIncrement() != 0) {
          return;
     }
     int missed = 1;

     InnerQueuedSubscriber<R> inner = current;

     Subscriber<? super R> a = downstream;

     ErrorMode em = errorMode;

     while (true) {
          long r = requested.get();
          long e = 0L;

          if (inner == null) {
               if (em != ErrorMode.END) {
                    Throwable ex = errors.get();

                    if (ex != null) {
                         cancelAll();
                         errors.tryTerminateConsumer(downstream);
                         return;
                    }
               }
               boolean outerDone = done;

               inner = subscribers.poll();

               if (outerDone && inner == null) {
                    errors.tryTerminateConsumer(downstream);
                    return;
               }

               if (inner != null) {
                    current = inner;
               }
          }

          boolean continueNextSource = false;

          if (inner != null) {
               SimpleQueue<R> q = inner.queue();
               if (q != null) {
                    while (e != r) {
                         if (cancelled) {
                              cancelAll();
                              return;
                         }
                         if (em == ErrorMode.IMMEDIATE) {
                              Throwable ex = errors.get();

                              if (ex != null) {
                                   current = null;
                                   inner.cancel();
                                   cancelAll();
                                   errors.tryTerminateConsumer(downstream);
                                   return;
                              }
                         }

                         boolean d = inner.isDone();

                         R v;

                         try {
                              v = q.poll();
                         } catch (Throwable ex) {
                              Exceptions.throwIfFatal(ex);
                              current = null;
                              inner.cancel();
                              cancelAll();
                              a.onError(ex);
                              return;
                         }

                         boolean empty = v == null;

                         if (d && empty) {
                              inner = null;
                              current = null;
                              upstream.request(1);
                              continueNextSource = true;
                              break;
                         }

                         if (empty) {
                              break;
                         }

                         a.onNext(v);
                         e++;
                         inner.request(1L);
                    }
                    if (e == r) {
                         if (cancelled) {
                              cancelAll();
                              return;
                         }
                         if (em == ErrorMode.IMMEDIATE) {
                              Throwable ex = errors.get();

                              if (ex != null) {
                                   current = null;
                                   inner.cancel();
                                   cancelAll();
                                   errors.tryTerminateConsumer(downstream);
                                   return;
                              }
                         }

                         boolean d = inner.isDone();

                         boolean empty = q.isEmpty();

                         if (d && empty) {
                              inner = null;
                              current = null;
                              upstream.request(1);
                              continueNextSource = true;
                         }
                    }
               }
          }

          if (e != 0L && r != Long.MAX_VALUE) {
               requested.addAndGet(-e);
          }

          if (!continueNextSource) { 
               missed = addAndGet(-missed);
               if (missed == 0) {
                    break;
               }
          } 
     }
}

@SuppressWarnings("unchecked") void remove(CacheDisposable<T> consumer) {
     while (true) {
          CacheDisposable<T>[] current = observers.get();

          int n = current.length;

          if (n == 0) {
               return;
          }
          int j = -1;
          for (int i = 0; i < n; i++) {
               if (current[i] == consumer) {
                    j = i;
                    break;
               }
          }
          if (j < 0) {
               return;
          }

          CacheDisposable<T>[] next;

          if (n == 1) {
               next = EMPTY;
          } else {
               next = new CacheDisposable[n - 1];
               System.arraycopy(current, 0, next, 0, j);

               System.arraycopy(current, j + 1, next, j, n - j - 1);
          }

          if (observers.compareAndSet(current, next)) {
               return;
          }
     }
}

void drainFused() {
     int missed = 1;
     while (true) {
          if (disposed) {
               return;
          }
          boolean d = done;
          Throwable ex = error;

          if (!delayError && d && ex != null) {
               disposed = true;
               downstream.onError(error);
               worker.dispose();
               return;
          }

          downstream.onNext(null);

          if (d) {
               disposed = true;
               ex = error;
               if (ex != null) {
                    downstream.onError(ex);
               } else {
                    downstream.onComplete();
               }
               worker.dispose();
               return;
          }

          missed = addAndGet(-missed);

          if (missed == 0) {
               break;
          }
     }
}

@SuppressWarnings("unchecked") void remove(PublishSubscription<T> ps) {
     while (true) {
          PublishSubscription<T>[] a = subscribers.get();

          if (a == TERMINATED || a == EMPTY) {
               return;
          }

          int n = a.length;
          int j = -1;
          for (int i = 0; i < n; i++) {
               if (a[i] == ps) {
                    j = i;
                    break;
               }
          }
          if (j < 0) {
               return;
          }

          PublishSubscription<T>[] b;

          if (n == 1) {
               b = EMPTY;
          } else 
          {
               b = new PublishSubscription[n - 1];
               System.arraycopy(a, 0, b, 0, j);
               System.arraycopy(a, j + 1, b, j, n - j - 1);
          }

          if (subscribers.compareAndSet(a, b)) {
               return;
          }
     }
}

private static String normalizeArch(String value) {
    value = normalize(value);
    if (value.matches("^(x8664|amd64|ia32e|em64t|x64)$")) {
        return "x86_64";
    }
    if (value.matches("^(x8632|x86|i[3-6]86|ia32|x32)$")) {
        return "x86_32";
    }

    if (value.matches("^(ia64w?|itanium64)$")) {
        return "itanium_64";
    }

    if ("ia64n".equals(value)) {
        return "itanium_32";
    }
    if (value.matches("^(sparc|sparc32)$")) {
        return "sparc_32";
    }
    if (value.matches("^(sparcv9|sparc64)$")) {
        return "sparc_64";
    }
    if (value.matches("^(arm|arm32)$")) {
        return "arm_32";
    }
    if ("aarch64".equals(value)) {
        return "aarch_64";
    }
    if (value.matches("^(mips|mips32)$")) {
        return "mips_32";
    }
    if (value.matches("^(mipsel|mips32el)$")) {
        return "mipsel_32";
    }
    if ("mips64".equals(value)) {
        return "mips_64";
    }
    if ("mips64el".equals(value)) {
        return "mipsel_64";
    }
    if (value.matches("^(ppc|ppc32)$")) {
        return "ppc_32";
    }
    if (value.matches("^(ppcle|ppc32le)$")) {
        return "ppcle_32";
    }
    if ("ppc64".equals(value)) {
        return "ppc_64";
    }
    if ("ppc64le".equals(value)) {
        return "ppcle_64";
    }
    if ("s390".equals(value)) {
        return "s390_32";
    }
    if ("s390x".equals(value)) {
        return "s390_64";
    }
    return value;
}

private static Map<String, Object> doGetLoggerInfo(Logger logger) {
    Map<String, Object> info = new HashMap<String, Object>();
    info.put(LoggerHelper.name, logger.getName());

    info.put(LoggerHelper.clazz, logger.getClass());
    CodeSource codeSource = logger.getClass().getProtectionDomain().getCodeSource();

    if (codeSource != null) {
        info.put(LoggerHelper.codeSource, codeSource.getLocation());
    }

    info.put(LoggerHelper.additivity, logger.getAdditivity());

    Level level = logger.getLevel(), effectiveLevel = logger.getEffectiveLevel();

    if (level != null) {
        info.put(LoggerHelper.level, level.toString());
    }
    if (effectiveLevel != null) {
        info.put(LoggerHelper.effectiveLevel, effectiveLevel.toString());
    }
    @SuppressWarnings("unchecked") List<Map<String, Object>> result = doGetLoggerAppenders(logger.getAllAppenders());
    info.put(LoggerHelper.appenders, result);
    return info;
}

@SuppressWarnings("resource")
public static long select(boolean v, long telnetPortPid, String select) 
throws InputMismatchException {
    Map<Long, String> processMap = listProcessByJps(v);
    // Put the port that is already listening at the first
    if (telnetPortPid > 0 && processMap.containsKey(telnetPortPid)) {
        String telnetPortProcess = processMap.get(telnetPortPid);
        processMap.remove(telnetPortPid);

        Map<Long, String> newProcessMap = new LinkedHashMap<Long, String>();

        newProcessMap.put(telnetPortPid, telnetPortProcess);

        newProcessMap.putAll(processMap);
        processMap = newProcessMap;
    }
    if (processMap.isEmpty()) {
        AnsiLog.info("Can not find java process. Try to run `jps` command lists the instrumented Java HotSpot VMs on the target system.");
        return -1;
    }
    // select target process by the '--select' option when match only one process
    if (select != null && !select.trim().isEmpty()) {
        int matchedSelectCount = 0;
        Long matchedPid = null;

        for (Entry<Long, String> entry : processMap.entrySet()) {
            if (entry.getValue().contains(select)) {
                matchedSelectCount++;
                matchedPid = entry.getKey();
            }
        }
        if (matchedSelectCount == 1) {
            return matchedPid;
        }
    }
    AnsiLog.info("Found existing java process, please choose one and input the serial number of the process, eg : 1. Then hit ENTER.");
    // print list
    int count = 1;
    for (String process : processMap.values()) {
        if (count == 1) {
            System.out.println("* [" + count + "]: " + process);
        } else {
            System.out.println("  [" + count + "]: " + process);
        }
        count++;
    }

    // read choice
    String line = new Scanner(System.in).nextLine();
    if (line.trim().isEmpty()) {
        // get the first process id
        return processMap.keySet().iterator().next();
    }
    int choice = new Scanner(line).nextInt();
    if (choice <= 0 || choice > processMap.size()) {
        return -1;
    }

    Iterator<Long> idIter = processMap.keySet().iterator();

    for (int i = 1; i <= choice; ++i) {
        if (i == choice) {
            return idIter.next();
        }
        idIter.next();
    }

    return -1;
}

void _processDo(int option) throws IOException {
    if (debugoptions) {
        System.err.println("RECEIVED DO: " + TelnetOption.getOption(option));
    }
    if (__notifhand != null) {
        __notifhand.receivedNegotiation(TelnetNotificationHandler.RECEIVED_DO, option);
    }

    boolean acceptNewState = false; /* open TelnetOptionHandler functionality (start)*/
    if (optionHandlers[option] != null) {
        acceptNewState = optionHandlers[option].getAcceptLocal();
    } else { /* open TelnetOptionHandler functionality (end)*//* TERMINAL-TYPE option (start)*/
        if (option == TERMINAL_TYPE && ((terminalType != null) && (terminalType.length() > 0))) {
            acceptNewState = true;
        }
    }/* open TelnetOptionHandler functionality (end)*/
    if (_willResponse[option] > 0) {
        --_willResponse[option];
        if (_willResponse[option] > 0 && _stateIsWill(option)) {
            --_willResponse[option];
        }
    }
    if (_willResponse[option] == 0) {
        if (!_requestedWont(option)) { // Other end has acknowledged option.

            switch (option)
            {

                default:
                    break;

            }
        }
        else {
            switch (option)
            {

                default:
                    break;

            }
            if (acceptNewState) {
                _setWantWill(option);
                _sendWill(option);
            } else {
                _willResponse[option]++; 
                _sendWont(option);
            }
        }
    }
    _setWill(option);
}

private void bind(Configure configure) throws Throwable {
    long start = System.currentTimeMillis();
    if (!isBindRef.compareAndSet(false, true)) {
        throw new IllegalStateException("already bind");
    }
    // init random port
    if (configure.getTelnetPort() != null && configure.getTelnetPort() == 0) {
        int newTelnetPort = SocketUtils.findAvailableTcpPort();
        configure.setTelnetPort(newTelnetPort);
        logger().info("generate random telnet port: " + newTelnetPort);
    }
    if (configure.getHttpPort() != null && configure.getHttpPort() == 0) {
        int newHttpPort = SocketUtils.findAvailableTcpPort();
        configure.setHttpPort(newHttpPort);
        logger().info("generate random http port: " + newHttpPort);
    }
    // try to find appName
    if (configure.getAppName() == null) {
        configure.setAppName(System.getProperty(ArthasConstants.PROJECT_NAME, System.getProperty(ArthasConstants.SPRING_APPLICATION_NAME, null)));
    }
    try {
        if (configure.getTunnelServer() != null) {
            tunnelClient = new TunnelClient();
            tunnelClient.setAppName(configure.getAppName());
            tunnelClient.setId(configure.getAgentId());
            tunnelClient.setTunnelServerUrl(configure.getTunnelServer());
            tunnelClient.setVersion(ArthasBanner.version());
            ChannelFuture channelFuture = tunnelClient.start();
            channelFuture.await(10, TimeUnit.SECONDS);
        }
    } catch (Throwable t) {
        logger().error("start tunnel client error", t);
    }
    try {
        ShellServerOptions options = new ShellServerOptions().setInstrumentation(instrumentation).setPid(PidUtils.currentLongPid())
        .setWelcomeMessage(ArthasBanner.welcome());
        if (configure.getSessionTimeout() != null) {
            options.setSessionTimeout(configure.getSessionTimeout() * 1000);
        }
        this.httpSessionManager = new HttpSessionManager();
        if (IPUtils.isAllZeroIP(configure.getIp()) && StringUtils.isBlank(configure.getPassword())) {
            //  listen 0.0.0.0 
            String errorMsg = "Listening on 0.0.0.0 is very dangerous! External users can connect to your machine! "
            + "No password is currently configured. " 
            + "Therefore, a default password is generated, "
            + "and clients need to use the password to connect!";
            AnsiLog.error(errorMsg);
            configure.setPassword(StringUtils.randomString(64));
            AnsiLog.error("Generated arthas password: " + configure.getPassword());
            logger().error(errorMsg);
            logger().info("Generated arthas password: " + configure.getPassword());
        }
        this.securityAuthenticator = new SecurityAuthenticatorImpl(configure.getUsername(), configure.getPassword());
        shellServer = new ShellServerImpl(options);
        List<String> disabledCommands = new ArrayList<String>();
        if (configure.getDisabledCommands() != null) {
            String[] strings = StringUtils.tokenizeToStringArray(configure.getDisabledCommands(), ",");
            if (strings != null) {
                disabledCommands.addAll(Arrays.asList(strings));
            }
        }
        BuiltinCommandPack builtinCommands = new BuiltinCommandPack(disabledCommands);
        List<CommandResolver> resolvers = new ArrayList<CommandResolver>();
        resolvers.add(builtinCommands);
        //worker group
        workerGroup = new NioEventLoopGroup(new DefaultThreadFactory("arthas-TermServer", true));
        // TODO: discover user provided command resolver
        if (!(configure.getTelnetPort() != null && configure.getTelnetPort() > 0)) {
            logger().info("telnet port is {}, skip bind telnet server.", configure.getTelnetPort());
        } else {
            logger().info("try to bind telnet server, host: {}, port: {}.", configure.getIp(), configure.getTelnetPort());
            shellServer.registerTermServer(new HttpTelnetTermServer(configure.getIp(), configure.getTelnetPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
        } 
        if (configure.getHttpPort() != null && configure.getHttpPort() > 0) {
            logger().info("try to bind http server, host: {}, port: {}.", configure.getIp(), configure.getHttpPort());
            shellServer.registerTermServer(new HttpTermServer(configure.getIp(), configure.getHttpPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
        } else {
            // listen local address in VM communication
            if (configure.getTunnelServer() != null) {
                shellServer.registerTermServer(new HttpTermServer(configure.getIp(), configure.getHttpPort(), options.getConnectionTimeout(), workerGroup, httpSessionManager));
            }
            logger().info("http port is {}, skip bind http server.", configure.getHttpPort());
        }
        for (CommandResolver resolver : resolvers) {
            shellServer.registerCommandResolver(resolver);
        }
        shellServer.listen(new BindHandler(isBindRef));
        if (!isBind()) {
            throw new IllegalStateException("Arthas failed to bind telnet or http port! Telnet port: " + String.valueOf(configure.getTelnetPort()) 
            + ", http port: "
            + String.valueOf(configure.getHttpPort()));
        }
        //http api session manager
        sessionManager = new SessionManagerImpl(options, shellServer.getCommandManager(), shellServer.getJobController());
        //http api handler
        httpApiHandler = new HttpApiHandler(historyManager, sessionManager);
        logger().info("as-server listening on network={};telnet={};http={};timeout={};", configure.getIp(), configure.getTelnetPort(), configure.getHttpPort(), options.getConnectionTimeout());
        // 
        if (configure.getStatUrl() != null) {
            logger().info("arthas stat url: {}", configure.getStatUrl());
        }
        UserStatUtil.setStatUrl(configure.getStatUrl());

        UserStatUtil.setAgentId(configure.getAgentId());
        UserStatUtil.arthasStart();
        try {
            SpyAPI.init();
        } catch (Throwable e) {
        // ignore
        }
        logger().info("as-server started in {} ms", System.currentTimeMillis() - start);
    } catch (Throwable e) {
        logger().error("Error during start as-server", e);
        destroy();
        throw e;
    }
}

private int __read(boolean mayBlock) throws IOException {
    int ch;
    while (true) {
        // If there is no more data AND we were told not to block,
        // just return WOULD_BLOCK (-2). (More efficient than exception.)
        if (!mayBlock && super.available() == 0) {
            return WOULD_BLOCK;
        }
        // Otherwise, exit only when we reach end of stream.
        if ((ch = super.read()) < 0) {
            return EOF;
        }
        ch = (ch & 0xff); /* Code Section added for supporting AYT (start)*/
        synchronized (__client)
        {
            __client._processAYTResponse();
        }/* Code Section added for supporting AYT (end)*//* Code Section added for supporting spystreams (start)*/
        __client._spyRead(ch); /* Code Section added for supporting spystreams (end)*/
        switch (__receiveState) {
            case _STATE_CR:
                if (ch == '\0')
                {
                    // Strip null
                    continue;
                }// How do we handle newline after cr?
                //  else if (ch == '\n' && _requestedDont(TelnetOption.ECHO) &&
                // Handle as normal data by falling through to _STATE_DATA case

                //$FALL-THROUGH$
            case _STATE_DATA:
                if (ch == TelnetCommand.IAC) {
                    __receiveState = _STATE_IAC;
                    continue;
                }
                if (ch != '\r') {
                    __receiveState = _STATE_DATA;
                }
                else {
                    synchronized (__client)
                    {
                        if (__client._requestedDont(TelnetOption.BINARY)) {
                            __receiveState = _STATE_CR;
                        } else {
                            __receiveState = _STATE_DATA;
                        }
                    }
                } 
                break;
            case _STATE_IAC:
                switch (ch) {
                    case TelnetCommand.WILL:
                        __receiveState = _STATE_WILL;
                        continue;
                    case TelnetCommand.WONT:
                        __receiveState = _STATE_WONT;
                        continue;
                    case TelnetCommand.DO:
                        __receiveState = _STATE_DO;
                        continue;
                    case TelnetCommand.DONT:
                        __receiveState = _STATE_DONT;
                        continue; /* TERMINAL-TYPE option (start)*/
                    case TelnetCommand.SB:
                        __suboption_count = 0;
                        __receiveState = _STATE_SB;
                        continue; /* TERMINAL-TYPE option (end)*/
                    case TelnetCommand.IAC:
                        __receiveState = _STATE_DATA;
                        break; // exit to enclosing switch to return IAC from read
                    case TelnetCommand.SE : // unexpected byte! ignore it (don't send it as a command)
                        __receiveState = _STATE_DATA;
                        continue;
                    default:
                        __receiveState = _STATE_DATA;
                        __client._processCommand(ch); // Notify the user
                        continue; // move on the next char
                }
                break; // exit and return from read
            case _STATE_WILL:
                synchronized (__client)
                {
                    __client._processWill(ch);
                    __client._flushOutputStream();
                }
                __receiveState = _STATE_DATA;
                continue;
            case _STATE_WONT:
                synchronized (__client)
                {
                    __client._processWont(ch);
                    __client._flushOutputStream();
                }
                __receiveState = _STATE_DATA;
                continue;
            case _STATE_DO:
                synchronized (__client)
                {
                    __client._processDo(ch);
                    __client._flushOutputStream();
                }
                __receiveState = _STATE_DATA;
                continue;
            case _STATE_DONT:
                synchronized (__client)
                {
                    __client._processDont(ch);
                    __client._flushOutputStream();
                }
                __receiveState = _STATE_DATA;
                continue; /* TERMINAL-TYPE option (start)*/
            case _STATE_SB:
                switch (ch) {
                    case TelnetCommand.IAC:
                        __receiveState = _STATE_IAC_SB;
                        continue;
                    default:
                        // store suboption char
                        if (__suboption_count < __suboption.length) {
                            __suboption[__suboption_count++] = ch;
                        }
                        break;
                }
                __receiveState = _STATE_SB;

                continue;
            case _STATE_IAC_SB : // IAC received during SB phase
                switch (ch) {
                    case TelnetCommand.SE:
                        synchronized (__client)
                        {
                            __client._processSuboption(__suboption, __suboption_count);
                            __client._flushOutputStream();
                        }
                        __receiveState = _STATE_DATA;
                        continue;
                    case TelnetCommand.IAC : // De-dup the duplicated IAC
                        if (__suboption_count < __suboption.length) {
                            __suboption[__suboption_count++] = ch;
                        }
                        break;
                    default :            // unexpected byte! ignore it
                        break;
                }
                __receiveState = _STATE_SB;
                continue; /* TERMINAL-TYPE option (end)*/
        }
        break;
    }
    return ch;
}

private static String formatFileSize(long size) {
    String hrSize;
    double b = size, k = size / 1024.0;
    double m = ((size / 1024.0) / 1024.0), g = (((size / 1024.0) / 1024.0) / 1024.0);
    double t = ((((size / 1024.0) / 1024.0) / 1024.0) / 1024.0);
    DecimalFormat dec = new DecimalFormat("0.00");
    if (t > 1) {
        hrSize = dec.format(t).concat(" TB");
    } else {
        if (g <= 1) {
            if (m > 1) {
                hrSize = dec.format(m).concat(" MB");
            } else {
                if (k > 1) {
                    hrSize = dec.format(k).concat(" KB");
                } else {
                    hrSize = dec.format(b).concat(" Bytes");
                }
            }
        }else {
            hrSize = dec.format(g).concat(" GB");
        } 
    }
    return hrSize;
}

@Override
public byte[] transform(ClassLoader loader, String className, Class<?> classBeingRedefined, ProtectionDomain protectionDomain, byte[] classfileBuffer) 
throws IllegalClassFormatException {
    if (className == null) {
        return null;
    }
    className = className.replace('/', '.');

    List<RetransformEntry> allRetransformEntries = allRetransformEntries();
    // 
    ListIterator<RetransformEntry> listIterator = allRetransformEntries.listIterator(allRetransformEntries.size());
    while (listIterator.hasPrevious()) {
        RetransformEntry retransformEntry = listIterator.previous();
        int id = retransformEntry.getId();
        // 
        boolean updateFlag = false;
        //  loader loader
        if (className.equals(retransformEntry.getClassName())) {
            if (retransformEntry.getClassLoaderClass() != null || retransformEntry.getHashCode() != null) {
                updateFlag = isLoaderMatch(retransformEntry, loader);
            } else {
                updateFlag = true;
            }
        }
        if (updateFlag) {
            logger.info("RetransformCommand match class: {}, id: {}, classLoaderClass: {}, hashCode: {}", className, id, retransformEntry.getClassLoaderClass(), retransformEntry.getHashCode());
            retransformEntry.incTransformCount();
            return retransformEntry.getBytes();
        }
    }
    return null;
}

public static void drawPlayException(TableElement table, ObjectVO throwableVO) {
    // :
    table.row("IS-RETURN", "" + false);
    table.row("IS-EXCEPTION", "" + true);
    // :
    Throwable cause, t = (Throwable) throwableVO.getObject();
    if (t instanceof InvocationTargetException) {
        cause = t.getCause();
    } else {
        cause = t;
    }
    if (throwableVO.needExpand()) {
        table.row("THROW-EXCEPTION", new ObjectView(cause, throwableVO.expandOrDefault()).draw());
    } else {
        StringWriter stringWriter = new StringWriter();
        PrintWriter printWriter = new PrintWriter(stringWriter);

        try {
            cause.printStackTrace(printWriter);
            table.row("THROW-EXCEPTION", stringWriter.toString());
        } finally {
            printWriter.close();
        }
    }
}

private AsyncProfiler profilerInstance() {
    if (profiler != null) {
        return profiler;
    }
    // try to load from special path
    if (ProfilerAction.load.toString().equals(action)) {
        profiler = AsyncProfiler.getInstance(this.actionArg);
    }
    if (libPath == null) {
        if (OSUtils.isLinux() || OSUtils.isMac()) {
            throw new IllegalStateException("Can not find libasyncProfiler so, please check the arthas directory.");
        } else {
            throw new IllegalStateException("Current OS do not support AsyncProfiler, Only support Linux/Mac.");
        }
    } else {
        // load from arthas directory
        // libattach Native Library already loaded in another classloader
        FileOutputStream tmpLibOutputStream = null;
        FileInputStream libInputStream = null;
        try {
            File tmpLibFile = File.createTempFile(VmTool.JNI_LIBRARY_NAME, null);
            tmpLibOutputStream = new FileOutputStream(tmpLibFile);
            libInputStream = new FileInputStream(libPath);
            IOUtils.copy(libInputStream, tmpLibOutputStream);
            libPath = tmpLibFile.getAbsolutePath();
            logger.debug("copy {} to {}", libPath, tmpLibFile);
        } catch (Throwable e) {
            logger.error("try to copy lib error! libPath: {}", libPath, e);
        } finally {
            IOUtils.close(libInputStream);
            IOUtils.close(tmpLibOutputStream);
        }
        profiler = AsyncProfiler.getInstance(libPath);
    } 
    return profiler;
}

private void doPostprocessing() {
    errCode = ERROR_NOTHING;
    errObject = null;
    Thread thread = Thread.currentThread();

    notifyPostProcessing(1);

    if (DEBUG) {
        thread.setName("[" + TAG + "]  ps = " + psAlgorithm + "  filename = " + storage.getName());
    }

    Exception exception = null;
    try {
        psAlgorithm.run(this);
    } catch (Exception err) {
        Log.e(TAG, "Post-processing failed. " + psAlgorithm.toString(), err);
        if (err instanceof InterruptedIOException || err instanceof ClosedByInterruptException || thread.isInterrupted()) {
            notifyError(DownloadMission.ERROR_POSTPROCESSING_STOPPED, null);
            return;
        }

        if (errCode == ERROR_NOTHING) {
            errCode = ERROR_POSTPROCESSING;
        }

        exception = err;
    } finally {
        notifyPostProcessing(errCode == ERROR_NOTHING ? 2 : 0);
    }
    if (errCode != ERROR_NOTHING) {
        if (exception == null) {
            exception = errObject;
        }
        notifyError(ERROR_POSTPROCESSING, exception);

        return;
    }
    notifyFinished();
}

void establishConnection(int threadId, HttpURLConnection conn) throws IOException, HttpError {
    int statusCode = conn.getResponseCode();
    if (DEBUG) {
        Log.d(TAG, threadId + ":[request]  Range=" + conn.getRequestProperty("Range"));
        Log.d(TAG, threadId + ":[response] Code=" + statusCode);

        Log.d(TAG, threadId + ":[response] Content-Length=" + conn.getContentLength());

        Log.d(TAG, threadId + ":[response] Content-Range=" + conn.getHeaderField("Content-Range"));
    }
    switch (statusCode) {
        case 204:
        case 205:
        case 207:
            throw new HttpError(statusCode);
        case 416:
            return; // let the download thread handle this error
        default:
            if (statusCode < 200 || statusCode > 299) {
                throw new HttpError(statusCode);
            }
    }
}

private Elst parseEdts(final Box ref) throws IOException {
    final Box b = untilBox(ref, ATOM_ELST);
    if (b == null) {
        return null;
    }

    final Elst obj = new Elst();

    final boolean v1 = stream.read() == 1;

    stream.skipBytes(3); // flags

    final int entryCount = stream.readInt();

    if (entryCount < 1) {
        obj.bMediaRate = 0x00010000; // default media rate (1.0)
        return obj;
    }

    if (!v1) {
        stream.skipBytes(DataReader.INTEGER_SIZE); // segment duration
        obj.mediaTime = stream.readInt();
    } else {
        stream.skipBytes(DataReader.LONG_SIZE); // segment duration
        obj.mediaTime = stream.readLong();

        // ignore all remain entries
        stream.skipBytes((entryCount - 1) * (DataReader.LONG_SIZE * 2));
    } 

    obj.bMediaRate = stream.readInt();

    return obj;
}

@Override
public void run() {
    boolean done;
    long start = mMission.fallbackResumeOffset;

    if (DEBUG && !mMission.unknownLength && start > 0) {
        Log.i(TAG, "Resuming a single-thread download at " + start);
    }
    try {
        long rangeStart = (mMission.unknownLength || start < 1) ? -1 : start;
        int mId = 1;

        mConn = mMission.openConnection(false, rangeStart, -1);

        if (mRetryCount == 0 && rangeStart == -1) {
            // workaround: bypass android connection pool
            mConn.setRequestProperty("Range", "bytes=0-");
        }

        mMission.establishConnection(mId, mConn);

        // check if the download can be resumed
        if (mConn.getResponseCode() == 416 && start > 0) {
            mMission.notifyProgress(-start);
            start = 0;
            mRetryCount--;
            throw new DownloadMission.HttpError(416);
        }

        // secondary check for the file length
        if (!mMission.unknownLength)
        {
            mMission.unknownLength = Utility.getContentLength(mConn) == -1;
        }

        if (mMission.unknownLength || mConn.getResponseCode() == 200) {
            // restart amount of bytes downloaded
            mMission.done = mMission.offsets[mMission.current] - mMission.offsets[0];
        }

        mF = mMission.storage.getStream();

        mF.seek(mMission.offsets[mMission.current] + start);

        mIs = mConn.getInputStream();

        byte[] buf = new byte[DownloadMission.BUFFER_SIZE];

        int len = 0;

        while (mMission.running && (len = mIs.read(buf, 0, buf.length)) != -1) {
            mF.write(buf, 0, len);
            start += len;
            mMission.notifyProgress(len);
        }

        dispose();

        // if thread goes interrupted check if the last part is written. This avoid re-download the whole file
        done = len == -1;
    } catch (Exception e) {
        dispose();
        mMission.fallbackResumeOffset = start;

        if (!mMission.running || e instanceof ClosedByInterruptException) {
            return;
        }

        if (e instanceof HttpError && ((HttpError) e).statusCode == ERROR_HTTP_FORBIDDEN) {
            // for youtube streams. The url has expired, recover
            dispose();
            mMission.doRecover(ERROR_HTTP_FORBIDDEN);
            return;
        }

        if (mRetryCount++ >= mMission.maxRetry) {
            mMission.notifyError(e);
            return;
        }

        if (DEBUG) {
            Log.e(TAG, "got exception, retrying...", e);
        }

        run(); // try again
        return;
    }
    if (done) {
        mMission.notifyFinished();
    } else {
        mMission.fallbackResumeOffset = start;
    }
}

private void flushAuxiliar(long amount) throws IOException {
    if (aux.length < 1) {
        return;
    }
    out.flush();
    aux.flush();

    boolean underflow = aux.offset < aux.length || out.offset < out.length;

    byte[] buffer = new byte[COPY_BUFFER_SIZE];

    aux.target.seek(0);

    out.target.seek(out.length);
    long length = amount;

    while (length > 0) {
        int read = (int) Math.min(length, Integer.MAX_VALUE);
        read = aux.target.read(buffer, 0, Math.min(read, buffer.length));

        if (read < 1) {
            amount -= length;
            break;
        }

        out.writeProof(buffer, read);

        length -= read;
    }
    if (!underflow) {
        out.offset += amount;
        aux.offset -= amount;
    } else {
        if (out.offset < out.length) {
            aux.offset = 0;
        } else {
            // calculate the aux underflow pointer
            if (aux.offset >= amount) {
                aux.offset -= amount;
                out.offset = out.length + amount;
            } else {
                out.offset += aux.offset;
                aux.offset = 0;
                out.target.seek(out.offset);
            } 
        } 
    } 
    out.length += amount;

    if (out.length > maxLengthKnown) {
        maxLengthKnown = out.length;
    }
    if (amount < aux.length) {
        // move the excess data to the beginning of the file
        long readOffset = amount;
        long writeOffset = 0;
        aux.length -= amount;
        length = aux.length;

        while (length > 0) {
            int read = (int) Math.min(length, Integer.MAX_VALUE);
            read = aux.target.read(buffer, 0, Math.min(read, buffer.length));

            aux.target.seek(writeOffset);

            aux.writeProof(buffer, read);

            writeOffset += read;
            readOffset += read;
            length -= read;
            aux.target.seek(readOffset);
        }

        aux.target.setLength(aux.length);

        return;
    }
    if (aux.length > THRESHOLD_AUX_LENGTH) {
        aux.target.setLength(THRESHOLD_AUX_LENGTH); // or setLength(0);
    }
    aux.reset();
}

private void showError(DownloadMission mission, UserAction action, @StringRes int reason) {
    StringBuilder request = new StringBuilder(256);
    request.append(mission.source);

    request.append(" [");

    if (mission.recoveryInfo != null) {
        for (MissionRecoveryInfo recovery : mission.recoveryInfo)
        {
            request.append(' ').append(recovery.toString()).append(' ');
        }
    }

    request.append("]");

    String service;

    try {
        service = NewPipe.getServiceByUrl(mission.source).getServiceInfo().getName();
    } catch (Exception e) {
        service = ErrorInfo.SERVICE_NONE;
    }

    ErrorUtil.createNotification(mContext, new ErrorInfo(ErrorInfo.Companion.throwableToStringList(mission.errObject), action, service, request.toString(), reason));
}

void startMission(DownloadMission mission) {
    synchronized (this) {
        mission.timestamp = System.currentTimeMillis();
        mission.mHandler = mHandler;

        mission.maxRetry = mPrefMaxRetry;
        // create metadata file
        while (true) {
            mission.metadata = new File(mPendingMissionsDir, String.valueOf(mission.timestamp));
            if (!mission.metadata.isFile() && !mission.metadata.exists()) {
                try {
                    if (!mission.metadata.createNewFile())
                    {
                        throw new RuntimeException("Cant create download metadata file");
                    }
                } catch (IOException e) {
                    throw new RuntimeException(e);
                }
                break;
            }

            mission.timestamp = System.currentTimeMillis();
        }
        mSelfMissionsControl = true;
        mMissionsPending.add(mission);

        // Before continue, save the metadata in case the internet connection is not available
        Utility.writeToFile(mission.metadata, mission);

        if (mission.storage == null) {
            // noting to do here
            mission.errCode = DownloadMission.ERROR_FILE_CREATION;
            if (mission.errObject != null)
            {
                mission.errObject = new IOException("DownloadMission.storage == NULL");
            }

            return;
        }

        boolean start = !mPrefQueueLimit || getRunningMissionsCount() < 1;

        if (canDownloadInCurrentNetwork() && start) {
            mission.start();
        }
    }
}

@Override int process(SharpStream out, SharpStream... sources) throws IOException {
    // check if the subtitle is already in srt and copy, this should never happen
    String format = getArgumentAt(0, null);
    boolean ignoreEmptyFrames = getArgumentAt(1, "true").equals("true");
    if (!(format == null || format.equals("ttml"))) {
        if (format.equals("srt")) {
            byte[] buffer = new byte[8 * 1024];
            int read;

            while ((read = sources[0].read(buffer)) > 0) {
                out.write(buffer, 0, read);
            }

            return OK_RESULT;
        }
    } else {
        SrtFromTtmlWriter writer = new SrtFromTtmlWriter(out, ignoreEmptyFrames);
        try {
            writer.build(sources[0]);
        } catch (Exception err) {
            Log.e(TAG, "subtitle parse failed", err);
            if (err instanceof IOException) { 
                return 1; 
            } else { 
                return 8; 
            } 
        }

        return OK_RESULT;
    } 
    throw new UnsupportedOperationException("Can't convert this subtitle, unimplemented format: " + format);
}

private void joinForThreads(int millis) {
    final Thread currentThread = Thread.currentThread();
    if (init != null && init != currentThread && init.isAlive()) {
        init.interrupt();
        if (millis > 0) {
            try {
                init.join(millis);
            } catch (InterruptedException e) {
                Log.w(TAG, "Initializer thread is still running", e);
                return;
            }
        }
    }
    // if a thread is still alive, possible reasons:
    //      slow device
    //      the user is spamming start/pause buttons
    //      start() method called quickly after pause()


    for (Thread thread : threads) {
        if (!thread.isAlive() || thread == Thread.currentThread()) {
            continue;
        }
        thread.interrupt();
    }
    try {
        for (Thread thread : threads) {
            if (!thread.isAlive()) {
                continue;
            }
            if (DEBUG) {
                Log.w(TAG, "thread alive: " + thread.getName());
            }

            if (millis > 0) {
                thread.join(millis);
            }
        }
    } catch (InterruptedException e) {
        throw new RuntimeException("A download thread is still running", e);
    }
}

private static String convertEmlToHtmlBasic(byte[] emlBytes, EmlToPdfRequest request) {
     if (emlBytes == null || emlBytes.length == 0) {
          throw new IllegalArgumentException("EML file is empty or null");
     }
     String emlContent = new String(emlBytes, StandardCharsets.UTF_8);

     // Basic email parsing
     String subject = extractBasicHeader(emlContent, "Subject:");

     String from = extractBasicHeader(emlContent, "From:");

     String to = extractBasicHeader(emlContent, "To:");

     String cc = extractBasicHeader(emlContent, "Cc:");

     String bcc = extractBasicHeader(emlContent, "Bcc:");

     String date = extractBasicHeader(emlContent, "Date:");

     // Try to extract HTML content
     String htmlBody = extractHtmlBody(emlContent);

     if (htmlBody == null) {
          String textBody = extractTextBody(emlContent);
          htmlBody = convertTextToHtml(textBody != null ? textBody : "Email content could not be parsed");
     }

     // Generate HTML with custom styling based on request
     StringBuilder html = new StringBuilder();

     html.append("<!DOCTYPE html>\n");

     html.append("<html><head><meta charset=\"UTF-8\">\n");

     html.append("<title>").append(escapeHtml(subject)).append("</title>\n");

     html.append("<style>\n");

     appendEnhancedStyles(html);
     html.append("</style>\n");
     html.append("</head><body>\n");
     html.append("<div class=\"email-container\">\n");

     html.append("<div class=\"email-header\">\n");

     html.append("<h1>").append(escapeHtml(subject)).append("</h1>\n");

     html.append("<div class=\"email-meta\">\n");

     html.append("<div><strong>From:</strong> ").append(escapeHtml(from)).append("</div>\n");

     html.append("<div><strong>To:</strong> ").append(escapeHtml(to)).append("</div>\n");

     // Include CC and BCC if present and requested
     if (request != null && request.isIncludeAllRecipients()) {
          if (!cc.trim().isEmpty()) {
               html.append("<div><strong>CC:</strong> ").append(escapeHtml(cc)).append("</div>\n");
          }
          if (!bcc.trim().isEmpty()) {
               html.append("<div><strong>BCC:</strong> ").append(escapeHtml(bcc)).append("</div>\n");
          }
     }

     if (!date.trim().isEmpty()) {
          html.append("<div><strong>Date:</strong> ").append(escapeHtml(date)).append("</div>\n");
     }

     html.append("</div></div>\n");

     html.append("<div class=\"email-body\">\n");

     html.append(processEmailHtmlBody(htmlBody));

     html.append("</div>\n");

     // Add attachment information - always check for and display attachments
     String attachmentInfo = extractAttachmentInfo(emlContent);

     if (!attachmentInfo.isEmpty()) {
          html.append("<div class=\"attachment-section\">\n");

          html.append("<h3>Attachments</h3>\n");

          html.append(attachmentInfo);

          // Add a status message about attachment inclusion
          if (request != null && request.isIncludeAttachments()) {
               html.append("<div class=\"attachment-inclusion-note\">\n");
               html.append("<p><strong>Note:</strong> Attachments are saved as external files and linked in this PDF. Click the links to open files externally.</p>\n");

               html.append("</div>\n");
          } else {
               html.append("<div class=\"attachment-info-note\">\n");
               html.append("<p><em>Attachment information displayed - files not included in PDF. Enable 'Include attachments' to embed files.</em></p>\n");

               html.append("</div>\n");
          }

          html.append("</div>\n");
     }

     // Show advanced features status if requested
     assert request != null;

     if (request.getFileInput().isEmpty()) {
          html.append("<div class=\"advanced-features-notice\">\n");
          html.append("<p><em>Note: Some advanced features require Jakarta Mail dependencies.</em></p>\n");

          html.append("</div>\n");
     }

     html.append("</div>\n");

     html.append("</body></html>");
     return html.toString();
}

private static boolean isValidJakartaMailMultipart(Object multipart) {
     if (multipart == null) {
          return false;
     }
     try {
          // Check if the object implements jakarta.mail.Multipart interface
          Class<?> multipartInterface = Class.forName("jakarta.mail.Multipart");
          if (!multipartInterface.isInstance(multipart)) {
               return false;
          }

          // Additional check for MimeMultipart
          try {
               Class<?> mimeMultipartClass = Class.forName("jakarta.mail.internet.MimeMultipart");
               if (mimeMultipartClass.isInstance(multipart)) {
                    log.debug("Found MimeMultipart instance for enhanced processing");

                    return true;
               }
          } catch (ClassNotFoundException e) {
               log.debug("MimeMultipart not available, using base Multipart interface");
          }

          return true;
     } catch (ClassNotFoundException e) {
          log.debug("Jakarta Mail Multipart interface not available for validation");

          return false;
     }
}

private static String extractBasicHeader(String emlContent, String headerName) {
     try {
          String[] lines = emlContent.split("\r?\n");
          for (int i = 0; i < lines.length; i++) {
               String line = lines[i];
               if (line.toLowerCase().startsWith(headerName.toLowerCase())) {
                    StringBuilder value = new StringBuilder(line.substring(headerName.length()).trim());
                    // Handle multi-line headers
                    for (int j = i + 1; j < lines.length; j++) {
                         if (lines[j].startsWith(" ") || lines[j].startsWith("\t")) {
                              value.append(" ").append(lines[j].trim());
                         } else {
                              break;
                         }
                    }

                    // Apply MIME header decoding
                    return safeMimeDecode(value.toString());
               }

               if (line.trim().isEmpty()) {
                    break;
               }
          }
     } catch (RuntimeException e) {
          log.warn("Error extracting header '{}': {}", headerName, e.getMessage());
     }
     return "";
}

private static void addAttachmentAnnotationsToDocument(PDDocument document, List<EmailAttachment> attachments) throws IOException {
     if (document.getNumberOfPages() == 0 || attachments == null || attachments.isEmpty()) {
          return;
     }
     // 1. Find the screen position of all attachment markers
     AttachmentMarkerPositionFinder finder = new AttachmentMarkerPositionFinder();

     finder.setSortByPosition(true); // Process pages in order

     finder.getText(document);

     List<MarkerPosition> markerPositions = finder.getPositions();

     // 2. Warn if the number of markers and attachments don't match
     if (markerPositions.size() != attachments.size()) {
          log.warn("Found {} attachment markers, but there are {} attachments. Annotation count may be incorrect.", markerPositions.size(), attachments.size());
     }

     // 3. Create an invisible annotation over each found marker
     int annotationsToAdd = Math.min(markerPositions.size(), attachments.size());

     for (int i = 0; i < annotationsToAdd; i++) {
          MarkerPosition position = markerPositions.get(i);

          EmailAttachment attachment = attachments.get(i);

          if (attachment.getEmbeddedFilename() != null) {
               PDPage page = document.getPage(position.getPageIndex());
               addAttachmentAnnotationToPage(document, page, attachment, position.getX(), position.getY());
          }
     }
}

