@Override
public Connection getConnection() {
    List < ConnectionPool > pools = getShuffledNodesPool();
    JedisException suppressed = null;
    for (ConnectionPool pool : pools) {
        Connection jedis = null;
        try {
            jedis = pool.getResource();

            if (jedis == null) {
                continue;
            }

            jedis.ping();
            return jedis;
        } catch (JedisException ex) {
            if (suppressed == null) { // remembering first suppressed exception
                suppressed = ex;
            }

            if (jedis != null) {
                jedis.close();
            }
        }
    }

    JedisException noReachableNode = new JedisException("No reachable shard.");

    if (suppressed != null) {
        noReachableNode.addSuppressed(suppressed);
    }

    throw noReachableNode;
}

private void processRequest(int limitPerDay, String identifier, Map<String, Bucket> buckets, HttpServletRequest request, HttpServletResponse response,
                            FilterChain filterChain) throws IOException, ServletException {
    Bucket userBucket = buckets.computeIfAbsent(identifier, k->
                            createUserBucket(limitPerDay));
    ConsumptionProbe probe = userBucket.tryConsumeAndReturnRemaining(1);

    if (probe.isConsumed()) {
        response.setHeader("X-Rate-Limit-Remaining", stripNewlines(Newlines.stripAll(Long.toString(probe.getRemainingTokens()))));
        filterChain.doFilter(request, response);
    } else {
        long waitForRefill = probe.getNanosToWaitForRefill()/ 1_000_000_000;
        response.setStatus(HttpStatus.TOO_MANY_REQUESTS.value());
        response.setHeader("X-Rate-Limit-Retry-After-Seconds", Newlines.stripAll(String.valueOf(waitForRefill)));
        response.getWriter().write("Rate limit exceeded for POST requests.");
    }
}

private void joinForThreads(int millis) {
final Thread currentThread = Thread.currentThread();
if (init != null && init != currentThread && init.isAlive()) {
    init.interrupt();

    if (millis > 0) {
                                                                 try {
        init.join(millis);
                                                                 } catch (InterruptedException e) {
                                                                   Log.w(TAG, "Initializer thread is still running", e);
                                                                   return;
                                                                 }
    }
}

    // if a thread is still alive, possible reasons:
    //      slow device
    //      the user is spamming start/pause buttons
    //      start() method called quickly after pause()

for (Thread thread : threads) {

    if (!thread.isAlive() || thread == Thread.currentThread()) continue;
    thread.interrupt();
}

try {
    for (Thread thread : threads) {

    if (!thread.isAlive()) continue;

    if (DEBUG) {
                                      Log.w(TAG, "thread alive: " + thread.getName());
    }

    if (millis > 0) thread.join(millis);
    }
} catch (InterruptedException e) {
  throw new RuntimeException("A download thread is still running", e);
}
}

@SuppressWarnings("resource" )
public static long select(boolean v, long telnetPortPid, String select) throws InputMismatchException {
Map<Long, String> processMap = listProcessByJps(v);
    // Put the port that is already listening at the first
if (telnetPortPid > 0 && processMap.containsKey(telnetPortPid)) {
    String telnetPortProcess = processMap.get(telnetPortPid);
    processMap.remove(telnetPortPid);
    Map<Long, String> newProcessMap = new LinkedHashMap<Long, String>();
    newProcessMap.put(telnetPortPid, telnetPortProcess);
    newProcessMap.putAll(processMap);
    processMap = newProcessMap;
}

if (processMap.isEmpty()) {
    AnsiLog.info("Can not find java process. Try to run `jps` command lists the instrumented Java HotSpot VMs on the target system.");
    return -1;
}

    // select target process by the '--select' option when match only one process

if (select != null && !select.trim().isEmpty()) {
    int matchedSelectCount = 0;
    Long matchedPid = null;
    for (Entry<Long, String> entry : processMap.entrySet()) {

                                                    if (entry.getValue().contains(select)) {
                                                                matchedSelectCount++;
                                                                matchedPid = entry.getKey();
                                                    }
    }

    if (matchedSelectCount == 1) {
                                                    return matchedPid;
    }
}

AnsiLog.info("Found existing java process, please choose one and input the serial number of the process, eg : 1. Then hit ENTER.");
    // print list
int count = 1;
for (String process : processMap.values()) {

    if (count == 1) {
                                               System.out.println("* [" + count + "]: " + process);
    } else {
      System.out.println("  [" + count + "]: " + process);
    }

    count++;
}

    // read choice

String line = new Scanner(System.in).nextLine();
if (line.trim().isEmpty()) {
        // get the first process id
    return processMap.keySet().iterator().next();
}

int choice = new Scanner(line).nextInt();
if (choice <= 0 || choice > processMap.size()) {
    return -1;
}

Iterator<Long> idIter = processMap.keySet().iterator();
for (int i = 1; i <= choice; ++i) {

    if (i == choice) {
                                      return idIter.next();
    }

    idIter.next();
}

return -1;
}

public void run() {
    try {
        while (!stopped) {
            try {
                select();
                processAcceptedConnections();
                processInterestOpsUpdateRequests();
            } catch (RuntimeException e) {
                LOG.warn("Ignoring unexpected runtime exception", e);
            } catch (Exception e) {
                LOG.warn("Ignoring unexpected exception", e);
            }
        }

            // Close connections still pending on the selector. Any others
            // with in-flight work, let drain out of the work queue.

        for (SelectionKey key : selector.keys()) {
            NIOServerCnxn cnxn = (NIOServerCnxn) key.attachment();

            if (cnxn.isSelectable()) {
                cnxn.close(ServerCnxn.DisconnectReason.SERVER_SHUTDOWN);
            }

            cleanupSelectionKey(key);
        }

        SocketChannel accepted;
        while ((accepted = acceptedQueue.poll()) != null) {
            fastCloseSock(accepted);
        }

        updateQueue.clear();
    }

    finally {
                closeSelector();
            // This will wake up the accept thread and the other selector
            // threads, and tell the worker thread pool to begin shutdown.
                NIOServerCnxnFactory.this.stop();
                LOG.info("selector thread exited run method");
    }
}

@Override
public CacheEntry set(CacheKey cacheKey, CacheEntry entry) {
    lock.lock();
    try {
        entry = putIntoStore(cacheKey, entry);

        EvictionPolicy policy = getEvictionPolicy();
        policy.touch(cacheKey);

        CacheKey evictedKey = policy.evictNext();

        if (evictedKey != null) {
            delete(evictedKey);
            stats.evict();
        }

        for (Object redisKey : cacheKey.getRedisKeys()) {
            ByteBuffer mapKey = makeKeyForRedisKeysToCacheKeys(redisKey);

            if (redisKeysToCacheKeys.containsKey(mapKey)) {
                redisKeysToCacheKeys.get(mapKey)
                .add(cacheKey);
            } else {
                Set < CacheKey < ?>> set = ConcurrentHashMap.newKeySet();
                set.add(cacheKey);
                redisKeysToCacheKeys.put(mapKey, set);
            }
        }

        stats.load();
        return entry;
    }

    finally {
                lock.unlock();
    }
}

@Override
public TSInfo build(Object data) {
    List<KeyValue> list = (List<KeyValue>) data;
    Map<String, Object> properties = new HashMap<>();
    Map<String, String> labels = null;
    Map<String, Rule> rules = null;
    List<Map<String, Object>> chunks = null;
    for (KeyValue propertyValue : list) {
        String prop = BuilderFactory.STRING.build(propertyValue.getKey());
        Object value = propertyValue.getValue();
        if (value instanceof List) {
            switch (prop) {
                case LABELS_PROPERTY:
                    labels = BuilderFactory.STRING_MAP.build(value);
                    value = labels;
                    break;
                case RULES_PROPERTY:
                    List<KeyValue> rulesDataList = (List<KeyValue>) value;
                    Map<String, List<Object>> rulesValueMap = new HashMap<>(rulesDataList.size(), 1f);
                    rules = new HashMap<>(rulesDataList.size());
                    for (KeyValue rkv : rulesDataList) {
                        String ruleName = BuilderFactory.STRING.build(rkv.getKey());
                        List<Object> ruleValueList = BuilderFactory.ENCODED_OBJECT_LIST.build(rkv.getValue());
                        rulesValueMap.put(ruleName, ruleValueList);
                        rules.put(ruleName, new Rule(ruleName, ruleValueList));
                    }
                    value = rulesValueMap;
                    break;
                case CHUNKS_PROPERTY:
                    List<List<KeyValue>> chunksDataList = (List<List<KeyValue>>) value;
                    List<Map<String, Object>> chunksValueList = new ArrayList<>(chunksDataList.size());
                    chunks = new ArrayList<>(chunksDataList.size());
                    for (List<KeyValue> chunkDataAsList : chunksDataList) {
                        Map<String, Object> chunk = chunkDataAsList.stream().collect(Collectors.toMap(kv->BuilderFactory.STRING.build(kv.getKey()), kv->BuilderFactory.ENCODED_OBJECT.build(kv.getValue())));
                        chunksValueList.add(chunk);
                        chunks.add(chunk);
                    }
                    value = chunksValueList;
                    break;
                default:
                    value = SafeEncoder.encodeObject(value);
                    break;
            }
        } else if (value instanceof byte[]) {
                   value = BuilderFactory.STRING.build(value);
                   if (DUPLICATE_POLICY_PROPERTY.equals(prop)) {
                       try {
                           value = DuplicatePolicy.valueOf(((String) value).toUpperCase());
                       } catch (Exception e) {

                       }
                   }
               }
        properties.put(prop, value);
    }
    return new TSInfo(properties, labels, rules, chunks);
}

private static String convertEmlToHtmlBasic(byte[] emlBytes,
                                            EmlToPdfRequest request) {
    if (emlBytes == null || emlBytes.length == 0) {
        throw new IllegalArgumentException("EML file is empty or null");
    }

    String emlContent = new String(emlBytes, StandardCharsets.UTF_8);

    // Basic email parsing
    String subject = extractBasicHeader(emlContent, "Subject:");
    String from = extractBasicHeader(emlContent, "From:");
    String to = extractBasicHeader(emlContent, "To:");
    String cc = extractBasicHeader(emlContent, "Cc:");
    String bcc = extractBasicHeader(emlContent, "Bcc:");
    String date = extractBasicHeader(emlContent, "Date:");

    // Try to extract HTML content
    String htmlBody = extractHtmlBody(emlContent);
    if (htmlBody == null) {
        String textBody = extractTextBody(emlContent);
        htmlBody = convertTextToHtml(textBody != null ? textBody : "Email content could not be parsed");
    }

    // Generate HTML with custom styling based on request

    StringBuilder html = new StringBuilder();
    html.append("<!DOCTYPE html>\n");
    html.append("<html><head><meta charset=\"UTF-8\">\n");
    html.append("<title>").append(escapeHtml(subject)).append("</title>\n");
    html.append("<style>\n");
    appendEnhancedStyles(html);
    html.append("</style>\n");
    html.append("</head><body>\n");
    html.append("<div class=\"email-container\">\n");
    html.append("<div class=\"email-header\">\n");
    html.append("<h1>").append(escapeHtml(subject)).append("</h1>\n");
    html.append("<div class=\"email-meta\">\n");
    html.append("<div><strong>From:</strong> ").append(escapeHtml(from)).append("</div>\n");
    html.append("<div><strong>To:</strong> ").append(escapeHtml(to)).append("</div>\n");

    // Include CC and BCC if present and requested

    if (request != null && request.isIncludeAllRecipients()) {
        if (!cc.trim().isEmpty()) {
            html.append("<div><strong>CC:</strong> ").append(escapeHtml(cc)).append("</div>\n");
        }

        if (!bcc.trim().isEmpty()) {
            html.append("<div><strong>BCC:</strong> ").append(escapeHtml(bcc)).append("</div>\n");
        }
    }

    if (!date.trim().isEmpty()) {
        html.append("<div><strong>Date:</strong> ").append(escapeHtml(date)).append("</div>\n");
    }

    html.append("</div></div>\n");
    html.append("<div class=\"email-body\">\n");
    html.append(processEmailHtmlBody(htmlBody));
    html.append("</div>\n");

    // Add attachment information - always check for and display attachments
    String attachmentInfo = extractAttachmentInfo(emlContent);
    if (!attachmentInfo.isEmpty()) {
        html.append("<div class=\"attachment-section\">\n");
        html.append("<h3>Attachments</h3>\n");
        html.append(attachmentInfo);

        // Add a status message about attachment inclusion

        if (request != null && request.isIncludeAttachments()) {
            html.append("<div class=\"attachment-inclusion-note\">\n");
            html.append("<p><strong>Note:</strong> Attachments are saved as external files and linked in this PDF. Click the links to open files externally.</p>\n");
            html.append("</div>\n");
        } else {
            html.append("<div class=\"attachment-info-note\">\n");
            html.append("<p><em>Attachment information displayed - files not included in PDF. Enable 'Include attachments' to embed files.</em></p>\n");
            html.append("</div>\n");
        }

        html.append("</div>\n");
    }

    // Show advanced features status if requested
    assert request != null;

    if (request.getFileInput().isEmpty()) {
        html.append("<div class=\"advanced-features-notice\">\n");
        html.append("<p><em>Note: Some advanced features require Jakarta Mail dependencies.</em></p>\n");
        html.append("</div>\n");
    }

    html.append("</div>\n");
    html.append("</body></html>");
    return html.toString();
}

private JsonNode validateLicense(String licenseKey,
                                 String machineFingerprint,
                                 LicenseContext context) throws Exception {
    String requestBody = String.format("{\"meta\":{\"key\":\"%s\",\"scope\":{\"fingerprint\":\"%s\"}}}", licenseKey, machineFingerprint);
    HttpRequest request = HttpRequest.newBuilder().uri(URI.create(BASE_URL + "/" + ACCOUNT_ID + "/licenses/actions/validate-key"))
                                                  .header("Content-Type", "application/vnd.api+json")
                                                  .header("Accept", "application/vnd.api+json")
                    // .header("Authorization", "License " + licenseKey)
                                                  .POST(HttpRequest.BodyPublishers.ofString(requestBody))
                                                  .build();
    HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
    log.debug("ValidateLicenseResponse body: {}", response.body());
    JsonNode jsonResponse = objectMapper.readTree(response.body());
    if (response.statusCode() == 200) {
        JsonNode metaNode = jsonResponse.path("meta");
        boolean isValid = metaNode.path("valid").asBoolean();
        String detail = metaNode.path("detail").asText();
        String code = metaNode.path("code").asText();
        log.info("License validity: {}", isValid);
        log.info("Validation detail: {}", detail);
        log.info("Validation code: {}", code);

        // Check if the license itself has floating attribute
        JsonNode licenseAttrs = jsonResponse.path("data").path("attributes");
        if (!licenseAttrs.isMissingNode()) {
            context.isFloatingLicense = licenseAttrs.path("floating").asBoolean(false);
            context.maxMachines = licenseAttrs.path("maxMachines").asInt(1);
            log.info("License floating (from license): {}, maxMachines: {}", context.isFloatingLicense, context.maxMachines);
        }

        // Also check the policy for floating license support if included

        JsonNode includedNode = jsonResponse.path("included");
        JsonNode policyNode = null;
        if (includedNode.isArray()) {
            for (JsonNode node : includedNode) {
                if ("policies".equals(node.path("type").asText())) {
                    policyNode = node;
                    break;
                }
            }
        }

        if (policyNode != null) {
            // Check if this is a floating license from policy
            boolean policyFloating = policyNode.path("attributes").path("floating")
                                                                  .asBoolean(false);
            int policyMaxMachines = policyNode.path("attributes").path("maxMachines")
                                                                 .asInt(1);

            // Policy takes precedence over license attributes
            if (policyFloating) {
                context.isFloatingLicense = true;
                context.maxMachines = policyMaxMachines;
            }

            log.info("License floating (from policy): {}, maxMachines: {}", context.isFloatingLicense, context.maxMachines);
        }

        // Extract user count, default to 1 if not specified

        int users = jsonResponse.path("data").path("attributes")
                                             .path("metadata")
                                             .path("users")
                                             .asInt(1);
        applicationProperties.getPremium().setMaxUsers(users);

        // Extract isEnterprise flag
        context.isEnterpriseLicense = jsonResponse.path("data").path("attributes")
                                                               .path("metadata")
                                                               .path("isEnterprise")
                                                               .asBoolean(false);
        log.debug(applicationProperties.toString());
    } else {
        log.error("Error validating license. Status code: {}", response.statusCode());
    }

    return jsonResponse;
}

void drain() {

if (getAndIncrement() != 0) {
    return;
}

int missed = 1;
Observer<? super R> downstream = this.downstream;
AtomicThrowable errors = this.errors;
AtomicReference<SwitchMapMaybeObserver<R>> inner = this.inner;
for (;;) {
    for (;;) {

             if (cancelled) {
                 return;
             }

             if (errors.get() != null) {

                 if (!delayErrors) {
                                           errors.tryTerminateConsumer(downstream);
                                           return;
                 }
             }

             boolean d = done;
             SwitchMapMaybeObserver<R> current = inner.get();
             boolean empty = current == null;
             if (d && empty) {
                 errors.tryTerminateConsumer(downstream);
                 return;
             }

             if (empty || current.item == null) {
                 break;
             }

             inner.compareAndSet(current, null);
             downstream.onNext(current.item);
    }

    missed = addAndGet(-missed);

    if (missed == 0) {
             break;
    }
}
}

void _processDo(int option) throws IOException {

if (debugoptions) {
    System.err.println("RECEIVED DO: " + TelnetOption.getOption(option));
}

if (__notifhand != null) {
    __notifhand.receivedNegotiation(TelnetNotificationHandler.RECEIVED_DO, option);
}

boolean acceptNewState = false;


    /* open TelnetOptionHandler functionality (start)*/
if (optionHandlers[option] != null) {
    acceptNewState = optionHandlers[option].getAcceptLocal();
} else {
    /* open TelnetOptionHandler functionality (end)*/
        /* TERMINAL-TYPE option (start)*/

  if (option == TERMINAL_TYPE) {

           if ((terminalType != null) && (terminalType.length() > 0)) {
                                   acceptNewState = true;
           }
  }
        /* TERMINAL-TYPE option (end)*/
    /* open TelnetOptionHandler functionality (start)*/
}
    /* open TelnetOptionHandler functionality (end)*/

if (_willResponse[option] > 0) {
    -- _willResponse[option];

    if (_willResponse[option] > 0 && _stateIsWill(option)) {
                                   -- _willResponse[option];
    }
}

if (_willResponse[option] == 0) {

    if (_requestedWont(option)) {
                                    switch(option) {
                                    default:
                                    break;
                                    }

                                    if (acceptNewState) {
                                    _setWantWill(option);
                                    _sendWill(option);
                                    } else {
                                      ++_willResponse[option];
                                      _sendWont(option);
                                    }
    } else {
            // Other end has acknowledged option.
      switch(option) {
      default:
      break;
      }
    }
}

_setWill(option);
}

synchronized public static ZooKeeper getClient(String contextPath, String session, int expireTime) throws IOException {
    final String connectionId = concat(contextPath, session);
    ZooKeeper zk = zkMap.get(connectionId);

    if (zk == null) {
        if (LOG.isInfoEnabled()) {
            LOG.info(String.format("creating new " + "connection for : '%s'", connectionId));
        }

        Endpoint e = contextMap.get(contextPath);
        zk = new ZooKeeper(e.getHostPort(), 30000, new MyWatcher(connectionId));

        for (Map.Entry<String, String> p : e.getZooKeeperAuthInfo().entrySet()) {
            zk.addAuthInfo("digest", String.format("%s:%s", p.getKey(), p.getValue()).getBytes());
        }

        zkMap.put(connectionId, zk);

       // a session should automatically expire after an amount of time

        if (session != null) {
            zkSessionTimers.put(connectionId, new SessionTimerTask(expireTime, session, contextPath, timer));
        }
    }

    return zk;
}

@Override
public Class < ?> build(Object data) {
    if (data == null) return null;

    String str = STRING.build(data);
    switch (str) {
        case "null":
            return null;
        case "boolean":
            return boolean.class;
        case "integer":
            return int.class;
        case "number":
            return float.class;
        case "string":
            return String.class;
        case "object":
            return Object.class;
        case "array":
            return List.class;
        default:
            throw new JedisException("Unknown type: " + str);
    }
}

private static boolean isValidJakartaMailMultipart(Object multipart) {
    if (multipart == null) return false;
    try {
        // Check if the object implements jakarta.mail.Multipart interface
        Class<?> multipartInterface = Class.forName("jakarta.mail.Multipart");
        if (!multipartInterface.isInstance(multipart)) {
            return false;
        }

        // Additional check for MimeMultipart
        try {
            Class<?> mimeMultipartClass = Class.forName("jakarta.mail.internet.MimeMultipart");
            if (mimeMultipartClass.isInstance(multipart)) {
                log.debug("Found MimeMultipart instance for enhanced processing");
                return true;
            }
        } catch (ClassNotFoundException e) {
            log.debug("MimeMultipart not available, using base Multipart interface");
        }

        return true;
    } catch (ClassNotFoundException e) {
        log.debug("Jakarta Mail Multipart interface not available for validation");
        return false;
    }
}

