private static String extractBasicHeader(String emlContent,
                                         String headerName) {
    try {
        String [] lines = emlContent.split("\r?\n");
        for (int i = 0; i < lines.length; i++) {
            String line = lines[i];
            if (line.toLowerCase().startsWith(headerName.toLowerCase())) {
                StringBuilder value = new StringBuilder(line.substring(headerName.length()).trim());
                // Handle multi-line headers
                for (int j = i + 1; j < lines.length; j++) {
                    if (lines[j].startsWith(" ")
                            || lines[j].startsWith("\t")) {
                        value.append(" ").append(lines[j].trim());
                    } else {
                        break;
                    }
                }
                // Apply MIME header decoding

                return safeMimeDecode(value.toString());
            }

            if (line.trim().isEmpty()) break;
        }
    } catch (RuntimeException e) {
        log.warn("Error extracting header '{}': {}", headerName, e.getMessage());
    }

    return "";
}

void drainLoop() {
Observer<? super T> actual = this.downstream;
int missed = 1;
for (;;) {
    for (;;) {

             if (disposed) {
                 singleItem = null;
                 queue = null;
                 return;
             }

             if (errors.get() != null) {
                 singleItem = null;
                 queue = null;
                 errors.tryTerminateConsumer(actual);
                 return;
             }

             int os = otherState;
             if (os == OTHER_STATE_HAS_VALUE) {
                 T v = singleItem;
                 singleItem = null;
                 otherState = OTHER_STATE_CONSUMED_OR_EMPTY;
                 os = OTHER_STATE_CONSUMED_OR_EMPTY;
                 actual.onNext(v);
             }

             boolean d = mainDone;
             SimplePlainQueue<T> q = queue;
             T v = q != null ? q.poll() : null;
             boolean empty = v == null;
             if (d && empty && os == OTHER_STATE_CONSUMED_OR_EMPTY) {
                 queue = null;
                 actual.onComplete();
                 return;
             }

             if (empty) {
                 break;
             }

             actual.onNext(v);
    }

    missed = addAndGet(-missed);

    if (missed == 0) {
             break;
    }
}
}

private static Object process(final RedisInputStream is) {
    final byte b = is.readByte();
// System.out.println("BYTE: " + (char) b);

    switch (b) {
    case PLUS_BYTE:
        return is.readLineBytes();
    case DOLLAR_BYTE:
    case EQUAL_BYTE:
        return processBulkReply(is);
    case ASTERISK_BYTE:
        return processMultiBulkReply(is);
    case UNDERSCORE_BYTE:
        return is.readNullCrLf();
    case HASH_BYTE:
        return is.readBooleanCrLf();
    case COLON_BYTE:
        return is.readLongCrLf();
    case COMMA_BYTE:
        return is.readDoubleCrLf();
    case LEFT_BRACE_BYTE:
        return is.readBigIntegerCrLf();
    case PERCENT_BYTE: // TODO: currently just to start working with HELLO
        return processMapKeyValueReply(is);
    case TILDE_BYTE: // TODO:
        return processMultiBulkReply(is);
    case GREATER_THAN_BYTE:
        return processMultiBulkReply(is);
    case MINUS_BYTE:
        processError(is);
        return null;
  // TODO: Blob error '!'
    default:
        throw new JedisConnectionException("Unknown reply: " + (char)b);
    }
}

@Override
public Connection getConnection() {
    List<ConnectionPool> pools = getShuffledNodesPool();
    JedisException suppressed = null;
    for (ConnectionPool pool : pools) {
        Connection jedis = null;
        try {
            jedis = pool.getResource();
            if (jedis == null) {
                continue;
            }
            jedis.ping();
            return jedis;
        } catch (JedisException ex) {
            if (suppressed == null) { // remembering first suppressed exception
                suppressed = ex;
            }
            if (jedis != null) {
                jedis.close();
            }
        }
    }


    JedisException noReachableNode = new JedisException("No reachable shard.");
    if (suppressed != null) {
        noReachableNode.addSuppressed(suppressed);
    }
    throw noReachableNode;
}

@POST @Produces({MediaType.APPLICATION_JSON, "application/javascript", MediaType.APPLICATION_XML})
public Response createSession(@QueryParam("op") String op, @DefaultValue("5") @QueryParam("expire") String expire, @Context UriInfo ui) {
    if (!op.equals("create")) {
        throw new WebApplicationException(Response.status(Response.Status.BAD_REQUEST).entity(new ZError(ui.getRequestUri().toString(), "")).build());
    }

    int expireInSeconds;
    try {
        expireInSeconds = Integer.parseInt(expire);
    } catch (NumberFormatException e) {
        throw new WebApplicationException(Response.status(Response.Status.BAD_REQUEST).build());
    }

    String uuid = UUID.randomUUID().toString();
    while (ZooKeeperService.isConnected(contextPath, uuid)) {
        uuid = UUID.randomUUID().toString();
    }

    // establish the connection to the ZooKeeper cluster

    try {
        ZooKeeperService.getClient(contextPath, uuid, expireInSeconds);
    } catch (IOException e) {
        LOG.error("Failed while trying to create a new session", e);
        throw new WebApplicationException(Response.status(Response.Status.INTERNAL_SERVER_ERROR).build());
    }

    URI uri = ui.getAbsolutePathBuilder().path(uuid).build();
    return Response.created(uri).entity(new JSONWithPadding(new ZSession(uuid, uri.toString()))).build();
}

private int __read(boolean mayBlock) throws IOException {
int ch;
while (true) {

        // If there is no more data AND we were told not to block,
        // just return WOULD_BLOCK (-2). (More efficient than exception.)

    if (!mayBlock && super.available() == 0) {
                 return WOULD_BLOCK;
    }

        // Otherwise, exit only when we reach end of stream.

    if ((ch = super.read()) < 0) {
                 return EOF;
    }

    ch = (ch&0xff);

        /* Code Section added for supporting AYT (start)*/
    synchronized(__client) {
    __client._processAYTResponse();
    }
        /* Code Section added for supporting AYT (end)*/

        /* Code Section added for supporting spystreams (start)*/

    __client._spyRead(ch);
        /* Code Section added for supporting spystreams (end)*/
    switch(__receiveState) {
    case _STATE_CR :

                           if (ch == '\0') {
                // Strip null
        continue;
                           }
            // How do we handle newline after cr?
            //  else if (ch == '\n' && _requestedDont(TelnetOption.ECHO) &&

            // Handle as normal data by falling through to _STATE_DATA case

            //$FALL-THROUGH$

                           case _STATE_DATA :

                           if (ch == TelnetCommand.IAC) {
        __receiveState = _STATE_IAC;
        continue;
                           }

                           if (ch == '\r') {
        synchronized(__client) {

                               if (__client._requestedDont(TelnetOption.BINARY)) {
            __receiveState = _STATE_CR;
                               } else {
                                 __receiveState = _STATE_DATA;
                               }
        }
                           } else {
                             __receiveState = _STATE_DATA;
                           }

                           break;
                           case _STATE_IAC :
    switch(ch) {
    case TelnetCommand.WILL :
               __receiveState = _STATE_WILL;
               continue;
               case TelnetCommand.WONT :
        __receiveState = _STATE_WONT;
        continue;
               case TelnetCommand.DO :
        __receiveState = _STATE_DO;
        continue;
               case TelnetCommand.DONT :
        __receiveState = _STATE_DONT;
        continue;
            /* TERMINAL-TYPE option (start)*/
               case TelnetCommand.SB :
        __suboption_count = 0;
        __receiveState = _STATE_SB;
        continue;
            /* TERMINAL-TYPE option (end)*/
               case TelnetCommand.IAC :
        __receiveState = _STATE_DATA;
        break; // exit to enclosing switch to return IAC from read
               case TelnetCommand.SE : // unexpected byte! ignore it (don't send it as a command)
        __receiveState = _STATE_DATA;
        continue;
               default:
        __receiveState = _STATE_DATA;
        __client._processCommand(ch); // Notify the user
        continue; // move on the next char
    }

    break; // exit and return from read
                           case _STATE_WILL :
    synchronized(__client) {
        __client._processWill(ch);
        __client._flushOutputStream();
    }

    __receiveState = _STATE_DATA;
    continue;
                           case _STATE_WONT :
    synchronized(__client) {
        __client._processWont(ch);
        __client._flushOutputStream();
    }

    __receiveState = _STATE_DATA;
    continue;
                           case _STATE_DO :
    synchronized(__client) {
        __client._processDo(ch);
        __client._flushOutputStream();
    }

    __receiveState = _STATE_DATA;
    continue;
                           case _STATE_DONT :
    synchronized(__client) {
        __client._processDont(ch);
        __client._flushOutputStream();
    }

    __receiveState = _STATE_DATA;
    continue;
        /* TERMINAL-TYPE option (start)*/
                           case _STATE_SB :
    switch(ch) {
    case TelnetCommand.IAC :
               __receiveState = _STATE_IAC_SB;
               continue;
               default:
                // store suboption char

               if (__suboption_count < __suboption.length) {
        __suboption[__suboption_count ++] = ch;
               }

               break;
    }

    __receiveState = _STATE_SB;
    continue;
                           case _STATE_IAC_SB : // IAC received during SB phase
    switch(ch) {
    case TelnetCommand.SE :
    synchronized(__client) {
        __client._processSuboption(__suboption, __suboption_count);
        __client._flushOutputStream();
    }

    __receiveState = _STATE_DATA;
    continue;
               case TelnetCommand.IAC : // De-dup the duplicated IAC

               if (__suboption_count < __suboption.length) {
        __suboption[__suboption_count ++] = ch;
               }

               break;
               default:            // unexpected byte! ignore it
        break;
    }

    __receiveState = _STATE_SB;
    continue;
        /* TERMINAL-TYPE option (end)*/
    }

    break;
}

return ch;
}

@Override
public Map.Entry<T, ProfilingInfo> build(Object data) {
    List list = (List) data;
    if (list == null || list.isEmpty()) return null;
    if (list.get(0)instanceof KeyValue) { // RESP3
        Object resultsData = null, profileData = null;

        for (KeyValue keyValue : (List<KeyValue>) data) {
            String keyStr = BuilderFactory.STRING.build(keyValue.getKey());

            switch (keyStr) {
            case PROFILE_STR_REDIS7:
            case PROFILE_STR_REDIS8:
                profileData = keyValue.getValue();
                break;
            case RESULTS_STR_REDIS7:
                resultsData = data;
                break;
            case RESULTS_STR_REDIS8:
                resultsData = keyValue.getValue();
                break;
            }
        }

        assert resultsData != null: "Could not detect Results data.";
        assert profileData != null: "Could not detect Profile data.";
        return KeyValue.of(resultsBuilder.build(resultsData), ProfilingInfo.PROFILING_INFO_BUILDER.build(profileData));
    }
    return KeyValue.of(resultsBuilder.build(list.get(0)), ProfilingInfo.PROFILING_INFO_BUILDER.build(list.get(1)));
}

private static void addAttachmentAnnotationsToDocument(PDDocument document,
                                                       List<EmailAttachment> attachments) throws IOException {
    if (document.getNumberOfPages() == 0
            || attachments == null
            || attachments.isEmpty()) {
        return;
    }

    // 1. Find the screen position of all attachment markers

    AttachmentMarkerPositionFinder finder = new AttachmentMarkerPositionFinder();
    finder.setSortByPosition(true); // Process pages in order
    finder.getText(document);
    List<MarkerPosition> markerPositions = finder.getPositions();

    // 2. Warn if the number of markers and attachments don't match
    if (markerPositions.size() != attachments.size()) {
        log.warn("Found {} attachment markers, but there are {} attachments. Annotation count may be incorrect.",
            markerPositions.size(),
                attachments.size());
    }

    // 3. Create an invisible annotation over each found marker

    int annotationsToAdd = Math.min(markerPositions.size(), attachments.size());
    for (int i = 0; i < annotationsToAdd; i++) {
        MarkerPosition position = markerPositions.get(i);
        EmailAttachment attachment = attachments.get(i);
        if (attachment.getEmbeddedFilename() != null) {
            PDPage page = document.getPage(position.getPageIndex());
            addAttachmentAnnotationToPage(document, page, attachment, position.getX(), position.getY());
        }
    }
}

@Override
void drain() {

if (getAndIncrement() != 0) {
    return;
}

int missed = 1;
final SimplePlainQueue<Object> queue = this.queue;
final Subscriber<? super Flowable<T>> downstream = this.downstream;
UnicastProcessor<T> window = this.window;
for (;;) {

    if (upstreamCancelled) {
             queue.clear();
             window = null;
             this.window = null;
    } else {
      boolean isDone = done;
      Object o = queue.poll();
      boolean isEmpty = o == null;
      if (isDone && isEmpty) {
               Throwable ex = error;
               if (ex != null) {

                                 if (window != null) {
                                   window.onError(ex);
                                 }

                                 downstream.onError(ex);
               } else {

                 if (window != null) {
                          window.onComplete();
                 }

                 downstream.onComplete();
               }

               cleanupResources();
               upstreamCancelled = true;
               continue;
      } else if (!isEmpty) {

               if (o instanceof WindowBoundaryRunnable) {
                               WindowBoundaryRunnable boundary = (WindowBoundaryRunnable) o;
                               if (boundary.index == emitted || !restartTimerOnMaxSize) {
                                                            this.count = 0;
                                                            window = createNewWindow(window);
                               }
               } else if (window != null) {
                               @SuppressWarnings("unchecked" ) T item = (T) o;
                               window.onNext(item);
                               long count = this.count + 1;
                               if (count == maxSize) {
                                              this.count = 0;
                                              window = createNewWindow(window);
                               } else {
                                 this.count = count;
                               }
                      }

               continue;
             }
    }

    missed = addAndGet(-missed);

    if (missed == 0) {
             break;
    }
}
}

public void schedule(WorkRequest workRequest, long id) {
    if (stopped) {
        workRequest.cleanup();
        return;
    }

    ScheduledWorkRequest scheduledWorkRequest = new ScheduledWorkRequest(workRequest);

    // If we have a worker thread pool, use that; otherwise, do the work
    // directly.
    int size = workers.size();

    if (size > 0) {
        try {
            // make sure to map negative ids as well to [0, size-1]
            int workerNum = ((int) (id % size) + size) % size;
            ExecutorService worker = workers.get(workerNum);
            worker.execute(scheduledWorkRequest);
        } catch (RejectedExecutionException e) {
            LOG.warn("ExecutorService rejected execution", e);
            workRequest.cleanup();
        }
    } else {
        // When there is no worker thread pool, do the work directly
        // and wait for its completion
        scheduledWorkRequest.run();
    }
}

@Override
public CacheEntry set(CacheKey cacheKey, CacheEntry entry) {
    lock.lock();
    try {
        entry = putIntoStore(cacheKey, entry);

        EvictionPolicy policy = getEvictionPolicy();
        policy.touch(cacheKey);

        CacheKey evictedKey = policy.evictNext();
        if (evictedKey != null) {
            delete(evictedKey);
            stats.evict();
        }
        for (Object redisKey : cacheKey.getRedisKeys()) {
            ByteBuffer mapKey = makeKeyForRedisKeysToCacheKeys(redisKey);
            if (redisKeysToCacheKeys.containsKey(mapKey)) {
                redisKeysToCacheKeys.get(mapKey).add(cacheKey);
            } else {
                Set<CacheKey<?>> set = ConcurrentHashMap.newKeySet();
                set.add(cacheKey);
                redisKeysToCacheKeys.put(mapKey, set);
            }
        }
        stats.load();
        return entry;
    } finally {
        lock.unlock();
    }
}

public SSLContext createSslContext() throws IOException, GeneralSecurityException {
    KeyManager[] keyManagers = null;
    TrustManager[] trustManagers = null;
    if (sslVerifyMode == SslVerifyMode.FULL) {
        this.sslParameters.setEndpointIdentificationAlgorithm("HTTPS");
    } else if (sslVerifyMode == SslVerifyMode.CA) {
               this.sslParameters.setEndpointIdentificationAlgorithm("");
           } else if (sslVerifyMode == SslVerifyMode.INSECURE) {
                      trustManagers = new TrustManager[] {
                                                         INSECURE_TRUST_MANAGER};
                  }

    if (keystoreResource != null) {
        KeyStore keyStore = KeyStore.getInstance(keyStoreType);

        try (InputStream keystoreStream = keystoreResource.get()) {
                                                                  keyStore.load(keystoreStream, keystorePassword);
        }

        KeyManagerFactory keyManagerFactory = KeyManagerFactory.getInstance(keyManagerAlgorithm);

        keyManagerFactory.init(keyStore, keystorePassword);
        keyManagers = keyManagerFactory.getKeyManagers();
    }

    if (trustManagers == null && truststoreResource != null) {
        KeyStore trustStore = KeyStore.getInstance(trustStoreType);

        try (InputStream truststoreStream = truststoreResource.get()) {
                                                                      trustStore.load(truststoreStream, truststorePassword);
        }

        TrustManagerFactory trustManagerFactory = TrustManagerFactory.getInstance(trustManagerAlgorithm);

        trustManagerFactory.init(trustStore);
        trustManagers = trustManagerFactory.getTrustManagers();
    }

    SSLContext sslContext = SSLContext.getInstance(sslProtocol);

    sslContext.init(keyManagers, trustManagers, null);
    return sslContext;
}

private static String formatFileSize(long size) {
String hrSize;
double b = size;
double k = size / 1024.0;
double m = ((size / 1024.0) / 1024.0);
double g = (((size / 1024.0) / 1024.0) / 1024.0);
double t = ((((size / 1024.0) / 1024.0) / 1024.0) / 1024.0);
DecimalFormat dec = new DecimalFormat("0.00");
if (t > 1) {
    hrSize = dec.format(t).concat(" TB");
} else if (g > 1) {
  hrSize = dec.format(g).concat(" GB");
} else if (m > 1) {
  hrSize = dec.format(m).concat(" MB");
       } else if (k > 1) {
         hrSize = dec.format(k).concat(" KB");
       } else {
         hrSize = dec.format(b).concat(" Bytes");
       }

return hrSize;
}

private boolean verifyCertificateLicense(String licenseFile,
                                         LicenseContext context) {
    try {
        String encodedPayload = licenseFile;
        // Remove the header
        encodedPayload = encodedPayload.replace(CERT_PREFIX, "");
        // Remove the footer
        encodedPayload = encodedPayload.replace(CERT_SUFFIX, "");
        // Remove all newlines
        encodedPayload = encodedPayload.replaceAll("\\r?\\n", "");


        byte [] payloadBytes = Base64.getDecoder().decode(encodedPayload);
        String payload = new String(payloadBytes);
        log.info("Decoded certificate payload: {}", payload);
        String encryptedData = "";
        String encodedSignature = "";
        String algorithm = "";
        try {
            JSONObject attrs = new JSONObject(payload);
            encryptedData = (String) attrs.get("enc");
            encodedSignature = (String) attrs.get("sig");
            algorithm = (String) attrs.get("alg");
        } catch (JSONException e) {
            log.error("Failed to parse license file: {}", e.getMessage());
            return false;
        }

        // Verify license file algorithm

        if (!"base64+ed25519".equals(algorithm)) {
            log.error("Unsupported algorithm: {}. Only base64+ed25519 is supported.", algorithm);
            return false;
        }

        // Verify signature

        boolean isSignatureValid = verifyEd25519Signature(encryptedData, encodedSignature);
        if (!isSignatureValid) {
            log.error("License file signature is invalid");
            return false;
        }

        log.info("License file signature is valid");

        // Decode the base64 data
        String decodedData;
        try {
            decodedData = new String(Base64.getDecoder().decode(encryptedData));
        } catch (IllegalArgumentException e) {
            log.error("Failed to decode license data: {}", e.getMessage());
            return false;
        }

        // Process the certificate data

        boolean isValid = processCertificateData(decodedData, context);
        return isValid;
    } catch (Exception e) {
        log.error("Error verifying certificate license: {}", e.getMessage(), e);
        return false;
    }
}

@SuppressLint("SwitchIntDef") // only fit, fill and zoom are supported by NewPipe @ResizeMode
public static int nextResizeModeAndSaveToPrefs(final Player player, @ResizeMode final int resizeMode) {
    final int newResizeMode;
    switch (resizeMode) {
        case AspectRatioFrameLayout.RESIZE_MODE_FIT:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FILL;
            break;
        case AspectRatioFrameLayout.RESIZE_MODE_FILL:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_ZOOM;
            break;
        case AspectRatioFrameLayout.RESIZE_MODE_ZOOM:
        default:
            newResizeMode = AspectRatioFrameLayout.RESIZE_MODE_FIT;
            break;
    }

    // save the new resize mode so it can be restored in a future session

    player.getPrefs().edit().putInt(player.getContext().getString(R.string.last_resize_mode), newResizeMode)
    .apply();
    return newResizeMode;
}

void drain() {

if (getAndIncrement() != 0) {
    return;
}

int missed = 1;
EqualObserver<T>[] as = observers;
final EqualObserver<T> observer1 = as[0];
final SpscLinkedArrayQueue<T> q1 = observer1.queue;
final EqualObserver<T> observer2 = as[1];
final SpscLinkedArrayQueue<T> q2 = observer2.queue;
for (;;) {
    for (;;) {

             if (cancelled) {
                 q1.clear();
                 q2.clear();
                 return;
             }

             boolean d1 = observer1.done;
             if (d1) {
                 Throwable e = observer1.error;
                 if (e != null) {
                         cancel(q1, q2);
                         downstream.onError(e);
                         return;
                 }
             }

             boolean d2 = observer2.done;
             if (d2) {
                 Throwable e = observer2.error;
                 if (e != null) {
                         cancel(q1, q2);
                         downstream.onError(e);
                         return;
                 }
             }

             if (v1 == null) {
                 v1 = q1.poll();
             }

             boolean e1 = v1 == null;
             if (v2 == null) {
                 v2 = q2.poll();
             }

             boolean e2 = v2 == null;
             if (d1 && d2 && e1 && e2) {
                 downstream.onSuccess(true);
                 return;
             }

             if ((d1 && d2) && (e1 != e2)) {
                 cancel(q1, q2);
                 downstream.onSuccess(false);
                 return;
             }

             if (!e1 && !e2) {
                 boolean c;
                 try {
                 c = comparer.test(v1, v2);
                 } catch (Throwable ex) {
                   Exceptions.throwIfFatal(ex);
                   cancel(q1, q2);
                   downstream.onError(ex);
                   return;
                 }

                 if (!c) {
                                 cancel(q1, q2);
                                 downstream.onSuccess(false);
                                 return;
                 }

                 v1 = null;
                 v2 = null;
             }

             if (e1 || e2) {
                 break;
             }
    }

    missed = addAndGet(-missed);

    if (missed == 0) {
             break;
    }
}
}

@Override
public TSInfo build(Object data) {
    List<KeyValue> list = (List<KeyValue>) data;
    Map<String, Object> properties = new HashMap< >();
    Map<String, String> labels = null;
    Map<String, Rule> rules = null;
    List<Map<String, Object>> chunks = null;

    for (KeyValue propertyValue : list) {
        String prop = BuilderFactory.STRING.build(propertyValue.getKey());
        Object value = propertyValue.getValue();
        if (value instanceof List) {
            switch (prop) {
            case LABELS_PROPERTY:
                labels = BuilderFactory.STRING_MAP.build(value);
                value = labels;
                break;
            case RULES_PROPERTY:
                List<KeyValue> rulesDataList = (List<KeyValue>) value;
                Map<String, List<Object>> rulesValueMap = new HashMap< >(rulesDataList.size(), 1f);

                rules = new HashMap< >(rulesDataList.size());
                for (KeyValue rkv : rulesDataList) {
                    String ruleName = BuilderFactory.STRING.build(rkv.getKey());
                    List<Object> ruleValueList = BuilderFactory.ENCODED_OBJECT_LIST.build(rkv.getValue());

                    rulesValueMap.put(ruleName, ruleValueList);
                    rules.put(ruleName, new Rule(ruleName, ruleValueList));
                }

                value = rulesValueMap;
                break;
            case CHUNKS_PROPERTY:
                List<List<KeyValue>> chunksDataList = (List<List<KeyValue>>) value;
                List<Map<String, Object>> chunksValueList = new ArrayList< >(chunksDataList.size());

                chunks = new ArrayList< >(chunksDataList.size());
                for (List<KeyValue> chunkDataAsList : chunksDataList) {
                    Map<String, Object> chunk = chunkDataAsList.stream().collect(Collectors.toMap(kv -> BuilderFactory.STRING.build(kv.getKey()), kv -> BuilderFactory.ENCODED_OBJECT.build(kv.getValue())));

                    chunksValueList.add(chunk);
                    chunks.add(chunk);
                }

                value = chunksValueList;
                break;
            default:
                value = SafeEncoder.encodeObject(value);
                break;
            }
        } else if (value instanceof byte[]) {
                   value = BuilderFactory.STRING.build(value);
                   if (DUPLICATE_POLICY_PROPERTY.equals(prop)) {
                       try {
                           value = DuplicatePolicy.valueOf(((String) value).toUpperCase());
                       } catch (Exception e) {
                       }
                   }
               }

        properties.put(prop, value);
    }
    return new TSInfo(properties, labels, rules, chunks);
}

@Override
public void close() throws IOException {
    boolean triedToClose = false, success = false;
    try {
        flush();
        ((FileOutputStream) out).getFD()
        .sync();
        triedToClose = true;
        super.close();
        success = true;
    }

    finally {
        if (success) {
            boolean renamed = tmpFile.renameTo(origFile);

            if (!renamed) {
                // On windows, renameTo does not replace.
                if (!origFile.delete() || !tmpFile.renameTo(origFile)) {
                    throw new IOException("Could not rename temporary file " + tmpFile + " to " + origFile);
                }
            }
        } else {
            if (!triedToClose) {
                // If we failed when flushing, try to close it to not leak
                // an FD
                IOUtils.closeStream(out);
            }
            // close wasn't successful, try to delete the tmp file

            if (!tmpFile.delete()) {
                LOG.warn("Unable to delete tmp file {}", tmpFile);
            }
        }
    }
}

private static String convertEmlToHtmlBasic(byte[] emlBytes, EmlToPdfRequest request) {

if (emlBytes == null || emlBytes.length == 0) {
    throw new IllegalArgumentException("EML file is empty or null");
}

String emlContent = new String(emlBytes, StandardCharsets.UTF_8);

    // Basic email parsing
String subject = extractBasicHeader(emlContent, "Subject:");
String from = extractBasicHeader(emlContent, "From:");
String to = extractBasicHeader(emlContent, "To:");
String cc = extractBasicHeader(emlContent, "Cc:");
String bcc = extractBasicHeader(emlContent, "Bcc:");
String date = extractBasicHeader(emlContent, "Date:");

    // Try to extract HTML content
String htmlBody = extractHtmlBody(emlContent);
if (htmlBody == null) {
    String textBody = extractTextBody(emlContent);
    htmlBody = convertTextToHtml(textBody != null ? textBody : "Email content could not be parsed");
}

    // Generate HTML with custom styling based on request

StringBuilder html = new StringBuilder();
html.append("<!DOCTYPE html>\n");
html.append("<html><head><meta charset=\"UTF-8\">\n");
html.append("<title>").append(escapeHtml(subject)).append("</title>\n");
html.append("<style>\n");
appendEnhancedStyles(html);
html.append("</style>\n");
html.append("</head><body>\n");
html.append("<div class=\"email-container\">\n");
html.append("<div class=\"email-header\">\n");
html.append("<h1>").append(escapeHtml(subject)).append("</h1>\n");
html.append("<div class=\"email-meta\">\n");
html.append("<div><strong>From:</strong> ").append(escapeHtml(from)).append("</div>\n");
html.append("<div><strong>To:</strong> ").append(escapeHtml(to)).append("</div>\n");

    // Include CC and BCC if present and requested

if (request != null && request.isIncludeAllRecipients()) {

    if (!cc.trim().isEmpty()) {
                                                             html.append("<div><strong>CC:</strong> ").append(escapeHtml(cc)).append("</div>\n");
    }

    if (!bcc.trim().isEmpty()) {
                                                             html.append("<div><strong>BCC:</strong> ").append(escapeHtml(bcc)).append("</div>\n");
    }
}

if (!date.trim().isEmpty()) {
    html.append("<div><strong>Date:</strong> ").append(escapeHtml(date)).append("</div>\n");
}

html.append("</div></div>\n");
html.append("<div class=\"email-body\">\n");
html.append(processEmailHtmlBody(htmlBody));
html.append("</div>\n");

    // Add attachment information - always check for and display attachments
String attachmentInfo = extractAttachmentInfo(emlContent);
if (!attachmentInfo.isEmpty()) {
    html.append("<div class=\"attachment-section\">\n");
    html.append("<h3>Attachments</h3>\n");
    html.append(attachmentInfo);

        // Add a status message about attachment inclusion

    if (request != null && request.isIncludeAttachments()) {
                                   html.append("<div class=\"attachment-inclusion-note\">\n");
                                   html.append("<p><strong>Note:</strong> Attachments are saved as external files and linked in this PDF. Click the links to open files externally.</p>\n");
                                   html.append("</div>\n");
    } else {
      html.append("<div class=\"attachment-info-note\">\n");
      html.append("<p><em>Attachment information displayed - files not included in PDF. Enable 'Include attachments' to embed files.</em></p>\n");
      html.append("</div>\n");
    }

    html.append("</div>\n");
}

    // Show advanced features status if requested

assert request != null;

if (request.getFileInput().isEmpty()) {
    html.append("<div class=\"advanced-features-notice\">\n");
    html.append("<p><em>Note: Some advanced features require Jakarta Mail dependencies.</em></p>\n");
    html.append("</div>\n");
}

html.append("</div>\n");
html.append("</body></html>");
return html.toString();
}

@Override
public Class<?> build(Object data) {
    if (data == null) return null;

    String str = STRING.build(data);
    switch (str) {
        case "null":
            return null;
        case "boolean":
            return boolean.class;
        case "integer":
            return int.class;
        case "number":
            return float.class;
        case "string":
            return String.class;
        case "object":
            return Object.class;
        case "array":
            return List.class;
        default:
            throw new JedisException("Unknown type: " + str);
    }
}

@Override
public byte[] transform(ClassLoader loader, String className, Class<?> classBeingRedefined, ProtectionDomain protectionDomain, byte[] classfileBuffer) throws IllegalClassFormatException {

if (className == null) {
    return null;
}

className = className.replace('/', '.');
List<RetransformEntry> allRetransformEntries = allRetransformEntries();
        // 
ListIterator<RetransformEntry> listIterator = allRetransformEntries.listIterator(allRetransformEntries.size());
while (listIterator.hasPrevious()) {
    RetransformEntry retransformEntry = listIterator.previous();
    int id = retransformEntry.getId();
            // 
    boolean updateFlag = false;
            //  loader loader
    if (className.equals(retransformEntry.getClassName())) {

                                       if (retransformEntry.getClassLoaderClass() != null || retransformEntry.getHashCode() != null) {
                                                               updateFlag = isLoaderMatch(retransformEntry, loader);
                                       } else {
                                         updateFlag = true;
                                       }
    }

    if (updateFlag) {
                                       logger.info("RetransformCommand match class: {}, id: {}, classLoaderClass: {}, hashCode: {}", className, id, retransformEntry.getClassLoaderClass(), retransformEntry.getHashCode());
                                       retransformEntry.incTransformCount();
                                       return retransformEntry.getBytes();
    }
}

return null;
}

private static void sanitizeHtmlFilesInZip(Path zipFilePath,
                                           boolean disableSanitize,
                                           TempFileManager tempFileManager) throws IOException {
    try (TempDirectory tempUnzippedDir = new TempDirectory(tempFileManager)) {
        try (ZipInputStream zipIn = ZipSecurity.createHardenedInputStream(new ByteArrayInputStream(Files.readAllBytes(zipFilePath)))) {
            ZipEntry entry = zipIn.getNextEntry();
            while(entry != null) {
                Path filePath = tempUnzippedDir.getPath().resolve(sanitizeZipFilename(entry.getName()));
                if (!entry.isDirectory()) {
                    Files.createDirectories(filePath.getParent());

                    if (entry.getName().toLowerCase()
                                       .endsWith(".html")
                            || entry.getName().toLowerCase()
                                              .endsWith(".htm")) {
                        String content = new String(zipIn.readAllBytes(), StandardCharsets.UTF_8);
                        String sanitizedContent = sanitizeHtmlContent(content, disableSanitize);
                        Files.write(filePath, sanitizedContent.getBytes(StandardCharsets.UTF_8));
                    } else {
                        Files.copy(zipIn, filePath);
                    }
                }

                zipIn.closeEntry();
                entry = zipIn.getNextEntry();
            }
        }

        // Repack the sanitized files

        zipDirectory(tempUnzippedDir.getPath(), zipFilePath);
    } // tempUnzippedDir auto-cleaned
}

