        args.add(DUPLICATE_POLICY).add(duplicatePolicy);
    }

    if (onDuplicate != null) {
        args.add(ON_DUPLICATE).add(onDuplicate);
    }

    if (ignore) {
        args.add(IGNORE).add(ignoreMaxTimediff ).add(ignoreMaxValDiff);
    }

    if (labels != null) {
        args.add(LABELS);
        labels.entrySet().forEach((entry)-> args.add(entry.getKey()).add(entry.getValue()));
    }
}

@Override
public void onBroadcastReceived(final Intent intent) {
    super.onBroadcastReceived(intent);

    if (Intent.ACTION_CONFIGURATION_CHANGED.equals(intent.getAction())) {
        // Close it because when changing orientation from portrait
        // (in fullscreen mode) the size of queue layout can be larger than the screen size
        closeItemsList();
    } else if (ACTION_PLAY_PAUSE.equals(intent.getAction())) {
        // Ensure that we have audio-only stream playing when a user
        // started to play from notification's play button from outside of the app
        if (!fragmentIsVisible) {
            onFragmentStopped();
        }
    } else if (VideoDetailFragment.ACTION_VIDEO_FRAGMENT_STOPPED.equals(intent.getAction())) {
               fragmentIsVisible = false;
               onFragmentStopped();
           } else if (VideoDetailFragment.ACTION_VIDEO_FRAGMENT_RESUMED.equals(intent.getAction())) {
        // Restore video source when user returns to the fragment
                      fragmentIsVisible = true;
                      player.useVideoSource(true);

        // When a user returns from background, the system UI will always be shown even if
        // controls are invisible: hide it in that case

                      if (!isControlsVisible()) {
                          hideSystemUIIfNeeded();
                      }
                  }
}

void drainLoop() {
int missed = 1;
JoinInnerSubscriber<T>[] s = this.subscribers;
int n = s.length;
Subscriber<? super T> a = this.downstream;
for (;;) {
    long r = requested.get();
    long e = 0;
    middle: while (e != r) {

             if (cancelled) {
                               cleanup();
                               return;
             }

             Throwable ex = errors.get();
             if (ex != null) {
                               cleanup();
                               a.onError(ex);
                               return;
             }

             boolean d = done.get() == 0;
             boolean empty = true;
             for (int i = 0; i < s.length; i++) {
                               JoinInnerSubscriber<T> inner = s[i];
                               SimplePlainQueue<T> q = inner.queue;
                               if (q != null) {
                                                    T v = q.poll();
                                                    if (v != null) {
                                                  empty = false;
                                                  a.onNext(v);
                                                  inner.requestOne();

                                                  if (++e == r) {
                                                                       break middle;
                                                  }
                                                    }
                               }
             }

             if (d && empty) {
                               a.onComplete();
                               return;
             }

             if (empty) {
                               break;
             }
            }

    if (e == r) {

             if (cancelled) {
                    cleanup();
                    return;
             }

             Throwable ex = errors.get();
             if (ex != null) {
                    cleanup();
                    a.onError(ex);
                    return;
             }

             boolean d = done.get() == 0;
             boolean empty = true;
             for (int i = 0; i < n; i++) {
                    JoinInnerSubscriber<T> inner = s[i];
                    SimpleQueue<T> q = inner.queue;
                    if (q != null && !q.isEmpty()) {
                                             empty = false;
                                             break;
                    }
             }

             if (d && empty) {
                    a.onComplete();
                    return;
             }
    }

    if (e != 0) {
             BackpressureHelper.produced(requested, e);
    }

    missed = addAndGet(-missed);

    if (missed == 0) {
             break;
    }
}
}

@Override
public Connection getConnection() {
    List<ConnectionPool> pools = getShuffledNodesPool();
    JedisException suppressed = null;

    for (ConnectionPool pool : pools) {
        Connection jedis = null;

        try {
            jedis = pool.getResource();
            if (jedis == null) {
                continue;
            }

            jedis.ping();
            return jedis;
        } catch (JedisException ex) {
            if (suppressed == null) { // remembering first suppressed exception
                suppressed = ex;
            }

            if (jedis != null) {
                jedis.close();
            }
        }
    }

    JedisException noReachableNode = new JedisException("No reachable shard.");
    if (suppressed != null) {
        noReachableNode.addSuppressed(suppressed);
    }

    throw noReachableNode;
}

private void doPostprocessing() {
    errCode = ERROR_NOTHING;
    errObject = null;
    Thread thread = Thread.currentThread();
    notifyPostProcessing(1);
    if (DEBUG) {
        thread.setName("[" + TAG + "]  ps = " + psAlgorithm + "  filename = " + storage.getName());
    }
    Exception exception = null;
    try {
        psAlgorithm.run(this);
    } catch (Exception err) {
        Log.e(TAG, "Post-processing failed. " + psAlgorithm.toString(), err);
        if (err instanceof InterruptedIOException || err instanceof ClosedByInterruptException || thread.isInterrupted()) {
            notifyError(DownloadMission.ERROR_POSTPROCESSING_STOPPED, null);
            return;
        }

        if (errCode == ERROR_NOTHING) errCode = ERROR_POSTPROCESSING;
        exception = err;
    }
    finally {
            notifyPostProcessing(errCode == ERROR_NOTHING ?
                2: 0);
    }

    if (errCode != ERROR_NOTHING) {
        if (exception == null) exception = errObject;
        notifyError(ERROR_POSTPROCESSING, exception );
        return;
    }
    notifyFinished();
}

synchronized private LogEntry readNextEntry() {
    try {
        try {
            while (true) {
                String line = in.readLine();

                if (line == null) {
                    break;
                }

                Matcher m = src.timep.matcher(line);

                if (m.lookingAt()) {
                    if (buf.length() > 0) {
                        LogEntry e = new Log4JEntry(src.timestampFromText(dateformat, buf), src.getServerId(), buf);
                        buf = line;
                        return e;
                    }

                    buf = line;
                } else if (buf.length() > 0) {
                           buf += line + "\n";
                       }
            }
        } catch (EOFException eof) {
        // ignore, we've simply come to the end of the file
        }

        if (buf.length() > 0) {
            LogEntry e = new Log4JEntry(src.timestampFromText(dateformat, buf), src.getServerId(), buf);
            buf = "";
            return e;
        }
    } catch (Exception e) {
        LOG.error("Error reading next entry in file (" + src.file + "): " + e);
        return null;
    }

    return null;
}

private static boolean isValidJakartaMailMultipart(Object multipart) {

if (multipart == null) return false;
try {
        // Check if the object implements jakarta.mail.Multipart interface
    Class<?> multipartInterface = Class.forName("jakarta.mail.Multipart");
    if (!multipartInterface.isInstance(multipart)) {
    return false;
    }

        // Additional check for MimeMultipart

    try {
    Class<?> mimeMultipartClass = Class.forName("jakarta.mail.internet.MimeMultipart");
    if (mimeMultipartClass.isInstance(multipart)) {
        log.debug("Found MimeMultipart instance for enhanced processing");
        return true;
    }
    } catch (ClassNotFoundException e) {
      log.debug("MimeMultipart not available, using base Multipart interface");
    }

    return true;
} catch (ClassNotFoundException e) {
  log.debug("Jakarta Mail Multipart interface not available for validation");
  return false;
}
}

