@Override
public Bitmap transform(final Bitmap source) {
    if (DEBUG) {
        Log.d(TAG, "Thumbnail - transform() called");
    }

    final float notificationThumbnailWidth = Math.min(context.getResources().getDimension(R.dimen.player_notification_thumbnail_width), source.getWidth());
    final Bitmap result = BitmapCompat.createScaledBitmap(source, (int) notificationThumbnailWidth, (int) (source.getHeight() / (source.getWidth() / notificationThumbnailWidth)), null, true);

    if (result == source || !result.isMutable()) {
                        // create a new mutable bitmap to prevent strange crashes on some
                        // devices (see #4638)
        final Bitmap copied = BitmapCompat.createScaledBitmap(source, (int) notificationThumbnailWidth - 1, (int) (source.getHeight() / (source.getWidth() / (notificationThumbnailWidth - 1))), null, true);
        source.recycle();
        return copied;
    } else {
        source.recycle();
        return result;
    }
}

private static
Map<String, Object> doGetLoggerInfo(Logger logger) {
    Map<String, Object> info = new HashMap<String, Object>();

    info.put(LoggerHelper.name, logger.getName());
    info.put(LoggerHelper.clazz, logger.getClass());
    CodeSource codeSource = logger.getClass().getProtectionDomain().getCodeSource();
    if (codeSource != null) {
        info.put(LoggerHelper.codeSource, codeSource.getLocation());
    }

    info.put(LoggerHelper.additivity, logger.getAdditivity());
    Level level = logger.getLevel(), effectiveLevel = logger.getEffectiveLevel();
    if (level != null) {
        info.put(LoggerHelper.level, level.toString());
    }

    if (effectiveLevel != null) {
        info.put(LoggerHelper.effectiveLevel, effectiveLevel.toString());
    }

    @SuppressWarnings("unchecked") List<Map<String, Object>> result = doGetLoggerAppenders(logger.getAllAppenders());

    info.put(LoggerHelper.appenders, result);
    return info;
}

public static String generateMachineFingerprint() {
    try {
        // Get the MAC address
        StringBuilder sb = new StringBuilder();
        InetAddress ip = InetAddress.getLocalHost();
        NetworkInterface network = NetworkInterface.getByInetAddress(ip);
        if (network == null) {
            Enumeration<NetworkInterface> networks = NetworkInterface.getNetworkInterfaces();
            while(networks.hasMoreElements()) {
                NetworkInterface net = networks.nextElement();
                byte [] mac = net.getHardwareAddress();
                if (mac != null) {
                    for (int i = 0; i < mac.length; i++) {
                        sb.append(String.format("%02X", mac[i]));
                    }
                    break; // Use the first network interface with a MAC address
                }
            }
        } else {
            byte [] mac = network.getHardwareAddress();
            if (mac != null) {
                for (int i = 0; i < mac.length; i++) {
                    sb.append(String.format("%02X", mac[i]));
                }
            }
        }

        // Hash the MAC address for privacy and consistency

        MessageDigest md = MessageDigest.getInstance("SHA-256");
        byte [] hash = md.digest(sb.toString().getBytes(StandardCharsets.UTF_8));
        StringBuilder fingerprint = new StringBuilder();
        for (byte b : hash) {
            fingerprint.append(String.format("%02x", b));
        }

        return fingerprint.toString();
    } catch (Exception e) {
        return "GenericID";
    }
}

private Elst parseEdts(final Box ref) throws IOException {
    final Box b = untilBox(ref, ATOM_ELST);
    if (b == null) {
        return null;
    }
    final Elst obj = new Elst();
    final boolean v1 = stream.read() == 1;
    stream.skipBytes(3); // flags
    final int entryCount = stream.readInt();
    if (entryCount < 1) {
        obj.bMediaRate = 0x00010000; // default media rate (1.0)
        return obj;
    }

    if (v1) {
        stream.skipBytes(DataReader.LONG_SIZE); // segment duration
        obj.mediaTime = stream.readLong();
        // ignore all remain entries
        stream.skipBytes((entryCount - 1)*(DataReader.LONG_SIZE*2));
    } else {
        stream.skipBytes(DataReader.INTEGER_SIZE); // segment duration
        obj.mediaTime = stream.readInt();
    }
    obj.bMediaRate = stream.readInt();
    return obj;
}

