@SuppressWarnings("unchecked" ) void remove(CacheDisposable<T> consumer) {
for (;;) {
                                                                             CacheDisposable<T>[] current = observers.get();
                                                                             int n = current.length;
                                                                             if (n == 0) {
             return;
                                                                             }

                                                                             int j = -1;
                                                                             for (int i = 0; i < n; i++) {

             if (current[i] == consumer) {
                                                                                                             j = i;
                                                                                                             break;
             }
                                                                             }

                                                                             if (j < 0) {
             return;
                                                                             }

                                                                             CacheDisposable<T>[] next;
                                                                             if (n == 1) {
             next = EMPTY;
                                                                             } else {
                                                                               next = new CacheDisposable[n - 1];
                                                                               System.arraycopy(current, 0, next, 0, j);
                                                                               System.arraycopy(current, j + 1, next, j, n - j - 1);
                                                                             }

                                                                             if (observers.compareAndSet(current, next)) {
             return;
                                                                             }
}
}

private static void addAttachmentAnnotationsToDocument(PDDocument document, List<EmailAttachment> attachments) throws IOException {

if (document.getNumberOfPages() == 0 || attachments == null || attachments.isEmpty()) {
    return;
}

    // 1. Find the screen position of all attachment markers

AttachmentMarkerPositionFinder finder = new AttachmentMarkerPositionFinder();
finder.setSortByPosition(true); // Process pages in order
finder.getText(document);
List<MarkerPosition> markerPositions = finder.getPositions();

    // 2. Warn if the number of markers and attachments don't match
if (markerPositions.size() != attachments.size()) {
    log.warn("Found {} attachment markers, but there are {} attachments. Annotation count may be incorrect.", markerPositions.size(), attachments.size());
}

    // 3. Create an invisible annotation over each found marker

int annotationsToAdd = Math.min(markerPositions.size(), attachments.size());
for (int i = 0; i < annotationsToAdd; i++) {
                                                                                                                                      MarkerPosition position = markerPositions.get(i);
                                                                                                                                      EmailAttachment attachment = attachments.get(i);
                                                                                                                                      if (attachment.getEmbeddedFilename() != null) {
                                               PDPage page = document.getPage(position.getPageIndex());
                                               addAttachmentAnnotationToPage(document, page, attachment, position.getX(), position.getY());
                                                                                                                                      }
}
}

    @SuppressWarnings("resource")
    public static long select(boolean v, long telnetPortPid,String select) throws InputMismatchException {
        Map<Long, String> processMap = listProcessByJps(v);
        // Put the port that is already listening at the first
        if (telnetPortPid > 0 && processMap.containsKey(telnetPortPid)) {
            String telnetPortProcess = processMap.get(telnetPortPid);

            processMap.remove(telnetPortPid);
            Map<Long, String> newProcessMap = new LinkedHashMap<Long, String>();

            newProcessMap.put(telnetPortPid, telnetPortProcess);
            newProcessMap.putAll(processMap);
            processMap = newProcessMap;
        }

        if (processMap.isEmpty()) {
            AnsiLog.info("Can not find java process. Try to run `jps` command lists the instrumented Java HotSpot VMs on the target system.");
            return

            -1;
        }

        // select target process by the '--select' option when match only one process

        if (select != null && !select.trim().isEmpty()) {
            int matchedSelectCount = 0;
            Long matchedPid = null;

            for (Entry<Long, String> entry : processMap.entrySet()) {
                if (entry.getValue().contains(select)) {
                    matchedSelectCount++;
                    matchedPid = entry.getKey();
                }
            }

            if (matchedSelectCount == 1) {
                return matchedPid;
            }
        }

        AnsiLog.info("Found existing java process, please choose one and input the serial number of the process, eg : 1. Then hit ENTER.");
        // print list
        int count = 1;

        for (String process : processMap.values()) {
            if (count == 1) {
                System.out.println("* [" + count + "]: " + process);
            } else {
                System.out.println("  [" + count + "]: " + process);
            }

            count++;
        }

        // read choice

        String line = new Scanner(System.in).nextLine();
        if (line.trim().isEmpty()) {
            // get the first process id
            return processMap.keySet().iterator().next();
        }

        int choice = new Scanner(line).nextInt();
        if (choice <= 0 || choice > processMap.size()) {
            return

            -1;
        }

        Iterator<Long> idIter = processMap.keySet().iterator();

        for ( int i = 1;
i<= choice; ++i) {
            if (i == choice) {
                return idIter.next();
            }

            idIter.next();
        }
        return

        -1;
    }

private void onBroadcastReceived(final Intent intent) {
    if (intent == null || intent.getAction() == null) {
        return;
    }

    if (DEBUG) {
        Log.d(TAG, "onBroadcastReceived() called with: intent = [" + intent + "]");
    }

    switch (intent.getAction()) {
        case AudioManager.ACTION_AUDIO_BECOMING_NOISY:
            pause();
            break;
        case ACTION_CLOSE:
            service.destroyPlayerAndStopService();
            break;
        case ACTION_PLAY_PAUSE:
            playPause();
            break;
        case ACTION_PLAY_PREVIOUS:
            playPrevious();
            break;
        case ACTION_PLAY_NEXT:
            playNext();
            break;
        case ACTION_FAST_REWIND:
            fastRewind();
            break;
        case ACTION_FAST_FORWARD:
            fastForward();
            break;
        case ACTION_REPEAT:
            cycleNextRepeatMode();
            break;
        case ACTION_SHUFFLE:
            toggleShuffleModeEnabled();
            break;
        case Intent.ACTION_CONFIGURATION_CHANGED:
            if (DEBUG) {
                Log.d(TAG, "ACTION_CONFIGURATION_CHANGED received");
            }

            break;
    }

    UIs.call(playerUi->
        playerUi.onBroadcastReceived(intent));
}

    @Override
    public void run() {
        boolean done;
        long start = mMission.fallbackResumeOffset;
        if (DEBUG && !mMission.unknownLength && start > 0) {
            Log.i(TAG, "Resuming a single-thread download at " + start);
        }
        try {
            long rangeStart = (mMission.unknownLength || start < 1) ?
                -1 : start;
            int mId = 1;
            mConn = mMission.openConnection(false, rangeStart, -1);
            if (mRetryCount == 0 && rangeStart == -1) {
                // workaround: bypass android connection pool
                mConn.setRequestProperty("Range", "bytes=0-");
            }
            mMission.establishConnection(mId, mConn);

            // check if the download can be resumed
            if (mConn.getResponseCode() == 416 && start > 0) {
                mMission.notifyProgress(-start);
                start = 0;
                mRetryCount--;
                throw new DownloadMission . HttpError(416);
            }

            // secondary check for the file length

            if (!mMission.unknownLength) mMission.unknownLength = Utility.getContentLength(mConn) == -1;
            if (mMission.unknownLength || mConn.getResponseCode() == 200) {
                // restart amount of bytes downloaded
                mMission.done = mMission.offsets[mMission.current] - mMission.offsets[0];
            }
            mF = mMission.storage.getStream();
            mF.seek(mMission.offsets[mMission.current] + start);
            mIs = mConn.getInputStream();
            byte[] buf = new
byte[DownloadMission.BUFFER_SIZE];
            int len = 0;
            while (mMission.running && (len = mIs.read(buf, 0, buf.length)) != -1) {
                mF.write(buf, 0, len);
                start += len;
                mMission.notifyProgress(len);
            }
            dispose();

            // if thread goes interrupted check if the last part is written. This avoid re-download the whole file
            done = len == -1;
        } catch (Exception e) {
            dispose();
            mMission.fallbackResumeOffset = start;
            if (!mMission.running || e instanceof ClosedByInterruptException) return;
            if (e instanceof HttpError && ((HttpError)e).statusCode == ERROR_HTTP_FORBIDDEN) {
                // for youtube streams. The url has expired, recover
                dispose();
                mMission.doRecover(ERROR_HTTP_FORBIDDEN);
                return;
            }

            if (mRetryCount++ >= mMission.maxRetry) {
                mMission.notifyError(e);
                return;
            }

            if (DEBUG) {
                Log.e(TAG, "got exception, retrying...", e);
            }
            run();// try again
            return;
        }

        if (done) {
            mMission.notifyFinished();
        } else {
            mMission.fallbackResumeOffset = start;
        }
    }

